INSERT INTO `t_operator_voice` VALUES (5, 1, '信赖提升后交谈1', '我已经打印了刚才为你做的血常规项目的医学报告，一切正常。不过你的血液有着非常特殊的性质耶......', 'char_285_medic2/CN_007');
INSERT INTO `t_operator_voice` VALUES (6, 1, '信赖提升后交谈2', '我喜欢的东西？是“可爱的可露希尔姐姐”。你问为什么？这个......我也不是很清楚为什么啊，可露希尔她在初始化我的首选项的时候就这么设置了，呜......', 'char_285_medic2/CN_008');
INSERT INTO `t_operator_voice` VALUES (7, 1, '信赖提升后交谈3', '我通常只有在有人受伤或者生病时，才能感觉到自己发挥了作用。但对我来说，只要博士你能保持健康，即使我无法发挥任何作用、只是一直跟着你，我也会很安心的......', 'char_285_medic2/CN_009');
INSERT INTO `t_operator_voice` VALUES (8, 1, '闲置', '博士你刚才打呼噜的声音，我已经偷偷录下来了。你的睡眠质量正常，深度睡眠时间为——', 'char_285_medic2/CN_010');
INSERT INTO `t_operator_voice` VALUES (9, 1, '干员报到', '嗨，你好，博士。你在期待我能对你多说些什么，但我不能，毕竟我只是一台医疗机器人......', 'char_285_medic2/CN_011');
INSERT INTO `t_operator_voice` VALUES (10, 1, '观看作战记录', '固件升级完成，请重新启动设备。请点击任意位置以重新启动。', 'char_285_medic2/CN_012');
INSERT INTO `t_operator_voice` VALUES (11, 1, '编入队伍', '医疗设备消毒，ok。电力，ok。随时可以出发。', 'char_285_medic2/CN_017');
INSERT INTO `t_operator_voice` VALUES (12, 1, '任命队长', '笨重的我除了急救和治疗以外就什么都做不了......就算如此，博士你还是打算让我领队吗？', 'char_285_medic2/CN_018');
INSERT INTO `t_operator_voice` VALUES (13, 1, '行动出发', '出发......希望大家都不要受伤。', 'char_285_medic2/CN_019');
INSERT INTO `t_operator_voice` VALUES (14, 1, '行动开始', '请小心应战！紧急治疗的任务请放心交给我。', 'char_285_medic2/CN_020');
INSERT INTO `t_operator_voice` VALUES (15, 1, '选中干员1', '需要治疗吗？', 'char_285_medic2/CN_021');
INSERT INTO `t_operator_voice` VALUES (16, 1, '选中干员2', '已就绪。', 'char_285_medic2/CN_022');
INSERT INTO `t_operator_voice` VALUES (17, 1, '部署1', '好。', 'char_285_medic2/CN_023');
INSERT INTO `t_operator_voice` VALUES (18, 1, '部署2', '收到。', 'char_285_medic2/CN_024');
INSERT INTO `t_operator_voice` VALUES (19, 1, '作战中1', '开始治疗。', 'char_285_medic2/CN_025');
INSERT INTO `t_operator_voice` VALUES (20, 1, '作战中2', '紧急治疗启动！', 'char_285_medic2/CN_026');
INSERT INTO `t_operator_voice` VALUES (21, 1, '作战中3', '镇痛剂已使用。', 'char_285_medic2/CN_027');
INSERT INTO `t_operator_voice` VALUES (22, 1, '作战中4', '设备消毒完毕，准备治疗。', 'char_285_medic2/CN_028');
INSERT INTO `t_operator_voice` VALUES (23, 1, '4星结束行动', '这样战斗下去受伤的人会越来越多的。人类的互相伤害究竟何时才能停止呢？', 'char_285_medic2/CN_029');
INSERT INTO `t_operator_voice` VALUES (24, 1, '3星结束行动', '辛苦了博士，真是一场恶战啊。那些敌人看上去是不是健康状况不太好啊，我可以为他们做治疗吗？', 'char_285_medic2/CN_030');
INSERT INTO `t_operator_voice` VALUES (25, 1, '非3星结束行动', '大家都没事吧？请不要动，我来帮你们紧急处理一下伤口。', 'char_285_medic2/CN_031');
INSERT INTO `t_operator_voice` VALUES (26, 1, '行动失败', '没关系我们已经暂时撤退了。那个......接下我要清创，可能会有点痛，请忍住哦。', 'char_285_medic2/CN_032');
INSERT INTO `t_operator_voice` VALUES (27, 1, '进驻设施', '“可爱的可露希尔姐姐”说这里需要帮忙。', 'char_285_medic2/CN_033');
INSERT INTO `t_operator_voice` VALUES (28, 1, '戳一下', '啊啊啊......', 'char_285_medic2/CN_034');
INSERT INTO `t_operator_voice` VALUES (29, 1, '信赖触摸', '我曾经找“可爱的可露希尔姐姐”，要求修改我“最喜欢的东西”变成博士你。但是她死活都不愿意......', 'char_285_medic2/CN_036');
INSERT INTO `t_operator_voice` VALUES (30, 1, '标题', '明日方舟。', 'char_285_medic2/CN_037');
INSERT INTO `t_operator_voice` VALUES (31, 1, '问候', '欢迎回来，博士。你的身体状况良好，要继续保持哦。', 'char_285_medic2/CN_042');
INSERT INTO `t_operator_voice` VALUES (32, 2, '任命助理', '感谢您选择Castle-3！很荣幸成为您的助理！', 'char_286_cast3/CN_001');
INSERT INTO `t_operator_voice` VALUES (33, 2, '交谈1', '请不要被我的外形吓坏，博士。这是我经过可露希尔改造的坚固金属外壳，可为您在战场以一敌百。', 'char_286_cast3/CN_002');
INSERT INTO `t_operator_voice` VALUES (34, 2, '交谈2', '注意，请不要用力拍打本设备，否则将会造成本设备损坏。', 'char_286_cast3/CN_003');
INSERT INTO `t_operator_voice` VALUES (35, 2, '交谈3', '野外的地形多变，但是Castle-3配备了性能卓越的引擎，采用四轮驱动方案，将会在任何时候载您离开困境！', 'char_286_cast3/CN_004');
INSERT INTO `t_operator_voice` VALUES (36, 2, '信赖提升后交谈1', '博士，当你遇到危险请一定找Castle-3！Castle-3为专业作战而生，是您最佳的安全方案。', 'char_286_cast3/CN_007');
INSERT INTO `t_operator_voice` VALUES (37, 2, '信赖提升后交谈2', '和您在一起Castle-3感到非常愉快，博士您如果有任何目标需要打击，Castle-3赴汤蹈火在所不辞！', 'char_286_cast3/CN_008');
INSERT INTO `t_operator_voice` VALUES (38, 2, '信赖提升后交谈3', '虽然Castle-3我的管理者是“可露希尔主人”，不过更多时候您对Castle-3来说更加重要，请相信Castle-3是您身边最贴心的作战伙伴！', 'char_286_cast3/CN_009');
INSERT INTO `t_operator_voice` VALUES (39, 2, '闲置', '进入休眠模式。按任意键以唤醒。', 'char_286_cast3/CN_010');
INSERT INTO `t_operator_voice` VALUES (40, 2, '干员报到', '您好，博士，欢迎使用Castle-3！请点击任意位置以开始初始化首选项。', 'char_286_cast3/CN_011');
INSERT INTO `t_operator_voice` VALUES (41, 2, '观看作战记录', '固件升级完成，请重新启动设备。请点击任意位置以重启动您可靠的近卫机器人！', 'char_286_cast3/CN_012');
INSERT INTO `t_operator_voice` VALUES (42, 2, '编入队伍', '设备检查完毕，通信正常。Castle-3已就绪，随时等待您的命令。', 'char_286_cast3/CN_017');
INSERT INTO `t_operator_voice` VALUES (43, 2, '任命队长', '了解，Castle-3是您带领队伍冲锋陷阵的理想之选！', 'char_286_cast3/CN_018');
INSERT INTO `t_operator_voice` VALUES (44, 2, '行动出发', '接下来，Castle-3要展现下工业级作战平台的实力了！', 'char_286_cast3/CN_019');
