INSERT INTO `t_operator_voice` VALUES (50, 2, '作战中1', '放马过来吧！', 'char_286_cast3/CN_025');
INSERT INTO `t_operator_voice` VALUES (51, 2, '作战中2', '别妄想能靠近一步，你们这些恶徒！', 'char_286_cast3/CN_026');
