INSERT INTO `t_operator_voice` VALUES (55, 2, '3星结束行动', '真是一场精彩的战斗，下一次再一起并肩作战吧！', 'char_286_cast3/CN_030');
INSERT INTO `t_operator_voice` VALUES (56, 2, '非3星结束行动', '大家都没事吧？希望Castle-3的表现令大家满意。', 'char_286_cast3/CN_031');
