INSERT INTO `t_operator_voice` VALUES (60, 2, '信赖触摸', '博士，要一起喝一杯吗？虽然我不能喝，不过我能请您。别担心，最后也是可露希尔主人付帐。', 'char_286_cast3/CN_036');
