INSERT INTO `t_operator_voice` VALUES (62, 2, '问候', '今天天气不错啊，博士！', 'char_286_cast3/CN_042');
INSERT INTO `t_operator_voice` VALUES (63, 3, '任命助理', '博士，只要告诉我今天的工作内容就行了。', 'char_502_nblade/CN_001');
INSERT INTO `t_operator_voice` VALUES (64, 3, '交谈1', '即使在工作之外的时间里，我也会注意您的状况。', 'char_502_nblade/CN_002');
INSERT INTO `t_operator_voice` VALUES (65, 3, '交谈2', '嗯？有什么事吗？', 'char_502_nblade/CN_003');
INSERT INTO `t_operator_voice` VALUES (66, 3, '交谈3', '我的皮肤对阳光的抵抗力非常弱，所以我希望您尽可能地把夜间行动的任务安排给我。', 'char_502_nblade/CN_004');
INSERT INTO `t_operator_voice` VALUES (67, 3, '信赖提升后交谈1', '虽然我们能做到的事有限，但我还是希望能帮忙减轻您的负担。', 'char_502_nblade/CN_007');
INSERT INTO `t_operator_voice` VALUES (68, 3, '信赖提升后交谈2', '每当我看到其他小队的干员们的活跃表现，总是会特别羡慕......', 'char_502_nblade/CN_008');
INSERT INTO `t_operator_voice` VALUES (69, 3, '信赖提升后交谈3', '我想为博士出战的心情，是不需要理由的。希望总有一天，我们也能活跃在日常战斗的最前线......', 'char_502_nblade/CN_009');
INSERT INTO `t_operator_voice` VALUES (70, 3, '闲置', '博士，让我今后也一直守护着您吧。', 'char_502_nblade/CN_010');
INSERT INTO `t_operator_voice` VALUES (71, 3, '干员报到', '先锋干员夜刀，你——就是我要保护的人吗？', 'char_502_nblade/CN_011');
