INSERT INTO `t_operator_voice` VALUES (98, 4, '信赖提升后交谈1', '博士，俺觉得，如果能更多地了解你，说不定这面盾牌也会变得更坚固。', 'char_500_noirc/CN_007');
INSERT INTO `t_operator_voice` VALUES (99, 4, '信赖提升后交谈2', '其实俺知道自己的极限在什么地方。但是，俺不想让夜刀对俺放心不下，这很重要。其他的，以后再说吧！', 'char_500_noirc/CN_008');
INSERT INTO `t_operator_voice` VALUES (100, 4, '信赖提升后交谈3', '无论什么地方，都需要俺这样的人对吧？不，俺可没有得意忘形。俺要说的是，大人物遭遇危险的时候，总得有挺身保护他们的那种人。', 'char_500_noirc/CN_009');
INSERT INTO `t_operator_voice` VALUES (101, 4, '闲置', '偶尔也得摘下面具，放松放松啊。', 'char_500_noirc/CN_010');
