INSERT INTO `t_operator_voice` VALUES (125, 5, '交谈2', '老夫我这样的体色很稀有吧？哈哈，那是在以前，潜入一间生化实验室的时候......哈，开玩笑的，开玩笑。', 'char_503_rang/CN_003');
INSERT INTO `t_operator_voice` VALUES (126, 5, '交谈3', '老夫我睡觉的时候也得戴着这条围巾，不然脖子啊，会着凉的。', 'char_503_rang/CN_004');
INSERT INTO `t_operator_voice` VALUES (127, 5, '信赖提升后交谈1', '老夫这幅样貌，对一些偏僻小镇的居民来说实在是太稀奇了。呵，招来一些异样的目光也是没办法的事。', 'char_503_rang/CN_007');
INSERT INTO `t_operator_voice` VALUES (128, 5, '信赖提升后交谈2', '没想到博士竟然这么在意老夫，除了杜林，可很少有人这样追问我平日里过的怎么样了。', 'char_503_rang/CN_008');
INSERT INTO `t_operator_voice` VALUES (129, 5, '信赖提升后交谈3', '实际上，老夫光着脚，就能根据地面的振动幅度来测量敌人的距离，这样也能更容易掌握敌人的方位。', 'char_503_rang/CN_009');
INSERT INTO `t_operator_voice` VALUES (130, 5, '闲置', '哈......打呼噜可不好，会被其他巡林者听见的。', 'char_503_rang/CN_010');
INSERT INTO `t_operator_voice` VALUES (131, 5, '干员报到', '狙击干员巡林者，等候差遣。', 'char_503_rang/CN_011');
INSERT INTO `t_operator_voice` VALUES (132, 5, '观看作战记录', '真不错。', 'char_503_rang/CN_012');
INSERT INTO `t_operator_voice` VALUES (133, 5, '编入队伍', '轮到老夫我出场了吗？', 'char_503_rang/CN_017');
INSERT INTO `t_operator_voice` VALUES (134, 5, '任命队长', '好，老夫我大展身手的时候到了。', 'char_503_rang/CN_018');
INSERT INTO `t_operator_voice` VALUES (135, 5, '行动出发', '出发了！', 'char_503_rang/CN_019');
INSERT INTO `t_operator_voice` VALUES (136, 5, '行动开始', '我看到猎物了，作战开始。', 'char_503_rang/CN_020');
INSERT INTO `t_operator_voice` VALUES (137, 5, '选中干员1', '请您吩咐。', 'char_503_rang/CN_021');
INSERT INTO `t_operator_voice` VALUES (138, 5, '选中干员2', '有什么命令？', 'char_503_rang/CN_022');
INSERT INTO `t_operator_voice` VALUES (139, 5, '部署1', '好。', 'char_503_rang/CN_023');
INSERT INTO `t_operator_voice` VALUES (140, 5, '部署2', '知道了。', 'char_503_rang/CN_024');
INSERT INTO `t_operator_voice` VALUES (141, 5, '作战中1', '我看到你了！', 'char_503_rang/CN_025');
INSERT INTO `t_operator_voice` VALUES (142, 5, '作战中2', '尝尝老夫这一箭！', 'char_503_rang/CN_026');
INSERT INTO `t_operator_voice` VALUES (143, 5, '4星结束行动', '无穷无尽的挑战哪，这正是咱们一直期望的不是吗，黑角？', 'char_503_rang/CN_029');
INSERT INTO `t_operator_voice` VALUES (144, 5, '3星结束行动', '真是一场酣畅淋漓的战斗！', 'char_503_rang/CN_030');
INSERT INTO `t_operator_voice` VALUES (145, 5, '非3星结束行动', '有得有失，马马虎虎吧。', 'char_503_rang/CN_031');
INSERT INTO `t_operator_voice` VALUES (146, 5, '行动失败', '糟糕啊......呼吸乱了......', 'char_503_rang/CN_032');
INSERT INTO `t_operator_voice` VALUES (147, 5, '进驻设施', '想要老夫我做什么，请不必顾虑，尽管说。', 'char_503_rang/CN_033');
INSERT INTO `t_operator_voice` VALUES (148, 5, '戳一下', '尾巴，可别拽断了。', 'char_503_rang/CN_034');
INSERT INTO `t_operator_voice` VALUES (149, 5, '信赖触摸', '没想到您居然好这口。', 'char_503_rang/CN_036');
INSERT INTO `t_operator_voice` VALUES (150, 5, '标题', '明日方舟。', 'char_503_rang/CN_037');
INSERT INTO `t_operator_voice` VALUES (151, 5, '问候', '博士，有什么事？', 'char_503_rang/CN_042');
INSERT INTO `t_operator_voice` VALUES (152, 6, '任命助理', '早——上——好，博......士，啊，那我把睡袋放这儿，接下来，我就继续睡了，麻——烦——你——了。', 'char_501_durin/CN_001');
INSERT INTO `t_operator_voice` VALUES (153, 6, '交谈1', '我的战斗方式？当然是用法术来战斗吧。让我来向你展示，大地的威严。', 'char_501_durin/CN_002');
INSERT INTO `t_operator_voice` VALUES (154, 6, '交谈2', '现在几点啊......？其实，早晨，中午，下午和晚上，都是我的非工作时间。', 'char_501_durin/CN_003');
INSERT INTO `t_operator_voice` VALUES (155, 6, '交谈3', '要帮我定制小号制服？不——要，大号制服在睡觉的时候能当被子，很——暖和。', 'char_501_durin/CN_004');
INSERT INTO `t_operator_voice` VALUES (156, 6, '信赖提升后交谈1', '博——士——我——请假！因为我，已经被邀请参加速睡大赛啦。', 'char_501_durin/CN_007');
INSERT INTO `t_operator_voice` VALUES (157, 6, '信赖提升后交谈2', '博士，来看看这个。这可不是我平时用的法杖，它是一枚钟摆。接下来博士会在五分钟之内睡着，不会给我安排任何工作......Zzzz......', 'char_501_durin/CN_008');
INSERT INTO `t_operator_voice` VALUES (158, 6, '信赖提升后交谈3', '唉，可以，和博士您聊聊我的故乡吗。', 'char_501_durin/CN_009');
INSERT INTO `t_operator_voice` VALUES (159, 6, '闲置', '好不容易把博士哄睡着了，我......竟然一点都不困了，可......恶......', 'char_501_durin/CN_010');
INSERT INTO `t_operator_voice` VALUES (160, 6, '干员报到', '喂——喂！能听到我说话吗？低头看下面啦！初次见面，大个子的博士，我是术师，杜林哦。', 'char_501_durin/CN_011');
INSERT INTO `t_operator_voice` VALUES (161, 6, '观看作战记录', '啊——我·终·于·变·强·啦。', 'char_501_durin/CN_012');
INSERT INTO `t_operator_voice` VALUES (162, 6, '编入队伍', '健康的秘诀，是好好睡觉。', 'char_501_durin/CN_017');
INSERT INTO `t_operator_voice` VALUES (163, 6, '任命队长', '这是队长命令，各位，现在就开始打盹，补充体力。', 'char_501_durin/CN_018');
INSERT INTO `t_operator_voice` VALUES (164, 6, '行动出发', '如果可以的话，我想在地上挖个洞当掩体，用来睡觉。', 'char_501_durin/CN_019');
INSERT INTO `t_operator_voice` VALUES (165, 6, '行动开始', '各位，拜托你们喽。', 'char_501_durin/CN_020');
INSERT INTO `t_operator_voice` VALUES (166, 6, '选中干员1', '哦，哦。', 'char_501_durin/CN_021');
INSERT INTO `t_operator_voice` VALUES (167, 6, '选中干员2', '嗯？', 'char_501_durin/CN_022');
INSERT INTO `t_operator_voice` VALUES (168, 6, '部署1', '真麻烦。', 'char_501_durin/CN_023');
INSERT INTO `t_operator_voice` VALUES (169, 6, '部署2', '我知道啦。', 'char_501_durin/CN_024');
INSERT INTO `t_operator_voice` VALUES (170, 6, '作战中1', '打倒你！', 'char_501_durin/CN_025');
INSERT INTO `t_operator_voice` VALUES (171, 6, '作战中2', '去吧！', 'char_501_durin/CN_026');
INSERT INTO `t_operator_voice` VALUES (172, 6, '4星结束行动', '干嘛要来处理这么棘手的目标啊？轻轻松松的不是更好嘛！还好赢了......', 'char_501_durin/CN_029');
INSERT INTO `t_operator_voice` VALUES (173, 6, '3星结束行动', '胜利，理所当然，在背后默默地用法术支援你们的可是我啊。', 'char_501_durin/CN_030');
INSERT INTO `t_operator_voice` VALUES (174, 6, '非3星结束行动', '博士，还在纠结放走了多少敌人吗？', 'char_501_durin/CN_031');
INSERT INTO `t_operator_voice` VALUES (175, 6, '行动失败', '呜......对不起......下次我会认真战斗的......', 'char_501_durin/CN_032');
INSERT INTO `t_operator_voice` VALUES (176, 6, '进驻设施', '巨人国......我来了！', 'char_501_durin/CN_033');
INSERT INTO `t_operator_voice` VALUES (177, 6, '戳一下', '咕？', 'char_501_durin/CN_034');
INSERT INTO `t_operator_voice` VALUES (178, 6, '信赖触摸', '现在我心情很好，给你变个魔术怎么样？', 'char_501_durin/CN_036');
INSERT INTO `t_operator_voice` VALUES (179, 6, '标题', '明日方舟。', 'char_501_durin/CN_037');
INSERT INTO `t_operator_voice` VALUES (180, 6, '问候', '呼啊......早安......', 'char_501_durin/CN_042');
INSERT INTO `t_operator_voice` VALUES (181, 7, '任命助理', '抱歉，我能做的事情很有限，也不太会说话，但作为助理感到很荣幸，如果我能帮到博士阁下那就太好了。', 'char_009_12fce/CN_001');
INSERT INTO `t_operator_voice` VALUES (182, 7, '交谈1', '博士阁下，一切不愉快的事情都会过去，大家都是相信您的到来能让一切变得更好，才愿意追随您的，请相信自己。', 'char_009_12fce/CN_002');
INSERT INTO `t_operator_voice` VALUES (183, 7, '交谈2', '很多感染者来到罗德岛生活，并没有怀着多么远大的目标，他们更希望是成为这里的一份子。在这个不再接受他们的世界里活下去，这样下去可不行......', 'char_009_12fce/CN_003');
INSERT INTO `t_operator_voice` VALUES (184, 7, '交谈3', '巨枭她虽然对人不是很友好，不过她并没有什么恶意，希望您可以多多包涵。', 'char_009_12fce/CN_004');
INSERT INTO `t_operator_voice` VALUES (185, 7, '信赖提升后交谈1', '12F？那是我来到这个世界的日子。博士你就把这天当作是我的生日吧。', 'char_009_12fce/CN_007');
INSERT INTO `t_operator_voice` VALUES (186, 7, '信赖提升后交谈2', '我可能追随不了一个人一辈子，不过现在至少是这一辈子我追随一个人的开始。', 'char_009_12fce/CN_008');
INSERT INTO `t_operator_voice` VALUES (187, 7, '信赖提升后交谈3', '“她”终于找到你了，博士。这真是一次漫长的旅程。你对“她”,以及所有人来说有着非常重要的意义......', 'char_009_12fce/CN_009');
INSERT INTO `t_operator_voice` VALUES (188, 7, '闲置', '即使是您合上眼的时候，我也会伴随在您的左右，保证您的安全，请安心休息吧。', 'char_009_12fce/CN_010');
INSERT INTO `t_operator_voice` VALUES (189, 7, '干员报到', '感谢阁下让我加入这里，从今天开始，我将尽全力为您效劳——作为一名术师。一直以来我都没有名字，如果感觉不方便的话，今后请用12F称呼我。', 'char_009_12fce/CN_011');
INSERT INTO `t_operator_voice` VALUES (190, 7, '观看作战记录', '原来是这样，我明白了。感谢您的指导。', 'char_009_12fce/CN_012');
INSERT INTO `t_operator_voice` VALUES (191, 7, '编入队伍', '虽然我能力不出众，但是我会尽我所能去战斗的。', 'char_009_12fce/CN_017');
INSERT INTO `t_operator_voice` VALUES (192, 7, '任命队长', '第一个选择我真的没关系吗？谢谢你博士。', 'char_009_12fce/CN_018');
INSERT INTO `t_operator_voice` VALUES (193, 7, '行动出发', '放轻松，博士。我们一定完成这次行动。', 'char_009_12fce/CN_019');
INSERT INTO `t_operator_voice` VALUES (194, 7, '行动开始', '诸位，请做好作战准备。', 'char_009_12fce/CN_020');
INSERT INTO `t_operator_voice` VALUES (195, 7, '选中干员1', '请指示。', 'char_009_12fce/CN_021');
INSERT INTO `t_operator_voice` VALUES (196, 7, '选中干员2', '我准备好了。', 'char_009_12fce/CN_022');
INSERT INTO `t_operator_voice` VALUES (197, 7, '部署1', '了解。', 'char_009_12fce/CN_023');
INSERT INTO `t_operator_voice` VALUES (198, 7, '部署2', '收到。', 'char_009_12fce/CN_024');
INSERT INTO `t_operator_voice` VALUES (199, 7, '作战中1', '请让我看见前进的道路。', 'char_009_12fce/CN_025');
INSERT INTO `t_operator_voice` VALUES (200, 7, '作战中2', '燃烧吧。', 'char_009_12fce/CN_026');
INSERT INTO `t_operator_voice` VALUES (201, 7, '作战中3', '驱除前方的障碍。', 'char_009_12fce/CN_027');
INSERT INTO `t_operator_voice` VALUES (202, 7, '作战中4', '巨枭，小心！', 'char_009_12fce/CN_028');
INSERT INTO `t_operator_voice` VALUES (203, 7, '4星结束行动', '我早已习惯了失败，但只有经历无数失败之后的胜利，才无比难得。不是吗，博士？', 'char_009_12fce/CN_029');
INSERT INTO `t_operator_voice` VALUES (204, 7, '3星结束行动', '这是属于您的胜利，我很荣幸能成为您的棋子。', 'char_009_12fce/CN_030');
INSERT INTO `t_operator_voice` VALUES (205, 7, '非3星结束行动', '有些危险，不过最后还是胜利了。巨枭你没事吧？', 'char_009_12fce/CN_031');
INSERT INTO `t_operator_voice` VALUES (206, 7, '行动失败', '败乃兵家常事，博士请不要气馁......我经历过许多战争，经验告诉我，属于您的胜利即将到来。', 'char_009_12fce/CN_032');
INSERT INTO `t_operator_voice` VALUES (207, 7, '进驻设施', '没事，请让我来帮忙吧。', 'char_009_12fce/CN_033');
INSERT INTO `t_operator_voice` VALUES (208, 7, '戳一下', '啊，请小心。', 'char_009_12fce/CN_034');
INSERT INTO `t_operator_voice` VALUES (209, 7, '信赖触摸', '博士，感谢你为我做了这么多，而我却没有什么可以报答你的，抱歉。', 'char_009_12fce/CN_036');
INSERT INTO `t_operator_voice` VALUES (210, 7, '标题', '明日方舟。', 'char_009_12fce/CN_037');
INSERT INTO `t_operator_voice` VALUES (211, 7, '问候', '早上好，博士。', 'char_009_12fce/CN_042');
INSERT INTO `t_operator_voice` VALUES (212, 8, '任命助理', '博士，有什么我能做的事吗？', 'char_123_fang/CN_001');
INSERT INTO `t_operator_voice` VALUES (213, 8, '交谈1', '谢谢您的信任！今后我也会继续加油的。', 'char_123_fang/CN_002');
INSERT INTO `t_operator_voice` VALUES (214, 8, '交谈2', '芙蓉和炎熔又吵架了，真让我头疼。明明是亲生姐妹啊，为什么会变成现在这样呢......', 'char_123_fang/CN_003');
INSERT INTO `t_operator_voice` VALUES (215, 8, '交谈3', '我们和杜宾老师经常进行模拟比赛，不过我完全摸不透老师的技巧。如果博士和阿米娅有时间来参观训练的话，可以亲自指导我们吗？', 'char_123_fang/CN_004');
INSERT INTO `t_operator_voice` VALUES (216, 8, '晋升后交谈1', '啊，我知道了，我的小队里又有人迟到了对吧。我会想办法解决的。', 'char_123_fang/CN_005');
INSERT INTO `t_operator_voice` VALUES (217, 8, '信赖提升后交谈1', '我也是感染者，但并没能像炎熔那样觉醒法术天赋......', 'char_123_fang/CN_007');
INSERT INTO `t_operator_voice` VALUES (218, 8, '信赖提升后交谈2', '罗德岛给了我们容身之地，博士，所以就算拼上自己的生命，我也一定要保护好所有人。', 'char_123_fang/CN_008');
INSERT INTO `t_operator_voice` VALUES (219, 8, '信赖提升后交谈3', '米格鲁诚实可靠，克洛丝洞察力强，炎熔很聪明，芙蓉充满活力，我一直很喜欢组里的大家......只要芙蓉不再强迫我吃她新创作的可怕料理就好......', 'char_123_fang/CN_009');
INSERT INTO `t_operator_voice` VALUES (220, 8, '闲置', '......嘘，别打扰博士休息，有事我们到外面说。', 'char_123_fang/CN_010');
INSERT INTO `t_operator_voice` VALUES (221, 8, '干员报到', '我是芬，长枪手，现在正担任着行动预备组A1的队长一职。我和我的小队一定会竭尽全力，成为罗德岛可靠的战力。', 'char_123_fang/CN_011');
INSERT INTO `t_operator_voice` VALUES (222, 8, '观看作战记录', '感谢您的指导。', 'char_123_fang/CN_012');
INSERT INTO `t_operator_voice` VALUES (223, 8, '精英化晋升1', '您的提拔是对我努力的最好肯定。', 'char_123_fang/CN_013');
INSERT INTO `t_operator_voice` VALUES (224, 8, '编入队伍', '请交给我。', 'char_123_fang/CN_017');
INSERT INTO `t_operator_voice` VALUES (225, 8, '任命队长', '没问题，我很了解队长该做什么。', 'char_123_fang/CN_018');
INSERT INTO `t_operator_voice` VALUES (226, 8, '行动出发', '全体人员，出发前务必要检查随身装备是否备齐了。', 'char_123_fang/CN_019');
INSERT INTO `t_operator_voice` VALUES (227, 8, '行动开始', '发现敌方单位。准备作战！', 'char_123_fang/CN_020');
INSERT INTO `t_operator_voice` VALUES (228, 8, '选中干员1', '准备行动。', 'char_123_fang/CN_021');
INSERT INTO `t_operator_voice` VALUES (229, 8, '选中干员2', '准备万全。', 'char_123_fang/CN_022');
INSERT INTO `t_operator_voice` VALUES (230, 8, '部署1', '各就各位。', 'char_123_fang/CN_023');
INSERT INTO `t_operator_voice` VALUES (231, 8, '部署2', '请交给我们。', 'char_123_fang/CN_024');
INSERT INTO `t_operator_voice` VALUES (232, 8, '作战中1', '刺穿。', 'char_123_fang/CN_025');
INSERT INTO `t_operator_voice` VALUES (233, 8, '作战中2', '大家跟着我！', 'char_123_fang/CN_026');
INSERT INTO `t_operator_voice` VALUES (234, 8, '4星结束行动', '只要有正确合理的作战计划，就算是我们这样的新手，也能赢下这样艰难的战斗！', 'char_123_fang/CN_029');
INSERT INTO `t_operator_voice` VALUES (235, 8, '3星结束行动', '我们按照预定目标，完成了此次作战。', 'char_123_fang/CN_030');
INSERT INTO `t_operator_voice` VALUES (236, 8, '非3星结束行动', '只要放走一个敌人，作战就不能说是成功的。米格鲁，下次我们也要加油。', 'char_123_fang/CN_031');
INSERT INTO `t_operator_voice` VALUES (237, 8, '行动失败', '非常抱歉，杜宾老师......', 'char_123_fang/CN_032');
INSERT INTO `t_operator_voice` VALUES (238, 8, '进驻设施', '是的，我回来了。克洛丝也在这里吗？', 'char_123_fang/CN_033');
INSERT INTO `t_operator_voice` VALUES (239, 8, '戳一下', '啊！', 'char_123_fang/CN_034');
INSERT INTO `t_operator_voice` VALUES (240, 8, '信赖触摸', '我帮上您的忙了吗？', 'char_123_fang/CN_036');
INSERT INTO `t_operator_voice` VALUES (241, 8, '标题', '明日方舟。', 'char_123_fang/CN_037');
INSERT INTO `t_operator_voice` VALUES (242, 8, '问候', '我随时都可以，博士。', 'char_123_fang/CN_042');
INSERT INTO `t_operator_voice` VALUES (243, 9, '任命助理', '等候您的命令，博士。', 'char_240_wyvern/CN_001');
INSERT INTO `t_operator_voice` VALUES (244, 9, '交谈1', '生化小组的两位前辈也在这里？那......以后能把我分配到前辈们的行动组吗？', 'char_240_wyvern/CN_002');
INSERT INTO `t_operator_voice` VALUES (245, 9, '交谈2', '呼哈......呼哈......再做三组体能训练......今天的锻炼就完成了......一半......博士......麻烦您帮我......拿下水壶......', 'char_240_wyvern/CN_003');
INSERT INTO `t_operator_voice` VALUES (246, 9, '交谈3', '想问我家乡的情况？那是片......残酷的荒漠，每天光是活着就很累了。和那里比起来，罗德岛就是天堂啊。', 'char_240_wyvern/CN_004');
INSERT INTO `t_operator_voice` VALUES (247, 9, '晋升后交谈1', '比起在黑钢实习的时候，我变得更强了。', 'char_240_wyvern/CN_005');
INSERT INTO `t_operator_voice` VALUES (248, 9, '信赖提升后交谈1', '黑钢的第二十二条规定：“武器离身者一律辞退”，所以我一直随身带着我的斧枪。唉？平时不用？但我记得......稍等......“此规定仅限入职训练与契约中干员。”......芙兰卡小姐为什么没提醒我呢......', 'char_240_wyvern/CN_007');
INSERT INTO `t_operator_voice` VALUES (249, 9, '信赖提升后交谈2', '杜宾教官太残忍了！为什么只有我要面对活生生的源石蜗牛呢！我，我真的下不了手啊！', 'char_240_wyvern/CN_008');
INSERT INTO `t_operator_voice` VALUES (250, 9, '信赖提升后交谈3', '我想......我明白芙兰卡和雷蛇呆在罗德岛的原因了。可能和黑钢相比，这里更像个家吧。除了任务之外，居然还有那么多有趣的东西......', 'char_240_wyvern/CN_009');
INSERT INTO `t_operator_voice` VALUES (251, 9, '闲置', '嘘，安静点，别打扰博士休息。', 'char_240_wyvern/CN_010');
INSERT INTO `t_operator_voice` VALUES (252, 9, '干员报到', '香草，以受训干员的身份前来学习。博士阁下，今后就请您多多指教了。', 'char_240_wyvern/CN_011');
INSERT INTO `t_operator_voice` VALUES (253, 9, '观看作战记录', '是时候整理笔记了，新知识得赶紧消化掉。', 'char_240_wyvern/CN_012');
INSERT INTO `t_operator_voice` VALUES (254, 9, '精英化晋升1', '这是......晋升勋章？给我的？谢，谢谢！', 'char_240_wyvern/CN_013');
INSERT INTO `t_operator_voice` VALUES (255, 9, '编入队伍', '香草会助大家一臂之力的！', 'char_240_wyvern/CN_017');
INSERT INTO `t_operator_voice` VALUES (256, 9, '任命队长', '我的话，真的可以吗？', 'char_240_wyvern/CN_018');
INSERT INTO `t_operator_voice` VALUES (257, 9, '行动出发', '我会加倍努力的。', 'char_240_wyvern/CN_019');
INSERT INTO `t_operator_voice` VALUES (258, 9, '行动开始', '不能给黑钢的各位丢脸！', 'char_240_wyvern/CN_020');
INSERT INTO `t_operator_voice` VALUES (259, 9, '选中干员1', '嗯？', 'char_240_wyvern/CN_021');
INSERT INTO `t_operator_voice` VALUES (260, 9, '选中干员2', '我会听从命令。', 'char_240_wyvern/CN_022');
INSERT INTO `t_operator_voice` VALUES (261, 9, '部署1', '好的。', 'char_240_wyvern/CN_023');
INSERT INTO `t_operator_voice` VALUES (262, 9, '部署2', '明白。', 'char_240_wyvern/CN_024');
INSERT INTO `t_operator_voice` VALUES (263, 9, '作战中1', '不会让你过去！', 'char_240_wyvern/CN_025');
INSERT INTO `t_operator_voice` VALUES (264, 9, '作战中2', '这把斧头可不是装饰品！', 'char_240_wyvern/CN_026');
INSERT INTO `t_operator_voice` VALUES (265, 9, '作战中3', '战斗可不仅仅是依靠经验！', 'char_240_wyvern/CN_027');
INSERT INTO `t_operator_voice` VALUES (266, 9, '作战中4', '接下我这一击吧！', 'char_240_wyvern/CN_028');
INSERT INTO `t_operator_voice` VALUES (267, 9, '4星结束行动', '呼，大圆满！', 'char_240_wyvern/CN_029');
INSERT INTO `t_operator_voice` VALUES (268, 9, '3星结束行动', '很成功的作战。', 'char_240_wyvern/CN_030');
INSERT INTO `t_operator_voice` VALUES (269, 9, '非3星结束行动', '如果再努力些的话，结果会更好点吧。', 'char_240_wyvern/CN_031');
INSERT INTO `t_operator_voice` VALUES (270, 9, '行动失败', '我......能力还不够......', 'char_240_wyvern/CN_032');
INSERT INTO `t_operator_voice` VALUES (271, 9, '进驻设施', '听从您的指挥。', 'char_240_wyvern/CN_033');
INSERT INTO `t_operator_voice` VALUES (272, 9, '戳一下', '唉唉？', 'char_240_wyvern/CN_034');
INSERT INTO `t_operator_voice` VALUES (273, 9, '信赖触摸', '博士，要一起吃晚饭吗？', 'char_240_wyvern/CN_036');
INSERT INTO `t_operator_voice` VALUES (274, 9, '标题', '明日方舟。', 'char_240_wyvern/CN_037');
INSERT INTO `t_operator_voice` VALUES (275, 9, '问候', '博士，早。', 'char_240_wyvern/CN_042');
INSERT INTO `t_operator_voice` VALUES (276, 10, '任命助理', '博士请放心，无论发生了什么，我都会保护你的。', 'char_192_falco/CN_001');
INSERT INTO `t_operator_voice` VALUES (277, 10, '交谈1', '不少黎博利人都有着卓越的视力，有些传说里，甚至有人能从倾盆大雨中辨别出雨滴的样子。听起来很厉害吧？', 'char_192_falco/CN_002');
INSERT INTO `t_operator_voice` VALUES (278, 10, '交谈2', '我在日常生活中表现得过于严肃了？抱歉，这可能是出于拉特兰守卫的一种职业习惯，我以后会多注意一点的。', 'char_192_falco/CN_003');
INSERT INTO `t_operator_voice` VALUES (279, 10, '交谈3', '对于大多数拉特兰人来说，过于厚重的铠甲并没有什么作用。当然了，在拉特兰举行比较盛大的仪式时，我们也会换上礼服的。', 'char_192_falco/CN_004');
INSERT INTO `t_operator_voice` VALUES (280, 10, '晋升后交谈1', '感谢你对我的信任，我一定不会辜负你的厚爱。', 'char_192_falco/CN_005');
INSERT INTO `t_operator_voice` VALUES (281, 10, '信赖提升后交谈1', '嗯......我已经向人事部递交了申请书。我想要成为博士你的贴身卫士。应该说，这也是种职业习惯吧......？', 'char_192_falco/CN_007');
INSERT INTO `t_operator_voice` VALUES (282, 10, '信赖提升后交谈2', '我从小成长在拉特兰，并没有什么机会接触其他的文明......罗德岛让我看到了许多我没法想象的生活，说不定，也不赖......', 'char_192_falco/CN_008');
INSERT INTO `t_operator_voice` VALUES (283, 10, '信赖提升后交谈3', '我不后悔了。嗯，是的，虽然当时她们强行把我从拉特兰拽了出来......但确实，这是一场有趣的冒险，我很喜欢。', 'char_192_falco/CN_009');
INSERT INTO `t_operator_voice` VALUES (284, 10, '闲置', '放心，博士，保护你是我的责任。', 'char_192_falco/CN_010');
INSERT INTO `t_operator_voice` VALUES (285, 10, '干员报到', '翎羽，原属拉特兰戍卫队，愿意成为你的戟、你的羽翼，为了你的生命而战。', 'char_192_falco/CN_011');
INSERT INTO `t_operator_voice` VALUES (286, 10, '观看作战记录', '唔，依靠战术也能补足实力上的差距，是吗。', 'char_192_falco/CN_012');
INSERT INTO `t_operator_voice` VALUES (287, 10, '精英化晋升1', '谢谢你，博士。作为卫士，你的嘉奖就是我最好的勋章。', 'char_192_falco/CN_013');
INSERT INTO `t_operator_voice` VALUES (288, 10, '编入队伍', '做好准备。', 'char_192_falco/CN_017');
INSERT INTO `t_operator_voice` VALUES (289, 10, '任命队长', '既然由我带领大家，那么，保证各位的安全就是我的义务。', 'char_192_falco/CN_018');
INSERT INTO `t_operator_voice` VALUES (290, 10, '行动出发', '保护好我方干员！', 'char_192_falco/CN_019');
INSERT INTO `t_operator_voice` VALUES (291, 10, '行动开始', '请大家提高警惕！', 'char_192_falco/CN_020');
INSERT INTO `t_operator_voice` VALUES (292, 10, '选中干员1', '嗯？', 'char_192_falco/CN_021');
INSERT INTO `t_operator_voice` VALUES (293, 10, '选中干员2', '知道了。', 'char_192_falco/CN_022');
INSERT INTO `t_operator_voice` VALUES (294, 10, '部署1', '守住这里。', 'char_192_falco/CN_023');
INSERT INTO `t_operator_voice` VALUES (295, 10, '部署2', '我明白。', 'char_192_falco/CN_024');
INSERT INTO `t_operator_voice` VALUES (296, 10, '作战中1', '你的速度，快得过我吗？', 'char_192_falco/CN_025');
INSERT INTO `t_operator_voice` VALUES (297, 10, '作战中2', '你的动作逃不过我的眼睛！', 'char_192_falco/CN_026');
INSERT INTO `t_operator_voice` VALUES (298, 10, '作战中3', '这把武器里，寄托了我的信念！', 'char_192_falco/CN_027');
INSERT INTO `t_operator_voice` VALUES (299, 10, '作战中4', '我的战斗，凝聚着拉特兰的荣耀！', 'char_192_falco/CN_028');
INSERT INTO `t_operator_voice` VALUES (300, 10, '4星结束行动', '没什么能让我退缩。', 'char_192_falco/CN_029');
INSERT INTO `t_operator_voice` VALUES (301, 10, '3星结束行动', '狂风吹打山岩，自己反而先粉身碎骨。', 'char_192_falco/CN_030');
INSERT INTO `t_operator_voice` VALUES (302, 10, '非3星结束行动', '重整队列！我会小心剩下的敌人！', 'char_192_falco/CN_031');
INSERT INTO `t_operator_voice` VALUES (303, 10, '行动失败', '还不可以......倒下！', 'char_192_falco/CN_032');
INSERT INTO `t_operator_voice` VALUES (304, 10, '进驻设施', '要我在这巡逻吗？', 'char_192_falco/CN_033');
INSERT INTO `t_operator_voice` VALUES (305, 10, '戳一下', '那里有什么吗？', 'char_192_falco/CN_034');
INSERT INTO `t_operator_voice` VALUES (306, 10, '信赖触摸', '博士......感觉我在擅用职权了呢......', 'char_192_falco/CN_036');
INSERT INTO `t_operator_voice` VALUES (307, 10, '标题', '明日方舟。', 'char_192_falco/CN_037');
INSERT INTO `t_operator_voice` VALUES (308, 10, '问候', '你好，博士。', 'char_192_falco/CN_042');
INSERT INTO `t_operator_voice` VALUES (309, 11, '任命助理', '您好，博士......请多关照。', 'char_208_melan/CN_001');
INSERT INTO `t_operator_voice` VALUES (310, 11, '交谈1', '那个......抱歉......我不太擅长，与人交谈......', 'char_208_melan/CN_002');
INSERT INTO `t_operator_voice` VALUES (311, 11, '交谈2', '是的......我的双亲是维多利亚的商人。但是我很长时间没能和他们联络上了......这个代号，能让我想起自己的家......', 'char_208_melan/CN_003');
INSERT INTO `t_operator_voice` VALUES (312, 11, '交谈3', '那、那位戴着面具的干员......让我感到......有些不安......', 'char_208_melan/CN_004');
INSERT INTO `t_operator_voice` VALUES (313, 11, '晋升后交谈1', '不，我并不是......不想接近大家，只是，我不知道......该怎么做才好......', 'char_208_melan/CN_005');
INSERT INTO `t_operator_voice` VALUES (314, 11, '信赖提升后交谈1', '对不起，安赛尔......我又让你担心了。不过，不用这样关照我，还有更多需要帮助的人在等着你呢。', 'char_208_melan/CN_007');
INSERT INTO `t_operator_voice` VALUES (315, 11, '信赖提升后交谈2', '矿石病，曾经让我拥有过强大的感知能力，而这份能力正在不断地消逝......但是，就算是这样，我也不会退缩的......我还想，和大家多说说话......', 'char_208_melan/CN_008');
INSERT INTO `t_operator_voice` VALUES (316, 11, '信赖提升后交谈3', '博士，您，能收下这瓶香水吗？这是我从家里带出来的......这种香水，有振奋精神的功效，在我没有精神的时候，我就会闻一闻它......', 'char_208_melan/CN_009');
INSERT INTO `t_operator_voice` VALUES (317, 11, '闲置', '梅莉？别太吵闹......博士已经很累了。', 'char_208_melan/CN_010');
INSERT INTO `t_operator_voice` VALUES (318, 11, '干员报到', '......玫兰莎。从现在起，我的利刃将为您所用。', 'char_208_melan/CN_011');
INSERT INTO `t_operator_voice` VALUES (319, 11, '观看作战记录', '谢谢您的指导......', 'char_208_melan/CN_012');
INSERT INTO `t_operator_voice` VALUES (320, 11, '精英化晋升1', '非常感谢您......那个，其他人也获得晋升了吗？我想，大家应该都和我一样，拥有这样的资格。', 'char_208_melan/CN_013');
INSERT INTO `t_operator_voice` VALUES (321, 11, '编入队伍', '嗯......我会遵照您的指示行动。', 'char_208_melan/CN_017');
INSERT INTO `t_operator_voice` VALUES (322, 11, '任命队长', '我......吗？我，不擅长指挥他人......', 'char_208_melan/CN_018');
INSERT INTO `t_operator_voice` VALUES (323, 11, '行动出发', '各位，为这个世界，带来更多和平吧。', 'char_208_melan/CN_019');
INSERT INTO `t_operator_voice` VALUES (324, 11, '行动开始', '安赛尔......接下来，就拜托你了。', 'char_208_melan/CN_020');
INSERT INTO `t_operator_voice` VALUES (325, 11, '选中干员1', '请指示......', 'char_208_melan/CN_021');
INSERT INTO `t_operator_voice` VALUES (326, 11, '选中干员2', '命令是......？', 'char_208_melan/CN_022');
INSERT INTO `t_operator_voice` VALUES (327, 11, '部署1', '了解了。', 'char_208_melan/CN_023');
INSERT INTO `t_operator_voice` VALUES (328, 11, '部署2', '尽我所能。', 'char_208_melan/CN_024');
INSERT INTO `t_operator_voice` VALUES (329, 11, '作战中1', '打倒你......！', 'char_208_melan/CN_025');
INSERT INTO `t_operator_voice` VALUES (330, 11, '作战中2', '斩。', 'char_208_melan/CN_026');
INSERT INTO `t_operator_voice` VALUES (331, 11, '4星结束行动', '无论什么样的困难，都无法阻止我前进。', 'char_208_melan/CN_029');
INSERT INTO `t_operator_voice` VALUES (332, 11, '3星结束行动', '赢了吗？太好了......', 'char_208_melan/CN_030');
INSERT INTO `t_operator_voice` VALUES (333, 11, '非3星结束行动', '我还能继续战斗。', 'char_208_melan/CN_031');
INSERT INTO `t_operator_voice` VALUES (334, 11, '行动失败', '失败了......竟然，会变成这样......', 'char_208_melan/CN_032');
INSERT INTO `t_operator_voice` VALUES (335, 11, '进驻设施', '这里......这里还有其他人在的吗......？', 'char_208_melan/CN_033');
INSERT INTO `t_operator_voice` VALUES (336, 11, '戳一下', '是、是谁——博士！', 'char_208_melan/CN_034');
INSERT INTO `t_operator_voice` VALUES (337, 11, '信赖触摸', '博士，感谢您一直这样关心我......那个，其实......我希望自己，今后也能更多地去战斗。', 'char_208_melan/CN_036');
INSERT INTO `t_operator_voice` VALUES (338, 11, '标题', '明日方舟。', 'char_208_melan/CN_037');
INSERT INTO `t_operator_voice` VALUES (339, 11, '问候', '博士。', 'char_208_melan/CN_042');
INSERT INTO `t_operator_voice` VALUES (340, 12, '任命助理', '耶！博士，现在轮到我陪您值班了！', 'char_209_ardign/CN_001');
INSERT INTO `t_operator_voice` VALUES (341, 12, '交谈1', '“卡缇”作为代号的含义是什么？啊哈~其实这是大家对我们种族的称呼，把它当做代号也是我家里的习惯啦。', 'char_209_ardign/CN_002');
INSERT INTO `t_operator_voice` VALUES (342, 12, '交谈2', '啊，另一个小组里也有和我同族的孩子耶~下次我去约她玩！', 'char_209_ardign/CN_003');
INSERT INTO `t_operator_voice` VALUES (343, 12, '交谈3', '我出生在莱塔尼亚的北方，那里到处都是雪和森林。对了，下次我来教您滑雪吧！', 'char_209_ardign/CN_004');
INSERT INTO `t_operator_voice` VALUES (344, 12, '晋升后交谈1', '大家经常抱着“重装干员的话，职责就是巩固好自己的防线”这样老旧的印象，这样的想法早就落伍啦！现在可是机动战的时代，速度才是最重要的！', 'char_209_ardign/CN_005');
INSERT INTO `t_operator_voice` VALUES (345, 12, '信赖提升后交谈1', '哼哼，这股香气，一定是玫兰莎！欸博士我和你说，我好想和她再亲近一点......那样的话，我们的团队协作一定会变得更加默契！', 'char_209_ardign/CN_007');
INSERT INTO `t_operator_voice` VALUES (346, 12, '信赖提升后交谈2', '嘿嘿，我超喜欢和孩子们一起玩的。在老家的时候，我每年都要拉着弟弟妹妹们坐着雪橇去旅行呢，好怀念啊~啊，说到雪橇——不知道安德切尔愿不愿意帮我做一个呢？', 'char_209_ardign/CN_008');
INSERT INTO `t_operator_voice` VALUES (347, 12, '信赖提升后交谈3', '我也有很多不喜欢的东西，比如说矿石病啊什么的......但是，反正为这些烦恼也没有用，还不如快快乐乐地度过每一天！而且，还有博士您在我身边呢！', 'char_209_ardign/CN_009');
INSERT INTO `t_operator_voice` VALUES (348, 12, '闲置', '汪！', 'char_209_ardign/CN_010');
INSERT INTO `t_operator_voice` VALUES (349, 12, '干员报到', '你——好！我第一次来到这么大的移动设施里面，现在超兴奋的！我是卡缇族的梅莉，您就是博士吗？', 'char_209_ardign/CN_011');
INSERT INTO `t_operator_voice` VALUES (350, 12, '观看作战记录', '欸嘿嘿，谢谢！', 'char_209_ardign/CN_012');
INSERT INTO `t_operator_voice` VALUES (351, 12, '精英化晋升1', '耶！我会努力争取下次晋升的！毕竟有一就有二嘛~', 'char_209_ardign/CN_013');
INSERT INTO `t_operator_voice` VALUES (352, 12, '编入队伍', '好耶！终于等到机会了！', 'char_209_ardign/CN_017');
INSERT INTO `t_operator_voice` VALUES (353, 12, '任命队长', '啊，安赛尔！哎呀，不会的不会的，我才不会带大家乱跑啦......', 'char_209_ardign/CN_018');
INSERT INTO `t_operator_voice` VALUES (354, 12, '行动出发', '排队出发！向目标战场，前进！', 'char_209_ardign/CN_019');
INSERT INTO `t_operator_voice` VALUES (355, 12, '行动开始', '走了！......史都华德，接下来我该怎么做来着？', 'char_209_ardign/CN_020');
INSERT INTO `t_operator_voice` VALUES (356, 12, '选中干员1', '在战场上，可不能犹豫！', 'char_209_ardign/CN_021');
INSERT INTO `t_operator_voice` VALUES (357, 12, '选中干员2', '好！', 'char_209_ardign/CN_022');
INSERT INTO `t_operator_voice` VALUES (358, 12, '部署1', '了解！', 'char_209_ardign/CN_023');
INSERT INTO `t_operator_voice` VALUES (359, 12, '部署2', '我会保护大家的！', 'char_209_ardign/CN_024');
INSERT INTO `t_operator_voice` VALUES (360, 12, '作战中1', '啊哈！', 'char_209_ardign/CN_025');
INSERT INTO `t_operator_voice` VALUES (361, 12, '作战中2', '你们的攻击毫无意义！', 'char_209_ardign/CN_026');
INSERT INTO `t_operator_voice` VALUES (362, 12, '4星结束行动', '我相信大家一定能做到，对吧安德切尔？', 'char_209_ardign/CN_029');
INSERT INTO `t_operator_voice` VALUES (363, 12, '3星结束行动', '耶——！大获全胜！', 'char_209_ardign/CN_030');
INSERT INTO `t_operator_voice` VALUES (364, 12, '非3星结束行动', '谢谢医疗组！你们都是我的救命恩人......', 'char_209_ardign/CN_031');
INSERT INTO `t_operator_voice` VALUES (365, 12, '行动失败', '呜......对不起......安赛尔......', 'char_209_ardign/CN_032');
INSERT INTO `t_operator_voice` VALUES (366, 12, '进驻设施', '史都华德，接下来我该做点什么好？', 'char_209_ardign/CN_033');
INSERT INTO `t_operator_voice` VALUES (367, 12, '戳一下', '哇！过山车——！', 'char_209_ardign/CN_034');
INSERT INTO `t_operator_voice` VALUES (368, 12, '信赖触摸', '玫兰莎？哎，怎么是博士......', 'char_209_ardign/CN_036');
INSERT INTO `t_operator_voice` VALUES (369, 12, '标题', '明日方舟。', 'char_209_ardign/CN_037');
INSERT INTO `t_operator_voice` VALUES (370, 12, '问候', '我在这里哦，博士！', 'char_209_ardign/CN_042');
INSERT INTO `t_operator_voice` VALUES (371, 13, '任命助理', '博士，今天我来担当您的护卫！放心，不会让可疑的人接近您的！', 'char_122_beagle/CN_001');
INSERT INTO `t_operator_voice` VALUES (372, 13, '交谈1', '我今天的训练成绩单......吗？呃，那个，您还是别看啦......', 'char_122_beagle/CN_002');
INSERT INTO `t_operator_voice` VALUES (373, 13, '交谈2', '每次战斗前我总是紧张得不得了，害得芬队长也要为我捏一把汗......我该怎么办好呢？博士><！', 'char_122_beagle/CN_003');
INSERT INTO `t_operator_voice` VALUES (374, 13, '交谈3', '芙蓉和炎熔总是来找我抱怨对方，可她们都是我的好朋友，各自的说法也都很有道理......博士，我该怎么办才好啊......？', 'char_122_beagle/CN_004');
INSERT INTO `t_operator_voice` VALUES (375, 13, '晋升后交谈1', '杜宾老师总是教育我们——“就算新人期结束，实战中也要以保命为优先“——这样的话。呜~不甘心，什么时候才能变成她那样帅气又强大的人呢？', 'char_122_beagle/CN_005');
INSERT INTO `t_operator_voice` VALUES (376, 13, '信赖提升后交谈1', '作为干员我还是新人，不过我也正在努力不是吗？嘿嘿，如果您也觉得我有进步，希望您能多多夸奖我一下......', 'char_122_beagle/CN_007');
INSERT INTO `t_operator_voice` VALUES (377, 13, '信赖提升后交谈2', '矿石病吗？已经不是很在意了，因为感觉和以前也没有多大的区别嘛~不过果然还是，希望和大家永远在一起，在病痊愈之前，一直在一起......呜......', 'char_122_beagle/CN_008');
INSERT INTO `t_operator_voice` VALUES (378, 13, '信赖提升后交谈3', '其实来这里之前，我对自己是没什么自信的......但是现在我能做到保护博士和大家了，真的很高兴！能保护所有人是我的梦想！所以，我也希望，如果这样的日子能一直持续下去就好了。', 'char_122_beagle/CN_009');
INSERT INTO `t_operator_voice` VALUES (379, 13, '闲置', '......好想在睡着的博士脸上恶作剧啊。', 'char_122_beagle/CN_010');
INSERT INTO `t_operator_voice` VALUES (380, 13, '干员报到', '我是米格鲁！虽然，招募评定的成绩不太理想，但就算作为实习生我也会努力帮忙的！', 'char_122_beagle/CN_011');
INSERT INTO `t_operator_voice` VALUES (381, 13, '观看作战记录', '做到了！我变得更强啦！', 'char_122_beagle/CN_012');
INSERT INTO `t_operator_voice` VALUES (382, 13, '精英化晋升1', '这样、这样的话！就不会被人喊“宇宙级新人！”啦！', 'char_122_beagle/CN_013');
INSERT INTO `t_operator_voice` VALUES (383, 13, '编入队伍', '我会加油的！', 'char_122_beagle/CN_017');
INSERT INTO `t_operator_voice` VALUES (384, 13, '任命队长', '欸，我是队长？不是芬做队长真的好吗？', 'char_122_beagle/CN_018');
INSERT INTO `t_operator_voice` VALUES (385, 13, '行动出发', '各位，出发了！', 'char_122_beagle/CN_019');
INSERT INTO `t_operator_voice` VALUES (386, 13, '行动开始', '各位，敌人来了哟！', 'char_122_beagle/CN_020');
INSERT INTO `t_operator_voice` VALUES (387, 13, '选中干员1', '请给我勇气！', 'char_122_beagle/CN_021');
INSERT INTO `t_operator_voice` VALUES (388, 13, '选中干员2', '要我上吗？', 'char_122_beagle/CN_022');
INSERT INTO `t_operator_voice` VALUES (389, 13, '部署1', '抵达目标地点了！', 'char_122_beagle/CN_023');
INSERT INTO `t_operator_voice` VALUES (390, 13, '部署2', '就是这里！', 'char_122_beagle/CN_024');
INSERT INTO `t_operator_voice` VALUES (391, 13, '作战中1', '哈~！', 'char_122_beagle/CN_025');
INSERT INTO `t_operator_voice` VALUES (392, 13, '作战中2', '看我~的！', 'char_122_beagle/CN_026');
INSERT INTO `t_operator_voice` VALUES (393, 13, '4星结束行动', '保护大家是我的使命！不管有什么困难，这个信念都不会动摇！', 'char_122_beagle/CN_029');
INSERT INTO `t_operator_voice` VALUES (394, 13, '3星结束行动', '敌人一个不漏的全都击溃了！罗德岛的各位是最棒的！', 'char_122_beagle/CN_030');
INSERT INTO `t_operator_voice` VALUES (395, 13, '非3星结束行动', '赢了赢了！不过，还是稍稍放走了几个敌人，欸嘿嘿......', 'char_122_beagle/CN_031');
INSERT INTO `t_operator_voice` VALUES (396, 13, '行动失败', '对不起......对不起......', 'char_122_beagle/CN_032');
INSERT INTO `t_operator_voice` VALUES (397, 13, '进驻设施', '我能帮你什么忙吗？', 'char_122_beagle/CN_033');
INSERT INTO `t_operator_voice` VALUES (398, 13, '戳一下', '博士！很、很痒的！', 'char_122_beagle/CN_034');
INSERT INTO `t_operator_voice` VALUES (399, 13, '信赖触摸', '博士，我可以每天都去您那里学习吗？', 'char_122_beagle/CN_036');
INSERT INTO `t_operator_voice` VALUES (400, 13, '标题', '明日方舟。', 'char_122_beagle/CN_037');
INSERT INTO `t_operator_voice` VALUES (401, 13, '问候', '你好呀，博士。', 'char_122_beagle/CN_042');
INSERT INTO `t_operator_voice` VALUES (402, 14, '任命助理', '博士？这么好的天气，再稍微睡一会儿好吗~', 'char_124_kroos/CN_001');
INSERT INTO `t_operator_voice` VALUES (403, 14, '交谈1', '嗯？马上要出发？我刚刚起床......顺便问一下，这里是博士的办公室吗？为什么我会在这里呢？', 'char_124_kroos/CN_002');
INSERT INTO `t_operator_voice` VALUES (404, 14, '交谈2', '为什么我要眯起眼睛？因为不想让人看到我的眼睛，谁都不让~♪', 'char_124_kroos/CN_003');
INSERT INTO `t_operator_voice` VALUES (405, 14, '交谈3', '我总是不小心受伤呢~不过芙蓉会治好我的，所以我才不怕~啊，不过“治愈料理”是另一回事，那个真是可怕的体验啊......', 'char_124_kroos/CN_004');
INSERT INTO `t_operator_voice` VALUES (406, 14, '晋升后交谈1', '博士~！我床头多了十个闹钟~！真是过分的恶作剧~到底是谁放的呢？', 'char_124_kroos/CN_005');
INSERT INTO `t_operator_voice` VALUES (407, 14, '信赖提升后交谈1', '博士，来玩个游戏吧？头上~放一个红苹果~', 'char_124_kroos/CN_007');
INSERT INTO `t_operator_voice` VALUES (408, 14, '信赖提升后交谈2', '我总是在找寻让人开心的事情~也是为了缓和矿石病的痛苦吧。能看到大家开开心心的模样，也是一件快乐的事呀~', 'char_124_kroos/CN_008');
INSERT INTO `t_operator_voice` VALUES (409, 14, '信赖提升后交谈3', '总有一天，罗德岛的旅途会结束的。要是能和大家一起，在温暖的天气里香甜地睡一觉，做一场醒不来的梦，该有多好啊~这是我最大的梦想哦~', 'char_124_kroos/CN_009');
INSERT INTO `t_operator_voice` VALUES (410, 14, '闲置', '博士竟然睡得比我还安稳呢......', 'char_124_kroos/CN_010');
INSERT INTO `t_operator_voice` VALUES (411, 14, '干员报到', '嗨~嗨~我是克洛丝哦~我还不成熟，从今天开始会加油的~', 'char_124_kroos/CN_011');
INSERT INTO `t_operator_voice` VALUES (412, 14, '观看作战记录', '谢谢啦~', 'char_124_kroos/CN_012');
INSERT INTO `t_operator_voice` VALUES (413, 14, '精英化晋升1', '晋升啦~做了个好梦呢~嗯？不是在梦中吗？', 'char_124_kroos/CN_013');
INSERT INTO `t_operator_voice` VALUES (414, 14, '编入队伍', '那个，集合地点在哪呢？', 'char_124_kroos/CN_017');
INSERT INTO `t_operator_voice` VALUES (415, 14, '任命队长', '欸~队长的话还是芬更好吧~', 'char_124_kroos/CN_018');
INSERT INTO `t_operator_voice` VALUES (416, 14, '行动出发', '大家~嗯，可以放松一下哦~', 'char_124_kroos/CN_019');
INSERT INTO `t_operator_voice` VALUES (417, 14, '行动开始', '为什么，大家不能好好相处呢......？', 'char_124_kroos/CN_020');
INSERT INTO `t_operator_voice` VALUES (418, 14, '选中干员1', '好~', 'char_124_kroos/CN_021');
INSERT INTO `t_operator_voice` VALUES (419, 14, '选中干员2', '慢慢来就好哦~', 'char_124_kroos/CN_022');
INSERT INTO `t_operator_voice` VALUES (420, 14, '部署1', '嗯嗯，听着呢~', 'char_124_kroos/CN_023');
INSERT INTO `t_operator_voice` VALUES (421, 14, '部署2', '战斗的时候可是不会睡着的~', 'char_124_kroos/CN_024');
INSERT INTO `t_operator_voice` VALUES (422, 14, '作战中1', '你在这里~', 'char_124_kroos/CN_025');
INSERT INTO `t_operator_voice` VALUES (423, 14, '作战中2', '在~这~里~哦', 'char_124_kroos/CN_026');
INSERT INTO `t_operator_voice` VALUES (424, 14, '作战中3', '瞄准~！', 'char_124_kroos/CN_027');
INSERT INTO `t_operator_voice` VALUES (425, 14, '4星结束行动', '呼~好辛苦啊......', 'char_124_kroos/CN_029');
INSERT INTO `t_operator_voice` VALUES (426, 14, '3星结束行动', '我觉得还可以哦！', 'char_124_kroos/CN_030');
INSERT INTO `t_operator_voice` VALUES (427, 14, '非3星结束行动', '嗯，稍微有点遗憾......', 'char_124_kroos/CN_031');
INSERT INTO `t_operator_voice` VALUES (428, 14, '行动失败', '对、对不起......下次我会更加努力的......！', 'char_124_kroos/CN_032');
INSERT INTO `t_operator_voice` VALUES (429, 14, '进驻设施', '在这里能睡个好觉吗？', 'char_124_kroos/CN_033');
INSERT INTO `t_operator_voice` VALUES (430, 14, '戳一下', '呼......', 'char_124_kroos/CN_034');
INSERT INTO `t_operator_voice` VALUES (431, 14, '信赖触摸', '嗯？我成长了吗~', 'char_124_kroos/CN_036');
INSERT INTO `t_operator_voice` VALUES (432, 14, '标题', '明日方舟。', 'char_124_kroos/CN_037');
INSERT INTO `t_operator_voice` VALUES (433, 14, '问候', '博士~啊呼~', 'char_124_kroos/CN_042');
INSERT INTO `t_operator_voice` VALUES (434, 15, '任命助理', '你手上的设备好像运行得不太稳定，可以让我看一下吗？', 'char_211_adnach/CN_001');
INSERT INTO `t_operator_voice` VALUES (435, 15, '交谈1', '呀，安赛尔，卡缇看上去挺精神的呢。需要我帮你做什么吗？', 'char_211_adnach/CN_002');
INSERT INTO `t_operator_voice` VALUES (436, 15, '交谈2', '我们队里的每个人都很有趣，特别是玫兰莎。虽然她好像总是躲着我，不过我总能感受到她散发的香味。', 'char_211_adnach/CN_003');
INSERT INTO `t_operator_voice` VALUES (437, 15, '交谈3', '卡缇，你在干什么？看上去很开心的样子。', 'char_211_adnach/CN_004');
INSERT INTO `t_operator_voice` VALUES (438, 15, '晋升后交谈1', '并不是每个拉特兰人都有使用铳的权力，因为铳对我们来说是不可多得的武器。在我的故乡，仿制铳部件的技术倒是很发达，制造弩的技术也在同步发展。这把装配了战术鱼骨的弩，就是这种技术的成果。', 'char_211_adnach/CN_005');
INSERT INTO `t_operator_voice` VALUES (439, 15, '信赖提升后交谈1', '您要尝尝我的手制甜点吗？说来有点不好意思，我只能做25种甜品，真是一个不合格的拉特兰人。', 'char_211_adnach/CN_007');
INSERT INTO `t_operator_voice` VALUES (440, 15, '信赖提升后交谈2', '这枚天使的轮环？我也不知道为什么，它的位置会偏移。凯尔希医生说，这似乎和大脑海马体异常有关。', 'char_211_adnach/CN_008');
INSERT INTO `t_operator_voice` VALUES (441, 15, '信赖提升后交谈3', '与其站在远处眺望着博士，不如和博士您站在一起，这样我会更加开心。', 'char_211_adnach/CN_009');
INSERT INTO `t_operator_voice` VALUES (442, 15, '闲置', '博士，休息时的容貌，也这样令人印象深刻。', 'char_211_adnach/CN_010');
INSERT INTO `t_operator_voice` VALUES (443, 15, '干员报到', '我是拉特兰出身，担任狙击干员的安德切尔。虽然我还没有得到铳的使用权，不过说到弩的灵活运用，我不会输给前辈们的！', 'char_211_adnach/CN_011');
INSERT INTO `t_operator_voice` VALUES (444, 15, '观看作战记录', '能被您这样重用，我很满足了。', 'char_211_adnach/CN_012');
INSERT INTO `t_operator_voice` VALUES (445, 15, '精英化晋升1', '非常感谢您的提拔。', 'char_211_adnach/CN_013');
INSERT INTO `t_operator_voice` VALUES (446, 15, '编入队伍', '没问题的。', 'char_211_adnach/CN_017');
INSERT INTO `t_operator_voice` VALUES (447, 15, '任命队长', '如果有东西坏掉了，可以让我看一下，也许能帮你修好的。', 'char_211_adnach/CN_018');
INSERT INTO `t_operator_voice` VALUES (448, 15, '行动出发', '出发时间到了，准备好了吗？', 'char_211_adnach/CN_019');
INSERT INTO `t_operator_voice` VALUES (449, 15, '行动开始', '各位，开始狩猎吧。', 'char_211_adnach/CN_020');
INSERT INTO `t_operator_voice` VALUES (450, 15, '选中干员1', '遵从您的指示。', 'char_211_adnach/CN_021');
INSERT INTO `t_operator_voice` VALUES (451, 15, '选中干员2', '嗯？', 'char_211_adnach/CN_022');
INSERT INTO `t_operator_voice` VALUES (452, 15, '部署1', '早点逃走比较好哦。', 'char_211_adnach/CN_023');
INSERT INTO `t_operator_voice` VALUES (453, 15, '部署2', '好的。', 'char_211_adnach/CN_024');
INSERT INTO `t_operator_voice` VALUES (454, 15, '作战中1', '这个！', 'char_211_adnach/CN_025');
INSERT INTO `t_operator_voice` VALUES (455, 15, '作战中2', '锁定！', 'char_211_adnach/CN_026');
INSERT INTO `t_operator_voice` VALUES (456, 15, '4星结束行动', '一切都在博士的掌控中哦。', 'char_211_adnach/CN_029');
INSERT INTO `t_operator_voice` VALUES (457, 15, '3星结束行动', '多亏有博士的指挥。', 'char_211_adnach/CN_030');
INSERT INTO `t_operator_voice` VALUES (458, 15, '非3星结束行动', '虽然作战成功了，但好像还有什么地方不太对劲......？', 'char_211_adnach/CN_031');
INSERT INTO `t_operator_voice` VALUES (459, 15, '行动失败', '非常，非常抱歉......', 'char_211_adnach/CN_032');
INSERT INTO `t_operator_voice` VALUES (460, 15, '进驻设施', '这里的气氛真不错。', 'char_211_adnach/CN_033');
INSERT INTO `t_operator_voice` VALUES (461, 15, '戳一下', '哎呀？', 'char_211_adnach/CN_034');
INSERT INTO `t_operator_voice` VALUES (462, 15, '信赖触摸', '博士？能和您在一起，我也很开心。', 'char_211_adnach/CN_036');
INSERT INTO `t_operator_voice` VALUES (463, 15, '标题', '明日方舟。', 'char_211_adnach/CN_037');
INSERT INTO `t_operator_voice` VALUES (464, 15, '问候', '愿上天祝福您，博士。', 'char_211_adnach/CN_042');
INSERT INTO `t_operator_voice` VALUES (465, 16, '任命助理', '有事？要没什么让我做的，这样，我给你做下占卜吧。', 'char_121_lava/CN_001');
INSERT INTO `t_operator_voice` VALUES (466, 16, '交谈1', '有什么工作就直接让我去做，总之别让我和那家伙待一起，我受不了。尤其那家伙还逼我吃奇怪的食物......想一想，就......呃呃......', 'char_121_lava/CN_002');
INSERT INTO `t_operator_voice` VALUES (467, 16, '交谈2', '我来这之前，可没人告诉我芙蓉也在罗德岛.....事到如今再想下船，已经晚了啊......', 'char_121_lava/CN_003');
INSERT INTO `t_operator_voice` VALUES (468, 16, '交谈3', '在战场上该怎么打，怎么嘲讽对手，怎么摧毁对手，在温室里长大的家伙根本就不懂，你说对吧，博士。', 'char_121_lava/CN_004');
INSERT INTO `t_operator_voice` VALUES (469, 16, '晋升后交谈1', '有句话你听说过吧，“妹妹就是正义”，也就是说，我的姐姐自然就是邪恶，至少，在我的观念里就是这样。', 'char_121_lava/CN_005');
INSERT INTO `t_operator_voice` VALUES (470, 16, '信赖提升后交谈1', '乐器？钢琴的话，我稍微会一点。我身在莱塔尼亚的老师曾经说过，学习音乐能提高对源石技艺的掌握能力，还能缓解矿石病对精神的影响......', 'char_121_lava/CN_007');
INSERT INTO `t_operator_voice` VALUES (471, 16, '信赖提升后交谈2', '肚子饿了......嗯？这个是你偷藏的糖？嗯嗯......我还在训练中呢，能吃这种甜食嘛？', 'char_121_lava/CN_008');
INSERT INTO `t_operator_voice` VALUES (472, 16, '信赖提升后交谈3', '其实，芙蓉那家伙也不是那么讨厌啦......过去的事情就过去了，现在她别整天逼我吃药剂和食物就行。毕竟救过我命的......也是那家伙啊......', 'char_121_lava/CN_009');
INSERT INTO `t_operator_voice` VALUES (473, 16, '闲置', '睡这么香，有点羡慕啊。', 'char_121_lava/CN_010');
INSERT INTO `t_operator_voice` VALUES (474, 16, '干员报到', '炎熔、术师。因为无聊想找点事做，所以就随便加入你们这了......但听说我的天敌也在这里，所以我能现在就辞职吗？', 'char_121_lava/CN_011');
INSERT INTO `t_operator_voice` VALUES (475, 16, '观看作战记录', '谢咯。', 'char_121_lava/CN_012');
INSERT INTO `t_operator_voice` VALUES (476, 16, '精英化晋升1', '我说过吧？我不为任何组织做事，不需要团队......但是......算了，谢谢。', 'char_121_lava/CN_013');
INSERT INTO `t_operator_voice` VALUES (477, 16, '编入队伍', '团队合作什么的......其他人最好别拖后腿啊。', 'char_121_lava/CN_017');
INSERT INTO `t_operator_voice` VALUES (478, 16, '任命队长', '我不太擅长这种工作，但无论如何也要做的话......', 'char_121_lava/CN_018');
INSERT INTO `t_operator_voice` VALUES (479, 16, '行动出发', '现在就出发，掩护就拜托了。', 'char_121_lava/CN_019');
INSERT INTO `t_operator_voice` VALUES (480, 16, '行动开始', '那些家伙的灵魂，注定要被我收割。', 'char_121_lava/CN_020');
INSERT INTO `t_operator_voice` VALUES (481, 16, '选中干员1', '你在等什么？', 'char_121_lava/CN_021');
INSERT INTO `t_operator_voice` VALUES (482, 16, '选中干员2', '快点。', 'char_121_lava/CN_022');
INSERT INTO `t_operator_voice` VALUES (483, 16, '部署1', '来吧。', 'char_121_lava/CN_023');
INSERT INTO `t_operator_voice` VALUES (484, 16, '部署2', '就这样。', 'char_121_lava/CN_024');
INSERT INTO `t_operator_voice` VALUES (485, 16, '作战中1', '游戏结束了。', 'char_121_lava/CN_025');
INSERT INTO `t_operator_voice` VALUES (486, 16, '作战中2', '亡灵也救不了你。', 'char_121_lava/CN_026');
INSERT INTO `t_operator_voice` VALUES (487, 16, '4星结束行动', '法术的最高境界，有点好奇啊。', 'char_121_lava/CN_029');
INSERT INTO `t_operator_voice` VALUES (488, 16, '3星结束行动', '占卜的结果我早就知道了，我们大获全胜。', 'char_121_lava/CN_030');
INSERT INTO `t_operator_voice` VALUES (489, 16, '非3星结束行动', '呃，占卜的结果有时候也不那么准确，如果过程中混入了杂音，得出的结果往往不怎么清晰。', 'char_121_lava/CN_031');
INSERT INTO `t_operator_voice` VALUES (490, 16, '行动失败', '可恶......邪念怎么那么强......', 'char_121_lava/CN_032');
INSERT INTO `t_operator_voice` VALUES (491, 16, '进驻设施', '好了够了，我喜欢一个人待着。', 'char_121_lava/CN_033');
INSERT INTO `t_operator_voice` VALUES (492, 16, '戳一下', '别碰我！哇啊啊！', 'char_121_lava/CN_034');
INSERT INTO `t_operator_voice` VALUES (493, 16, '信赖触摸', '别、别以为这么做就能赚取我的好感啊！', 'char_121_lava/CN_036');
INSERT INTO `t_operator_voice` VALUES (494, 16, '标题', '明日方舟。', 'char_121_lava/CN_037');
INSERT INTO `t_operator_voice` VALUES (495, 16, '问候', '是博士啊。早。', 'char_121_lava/CN_042');
INSERT INTO `t_operator_voice` VALUES (496, 17, '任命助理', '您叫我吗，博士！我很高兴能和博士待在一起！', 'char_120_hibisc/CN_001');
INSERT INTO `t_operator_voice` VALUES (497, 17, '交谈1', '博士！凯尔希医生说博士应该更注意健康哦。简单说的话，就是要·减·少·进·食！这样！', 'char_120_hibisc/CN_002');
INSERT INTO `t_operator_voice` VALUES (498, 17, '交谈2', '那个，博士，如果我妹妹还是那样态度蛮横，请一定要告诉我哦！那个孩子缺少了我的治疗是绝对不行的！', 'char_120_hibisc/CN_003');
INSERT INTO `t_operator_voice` VALUES (499, 17, '交谈3', '凯尔希老师教我的知识都很有用，虽然也很难学。不过，因为营养学本来就是我最擅长的，所以我觉得自己做的很好哦。', 'char_120_hibisc/CN_004');
INSERT INTO `t_operator_voice` VALUES (500, 17, '晋升后交谈1', '比起刚加入罗德岛时，现在我对大家更放心了！大家也会按时吃掉我的健康食品。只是，为什么吃完之后，大家的表情都变得那么奇怪呢？', 'char_120_hibisc/CN_005');
INSERT INTO `t_operator_voice` VALUES (501, 17, '信赖提升后交谈1', '锵——博士特供健康餐！清咖啡、不加肉类、芝士、酸黄瓜的生菜汉堡！还有，还有用十二种蔬菜混合制成的沙拉哦！', 'char_120_hibisc/CN_007');
INSERT INTO `t_operator_voice` VALUES (502, 17, '信赖提升后交谈2', '那个，博士，您的家人在什么地方呢？他们还好吗？有些事，正因为是亲人才难以沟通，但我觉得也不能因为很难就拒绝沟通，对吧？', 'char_120_hibisc/CN_008');
INSERT INTO `t_operator_voice` VALUES (503, 17, '信赖提升后交谈3', '如果一个人吃饭太寂寞的话，就让我来陪您一起吃吧！对啦，还可以叫上炎熔！', 'char_120_hibisc/CN_009');
INSERT INTO `t_operator_voice` VALUES (504, 17, '闲置', '博士，您睡着了吗？那就请好好休息吧，充分的睡眠对健康很有好处。', 'char_120_hibisc/CN_010');
INSERT INTO `t_operator_voice` VALUES (505, 17, '干员报到', '实习医生芙蓉，今天开始，我会负责您和罗德岛各位干员的健康管理项目，请多多关照！', 'char_120_hibisc/CN_011');
INSERT INTO `t_operator_voice` VALUES (506, 17, '观看作战记录', '原来如此原来如此！是这样啊！', 'char_120_hibisc/CN_012');
INSERT INTO `t_operator_voice` VALUES (507, 17, '精英化晋升1', '很荣幸能获得晋升！博士，今后我也会为大家努力进行健康管理，请随时监督我！', 'char_120_hibisc/CN_013');
INSERT INTO `t_operator_voice` VALUES (508, 17, '编入队伍', '不会让您失望的！就让我来保护大家！', 'char_120_hibisc/CN_017');
INSERT INTO `t_operator_voice` VALUES (509, 17, '任命队长', '我知道了！大家的生命就由我来守护！首先，应该准备好特制缓释秘药......', 'char_120_hibisc/CN_018');
INSERT INTO `t_operator_voice` VALUES (510, 17, '行动出发', '我不在的时候，博士一定要注意自己的安全！', 'char_120_hibisc/CN_019');
INSERT INTO `t_operator_voice` VALUES (511, 17, '行动开始', '准备治疗！', 'char_120_hibisc/CN_020');
INSERT INTO `t_operator_voice` VALUES (512, 17, '选中干员1', '别怕！', 'char_120_hibisc/CN_021');
INSERT INTO `t_operator_voice` VALUES (513, 17, '选中干员2', '博士？', 'char_120_hibisc/CN_022');
INSERT INTO `t_operator_voice` VALUES (514, 17, '部署1', '嘿咻。', 'char_120_hibisc/CN_023');
INSERT INTO `t_operator_voice` VALUES (515, 17, '部署2', '这里很安全！', 'char_120_hibisc/CN_024');
INSERT INTO `t_operator_voice` VALUES (516, 17, '作战中1', '请睁开眼睛！', 'char_120_hibisc/CN_025');
INSERT INTO `t_operator_voice` VALUES (517, 17, '作战中2', '我能让你的伤口快速愈合！', 'char_120_hibisc/CN_026');
INSERT INTO `t_operator_voice` VALUES (518, 17, '4星结束行动', '无论有多少伤员，我都能用这把杖治好大家！', 'char_120_hibisc/CN_029');
INSERT INTO `t_operator_voice` VALUES (519, 17, '3星结束行动', '每个人都平平安安的，就是对我最大的鼓励啦。', 'char_120_hibisc/CN_030');
INSERT INTO `t_operator_voice` VALUES (520, 17, '非3星结束行动', '博士！大家！没有受伤吧？', 'char_120_hibisc/CN_031');
INSERT INTO `t_operator_voice` VALUES (521, 17, '行动失败', '不对......需要急救！', 'char_120_hibisc/CN_032');
INSERT INTO `t_operator_voice` VALUES (522, 17, '进驻设施', '有什么需要帮忙的吗？', 'char_120_hibisc/CN_033');
INSERT INTO `t_operator_voice` VALUES (523, 17, '戳一下', '啊呀~！', 'char_120_hibisc/CN_034');
INSERT INTO `t_operator_voice` VALUES (524, 17, '信赖触摸', '博士，要试一下新的功能饮品吗？', 'char_120_hibisc/CN_036');
INSERT INTO `t_operator_voice` VALUES (525, 17, '标题', '明日方舟。', 'char_120_hibisc/CN_037');
INSERT INTO `t_operator_voice` VALUES (526, 17, '问候', '博士，要不要尝尝这个？', 'char_120_hibisc/CN_042');
INSERT INTO `t_operator_voice` VALUES (527, 18, '任命助理', '博士，打针时间到了哦。呃，您、您没事吧！难道您晕针吗？', 'char_212_ansel/CN_001');
INSERT INTO `t_operator_voice` VALUES (528, 18, '交谈1', '就像凯尔希老师说的那样，博士您不锻炼一下是不行的。虽然我还是新人，但也请博士好好听我的话哟。', 'char_212_ansel/CN_002');
INSERT INTO `t_operator_voice` VALUES (529, 18, '交谈2', '卡缇！下次别在用那种攻击动作了，都说了这样会让你自己受伤的！', 'char_212_ansel/CN_003');
INSERT INTO `t_operator_voice` VALUES (530, 18, '交谈3', '真是的......博士，自己的身体，只有自己才能保护得好哦。', 'char_212_ansel/CN_004');
INSERT INTO `t_operator_voice` VALUES (531, 18, '晋升后交谈1', '您问我连续上夜班，身体吃得消吗？嗯，没问题的，我所属的种族，体内激素分泌的方式比较特别，比较利于我们在夜间活动。这样正好能和芙蓉小姐换班哟。', 'char_212_ansel/CN_005');
INSERT INTO `t_operator_voice` VALUES (532, 18, '信赖提升后交谈1', '请一定要戒掉夜宵哦。也不要把我也吃夜宵当成借口。我在深夜吃东西是种族特性的缘故！对，就是种族特性！', 'char_212_ansel/CN_007');
INSERT INTO `t_operator_voice` VALUES (533, 18, '信赖提升后交谈2', '我是安赛尔。虽然有点不好意思，但我选择医学之路，是因为从小时候起，我的身体就很虚弱。但既然现在身为干员，就不能给雇主添麻烦。所以请像要求其他人那样对待我。', 'char_212_ansel/CN_008');
INSERT INTO `t_operator_voice` VALUES (534, 18, '信赖提升后交谈3', '其实大家真的很努力了。不过听凯尔希老师说，史都华德和玫兰莎的状况都不太好......我的工作就是尽可能让他们没有后顾之忧。博士，一起加油吧。', 'char_212_ansel/CN_009');
INSERT INTO `t_operator_voice` VALUES (535, 18, '闲置', '安德切尔，能让卡缇稍微安静一下吗？博士休息了。', 'char_212_ansel/CN_010');
INSERT INTO `t_operator_voice` VALUES (536, 18, '干员报到', '我是实习医生安赛尔，今后将承担一些外科医疗方面的辅助工作，还请您多多指教。', 'char_212_ansel/CN_011');
INSERT INTO `t_operator_voice` VALUES (537, 18, '观看作战记录', '我没给您添麻烦就好。', 'char_212_ansel/CN_012');
INSERT INTO `t_operator_voice` VALUES (538, 18, '精英化晋升1', '您这样重视我们医疗干员的处境，我很高兴。', 'char_212_ansel/CN_013');
INSERT INTO `t_operator_voice` VALUES (539, 18, '编入队伍', '我随时都可以哦。', 'char_212_ansel/CN_017');
INSERT INTO `t_operator_voice` VALUES (540, 18, '任命队长', '医疗支援是战术小组应最优先考虑的事项。', 'char_212_ansel/CN_018');
INSERT INTO `t_operator_voice` VALUES (541, 18, '行动出发', '好，战斗中用的药品都准备好了。', 'char_212_ansel/CN_019');
INSERT INTO `t_operator_voice` VALUES (542, 18, '行动开始', '注射针剂已经备好了。请做好开战前的准备。', 'char_212_ansel/CN_020');
INSERT INTO `t_operator_voice` VALUES (543, 18, '选中干员1', '急救箱已经准备好了。', 'char_212_ansel/CN_021');
INSERT INTO `t_operator_voice` VALUES (544, 18, '选中干员2', '我在等待您的指示。', 'char_212_ansel/CN_022');
INSERT INTO `t_operator_voice` VALUES (545, 18, '部署1', '准备治疗！', 'char_212_ansel/CN_023');
INSERT INTO `t_operator_voice` VALUES (546, 18, '部署2', '请坚持住！', 'char_212_ansel/CN_024');
INSERT INTO `t_operator_voice` VALUES (547, 18, '作战中1', '清除！', 'char_212_ansel/CN_025');
INSERT INTO `t_operator_voice` VALUES (548, 18, '作战中2', '这是给你的药，请拿好！', 'char_212_ansel/CN_026');
INSERT INTO `t_operator_voice` VALUES (549, 18, '4星结束行动', '呼——还好药品带够了。各位，请安心。', 'char_212_ansel/CN_029');
INSERT INTO `t_operator_voice` VALUES (550, 18, '3星结束行动', '大家活跃的姿态，真的让我刮目相看，太棒了。', 'char_212_ansel/CN_030');
INSERT INTO `t_operator_voice` VALUES (551, 18, '非3星结束行动', '受伤的人请稍等，我马上就去为你治疗。', 'char_212_ansel/CN_031');
INSERT INTO `t_operator_voice` VALUES (552, 18, '行动失败', '有人受伤了！我要把伤员搬回后方，请掩护我！', 'char_212_ansel/CN_032');
INSERT INTO `t_operator_voice` VALUES (553, 18, '进驻设施', '我来检查一下这里的医药品储备情况。', 'char_212_ansel/CN_033');
INSERT INTO `t_operator_voice` VALUES (554, 18, '戳一下', '哇，博士，请别捣乱啦！', 'char_212_ansel/CN_034');
INSERT INTO `t_operator_voice` VALUES (555, 18, '信赖触摸', '博士，您能喜欢我，我真的很高兴。', 'char_212_ansel/CN_036');
INSERT INTO `t_operator_voice` VALUES (556, 18, '标题', '明日方舟。', 'char_212_ansel/CN_037');
INSERT INTO `t_operator_voice` VALUES (557, 18, '问候', '您好，博士。', 'char_212_ansel/CN_042');
INSERT INTO `t_operator_voice` VALUES (558, 19, '任命助理', '欢迎回来，博士，史都华德为您效力。', 'char_210_stward/CN_001');
INSERT INTO `t_operator_voice` VALUES (559, 19, '交谈1', '史都华德是我的姓氏，也是我作为干员的代号。如果是博士您的话，应该可以从这个姓氏中，解读出我的先祖所从事的职业。', 'char_210_stward/CN_002');
INSERT INTO `t_operator_voice` VALUES (560, 19, '交谈2', '没想到能在这里遇到我的同族。那个孩子是在寻找什么人吗？我能理解这种心情......啊，不，没什么。', 'char_210_stward/CN_003');
INSERT INTO `t_operator_voice` VALUES (561, 19, '交谈3', '人们常说我族的毛色是“雪的颜色”。博士，您，见过雪吗？', 'char_210_stward/CN_004');
INSERT INTO `t_operator_voice` VALUES (562, 19, '晋升后交谈1', '炎熔小姐的战斗方式有些与众不同。我曾以为，她使用手中的书本来释放法术，没想到，真正的施术工具却是她的那把匕首。这样的战斗风格，让我想起了那位曾经教过我法术的通灵师。', 'char_210_stward/CN_005');
INSERT INTO `t_operator_voice` VALUES (563, 19, '信赖提升后交谈1', '能为博士服务，我感到非常荣幸。若是可以的话，我希望您能更多关照玫兰莎小姐一些。她，总是一个人......', 'char_210_stward/CN_007');
INSERT INTO `t_operator_voice` VALUES (564, 19, '信赖提升后交谈2', '安德切尔，真是让人捉摸不透......很难说清楚他平时到底在想些什么。', 'char_210_stward/CN_008');
INSERT INTO `t_operator_voice` VALUES (565, 19, '信赖提升后交谈3', '安赛尔......请放心。事到如今，我早已不会在意自己感染者的身份了。不如说，我因此而获得了许多帮助。在它夺走我重要的东西之前，我会先利用它，为大家创造一个能安心生活的地方。', 'char_210_stward/CN_009');
INSERT INTO `t_operator_voice` VALUES (566, 19, '闲置', '累了吗？请多多休息吧，明天罗德岛也需要依靠博士您的力量。', 'char_210_stward/CN_010');
INSERT INTO `t_operator_voice` VALUES (567, 19, '干员报到', '术师史都华德，从今天开始，还请您多多关照。', 'char_210_stward/CN_011');
INSERT INTO `t_operator_voice` VALUES (568, 19, '观看作战记录', '我学到了很多东西。谢谢您。', 'char_210_stward/CN_012');
INSERT INTO `t_operator_voice` VALUES (569, 19, '精英化晋升1', '我的工作成果能让您满意就足够了。杜宾老师也会很高兴吧。', 'char_210_stward/CN_013');
INSERT INTO `t_operator_voice` VALUES (570, 19, '编入队伍', '各位，有何种需求？', 'char_210_stward/CN_017');
INSERT INTO `t_operator_voice` VALUES (571, 19, '任命队长', '卡缇，行动之前要多注意气氛。', 'char_210_stward/CN_018');
INSERT INTO `t_operator_voice` VALUES (572, 19, '行动出发', '那么，请您恭候佳音。', 'char_210_stward/CN_019');
INSERT INTO `t_operator_voice` VALUES (573, 19, '行动开始', '他们，也许再没有机会看到明天的太阳了。', 'char_210_stward/CN_020');
INSERT INTO `t_operator_voice` VALUES (574, 19, '选中干员1', '请下令。', 'char_210_stward/CN_021');
INSERT INTO `t_operator_voice` VALUES (575, 19, '选中干员2', '为您效劳。', 'char_210_stward/CN_022');
INSERT INTO `t_operator_voice` VALUES (576, 19, '部署1', '我知道了。', 'char_210_stward/CN_023');
INSERT INTO `t_operator_voice` VALUES (577, 19, '部署2', '明白。', 'char_210_stward/CN_024');
INSERT INTO `t_operator_voice` VALUES (578, 19, '作战中1', '冻结吧！', 'char_210_stward/CN_025');
INSERT INTO `t_operator_voice` VALUES (579, 19, '作战中2', '倒下吧。', 'char_210_stward/CN_026');
INSERT INTO `t_operator_voice` VALUES (580, 19, '4星结束行动', '只要战术运用得当，我们能够打破任何艰难的局面。', 'char_210_stward/CN_029');
INSERT INTO `t_operator_voice` VALUES (581, 19, '3星结束行动', '这是完美的胜利。大家辛苦了！', 'char_210_stward/CN_030');
INSERT INTO `t_operator_voice` VALUES (582, 19, '非3星结束行动', '安赛尔，请确认伤者的情况，准备下一场战斗。', 'char_210_stward/CN_031');
INSERT INTO `t_operator_voice` VALUES (583, 19, '行动失败', '......哪里出了问题吗？', 'char_210_stward/CN_032');
INSERT INTO `t_operator_voice` VALUES (584, 19, '进驻设施', '卡缇，你在那里吗......？等一下，别碰器材！', 'char_210_stward/CN_033');
INSERT INTO `t_operator_voice` VALUES (585, 19, '戳一下', '嗯？', 'char_210_stward/CN_034');
INSERT INTO `t_operator_voice` VALUES (586, 19, '信赖触摸', '有什么我能为您效劳的吗，博士？', 'char_210_stward/CN_036');
INSERT INTO `t_operator_voice` VALUES (587, 19, '标题', '明日方舟。', 'char_210_stward/CN_037');
INSERT INTO `t_operator_voice` VALUES (588, 19, '问候', '请多关照，博士。', 'char_210_stward/CN_042');
INSERT INTO `t_operator_voice` VALUES (589, 20, '任命助理', '我会把这些都安排好的，交给我吧。', 'char_278_orchid/CN_001');
INSERT INTO `t_operator_voice` VALUES (590, 20, '交谈1', '希望在我忙着处理博士你安排的事务时，组里的其他人不要闹出什么岔子......啊啊啊......一想到这件事，就开始心慌了！', 'char_278_orchid/CN_002');
INSERT INTO `t_operator_voice` VALUES (591, 20, '交谈2', '平静的工作，平静的生活，我想过的也就是这样的日子而已。', 'char_278_orchid/CN_003');
INSERT INTO `t_operator_voice` VALUES (592, 20, '交谈3', '别说了，我不是不擅长和行动预备组A6的其他队员相处，我是看到他们就头疼......', 'char_278_orchid/CN_004');
INSERT INTO `t_operator_voice` VALUES (593, 20, '晋升后交谈1', '......组里的其他人每天都在惹麻烦，也有自闭的，听不懂话的，屡教不改的......要到哪一天大家才会安稳工作啊......', 'char_278_orchid/CN_005');
INSERT INTO `t_operator_voice` VALUES (594, 20, '信赖提升后交谈1', '我也不知道自己为什么会被分到这个小组......明明只是想要普普通通地生活的我，为什么要照顾这些问题儿童啊？！不对，月见夜根本不是儿童！', 'char_278_orchid/CN_007');
INSERT INTO `t_operator_voice` VALUES (595, 20, '信赖提升后交谈2', '泡普卡，摸摸她......空爆，绑起来......斑点，别让他生气......月见夜，直接砸晕带走......这样博士你......就也知道怎么去应对A6小组的其他人了......', 'char_278_orchid/CN_008');
INSERT INTO `t_operator_voice` VALUES (596, 20, '信赖提升后交谈3', '博士要尝尝吗？这些是我自己做的蛋糕，我正准备拿去分给小组里的其他成员，他们最喜欢水果蛋糕了。其实，A6的各位就是群小孩子而已嘛......', 'char_278_orchid/CN_009');
INSERT INTO `t_operator_voice` VALUES (597, 20, '闲置', '今天已经不剩多少时间了，您今天的任务完成了吗？', 'char_278_orchid/CN_010');
INSERT INTO `t_operator_voice` VALUES (598, 20, '干员报到', '预备行动组A6的组长，梓兰，前来报道。您就是Dr.{@nickname}吗，接下来的工作请多多关照。', 'char_278_orchid/CN_011');
INSERT INTO `t_operator_voice` VALUES (599, 20, '观看作战记录', '真是让人受益匪浅的录像，我会好好记下来的。', 'char_278_orchid/CN_012');
INSERT INTO `t_operator_voice` VALUES (600, 20, '精英化晋升1', '十分感谢，之后我也会努力提高自己，为博士争取更好的业绩！', 'char_278_orchid/CN_013');
INSERT INTO `t_operator_voice` VALUES (601, 20, '编入队伍', '我会好好完成我的任务的。', 'char_278_orchid/CN_017');
INSERT INTO `t_operator_voice` VALUES (602, 20, '任命队长', '请各位严格遵循战术手册行事，谢谢配合！', 'char_278_orchid/CN_018');
INSERT INTO `t_operator_voice` VALUES (603, 20, '行动出发', '战斗开始！大家请小心！', 'char_278_orchid/CN_019');
INSERT INTO `t_operator_voice` VALUES (604, 20, '行动开始', '请大家务必要按照计划执行任务！全体人员散开！', 'char_278_orchid/CN_020');
INSERT INTO `t_operator_voice` VALUES (605, 20, '选中干员1', '请说！', 'char_278_orchid/CN_021');
INSERT INTO `t_operator_voice` VALUES (606, 20, '选中干员2', '在！', 'char_278_orchid/CN_022');
INSERT INTO `t_operator_voice` VALUES (607, 20, '部署1', '梓兰，准备就绪。', 'char_278_orchid/CN_023');
INSERT INTO `t_operator_voice` VALUES (608, 20, '部署2', '一切按计划进行。', 'char_278_orchid/CN_024');
INSERT INTO `t_operator_voice` VALUES (609, 20, '作战中1', '目标锁定！', 'char_278_orchid/CN_025');
INSERT INTO `t_operator_voice` VALUES (610, 20, '作战中2', '阻止敌人的脚步！', 'char_278_orchid/CN_026');
INSERT INTO `t_operator_voice` VALUES (611, 20, '作战中3', '别烦我！', 'char_278_orchid/CN_027');
INSERT INTO `t_operator_voice` VALUES (612, 20, '作战中4', '工作已经够多了！', 'char_278_orchid/CN_028');
INSERT INTO `t_operator_voice` VALUES (613, 20, '4星结束行动', '不愧是博士，即使是面对这样的敌人，作战指挥也是如此完美。', 'char_278_orchid/CN_029');
INSERT INTO `t_operator_voice` VALUES (614, 20, '3星结束行动', '完美完成了工作！看来今天可以好好休息一下了。', 'char_278_orchid/CN_030');
INSERT INTO `t_operator_voice` VALUES (615, 20, '非3星结束行动', '......下次还得更加努力才行。', 'char_278_orchid/CN_031');
INSERT INTO `t_operator_voice` VALUES (616, 20, '行动失败', '大家都没事吧，我们快离开这里！', 'char_278_orchid/CN_032');
INSERT INTO `t_operator_voice` VALUES (617, 20, '进驻设施', '真是吵闹的地方......', 'char_278_orchid/CN_033');
INSERT INTO `t_operator_voice` VALUES (618, 20, '戳一下', '要做什么呀......', 'char_278_orchid/CN_034');
INSERT INTO `t_operator_voice` VALUES (619, 20, '信赖触摸', '啊啊？还要加班？我就知道——', 'char_278_orchid/CN_036');
INSERT INTO `t_operator_voice` VALUES (620, 20, '标题', '明日方舟。', 'char_278_orchid/CN_037');
INSERT INTO `t_operator_voice` VALUES (621, 20, '问候', '博士你好，今天也好好把计划的任务完成吧。', 'char_278_orchid/CN_042');
INSERT INTO `t_operator_voice` VALUES (622, 21, '任命助理', '没发现我在你身后？那当然啊～我可是猫，怎么会有脚步声呢？', 'char_141_nights/CN_001');
INSERT INTO `t_operator_voice` VALUES (623, 21, '交谈1', '我的帽子，你可别乱动~它已经疯掉了，要是不小心说出太多秘密给你听到，那对博士可是没好处的哦~', 'char_141_nights/CN_002');
INSERT INTO `t_operator_voice` VALUES (624, 21, '交谈2', '没想到罗德岛连越狱犯都敢接收？是想看看我身上的地图吗？还是想了解了解越狱的办法？', 'char_141_nights/CN_003');
INSERT INTO `t_operator_voice` VALUES (625, 21, '交谈3', '“盗亦有道”——窃贼也有自己的规则哦，说不定是这样的吧~', 'char_141_nights/CN_004');
INSERT INTO `t_operator_voice` VALUES (626, 21, '晋升后交谈1', '呜喵？不用在意哦，是我的自言自语而已~', 'char_141_nights/CN_005');
INSERT INTO `t_operator_voice` VALUES (627, 21, '晋升后交谈2', '出发吧，别担心，凡是我朋友走过的地方，都有我的眼线。', 'char_141_nights/CN_006');
INSERT INTO `t_operator_voice` VALUES (628, 21, '信赖提升后交谈1', '好奇心可是会害死猫的哟~', 'char_141_nights/CN_007');
INSERT INTO `t_operator_voice` VALUES (629, 21, '信赖提升后交谈2', '我现在，心情很好，来给你个谜语猜猜吧~『为什么乌鸦长得像写字台？』呢？', 'char_141_nights/CN_008');
INSERT INTO `t_operator_voice` VALUES (630, 21, '信赖提升后交谈3', '多谢你给我新的身份，但是啊......你是不是看到了？看到了啊，一些本该只属于我自己的秘密？', 'char_141_nights/CN_009');
INSERT INTO `t_operator_voice` VALUES (631, 21, '闲置', '......既然这家伙也睡了，那就差不多——该开始“干活”了喵~', 'char_141_nights/CN_010');
INSERT INTO `t_operator_voice` VALUES (632, 21, '干员报到', '你好~叫我夜烟~我喜欢轻轻松松的工作环境，和一个不会向我问这问那的雇主~', 'char_141_nights/CN_011');
INSERT INTO `t_operator_voice` VALUES (633, 21, '观看作战记录', '好吃~', 'char_141_nights/CN_012');
INSERT INTO `t_operator_voice` VALUES (634, 21, '精英化晋升1', '晋升？那是什么？很好吃吗？', 'char_141_nights/CN_013');
INSERT INTO `t_operator_voice` VALUES (635, 21, '精英化晋升2', '总算是挣脱了旧的枷锁，却很快又被装上了新的。这也许就是窃贼的宿命吧。', 'char_141_nights/CN_014');
INSERT INTO `t_operator_voice` VALUES (636, 21, '编入队伍', '让我自由一点儿就行。', 'char_141_nights/CN_017');
INSERT INTO `t_operator_voice` VALUES (637, 21, '任命队长', '干嘛还要有队长，自由作战不好嘛？', 'char_141_nights/CN_018');
INSERT INTO `t_operator_voice` VALUES (638, 21, '行动出发', '呜喵......这是令我狂喜的，自由的味道！', 'char_141_nights/CN_019');
INSERT INTO `t_operator_voice` VALUES (639, 21, '行动开始', '大家可以自由作战哦~', 'char_141_nights/CN_020');
INSERT INTO `t_operator_voice` VALUES (640, 21, '选中干员1', '喵呼？', 'char_141_nights/CN_021');
INSERT INTO `t_operator_voice` VALUES (641, 21, '选中干员2', '要上了哟——', 'char_141_nights/CN_022');
INSERT INTO `t_operator_voice` VALUES (642, 21, '部署1', '来了来了~', 'char_141_nights/CN_023');
INSERT INTO `t_operator_voice` VALUES (643, 21, '部署2', '像影子一样~', 'char_141_nights/CN_024');
INSERT INTO `t_operator_voice` VALUES (644, 21, '作战中1', '这个怎么样？', 'char_141_nights/CN_025');
INSERT INTO `t_operator_voice` VALUES (645, 21, '作战中2', '太大意可不行哦~', 'char_141_nights/CN_026');
INSERT INTO `t_operator_voice` VALUES (646, 21, '作战中3', '你觉得自己很强对吧？', 'char_141_nights/CN_027');
INSERT INTO `t_operator_voice` VALUES (647, 21, '作战中4', '别太自信哦~', 'char_141_nights/CN_028');
INSERT INTO `t_operator_voice` VALUES (648, 21, '4星结束行动', '永远不允许有什么在猫头上称王称霸~', 'char_141_nights/CN_029');
INSERT INTO `t_operator_voice` VALUES (649, 21, '3星结束行动', '完美胜利？无所谓啦~能赢就好。', 'char_141_nights/CN_030');
INSERT INTO `t_operator_voice` VALUES (650, 21, '非3星结束行动', '你知道你放走了多少敌人嘛？这样可不行啊~', 'char_141_nights/CN_031');
INSERT INTO `t_operator_voice` VALUES (651, 21, '行动失败', '虽然已经很习惯逃跑了，但这次真是太狼狈了......', 'char_141_nights/CN_032');
INSERT INTO `t_operator_voice` VALUES (652, 21, '进驻设施', '被炉~被炉在哪儿？', 'char_141_nights/CN_033');
INSERT INTO `t_operator_voice` VALUES (653, 21, '戳一下', '喵？', 'char_141_nights/CN_034');
INSERT INTO `t_operator_voice` VALUES (654, 21, '信赖触摸', '喵？这些还不够哦。凡是我得不到的东西，我都想要~', 'char_141_nights/CN_036');
INSERT INTO `t_operator_voice` VALUES (655, 21, '标题', '明日方舟。', 'char_141_nights/CN_037');
INSERT INTO `t_operator_voice` VALUES (656, 21, '问候', '博士，你在啊喵~', 'char_141_nights/CN_042');
INSERT INTO `t_operator_voice` VALUES (657, 22, '任命助理', '嗯，博士。您今天的运势是......', 'char_109_fmout/CN_001');
INSERT INTO `t_operator_voice` VALUES (658, 22, '交谈1', '您也想知道，自己未来的命运吗......', 'char_109_fmout/CN_002');
INSERT INTO `t_operator_voice` VALUES (659, 22, '交谈2', '随时都别忘记......未知的力量引起的反作用，很可能反噬我们自身。', 'char_109_fmout/CN_003');
INSERT INTO `t_operator_voice` VALUES (660, 22, '交谈3', '塔罗只能祛除内心的雾霭，却从来不曾拥有万事皆能的神性。', 'char_109_fmout/CN_004');
INSERT INTO `t_operator_voice` VALUES (661, 22, '晋升后交谈1', '命运就像大地，谁也不能挣脱它的枷锁。', 'char_109_fmout/CN_005');
INSERT INTO `t_operator_voice` VALUES (662, 22, '晋升后交谈2', '人无法挣脱大地的桎梏，却能改变大地的样貌。人类的行为中潜藏着无数的可能性。', 'char_109_fmout/CN_006');
INSERT INTO `t_operator_voice` VALUES (663, 22, '信赖提升后交谈1', '运势中，存有无数主题，而最常见的就是......牌运。若要改变牌运，就要勇于改变命运。', 'char_109_fmout/CN_007');
INSERT INTO `t_operator_voice` VALUES (664, 22, '信赖提升后交谈2', '最近在占卜师之间有一个流行的仪式，我们称之为“命运之手”，您想试试看吗？', 'char_109_fmout/CN_008');
INSERT INTO `t_operator_voice` VALUES (665, 22, '信赖提升后交谈3', '对“死亡”的占卜，在我们业内也算是种禁忌。但我认为，“死亡”并非终点，而是一个新的开始。', 'char_109_fmout/CN_009');
INSERT INTO `t_operator_voice` VALUES (666, 22, '闲置', '偶尔，梦境会为你展示有趣的启示，如果体验了这样的梦，要好好保存在记忆中哦，博士。', 'char_109_fmout/CN_010');
INSERT INTO `t_operator_voice` VALUES (667, 22, '干员报到', '遵从阿尔克纳的指引，我的命运终于与您交汇了，博士。', 'char_109_fmout/CN_011');
INSERT INTO `t_operator_voice` VALUES (668, 22, '观看作战记录', '有灵感了呢。', 'char_109_fmout/CN_012');
INSERT INTO `t_operator_voice` VALUES (669, 22, '精英化晋升1', '我已经在水晶里预见了这次晋升哦？呵呵，只是个玩笑而已。', 'char_109_fmout/CN_013');
INSERT INTO `t_operator_voice` VALUES (670, 22, '精英化晋升2', '博士，你想知晓......自己的命运终将去往何方？很遗憾，我帮不了你。为你揭示那些秘密，并不是我的使命。', 'char_109_fmout/CN_014');
INSERT INTO `t_operator_voice` VALUES (671, 22, '编入队伍', '请稍候，先来占卜一下这次战斗的结果吧。', 'char_109_fmout/CN_017');
INSERT INTO `t_operator_voice` VALUES (672, 22, '任命队长', '那么，就让我来为大家祈祷吧。', 'char_109_fmout/CN_018');
INSERT INTO `t_operator_voice` VALUES (673, 22, '行动出发', '命运变幻无常，唯有胜者永存于世。', 'char_109_fmout/CN_019');
INSERT INTO `t_operator_voice` VALUES (674, 22, '行动开始', '我看到了结果。胜利属于罗德岛。', 'char_109_fmout/CN_020');
INSERT INTO `t_operator_voice` VALUES (675, 22, '选中干员1', '明智的选择。', 'char_109_fmout/CN_021');
INSERT INTO `t_operator_voice` VALUES (676, 22, '选中干员2', '我的回合。', 'char_109_fmout/CN_022');
INSERT INTO `t_operator_voice` VALUES (677, 22, '部署1', '抽一张卡片。', 'char_109_fmout/CN_023');
INSERT INTO `t_operator_voice` VALUES (678, 22, '部署2', '不要违抗你的命运。', 'char_109_fmout/CN_024');
INSERT INTO `t_operator_voice` VALUES (679, 22, '作战中1', '虚空之塔。', 'char_109_fmout/CN_025');
INSERT INTO `t_operator_voice` VALUES (680, 22, '作战中2', '行空之战车。', 'char_109_fmout/CN_026');
INSERT INTO `t_operator_voice` VALUES (681, 22, '作战中3', '折断帝王的权杖。', 'char_109_fmout/CN_027');
INSERT INTO `t_operator_voice` VALUES (682, 22, '作战中4', '女祭司毁灭一切。', 'char_109_fmout/CN_028');
INSERT INTO `t_operator_voice` VALUES (683, 22, '4星结束行动', '是命运扼住我们的咽喉，亦或是，我们将要对命运作绝地反击？', 'char_109_fmout/CN_029');
INSERT INTO `t_operator_voice` VALUES (684, 22, '3星结束行动', '我已经将他们全部移动到墓地之中了。', 'char_109_fmout/CN_030');
INSERT INTO `t_operator_voice` VALUES (685, 22, '非3星结束行动', '该重新洗牌了。', 'char_109_fmout/CN_031');
INSERT INTO `t_operator_voice` VALUES (686, 22, '行动失败', '不，这不该是我们应有的命运......', 'char_109_fmout/CN_032');
INSERT INTO `t_operator_voice` VALUES (687, 22, '进驻设施', '这个房间里面似乎潜藏着厄运，需要我用我的力量帮你做点什么吗？', 'char_109_fmout/CN_033');
INSERT INTO `t_operator_voice` VALUES (688, 22, '戳一下', '嗯？怎么了？', 'char_109_fmout/CN_034');
INSERT INTO `t_operator_voice` VALUES (689, 22, '信赖触摸', '想让我为您占卜下今后的运气？是嘛~请稍等一下。', 'char_109_fmout/CN_036');
INSERT INTO `t_operator_voice` VALUES (690, 22, '标题', '明日方舟。', 'char_109_fmout/CN_037');
INSERT INTO `t_operator_voice` VALUES (691, 22, '问候', '幸运之风吹拂着你，博士。', 'char_109_fmout/CN_042');
INSERT INTO `t_operator_voice` VALUES (692, 23, '任命助理', '有，有什么我可以帮忙的事情吗？直接吩咐我就好了。', 'char_235_jesica/CN_001');
INSERT INTO `t_operator_voice` VALUES (693, 23, '交谈1', '芙兰卡前辈和雷蛇前辈，都是我憧憬的对象，我也想成为像她们那样优秀的人......我会更加努力的！博士，您会帮我吗？', 'char_235_jesica/CN_002');
INSERT INTO `t_operator_voice` VALUES (694, 23, '交谈2', '那个，听说香草也在这里？她来黑钢实习的时候在我的小队里，我们一起行动过，如今她也成为可靠的队友了，我却还没有......', 'char_235_jesica/CN_003');
INSERT INTO `t_operator_voice` VALUES (695, 23, '交谈3', 'Dr.{@nickname}，我是不是看起来没有什么威严呢？其实我也有自觉的，也想要做出改变......', 'char_235_jesica/CN_004');
INSERT INTO `t_operator_voice` VALUES (696, 23, '晋升后交谈1', '有的时候觉得自己还在黑钢，明明已经在罗德岛经历了这么多事情，我也不是过去的我了，真奇怪......是我太想念以前的日子了吗？但是我可一点都不想念过去的自己。', 'char_235_jesica/CN_005');
INSERT INTO `t_operator_voice` VALUES (697, 23, '晋升后交谈2', '没有成果的话，努力的过程就没有意义，至少我是这么想的。所以我不想被人看见我独自训练的样子。但有Dr.{@nickname}陪在身边，我又觉得很安心......', 'char_235_jesica/CN_006');
INSERT INTO `t_operator_voice` VALUES (698, 23, '信赖提升后交谈1', '前几天训练时不小心拉伤了肌肉，没关系的请不、不要担心！嘶......我不会给您和罗德岛的大家添麻烦的！', 'char_235_jesica/CN_007');
INSERT INTO `t_operator_voice` VALUES (699, 23, '信赖提升后交谈2', '站住别动，举起手来！......呃，吓到您了吗，芙兰卡前辈告诉我说话要有底气，我只是想尝试一下，对不起......', 'char_235_jesica/CN_008');
INSERT INTO `t_operator_voice` VALUES (700, 23, '信赖提升后交谈3', '不论是在黑钢还是在罗德岛，优秀的人都太多了，我很清楚自己的实力与前辈们之间的差距......不过如果您偶尔夸奖我一下，我还是会开心的......', 'char_235_jesica/CN_009');
INSERT INTO `t_operator_voice` VALUES (701, 23, '闲置', '博士，博士？睡着了吗？我帮您把办公桌收拾一下哦。', 'char_235_jesica/CN_010');
INSERT INTO `t_operator_voice` VALUES (702, 23, '干员报到', '请问是Dr.{@nickname}吗......您好，我是黑钢国际的杰西卡，我，我不太擅长自我介绍，呜......', 'char_235_jesica/CN_011');
INSERT INTO `t_operator_voice` VALUES (703, 23, '观看作战记录', '这样好像要比独自训练的收获更多一些？', 'char_235_jesica/CN_012');
INSERT INTO `t_operator_voice` VALUES (704, 23, '精英化晋升1', '得到您的认可我、我有点开心，我稍微哭一下，就一下，呜......', 'char_235_jesica/CN_013');
INSERT INTO `t_operator_voice` VALUES (705, 23, '精英化晋升2', '啊，博士，我已经成为优秀的干员了吗？谢谢您一直以来都陪在我身边，正因为这样，我才能坚持下来......！', 'char_235_jesica/CN_014');
INSERT INTO `t_operator_voice` VALUES (706, 23, '编入队伍', '请下指令，我会好好执行！', 'char_235_jesica/CN_017');
INSERT INTO `t_operator_voice` VALUES (707, 23, '任命队长', '我一定......不会辜负您的信任。', 'char_235_jesica/CN_018');
INSERT INTO `t_operator_voice` VALUES (708, 23, '行动出发', '集中精神......直视前方。', 'char_235_jesica/CN_019');
INSERT INTO `t_operator_voice` VALUES (709, 23, '行动开始', '这次我要证明自己......！', 'char_235_jesica/CN_020');
INSERT INTO `t_operator_voice` VALUES (710, 23, '选中干员1', '待命中......', 'char_235_jesica/CN_021');
INSERT INTO `t_operator_voice` VALUES (711, 23, '选中干员2', '呜哇？！', 'char_235_jesica/CN_022');
INSERT INTO `t_operator_voice` VALUES (712, 23, '部署1', '呼......', 'char_235_jesica/CN_023');
INSERT INTO `t_operator_voice` VALUES (713, 23, '部署2', '好了......', 'char_235_jesica/CN_024');
INSERT INTO `t_operator_voice` VALUES (714, 23, '作战中1', '不可原谅......！', 'char_235_jesica/CN_025');
INSERT INTO `t_operator_voice` VALUES (715, 23, '作战中2', '站住别动！', 'char_235_jesica/CN_026');
INSERT INTO `t_operator_voice` VALUES (716, 23, '作战中3', '不会退缩。', 'char_235_jesica/CN_027');
INSERT INTO `t_operator_voice` VALUES (717, 23, '作战中4', '我可不会手下留情！', 'char_235_jesica/CN_028');
INSERT INTO `t_operator_voice` VALUES (718, 23, '4星结束行动', '山顶才是我的目标......可不能允许自己止步于山腰。', 'char_235_jesica/CN_029');
INSERT INTO `t_operator_voice` VALUES (719, 23, '3星结束行动', '我做到了......？我用自己的力量保护了大家吗？', 'char_235_jesica/CN_030');
INSERT INTO `t_operator_voice` VALUES (720, 23, '非3星结束行动', '这还不是最好的结果，不能因为这样的胜利而骄傲懈怠......', 'char_235_jesica/CN_031');
INSERT INTO `t_operator_voice` VALUES (721, 23, '行动失败', '请求医疗支援！都是我拖累了大家......对不起、对不起、对不起！', 'char_235_jesica/CN_032');
INSERT INTO `t_operator_voice` VALUES (722, 23, '进驻设施', '罗德岛的大家好相处吗......？', 'char_235_jesica/CN_033');
INSERT INTO `t_operator_voice` VALUES (723, 23, '戳一下', '要去哪里......', 'char_235_jesica/CN_034');
INSERT INTO `t_operator_voice` VALUES (724, 23, '信赖触摸', '料理的话我也是会做的......', 'char_235_jesica/CN_036');
INSERT INTO `t_operator_voice` VALUES (725, 23, '标题', '明日方舟。', 'char_235_jesica/CN_037');
INSERT INTO `t_operator_voice` VALUES (726, 23, '问候', '早上好......', 'char_235_jesica/CN_042');
INSERT INTO `t_operator_voice` VALUES (727, 24, '任命助理', '好怀念的气息啊。这里是......？', 'char_126_shotst/CN_001');
INSERT INTO `t_operator_voice` VALUES (728, 24, '交谈1', '来自卡西米尔的风，您感受到了吗？', 'char_126_shotst/CN_002');
INSERT INTO `t_operator_voice` VALUES (729, 24, '交谈2', '罗德岛和我待过的地方不一样，这里没有可以点起篝火的地方，真遗憾。不知道在甲板上能不能行，我想给大家创造一个近距离说话聊天的场所。', 'char_126_shotst/CN_003');
INSERT INTO `t_operator_voice` VALUES (730, 24, '交谈3', '这个任务登记系统......？呃，要怎么操作呢？我对电子设备真的不怎么在行......', 'char_126_shotst/CN_004');
INSERT INTO `t_operator_voice` VALUES (731, 24, '晋升后交谈1', '最近罗德岛增加了许多新面孔啊。为了让他们更顺利地融入罗德岛，必要的时候我会去帮助他们的。', 'char_126_shotst/CN_005');
INSERT INTO `t_operator_voice` VALUES (732, 24, '晋升后交谈2', '那位卡西米尔骑士......嗯，我没认错，我以前就认识她，那时候她还是个喜欢读书的内向女孩呢。变化可真大啊。', 'char_126_shotst/CN_006');
INSERT INTO `t_operator_voice` VALUES (733, 24, '信赖提升后交谈1', '嘘——看，我发现了什么，这儿有一位落单的博士，我们可以尝试捕捉他！吓到您了吗？呵呵~', 'char_126_shotst/CN_007');
INSERT INTO `t_operator_voice` VALUES (734, 24, '信赖提升后交谈2', '偶尔的，我会想起在森林中的故乡时度过的日子。每年都有各种各样的竞赛可以参加，真怀念啊。不知道，我的故乡现在又变成什么样了呢。', 'char_126_shotst/CN_008');
INSERT INTO `t_operator_voice` VALUES (735, 24, '信赖提升后交谈3', '博士的背后，无论什么时候都可以放心地交给我。', 'char_126_shotst/CN_009');
INSERT INTO `t_operator_voice` VALUES (736, 24, '闲置', '博士也会这样毫无防备地睡着啊，呵呵。', 'char_126_shotst/CN_010');
INSERT INTO `t_operator_voice` VALUES (737, 24, '干员报到', '你好，我是流星，来自森林的弓箭手。您需要擅长远程援护的干员吗？', 'char_126_shotst/CN_011');
INSERT INTO `t_operator_voice` VALUES (738, 24, '观看作战记录', '弓术的练习，哪怕一天也不能松懈呀。', 'char_126_shotst/CN_012');
INSERT INTO `t_operator_voice` VALUES (739, 24, '精英化晋升1', '我很荣幸获得这次晋升机会。', 'char_126_shotst/CN_013');
INSERT INTO `t_operator_voice` VALUES (740, 24, '精英化晋升2', '谢谢博士的认可。今后就让我继续为博士贡献自己的力量吧。', 'char_126_shotst/CN_014');
INSERT INTO `t_operator_voice` VALUES (741, 24, '编入队伍', '远程援护就交给我吧。', 'char_126_shotst/CN_017');
INSERT INTO `t_operator_voice` VALUES (742, 24, '任命队长', '我会照顾好大家的。', 'char_126_shotst/CN_018');
INSERT INTO `t_operator_voice` VALUES (743, 24, '行动出发', '伙伴们，出发。', 'char_126_shotst/CN_019');
INSERT INTO `t_operator_voice` VALUES (744, 24, '行动开始', '展示我们力量的时候到了。', 'char_126_shotst/CN_020');
INSERT INTO `t_operator_voice` VALUES (745, 24, '选中干员1', '请您下令。', 'char_126_shotst/CN_021');
INSERT INTO `t_operator_voice` VALUES (746, 24, '选中干员2', '我已等候多时。', 'char_126_shotst/CN_022');
INSERT INTO `t_operator_voice` VALUES (747, 24, '部署1', '风吹向了正确的地方。', 'char_126_shotst/CN_023');
INSERT INTO `t_operator_voice` VALUES (748, 24, '部署2', '我会保持警戒。', 'char_126_shotst/CN_024');
INSERT INTO `t_operator_voice` VALUES (749, 24, '作战中1', '有破绽！', 'char_126_shotst/CN_025');
INSERT INTO `t_operator_voice` VALUES (750, 24, '作战中2', '风！', 'char_126_shotst/CN_026');
INSERT INTO `t_operator_voice` VALUES (751, 24, '作战中3', '就是这一箭！', 'char_126_shotst/CN_027');
INSERT INTO `t_operator_voice` VALUES (752, 24, '作战中4', '致命一击。', 'char_126_shotst/CN_028');
INSERT INTO `t_operator_voice` VALUES (753, 24, '4星结束行动', '不管是什么样的难关，只要我们齐心协力，就一定能克服过去。', 'char_126_shotst/CN_029');
INSERT INTO `t_operator_voice` VALUES (754, 24, '3星结束行动', '干的漂亮，大家都是最棒的。', 'char_126_shotst/CN_030');
INSERT INTO `t_operator_voice` VALUES (755, 24, '非3星结束行动', '请伤者留在原地，别乱动，我马上就去帮你。', 'char_126_shotst/CN_031');
INSERT INTO `t_operator_voice` VALUES (756, 24, '行动失败', '抱歉，不应该是这样的......', 'char_126_shotst/CN_032');
INSERT INTO `t_operator_voice` VALUES (757, 24, '进驻设施', '我还是不太习惯这么狭小的房间啊。', 'char_126_shotst/CN_033');
INSERT INTO `t_operator_voice` VALUES (758, 24, '戳一下', '哎呀？有什么事吗？', 'char_126_shotst/CN_034');
INSERT INTO `t_operator_voice` VALUES (759, 24, '信赖触摸', '和博士在一起的时光，就像沐浴在春天的阳光里一样温暖，很舒服。', 'char_126_shotst/CN_036');
INSERT INTO `t_operator_voice` VALUES (760, 24, '标题', '明日方舟。', 'char_126_shotst/CN_037');
INSERT INTO `t_operator_voice` VALUES (761, 24, '问候', '博士，您好。', 'char_126_shotst/CN_042');
INSERT INTO `t_operator_voice` VALUES (762, 25, '任命助理', '白雪，随时守候。', 'char_118_yuki/CN_001');
INSERT INTO `t_operator_voice` VALUES (763, 25, '交谈1', '文月公主是我的主上，你是我的雇主。', 'char_118_yuki/CN_002');
INSERT INTO `t_operator_voice` VALUES (764, 25, '交谈2', '爱好？海岬，看雪。', 'char_118_yuki/CN_003');
INSERT INTO `t_operator_voice` VALUES (765, 25, '交谈3', '所属？机密。', 'char_118_yuki/CN_004');
INSERT INTO `t_operator_voice` VALUES (766, 25, '晋升后交谈1', '出身？东国。', 'char_118_yuki/CN_005');
INSERT INTO `t_operator_voice` VALUES (767, 25, '晋升后交谈2', '武器？手里剑·银风，东方重工制造。', 'char_118_yuki/CN_006');
INSERT INTO `t_operator_voice` VALUES (768, 25, '信赖提升后交谈1', '喜欢的食物？鮨。', 'char_118_yuki/CN_007');
INSERT INTO `t_operator_voice` VALUES (769, 25, '信赖提升后交谈2', '年龄？无可奉告。', 'char_118_yuki/CN_008');
INSERT INTO `t_operator_voice` VALUES (770, 25, '信赖提升后交谈3', '职责？保护你。', 'char_118_yuki/CN_009');
INSERT INTO `t_operator_voice` VALUES (771, 25, '闲置', '......寂静。', 'char_118_yuki/CN_010');
INSERT INTO `t_operator_voice` VALUES (772, 25, '干员报到', '白雪，依照文月公主的嘱托，协助陈小姐行动，并听从你的差遣。', 'char_118_yuki/CN_011');
INSERT INTO `t_operator_voice` VALUES (773, 25, '观看作战记录', '感谢。', 'char_118_yuki/CN_012');
INSERT INTO `t_operator_voice` VALUES (774, 25, '精英化晋升1', '晋升？白雪领命。', 'char_118_yuki/CN_013');
INSERT INTO `t_operator_voice` VALUES (775, 25, '精英化晋升2', '白雪，愿为你献上更多力量。', 'char_118_yuki/CN_014');
INSERT INTO `t_operator_voice` VALUES (776, 25, '编入队伍', '明白。', 'char_118_yuki/CN_017');
INSERT INTO `t_operator_voice` VALUES (777, 25, '任命队长', '领命。', 'char_118_yuki/CN_018');
INSERT INTO `t_operator_voice` VALUES (778, 25, '行动出发', '行动开始。', 'char_118_yuki/CN_019');
INSERT INTO `t_operator_voice` VALUES (779, 25, '行动开始', '风雪将掩埋这片战场。', 'char_118_yuki/CN_020');
INSERT INTO `t_operator_voice` VALUES (780, 25, '选中干员1', '好。', 'char_118_yuki/CN_021');
INSERT INTO `t_operator_voice` VALUES (781, 25, '选中干员2', '准备。', 'char_118_yuki/CN_022');
INSERT INTO `t_operator_voice` VALUES (782, 25, '部署1', '了解。', 'char_118_yuki/CN_023');
INSERT INTO `t_operator_voice` VALUES (783, 25, '部署2', '明白。', 'char_118_yuki/CN_024');
INSERT INTO `t_operator_voice` VALUES (784, 25, '作战中1', '入灭。', 'char_118_yuki/CN_025');
INSERT INTO `t_operator_voice` VALUES (785, 25, '作战中2', '破碎。', 'char_118_yuki/CN_026');
INSERT INTO `t_operator_voice` VALUES (786, 25, '作战中3', '无常之风。', 'char_118_yuki/CN_027');
INSERT INTO `t_operator_voice` VALUES (787, 25, '作战中4', '断绝。', 'char_118_yuki/CN_028');
INSERT INTO `t_operator_voice` VALUES (788, 25, '4星结束行动', '敌尽则我进，我进则敌尽。', 'char_118_yuki/CN_029');
INSERT INTO `t_operator_voice` VALUES (789, 25, '3星结束行动', '无人生还，大胜。', 'char_118_yuki/CN_030');
INSERT INTO `t_operator_voice` VALUES (790, 25, '非3星结束行动', '还有漏网之鱼，不可大意。', 'char_118_yuki/CN_031');
INSERT INTO `t_operator_voice` VALUES (791, 25, '行动失败', '若再战，必将百倍奉还。', 'char_118_yuki/CN_032');
INSERT INTO `t_operator_voice` VALUES (792, 25, '进驻设施', '一间很棒的屋子。', 'char_118_yuki/CN_033');
INSERT INTO `t_operator_voice` VALUES (793, 25, '戳一下', '是。', 'char_118_yuki/CN_034');
INSERT INTO `t_operator_voice` VALUES (794, 25, '信赖触摸', '你的关照，白雪定会铭记于心。', 'char_118_yuki/CN_036');
INSERT INTO `t_operator_voice` VALUES (795, 25, '标题', '明日方舟。', 'char_118_yuki/CN_037');
INSERT INTO `t_operator_voice` VALUES (796, 25, '问候', '雇主，你好。', 'char_118_yuki/CN_042');
INSERT INTO `t_operator_voice` VALUES (797, 26, '任命助理', '护卫的职责可不是傻站着发呆，让在下陪您聊一会儿如何。', 'char_198_blackd/CN_001');
INSERT INTO `t_operator_voice` VALUES (798, 26, '交谈1', '定位准确，环境适当，在下手中的信件就一定会准时送达。毕竟这可是在下赖以生存的活计啊。', 'char_198_blackd/CN_002');
INSERT INTO `t_operator_voice` VALUES (799, 26, '交谈2', '肚子饿了？想吃点什么尽管说，虽然比不上角峰大哥，在下对自己的手艺还是有点信心的。', 'char_198_blackd/CN_003');
INSERT INTO `t_operator_voice` VALUES (800, 26, '交谈3', '罗德岛的通讯设备太发达了吧？这样下去......在下是要失业了！', 'char_198_blackd/CN_004');
INSERT INTO `t_operator_voice` VALUES (801, 26, '晋升后交谈1', '还有什么吩咐吗？可不要让在下等太久喔。', 'char_198_blackd/CN_005');
INSERT INTO `t_operator_voice` VALUES (802, 26, '晋升后交谈2', '唔？信件上会留下淡淡的味道？是错觉吧，错觉。', 'char_198_blackd/CN_006');
INSERT INTO `t_operator_voice` VALUES (803, 26, '信赖提升后交谈1', '银灰老板他对在下有救命之恩。如果是必要的事情，无论是什么，在下都会替他去做。', 'char_198_blackd/CN_007');
INSERT INTO `t_operator_voice` VALUES (804, 26, '信赖提升后交谈2', '这些货物都要在下送吗？数量太多了吧，博士，别为难在下呀~', 'char_198_blackd/CN_008');
INSERT INTO `t_operator_voice` VALUES (805, 26, '信赖提升后交谈3', '真是败给你了......那我就暂且收起这营业性笑容好了。希望从今往后，我都能以真正的表情面对你。', 'char_198_blackd/CN_009');
INSERT INTO `t_operator_voice` VALUES (806, 26, '闲置', '博士，有新的讯息......嘛，算了。', 'char_198_blackd/CN_010');
INSERT INTO `t_operator_voice` VALUES (807, 26, '干员报到', '尊敬的罗德岛领袖，您的盟友，雪境的讯使在此。您与银灰老板的契约期内，碰上困难，就试着打个响指呼唤在下吧。', 'char_198_blackd/CN_011');
INSERT INTO `t_operator_voice` VALUES (808, 26, '观看作战记录', '哇，完成了不得了的任务。', 'char_198_blackd/CN_012');
INSERT INTO `t_operator_voice` VALUES (809, 26, '精英化晋升1', '让在下上场的话，您的工作一定会更加轻松的。', 'char_198_blackd/CN_013');
INSERT INTO `t_operator_voice` VALUES (810, 26, '精英化晋升2', '作为雪境的讯使，危急关头，在下定会立刻出现在您身边。', 'char_198_blackd/CN_014');
INSERT INTO `t_operator_voice` VALUES (811, 26, '编入队伍', '那么，战场侦查就交给在下吧。', 'char_198_blackd/CN_017');
INSERT INTO `t_operator_voice` VALUES (812, 26, '任命队长', '绝不辱没雪境之名。', 'char_198_blackd/CN_018');
INSERT INTO `t_operator_voice` VALUES (813, 26, '行动出发', '总有人破坏这份难得的和谐，真可惜。', 'char_198_blackd/CN_019');
INSERT INTO `t_operator_voice` VALUES (814, 26, '行动开始', '他们会为此付出代价的。', 'char_198_blackd/CN_020');
INSERT INTO `t_operator_voice` VALUES (815, 26, '选中干员1', '听从差遣。', 'char_198_blackd/CN_021');
INSERT INTO `t_operator_voice` VALUES (816, 26, '选中干员2', '待命中。', 'char_198_blackd/CN_022');
INSERT INTO `t_operator_voice` VALUES (817, 26, '部署1', '嗯。', 'char_198_blackd/CN_023');
INSERT INTO `t_operator_voice` VALUES (818, 26, '部署2', '进攻。', 'char_198_blackd/CN_024');
INSERT INTO `t_operator_voice` VALUES (819, 26, '作战中1', '突击！', 'char_198_blackd/CN_025');
INSERT INTO `t_operator_voice` VALUES (820, 26, '作战中2', '怎样？', 'char_198_blackd/CN_026');
INSERT INTO `t_operator_voice` VALUES (821, 26, '作战中3', '哈——', 'char_198_blackd/CN_027');
INSERT INTO `t_operator_voice` VALUES (822, 26, '作战中4', '加速！', 'char_198_blackd/CN_028');
INSERT INTO `t_operator_voice` VALUES (823, 26, '4星结束行动', '让在下用最快的速度，将胜利的讯息送回罗德岛。', 'char_198_blackd/CN_029');
INSERT INTO `t_operator_voice` VALUES (824, 26, '3星结束行动', '清理完战场以后一起去吃顿好的如何，我请客。', 'char_198_blackd/CN_030');
INSERT INTO `t_operator_voice` VALUES (825, 26, '非3星结束行动', '有人受伤了吗，在下这里有药水和绷带。', 'char_198_blackd/CN_031');
INSERT INTO `t_operator_voice` VALUES (826, 26, '行动失败', '先离开这儿——走这条路，快！', 'char_198_blackd/CN_032');
INSERT INTO `t_operator_voice` VALUES (827, 26, '进驻设施', '先和室友邻居们打个招呼。', 'char_198_blackd/CN_033');
INSERT INTO `t_operator_voice` VALUES (828, 26, '戳一下', '有事吗？', 'char_198_blackd/CN_034');
INSERT INTO `t_operator_voice` VALUES (829, 26, '信赖触摸', '好啦好啦，一会给你做点吃的。', 'char_198_blackd/CN_036');
INSERT INTO `t_operator_voice` VALUES (830, 26, '标题', '明日方舟。', 'char_198_blackd/CN_037');
INSERT INTO `t_operator_voice` VALUES (831, 26, '问候', '随时为您效劳。', 'char_198_blackd/CN_042');
INSERT INTO `t_operator_voice` VALUES (832, 27, '任命助理', '与其花时间在这里唠唠叨叨，还不如赶紧把手上的工作做完。', 'char_149_scave/CN_001');
INSERT INTO `t_operator_voice` VALUES (833, 27, '交谈1', '最好记住，适应不了恶劣环境的家伙，就只是单纯的累赘。', 'char_149_scave/CN_002');
INSERT INTO `t_operator_voice` VALUES (834, 27, '交谈2', '工作、糊口、逃亡，不断循环，没什么大不了的，这世道，大家不都一样吗？', 'char_149_scave/CN_003');
INSERT INTO `t_operator_voice` VALUES (835, 27, '交谈3', '我的工作就是处理那些大人物的“麻烦事”，所以他们都叫我“清道夫”。', 'char_149_scave/CN_004');
INSERT INTO `t_operator_voice` VALUES (836, 27, '晋升后交谈1', '就像硬币有正反面一样，表面再怎么光鲜的东西，背面一定是无比肮脏的。', 'char_149_scave/CN_005');
INSERT INTO `t_operator_voice` VALUES (837, 27, '晋升后交谈2', '我看起来比刚来的时候适应多了？嘁，还有这回事！我只是渐渐习惯了在地面上工作而已。', 'char_149_scave/CN_006');
INSERT INTO `t_operator_voice` VALUES (838, 27, '信赖提升后交谈1', '那家伙就是普罗旺斯吧......说是因为进入天灾区救人，结果被感染的倒霉家伙什么的......唔，尾巴居然变得那么大了吗......', 'char_149_scave/CN_007');
INSERT INTO `t_operator_voice` VALUES (839, 27, '信赖提升后交谈2', '知道吗，博士......我本来很讨厌所谓的合作关系，因为我觉得，我们一定不会喜欢彼此的做事方式。不过，不知道从什么时候开始，我发现，事情并非如此。', 'char_149_scave/CN_008');
INSERT INTO `t_operator_voice` VALUES (840, 27, '信赖提升后交谈3', '如果她还活着的话......现在，我只能背负着她的愿望，独自活下去。', 'char_149_scave/CN_009');
INSERT INTO `t_operator_voice` VALUES (841, 27, '闲置', '......为什么这家伙能在完全不设防的地方睡着？', 'char_149_scave/CN_010');
INSERT INTO `t_operator_voice` VALUES (842, 27, '干员报到', '我是清道夫，肮脏的工作我来做，你和那些家伙一样，只要付钱就行了。', 'char_149_scave/CN_011');
INSERT INTO `t_operator_voice` VALUES (843, 27, '观看作战记录', '还没有，还不够......', 'char_149_scave/CN_012');
INSERT INTO `t_operator_voice` VALUES (844, 27, '精英化晋升1', '还不够，我必须变得更强才行......', 'char_149_scave/CN_013');
INSERT INTO `t_operator_voice` VALUES (845, 27, '精英化晋升2', '谢谢你，博士。战斗的理由，我现在似乎弄清楚了一些。', 'char_149_scave/CN_014');
INSERT INTO `t_operator_voice` VALUES (846, 27, '编入队伍', '入队而已，必须要说点什么吗？', 'char_149_scave/CN_017');
INSERT INTO `t_operator_voice` VALUES (847, 27, '任命队长', '你们，别拖我后腿。', 'char_149_scave/CN_018');
INSERT INTO `t_operator_voice` VALUES (848, 27, '行动出发', '隐藏气息，别被敌人发现了！', 'char_149_scave/CN_019');
INSERT INTO `t_operator_voice` VALUES (849, 27, '行动开始', '哼，垃圾扫除的时间到了。', 'char_149_scave/CN_020');
INSERT INTO `t_operator_voice` VALUES (850, 27, '选中干员1', '赶快。', 'char_149_scave/CN_021');
INSERT INTO `t_operator_voice` VALUES (851, 27, '选中干员2', '要动手？', 'char_149_scave/CN_022');
INSERT INTO `t_operator_voice` VALUES (852, 27, '部署1', '嘁。', 'char_149_scave/CN_023');
INSERT INTO `t_operator_voice` VALUES (853, 27, '部署2', '我会做的。', 'char_149_scave/CN_024');
INSERT INTO `t_operator_voice` VALUES (854, 27, '作战中1', '垃圾。', 'char_149_scave/CN_025');
INSERT INTO `t_operator_voice` VALUES (855, 27, '作战中2', '在阴沟里挣扎吧！', 'char_149_scave/CN_026');
INSERT INTO `t_operator_voice` VALUES (856, 27, '作战中3', '你已经没法回头了！', 'char_149_scave/CN_027');
INSERT INTO `t_operator_voice` VALUES (857, 27, '作战中4', '没人可怜你！', 'char_149_scave/CN_028');
INSERT INTO `t_operator_voice` VALUES (858, 27, '4星结束行动', '真是一次意料之外的任务。', 'char_149_scave/CN_029');
INSERT INTO `t_operator_voice` VALUES (859, 27, '3星结束行动', '这样的结果，要怪就怪他们自己太过弱小。', 'char_149_scave/CN_030');
INSERT INTO `t_operator_voice` VALUES (860, 27, '非3星结束行动', '嘁，放走了好几个人。如果你的指挥只有这种程度，算我白期待了。', 'char_149_scave/CN_031');
INSERT INTO `t_operator_voice` VALUES (861, 27, '行动失败', '可恶！又搞砸了......', 'char_149_scave/CN_032');
INSERT INTO `t_operator_voice` VALUES (862, 27, '进驻设施', '给我个阴暗的角落就行。', 'char_149_scave/CN_033');
INSERT INTO `t_operator_voice` VALUES (863, 27, '戳一下', '唔呃！放开我！', 'char_149_scave/CN_034');
INSERT INTO `t_operator_voice` VALUES (864, 27, '信赖触摸', '你也想被“清理”一下吗？', 'char_149_scave/CN_036');
INSERT INTO `t_operator_voice` VALUES (865, 27, '标题', '明日方舟。', 'char_149_scave/CN_037');
INSERT INTO `t_operator_voice` VALUES (866, 27, '问候', '有任务？', 'char_149_scave/CN_042');
INSERT INTO `t_operator_voice` VALUES (867, 28, '任命助理', '喂，喂，博士！我还在看乐队演出呢，别挡着屏幕啊，刚刚可是最精彩的部分！', 'char_290_vigna/CN_001');
INSERT INTO `t_operator_voice` VALUES (868, 28, '交谈1', '有些人常挂在嘴边的那些对抗不公啦，坚持自我什么的，对于我们萨卡兹人来说，根本就是生活的一部分！', 'char_290_vigna/CN_002');
INSERT INTO `t_operator_voice` VALUES (869, 28, '交谈2', '我很喜欢乐队。不，是非常非常喜欢！当然，作为吉他手，每一拍节奏每一个和弦，都很重要！只有孜孜不倦地练习，才能带给听众最震撼的独奏！', 'char_290_vigna/CN_003');
INSERT INTO `t_operator_voice` VALUES (870, 28, '交谈3', '你觉得我对大家都很礼貌，对你却很严厉？那当然，因为管理者只靠自律是不够的！我严格地要求自己，也会那样严格地要求博士你哦。', 'char_290_vigna/CN_004');
INSERT INTO `t_operator_voice` VALUES (871, 28, '晋升后交谈1', '唔呃，这气味......难道是芙蓉送来的午餐便当？帮你吃这个？我拒绝！这、这怎么看都不算是工作内容之一吧？', 'char_290_vigna/CN_005');
INSERT INTO `t_operator_voice` VALUES (872, 28, '晋升后交谈2', '人生的目标，就应该靠自己的努力去达成吧。想要变强，我就会去锻炼......手中的武器不够锋利，我就亲手去改造它！不管别人怎么说，我都会坚持自己的想法，直到达到目标为止！', 'char_290_vigna/CN_006');
INSERT INTO `t_operator_voice` VALUES (873, 28, '信赖提升后交谈1', '欸？你说外面在下暴雨？不行，今天的露天音乐会绝不能取消......别拦着我，让我出门啦！区区一场暴风雨，怎么可能熄灭我对吉他的热情！', 'char_290_vigna/CN_007');
INSERT INTO `t_operator_voice` VALUES (874, 28, '信赖提升后交谈2', '鉴于我足够可靠，所以有重要的任务托付给我？嗯哼哼，就让我来看看！嗯......在罗德岛限时大促销里，抢购以下热门商品......呃，超、超难的？！', 'char_290_vigna/CN_008');
INSERT INTO `t_operator_voice` VALUES (875, 28, '信赖提升后交谈3', '不管舞台下的观众发出多少嘘声，乐队都得坚持到演出结束才行，被打击就中途退场是很不负责任的。萨卡兹人和感染者的处境也一样，现在逃走的话，就算是彻底认输了呀。', 'char_290_vigna/CN_009');
INSERT INTO `t_operator_voice` VALUES (876, 28, '闲置', '不要睡在这个地方啊！唔呃......可恶，搬不动这人......', 'char_290_vigna/CN_010');
INSERT INTO `t_operator_voice` VALUES (877, 28, '干员报到', '请问，这里是Dr.{@nickname}的办公室吗，我找博士很久了，麻烦你......什么？你就是博士？哼哼，干员红豆来报到了！', 'char_290_vigna/CN_011');
INSERT INTO `t_operator_voice` VALUES (878, 28, '观看作战记录', '要跟得上不断变快的战斗节奏，就必须坚持挥洒汗水，没错吧！', 'char_290_vigna/CN_012');
INSERT INTO `t_operator_voice` VALUES (879, 28, '精英化晋升1', '晋升徽章，我没戴歪吧？好，我马上去拍纪念照！', 'char_290_vigna/CN_013');
INSERT INTO `t_operator_voice` VALUES (880, 28, '精英化晋升2', '我虽然不擅长凭借力量取胜，但能用好这把长枪的人，也不会有多少！', 'char_290_vigna/CN_014');
INSERT INTO `t_operator_voice` VALUES (881, 28, '编入队伍', '我会努力学习大家的战斗技巧的。', 'char_290_vigna/CN_017');
INSERT INTO `t_operator_voice` VALUES (882, 28, '任命队长', '虽然是队长，也请大家多多指出我不足的地方。', 'char_290_vigna/CN_018');
INSERT INTO `t_operator_voice` VALUES (883, 28, '行动出发', '各位，请做好出发准备，也请多多配合我！', 'char_290_vigna/CN_019');
INSERT INTO `t_operator_voice` VALUES (884, 28, '行动开始', '好好看着吧，接下来可是我的表演时间！', 'char_290_vigna/CN_020');
INSERT INTO `t_operator_voice` VALUES (885, 28, '选中干员1', '哦！', 'char_290_vigna/CN_021');
INSERT INTO `t_operator_voice` VALUES (886, 28, '选中干员2', '嗯。', 'char_290_vigna/CN_022');
INSERT INTO `t_operator_voice` VALUES (887, 28, '部署1', '我来了！', 'char_290_vigna/CN_023');
INSERT INTO `t_operator_voice` VALUES (888, 28, '部署2', '战斗姿态！', 'char_290_vigna/CN_024');
INSERT INTO `t_operator_voice` VALUES (889, 28, '作战中1', '多说无益！', 'char_290_vigna/CN_025');
INSERT INTO `t_operator_voice` VALUES (890, 28, '作战中2', '我血液在沸腾！', 'char_290_vigna/CN_026');
INSERT INTO `t_operator_voice` VALUES (891, 28, '作战中3', '不会输给你的！', 'char_290_vigna/CN_027');
INSERT INTO `t_operator_voice` VALUES (892, 28, '作战中4', '就是现在！搅碎敌人吧！', 'char_290_vigna/CN_028');
INSERT INTO `t_operator_voice` VALUES (893, 28, '4星结束行动', '不管敌人变得有多强大，都不能让我停止演奏！啊，不，是战斗！', 'char_290_vigna/CN_029');
INSERT INTO `t_operator_voice` VALUES (894, 28, '3星结束行动', '大家都做的很好！不过博士，想听我表扬你，可要等到回去以后哦~', 'char_290_vigna/CN_030');
INSERT INTO `t_operator_voice` VALUES (895, 28, '非3星结束行动', '现在还不安全！请立刻巩固阵形！', 'char_290_vigna/CN_031');
INSERT INTO `t_operator_voice` VALUES (896, 28, '行动失败', '唔呃......不行，还要更加地努力！', 'char_290_vigna/CN_032');
INSERT INTO `t_operator_voice` VALUES (897, 28, '进驻设施', '请问有没有隔音室？我好想弹吉他啊！', 'char_290_vigna/CN_033');
INSERT INTO `t_operator_voice` VALUES (898, 28, '戳一下', '干什么呀！不要碰我啦！', 'char_290_vigna/CN_034');
INSERT INTO `t_operator_voice` VALUES (899, 28, '信赖触摸', '别、别摸我的头！会长不高的！', 'char_290_vigna/CN_036');
INSERT INTO `t_operator_voice` VALUES (900, 28, '标题', '明日方舟。', 'char_290_vigna/CN_037');
INSERT INTO `t_operator_voice` VALUES (901, 28, '问候', '有事吗？', 'char_290_vigna/CN_042');
INSERT INTO `t_operator_voice` VALUES (902, 29, '任命助理', '博士，我安排下去的基础训练项目，新人们都有在认真练习吗？', 'char_130_doberm/CN_001');
INSERT INTO `t_operator_voice` VALUES (903, 29, '交谈1', '合理且有规律的饮食也是日常训练的一环。凡是抗议这项规则的人，首先到甲板上跑五十圈再说。', 'char_130_doberm/CN_002');
INSERT INTO `t_operator_voice` VALUES (904, 29, '交谈2', '作为教官，日常教学理应不断在严厉和宽容间寻求平衡。老实说，我觉得，我对芬是不是有些太苛刻了。', 'char_130_doberm/CN_003');
INSERT INTO `t_operator_voice` VALUES (905, 29, '交谈3', '为干员编排小组时，要记得做到人尽其才，让每个人能最大程度地发挥力量。干员的个人能力至关重要，但博士你的判断能力才是最需要考验的。', 'char_130_doberm/CN_004');
INSERT INTO `t_operator_voice` VALUES (906, 29, '晋升后交谈1', '博士，干员的训练，不仅要改善他们的体质，增进他们的能力，还要注重提升他们精神方面的修养。别忘了，他们必须拥有能对抗审讯和种种精神干扰的心理素质。', 'char_130_doberm/CN_005');
INSERT INTO `t_operator_voice` VALUES (907, 29, '晋升后交谈2', '你在找那些新人？他们正在罗德岛甲板上训练。啊，我当然知道这会儿外面是什么天气。', 'char_130_doberm/CN_006');
INSERT INTO `t_operator_voice` VALUES (908, 29, '信赖提升后交谈1', '异常的变化......呵呵，“猎犬”的眼睛是无法被欺瞒的。', 'char_130_doberm/CN_007');
INSERT INTO `t_operator_voice` VALUES (909, 29, '信赖提升后交谈2', '如果你觉得，“只要坐在指挥官的位子上，就可以随便放松懈怠”，我一定会纠正你的想法，博士。', 'char_130_doberm/CN_008');
INSERT INTO `t_operator_voice` VALUES (910, 29, '信赖提升后交谈3', '要与博士共度艰难的时光，我就必须更加严格地要求自己。在这种环境下，想与你一起走得更远，唯一的出路，就只有无止尽地磨练自己的心性。', 'char_130_doberm/CN_009');
INSERT INTO `t_operator_voice` VALUES (911, 29, '闲置', '不许偷懒！', 'char_130_doberm/CN_010');
INSERT INTO `t_operator_voice` VALUES (912, 29, '干员报到', '敬礼！曾属玻利瓦尔国民警备队，我是教官杜宾。长话短说，请把需受训干员的名单给我看一下。', 'char_130_doberm/CN_011');
INSERT INTO `t_operator_voice` VALUES (913, 29, '观看作战记录', '这正是平时训练的成果。', 'char_130_doberm/CN_012');
INSERT INTO `t_operator_voice` VALUES (914, 29, '精英化晋升1', '晋升啊，感谢。我不过是在履行自己的职责。', 'char_130_doberm/CN_013');
INSERT INTO `t_operator_voice` VALUES (915, 29, '精英化晋升2', '我肩负着为罗德岛培养未来人才的重任，因此，为了配得上这份职责，我会尽我所能。', 'char_130_doberm/CN_014');
INSERT INTO `t_operator_voice` VALUES (916, 29, '编入队伍', '了解。', 'char_130_doberm/CN_017');
INSERT INTO `t_operator_voice` VALUES (917, 29, '任命队长', '那么，请给我看一下任务简报。', 'char_130_doberm/CN_018');
INSERT INTO `t_operator_voice` VALUES (918, 29, '行动出发', '通知全体人员，这一次，不是训练！', 'char_130_doberm/CN_019');
INSERT INTO `t_operator_voice` VALUES (919, 29, '行动开始', '审问时间到了。', 'char_130_doberm/CN_020');
INSERT INTO `t_operator_voice` VALUES (920, 29, '选中干员1', '待命中。', 'char_130_doberm/CN_021');
INSERT INTO `t_operator_voice` VALUES (921, 29, '选中干员2', '请给我命令。', 'char_130_doberm/CN_022');
INSERT INTO `t_operator_voice` VALUES (922, 29, '部署1', '了解。', 'char_130_doberm/CN_023');
INSERT INTO `t_operator_voice` VALUES (923, 29, '部署2', '开始追踪。', 'char_130_doberm/CN_024');
INSERT INTO `t_operator_voice` VALUES (924, 29, '作战中1', '你们这些恶徒。', 'char_130_doberm/CN_025');
INSERT INTO `t_operator_voice` VALUES (925, 29, '作战中2', '接招！', 'char_130_doberm/CN_026');
INSERT INTO `t_operator_voice` VALUES (926, 29, '作战中3', '撕裂！', 'char_130_doberm/CN_027');
INSERT INTO `t_operator_voice` VALUES (927, 29, '作战中4', '坚持住！', 'char_130_doberm/CN_028');
INSERT INTO `t_operator_voice` VALUES (928, 29, '4星结束行动', '不管局面有多艰难，只要牢记最基础的战斗方法，关键时刻就能救下自己的命。', 'char_130_doberm/CN_029');
INSERT INTO `t_operator_voice` VALUES (929, 29, '3星结束行动', '磨练好技巧再来吧，废物们。', 'char_130_doberm/CN_030');
INSERT INTO `t_operator_voice` VALUES (930, 29, '非3星结束行动', '虽然作战是成功的，但细节仍有改善的余地。', 'char_130_doberm/CN_031');
INSERT INTO `t_operator_voice` VALUES (931, 29, '行动失败', '这次作战失败，责任由我承担。', 'char_130_doberm/CN_032');
INSERT INTO `t_operator_voice` VALUES (932, 29, '进驻设施', '新人们有点害怕我？这是理所当然的。', 'char_130_doberm/CN_033');
INSERT INTO `t_operator_voice` VALUES (933, 29, '戳一下', '请注意自己的言行举止。', 'char_130_doberm/CN_034');
INSERT INTO `t_operator_voice` VALUES (934, 29, '信赖触摸', '让我心情变好的方法啊......哼，也许，参与一场审讯也可以。', 'char_130_doberm/CN_036');
INSERT INTO `t_operator_voice` VALUES (935, 29, '标题', '明日方舟。', 'char_130_doberm/CN_037');
INSERT INTO `t_operator_voice` VALUES (936, 29, '问候', '早上好，博士。', 'char_130_doberm/CN_042');
INSERT INTO `t_operator_voice` VALUES (937, 30, '任命助理', '博士是不是饿了？我做的饭团可好吃了，拿一两个去吧！', 'char_289_gyuki/CN_001');
INSERT INTO `t_operator_voice` VALUES (938, 30, '交谈1', '我喜欢这样几件事：比试武技，还有啊，踢足球！对啦还有插花。把我当作仅仅是痴迷战斗的古典武者——有这种想法的人，真是太天真了！', 'char_289_gyuki/CN_002');
INSERT INTO `t_operator_voice` VALUES (939, 30, '交谈2', '我的种族以其他种族为食？这谣言，你是从谁那听来的？嘁，那种只敢躲在角落里中伤别人的懦夫，要是被我抓到，可得好好教训教训他！', 'char_289_gyuki/CN_003');
INSERT INTO `t_operator_voice` VALUES (940, 30, '交谈3', '我的身体天生就有自愈的能力。就算是因为战斗受了伤，也很快就能愈合。', 'char_289_gyuki/CN_004');
INSERT INTO `t_operator_voice` VALUES (941, 30, '晋升后交谈1', '博士，罗德岛要举办足球赛了！走吧，你可一定和我一起报名！放心放心，只要我缠丸在场，对手就只有认输的份！博士你只要考虑获奖时摆什么POSE就行啦！', 'char_289_gyuki/CN_005');
INSERT INTO `t_operator_voice` VALUES (942, 30, '晋升后交谈2', '战斗中的我看起来像另一个人？以冷酷的姿态击败所有敌人，眼神中不带丝毫感情？哦豁~那不是非常帅气的吗？真想好好见识一下啊，战斗时的我！', 'char_289_gyuki/CN_006');
INSERT INTO `t_operator_voice` VALUES (943, 30, '信赖提升后交谈1', '缠丸我啊，总是一不小心就弄坏周围的东西，所以我就向花道老师学了插花，试着控制自己的力量。而且，有那么多漂亮的花围着自己，本来就挺让人开心的！', 'char_289_gyuki/CN_007');
INSERT INTO `t_operator_voice` VALUES (944, 30, '信赖提升后交谈2', '说到战斗策略......是的，我完全不擅长呢，所以才总是会陷入危险的战斗吧！不过，博士你的命令每次都能让我及时清醒，有你站在我背后，真好。', 'char_289_gyuki/CN_008');
INSERT INTO `t_operator_voice` VALUES (945, 30, '信赖提升后交谈3', '其实，我是个太容易轻信他人的笨蛋，好几次都被骗的很惨，甚至差点就死掉了。但缠丸我啊，是绝对信任博士的，因为博士你绝对不会背叛我。', 'char_289_gyuki/CN_009');
INSERT INTO `t_operator_voice` VALUES (946, 30, '闲置', '......博士，别睡啊？不行不行，没人和我说话我会无聊死的！', 'char_289_gyuki/CN_010');
INSERT INTO `t_operator_voice` VALUES (947, 30, '干员报到', '缠丸，现在正式加入罗德岛！我来负责战斗，指挥就交给博士你了！', 'char_289_gyuki/CN_011');
INSERT INTO `t_operator_voice` VALUES (948, 30, '观看作战记录', '这样就又可以学到很多战斗技巧了吧。', 'char_289_gyuki/CN_012');
INSERT INTO `t_operator_voice` VALUES (949, 30, '精英化晋升1', '博士，善用我的力量，就是尊重我。', 'char_289_gyuki/CN_013');
INSERT INTO `t_operator_voice` VALUES (950, 30, '精英化晋升2', '博士，请退后。如果全力挥舞的话，我的武器可能会伤到你。但缠丸我，想要为你取得胜利。', 'char_289_gyuki/CN_014');
INSERT INTO `t_operator_voice` VALUES (951, 30, '编入队伍', '哈~各位看起来都挺强的，不如我们先来热身一下？', 'char_289_gyuki/CN_017');
INSERT INTO `t_operator_voice` VALUES (952, 30, '任命队长', '呃，要让我当队长？那不就意味着我没法放手一搏了嘛？', 'char_289_gyuki/CN_018');
INSERT INTO `t_operator_voice` VALUES (953, 30, '行动出发', '各位都准备好大干一场了吧？那还等什么呢！', 'char_289_gyuki/CN_019');
INSERT INTO `t_operator_voice` VALUES (954, 30, '行动开始', '久等了，我的敌人们啊。', 'char_289_gyuki/CN_020');
INSERT INTO `t_operator_voice` VALUES (955, 30, '选中干员1', '我在这里呢。', 'char_289_gyuki/CN_021');
INSERT INTO `t_operator_voice` VALUES (956, 30, '选中干员2', '还要等多久哦？', 'char_289_gyuki/CN_022');
INSERT INTO `t_operator_voice` VALUES (957, 30, '部署1', '有战场的气息。', 'char_289_gyuki/CN_023');
INSERT INTO `t_operator_voice` VALUES (958, 30, '部署2', '让他们颤抖吧。', 'char_289_gyuki/CN_024');
INSERT INTO `t_operator_voice` VALUES (959, 30, '作战中1', '敢站到我面前，有胆量！', 'char_289_gyuki/CN_025');
INSERT INTO `t_operator_voice` VALUES (960, 30, '作战中2', '做好觉悟了吗？', 'char_289_gyuki/CN_026');
INSERT INTO `t_operator_voice` VALUES (961, 30, '作战中3', '就把你连武器一起切碎！', 'char_289_gyuki/CN_027');
INSERT INTO `t_operator_voice` VALUES (962, 30, '作战中4', '倒下吧！', 'char_289_gyuki/CN_028');
INSERT INTO `t_operator_voice` VALUES (963, 30, '4星结束行动', '火海深渊，刀山剑林，太令人满足了！我中意的战场就是这个样子！', 'char_289_gyuki/CN_029');
INSERT INTO `t_operator_voice` VALUES (964, 30, '3星结束行动', '博士，我全都解决了！唔......四周一片狼藉，难道都是我搞的？', 'char_289_gyuki/CN_030');
INSERT INTO `t_operator_voice` VALUES (965, 30, '非3星结束行动', '我去追逃兵咯？不然这场战斗，根本不能算是结束了啊。', 'char_289_gyuki/CN_031');
INSERT INTO `t_operator_voice` VALUES (966, 30, '行动失败', '撤退！我来垫后，小心，别受伤！', 'char_289_gyuki/CN_032');
INSERT INTO `t_operator_voice` VALUES (967, 30, '进驻设施', '唔呃，撞、撞到角了！', 'char_289_gyuki/CN_033');
INSERT INTO `t_operator_voice` VALUES (968, 30, '戳一下', '哈？', 'char_289_gyuki/CN_034');
INSERT INTO `t_operator_voice` VALUES (969, 30, '信赖触摸', '有什么高兴的事？一定要和缠丸我分享哦！', 'char_289_gyuki/CN_036');
INSERT INTO `t_operator_voice` VALUES (970, 30, '标题', '明日方舟。', 'char_289_gyuki/CN_037');
INSERT INTO `t_operator_voice` VALUES (971, 30, '问候', '嗯嗯，我在听呢。', 'char_289_gyuki/CN_042');
INSERT INTO `t_operator_voice` VALUES (972, 31, '任命助理', '要听点音乐吗？我的耳机就借你一会儿吧。', 'char_193_frostl/CN_001');
INSERT INTO `t_operator_voice` VALUES (973, 31, '交谈1', '抱歉，请和我保持一点距离。', 'char_193_frostl/CN_002');
INSERT INTO `t_operator_voice` VALUES (974, 31, '交谈2', '我从没想过，自己会有从事救援工作的这一天。', 'char_193_frostl/CN_003');
INSERT INTO `t_operator_voice` VALUES (975, 31, '交谈3', '挥舞这把斧头的时间，说不定比我识字的时间都长。', 'char_193_frostl/CN_004');
INSERT INTO `t_operator_voice` VALUES (976, 31, '晋升后交谈1', '我喜欢的歌，应该也不怎么流行吧。这些歌只适合独处的时候哼给自己听。', 'char_193_frostl/CN_005');
INSERT INTO `t_operator_voice` VALUES (977, 31, '晋升后交谈2', '不要眨眼。见证这战技的机会，只有这一瞬间。', 'char_193_frostl/CN_006');
INSERT INTO `t_operator_voice` VALUES (978, 31, '信赖提升后交谈1', '喝点吗，我请。工作结束之后，还是稍微放松一会儿比较好。', 'char_193_frostl/CN_007');
INSERT INTO `t_operator_voice` VALUES (979, 31, '信赖提升后交谈2', '直到身边的人接连逝去，我才意识到作为人去活着的可贵。很讽刺吧？呵，我也觉得。', 'char_193_frostl/CN_008');
INSERT INTO `t_operator_voice` VALUES (980, 31, '信赖提升后交谈3', '佣兵不过是种工作，出于什么目的、造成什么后果，并不由我决定。以后，这种“我该担负什么责任”的考量，就交给你了。', 'char_193_frostl/CN_009');
INSERT INTO `t_operator_voice` VALUES (981, 31, '闲置', '哼——哼哼♪哼......哼哼......♪', 'char_193_frostl/CN_010');
INSERT INTO `t_operator_voice` VALUES (982, 31, '干员报到', '霜叶，前佣兵，现在，是你的干员。随意调遣我吧，我什么都能做。', 'char_193_frostl/CN_011');
INSERT INTO `t_operator_voice` VALUES (983, 31, '观看作战记录', '谢谢。', 'char_193_frostl/CN_012');
INSERT INTO `t_operator_voice` VALUES (984, 31, '精英化晋升1', '提上正轨了。', 'char_193_frostl/CN_013');
INSERT INTO `t_operator_voice` VALUES (985, 31, '精英化晋升2', '呼......也不错。久违的充实感。', 'char_193_frostl/CN_014');
INSERT INTO `t_operator_voice` VALUES (986, 31, '编入队伍', '我会履行自己的义务。', 'char_193_frostl/CN_017');
INSERT INTO `t_operator_voice` VALUES (987, 31, '任命队长', '各行其道，各尽其职。', 'char_193_frostl/CN_018');
INSERT INTO `t_operator_voice` VALUES (988, 31, '行动出发', '正好，我也想活动下身体了。', 'char_193_frostl/CN_019');
INSERT INTO `t_operator_voice` VALUES (989, 31, '行动开始', '支离破碎吧——！', 'char_193_frostl/CN_020');
INSERT INTO `t_operator_voice` VALUES (990, 31, '选中干员1', '嗯？', 'char_193_frostl/CN_021');
INSERT INTO `t_operator_voice` VALUES (991, 31, '选中干员2', '有什么命令？', 'char_193_frostl/CN_022');
INSERT INTO `t_operator_voice` VALUES (992, 31, '部署1', '这样就可以了。', 'char_193_frostl/CN_023');
INSERT INTO `t_operator_voice` VALUES (993, 31, '部署2', '唔，可以。', 'char_193_frostl/CN_024');
INSERT INTO `t_operator_voice` VALUES (994, 31, '作战中1', '寒冷，不止我的体液。', 'char_193_frostl/CN_025');
INSERT INTO `t_operator_voice` VALUES (995, 31, '作战中2', '延伸我的锋芒吧——！', 'char_193_frostl/CN_026');
INSERT INTO `t_operator_voice` VALUES (996, 31, '作战中3', '凡有生气，尽都如草。', 'char_193_frostl/CN_027');
INSERT INTO `t_operator_voice` VALUES (997, 31, '作战中4', '让头脑清醒点吧。', 'char_193_frostl/CN_028');
INSERT INTO `t_operator_voice` VALUES (998, 31, '4星结束行动', '战争尽头的荒芒，已不知道经历了多少次。', 'char_193_frostl/CN_029');
INSERT INTO `t_operator_voice` VALUES (999, 31, '3星结束行动', '我记得每一个敌人。一个逃走的也没有。', 'char_193_frostl/CN_030');
INSERT INTO `t_operator_voice` VALUES (1000, 31, '非3星结束行动', '没有损失人手吧。', 'char_193_frostl/CN_031');
INSERT INTO `t_operator_voice` VALUES (1001, 31, '行动失败', '我来断后......！', 'char_193_frostl/CN_032');
INSERT INTO `t_operator_voice` VALUES (1002, 31, '进驻设施', '想要比较......私人一点的空间呢。', 'char_193_frostl/CN_033');
INSERT INTO `t_operator_voice` VALUES (1003, 31, '戳一下', '唔呀。', 'char_193_frostl/CN_034');
INSERT INTO `t_operator_voice` VALUES (1004, 31, '信赖触摸', '一起去喝一杯吧？', 'char_193_frostl/CN_036');
INSERT INTO `t_operator_voice` VALUES (1005, 31, '标题', '明日方舟。', 'char_193_frostl/CN_037');
INSERT INTO `t_operator_voice` VALUES (1006, 31, '问候', '博士。', 'char_193_frostl/CN_042');
INSERT INTO `t_operator_voice` VALUES (1007, 32, '任命助理', '那个，我、我该站在哪里才好？坐，坐下？......那，那我该坐在哪里？', 'char_127_estell/CN_001');
INSERT INTO `t_operator_voice` VALUES (1008, 32, '交谈1', '我的伙伴，就是那只小鸟，最近它总是失踪，那个，不会是不喜欢再和我待在一起了吧......', 'char_127_estell/CN_002');
INSERT INTO `t_operator_voice` VALUES (1009, 32, '交谈2', '我这样子，看起来根本不像是干员吧......有我这样的干员，您也不会高兴的。请放心，我不会说自己属于罗德岛的......', 'char_127_estell/CN_003');
INSERT INTO `t_operator_voice` VALUES (1010, 32, '交谈3', '如您所见，我的头发没法编一些常见的发型，所以就只能像这样把头发放下来......果然我这样，还是不太好吧......', 'char_127_estell/CN_004');
INSERT INTO `t_operator_voice` VALUES (1011, 32, '晋升后交谈1', '我这对变异的角很显眼吧，本来我以为，自己已经习惯被人观看了......果然......还是会觉得不好意思啊......', 'char_127_estell/CN_005');
INSERT INTO `t_operator_voice` VALUES (1012, 32, '晋升后交谈2', '我生活在荒野时认识的朋友们，也渐渐聚集在罗德岛了呢......这样，多多少少，我也不会那么孤单了......我会努力战斗保护大家的。', 'char_127_estell/CN_006');
INSERT INTO `t_operator_voice` VALUES (1013, 32, '信赖提升后交谈1', '其实从小我就一直......一直、梦想成为一个公主......那个，您想笑就笑吧，我、没关系的......', 'char_127_estell/CN_007');
INSERT INTO `t_operator_voice` VALUES (1014, 32, '信赖提升后交谈2', '我一直被敌人“怪胎、怪胎“地叫......但......是啊，我是怪胎，是想要保护博士，想要保护所有人的怪胎。', 'char_127_estell/CN_008');
INSERT INTO `t_operator_voice` VALUES (1015, 32, '信赖提升后交谈3', '那个，总是很活泼的医生，好像和我是同族人吧......虽然我挺想和她说说话的，但是......还是有点害怕她......', 'char_127_estell/CN_009');
INSERT INTO `t_operator_voice` VALUES (1016, 32, '闲置', '这下没有人盯着我看了吧......？呼——终于......', 'char_127_estell/CN_010');
INSERT INTO `t_operator_voice` VALUES (1017, 32, '干员报到', '您、您好......那个，呃，对不起......我的角不小心把您办公室的门捅穿......了......', 'char_127_estell/CN_011');
INSERT INTO `t_operator_voice` VALUES (1018, 32, '观看作战记录', '比现在更强的力量，我真能控制得了吗......？', 'char_127_estell/CN_012');
INSERT INTO `t_operator_voice` VALUES (1019, 32, '精英化晋升1', '晋升是什么......？虽然还不太明白，但我确实帮上您的忙了，是吗？', 'char_127_estell/CN_013');
INSERT INTO `t_operator_voice` VALUES (1020, 32, '精英化晋升2', '多亏博士您，让我有了一点自信......我究竟该做什么，是为了谁拥有这样的力量，现在，我现在好像稍微明白了。', 'char_127_estell/CN_014');
INSERT INTO `t_operator_voice` VALUES (1021, 32, '编入队伍', '请大家别太注意我......', 'char_127_estell/CN_017');
INSERT INTO `t_operator_voice` VALUES (1022, 32, '任命队长', '哇、我......！？真、真的没问题吗......？', 'char_127_estell/CN_018');
INSERT INTO `t_operator_voice` VALUES (1023, 32, '行动出发', '那个，各、各位，要出击了......！下、下令，就是这样的感觉吧？', 'char_127_estell/CN_019');
INSERT INTO `t_operator_voice` VALUES (1024, 32, '行动开始', '不、不会让那些恶徒，得逞的！', 'char_127_estell/CN_020');
INSERT INTO `t_operator_voice` VALUES (1025, 32, '选中干员1', '啊，我吗......？', 'char_127_estell/CN_021');
INSERT INTO `t_operator_voice` VALUES (1026, 32, '选中干员2', '让我来！', 'char_127_estell/CN_022');
INSERT INTO `t_operator_voice` VALUES (1027, 32, '部署1', '我来防守这里的话，应该能吸引到很多敌人......', 'char_127_estell/CN_023');
INSERT INTO `t_operator_voice` VALUES (1028, 32, '部署2', '让敌人感到恐惧，我能做到吧......？', 'char_127_estell/CN_024');
INSERT INTO `t_operator_voice` VALUES (1029, 32, '作战中1', '哈啊！', 'char_127_estell/CN_025');
INSERT INTO `t_operator_voice` VALUES (1030, 32, '作战中2', '这里，不让过！', 'char_127_estell/CN_026');
INSERT INTO `t_operator_voice` VALUES (1031, 32, '作战中3', '我不会放你们离开的！', 'char_127_estell/CN_027');
INSERT INTO `t_operator_voice` VALUES (1032, 32, '作战中4', '请你害怕我吧！', 'char_127_estell/CN_028');
INSERT INTO `t_operator_voice` VALUES (1033, 32, '4星结束行动', '再加把劲......嗯，再加把劲！', 'char_127_estell/CN_029');
INSERT INTO `t_operator_voice` VALUES (1034, 32, '3星结束行动', '您会为我们的战果感到骄傲吗，博士？', 'char_127_estell/CN_030');
INSERT INTO `t_operator_voice` VALUES (1035, 32, '非3星结束行动', '太好了......我们赢了！博士！', 'char_127_estell/CN_031');
INSERT INTO `t_operator_voice` VALUES (1036, 32, '行动失败', '果然......我什么都没做到......', 'char_127_estell/CN_032');
INSERT INTO `t_operator_voice` VALUES (1037, 32, '进驻设施', '哇啊......这里，好宽阔！太好了......', 'char_127_estell/CN_033');
INSERT INTO `t_operator_voice` VALUES (1038, 32, '戳一下', '那个......？', 'char_127_estell/CN_034');
INSERT INTO `t_operator_voice` VALUES (1039, 32, '信赖触摸', '博士总是这么关心我，我非常、非常高兴......', 'char_127_estell/CN_036');
INSERT INTO `t_operator_voice` VALUES (1040, 32, '标题', '明日方舟。', 'char_127_estell/CN_037');
INSERT INTO `t_operator_voice` VALUES (1041, 32, '问候', '博，博士...', 'char_127_estell/CN_042');
INSERT INTO `t_operator_voice` VALUES (1042, 33, '任命助理', '猫猫又增加了，好开心！', 'char_185_frncat/CN_001');
INSERT INTO `t_operator_voice` VALUES (1043, 33, '交谈1', '拉特兰的蛋糕，非常好吃！不过，是不是所有干员都要严格控制体重来着......？呜......', 'char_185_frncat/CN_002');
INSERT INTO `t_operator_voice` VALUES (1044, 33, '交谈2', '手套......这副手套不能摘下来，因为不想让博士看见我的手......只有这个要求，是不行的......对不起......', 'char_185_frncat/CN_003');
INSERT INTO `t_operator_voice` VALUES (1045, 33, '交谈3', '要、要出发了吗？好、好的，请让我准备一下...！', 'char_185_frncat/CN_004');
INSERT INTO `t_operator_voice` VALUES (1046, 33, '晋升后交谈1', '慕斯的尾巴有两条，很奇怪吗？难，难道，我看起来比自己想象的还要奇怪吗？呜......', 'char_185_frncat/CN_005');
INSERT INTO `t_operator_voice` VALUES (1047, 33, '晋升后交谈2', '其实没有发病的时候，我的手不会变成什么奇怪的样子......能像现在这样用手抱一抱猫猫们，我已经很满足了......', 'char_185_frncat/CN_006');
INSERT INTO `t_operator_voice` VALUES (1048, 33, '信赖提升后交谈1', '博士，芒果味和抹茶味的蛋糕，您喜欢哪一种呢？我试着做了一些......咦，您已经吃过了吗？这样啊......', 'char_185_frncat/CN_007');
INSERT INTO `t_operator_voice` VALUES (1049, 33, '信赖提升后交谈2', '博、博士！那位空小姐，是我最喜欢的偶像空小姐吧？！我没认错！为什么她会在罗德岛......那、那个，能请她帮我签个名吗？', 'char_185_frncat/CN_008');
INSERT INTO `t_operator_voice` VALUES (1050, 33, '信赖提升后交谈3', '博士，您知道爱丽榭大道么？嗯嗯，那里是我出生的地方，真想回去看一看啊......嗯？不，我已经不会感到寂寞了，毕竟，您就是我的家人呀！', 'char_185_frncat/CN_009');
INSERT INTO `t_operator_voice` VALUES (1051, 33, '闲置', '呜......博士不理我了......', 'char_185_frncat/CN_010');
INSERT INTO `t_operator_voice` VALUES (1052, 33, '干员报到', '你好......请、请叫我慕斯......那个，我的手，请您不要盯着我的手看......', 'char_185_frncat/CN_011');
INSERT INTO `t_operator_voice` VALUES (1053, 33, '观看作战记录', '我绝对不会忘记这份恩情的！', 'char_185_frncat/CN_012');
INSERT INTO `t_operator_voice` VALUES (1054, 33, '精英化晋升1', '欸？晋、晋升？我还没准备好......', 'char_185_frncat/CN_013');
INSERT INTO `t_operator_voice` VALUES (1055, 33, '精英化晋升2', '博士，我、我知道自己能力有限，所以，我一定把力所能及的事，全都做好！这也是我唯一能报答博士的方式了......', 'char_185_frncat/CN_014');
INSERT INTO `t_operator_voice` VALUES (1056, 33, '编入队伍', '嗯，我会努力的。', 'char_185_frncat/CN_017');
INSERT INTO `t_operator_voice` VALUES (1057, 33, '任命队长', '我一定能做好的，加油，慕斯！', 'char_185_frncat/CN_018');
INSERT INTO `t_operator_voice` VALUES (1058, 33, '行动出发', '我不太起眼，很适合去侦查敌情......', 'char_185_frncat/CN_019');
INSERT INTO `t_operator_voice` VALUES (1059, 33, '行动开始', '侦查结束，现在向您报告敌人的位置。', 'char_185_frncat/CN_020');
INSERT INTO `t_operator_voice` VALUES (1060, 33, '选中干员1', '我准备好了！', 'char_185_frncat/CN_021');
INSERT INTO `t_operator_voice` VALUES (1061, 33, '选中干员2', '嗯！', 'char_185_frncat/CN_022');
INSERT INTO `t_operator_voice` VALUES (1062, 33, '部署1', '慕斯，不会害怕......！', 'char_185_frncat/CN_023');
INSERT INTO `t_operator_voice` VALUES (1063, 33, '部署2', '从这里开始，是慕斯的战斗了......！', 'char_185_frncat/CN_024');
INSERT INTO `t_operator_voice` VALUES (1064, 33, '作战中1', '呀！', 'char_185_frncat/CN_025');
INSERT INTO `t_operator_voice` VALUES (1065, 33, '作战中2', '离、离我远点！', 'char_185_frncat/CN_026');
INSERT INTO `t_operator_voice` VALUES (1066, 33, '作战中3', '我也可以战斗的......！', 'char_185_frncat/CN_027');
INSERT INTO `t_operator_voice` VALUES (1067, 33, '作战中4', '你、你绝对会后悔的......！', 'char_185_frncat/CN_028');
INSERT INTO `t_operator_voice` VALUES (1068, 33, '4星结束行动', '就算是这样残酷的战斗，慕斯也坚持下来了呢......', 'char_185_frncat/CN_029');
INSERT INTO `t_operator_voice` VALUES (1069, 33, '3星结束行动', '呼——啊，对不起，虽然我不是很喜欢战斗，但是......', 'char_185_frncat/CN_030');
INSERT INTO `t_operator_voice` VALUES (1070, 33, '非3星结束行动', '给、给大家拖后腿了吗？', 'char_185_frncat/CN_031');
INSERT INTO `t_operator_voice` VALUES (1071, 33, '行动失败', '我还能......再坚持一下......', 'char_185_frncat/CN_032');
INSERT INTO `t_operator_voice` VALUES (1072, 33, '进驻设施', '你、你好，我进来了......', 'char_185_frncat/CN_033');
INSERT INTO `t_operator_voice` VALUES (1073, 33, '戳一下', '呀！', 'char_185_frncat/CN_034');
INSERT INTO `t_operator_voice` VALUES (1074, 33, '信赖触摸', '博士，我、我实在忍不住了......能给我抓板吗？呜呜呜......', 'char_185_frncat/CN_036');
INSERT INTO `t_operator_voice` VALUES (1075, 33, '标题', '明日方舟。', 'char_185_frncat/CN_037');
INSERT INTO `t_operator_voice` VALUES (1076, 33, '问候', '你好，博士。', 'char_185_frncat/CN_042');
INSERT INTO `t_operator_voice` VALUES (1077, 34, '任命助理', '啊~啊~~要让我守在您身边吗？这样的指示~真是令我心跳加速呢~~', 'char_237_gravel/CN_001');
INSERT INTO `t_operator_voice` VALUES (1078, 34, '交谈1', '......真是有意思呢。', 'char_237_gravel/CN_002');
INSERT INTO `t_operator_voice` VALUES (1079, 34, '交谈2', '不用管我哦，请继续您的工作就好~', 'char_237_gravel/CN_003');
INSERT INTO `t_operator_voice` VALUES (1080, 34, '交谈3', '嗯？我在看什么？在看你哟。', 'char_237_gravel/CN_004');
INSERT INTO `t_operator_voice` VALUES (1081, 34, '晋升后交谈1', '如果您需要的话，我也会尽力去变强的~', 'char_237_gravel/CN_005');
INSERT INTO `t_operator_voice` VALUES (1082, 34, '晋升后交谈2', '嘻嘻......这样一来，您就不必担心会受到突然袭击了哦~因为——我可一直在你的身边呐~', 'char_237_gravel/CN_006');
INSERT INTO `t_operator_voice` VALUES (1083, 34, '信赖提升后交谈1', '您也稍微对我有些兴趣了吗？我可是一直对您很感兴趣呢。', 'char_237_gravel/CN_007');
INSERT INTO `t_operator_voice` VALUES (1084, 34, '信赖提升后交谈2', '最近这段时间承蒙您照顾了，以卡西米尔骑士之名起誓，必定以身回报此恩......唔，不习惯？我怎么说也是正规骑士出身，说辞总是学过几句的呢。', 'char_237_gravel/CN_008');
INSERT INTO `t_operator_voice` VALUES (1085, 34, '信赖提升后交谈3', '自从我被拐到卡西米尔后，已经很久没人对我这样温柔了。所以您若是不嫌弃，以骑士的荣耀起誓，我砾，不，我塞诺蜜将永远侍奉于您左右。', 'char_237_gravel/CN_009');
INSERT INTO `t_operator_voice` VALUES (1086, 34, '闲置', '我守在这里，有没有让你感到心跳加速呢？', 'char_237_gravel/CN_010');
INSERT INTO `t_operator_voice` VALUES (1087, 34, '干员报到', '啾——嗯？怎么脸红了？这只是初见的问候，可没什么其他的意思哦。那么，卡西米尔骑士砾，就请您多有包涵啦，嘻嘻。', 'char_237_gravel/CN_011');
INSERT INTO `t_operator_voice` VALUES (1088, 34, '观看作战记录', '罗德岛的战术都是博士您安排的吗？这就令人很感兴趣了呢......', 'char_237_gravel/CN_012');
INSERT INTO `t_operator_voice` VALUES (1089, 34, '精英化晋升1', '这是博士您的认可吗？真开心！！', 'char_237_gravel/CN_013');
INSERT INTO `t_operator_voice` VALUES (1090, 34, '精英化晋升2', '这是意味着您很需要我对吧？嘻嘻......', 'char_237_gravel/CN_014');
INSERT INTO `t_operator_voice` VALUES (1091, 34, '编入队伍', '一起行动吗？嘻嘻......', 'char_237_gravel/CN_017');
INSERT INTO `t_operator_voice` VALUES (1092, 34, '任命队长', '被选中了呢~', 'char_237_gravel/CN_018');
INSERT INTO `t_operator_voice` VALUES (1093, 34, '行动出发', '我们一起去吧~', 'char_237_gravel/CN_019');
INSERT INTO `t_operator_voice` VALUES (1094, 34, '行动开始', '嘻嘻......', 'char_237_gravel/CN_020');
INSERT INTO `t_operator_voice` VALUES (1095, 34, '选中干员1', '嗯？', 'char_237_gravel/CN_021');
INSERT INTO `t_operator_voice` VALUES (1096, 34, '选中干员2', '您有指示？', 'char_237_gravel/CN_022');
INSERT INTO `t_operator_voice` VALUES (1097, 34, '部署1', '就这里了！', 'char_237_gravel/CN_023');
INSERT INTO `t_operator_voice` VALUES (1098, 34, '部署2', '是个好阵地。', 'char_237_gravel/CN_024');
INSERT INTO `t_operator_voice` VALUES (1099, 34, '作战中1', '我是你的影子。', 'char_237_gravel/CN_025');
INSERT INTO `t_operator_voice` VALUES (1100, 34, '作战中2', '嘻嘻......', 'char_237_gravel/CN_026');
INSERT INTO `t_operator_voice` VALUES (1101, 34, '作战中3', '你走得了吗？', 'char_237_gravel/CN_027');
INSERT INTO `t_operator_voice` VALUES (1102, 34, '作战中4', '鼠群，聚集起来吧！', 'char_237_gravel/CN_028');
INSERT INTO `t_operator_voice` VALUES (1103, 34, '4星结束行动', '您的战术真的是完美无缺呢~', 'char_237_gravel/CN_029');
INSERT INTO `t_operator_voice` VALUES (1104, 34, '3星结束行动', '刀还没染红就结束了，嘻嘻......', 'char_237_gravel/CN_030');
INSERT INTO `t_operator_voice` VALUES (1105, 34, '非3星结束行动', '逃吧，你们还能逃多远呢。', 'char_237_gravel/CN_031');
INSERT INTO `t_operator_voice` VALUES (1106, 34, '行动失败', '对不起，我......又失误了吗......', 'char_237_gravel/CN_032');
INSERT INTO `t_operator_voice` VALUES (1107, 34, '进驻设施', '这个房间，好像有很多可以躲藏的地方呢~', 'char_237_gravel/CN_033');
INSERT INTO `t_operator_voice` VALUES (1108, 34, '戳一下', '嘻嘻......', 'char_237_gravel/CN_034');
INSERT INTO `t_operator_voice` VALUES (1109, 34, '信赖触摸', '是的，我在这里，一直都在呢......', 'char_237_gravel/CN_036');
INSERT INTO `t_operator_voice` VALUES (1110, 34, '标题', '明日方舟。', 'char_237_gravel/CN_037');
INSERT INTO `t_operator_voice` VALUES (1111, 34, '问候', '今天还好吗，博士~', 'char_237_gravel/CN_042');
INSERT INTO `t_operator_voice` VALUES (1112, 35, '任命助理', '博士，你没忘记带什么东西吧~', 'char_236_rope/CN_001');
INSERT INTO `t_operator_voice` VALUES (1113, 35, '交谈1', '从别人口袋里拿东西来填饱肚子？有什么办法，我就是这样长大的，不然我早就饿死了。', 'char_236_rope/CN_002');
INSERT INTO `t_operator_voice` VALUES (1114, 35, '交谈2', '钩索想要耐用，绳子才是关键。嗯？你问我为什么喜欢用钩索？你去徒手爬爬楼不就知道了？', 'char_236_rope/CN_003');
INSERT INTO `t_operator_voice` VALUES (1115, 35, '交谈3', '是的是的，我会好好听从博士教导的，我能回宿舍了吗，一会还有事呢。好啦好啦！别说了！救命！', 'char_236_rope/CN_004');
INSERT INTO `t_operator_voice` VALUES (1116, 35, '晋升后交谈1', '如果不是你们罗德岛，我可能还在蹲号子。你们是怎么知道我的？唔？从哪里听说了我的身手吗？', 'char_236_rope/CN_005');
INSERT INTO `t_operator_voice` VALUES (1117, 35, '晋升后交谈2', '其实在贫民窟活下来有很多方式，只不过——活不下来的更多。说实在的，我可没得选，希望你能明白这点。', 'char_236_rope/CN_006');
INSERT INTO `t_operator_voice` VALUES (1118, 35, '信赖提升后交谈1', '父母把我赶出家门，让我流落街头自生自灭。比起那样的地方，当然是在罗德岛更加快活些。', 'char_236_rope/CN_007');
INSERT INTO `t_operator_voice` VALUES (1119, 35, '信赖提升后交谈2', '有许多人生了这种病，我也觉得自己要没救了，还好你们找到我......说实在的，博士，你会帮我治好的吧？不能？嘛，也没差。', 'char_236_rope/CN_008');
INSERT INTO `t_operator_voice` VALUES (1120, 35, '信赖提升后交谈3', '以前吃饭上顿不接下顿，现在能天天吃到舒服，不得不说，罗德岛还是不错的。哼......你想听我谢谢你吗？算了吧。哎呀你已经听过了！', 'char_236_rope/CN_009');
INSERT INTO `t_operator_voice` VALUES (1121, 35, '闲置', '啊这椅子，可恶！有，有点舒服！', 'char_236_rope/CN_010');
INSERT INTO `t_operator_voice` VALUES (1122, 35, '干员报到', '我叫暗索，很高兴认识你。啊对了，你的饭卡还在这里，别忘记拿了。唔？为什么在我这儿？这是秘·密·哦。', 'char_236_rope/CN_011');
INSERT INTO `t_operator_voice` VALUES (1123, 35, '观看作战记录', '看这东西真的管用么？', 'char_236_rope/CN_012');
INSERT INTO `t_operator_voice` VALUES (1124, 35, '精英化晋升1', '工钱啦，工钱。比起这个，还是工钱更实在。', 'char_236_rope/CN_013');
INSERT INTO `t_operator_voice` VALUES (1125, 35, '精英化晋升2', '好啦，收了你那么多钱，我也该认真干活咯！', 'char_236_rope/CN_014');
INSERT INTO `t_operator_voice` VALUES (1126, 35, '编入队伍', '好、好，来了~', 'char_236_rope/CN_017');
INSERT INTO `t_operator_voice` VALUES (1127, 35, '任命队长', '当队长？以前可是要拿额外工钱的，看在你的面子上就不强求啦。', 'char_236_rope/CN_018');
INSERT INTO `t_operator_voice` VALUES (1128, 35, '行动出发', '要提前勘察好地图和路线哦，这可是经验之谈。', 'char_236_rope/CN_019');
INSERT INTO `t_operator_voice` VALUES (1129, 35, '行动开始', '那么，敌人身上会有什么好东西呢？', 'char_236_rope/CN_020');
INSERT INTO `t_operator_voice` VALUES (1130, 35, '选中干员1', '正面对决可不适合我啊。', 'char_236_rope/CN_021');
INSERT INTO `t_operator_voice` VALUES (1131, 35, '选中干员2', '运用好我的长处，拜托了~', 'char_236_rope/CN_022');
INSERT INTO `t_operator_voice` VALUES (1132, 35, '部署1', '偷偷摸摸~', 'char_236_rope/CN_023');
INSERT INTO `t_operator_voice` VALUES (1133, 35, '部署2', '这是个好位置吗？', 'char_236_rope/CN_024');
INSERT INTO `t_operator_voice` VALUES (1134, 35, '作战中1', '啊哈！', 'char_236_rope/CN_025');
INSERT INTO `t_operator_voice` VALUES (1135, 35, '作战中2', '来来来~', 'char_236_rope/CN_026');
INSERT INTO `t_operator_voice` VALUES (1136, 35, '作战中3', '嘿——！', 'char_236_rope/CN_027');
INSERT INTO `t_operator_voice` VALUES (1137, 35, '作战中4', '被这钩子勾中可就跑不了了！', 'char_236_rope/CN_028');
INSERT INTO `t_operator_voice` VALUES (1138, 35, '4星结束行动', '这一场可比平时刺激多了，当然，收入也......哼哼~', 'char_236_rope/CN_029');
INSERT INTO `t_operator_voice` VALUES (1139, 35, '3星结束行动', '有些好东西，留在敌人身上也是种浪费~你说呢？', 'char_236_rope/CN_030');
INSERT INTO `t_operator_voice` VALUES (1140, 35, '非3星结束行动', '跑了几个人？那就别追了吧，说不定能改过自新呢？', 'char_236_rope/CN_031');
INSERT INTO `t_operator_voice` VALUES (1141, 35, '行动失败', '......喂喂，还是和我一起先溜吧？保命要紧啊！', 'char_236_rope/CN_032');
INSERT INTO `t_operator_voice` VALUES (1142, 35, '进驻设施', '这个房间有没有通风管道？没什么，就问下。', 'char_236_rope/CN_033');
INSERT INTO `t_operator_voice` VALUES (1143, 35, '戳一下', '呃，有谁来抓我了吗！', 'char_236_rope/CN_034');
INSERT INTO `t_operator_voice` VALUES (1144, 35, '信赖触摸', '其实我也想学一学如何做个普通的女孩子的。可是吧，唉，那样是不是不太适合我呢？', 'char_236_rope/CN_036');
INSERT INTO `t_operator_voice` VALUES (1145, 35, '标题', '明日方舟。', 'char_236_rope/CN_037');
INSERT INTO `t_operator_voice` VALUES (1146, 35, '问候', '嗨，博士。', 'char_236_rope/CN_042');
INSERT INTO `t_operator_voice` VALUES (1147, 36, '任命助理', '那个，您提交给凯尔希医生的报告书，最后几处笔迹已经乱七八糟了......请别再熬夜了......', 'char_117_myrrh/CN_001');
INSERT INTO `t_operator_voice` VALUES (1148, 36, '交谈1', '这里到处都是没见过的东西呢......！啊，东张西望好像不太好，对不起......', 'char_117_myrrh/CN_002');
INSERT INTO `t_operator_voice` VALUES (1149, 36, '交谈2', '虽然草药确实很苦，但还是希望大家更关注它的药效......', 'char_117_myrrh/CN_003');
INSERT INTO `t_operator_voice` VALUES (1150, 36, '交谈3', '那个，博士，哈默妮的病情，还没有好转吗......嗯，我知道了，我工作回来就去照顾她。', 'char_117_myrrh/CN_004');
INSERT INTO `t_operator_voice` VALUES (1151, 36, '晋升后交谈1', '我的草药很苦，不受欢迎......呜呜......但我只会调配苦味的药......您说多听取下大家的意见比较好？我、我能行吗？我不太擅长说话......但我会努力试试的。', 'char_117_myrrh/CN_005');
INSERT INTO `t_operator_voice` VALUES (1152, 36, '晋升后交谈2', '小时候，我和哈默妮常常跑去各种地方探险。嗯，是的，我们是在城区的废墟中长大的，那时候的我们，非常想看一看外面的世界......', 'char_117_myrrh/CN_006');
INSERT INTO `t_operator_voice` VALUES (1153, 36, '信赖提升后交谈1', '哈默妮是我的第一个，也是最好的朋友......可是，她也是因为我，变成了现在的样子......她因为矿石病而缩短的生命，我一定要为她夺回来。', 'char_117_myrrh/CN_007');
INSERT INTO `t_operator_voice` VALUES (1154, 36, '信赖提升后交谈2', '听了调香师小姐的建议，我在草药中混合了香料，果然赢得了大家的喜爱！拿出勇气来听取大家的意见，真是太好了......', 'char_117_myrrh/CN_008');
INSERT INTO `t_operator_voice` VALUES (1155, 36, '信赖提升后交谈3', '哈默妮说，最近的我，变得比以前更有精神了，您也这样觉得吗？一定是因为有博士及时指点我，如果没有您，我该怎么办呢......', 'char_117_myrrh/CN_009');
INSERT INTO `t_operator_voice` VALUES (1156, 36, '闲置', '博士......是不是睡着了？这下可以仔细观察博士的办公室了呢......', 'char_117_myrrh/CN_010');
INSERT INTO `t_operator_voice` VALUES (1157, 36, '干员报到', '那个，我是草药师末药.....这个代号是凯尔希医生为我指定的，据说，它指的是“生命的短暂”，医生说，她希望我要牢记这个寓意......', 'char_117_myrrh/CN_011');
INSERT INTO `t_operator_voice` VALUES (1158, 36, '观看作战记录', '非常感谢......！这样就能做出更好的药了......！', 'char_117_myrrh/CN_012');
INSERT INTO `t_operator_voice` VALUES (1159, 36, '精英化晋升1', '这就叫做......晋升？是这样的吗......？职位什么的，我还不是很懂......', 'char_117_myrrh/CN_013');
INSERT INTO `t_operator_voice` VALUES (1160, 36, '精英化晋升2', '以前常常和哈默妮一起四处探索......这样的秘境和废墟。越是危险的地方，就越能收集罕见的药材。', 'char_117_myrrh/CN_014');
INSERT INTO `t_operator_voice` VALUES (1161, 36, '编入队伍', '那个，我尽量不拖大家的后腿......', 'char_117_myrrh/CN_017');
INSERT INTO `t_operator_voice` VALUES (1162, 36, '任命队长', '就从努力直视大家的眼睛开始......', 'char_117_myrrh/CN_018');
INSERT INTO `t_operator_voice` VALUES (1163, 36, '行动出发', '啊，博士，我要出发了。', 'char_117_myrrh/CN_019');
INSERT INTO `t_operator_voice` VALUES (1164, 36, '行动开始', '各位，我不会让你们受伤的......！', 'char_117_myrrh/CN_020');
INSERT INTO `t_operator_voice` VALUES (1165, 36, '选中干员1', '我在。', 'char_117_myrrh/CN_021');
INSERT INTO `t_operator_voice` VALUES (1166, 36, '选中干员2', '来了。', 'char_117_myrrh/CN_022');
INSERT INTO `t_operator_voice` VALUES (1167, 36, '部署1', '我要小心一点。', 'char_117_myrrh/CN_023');
INSERT INTO `t_operator_voice` VALUES (1168, 36, '部署2', '有没有需要治疗的人呢？', 'char_117_myrrh/CN_024');
INSERT INTO `t_operator_voice` VALUES (1169, 36, '作战中1', '要、要扔得准一点......', 'char_117_myrrh/CN_025');
INSERT INTO `t_operator_voice` VALUES (1170, 36, '作战中2', '药来了！', 'char_117_myrrh/CN_026');
INSERT INTO `t_operator_voice` VALUES (1171, 36, '作战中3', '这是药，请收下！', 'char_117_myrrh/CN_027');
INSERT INTO `t_operator_voice` VALUES (1172, 36, '作战中4', '没关系，可以治好的......！', 'char_117_myrrh/CN_028');
INSERT INTO `t_operator_voice` VALUES (1173, 36, '4星结束行动', '赢了？真的吗？这么严酷的战斗都能......博士真的好厉害......！', 'char_117_myrrh/CN_029');
INSERT INTO `t_operator_voice` VALUES (1174, 36, '3星结束行动', '大家，这次的药效......算了，回去再问吧......', 'char_117_myrrh/CN_030');
INSERT INTO `t_operator_voice` VALUES (1175, 36, '非3星结束行动', '唔唔，手忙脚乱的......没犯大错真是太好了......', 'char_117_myrrh/CN_031');
INSERT INTO `t_operator_voice` VALUES (1176, 36, '行动失败', '药瓶......药瓶全都碎掉了.....', 'char_117_myrrh/CN_032');
INSERT INTO `t_operator_voice` VALUES (1177, 36, '进驻设施', '唔，我觉得衣服上还残留着草药的味道......', 'char_117_myrrh/CN_033');
INSERT INTO `t_operator_voice` VALUES (1178, 36, '戳一下', '唔哇！', 'char_117_myrrh/CN_034');
INSERT INTO `t_operator_voice` VALUES (1179, 36, '信赖触摸', '博士，那个，最近我调制了安定情绪的汤药。对身体是有好处的，只是确实有点苦......', 'char_117_myrrh/CN_036');
INSERT INTO `t_operator_voice` VALUES (1180, 36, '标题', '明日方舟。', 'char_117_myrrh/CN_037');
INSERT INTO `t_operator_voice` VALUES (1181, 36, '问候', '早、早上好，博士。', 'char_117_myrrh/CN_042');
INSERT INTO `t_operator_voice` VALUES (1182, 37, '任命助理', '休息的时候就该好好放松，对吧？不过，你现在得接受治疗。', 'char_187_ccheal/CN_001');
INSERT INTO `t_operator_voice` VALUES (1183, 37, '交谈1', '哈？温柔可爱？那不是护士吗？我可是专业的治疗者，对治疗之外的事情没有兴趣。', 'char_187_ccheal/CN_002');
INSERT INTO `t_operator_voice` VALUES (1184, 37, '交谈2', '不知道为什么，来这里以后，凯尔希医生总是告诫我，要好好对待患者。', 'char_187_ccheal/CN_003');
INSERT INTO `t_operator_voice` VALUES (1185, 37, '交谈3', '一般的医生，总是在烦恼要怎么保护好自己，但我的烦恼是......怎么才能让患者不害怕我。', 'char_187_ccheal/CN_004');
INSERT INTO `t_operator_voice` VALUES (1186, 37, '晋升后交谈1', '接受治疗时，认真听我说话！看我这里！举起双手！不许乱动！', 'char_187_ccheal/CN_005');
INSERT INTO `t_operator_voice` VALUES (1187, 37, '晋升后交谈2', '医生和士兵，救人者和杀生者，人们都觉得这两种人做的事正好相反，不过我倒觉得这两者都一样，都是牺牲者。', 'char_187_ccheal/CN_006');
INSERT INTO `t_operator_voice` VALUES (1188, 37, '信赖提升后交谈1', '啊？我没对别人动粗啊，就是想和他们寒暄寒暄。你看，就像这样......就是一种用肢体动作触碰对方，拉近彼此距离的行为嘛......', 'char_187_ccheal/CN_007');
INSERT INTO `t_operator_voice` VALUES (1189, 37, '信赖提升后交谈2', '有点怀念故乡了......虽然当医生是我自己选的，但完全没想到会变成现在这样。人生的岔路，还真是让我走上了啊。', 'char_187_ccheal/CN_008');
INSERT INTO `t_operator_voice` VALUES (1190, 37, '信赖提升后交谈3', '不管博士还是我，都是走在没人走过的路上的旅人哪。就因为这样，所以咱们才能互相理解吧。', 'char_187_ccheal/CN_009');
INSERT INTO `t_operator_voice` VALUES (1191, 37, '闲置', '这家伙，睡着了吗？哈哈~', 'char_187_ccheal/CN_010');
INSERT INTO `t_operator_voice` VALUES (1192, 37, '干员报到', '我是医疗术师嘉维尔。先说好了，对我，可不能以貌取人，明白吗？', 'char_187_ccheal/CN_011');
INSERT INTO `t_operator_voice` VALUES (1193, 37, '观看作战记录', '嘁，我还以为是要上格斗课呢。', 'char_187_ccheal/CN_012');
INSERT INTO `t_operator_voice` VALUES (1194, 37, '精英化晋升1', '这家伙那家伙的都害怕我给他们治疗，只有你一个人接受我，多谢啦。', 'char_187_ccheal/CN_013');
INSERT INTO `t_operator_voice` VALUES (1195, 37, '精英化晋升2', '好了玩耍到此为止，接下来我就认真一下！', 'char_187_ccheal/CN_014');
INSERT INTO `t_operator_voice` VALUES (1196, 37, '编入队伍', '后方就交给我，你们只管往前冲！', 'char_187_ccheal/CN_017');
INSERT INTO `t_operator_voice` VALUES (1197, 37, '任命队长', '好！一个个的都排好队！', 'char_187_ccheal/CN_018');
INSERT INTO `t_operator_voice` VALUES (1198, 37, '行动出发', '好，各位都冷静点。', 'char_187_ccheal/CN_019');
INSERT INTO `t_operator_voice` VALUES (1199, 37, '行动开始', '来了！', 'char_187_ccheal/CN_020');
INSERT INTO `t_operator_voice` VALUES (1200, 37, '选中干员1', '开始吧。', 'char_187_ccheal/CN_021');
INSERT INTO `t_operator_voice` VALUES (1201, 37, '选中干员2', '要我做什么好？', 'char_187_ccheal/CN_022');
INSERT INTO `t_operator_voice` VALUES (1202, 37, '部署1', '准备治疗！', 'char_187_ccheal/CN_023');
INSERT INTO `t_operator_voice` VALUES (1203, 37, '部署2', '需要治疗的家伙站出来！', 'char_187_ccheal/CN_024');
INSERT INTO `t_operator_voice` VALUES (1204, 37, '作战中1', '想被治疗，先到我够得着的地方来！', 'char_187_ccheal/CN_025');
INSERT INTO `t_operator_voice` VALUES (1205, 37, '作战中2', '干什么呢你！', 'char_187_ccheal/CN_026');
INSERT INTO `t_operator_voice` VALUES (1206, 37, '作战中3', '振作起来啊！', 'char_187_ccheal/CN_027');
INSERT INTO `t_operator_voice` VALUES (1207, 37, '作战中4', '现在就来救你！', 'char_187_ccheal/CN_028');
INSERT INTO `t_operator_voice` VALUES (1208, 37, '4星结束行动', '就这种程度的对手，轻轻松松啊。', 'char_187_ccheal/CN_029');
INSERT INTO `t_operator_voice` VALUES (1209, 37, '3星结束行动', '那些家伙也太弱了，我还想着，能拿这把杖多敲打他们几下咧。', 'char_187_ccheal/CN_030');
INSERT INTO `t_operator_voice` VALUES (1210, 37, '非3星结束行动', '你们，没接受过应急医疗训练嘛！', 'char_187_ccheal/CN_031');
INSERT INTO `t_operator_voice` VALUES (1211, 37, '行动失败', '可恶......别以为我就这么放弃了。', 'char_187_ccheal/CN_032');
INSERT INTO `t_operator_voice` VALUES (1212, 37, '进驻设施', '看看都有哪些有趣的家伙在啊？', 'char_187_ccheal/CN_033');
INSERT INTO `t_operator_voice` VALUES (1213, 37, '戳一下', '喂，快住手！', 'char_187_ccheal/CN_034');
INSERT INTO `t_operator_voice` VALUES (1214, 37, '信赖触摸', '想让我老实听话？不 可 能！', 'char_187_ccheal/CN_036');
INSERT INTO `t_operator_voice` VALUES (1215, 37, '标题', '明日方舟。', 'char_187_ccheal/CN_037');
INSERT INTO `t_operator_voice` VALUES (1216, 37, '问候', '哟，博士。', 'char_187_ccheal/CN_042');
INSERT INTO `t_operator_voice` VALUES (1217, 38, '任命助理', '要好好听我的话哦，博士。', 'char_181_flower/CN_001');
INSERT INTO `t_operator_voice` VALUES (1218, 38, '交谈1', '这个香气，怎么样？是不是有种，被治愈了的感觉？多亏玫兰莎给我的样品，我才获得了这种灵感。', 'char_181_flower/CN_002');
INSERT INTO `t_operator_voice` VALUES (1219, 38, '交谈2', '谢谢您帮我建设花卉温室，我想，这里会是大家都能放松休息的好地方。', 'char_181_flower/CN_003');
INSERT INTO `t_operator_voice` VALUES (1220, 38, '交谈3', '啊，您醒了吗？是不是一点疲惫都感觉不到了？看来我新调制的香薰起了作用。毕竟，这也算是我的自信之作呢。', 'char_181_flower/CN_004');
INSERT INTO `t_operator_voice` VALUES (1221, 38, '晋升后交谈1', '水仙、燕子花、长寿花、波罗尼——这些用来培育花朵的种子，我总是会随身携带的哦。', 'char_181_flower/CN_005');
INSERT INTO `t_operator_voice` VALUES (1222, 38, '晋升后交谈2', '哎？您问这孩子吗？虽然它还很小，不过，她可总能在调制过程中帮上大忙呢。', 'char_181_flower/CN_006');
INSERT INTO `t_operator_voice` VALUES (1223, 38, '信赖提升后交谈1', '蚂蚁们的社会关系，是依靠气味来维持的吧？我觉得调香师和顾客的关系也是如此。', 'char_181_flower/CN_007');
INSERT INTO `t_operator_voice` VALUES (1224, 38, '信赖提升后交谈2', '最近您经常来温室做客呢......哎呀，您的袖口，沾着些花粉哟？好，已经帮您擦掉了~', 'char_181_flower/CN_008');
INSERT INTO `t_operator_voice` VALUES (1225, 38, '信赖提升后交谈3', '您好像睡了很久呢，这次，又做了什么样的梦呢？', 'char_181_flower/CN_009');
INSERT INTO `t_operator_voice` VALUES (1226, 38, '闲置', '嗯，博士？哎，薰衣草的效果，超乎我的意料呢......', 'char_181_flower/CN_010');
INSERT INTO `t_operator_voice` VALUES (1227, 38, '干员报到', '初次见面，博士。我是调香师莱娜。您听说过调香师这种职业吗？', 'char_181_flower/CN_011');
INSERT INTO `t_operator_voice` VALUES (1228, 38, '观看作战记录', '就拜托您检验我的成果了。', 'char_181_flower/CN_012');
INSERT INTO `t_operator_voice` VALUES (1229, 38, '精英化晋升1', '有关晋升的消息......您是特意来向我传达这个的吗？真是不好意思，博士......啊，抱歉，请稍等。没事，只是您的领角皱了，我稍微整理了一下~', 'char_181_flower/CN_013');
INSERT INTO `t_operator_voice` VALUES (1230, 38, '精英化晋升2', '其实我不太适合那些蛮不讲理的战斗方式。不过，如果是为了博士，我也是会去努力适应的。', 'char_181_flower/CN_014');
INSERT INTO `t_operator_voice` VALUES (1231, 38, '编入队伍', '非常乐意。', 'char_181_flower/CN_017');
INSERT INTO `t_operator_voice` VALUES (1232, 38, '任命队长', '调香与治疗，无论哪一项工作，我都会让博士您满意的。', 'char_181_flower/CN_018');
INSERT INTO `t_operator_voice` VALUES (1233, 38, '行动出发', '大家，千万要注意安全。', 'char_181_flower/CN_019');
INSERT INTO `t_operator_voice` VALUES (1234, 38, '行动开始', '这会是一场怎样的战斗呢。', 'char_181_flower/CN_020');
INSERT INTO `t_operator_voice` VALUES (1235, 38, '选中干员1', '嗯。', 'char_181_flower/CN_021');
INSERT INTO `t_operator_voice` VALUES (1236, 38, '选中干员2', '我没问题的。', 'char_181_flower/CN_022');
INSERT INTO `t_operator_voice` VALUES (1237, 38, '部署1', '这就是战场啊。', 'char_181_flower/CN_023');
INSERT INTO `t_operator_voice` VALUES (1238, 38, '部署2', '我不会害怕的。', 'char_181_flower/CN_024');
INSERT INTO `t_operator_voice` VALUES (1239, 38, '作战中1', '恢复神智吧。', 'char_181_flower/CN_025');
INSERT INTO `t_operator_voice` VALUES (1240, 38, '作战中2', '打起精神来。', 'char_181_flower/CN_026');
INSERT INTO `t_operator_voice` VALUES (1241, 38, '作战中3', '喜欢这种香味吗？', 'char_181_flower/CN_027');
INSERT INTO `t_operator_voice` VALUES (1242, 38, '作战中4', '冷静一些哪。', 'char_181_flower/CN_028');
INSERT INTO `t_operator_voice` VALUES (1243, 38, '4星结束行动', '我一直都相信啊，只要是博士来担任指挥的话，我们就一定会获胜的。', 'char_181_flower/CN_029');
INSERT INTO `t_operator_voice` VALUES (1244, 38, '3星结束行动', '真是太好了，博士。', 'char_181_flower/CN_030');
INSERT INTO `t_operator_voice` VALUES (1245, 38, '非3星结束行动', '大家辛苦了，请好好休息下吧。', 'char_181_flower/CN_031');
INSERT INTO `t_operator_voice` VALUES (1246, 38, '行动失败', '虽然没能赢下战斗确实有些遗憾，但我是不会气馁的。', 'char_181_flower/CN_032');
INSERT INTO `t_operator_voice` VALUES (1247, 38, '进驻设施', '还是博士的身边，最适合我呢。', 'char_181_flower/CN_033');
INSERT INTO `t_operator_voice` VALUES (1248, 38, '戳一下', '哎呀。', 'char_181_flower/CN_034');
INSERT INTO `t_operator_voice` VALUES (1249, 38, '信赖触摸', '博士身上......有着美妙的香气呢。', 'char_181_flower/CN_036');
INSERT INTO `t_operator_voice` VALUES (1250, 38, '标题', '明日方舟。', 'char_181_flower/CN_037');
INSERT INTO `t_operator_voice` VALUES (1251, 38, '问候', '博士，您好。', 'char_181_flower/CN_042');
INSERT INTO `t_operator_voice` VALUES (1252, 39, '任命助理', '您的货物我已经搬过去了。举手之劳，别客气。', 'char_199_yak/CN_001');
INSERT INTO `t_operator_voice` VALUES (1253, 39, '交谈1', '盾牌，就是守护。', 'char_199_yak/CN_002');
INSERT INTO `t_operator_voice` VALUES (1254, 39, '交谈2', '我从银灰少爷......不，老爷。我从老爷少年时就开始负责保护他。', 'char_199_yak/CN_003');
INSERT INTO `t_operator_voice` VALUES (1255, 39, '交谈3', '谈论这个是不礼貌的，但恩雅小姐的遭遇......确实令人感到忧伤。她理应有更好的生活。', 'char_199_yak/CN_004');
INSERT INTO `t_operator_voice` VALUES (1256, 39, '晋升后交谈1', '万一，这面盾有什么损坏的话，就去找那位叫火神的工匠修理，没错吧？', 'char_199_yak/CN_005');
INSERT INTO `t_operator_voice` VALUES (1257, 39, '晋升后交谈2', '意外，真是意外。那位铁匠的手艺真的不错。我得好好谢谢她才行。', 'char_199_yak/CN_006');
INSERT INTO `t_operator_voice` VALUES (1258, 39, '信赖提升后交谈1', '罗德岛的生活十分有趣，相比之下，在雪境度过的日子都比较平淡。', 'char_199_yak/CN_007');
INSERT INTO `t_operator_voice` VALUES (1259, 39, '信赖提升后交谈2', '虽然老爷派我来保护二小姐，老实说，我不认为二小姐是会坐在那里乖乖等人来保护她的类型。', 'char_199_yak/CN_008');
INSERT INTO `t_operator_voice` VALUES (1260, 39, '信赖提升后交谈3', '银灰老爷对你如此器重的原因，我多少也有点明白了。以后请把我也当作你的盾。', 'char_199_yak/CN_009');
INSERT INTO `t_operator_voice` VALUES (1261, 39, '闲置', '有时候也会忽然想念起雪境的生活。', 'char_199_yak/CN_010');
INSERT INTO `t_operator_voice` VALUES (1262, 39, '干员报到', '同盟者，在此对您致以雪境之礼。我是银灰老爷此次派驻的护卫之一，代号为角峰。', 'char_199_yak/CN_011');
INSERT INTO `t_operator_voice` VALUES (1263, 39, '观看作战记录', '感谢。', 'char_199_yak/CN_012');
INSERT INTO `t_operator_voice` VALUES (1264, 39, '精英化晋升1', '承蒙您的另眼相待，实在愧不敢当。', 'char_199_yak/CN_013');
INSERT INTO `t_operator_voice` VALUES (1265, 39, '精英化晋升2', '即使这盾下一刻粉碎，我也绝不会退后半步。此举无关忠义，只是信念使然。', 'char_199_yak/CN_014');
INSERT INTO `t_operator_voice` VALUES (1266, 39, '编入队伍', '听从吩咐。', 'char_199_yak/CN_017');
INSERT INTO `t_operator_voice` VALUES (1267, 39, '任命队长', '不敢当。', 'char_199_yak/CN_018');
INSERT INTO `t_operator_voice` VALUES (1268, 39, '行动出发', '各位，由我护卫。', 'char_199_yak/CN_019');
INSERT INTO `t_operator_voice` VALUES (1269, 39, '行动开始', '这是有准备的开始。', 'char_199_yak/CN_020');
INSERT INTO `t_operator_voice` VALUES (1270, 39, '选中干员1', '我可以担当先锋。', 'char_199_yak/CN_021');
INSERT INTO `t_operator_voice` VALUES (1271, 39, '选中干员2', '我的盾准备好了。', 'char_199_yak/CN_022');
INSERT INTO `t_operator_voice` VALUES (1272, 39, '部署1', '哼。', 'char_199_yak/CN_023');
INSERT INTO `t_operator_voice` VALUES (1273, 39, '部署2', '喝！', 'char_199_yak/CN_024');
INSERT INTO `t_operator_voice` VALUES (1274, 39, '作战中1', '自讨苦吃。', 'char_199_yak/CN_025');
INSERT INTO `t_operator_voice` VALUES (1275, 39, '作战中2', '这不止是力量而已。', 'char_199_yak/CN_026');
INSERT INTO `t_operator_voice` VALUES (1276, 39, '作战中3', '有此信念，便能化身鬼神！', 'char_199_yak/CN_027');
INSERT INTO `t_operator_voice` VALUES (1277, 39, '作战中4', '你的觉悟根本不够！', 'char_199_yak/CN_028');
INSERT INTO `t_operator_voice` VALUES (1278, 39, '4星结束行动', '还没有发挥出百分之百的力量！', 'char_199_yak/CN_029');
INSERT INTO `t_operator_voice` VALUES (1279, 39, '3星结束行动', '真是不痛不痒的进攻。', 'char_199_yak/CN_030');
INSERT INTO `t_operator_voice` VALUES (1280, 39, '非3星结束行动', '下次一定会为您献上完美的胜利。', 'char_199_yak/CN_031');
INSERT INTO `t_operator_voice` VALUES (1281, 39, '行动失败', '不该如此轻视敌人的，这是代价。', 'char_199_yak/CN_032');
INSERT INTO `t_operator_voice` VALUES (1282, 39, '进驻设施', '真是不错的房间，装修讲究，气氛祥和。', 'char_199_yak/CN_033');
INSERT INTO `t_operator_voice` VALUES (1283, 39, '戳一下', '嗯。', 'char_199_yak/CN_034');
INSERT INTO `t_operator_voice` VALUES (1284, 39, '信赖触摸', '这是我的拿手菜，味道怎么样。', 'char_199_yak/CN_036');
INSERT INTO `t_operator_voice` VALUES (1285, 39, '标题', '明日方舟。', 'char_199_yak/CN_037');
INSERT INTO `t_operator_voice` VALUES (1286, 39, '问候', '您好，博士。', 'char_199_yak/CN_042');
INSERT INTO `t_operator_voice` VALUES (1287, 40, '任命助理', '我来啦！棒球和桌上游戏，挑一个来玩吧？诶？工作中？', 'char_150_snakek/CN_001');
INSERT INTO `t_operator_voice` VALUES (1288, 40, '交谈1', '你问我，我是在哪儿出生的？唔，嗯......不太清楚。管它呢！', 'char_150_snakek/CN_002');
INSERT INTO `t_operator_voice` VALUES (1289, 40, '交谈2', '你好，罗德岛老师！嗯......好像你不是叫这个名字？', 'char_150_snakek/CN_003');
INSERT INTO `t_operator_voice` VALUES (1290, 40, '交谈3', '唔，我今天的工作是什么来着？是不是要抓一些细长的生物？', 'char_150_snakek/CN_004');
INSERT INTO `t_operator_voice` VALUES (1291, 40, '晋升后交谈1', '不行！不要敲我的背包！啊，对不起，我不是冲你生气，不小心一点的话，手会受伤的。让我来给你演示一下！', 'char_150_snakek/CN_005');
INSERT INTO `t_operator_voice` VALUES (1292, 40, '晋升后交谈2', '以后谁要是敢欺负你，我就用球棍扁他们！呃，还、还真有？其实我对打架不太在行，嘿嘿......', 'char_150_snakek/CN_006');
INSERT INTO `t_operator_voice` VALUES (1293, 40, '信赖提升后交谈1', '我啊，喜欢花一天时间从街头走到街尾，一边晒太阳一边观察行人，然后......然后就迷路啦！', 'char_150_snakek/CN_007');
INSERT INTO `t_operator_voice` VALUES (1294, 40, '信赖提升后交谈2', '帮我看病，还给我住的地方，博士真是个好人......', 'char_150_snakek/CN_008');
INSERT INTO `t_operator_voice` VALUES (1295, 40, '信赖提升后交谈3', '博士是我的哥哥？还是姐姐？没关系，哪种都行！......就算你赶我走，我也是不会离开的！反正我也走不了多远嘛。', 'char_150_snakek/CN_009');
INSERT INTO `t_operator_voice` VALUES (1296, 40, '闲置', '博士没时间理我，我一个人玩会儿吧......', 'char_150_snakek/CN_010');
INSERT INTO `t_operator_voice` VALUES (1297, 40, '干员报到', '欸？这里是什么地方？我是谁？', 'char_150_snakek/CN_011');
INSERT INTO `t_operator_voice` VALUES (1298, 40, '观看作战记录', '变硬！除了变硬我什么都不会！', 'char_150_snakek/CN_012');
INSERT INTO `t_operator_voice` VALUES (1299, 40, '精英化晋升1', '晋升？我不懂啊......比起这个，再玩一会儿吧，我们两个一起！！', 'char_150_snakek/CN_013');
INSERT INTO `t_operator_voice` VALUES (1300, 40, '精英化晋升2', '博士博士，我不在乎什么新职位哦，我只要每天都有博士陪伴着我就好了，你能答应我吗？', 'char_150_snakek/CN_014');
INSERT INTO `t_operator_voice` VALUES (1301, 40, '编入队伍', '有很多朋友在这里呢！', 'char_150_snakek/CN_017');
INSERT INTO `t_operator_voice` VALUES (1302, 40, '任命队长', '大家一起出发吧！', 'char_150_snakek/CN_018');
INSERT INTO `t_operator_voice` VALUES (1303, 40, '行动出发', '大家一起的话，一定没问题的！', 'char_150_snakek/CN_019');
INSERT INTO `t_operator_voice` VALUES (1304, 40, '行动开始', '大家一起玩吧！', 'char_150_snakek/CN_020');
INSERT INTO `t_operator_voice` VALUES (1305, 40, '选中干员1', '是——', 'char_150_snakek/CN_021');
INSERT INTO `t_operator_voice` VALUES (1306, 40, '选中干员2', '博士！', 'char_150_snakek/CN_022');
INSERT INTO `t_operator_voice` VALUES (1307, 40, '部署1', '来吧来吧！', 'char_150_snakek/CN_023');
INSERT INTO `t_operator_voice` VALUES (1308, 40, '部署2', '你们这些笨蛋！我才是你们的对手！', 'char_150_snakek/CN_024');
INSERT INTO `t_operator_voice` VALUES (1309, 40, '作战中1', '没用没用没用！', 'char_150_snakek/CN_025');
INSERT INTO `t_operator_voice` VALUES (1310, 40, '作战中2', '来陪我玩玩吧！', 'char_150_snakek/CN_026');
INSERT INTO `t_operator_voice` VALUES (1311, 40, '作战中3', '这是我的主场！', 'char_150_snakek/CN_027');
INSERT INTO `t_operator_voice` VALUES (1312, 40, '作战中4', '绝对不会后退的！', 'char_150_snakek/CN_028');
INSERT INTO `t_operator_voice` VALUES (1313, 40, '4星结束行动', '很难吗？感觉和平时的战斗也没什么区别啊？', 'char_150_snakek/CN_029');
INSERT INTO `t_operator_voice` VALUES (1314, 40, '3星结束行动', '全垒打！！', 'char_150_snakek/CN_030');
INSERT INTO `t_operator_voice` VALUES (1315, 40, '非3星结束行动', '安打！', 'char_150_snakek/CN_031');
INSERT INTO `t_operator_voice` VALUES (1316, 40, '行动失败', '呜啊啊啊啊......出局了....', 'char_150_snakek/CN_032');
INSERT INTO `t_operator_voice` VALUES (1317, 40, '进驻设施', '别用火球打我哦？', 'char_150_snakek/CN_033');
INSERT INTO `t_operator_voice` VALUES (1318, 40, '戳一下', '哇，好玩！', 'char_150_snakek/CN_034');
INSERT INTO `t_operator_voice` VALUES (1319, 40, '信赖触摸', '博士，博士！再稍微玩一会儿吧？就一小会儿！', 'char_150_snakek/CN_036');
INSERT INTO `t_operator_voice` VALUES (1320, 40, '标题', '明日方舟。', 'char_150_snakek/CN_037');
INSERT INTO `t_operator_voice` VALUES (1321, 40, '问候', '博士！你好！', 'char_150_snakek/CN_042');
INSERT INTO `t_operator_voice` VALUES (1322, 41, '任命助理', '博士博士，您是不是饿了？嘿嘿，老远就听到您肚子叫呢，像打雷一样~', 'char_196_sunbr/CN_001');
INSERT INTO `t_operator_voice` VALUES (1323, 41, '交谈1', '这个盾牌？轻轻松松就能举起来哦。嘿~咻~看呀！欸，我能好好举住，不会让它掉下来的，您不躲那么远也没关系的啦~', 'char_196_sunbr/CN_002');
INSERT INTO `t_operator_voice` VALUES (1324, 41, '交谈2', '破坏了我们城市的坏人，一个都不能原谅！我要用这个平底锅，把他们全都拍扁！', 'char_196_sunbr/CN_003');
INSERT INTO `t_operator_voice` VALUES (1325, 41, '交谈3', '凛冬姐说她会保护好古米，真是太小看我啦！大家一起逃跑的时候，可是古米一直在用这面盾牌保护大家的！欸，不是盾牌，是金库的保险门？连博士都这么说！', 'char_196_sunbr/CN_004');
INSERT INTO `t_operator_voice` VALUES (1326, 41, '晋升后交谈1', '博士，想不想见识下古米烧菜的手艺？欸嘿嘿~马上您就能尝到了哦，因为古米已经向可露希尔姐姐提交了申请，只要她同意的话，古米就可以在食堂帮厨了哦！', 'char_196_sunbr/CN_005');
INSERT INTO `t_operator_voice` VALUES (1327, 41, '晋升后交谈2', '锵锵——！古米特制，煎蛋肉排哦！怎么样？很好吃吧？嘿嘿，虽然很难在战斗中掌握火候，不过平时的我是绝对不会失误的！', 'char_196_sunbr/CN_006');
INSERT INTO `t_operator_voice` VALUES (1328, 41, '信赖提升后交谈1', '我们五个人，并不是从开始就在一起的呢。', 'char_196_sunbr/CN_007');
INSERT INTO `t_operator_voice` VALUES (1329, 41, '信赖提升后交谈2', '古米看起来很开心吗？其实，看到那样的景象之后，没人能高高兴兴地生活下去......只是，为了让姐姐们能早点忘记那些乱七八糟的事情，古米必须要振作起来！', 'char_196_sunbr/CN_008');
INSERT INTO `t_operator_voice` VALUES (1330, 41, '信赖提升后交谈3', '古米......也想找到爸爸妈妈，想了很多次了，可是，如果现在古米离开大家的话，也许真的什么都做不到......所以，拜托博士！古米不想只是给大家准备食材，也想学会怎么战斗！', 'char_196_sunbr/CN_009');
INSERT INTO `t_operator_voice` VALUES (1331, 41, '闲置', '博士，睡着了吗？那我读一会儿闲书的话，应该不算偷懒吧？', 'char_196_sunbr/CN_010');
INSERT INTO `t_operator_voice` VALUES (1332, 41, '干员报到', '我是古米！您就是博士吗？感谢您和罗德岛的大家一起帮助了我们！古米没有特别的能力，但只要是能做到的就一定竭尽全力！就让古米成为博士的力量吧！', 'char_196_sunbr/CN_011');
INSERT INTO `t_operator_voice` VALUES (1333, 41, '观看作战记录', '力量涌出来啦！说不定现在古米连博士都能举起来？', 'char_196_sunbr/CN_012');
INSERT INTO `t_operator_voice` VALUES (1334, 41, '精英化晋升1', '晋升之后，古米就是罗德岛三星大厨了吧？开玩笑的，欸嘿嘿~', 'char_196_sunbr/CN_013');
INSERT INTO `t_operator_voice` VALUES (1335, 41, '精英化晋升2', '其实，除了做饭之外，古米还想做更多，更多的事！不过，无论古米有没有做那些事的力量，只要博士让古米去做，古米一样会去做的！', 'char_196_sunbr/CN_014');
INSERT INTO `t_operator_voice` VALUES (1336, 41, '编入队伍', '古米，准备完毕！', 'char_196_sunbr/CN_017');
INSERT INTO `t_operator_voice` VALUES (1337, 41, '任命队长', '欸欸欸？古米来当队长？', 'char_196_sunbr/CN_018');
INSERT INTO `t_operator_voice` VALUES (1338, 41, '行动出发', '博士，等我们回来哦。', 'char_196_sunbr/CN_019');
INSERT INTO `t_operator_voice` VALUES (1339, 41, '行动开始', '古米才不害怕！', 'char_196_sunbr/CN_020');
INSERT INTO `t_operator_voice` VALUES (1340, 41, '选中干员1', '让我来！', 'char_196_sunbr/CN_021');
INSERT INTO `t_operator_voice` VALUES (1341, 41, '选中干员2', '选我选我！', 'char_196_sunbr/CN_022');
INSERT INTO `t_operator_voice` VALUES (1342, 41, '部署1', '已经就位了！', 'char_196_sunbr/CN_023');
INSERT INTO `t_operator_voice` VALUES (1343, 41, '部署2', '古米来守护大家！', 'char_196_sunbr/CN_024');
INSERT INTO `t_operator_voice` VALUES (1344, 41, '作战中1', '古米的特制菜肴哦！', 'char_196_sunbr/CN_025');
INSERT INTO `t_operator_voice` VALUES (1345, 41, '作战中2', '给你吃好吃的！', 'char_196_sunbr/CN_026');
INSERT INTO `t_operator_voice` VALUES (1346, 41, '作战中3', '大家肚子饿了吗？', 'char_196_sunbr/CN_027');
INSERT INTO `t_operator_voice` VALUES (1347, 41, '作战中4', '打起精神来！', 'char_196_sunbr/CN_028');
INSERT INTO `t_operator_voice` VALUES (1348, 41, '4星结束行动', '敌人该准备投降了吧？毕竟只要有博士在，他们就赢不了！', 'char_196_sunbr/CN_029');
INSERT INTO `t_operator_voice` VALUES (1349, 41, '3星结束行动', '其实我不想做到这种地步......可是，如果不战斗的话，敌人就会伤害到我们......！', 'char_196_sunbr/CN_030');
INSERT INTO `t_operator_voice` VALUES (1350, 41, '非3星结束行动', '能赢真是太好了！好开心！', 'char_196_sunbr/CN_031');
INSERT INTO `t_operator_voice` VALUES (1351, 41, '行动失败', '呜......大家都受伤了吗......博士呢？博士没事吧？', 'char_196_sunbr/CN_032');
INSERT INTO `t_operator_voice` VALUES (1352, 41, '进驻设施', '哇，好大的房间，能装下好多人！', 'char_196_sunbr/CN_033');
INSERT INTO `t_operator_voice` VALUES (1353, 41, '戳一下', '哈哈哈！> <', 'char_196_sunbr/CN_034');
INSERT INTO `t_operator_voice` VALUES (1354, 41, '信赖触摸', '博士！就算古米做的菜再好吃，也不能一口气吃太多哦！那样会变重的！', 'char_196_sunbr/CN_036');
INSERT INTO `t_operator_voice` VALUES (1355, 41, '标题', '明日方舟。', 'char_196_sunbr/CN_037');
INSERT INTO `t_operator_voice` VALUES (1356, 41, '问候', '博士，要吃点东西吗？', 'char_196_sunbr/CN_042');
INSERT INTO `t_operator_voice` VALUES (1357, 42, '任命助理', '早安，博士。我可以画一幅您的肖像画吗？啊，我会注意不让它实体化的。而且，也不是什么都能实体化的啦。', 'char_110_deepcl/CN_001');
INSERT INTO `t_operator_voice` VALUES (1358, 42, '交谈1', '我出现在这里，似乎有些唐突吧...', 'char_110_deepcl/CN_002');
INSERT INTO `t_operator_voice` VALUES (1359, 42, '交谈2', '博士，我的瞳孔里倒映着的你，到底是什么颜色呢。', 'char_110_deepcl/CN_003');
INSERT INTO `t_operator_voice` VALUES (1360, 42, '交谈3', '罗德岛有很多有趣的创作素材啊，灵感，渐渐涌出来了呢。', 'char_110_deepcl/CN_004');
INSERT INTO `t_operator_voice` VALUES (1361, 42, '晋升后交谈1', '看到我可爱的“助手”们了吗？你觉得它们有点可怕？明明很可爱的！', 'char_110_deepcl/CN_005');
INSERT INTO `t_operator_voice` VALUES (1362, 42, '晋升后交谈2', '呵呵，这就是你的颜色吗——凝视我吧，让我染上更多属于你的颜色。', 'char_110_deepcl/CN_006');
INSERT INTO `t_operator_voice` VALUES (1363, 42, '信赖提升后交谈1', '嗯，没错，就算谁也不理解我，我也会这样继续画下去。无论它们会变成什么样的怪物......', 'char_110_deepcl/CN_007');
INSERT INTO `t_operator_voice` VALUES (1364, 42, '信赖提升后交谈2', '我们创作者是什么样的人呢，每个月都有三十天以上的瓶颈期，觉得自己怎么画都画不好，而且只有深夜才有效率......', 'char_110_deepcl/CN_008');
INSERT INTO `t_operator_voice` VALUES (1365, 42, '信赖提升后交谈3', '斑驳世界终在它幽暗腹中安然长眠......没错，那是深海真正的颜色。只看得到黑色吗？看来，我还没能将一切都与你分享呢。', 'char_110_deepcl/CN_009');
INSERT INTO `t_operator_voice` VALUES (1366, 42, '闲置', '是在......做梦吗？你那梦中的颜色——是否也像世间万物般绚烂呢，博士......', 'char_110_deepcl/CN_010');
INSERT INTO `t_operator_voice` VALUES (1367, 42, '干员报到', '请叫我“深海色”。我的职业是画家，对，只是一个“画家”哦。', 'char_110_deepcl/CN_011');
INSERT INTO `t_operator_voice` VALUES (1368, 42, '观看作战记录', '这种不安的感觉是......?', 'char_110_deepcl/CN_012');
INSERT INTO `t_operator_voice` VALUES (1369, 42, '精英化晋升1', '呃，别再这么关心我的事了。如果再这样亲切对待我的话，我，我不知道自己会变成什么样子......', 'char_110_deepcl/CN_013');
INSERT INTO `t_operator_voice` VALUES (1370, 42, '精英化晋升2', '谢谢你，博士，现在，让我们完成这幅作品吧？', 'char_110_deepcl/CN_014');
INSERT INTO `t_operator_voice` VALUES (1371, 42, '编入队伍', '真的要我上战场吗？不会吧？！', 'char_110_deepcl/CN_017');
INSERT INTO `t_operator_voice` VALUES (1372, 42, '任命队长', '我没法领导这么多人啦。', 'char_110_deepcl/CN_018');
INSERT INTO `t_operator_voice` VALUES (1373, 42, '行动出发', '大家要加油哦！', 'char_110_deepcl/CN_019');
INSERT INTO `t_operator_voice` VALUES (1374, 42, '行动开始', '那就是敌人么！？', 'char_110_deepcl/CN_020');
INSERT INTO `t_operator_voice` VALUES (1375, 42, '选中干员1', '我吗？', 'char_110_deepcl/CN_021');
INSERT INTO `t_operator_voice` VALUES (1376, 42, '选中干员2', '我该怎么办？', 'char_110_deepcl/CN_022');
INSERT INTO `t_operator_voice` VALUES (1377, 42, '部署1', '涂涂抹抹~', 'char_110_deepcl/CN_023');
INSERT INTO `t_operator_voice` VALUES (1378, 42, '部署2', '唉唉唉，等一下啦！', 'char_110_deepcl/CN_024');
INSERT INTO `t_operator_voice` VALUES (1379, 42, '作战中1', '别着急。', 'char_110_deepcl/CN_025');
INSERT INTO `t_operator_voice` VALUES (1380, 42, '作战中2', '慢慢来吧。', 'char_110_deepcl/CN_026');
INSERT INTO `t_operator_voice` VALUES (1381, 42, '作战中3', '和我的“助手”们过两招吧。', 'char_110_deepcl/CN_027');
INSERT INTO `t_operator_voice` VALUES (1382, 42, '作战中4', '这些孩子，你赢得了吗？', 'char_110_deepcl/CN_028');
INSERT INTO `t_operator_voice` VALUES (1383, 42, '4星结束行动', '如果真的惹恼了我，可不知道会有什么事情发生哦。', 'char_110_deepcl/CN_029');
INSERT INTO `t_operator_voice` VALUES (1384, 42, '3星结束行动', '大家胜利的英姿，就让我画下来吧。', 'char_110_deepcl/CN_030');
INSERT INTO `t_operator_voice` VALUES (1385, 42, '非3星结束行动', '啊，衣服又弄脏了......', 'char_110_deepcl/CN_031');
INSERT INTO `t_operator_voice` VALUES (1386, 42, '行动失败', '要是作战的结果，也能像画作一样重新涂改就好了......', 'char_110_deepcl/CN_032');
INSERT INTO `t_operator_voice` VALUES (1387, 42, '进驻设施', '这个房间的设计很好看啊。', 'char_110_deepcl/CN_033');
INSERT INTO `t_operator_voice` VALUES (1388, 42, '戳一下', '嗯嗯？', 'char_110_deepcl/CN_034');
INSERT INTO `t_operator_voice` VALUES (1389, 42, '信赖触摸', '博士，难得有时间休息，要来看看我最新的作品吗？', 'char_110_deepcl/CN_036');
INSERT INTO `t_operator_voice` VALUES (1390, 42, '标题', '明日方舟。', 'char_110_deepcl/CN_037');
INSERT INTO `t_operator_voice` VALUES (1391, 42, '问候', '早上好，博士。', 'char_110_deepcl/CN_042');
INSERT INTO `t_operator_voice` VALUES (1392, 43, '任命助理', '博士，我已经按您的要求，将源石和其他矿物甄别分拣完毕了，还有别的需求吗？', 'char_183_skgoat/CN_001');
INSERT INTO `t_operator_voice` VALUES (1393, 43, '交谈1', '矿石病真麻烦啊，我现在，就连行动都会被凯尔希医生限制。寿命会被矿石病削减？不，这个倒无所谓啦。', 'char_183_skgoat/CN_002');
INSERT INTO `t_operator_voice` VALUES (1394, 43, '交谈2', '这把音叉？是魔杖。它不仅是武器，也是用在地质勘探上的工具，用起来十分顺手。', 'char_183_skgoat/CN_003');
INSERT INTO `t_operator_voice` VALUES (1395, 43, '交谈3', '没有遇见罗德岛的话，我可能还在全职从事地质研究的工作。但没遇见你们的话，我可能也已经没命了。', 'char_183_skgoat/CN_004');
INSERT INTO `t_operator_voice` VALUES (1396, 43, '晋升后交谈1', '啊？我有黑眼圈了？讨厌，我就知道......', 'char_183_skgoat/CN_005');
INSERT INTO `t_operator_voice` VALUES (1397, 43, '晋升后交谈2', '我背后的是什么？这是......算了，解释起来会很麻烦，等有时间再说吧。', 'char_183_skgoat/CN_006');
INSERT INTO `t_operator_voice` VALUES (1398, 43, '信赖提升后交谈1', '有些石头是会跟我说话的，絮絮叨叨，相互争吵不休，把我弄到失眠......你以为我在开玩笑？我难道像是在开玩笑的样子吗？', 'char_183_skgoat/CN_007');
INSERT INTO `t_operator_voice` VALUES (1399, 43, '信赖提升后交谈2', '普罗旺斯还是一如既往的神经大条，再不注意，她可就要变成我这样的重症感染者了。你们最好也多和她说说。', 'char_183_skgoat/CN_008');
INSERT INTO `t_operator_voice` VALUES (1400, 43, '信赖提升后交谈3', '就是这个，就是这个！你看，你看......至少距今三十五万年的地壳挤压，才会形成这种截面！太棒了......博士，知道吗，那些一直以来反驳这项理论的人才是愚昧的......！', 'char_183_skgoat/CN_009');
INSERT INTO `t_operator_voice` VALUES (1401, 43, '闲置', '博士......哎，是不是工作得太疲惫了。', 'char_183_skgoat/CN_010');
INSERT INTO `t_operator_voice` VALUES (1402, 43, '干员报到', '地灵，地质学者兼术师，向你报道。我每天的工作时间是八小时，如果加班的话要额外支付加班费。', 'char_183_skgoat/CN_011');
INSERT INTO `t_operator_voice` VALUES (1403, 43, '观看作战记录', '嗯，这是学术资料还是别的什么？', 'char_183_skgoat/CN_012');
INSERT INTO `t_operator_voice` VALUES (1404, 43, '精英化晋升1', '科研方法也有不断精进的必要呢。', 'char_183_skgoat/CN_013');
INSERT INTO `t_operator_voice` VALUES (1405, 43, '精英化晋升2', '冒昧提醒一下，我只拿一份薪水。不加薪的话，就算升职，我也不会做更多的工作哦。', 'char_183_skgoat/CN_014');
INSERT INTO `t_operator_voice` VALUES (1406, 43, '编入队伍', '知道了。', 'char_183_skgoat/CN_017');
INSERT INTO `t_operator_voice` VALUES (1407, 43, '任命队长', '希望这会是一支能应付各种极端环境的小队。', 'char_183_skgoat/CN_018');
INSERT INTO `t_operator_voice` VALUES (1408, 43, '行动出发', '放心吧，我们很快就能回来。', 'char_183_skgoat/CN_019');
INSERT INTO `t_operator_voice` VALUES (1409, 43, '行动开始', '不能让他们再这样滥用源石。', 'char_183_skgoat/CN_020');
INSERT INTO `t_operator_voice` VALUES (1410, 43, '选中干员1', '在。', 'char_183_skgoat/CN_021');
INSERT INTO `t_operator_voice` VALUES (1411, 43, '选中干员2', '作战计划是？', 'char_183_skgoat/CN_022');
INSERT INTO `t_operator_voice` VALUES (1412, 43, '部署1', '准备完毕。', 'char_183_skgoat/CN_023');
INSERT INTO `t_operator_voice` VALUES (1413, 43, '部署2', '敌人在哪里？', 'char_183_skgoat/CN_024');
INSERT INTO `t_operator_voice` VALUES (1414, 43, '作战中1', '陷下去吧！', 'char_183_skgoat/CN_025');
INSERT INTO `t_operator_voice` VALUES (1415, 43, '作战中2', '大地的力量......', 'char_183_skgoat/CN_026');
INSERT INTO `t_operator_voice` VALUES (1416, 43, '作战中3', '到此为止了！', 'char_183_skgoat/CN_027');
INSERT INTO `t_operator_voice` VALUES (1417, 43, '作战中4', '你不该踏足这里！', 'char_183_skgoat/CN_028');
INSERT INTO `t_operator_voice` VALUES (1418, 43, '4星结束行动', '敌人的这种行为，只会招致灾难。', 'char_183_skgoat/CN_029');
INSERT INTO `t_operator_voice` VALUES (1419, 43, '3星结束行动', '所有敌人都处理完毕了。', 'char_183_skgoat/CN_030');
INSERT INTO `t_operator_voice` VALUES (1420, 43, '非3星结束行动', '是一场不错的胜利呢，博士。', 'char_183_skgoat/CN_031');
INSERT INTO `t_operator_voice` VALUES (1421, 43, '行动失败', '怎么会这样......', 'char_183_skgoat/CN_032');
INSERT INTO `t_operator_voice` VALUES (1422, 43, '进驻设施', '这样的环境，挺适合研究的。', 'char_183_skgoat/CN_033');
INSERT INTO `t_operator_voice` VALUES (1423, 43, '戳一下', '唔......', 'char_183_skgoat/CN_034');
INSERT INTO `t_operator_voice` VALUES (1424, 43, '信赖触摸', '这块矿石......给我的？真的可以吗......这......很贵重啊？', 'char_183_skgoat/CN_036');
INSERT INTO `t_operator_voice` VALUES (1425, 43, '标题', '明日方舟。', 'char_183_skgoat/CN_037');
INSERT INTO `t_operator_voice` VALUES (1426, 43, '问候', '博士，该工作了。', 'char_183_skgoat/CN_042');
INSERT INTO `t_operator_voice` VALUES (1427, 44, '任命助理', '已经准备好为您服务了，长官！', 'char_277_sqrrel/CN_001');
INSERT INTO `t_operator_voice` VALUES (1428, 44, '交谈1', '为了安全起见，正在为您确认逃生路线和消防设备，请稍等片刻！', 'char_277_sqrrel/CN_002');
INSERT INTO `t_operator_voice` VALUES (1429, 44, '交谈2', '过道内严禁烟火，请各位干员不要在走道吸烟，谢谢配合！', 'char_277_sqrrel/CN_003');
INSERT INTO `t_operator_voice` VALUES (1430, 44, '交谈3', '为大家科普消防知识也是我职责的一部分就先从您开始吧长官！', 'char_277_sqrrel/CN_004');
INSERT INTO `t_operator_voice` VALUES (1431, 44, '晋升后交谈1', '别看我个子小，有些任务可是只有我才能完成的。', 'char_277_sqrrel/CN_005');
INSERT INTO `t_operator_voice` VALUES (1432, 44, '晋升后交谈2', '长官，我很紧张，其实......我不擅长说话......谢谢您......的关心！', 'char_277_sqrrel/CN_006');
INSERT INTO `t_operator_voice` VALUES (1433, 44, '信赖提升后交谈1', '有时候，我一张嘴说话就停不下来，长官我会被讨厌吗......呜......', 'char_277_sqrrel/CN_007');
