INSERT INTO `t_operator_voice` VALUES (1435, 44, '信赖提升后交谈3', '长官，她她她们又开始在房间里烧烤了，您快帮忙制止一下哎呀呀呀！谁把我的灭火器换成伊芙利特的喷火背包了啊啊啊啊！长官您没事吧！', 'char_277_sqrrel/CN_009');
