INSERT INTO `t_operator_voice` VALUES (1564, 47, '信赖触摸', '怎么了？博士。', 'char_102_texas/CN_036');
INSERT INTO `t_operator_voice` VALUES (1565, 47, '标题', '明日方舟。', 'char_102_texas/CN_037');
INSERT INTO `t_operator_voice` VALUES (1566, 47, '问候', '早。', 'char_102_texas/CN_042');
INSERT INTO `t_operator_voice` VALUES (1567, 48, '任命助理', '现在是换班时间，博士，不必在意我，请继续工作吧。', 'char_106_franka/CN_001');
INSERT INTO `t_operator_voice` VALUES (1568, 48, '交谈1', '在BSW我被任命为生化防护响应专员——通称“BPRS”。是的，就是大家都熟悉的那种形象，穿着像桶一样的全套防化服......', 'char_106_franka/CN_002');
INSERT INTO `t_operator_voice` VALUES (1569, 48, '交谈2', '什么？我有洁癖？呵呵，博士出乎意料地很敢说话呢，有胆量哦。', 'char_106_franka/CN_003');
INSERT INTO `t_operator_voice` VALUES (1570, 48, '交谈3', '我认为部分干员的房间最好被净化下，比如那位高高在上的优等生小姐的，您觉得怎么样？', 'char_106_franka/CN_004');
INSERT INTO `t_operator_voice` VALUES (1571, 48, '晋升后交谈1', '想欣赏下我这把剑的精致花纹吗？那么，请好好地拿着，绝对不要松手，接下来它会在三秒钟内加热到2500度......呵呵，开玩笑的啦。', 'char_106_franka/CN_005');
INSERT INTO `t_operator_voice` VALUES (1572, 48, '晋升后交谈2', '请注意那些布满化学烟雾、有毒物质和有害辐射的房间......对，我确实在说凯尔希医生的实验室。', 'char_106_franka/CN_006');
INSERT INTO `t_operator_voice` VALUES (1573, 48, '信赖提升后交谈1', '生化防护的相关心得？这个嘛，比如说饭后一定要刷牙？', 'char_106_franka/CN_007');
INSERT INTO `t_operator_voice` VALUES (1574, 48, '信赖提升后交谈2', '雷蛇晋升后的姿态真是惹眼啊。「我这么好看！打这儿，打这儿！」，她一定是想这么说的吧？', 'char_106_franka/CN_008');
INSERT INTO `t_operator_voice` VALUES (1575, 48, '信赖提升后交谈3', '啊啊，如果罗德岛这么自由的话，干脆就从黑钢辞职好了~不过这样雷蛇会很伤心呢~', 'char_106_franka/CN_009');
INSERT INTO `t_operator_voice` VALUES (1576, 48, '闲置', '你手腕上停了只蚊子，博士。', 'char_106_franka/CN_010');
INSERT INTO `t_operator_voice` VALUES (1577, 48, '干员报到', '防化干员芙兰卡，为了最大限度抑制矿石带来的污染，就让我贡献一份力量吧。', 'char_106_franka/CN_011');
INSERT INTO `t_operator_voice` VALUES (1578, 48, '观看作战记录', '这样的技巧，无论什么样的场合里我都能灵活运用哦。', 'char_106_franka/CN_012');
INSERT INTO `t_operator_voice` VALUES (1579, 48, '精英化晋升1', '哦，晋升？比起黑钢，这里要有趣多了。', 'char_106_franka/CN_013');
INSERT INTO `t_operator_voice` VALUES (1580, 48, '精英化晋升2', '哼哼，你想见识下这把剑最闪耀的样子吗？遗憾，现在的博士还没有那个资格。再努力点，也许那一天会到来的哦。', 'char_106_franka/CN_014');
INSERT INTO `t_operator_voice` VALUES (1581, 48, '编入队伍', '好，准备完毕。', 'char_106_franka/CN_017');
INSERT INTO `t_operator_voice` VALUES (1582, 48, '任命队长', '培养优秀部下的好处，就是能把麻烦事统统推给他们。', 'char_106_franka/CN_018');
INSERT INTO `t_operator_voice` VALUES (1583, 48, '行动出发', '那么，我们出发。', 'char_106_franka/CN_019');
INSERT INTO `t_operator_voice` VALUES (1584, 48, '行动开始', '很遗憾，这些人已经被污染了，那样的话......', 'char_106_franka/CN_020');
INSERT INTO `t_operator_voice` VALUES (1585, 48, '选中干员1', '时间有限。', 'char_106_franka/CN_021');
INSERT INTO `t_operator_voice` VALUES (1586, 48, '选中干员2', '没时间犹豫了。', 'char_106_franka/CN_022');
INSERT INTO `t_operator_voice` VALUES (1587, 48, '部署1', '我对进攻不怎么在行~', 'char_106_franka/CN_023');
INSERT INTO `t_operator_voice` VALUES (1588, 48, '部署2', '重装甲好可怕......骗你的~', 'char_106_franka/CN_024');
INSERT INTO `t_operator_voice` VALUES (1589, 48, '作战中1', '发现你的弱点了～', 'char_106_franka/CN_025');
INSERT INTO `t_operator_voice` VALUES (1590, 48, '作战中2', '烧起来吧。', 'char_106_franka/CN_026');
INSERT INTO `t_operator_voice` VALUES (1591, 48, '作战中3', '停止呼吸咯～', 'char_106_franka/CN_027');
INSERT INTO `t_operator_voice` VALUES (1592, 48, '作战中4', '骗~你的。', 'char_106_franka/CN_028');
INSERT INTO `t_operator_voice` VALUES (1593, 48, '4星结束行动', '沉溺于力量，是最愚蠢的。', 'char_106_franka/CN_029');
INSERT INTO `t_operator_voice` VALUES (1594, 48, '3星结束行动', '真是一次完美的行动，做的很好哦。', 'char_106_franka/CN_030');
INSERT INTO `t_operator_voice` VALUES (1595, 48, '非3星结束行动', '理所当然的结果。', 'char_106_franka/CN_031');
INSERT INTO `t_operator_voice` VALUES (1596, 48, '行动失败', '在这种地方沮丧又有什么用？立刻撤退。', 'char_106_franka/CN_032');
INSERT INTO `t_operator_voice` VALUES (1597, 48, '进驻设施', '没有红茶吗？', 'char_106_franka/CN_033');
INSERT INTO `t_operator_voice` VALUES (1598, 48, '戳一下', '有事？', 'char_106_franka/CN_034');
INSERT INTO `t_operator_voice` VALUES (1599, 48, '信赖触摸', '我们在黑钢时的事，想了解了解吗？', 'char_106_franka/CN_036');
INSERT INTO `t_operator_voice` VALUES (1600, 48, '标题', '明日方舟。', 'char_106_franka/CN_037');
INSERT INTO `t_operator_voice` VALUES (1601, 48, '问候', '博士，早安。', 'char_106_franka/CN_042');
INSERT INTO `t_operator_voice` VALUES (1602, 49, '任命助理', '要是有敌人就大声喊，老子我会保护你。', 'char_155_tiger/CN_001');
INSERT INTO `t_operator_voice` VALUES (1603, 49, '交谈1', '那家伙跑哪去了？总是跟在主子身边，矮矮的又晒得很黑，名字叫“摩根”的家伙......啊！别跑！', 'char_155_tiger/CN_002');
INSERT INTO `t_operator_voice` VALUES (1604, 49, '交谈2', '你帮我，我帮你，理所当然的吧。你要是尊重我，我就不会怠慢你。', 'char_155_tiger/CN_003');
INSERT INTO `t_operator_voice` VALUES (1605, 49, '交谈3', '我家主子好像还挺喜欢你这里的。还真是稀奇事啊，我们很少在同个地方停留太久的。', 'char_155_tiger/CN_004');
INSERT INTO `t_operator_voice` VALUES (1606, 49, '晋升后交谈1', '不管做什么工作，都得拿出气势。', 'char_155_tiger/CN_005');
INSERT INTO `t_operator_voice` VALUES (1607, 49, '晋升后交谈2', '哦~哦~不要着急慢慢吃吧~喂！给我再拿些肉吧！就这点，完全不够这孩子吃！', 'char_155_tiger/CN_006');
INSERT INTO `t_operator_voice` VALUES (1608, 49, '信赖提升后交谈1', '摩根那笨蛋，别看总是游手好闲的，她好歹算是给我们出谋划策的，该做的事还是会去做，虽然大多数时间都在给我捣乱。啧......！', 'char_155_tiger/CN_007');
INSERT INTO `t_operator_voice` VALUES (1609, 49, '信赖提升后交谈2', '单手拿锤威风凛凛的王，帅吧？但老子我，还是更期待她用剑的样子哪。', 'char_155_tiger/CN_008');
INSERT INTO `t_operator_voice` VALUES (1610, 49, '信赖提升后交谈3', '尽管开口！不用考虑那么多，就吩咐吧，你想解决谁？', 'char_155_tiger/CN_009');
INSERT INTO `t_operator_voice` VALUES (1611, 49, '闲置', '哼。放松警惕了吗？就算你被老虎吃掉，我也不管哦。', 'char_155_tiger/CN_010');
INSERT INTO `t_operator_voice` VALUES (1612, 49, '干员报到', '老子是因陀罗，没什么乱七八糟的能力，干架靠的就是这双拳头。那么，请多关照！', 'char_155_tiger/CN_011');
INSERT INTO `t_operator_voice` VALUES (1613, 49, '观看作战记录', '哦，这个不错，我喜欢。', 'char_155_tiger/CN_012');
INSERT INTO `t_operator_voice` VALUES (1614, 49, '精英化晋升1', '晋升固然好，打架也不赖！', 'char_155_tiger/CN_013');
INSERT INTO `t_operator_voice` VALUES (1615, 49, '精英化晋升2', '别怕，我能保护你。', 'char_155_tiger/CN_014');
INSERT INTO `t_operator_voice` VALUES (1616, 49, '编入队伍', '战场在哪？', 'char_155_tiger/CN_017');
INSERT INTO `t_operator_voice` VALUES (1617, 49, '任命队长', '又是谁想要挨揍？', 'char_155_tiger/CN_018');
INSERT INTO `t_operator_voice` VALUES (1618, 49, '行动出发', '你们干你们的活，没事别管我！', 'char_155_tiger/CN_019');
INSERT INTO `t_operator_voice` VALUES (1619, 49, '行动开始', '放马过来吧！', 'char_155_tiger/CN_020');
INSERT INTO `t_operator_voice` VALUES (1620, 49, '选中干员1', '嘿！', 'char_155_tiger/CN_021');
INSERT INTO `t_operator_voice` VALUES (1621, 49, '选中干员2', '别拖拖拉拉的！', 'char_155_tiger/CN_022');
INSERT INTO `t_operator_voice` VALUES (1622, 49, '部署1', '哈哈！', 'char_155_tiger/CN_023');
INSERT INTO `t_operator_voice` VALUES (1623, 49, '部署2', '好，轮到我了！', 'char_155_tiger/CN_024');
INSERT INTO `t_operator_voice` VALUES (1624, 49, '作战中1', '你这个混球！', 'char_155_tiger/CN_025');
INSERT INTO `t_operator_voice` VALUES (1625, 49, '作战中2', '有什么好怕的！', 'char_155_tiger/CN_026');
INSERT INTO `t_operator_voice` VALUES (1626, 49, '作战中3', '你玩完了！', 'char_155_tiger/CN_027');
INSERT INTO `t_operator_voice` VALUES (1627, 49, '作战中4', '没用！', 'char_155_tiger/CN_028');
INSERT INTO `t_operator_voice` VALUES (1628, 49, '4星结束行动', '过瘾！太过瘾了！', 'char_155_tiger/CN_029');
INSERT INTO `t_operator_voice` VALUES (1629, 49, '3星结束行动', '不见红，不畅快！', 'char_155_tiger/CN_030');
INSERT INTO `t_operator_voice` VALUES (1630, 49, '非3星结束行动', '嘁，有人逃跑了，扫兴。', 'char_155_tiger/CN_031');
INSERT INTO `t_operator_voice` VALUES (1631, 49, '行动失败', '你，不错嘛，给我等着——！', 'char_155_tiger/CN_032');
INSERT INTO `t_operator_voice` VALUES (1632, 49, '进驻设施', '这房间怎么回事啊，比我过去待的要窄好多。', 'char_155_tiger/CN_033');
INSERT INTO `t_operator_voice` VALUES (1633, 49, '戳一下', '喂，敢动我，把你打飞哦！', 'char_155_tiger/CN_034');
INSERT INTO `t_operator_voice` VALUES (1634, 49, '信赖触摸', '来吧！拥抱一下！对，就是拥抱的那个意思！', 'char_155_tiger/CN_036');
INSERT INTO `t_operator_voice` VALUES (1635, 49, '标题', '明日方舟。', 'char_155_tiger/CN_037');
INSERT INTO `t_operator_voice` VALUES (1636, 49, '问候', '哟！博士。', 'char_155_tiger/CN_042');
INSERT INTO `t_operator_voice` VALUES (1637, 50, '任命助理', '哟，博士。就算我把武器带进这里，你也会原谅我的对吧。那我就坐在这里了。', 'char_140_whitew/CN_001');
INSERT INTO `t_operator_voice` VALUES (1638, 50, '交谈1', '有人想伤害你？好好想想你过去的所作所为，是不是在哪结下过什么仇怨啊。', 'char_140_whitew/CN_002');
INSERT INTO `t_operator_voice` VALUES (1639, 50, '交谈2', '如果有人借复仇的名义来杀你，不要犹豫，干掉那个人。一旦你决定拿起武器，杀人就不需要什么理由。', 'char_140_whitew/CN_003');
INSERT INTO `t_operator_voice` VALUES (1640, 50, '交谈3', '这么久没见，德克萨斯身边多了不少人嘛！哈哈，这很好！也让我和他们认识一下！', 'char_140_whitew/CN_004');
INSERT INTO `t_operator_voice` VALUES (1641, 50, '晋升后交谈1', '你觉得我是杀手？好啊，你当然可以随意解读我。我可以成为任何人，也能做任何工作，在任何时间都能干掉我想干掉的人，只要我想！', 'char_140_whitew/CN_005');
INSERT INTO `t_operator_voice` VALUES (1642, 50, '晋升后交谈2', '我喜欢力量，博士，尤其是你还向我提供了可以尽情宣泄力量的职位......如果说这个疯狂的世界还剩下什么乐趣，就只剩下用我的力量征服一切这件事了吧！', 'char_140_whitew/CN_006');
INSERT INTO `t_operator_voice` VALUES (1643, 50, '信赖提升后交谈1', '德克萨斯？呵呵，虽然她在害怕我，不过我没打算对她做些什么。我只是想让她变回以前那个德克萨斯，而不是像现在这样怯懦......', 'char_140_whitew/CN_007');
INSERT INTO `t_operator_voice` VALUES (1644, 50, '信赖提升后交谈2', '......那个红色的......没错吧......！博士，我啊，是绝对不能靠近她的！但是，但是——这感觉，这感觉......！如果战斗的话一定会死，但是，还是想和那家伙打上一场啊......！', 'char_140_whitew/CN_008');
INSERT INTO `t_operator_voice` VALUES (1645, 50, '信赖提升后交谈3', '想了解我和德克萨斯过去的事？哈哈，还真是挑起了一个不得了的话题啊你！总有一天你会知道的，但，绝不会是我来告诉你......', 'char_140_whitew/CN_009');
INSERT INTO `t_operator_voice` VALUES (1646, 50, '闲置', '睡吧，至少现在，你还能做个美梦......', 'char_140_whitew/CN_010');
INSERT INTO `t_operator_voice` VALUES (1647, 50, '干员报到', '暴虐的恶人阻断正义的道路，我的主人啊，以复仇与恶意为名，引领弱小的人吧。', 'char_140_whitew/CN_011');
INSERT INTO `t_operator_voice` VALUES (1648, 50, '观看作战记录', '你爱我不断提升的力量，对吧！', 'char_140_whitew/CN_012');
INSERT INTO `t_operator_voice` VALUES (1649, 50, '精英化晋升1', '哈哈哈，好，我喜欢你对我的信任。德克萨斯做得到吗？', 'char_140_whitew/CN_013');
INSERT INTO `t_operator_voice` VALUES (1650, 50, '精英化晋升2', '对对，就是这样，解放更多的力量吧，你该这样做！', 'char_140_whitew/CN_014');
INSERT INTO `t_operator_voice` VALUES (1651, 50, '编入队伍', '让德克萨斯来担任队长！', 'char_140_whitew/CN_017');
INSERT INTO `t_operator_voice` VALUES (1652, 50, '任命队长', '把德克萨斯放到我的小队来！', 'char_140_whitew/CN_018');
INSERT INTO `t_operator_voice` VALUES (1653, 50, '行动出发', '哈哈，诸位，反正我记不住你们的脸......算了，出发吧？', 'char_140_whitew/CN_019');
INSERT INTO `t_operator_voice` VALUES (1654, 50, '行动开始', '你们就是敌人？那就拜托你们进攻用点力了，别让我太无聊！', 'char_140_whitew/CN_020');
INSERT INTO `t_operator_voice` VALUES (1655, 50, '选中干员1', '让我见识见识你的能耐。', 'char_140_whitew/CN_021');
INSERT INTO `t_operator_voice` VALUES (1656, 50, '选中干员2', '你准备怎么安排？', 'char_140_whitew/CN_022');
INSERT INTO `t_operator_voice` VALUES (1657, 50, '部署1', '不错的位置啊。', 'char_140_whitew/CN_023');
INSERT INTO `t_operator_voice` VALUES (1658, 50, '部署2', '哈哈！', 'char_140_whitew/CN_024');
INSERT INTO `t_operator_voice` VALUES (1659, 50, '作战中1', '加油！还差一点。你就能干掉我了！', 'char_140_whitew/CN_025');
INSERT INTO `t_operator_voice` VALUES (1660, 50, '作战中2', '你能做到吗？', 'char_140_whitew/CN_026');
INSERT INTO `t_operator_voice` VALUES (1661, 50, '作战中3', '试着抵抗我吧！', 'char_140_whitew/CN_027');
INSERT INTO `t_operator_voice` VALUES (1662, 50, '作战中4', '再加把劲！', 'char_140_whitew/CN_028');
INSERT INTO `t_operator_voice` VALUES (1663, 50, '4星结束行动', '这样就结束了？真无聊。', 'char_140_whitew/CN_029');
INSERT INTO `t_operator_voice` VALUES (1664, 50, '3星结束行动', '呵呵，喜欢我做的千层酥吗？', 'char_140_whitew/CN_030');
INSERT INTO `t_operator_voice` VALUES (1665, 50, '非3星结束行动', '马马虎虎吧，让那几个人逃走，我也有责任。', 'char_140_whitew/CN_031');
INSERT INTO `t_operator_voice` VALUES (1666, 50, '行动失败', '棒极了，这下，我践踏他们的欲望变得更强了！哈哈哈！', 'char_140_whitew/CN_032');
INSERT INTO `t_operator_voice` VALUES (1667, 50, '进驻设施', '咦？刚才一瞬间，好像看到了一个红色的影子。', 'char_140_whitew/CN_033');
INSERT INTO `t_operator_voice` VALUES (1668, 50, '戳一下', '呵呵。', 'char_140_whitew/CN_034');
INSERT INTO `t_operator_voice` VALUES (1669, 50, '信赖触摸', '越了解你，也就越了解德克萨斯。太棒了。', 'char_140_whitew/CN_036');
INSERT INTO `t_operator_voice` VALUES (1670, 50, '标题', '明日方舟。', 'char_140_whitew/CN_037');
INSERT INTO `t_operator_voice` VALUES (1671, 50, '问候', '呵，你好，博士。', 'char_140_whitew/CN_042');
INSERT INTO `t_operator_voice` VALUES (1672, 51, '任命助理', '您好。在今天的工作开始之前，请稍微给我一些，用于祈祷的时间......', 'char_143_ghost/CN_001');
INSERT INTO `t_operator_voice` VALUES (1673, 51, '交谈1', '慈悲的使者，请守卫我的睡梦，保护我的心灵......', 'char_143_ghost/CN_002');
INSERT INTO `t_operator_voice` VALUES (1674, 51, '交谈2', '呵呵，切割啊......切割是件很快乐的事。是啊，有些东西，相互之间并什么没有联系，从一开始，就不该结合在一起......', 'char_143_ghost/CN_003');
INSERT INTO `t_operator_voice` VALUES (1675, 51, '交谈3', '呵呵呵......哈哈......哈哈哈哈......', 'char_143_ghost/CN_004');
INSERT INTO `t_operator_voice` VALUES (1676, 51, '晋升后交谈1', '呵呵，为什么您要避开我的目光呢？这么做也是没用的......您想要的东西是什么，我全都知道。', 'char_143_ghost/CN_005');
INSERT INTO `t_operator_voice` VALUES (1677, 51, '晋升后交谈2', '我一直、一直想带您去一个地方，一个能听到“秘密”的地方。', 'char_143_ghost/CN_006');
INSERT INTO `t_operator_voice` VALUES (1678, 51, '信赖提升后交谈1', '你们究竟对我做了什么......？为什么让我吃那种药？是谁把我带到这里的？请告诉我......！！！', 'char_143_ghost/CN_007');
INSERT INTO `t_operator_voice` VALUES (1679, 51, '信赖提升后交谈2', '那个人告诉我的都是对的......解剖、撕裂、切碎，一切都是、都是给予他们的救赎！', 'char_143_ghost/CN_008');
INSERT INTO `t_operator_voice` VALUES (1680, 51, '信赖提升后交谈3', '在您的梦中，经常会出现些无法理解的事物，是吗？呵，那些景象，是那个人向你投递来的，无望的信件。而我，作为那个人的使者，把它送到了，呵呵......', 'char_143_ghost/CN_009');
INSERT INTO `t_operator_voice` VALUES (1681, 51, '闲置', '......听，茫茫的万物之主，在黑暗中，喃喃自语......', 'char_143_ghost/CN_010');
INSERT INTO `t_operator_voice` VALUES (1682, 51, '干员报到', '幽灵鲨......那些‘死者’都这样称呼我。那位医生告诉我，继续获得治疗的条件，是为罗德岛而战。嗯，我会的，我会把我的力量，全都献给你们。', 'char_143_ghost/CN_011');
INSERT INTO `t_operator_voice` VALUES (1683, 51, '观看作战记录', '力量......流入了我的身体......', 'char_143_ghost/CN_012');
INSERT INTO `t_operator_voice` VALUES (1684, 51, '精英化晋升1', '你的意志，我无权违背......', 'char_143_ghost/CN_013');
INSERT INTO `t_operator_voice` VALUES (1685, 51, '精英化晋升2', '这样的职位，可以给我带来新的喜悦吗？还是说，为了你而战斗，才是我真正的快乐呢？', 'char_143_ghost/CN_014');
INSERT INTO `t_operator_voice` VALUES (1686, 51, '编入队伍', '各位，晚安......', 'char_143_ghost/CN_017');
INSERT INTO `t_operator_voice` VALUES (1687, 51, '任命队长', '如您所愿。', 'char_143_ghost/CN_018');
INSERT INTO `t_operator_voice` VALUES (1688, 51, '行动出发', '这将是一条，充满牺牲的道路。', 'char_143_ghost/CN_019');
INSERT INTO `t_operator_voice` VALUES (1689, 51, '行动开始', '我将这些迷失在战场上的灵魂，全部献给您......', 'char_143_ghost/CN_020');
INSERT INTO `t_operator_voice` VALUES (1690, 51, '选中干员1', '静候您的讯息。', 'char_143_ghost/CN_021');
INSERT INTO `t_operator_voice` VALUES (1691, 51, '选中干员2', '请告诉我结果。', 'char_143_ghost/CN_022');
INSERT INTO `t_operator_voice` VALUES (1692, 51, '部署1', '我来自深邃的海洋......', 'char_143_ghost/CN_023');
INSERT INTO `t_operator_voice` VALUES (1693, 51, '部署2', '是您唤醒了我。', 'char_143_ghost/CN_024');
INSERT INTO `t_operator_voice` VALUES (1694, 51, '作战中1', '取悦我吧。', 'char_143_ghost/CN_025');
INSERT INTO `t_operator_voice` VALUES (1695, 51, '作战中2', '回归深渊吧。', 'char_143_ghost/CN_026');
INSERT INTO `t_operator_voice` VALUES (1696, 51, '作战中3', '没有价值的东西，我毫无兴趣。', 'char_143_ghost/CN_027');
INSERT INTO `t_operator_voice` VALUES (1697, 51, '作战中4', '不许逃走。', 'char_143_ghost/CN_028');
INSERT INTO `t_operator_voice` VALUES (1698, 51, '4星结束行动', '我战胜了您给予我的全部考验，您看到了吗？', 'char_143_ghost/CN_029');
INSERT INTO `t_operator_voice` VALUES (1699, 51, '3星结束行动', '请将迷失的灵魂们，引渡到安息之地吧。', 'char_143_ghost/CN_030');
INSERT INTO `t_operator_voice` VALUES (1700, 51, '非3星结束行动', '......谁都，别想逃走！', 'char_143_ghost/CN_031');
INSERT INTO `t_operator_voice` VALUES (1701, 51, '行动失败', '必须让他们偿还......必须......', 'char_143_ghost/CN_032');
INSERT INTO `t_operator_voice` VALUES (1702, 51, '进驻设施', '可以给我一个，看得见星星的房间吗？', 'char_143_ghost/CN_033');
INSERT INTO `t_operator_voice` VALUES (1703, 51, '戳一下', '呵呵......', 'char_143_ghost/CN_034');
INSERT INTO `t_operator_voice` VALUES (1704, 51, '信赖触摸', '博士......你背后究竟是什么样的力量......使我这样欣喜若狂呢......', 'char_143_ghost/CN_036');
INSERT INTO `t_operator_voice` VALUES (1705, 51, '标题', '明日方舟。', 'char_143_ghost/CN_037');
INSERT INTO `t_operator_voice` VALUES (1706, 51, '问候', '你是......啊，这不是博士吗？', 'char_143_ghost/CN_042');
INSERT INTO `t_operator_voice` VALUES (1707, 52, '任命助理', '哎呀，你好，博士。今天的您看起来也很健康，这比什么都好。', 'char_129_bluep/CN_001');
INSERT INTO `t_operator_voice` VALUES (1708, 52, '交谈1', '我准备了解毒剂，它能使普通的毒物完全无效。希望您在每次作战前都服用一杯，可以吗？', 'char_129_bluep/CN_002');
INSERT INTO `t_operator_voice` VALUES (1709, 52, '交谈2', '因为我身体中的毒素也是一种优质的药品原料，所以凯尔希医生经常把我叫去她的实验室......', 'char_129_bluep/CN_003');
INSERT INTO `t_operator_voice` VALUES (1710, 52, '交谈3', '哎呀，一直盯着我看，有什么事吗？......嗯？我的眼睛很罕见？博士，这是在称赞我吗？', 'char_129_bluep/CN_004');
INSERT INTO `t_operator_voice` VALUES (1711, 52, '晋升后交谈1', '我是敌人最畏惧的存在。没办法啊，比起被“毒物”折磨而死，不如抢先杀死我，敌人们理所当然会这样选择吧......只是有时候，我的同伴也会思考同样的事，这让我有些不安。', 'char_129_bluep/CN_005');
INSERT INTO `t_operator_voice` VALUES (1712, 52, '晋升后交谈2', '为什么我在盯着这块蛋糕发呆？其实蛋糕是我为大家烤的，只是谁都不肯吃......难道，博士想要尝尝看吗？', 'char_129_bluep/CN_006');
INSERT INTO `t_operator_voice` VALUES (1713, 52, '信赖提升后交谈1', '博士总是穿着相同的衣服呢，不如让我帮您挑选些新的吧？我觉得自己还挺有挑衣服的品味......哎，制服是不能随便更换的......这样吗？', 'char_129_bluep/CN_007');
INSERT INTO `t_operator_voice` VALUES (1714, 52, '信赖提升后交谈2', '因为我是“毒物”，所以谁都不想接近我......但，这样的我的身边，现在总能有博士相伴。我很开心哟，真的。', 'char_129_bluep/CN_008');
INSERT INTO `t_operator_voice` VALUES (1715, 52, '信赖提升后交谈3', '实际上，我的身体和大家没什么不同，即使我是“毒物”，只是触碰的话并不会发生什么坏事。如果您能亲自证明这一点的话，也许大家就能相信......博士，可以在大家面前，触碰我的手吗？', 'char_129_bluep/CN_009');
INSERT INTO `t_operator_voice` VALUES (1716, 52, '闲置', '博士？哎呀，好像睡着了。有没有毯子能给博士盖一下呢。', 'char_129_bluep/CN_010');
INSERT INTO `t_operator_voice` VALUES (1717, 52, '干员报到', '初次见面，我是蓝毒，想必您已经听凯尔希医生提起过我，那么，该如何运用我这“毒物”的能力，就交由您来决定了。', 'char_129_bluep/CN_011');
INSERT INTO `t_operator_voice` VALUES (1718, 52, '观看作战记录', '似乎听到了毒液沸腾的声音，真让人心情愉快啊。', 'char_129_bluep/CN_012');
INSERT INTO `t_operator_voice` VALUES (1719, 52, '精英化晋升1', '很感谢您。能在这么多的战斗中有所建树，我的愿望实现了呢。', 'char_129_bluep/CN_013');
INSERT INTO `t_operator_voice` VALUES (1720, 52, '精英化晋升2', '与其作为“毒物”浑浑噩噩地活下去，不如坦率接受这样的自己，努力去改变命运——能让我实现这小小梦想的人，正是博士您啊。所以，真的，真的非常感谢......', 'char_129_bluep/CN_014');
INSERT INTO `t_operator_voice` VALUES (1721, 52, '编入队伍', '这样很好，我很适合做辅助进攻的工作。', 'char_129_bluep/CN_017');
INSERT INTO `t_operator_voice` VALUES (1722, 52, '任命队长', '派我去援护其他人比较好哦？否则我的毒液，说不定会伤到博士......', 'char_129_bluep/CN_018');
INSERT INTO `t_operator_voice` VALUES (1723, 52, '行动出发', '各位，没必要离我那么远的......', 'char_129_bluep/CN_019');
INSERT INTO `t_operator_voice` VALUES (1724, 52, '行动开始', '我为你们准备了“礼物”。喜欢吗？', 'char_129_bluep/CN_020');
INSERT INTO `t_operator_voice` VALUES (1725, 52, '选中干员1', '恭候您的指令。', 'char_129_bluep/CN_021');
INSERT INTO `t_operator_voice` VALUES (1726, 52, '选中干员2', '请不要怕我。', 'char_129_bluep/CN_022');
INSERT INTO `t_operator_voice` VALUES (1727, 52, '部署1', '请安心，毒素是不会传染的。', 'char_129_bluep/CN_023');
INSERT INTO `t_operator_voice` VALUES (1728, 52, '部署2', '毒剂准备好了哦。', 'char_129_bluep/CN_024');
INSERT INTO `t_operator_voice` VALUES (1729, 52, '作战中1', '这种毒素，将会慢慢侵蚀你的生命......', 'char_129_bluep/CN_025');
INSERT INTO `t_operator_voice` VALUES (1730, 52, '作战中2', '是不是看到幻觉了呢？', 'char_129_bluep/CN_026');
INSERT INTO `t_operator_voice` VALUES (1731, 52, '作战中3', '我之蜜糖，汝之砒霜。', 'char_129_bluep/CN_027');
INSERT INTO `t_operator_voice` VALUES (1732, 52, '作战中4', '毒吻。', 'char_129_bluep/CN_028');
INSERT INTO `t_operator_voice` VALUES (1733, 52, '4星结束行动', '用“毒物”去战胜阴谋和罪恶。会不会让您觉得这是种讽刺呢？', 'char_129_bluep/CN_029');
INSERT INTO `t_operator_voice` VALUES (1734, 52, '3星结束行动', '有种方法，能让这些残骸在一瞬间溶化掉......您想欣赏一下吗？', 'char_129_bluep/CN_030');
INSERT INTO `t_operator_voice` VALUES (1735, 52, '非3星结束行动', '还好，没弄脏衣服就结束了。', 'char_129_bluep/CN_031');
INSERT INTO `t_operator_voice` VALUES (1736, 52, '行动失败', '真是可笑的战斗，就像“毒物”被自己毒死一样......', 'char_129_bluep/CN_032');
INSERT INTO `t_operator_voice` VALUES (1737, 52, '进驻设施', '适当的休息，也是为了工作更有状态哦。', 'char_129_bluep/CN_033');
INSERT INTO `t_operator_voice` VALUES (1738, 52, '戳一下', '欸？', 'char_129_bluep/CN_034');
INSERT INTO `t_operator_voice` VALUES (1739, 52, '信赖触摸', '您、碰到我了？再做、再做一次，能拜托您再做一次吗？', 'char_129_bluep/CN_036');
INSERT INTO `t_operator_voice` VALUES (1740, 52, '标题', '明日方舟。', 'char_129_bluep/CN_037');
INSERT INTO `t_operator_voice` VALUES (1741, 52, '问候', '你好，博士。', 'char_129_bluep/CN_042');
INSERT INTO `t_operator_voice` VALUES (1742, 53, '任命助理', '哈——欠。博士，我可以请会儿假，稍微离开一下吗？', 'char_204_platnm/CN_001');
INSERT INTO `t_operator_voice` VALUES (1743, 53, '交谈1', '不用担心我的弓，每天我都有在好好保养。而且，它可比你要结实多了，用不着质疑它。', 'char_204_platnm/CN_002');
INSERT INTO `t_operator_voice` VALUES (1744, 53, '交谈2', '罗德岛确实有不少有趣的人，如果能找到一两个朋友当旅伴也挺不错的。', 'char_204_platnm/CN_003');
INSERT INTO `t_operator_voice` VALUES (1745, 53, '交谈3', '现在的卡西米尔骑士，不是脑袋里都练出肌肉的傻瓜，就是沉迷广告牌烟火演出的小丑，啊啊，真~~无聊。', 'char_204_platnm/CN_004');
INSERT INTO `t_operator_voice` VALUES (1746, 53, '晋升后交谈1', '本来打算在完成手上的任务之后就回国的，嗯，现在我改变主意了。理由？哎呀，特意打听这种事，可是不懂少女心的表现哦。', 'char_204_platnm/CN_005');
INSERT INTO `t_operator_voice` VALUES (1747, 53, '晋升后交谈2', '想问白金这个称呼是怎么来的？这个嘛，卡西米尔的骑士杀手也有级别之分哦。虽然在我之上，还有玄铁三人和青金两人，但身在白金大位的，到我死去为止，有且只能有我一个人。所以，我就干脆拿这个作为代号使用啦。', 'char_204_platnm/CN_006');
INSERT INTO `t_operator_voice` VALUES (1748, 53, '信赖提升后交谈1', '啊，是临光小姐！没有没有，我没打算对她出手。不如说，我更害怕她看到我就拔出战锤冲过来嘛。', 'char_204_platnm/CN_007');
INSERT INTO `t_operator_voice` VALUES (1749, 53, '信赖提升后交谈2', '卡西米尔还......真挺无聊的。唉~想到自己几乎快要被罗德岛的生活俘虏，我居然会有些生气呢。', 'char_204_platnm/CN_008');
INSERT INTO `t_operator_voice` VALUES (1750, 53, '信赖提升后交谈3', '您有没有思考过，要如何维系罗德岛各位之间的关系呢？要知道，各个紧密连环间的空隙是结构中最为脆弱的部分；而利刃总会刺向铠甲的缝隙，一旦被趁虚而入，辛苦铸就的一切，顷刻间就会崩塌殆尽。', 'char_204_platnm/CN_009');
INSERT INTO `t_operator_voice` VALUES (1751, 53, '闲置', '真无聊~就不能发生些有意思的事吗~？', 'char_204_platnm/CN_010');
INSERT INTO `t_operator_voice` VALUES (1752, 53, '干员报到', '卡西米尔无胄盟刺客，依约而来。怎么称呼？嗯......就叫我白金吧，请多关照咯。', 'char_204_platnm/CN_011');
INSERT INTO `t_operator_voice` VALUES (1753, 53, '观看作战记录', '好啊，仿佛这张弓都变轻了呢，谢谢。', 'char_204_platnm/CN_012');
INSERT INTO `t_operator_voice` VALUES (1754, 53, '精英化晋升1', '晋升？白金位还不够可怕吗？哦~你真的，说了很有趣的话呢。', 'char_204_platnm/CN_013');
INSERT INTO `t_operator_voice` VALUES (1755, 53, '精英化晋升2', '呵......白金的真正面貌，你当真有胆量见识吗，我可爱的博士？', 'char_204_platnm/CN_014');
INSERT INTO `t_operator_voice` VALUES (1756, 53, '编入队伍', '白金，入列。', 'char_204_platnm/CN_017');
INSERT INTO `t_operator_voice` VALUES (1757, 53, '任命队长', '欸~？让我指挥？我可是没有指挥经验的哦？', 'char_204_platnm/CN_018');
INSERT INTO `t_operator_voice` VALUES (1758, 53, '行动出发', '接下来，会发生一场有趣的战斗吗~？', 'char_204_platnm/CN_019');
INSERT INTO `t_operator_voice` VALUES (1759, 53, '行动开始', '一起攻过来也可以，反正你们都很弱。', 'char_204_platnm/CN_020');
INSERT INTO `t_operator_voice` VALUES (1760, 53, '选中干员1', '请指示。', 'char_204_platnm/CN_021');
INSERT INTO `t_operator_voice` VALUES (1761, 53, '选中干员2', '目标是谁？', 'char_204_platnm/CN_022');
INSERT INTO `t_operator_voice` VALUES (1762, 53, '部署1', '抹除开始。', 'char_204_platnm/CN_023');
INSERT INTO `t_operator_voice` VALUES (1763, 53, '部署2', '迎战。', 'char_204_platnm/CN_024');
INSERT INTO `t_operator_voice` VALUES (1764, 53, '作战中1', '数一，碎足。', 'char_204_platnm/CN_025');
INSERT INTO `t_operator_voice` VALUES (1765, 53, '作战中2', '数二，破甲。', 'char_204_platnm/CN_026');
INSERT INTO `t_operator_voice` VALUES (1766, 53, '作战中3', '数三，贯身——', 'char_204_platnm/CN_027');
INSERT INTO `t_operator_voice` VALUES (1767, 53, '作战中4', '——如此一来，便是将军喽！', 'char_204_platnm/CN_028');
INSERT INTO `t_operator_voice` VALUES (1768, 53, '4星结束行动', '很好啊，已经让我十分享受了呢！', 'char_204_platnm/CN_029');
INSERT INTO `t_operator_voice` VALUES (1769, 53, '3星结束行动', '将死。不好意思，是我的胜利呢。', 'char_204_platnm/CN_030');
INSERT INTO `t_operator_voice` VALUES (1770, 53, '非3星结束行动', '没能全部处决啊，很可惜。', 'char_204_platnm/CN_031');
INSERT INTO `t_operator_voice` VALUES (1771, 53, '行动失败', '别放弃......坚持到最后的人才是胜者。', 'char_204_platnm/CN_032');
INSERT INTO `t_operator_voice` VALUES (1772, 53, '进驻设施', '这里的环境，感觉意外的不错嘛~', 'char_204_platnm/CN_033');
INSERT INTO `t_operator_voice` VALUES (1773, 53, '戳一下', '唔嗯......', 'char_204_platnm/CN_034');
INSERT INTO `t_operator_voice` VALUES (1774, 53, '信赖触摸', '要不要陪我去城市里走走？不方便的话，下次也可以。', 'char_204_platnm/CN_036');
INSERT INTO `t_operator_voice` VALUES (1775, 53, '标题', '明日方舟。', 'char_204_platnm/CN_037');
INSERT INTO `t_operator_voice` VALUES (1776, 53, '问候', '气色不错呢，博士~', 'char_204_platnm/CN_042');
INSERT INTO `t_operator_voice` VALUES (1777, 54, '任命助理', '现在是我的值班时间。如果打扰到你办公了，我可以到外面待着。', 'char_219_meteo/CN_001');
INSERT INTO `t_operator_voice` VALUES (1778, 54, '交谈1', '你有好好调查过每位新加入干员的个人信息和情报吗？', 'char_219_meteo/CN_002');
INSERT INTO `t_operator_voice` VALUES (1779, 54, '交谈2', '只要战争仍在继续，就不会有什么地方是绝对安全的。这也同样适用于罗德岛，请你务必牢记这点。', 'char_219_meteo/CN_003');
INSERT INTO `t_operator_voice` VALUES (1780, 54, '交谈3', '坦白地说，你的周围混杂着许多人，不觉得这样其实很危险吗？', 'char_219_meteo/CN_004');
INSERT INTO `t_operator_voice` VALUES (1781, 54, '晋升后交谈1', '居然有那么多和我一样的萨卡兹人来到了罗德岛，并且相互之间还能和睦相处，这确实让我很惊讶......', 'char_219_meteo/CN_005');
INSERT INTO `t_operator_voice` VALUES (1782, 54, '晋升后交谈2', '我这把弩炮虽然不太容易操作，但威力绝不下于拉特兰人的铳。我将用它捍卫我族的荣耀。', 'char_219_meteo/CN_006');
INSERT INTO `t_operator_voice` VALUES (1783, 54, '信赖提升后交谈1', '我见过守林人了。那个孩子，把我们并肩作战过的组织名当成自己的代号。也许那孩子的时间，在故乡被毁灭之时就已经停止了......', 'char_219_meteo/CN_007');
INSERT INTO `t_operator_voice` VALUES (1784, 54, '信赖提升后交谈2', '发生在我和守林人之间的那段过去，还是等她亲口告诉你吧......只能说，把叛军引向她故乡的人并不是我，只是......没人会相信一个萨卡兹人的话。', 'char_219_meteo/CN_008');
INSERT INTO `t_operator_voice` VALUES (1785, 54, '信赖提升后交谈3', '她有她的复仇，我有我的使命，既然如此，即使我们在这罗德岛重逢，或许依旧会走向各自的结局吧。', 'char_219_meteo/CN_009');
INSERT INTO `t_operator_voice` VALUES (1786, 54, '闲置', '哪里来的声音？难道是有谁在监视这个房间吗？还是说......只是我的错觉罢了？', 'char_219_meteo/CN_010');
INSERT INTO `t_operator_voice` VALUES (1787, 54, '干员报到', '代号陨星，职业是自由佣兵。如你所见，是萨卡兹人。即使这样，你还依然打算雇佣我吗？', 'char_219_meteo/CN_011');
INSERT INTO `t_operator_voice` VALUES (1788, 54, '观看作战记录', '瞄准技术提升了。', 'char_219_meteo/CN_012');
INSERT INTO `t_operator_voice` VALUES (1789, 54, '精英化晋升1', '想要提拔我吗？ 看来，选择在这儿工作，果然是对的。', 'char_219_meteo/CN_013');
INSERT INTO `t_operator_voice` VALUES (1790, 54, '精英化晋升2', '这次，真的十分谢谢你。没想到，我这样的人，居然能在罗德岛获得如此稳定的一份工作。我会竭尽所能，回报你的信赖。', 'char_219_meteo/CN_014');
INSERT INTO `t_operator_voice` VALUES (1791, 54, '编入队伍', '我来了。', 'char_219_meteo/CN_017');
INSERT INTO `t_operator_voice` VALUES (1792, 54, '任命队长', '要我做队长吗？严格来说，我不太擅长对别人发号施令。', 'char_219_meteo/CN_018');
INSERT INTO `t_operator_voice` VALUES (1793, 54, '行动出发', '我会好好照顾大家的。', 'char_219_meteo/CN_019');
INSERT INTO `t_operator_voice` VALUES (1794, 54, '行动开始', '已经确认了敌情。各位，请做好战斗的准备。', 'char_219_meteo/CN_020');
INSERT INTO `t_operator_voice` VALUES (1795, 54, '选中干员1', '哦，好。', 'char_219_meteo/CN_021');
INSERT INTO `t_operator_voice` VALUES (1796, 54, '选中干员2', '没问题。', 'char_219_meteo/CN_022');
INSERT INTO `t_operator_voice` VALUES (1797, 54, '部署1', '视野良好。', 'char_219_meteo/CN_023');
INSERT INTO `t_operator_voice` VALUES (1798, 54, '部署2', '一口气解决他们。', 'char_219_meteo/CN_024');
INSERT INTO `t_operator_voice` VALUES (1799, 54, '作战中1', '射击。', 'char_219_meteo/CN_025');
INSERT INTO `t_operator_voice` VALUES (1800, 54, '作战中2', '开火。', 'char_219_meteo/CN_026');
INSERT INTO `t_operator_voice` VALUES (1801, 54, '作战中3', '集中火力。', 'char_219_meteo/CN_027');
INSERT INTO `t_operator_voice` VALUES (1802, 54, '作战中4', '退后！', 'char_219_meteo/CN_028');
INSERT INTO `t_operator_voice` VALUES (1803, 54, '4星结束行动', '我不想再看到熊熊燃烧的战火了，让一切，都结束吧。', 'char_219_meteo/CN_029');
INSERT INTO `t_operator_voice` VALUES (1804, 54, '3星结束行动', '想从我布置完善的包围网中脱逃，真是异想天开。', 'char_219_meteo/CN_030');
INSERT INTO `t_operator_voice` VALUES (1805, 54, '非3星结束行动', '有人逃走了，现在还不能大意。', 'char_219_meteo/CN_031');
INSERT INTO `t_operator_voice` VALUES (1806, 54, '行动失败', '撤退吧！回到据点，重整姿态。', 'char_219_meteo/CN_032');
INSERT INTO `t_operator_voice` VALUES (1807, 54, '进驻设施', '这里是什么地方？', 'char_219_meteo/CN_033');
INSERT INTO `t_operator_voice` VALUES (1808, 54, '戳一下', '呃！什么？', 'char_219_meteo/CN_034');
INSERT INTO `t_operator_voice` VALUES (1809, 54, '信赖触摸', '这个，是要送给我吗？啊，谢谢。', 'char_219_meteo/CN_036');
INSERT INTO `t_operator_voice` VALUES (1810, 54, '标题', '明日方舟。', 'char_219_meteo/CN_037');
INSERT INTO `t_operator_voice` VALUES (1811, 54, '问候', '博士，你好吗？', 'char_219_meteo/CN_042');
INSERT INTO `t_operator_voice` VALUES (1812, 55, '任命助理', '火灾警报器在响？失礼了，只是因为我经过了你的办公室而已。我的体温可能又过高了。', 'char_166_skfire/CN_001');
INSERT INTO `t_operator_voice` VALUES (1813, 55, '交谈1', '没有无法学会的语言，没有无法理解的法术，这是理所应当的吧？......嗯？只有我是特别的？', 'char_166_skfire/CN_002');
INSERT INTO `t_operator_voice` VALUES (1814, 55, '交谈2', '人生？那种东西——哼，直到上次袭击为止都还算符合预期。但在整合运动谋害菲尔之后，“消灭他们”已经是我日程表里最优先的事项了。', 'char_166_skfire/CN_003');
INSERT INTO `t_operator_voice` VALUES (1815, 55, '交谈3', '就凭罗德岛现在的软弱手段，只会拖我们“王者之杖”的后腿。', 'char_166_skfire/CN_004');
INSERT INTO `t_operator_voice` VALUES (1816, 55, '晋升后交谈1', '你能劝一劝阿米娅吗？她那样还挺可怜的。善良只会蒙蔽她，对我来说......适合恶徒的只有毁灭一种结局。', 'char_166_skfire/CN_005');
INSERT INTO `t_operator_voice` VALUES (1817, 55, '晋升后交谈2', '你现在的实力，根本达不到我们【王者之杖】的平均水平。呵，没错，我是很强，而我们五人汇聚起的力量，更是连语言都没法衡量。', 'char_166_skfire/CN_006');
INSERT INTO `t_operator_voice` VALUES (1818, 55, '信赖提升后交谈1', '七曜的那把刀，是白送给她的，据说它的前任主人是一位能一刀斩下东国所有的樱花的剑豪。然而即使是这样的人，还是被白击败，输掉了这把刀哦。', 'char_166_skfire/CN_007');
INSERT INTO `t_operator_voice` VALUES (1819, 55, '信赖提升后交谈2', '你那件衣服是怎么回事？咦？是我烧破的？但......不可能啊，我对自己身体的控制是很完美的......大概吧？我......抱歉，我很抱歉。', 'char_166_skfire/CN_008');
INSERT INTO `t_operator_voice` VALUES (1820, 55, '信赖提升后交谈3', '即便你我的想法截然不同，但罗德岛那拼命努力、不断奔驰的样子......很不错。对，我是在夸奖你们。这很美妙。', 'char_166_skfire/CN_009');
INSERT INTO `t_operator_voice` VALUES (1821, 55, '闲置', '如此怠惰。你就打算这样偷懒贪睡，荒废时间吗？', 'char_166_skfire/CN_010');
INSERT INTO `t_operator_voice` VALUES (1822, 55, '干员报到', '“王者之杖”所属，天火。在达成目的之前，我会暂时在罗德岛停留。目的？当然是将整合运动焚烧殆尽。', 'char_166_skfire/CN_011');
INSERT INTO `t_operator_voice` VALUES (1823, 55, '观看作战记录', '仅凭如此，可无法引出我真正的力量。', 'char_166_skfire/CN_012');
INSERT INTO `t_operator_voice` VALUES (1824, 55, '精英化晋升1', '理所应当。', 'char_166_skfire/CN_013');
INSERT INTO `t_operator_voice` VALUES (1825, 55, '精英化晋升2', '哼，偶尔享受些符合身份的待遇，也算是种休息。', 'char_166_skfire/CN_014');
INSERT INTO `t_operator_voice` VALUES (1826, 55, '编入队伍', '战场的温度太低了。', 'char_166_skfire/CN_017');
INSERT INTO `t_operator_voice` VALUES (1827, 55, '任命队长', '太阳的光辉，庇护着我。', 'char_166_skfire/CN_018');
INSERT INTO `t_operator_voice` VALUES (1828, 55, '行动出发', '出击。', 'char_166_skfire/CN_019');
INSERT INTO `t_operator_voice` VALUES (1829, 55, '行动开始', '在这天火的威光前拜伏吧。', 'char_166_skfire/CN_020');
INSERT INTO `t_operator_voice` VALUES (1830, 55, '选中干员1', '来吧。', 'char_166_skfire/CN_021');
INSERT INTO `t_operator_voice` VALUES (1831, 55, '选中干员2', '你打算怎么做？', 'char_166_skfire/CN_022');
INSERT INTO `t_operator_voice` VALUES (1832, 55, '部署1', '噩运只等待弱者坠落。', 'char_166_skfire/CN_023');
INSERT INTO `t_operator_voice` VALUES (1833, 55, '部署2', '智者先攻。', 'char_166_skfire/CN_024');
INSERT INTO `t_operator_voice` VALUES (1834, 55, '作战中1', '去吧。', 'char_166_skfire/CN_025');
INSERT INTO `t_operator_voice` VALUES (1835, 55, '作战中2', '火，炎，焱。', 'char_166_skfire/CN_026');
INSERT INTO `t_operator_voice` VALUES (1836, 55, '作战中3', '日炎将你焚尽！', 'char_166_skfire/CN_027');
INSERT INTO `t_operator_voice` VALUES (1837, 55, '作战中4', '天空将你点燃！', 'char_166_skfire/CN_028');
INSERT INTO `t_operator_voice` VALUES (1838, 55, '4星结束行动', '没有日炎烧不尽的灵魂。', 'char_166_skfire/CN_029');
INSERT INTO `t_operator_voice` VALUES (1839, 55, '3星结束行动', '你们啊，都被烧干净了，难道还想留下些什么吗？', 'char_166_skfire/CN_030');
INSERT INTO `t_operator_voice` VALUES (1840, 55, '非3星结束行动', '就只有这种程度......吗。', 'char_166_skfire/CN_031');
INSERT INTO `t_operator_voice` VALUES (1841, 55, '行动失败', '不可能......我这样的......怎么会......', 'char_166_skfire/CN_032');
INSERT INTO `t_operator_voice` VALUES (1842, 55, '进驻设施', '就不能为我准备一间更凉快点的房间吗？', 'char_166_skfire/CN_033');
INSERT INTO `t_operator_voice` VALUES (1843, 55, '戳一下', '你的手想被烧焦吗？', 'char_166_skfire/CN_034');
INSERT INTO `t_operator_voice` VALUES (1844, 55, '信赖触摸', '博士，可以为我说明一下这种理论吗？只要占用你十二分钟就够了。', 'char_166_skfire/CN_036');
INSERT INTO `t_operator_voice` VALUES (1845, 55, '标题', '明日方舟。', 'char_166_skfire/CN_037');
INSERT INTO `t_operator_voice` VALUES (1846, 55, '问候', '心情如何？', 'char_166_skfire/CN_042');
INSERT INTO `t_operator_voice` VALUES (1847, 56, '任命助理', '建筑设计，工业研发，科学研究，只要博士想得到的，我都能做到。', 'char_242_otter/CN_001');
INSERT INTO `t_operator_voice` VALUES (1848, 56, '交谈1', '工程部的人每次都不认可我的提案，是不是不喜欢我啊，怎么办......对了博士，你去代我提交下这份改进意见吧！就这么定了！', 'char_242_otter/CN_002');
INSERT INTO `t_operator_voice` VALUES (1849, 56, '交谈2', '莱茵生命里像我这样的工作室还有很多啦，但别的工作室三周才能完成一半的任务，我一周就能搞定。这可不是在吹牛哦。', 'char_242_otter/CN_003');
INSERT INTO `t_operator_voice` VALUES (1850, 56, '交谈3', '我做研究的时候千万别打扰我，我是会让咪波咬你的！我自己也会！', 'char_242_otter/CN_004');
INSERT INTO `t_operator_voice` VALUES (1851, 56, '晋升后交谈1', '晚上总是会很有灵感呢......但又......非常困！不，不行了，枕头在哪......我要休息一下......', 'char_242_otter/CN_005');
INSERT INTO `t_operator_voice` VALUES (1852, 56, '晋升后交谈2', '嗯？你想问为什么它们叫咪波？咪波是它的启动音效嘛，我觉得挺合适的。不像？那你可以摁下这个开关——等等！不是那个！那是自毁———呜啊啊啊，快跑！！', 'char_242_otter/CN_006');
INSERT INTO `t_operator_voice` VALUES (1853, 56, '信赖提升后交谈1', '刚刚走过去的......是赫默吧。如果那时候......喔，抱歉，保密协议还没过期，我不该多嘴的。', 'char_242_otter/CN_007');
INSERT INTO `t_operator_voice` VALUES (1854, 56, '信赖提升后交谈2', '闲下来就只会感到空虚呢......工程师就是这样的人啦。博士也会无所事事吗？不会？', 'char_242_otter/CN_008');
INSERT INTO `t_operator_voice` VALUES (1855, 56, '信赖提升后交谈3', '博士，我能申请——不是！不用十平米！上次是我不对，这回——三平米就够了！别，别跑啊博士！一平米实用科研面积也行！求你了！', 'char_242_otter/CN_009');
INSERT INTO `t_operator_voice` VALUES (1856, 56, '闲置', '哈啊......咖啡机在哪里......呼，博士你也要来点儿吗', 'char_242_otter/CN_010');
INSERT INTO `t_operator_voice` VALUES (1857, 56, '干员报到', '莱茵生命所属，鲁特拉工作室，竭诚为您服务。是的，没有其他成员，我一人就是整个工作室。很厉害吧！', 'char_242_otter/CN_011');
INSERT INTO `t_operator_voice` VALUES (1858, 56, '观看作战记录', '这个结构——有些意思，我得研究一下。', 'char_242_otter/CN_012');
INSERT INTO `t_operator_voice` VALUES (1859, 56, '精英化晋升1', '这次涨薪可真是及时，我又能升级一下设备了。', 'char_242_otter/CN_013');
INSERT INTO `t_operator_voice` VALUES (1860, 56, '精英化晋升2', '我向您保证过一定能出成果，现在，怎么样，是不是很令人满意呀~', 'char_242_otter/CN_014');
INSERT INTO `t_operator_voice` VALUES (1861, 56, '编入队伍', '咪波们，该上场咯！', 'char_242_otter/CN_017');
INSERT INTO `t_operator_voice` VALUES (1862, 56, '任命队长', '要同时指挥大家和咪波，就算是我也有点手忙脚乱......！', 'char_242_otter/CN_018');
INSERT INTO `t_operator_voice` VALUES (1863, 56, '行动出发', '这次也能得到良好的实验数据吧！', 'char_242_otter/CN_019');
INSERT INTO `t_operator_voice` VALUES (1864, 56, '行动开始', '上吧咪波，给敌人看看我们莱茵生命的技术力！', 'char_242_otter/CN_020');
INSERT INTO `t_operator_voice` VALUES (1865, 56, '选中干员1', '呜哇！', 'char_242_otter/CN_021');
INSERT INTO `t_operator_voice` VALUES (1866, 56, '选中干员2', '小心，小心一点！', 'char_242_otter/CN_022');
INSERT INTO `t_operator_voice` VALUES (1867, 56, '部署1', '轻拿轻放！', 'char_242_otter/CN_023');
INSERT INTO `t_operator_voice` VALUES (1868, 56, '部署2', '精密仪器是很贵重的！', 'char_242_otter/CN_024');
INSERT INTO `t_operator_voice` VALUES (1869, 56, '作战中1', '去吧，咪波三号！', 'char_242_otter/CN_025');
INSERT INTO `t_operator_voice` VALUES (1870, 56, '作战中2', '咪波，咬他！', 'char_242_otter/CN_026');
INSERT INTO `t_operator_voice` VALUES (1871, 56, '作战中3', '身体破损的话，再造一个就好了！', 'char_242_otter/CN_027');
INSERT INTO `t_operator_voice` VALUES (1872, 56, '作战中4', '我的咪波们可不仅仅是机械那么简单！', 'char_242_otter/CN_028');
INSERT INTO `t_operator_voice` VALUES (1873, 56, '4星结束行动', '来~咪波！给大家跳支舞庆祝一下！', 'char_242_otter/CN_029');
INSERT INTO `t_operator_voice` VALUES (1874, 56, '3星结束行动', '嗯，就这样轻轻松松碾压敌人吧。', 'char_242_otter/CN_030');
INSERT INTO `t_operator_voice` VALUES (1875, 56, '非3星结束行动', '设备稍微有些缺损，修理一下就能继续工作了。', 'char_242_otter/CN_031');
INSERT INTO `t_operator_voice` VALUES (1876, 56, '行动失败', '不！咪波——！......只，只能再造几台身体了......', 'char_242_otter/CN_032');
INSERT INTO `t_operator_voice` VALUES (1877, 56, '进驻设施', '我想要个新的工作室了！', 'char_242_otter/CN_033');
INSERT INTO `t_operator_voice` VALUES (1878, 56, '戳一下', '呜哇！我还在工作中啊！', 'char_242_otter/CN_034');
INSERT INTO `t_operator_voice` VALUES (1879, 56, '信赖触摸', '博士，让我摸摸你的头，会很有灵感的！', 'char_242_otter/CN_036');
INSERT INTO `t_operator_voice` VALUES (1880, 56, '标题', '明日方舟。', 'char_242_otter/CN_037');
INSERT INTO `t_operator_voice` VALUES (1881, 56, '问候', '博士！你好！', 'char_242_otter/CN_042');
INSERT INTO `t_operator_voice` VALUES (1882, 57, '任命助理', '博士，这些是今天的工作——是啊，堆积如山呢，一起加油吧。', 'char_108_silent/CN_001');
INSERT INTO `t_operator_voice` VALUES (1883, 57, '交谈1', '伊芙利特又在闹了吗？抱歉，之后我会责备她的。', 'char_108_silent/CN_002');
INSERT INTO `t_operator_voice` VALUES (1884, 57, '交谈2', 'Zzzzz......嗯？呃，我只是想小睡一下......果然还是不太适应在白天工作。', 'char_108_silent/CN_003');
INSERT INTO `t_operator_voice` VALUES (1885, 57, '交谈3', '离开莱茵生命的理由？因为发生过很多事，我也不知道该从什么时候说起......', 'char_108_silent/CN_004');
INSERT INTO `t_operator_voice` VALUES (1886, 57, '晋升后交谈1', '塞雷娅这个名字,您听过吗？她以前和我一样，也是莱茵生命的科学家——然后，她也是让伊芙利特诞生的人之一......', 'char_108_silent/CN_005');
INSERT INTO `t_operator_voice` VALUES (1887, 57, '晋升后交谈2', '莱茵生命所做的有关生命的研究，已经到了尝试将源石碎片植入身体的地步......对不起，我们还是换个话题吧。', 'char_108_silent/CN_006');
INSERT INTO `t_operator_voice` VALUES (1888, 57, '信赖提升后交谈1', '塞雷娅？您说她在罗德岛？是来找伊芙利特吗......那时候她明明头也不回地就离开了，为什么现在还要......！', 'char_108_silent/CN_007');
INSERT INTO `t_operator_voice` VALUES (1889, 57, '信赖提升后交谈2', '我不会让伊芙利特见塞雷娅的。也请您相信，我这样做，是对伊芙利特好......！', 'char_108_silent/CN_008');
INSERT INTO `t_operator_voice` VALUES (1890, 57, '信赖提升后交谈3', '和我一样，您也在试图清除矿石病吗？听说罗德岛的医疗技术开发也与您有关。呵呵，对我们这些研究者来说，这真是让人憧憬呢。', 'char_108_silent/CN_009');
INSERT INTO `t_operator_voice` VALUES (1891, 57, '闲置', '......博士，睡着了吗？算了，剩下的工作我来做吧。', 'char_108_silent/CN_010');
INSERT INTO `t_operator_voice` VALUES (1892, 57, '干员报到', '奥利维亚·赫默，莱茵生命医学研究所的源石研究者。清除这个世界上所有的矿石病，这是我的愿望，即使为此面临生命危险也不会改变。', 'char_108_silent/CN_011');
INSERT INTO `t_operator_voice` VALUES (1893, 57, '观看作战记录', '我会好好运用这份新的力量。', 'char_108_silent/CN_012');
INSERT INTO `t_operator_voice` VALUES (1894, 57, '精英化晋升1', '我开发的药物非常有效吗？太好了，比起晋升，这个消息更让我高兴。', 'char_108_silent/CN_013');
INSERT INTO `t_operator_voice` VALUES (1895, 57, '精英化晋升2', '博士，我等待着，等待着从这片大地上彻底清除矿石病的那天，与您并肩作战，共同研究，这一天，也许终会到来......', 'char_108_silent/CN_014');
INSERT INTO `t_operator_voice` VALUES (1896, 57, '编入队伍', '明白，我会做好支援工作。', 'char_108_silent/CN_017');
INSERT INTO `t_operator_voice` VALUES (1897, 57, '任命队长', '队员们，全力以赴地战斗吧。', 'char_108_silent/CN_018');
INSERT INTO `t_operator_voice` VALUES (1898, 57, '行动出发', '这场战斗，也是源石的错吗？', 'char_108_silent/CN_019');
INSERT INTO `t_operator_voice` VALUES (1899, 57, '行动开始', '观察并确定他们的感染类型，这对实验很有用。', 'char_108_silent/CN_020');
INSERT INTO `t_operator_voice` VALUES (1900, 57, '选中干员1', '博士，让我去吧。', 'char_108_silent/CN_021');
INSERT INTO `t_operator_voice` VALUES (1901, 57, '选中干员2', '没有异常。', 'char_108_silent/CN_022');
INSERT INTO `t_operator_voice` VALUES (1902, 57, '部署1', '集中精力。', 'char_108_silent/CN_023');
INSERT INTO `t_operator_voice` VALUES (1903, 57, '部署2', '保持冷静。', 'char_108_silent/CN_024');
INSERT INTO `t_operator_voice` VALUES (1904, 57, '作战中1', '我会治好你的。', 'char_108_silent/CN_025');
INSERT INTO `t_operator_voice` VALUES (1905, 57, '作战中2', '别慌张。', 'char_108_silent/CN_026');
INSERT INTO `t_operator_voice` VALUES (1906, 57, '作战中3', '去吧。', 'char_108_silent/CN_027');
INSERT INTO `t_operator_voice` VALUES (1907, 57, '作战中4', '帮助大家。', 'char_108_silent/CN_028');
INSERT INTO `t_operator_voice` VALUES (1908, 57, '4星结束行动', '为了战胜比现在更强的敌人，我们难道也要更多利用源石的力量......吗？', 'char_108_silent/CN_029');
INSERT INTO `t_operator_voice` VALUES (1909, 57, '3星结束行动', '医疗组请马上进行组织采取，这将成为此地区的感染样本。', 'char_108_silent/CN_030');
INSERT INTO `t_operator_voice` VALUES (1910, 57, '非3星结束行动', '请伤者冷静下来，医疗组会为各位进行治疗。', 'char_108_silent/CN_031');
INSERT INTO `t_operator_voice` VALUES (1911, 57, '行动失败', '......现在不是沮丧的时候，我们必须立刻撤退。', 'char_108_silent/CN_032');
INSERT INTO `t_operator_voice` VALUES (1912, 57, '进驻设施', '这个房间......糟糕，是那种会让人产生睡意的设计风格！', 'char_108_silent/CN_033');
INSERT INTO `t_operator_voice` VALUES (1913, 57, '戳一下', '嗯？', 'char_108_silent/CN_034');
INSERT INTO `t_operator_voice` VALUES (1914, 57, '信赖触摸', '博士，您最近变得更温柔了呢。', 'char_108_silent/CN_036');
INSERT INTO `t_operator_voice` VALUES (1915, 57, '标题', '明日方舟。', 'char_108_silent/CN_037');
INSERT INTO `t_operator_voice` VALUES (1916, 57, '问候', '您好，博士。', 'char_108_silent/CN_042');
INSERT INTO `t_operator_voice` VALUES (1917, 58, '任命助理', '博士，凯尔希医生让我给您做个血液化验，在我准备好之前先随便聊聊吧。', 'char_171_bldsk/CN_001');
INSERT INTO `t_operator_voice` VALUES (1918, 58, '交谈1', '我能不能控制吸血冲动？是不是小看我了呀！作为经受过训练的专业医生，我还是能分得清食物和病人的！', 'char_171_bldsk/CN_002');
INSERT INTO `t_operator_voice` VALUES (1919, 58, '交谈2', '记录全体干员的血液常规指数是我的工作，即使当事人不太情愿，也是没办法的......这些血？这些......当然是用作血液检查的样本啊！你在想什么啦！', 'char_171_bldsk/CN_003');
INSERT INTO `t_operator_voice` VALUES (1920, 58, '交谈3', '这里居然还有一位我的同族，而且担任的职位居然是......工程师？比起血液，她好像更喜欢机油的味道欸？', 'char_171_bldsk/CN_004');
INSERT INTO `t_operator_voice` VALUES (1921, 58, '晋升后交谈1', '成为医生的理由？其实也不是什么高尚的念头啦，仅仅是出于我们一族畸形的遭遇吧。之后，我的能力意外被人需要，事情就顺理成章地变成这样了......', 'char_171_bldsk/CN_005');
INSERT INTO `t_operator_voice` VALUES (1922, 58, '晋升后交谈2', '我们抑制本能冲动的方法？有一整套的自我暗示课程，食用的血液也有代用品，总之方法多种多样。', 'char_171_bldsk/CN_006');
INSERT INTO `t_operator_voice` VALUES (1923, 58, '信赖提升后交谈1', '这只是采血后的常规处理啊，博士请不要动......唔，血、血液......您的血......我，我从来没闻过这么香的味道......我似乎被，被迷惑住了......！', 'char_171_bldsk/CN_007');
INSERT INTO `t_operator_voice` VALUES (1924, 58, '信赖提升后交谈2', '补充生理盐水！更换过滤器！砂罐、碳罐消毒......好嘞。博士，医疗设备可需要好好维护哦，凯尔希医生说过，等到博士您肾衰竭的时候，就得靠这些设备救命了......哎呀，开，开玩笑的啦。', 'char_171_bldsk/CN_008');
INSERT INTO `t_operator_voice` VALUES (1925, 58, '信赖提升后交谈3', '好啦好啦，我承认，以前我是品尝过不少种族不同类型的血，而且，也确实以分析它们味道间的差异为乐......现在我没尝过的，大概就只有博士您的血了吧？呵呵......', 'char_171_bldsk/CN_009');
INSERT INTO `t_operator_voice` VALUES (1926, 58, '闲置', '哎，你居然还有时间睡觉，真让人羡慕啊。', 'char_171_bldsk/CN_010');
INSERT INTO `t_operator_voice` VALUES (1927, 58, '干员报到', '我，华法琳，血液学专科医师。比起普通的治疗法术，我会利用血液学相关知识和经验，为你们提供比常规方式更好的医疗保障。那么，请多关照啦。', 'char_171_bldsk/CN_011');
INSERT INTO `t_operator_voice` VALUES (1928, 58, '观看作战记录', '嗯。工作效率好像提高了。', 'char_171_bldsk/CN_012');
INSERT INTO `t_operator_voice` VALUES (1929, 58, '精英化晋升1', '......所以说，您有在认真听吗？急救备用的稀有血型呀，想要搜集这些血液是非常艰难的，所以......啊？晋升？是现在吗？', 'char_171_bldsk/CN_013');
INSERT INTO `t_operator_voice` VALUES (1930, 58, '精英化晋升2', '也就是说，今后的工作会更繁重了，对吧？我早就预料到会有这一天了！毕竟，我原本就是这个领域的专家嘛。放心吧，一切都已经准备好了！', 'char_171_bldsk/CN_014');
INSERT INTO `t_operator_voice` VALUES (1931, 58, '编入队伍', '等一下，至少等我准备好医疗器具之后再说。', 'char_171_bldsk/CN_017');
INSERT INTO `t_operator_voice` VALUES (1932, 58, '任命队长', '队长只需要向您负责就好了，对吧？', 'char_171_bldsk/CN_018');
INSERT INTO `t_operator_voice` VALUES (1933, 58, '行动出发', '医疗用品都准备好了，随时都可以出发。', 'char_171_bldsk/CN_019');
INSERT INTO `t_operator_voice` VALUES (1934, 58, '行动开始', '现在还不太了解敌人的情况，毕竟光是照料我方人员，就把我的时间都排满了......', 'char_171_bldsk/CN_020');
INSERT INTO `t_operator_voice` VALUES (1935, 58, '选中干员1', '请说。', 'char_171_bldsk/CN_021');
INSERT INTO `t_operator_voice` VALUES (1936, 58, '选中干员2', '医疗器械设置完毕。', 'char_171_bldsk/CN_022');
INSERT INTO `t_operator_voice` VALUES (1937, 58, '部署1', '负伤的人，要记得立刻求救！', 'char_171_bldsk/CN_023');
INSERT INTO `t_operator_voice` VALUES (1938, 58, '部署2', '开始治疗。', 'char_171_bldsk/CN_024');
INSERT INTO `t_operator_voice` VALUES (1939, 58, '作战中1', '准备输血！', 'char_171_bldsk/CN_025');
INSERT INTO `t_operator_voice` VALUES (1940, 58, '作战中2', '止血钳！', 'char_171_bldsk/CN_026');
INSERT INTO `t_operator_voice` VALUES (1941, 58, '作战中3', '这里有急救箱，请用这个！', 'char_171_bldsk/CN_027');
INSERT INTO `t_operator_voice` VALUES (1942, 58, '作战中4', '我马上就来！', 'char_171_bldsk/CN_028');
INSERT INTO `t_operator_voice` VALUES (1943, 58, '4星结束行动', '医疗小队也这么紧张，后方部队的压力实在不小......还好，我已经习惯啦。', 'char_171_bldsk/CN_029');
INSERT INTO `t_operator_voice` VALUES (1944, 58, '3星结束行动', '战场是一个只会制造可怕悲剧的地方，希望那些人也能理解到这点。', 'char_171_bldsk/CN_030');
INSERT INTO `t_operator_voice` VALUES (1945, 58, '非3星结束行动', '优先救助伤员吧，先别管那些逃跑的敌人了。', 'char_171_bldsk/CN_031');
INSERT INTO `t_operator_voice` VALUES (1946, 58, '行动失败', '对不起，备用药剂也不够了......', 'char_171_bldsk/CN_032');
INSERT INTO `t_operator_voice` VALUES (1947, 58, '进驻设施', '果然，我还是习惯一个人思考问题啊......', 'char_171_bldsk/CN_033');
INSERT INTO `t_operator_voice` VALUES (1948, 58, '戳一下', '欸，您在做什么呀......', 'char_171_bldsk/CN_034');
INSERT INTO `t_operator_voice` VALUES (1949, 58, '信赖触摸', '博士，给您做定期检查的日子，是不是快到了？', 'char_171_bldsk/CN_036');
INSERT INTO `t_operator_voice` VALUES (1950, 58, '标题', '明日方舟。', 'char_171_bldsk/CN_037');
INSERT INTO `t_operator_voice` VALUES (1951, 58, '问候', '你好，博士。', 'char_171_bldsk/CN_042');
INSERT INTO `t_operator_voice` VALUES (1952, 59, '任命助理', '很高兴见到您，博士，愿光明护佑您。', 'char_148_nearl/CN_001');
INSERT INTO `t_operator_voice` VALUES (1953, 59, '交谈1', '我希望被分配到最危险的战场，这样，我和我的盾才能保护每一位战友。', 'char_148_nearl/CN_002');
INSERT INTO `t_operator_voice` VALUES (1954, 59, '交谈2', '“骑士”的称号理应是荣耀的象征，却不知曾几何时，它已经被当成了商品......', 'char_148_nearl/CN_003');
INSERT INTO `t_operator_voice` VALUES (1955, 59, '交谈3', '只要光仍在指引我，残忍便不能使我屈服，暴虐也不能让我屈膝。', 'char_148_nearl/CN_004');
INSERT INTO `t_operator_voice` VALUES (1956, 59, '晋升后交谈1', '非常感谢你，博士。', 'char_148_nearl/CN_005');
INSERT INTO `t_operator_voice` VALUES (1957, 59, '晋升后交谈2', '卡西米尔的骑士精神，本应是这片大地上苦难者的救星，但现在的它，已经彻底沦落为被资本肆意操弄的腐朽遗产。没有比这更耻辱的了。', 'char_148_nearl/CN_006');
INSERT INTO `t_operator_voice` VALUES (1958, 59, '信赖提升后交谈1', '闪灵也是引导我前进的光芒。是的，她背负着我一直追寻的希望。', 'char_148_nearl/CN_007');
INSERT INTO `t_operator_voice` VALUES (1959, 59, '信赖提升后交谈2', '当闪灵听到罗德岛这一名字，她似乎动摇了，我却不知道她动摇的理由。', 'char_148_nearl/CN_008');
INSERT INTO `t_operator_voice` VALUES (1960, 59, '信赖提升后交谈3', '这被矿石病侵袭的时代，将会为罗德岛的希望之光所照耀。', 'char_148_nearl/CN_009');
INSERT INTO `t_operator_voice` VALUES (1961, 59, '闲置', '稍稍，享受下这短暂的休憩吧。', 'char_148_nearl/CN_010');
INSERT INTO `t_operator_voice` VALUES (1962, 59, '干员报到', '玛嘉烈·临光，卡西米尔耀骑士......不，就让我作为一名罗德岛干员，用这面盾牌捍卫您的生命、您的荣耀。', 'char_148_nearl/CN_011');
INSERT INTO `t_operator_voice` VALUES (1963, 59, '观看作战记录', '感谢您给予我力量。', 'char_148_nearl/CN_012');
INSERT INTO `t_operator_voice` VALUES (1964, 59, '精英化晋升1', '感谢你，博士。我的力量为拯救苦难而存在，是的，无论如何，我都会铭记于心。', 'char_148_nearl/CN_013');
INSERT INTO `t_operator_voice` VALUES (1965, 59, '精英化晋升2', '闪灵曾说过，黎明还很遥远。但我相信总有一天，我们会亲手拾取那些逝去已久的荣光。', 'char_148_nearl/CN_014');
INSERT INTO `t_operator_voice` VALUES (1966, 59, '编入队伍', '一切伤害，都由我来阻挡。', 'char_148_nearl/CN_017');
INSERT INTO `t_operator_voice` VALUES (1967, 59, '任命队长', '绝不辱没罗德岛之名。', 'char_148_nearl/CN_018');
INSERT INTO `t_operator_voice` VALUES (1968, 59, '行动出发', '准备妥当了，出发。', 'char_148_nearl/CN_019');
INSERT INTO `t_operator_voice` VALUES (1969, 59, '行动开始', '荣耀必须被捍卫。', 'char_148_nearl/CN_020');
INSERT INTO `t_operator_voice` VALUES (1970, 59, '选中干员1', '以您的名义。', 'char_148_nearl/CN_021');
INSERT INTO `t_operator_voice` VALUES (1971, 59, '选中干员2', '我将为您竭尽全力。', 'char_148_nearl/CN_022');
INSERT INTO `t_operator_voice` VALUES (1972, 59, '部署1', '我来掩护。', 'char_148_nearl/CN_023');
INSERT INTO `t_operator_voice` VALUES (1973, 59, '部署2', '请躲在我身后。', 'char_148_nearl/CN_024');
INSERT INTO `t_operator_voice` VALUES (1974, 59, '作战中1', '光。', 'char_148_nearl/CN_025');
INSERT INTO `t_operator_voice` VALUES (1975, 59, '作战中2', '我在。', 'char_148_nearl/CN_026');
INSERT INTO `t_operator_voice` VALUES (1976, 59, '作战中3', '别担心。', 'char_148_nearl/CN_027');
INSERT INTO `t_operator_voice` VALUES (1977, 59, '作战中4', '复苏吧。', 'char_148_nearl/CN_028');
INSERT INTO `t_operator_voice` VALUES (1978, 59, '4星结束行动', '真正的骑士决不屈服于暴力，在这把战锤面前忏悔吧。', 'char_148_nearl/CN_029');
INSERT INTO `t_operator_voice` VALUES (1979, 59, '3星结束行动', '向前迈进吧，总有一天，我们可以......', 'char_148_nearl/CN_030');
INSERT INTO `t_operator_voice` VALUES (1980, 59, '非3星结束行动', '真正的骑士不会追击失去战意的人，无论什么样的人，都应该给予他们重新选择的机会。', 'char_148_nearl/CN_031');
INSERT INTO `t_operator_voice` VALUES (1981, 59, '行动失败', '别在这里放弃。', 'char_148_nearl/CN_032');
INSERT INTO `t_operator_voice` VALUES (1982, 59, '进驻设施', '随便走走就好了。', 'char_148_nearl/CN_033');
INSERT INTO `t_operator_voice` VALUES (1983, 59, '戳一下', '有敌人......呃，博士？', 'char_148_nearl/CN_034');
INSERT INTO `t_operator_voice` VALUES (1984, 59, '信赖触摸', '博士，那个......咳，我可以进来吗？听说您的办公室里，收藏了大量的书籍。', 'char_148_nearl/CN_036');
INSERT INTO `t_operator_voice` VALUES (1985, 59, '标题', '明日方舟。', 'char_148_nearl/CN_037');
INSERT INTO `t_operator_voice` VALUES (1986, 59, '问候', '早上好，博士。', 'char_148_nearl/CN_042');
INSERT INTO `t_operator_voice` VALUES (1987, 60, '任命助理', '这里......有危险的气味。', 'char_144_red/CN_001');
INSERT INTO `t_operator_voice` VALUES (1988, 60, '交谈1', '红要单独行动。', 'char_144_red/CN_002');
INSERT INTO `t_operator_voice` VALUES (1989, 60, '交谈2', '名字，报酬，杀掉。很简单。凯尔希与红是这么约定的。', 'char_144_red/CN_003');
INSERT INTO `t_operator_voice` VALUES (1990, 60, '交谈3', '狼有牙齿，红有刀。狼不会休息，所以红也不能。', 'char_144_red/CN_004');
INSERT INTO `t_operator_voice` VALUES (1991, 60, '晋升后交谈1', '“外婆”在呼唤......红能听见。', 'char_144_red/CN_005');
INSERT INTO `t_operator_voice` VALUES (1992, 60, '晋升后交谈2', '......荒野的风，尘土里的残骸，群星下的嚎叫......红闻到了。那是狼的气味。红，加入狩猎。', 'char_144_red/CN_006');
INSERT INTO `t_operator_voice` VALUES (1993, 60, '信赖提升后交谈1', '家人？红没有家人。红一开始就是孤身一人。博士是......需要家人吗？', 'char_144_red/CN_007');
INSERT INTO `t_operator_voice` VALUES (1994, 60, '信赖提升后交谈2', '她们都躲着红。红其实只是想......摸摸她们的尾巴。普罗旺斯的，德克萨斯的......红在她们身上，能闻到红喜欢的味道。', 'char_144_red/CN_008');
INSERT INTO `t_operator_voice` VALUES (1995, 60, '信赖提升后交谈3', '博士，红也想过......如果红不是猎狼人，会不会也能有不一样的生活？这个问题要红......自己回答？唔。', 'char_144_red/CN_009');
INSERT INTO `t_operator_voice` VALUES (1996, 60, '闲置', '博士。不，只是觉得你睡着了。现在，还不能睡。', 'char_144_red/CN_010');
INSERT INTO `t_operator_voice` VALUES (1997, 60, '干员报到', '猎狼人，红，闻到了狼的气息。', 'char_144_red/CN_011');
INSERT INTO `t_operator_voice` VALUES (1998, 60, '观看作战记录', '只要对狩猎有利。', 'char_144_red/CN_012');
INSERT INTO `t_operator_voice` VALUES (1999, 60, '精英化晋升1', '无所谓。', 'char_144_red/CN_013');
INSERT INTO `t_operator_voice` VALUES (2000, 60, '精英化晋升2', '伪装？荣誉可以伪装自己，红能接受。', 'char_144_red/CN_014');
INSERT INTO `t_operator_voice` VALUES (2001, 60, '编入队伍', '成功混进来了。', 'char_144_red/CN_017');
INSERT INTO `t_operator_voice` VALUES (2002, 60, '任命队长', '红该做什么？', 'char_144_red/CN_018');
INSERT INTO `t_operator_voice` VALUES (2003, 60, '行动出发', '狩猎，开始。', 'char_144_red/CN_019');
INSERT INTO `t_operator_voice` VALUES (2004, 60, '行动开始', '气味，很浓烈。', 'char_144_red/CN_020');
INSERT INTO `t_operator_voice` VALUES (2005, 60, '选中干员1', '快。', 'char_144_red/CN_021');
INSERT INTO `t_operator_voice` VALUES (2006, 60, '选中干员2', '简单明了。', 'char_144_red/CN_022');
INSERT INTO `t_operator_voice` VALUES (2007, 60, '部署1', '速度。', 'char_144_red/CN_023');
INSERT INTO `t_operator_voice` VALUES (2008, 60, '部署2', '技巧。', 'char_144_red/CN_024');
INSERT INTO `t_operator_voice` VALUES (2009, 60, '作战中1', '从阴影中......！', 'char_144_red/CN_025');
INSERT INTO `t_operator_voice` VALUES (2010, 60, '作战中2', '搅乱他们。', 'char_144_red/CN_026');
INSERT INTO `t_operator_voice` VALUES (2011, 60, '作战中3', '你无路可退。', 'char_144_red/CN_027');
INSERT INTO `t_operator_voice` VALUES (2012, 60, '作战中4', '红的眼中——倒映出你的死。', 'char_144_red/CN_028');
INSERT INTO `t_operator_voice` VALUES (2013, 60, '4星结束行动', '全灭。', 'char_144_red/CN_029');
INSERT INTO `t_operator_voice` VALUES (2014, 60, '3星结束行动', '猎物的下场，只有一种。', 'char_144_red/CN_030');
INSERT INTO `t_operator_voice` VALUES (2015, 60, '非3星结束行动', '收获......不够丰富。', 'char_144_red/CN_031');
INSERT INTO `t_operator_voice` VALUES (2016, 60, '行动失败', '......红的刀......还不够锋利......', 'char_144_red/CN_032');
INSERT INTO `t_operator_voice` VALUES (2017, 60, '进驻设施', '红，不太习惯室内，不过，也不讨厌。', 'char_144_red/CN_033');
INSERT INTO `t_operator_voice` VALUES (2018, 60, '戳一下', '当心手，别被红刮伤了。', 'char_144_red/CN_034');
INSERT INTO `t_operator_voice` VALUES (2019, 60, '信赖触摸', '咻......呼......', 'char_144_red/CN_036');
INSERT INTO `t_operator_voice` VALUES (2020, 60, '标题', '明日方舟。', 'char_144_red/CN_037');
INSERT INTO `t_operator_voice` VALUES (2021, 60, '问候', '博士，累了吗？', 'char_144_red/CN_042');
INSERT INTO `t_operator_voice` VALUES (2022, 61, '任命助理', '博士，今天值班的人是我。', 'char_107_liskam/CN_001');
INSERT INTO `t_operator_voice` VALUES (2023, 61, '交谈1', '无论什么任务都要认真对待，这是我们专业保全的基本原则。', 'char_107_liskam/CN_002');
INSERT INTO `t_operator_voice` VALUES (2024, 61, '交谈2', '其实，我很不擅长应付芙兰卡这样的人。当初是怎么和她成为搭档的，我到现在都不清楚......', 'char_107_liskam/CN_003');
INSERT INTO `t_operator_voice` VALUES (2025, 61, '交谈3', '进入BSW必须经过严格的选拔。我很矮小，也没有优秀的法术天赋和血统，所以被大家说是绝对不可能过关的，就连我自己也曾这样认为......', 'char_107_liskam/CN_004');
INSERT INTO `t_operator_voice` VALUES (2026, 61, '晋升后交谈1', 'BSW作为安保公司是顶级的，它也很适合我。只是，在罗德岛的时候，我会感觉更加自由一点。', 'char_107_liskam/CN_005');
INSERT INTO `t_operator_voice` VALUES (2027, 61, '晋升后交谈2', '能走到这一步，真是不可思议啊！简直就像真正的龙一样......！啊，咳咳，不、不，只要对作战有利，别的怎样都好啦！', 'char_107_liskam/CN_006');
INSERT INTO `t_operator_voice` VALUES (2028, 61, '信赖提升后交谈1', '博士，您能这样信任我，真的非常感谢。', 'char_107_liskam/CN_007');
INSERT INTO `t_operator_voice` VALUES (2029, 61, '信赖提升后交谈2', '我比看上去更好相处，熟识我的人都这么说。博士也这么想吗？', 'char_107_liskam/CN_008');
INSERT INTO `t_operator_voice` VALUES (2030, 61, '信赖提升后交谈3', '芙兰卡又和您说我在黑钢时的事？啊啊啊，真是的......别相信她说的话啊！', 'char_107_liskam/CN_009');
INSERT INTO `t_operator_voice` VALUES (2031, 61, '闲置', '博士......现在还是工作时间哦。', 'char_107_liskam/CN_010');
INSERT INTO `t_operator_voice` VALUES (2032, 61, '干员报到', '重装干员雷蛇，待命中。我曾参与数次要人保全、据点攻坚、人质解救、威胁肃清的特种行动。在此次派遣行动期间，我会严格保障您的人身安全。', 'char_107_liskam/CN_011');
INSERT INTO `t_operator_voice` VALUES (2033, 61, '观看作战记录', '升级完成。', 'char_107_liskam/CN_012');
INSERT INTO `t_operator_voice` VALUES (2034, 61, '精英化晋升1', '非常感谢您的任命，博士。', 'char_107_liskam/CN_013');
INSERT INTO `t_operator_voice` VALUES (2035, 61, '精英化晋升2', '谢谢您，博士。作为您的干员，我将全身心地投入到保护罗德岛的工作中。', 'char_107_liskam/CN_014');
INSERT INTO `t_operator_voice` VALUES (2036, 61, '编入队伍', '准备完毕。', 'char_107_liskam/CN_017');
INSERT INTO `t_operator_voice` VALUES (2037, 61, '任命队长', '了解，我会严格要求队员们遵守罗德岛的行为准则和秩序。', 'char_107_liskam/CN_018');
INSERT INTO `t_operator_voice` VALUES (2038, 61, '行动出发', '出击许可已经下达。', 'char_107_liskam/CN_019');
INSERT INTO `t_operator_voice` VALUES (2039, 61, '行动开始', '等待下一步指示。', 'char_107_liskam/CN_020');
INSERT INTO `t_operator_voice` VALUES (2040, 61, '选中干员1', '我已就位。', 'char_107_liskam/CN_021');
INSERT INTO `t_operator_voice` VALUES (2041, 61, '选中干员2', '雷蛇，准备完毕。', 'char_107_liskam/CN_022');
INSERT INTO `t_operator_voice` VALUES (2042, 61, '部署1', '视野清晰。', 'char_107_liskam/CN_023');
INSERT INTO `t_operator_voice` VALUES (2043, 61, '部署2', '时刻准备。', 'char_107_liskam/CN_024');
INSERT INTO `t_operator_voice` VALUES (2044, 61, '作战中1', '雷鸣。', 'char_107_liskam/CN_025');
INSERT INTO `t_operator_voice` VALUES (2045, 61, '作战中2', '闪电风暴。', 'char_107_liskam/CN_026');
INSERT INTO `t_operator_voice` VALUES (2046, 61, '作战中3', '一人一颗子弹。', 'char_107_liskam/CN_027');
INSERT INTO `t_operator_voice` VALUES (2047, 61, '作战中4', '充能！', 'char_107_liskam/CN_028');
INSERT INTO `t_operator_voice` VALUES (2048, 61, '4星结束行动', '这算什么，我和芙兰卡可经历过更惨烈的战斗。', 'char_107_liskam/CN_029');
INSERT INTO `t_operator_voice` VALUES (2049, 61, '3星结束行动', '清除。已经镇压全部目标。', 'char_107_liskam/CN_030');
INSERT INTO `t_operator_voice` VALUES (2050, 61, '非3星结束行动', '安全，此区域已被肃清。', 'char_107_liskam/CN_031');
INSERT INTO `t_operator_voice` VALUES (2051, 61, '行动失败', '救护班和伤员一起撤退！能战斗的人和我一起阻挡敌人!', 'char_107_liskam/CN_032');
INSERT INTO `t_operator_voice` VALUES (2052, 61, '进驻设施', '这房间会漏电吗？有没有做好保护措施？', 'char_107_liskam/CN_033');
INSERT INTO `t_operator_voice` VALUES (2053, 61, '戳一下', '请小心触电。', 'char_107_liskam/CN_034');
INSERT INTO `t_operator_voice` VALUES (2054, 61, '信赖触摸', '医生，芙兰卡的治疗还在进行吗？对了，工作之外博士请好好休息。', 'char_107_liskam/CN_036');
INSERT INTO `t_operator_voice` VALUES (2055, 61, '标题', '明日方舟。', 'char_107_liskam/CN_037');
INSERT INTO `t_operator_voice` VALUES (2056, 61, '问候', '早上好，博士。', 'char_107_liskam/CN_042');
INSERT INTO `t_operator_voice` VALUES (2057, 62, '任命助理', '放心吧老板，不论在哪儿我都是您生命安全的保障！对了对了，每次行动前要扫一下这里的码，别忘记哟~', 'char_201_moeshd/CN_001');
INSERT INTO `t_operator_voice` VALUES (2058, 62, '交谈1', '努力工作，赚取报酬，都是为了提升自己的生活品质嘛。', 'char_201_moeshd/CN_002');
INSERT INTO `t_operator_voice` VALUES (2059, 62, '交谈2', '大家的装备都在闪闪发光！好想摸一下啊～', 'char_201_moeshd/CN_003');
INSERT INTO `t_operator_voice` VALUES (2060, 62, '交谈3', '我最喜欢新装备了！无论是看到什么都好想买下来啊。就这样，我陷入了无止境的购物漩涡......', 'char_201_moeshd/CN_004');
INSERT INTO `t_operator_voice` VALUES (2061, 62, '晋升后交谈1', '为了补贴每个月的花销，我会经常搜集些货物，做点小生意！当然，可都是些物美价廉的好东西哦！', 'char_201_moeshd/CN_005');
INSERT INTO `t_operator_voice` VALUES (2062, 62, '晋升后交谈2', '这也是运货，那也是运货，既然都是运货，我当然更愿意搬运些好用的东西，或者美味的食物！', 'char_201_moeshd/CN_006');
INSERT INTO `t_operator_voice` VALUES (2063, 62, '信赖提升后交谈1', '老板老板，我找到了特别好吃的东西哦！来尝尝看吧？哼哼，除了装备之外，我可最喜欢美味的食物了！', 'char_201_moeshd/CN_007');
INSERT INTO `t_operator_voice` VALUES (2064, 62, '信赖提升后交谈2', '老板！要不要来看看我刚进的一批货？潮款服装，日用工具，办公用品，武器装备，应有尽有哦！买的越多，折扣越大哦！', 'char_201_moeshd/CN_008');
INSERT INTO `t_operator_voice` VALUES (2065, 62, '信赖提升后交谈3', '老板，谢谢您！为我介绍了这么多的工作！托您的福，以前买不起的东西，现在我都负担的起啦！您是最好的老板！最喜欢您了！', 'char_201_moeshd/CN_009');
INSERT INTO `t_operator_voice` VALUES (2066, 62, '闲置', '哎呀~这个月也完蛋了......', 'char_201_moeshd/CN_010');
INSERT INTO `t_operator_voice` VALUES (2067, 62, '干员报到', '我是可颂。老板您的运气可真好，能得到我这样安全可靠刀枪不入的优质干员！另外，现在雇佣我的话，还可以给您打八折哦！', 'char_201_moeshd/CN_011');
INSERT INTO `t_operator_voice` VALUES (2068, 62, '观看作战记录', '变强的话，就能接到更多工作了吧~', 'char_201_moeshd/CN_012');
INSERT INTO `t_operator_voice` VALUES (2069, 62, '精英化晋升1', '多亏了老板，我才能得到这么好的岗位，太感谢您了！', 'char_201_moeshd/CN_013');
INSERT INTO `t_operator_voice` VALUES (2070, 62, '精英化晋升2', '老板老板，您发的奖金，我可没有乱花哦。快看快看，这些都是用来保护您的新装备！', 'char_201_moeshd/CN_014');
INSERT INTO `t_operator_voice` VALUES (2071, 62, '编入队伍', '防卫就交给我吧！', 'char_201_moeshd/CN_017');
INSERT INTO `t_operator_voice` VALUES (2072, 62, '任命队长', '大家！让我们漂漂亮亮地完成这次工作吧！', 'char_201_moeshd/CN_018');
INSERT INTO `t_operator_voice` VALUES (2073, 62, '行动出发', '一定给老板一个您满意的战果！', 'char_201_moeshd/CN_019');
INSERT INTO `t_operator_voice` VALUES (2074, 62, '行动开始', '敌人给咱们送装备来了！', 'char_201_moeshd/CN_020');
INSERT INTO `t_operator_voice` VALUES (2075, 62, '选中干员1', '什么什么？', 'char_201_moeshd/CN_021');
INSERT INTO `t_operator_voice` VALUES (2076, 62, '选中干员2', '让我试试新装备吧！', 'char_201_moeshd/CN_022');
INSERT INTO `t_operator_voice` VALUES (2077, 62, '部署1', '嘿咻！', 'char_201_moeshd/CN_023');
INSERT INTO `t_operator_voice` VALUES (2078, 62, '部署2', '到了！', 'char_201_moeshd/CN_024');
INSERT INTO `t_operator_voice` VALUES (2079, 62, '作战中1', '停下！', 'char_201_moeshd/CN_025');
INSERT INTO `t_operator_voice` VALUES (2080, 62, '作战中2', '此路不通！', 'char_201_moeshd/CN_026');
INSERT INTO `t_operator_voice` VALUES (2081, 62, '作战中3', '雷霆一击！', 'char_201_moeshd/CN_027');
INSERT INTO `t_operator_voice` VALUES (2082, 62, '作战中4', '这招对我没用！', 'char_201_moeshd/CN_028');
INSERT INTO `t_operator_voice` VALUES (2083, 62, '4星结束行动', '敌人有没有掉落宝箱什么的呢？', 'char_201_moeshd/CN_029');
INSERT INTO `t_operator_voice` VALUES (2084, 62, '3星结束行动', '耶～任务完成！', 'char_201_moeshd/CN_030');
INSERT INTO `t_operator_voice` VALUES (2085, 62, '非3星结束行动', '欸，敌人逃跑了，我的业务信誉又要下跌啦......', 'char_201_moeshd/CN_031');
INSERT INTO `t_operator_voice` VALUES (2086, 62, '行动失败', '太糟了，不光输了战斗，还弄丢了钱包......', 'char_201_moeshd/CN_032');
INSERT INTO `t_operator_voice` VALUES (2087, 62, '进驻设施', '这里有没有宝箱？', 'char_201_moeshd/CN_033');
INSERT INTO `t_operator_voice` VALUES (2088, 62, '戳一下', '嗯？', 'char_201_moeshd/CN_034');
INSERT INTO `t_operator_voice` VALUES (2089, 62, '信赖触摸', '老板老板，有什么好东西吗？也给我看一下！', 'char_201_moeshd/CN_036');
INSERT INTO `t_operator_voice` VALUES (2090, 62, '标题', '明日方舟。', 'char_201_moeshd/CN_037');
INSERT INTO `t_operator_voice` VALUES (2091, 62, '问候', '老板~欢迎光临！', 'char_201_moeshd/CN_042');
INSERT INTO `t_operator_voice` VALUES (2092, 63, '任命助理', '嗯？想看我铸造武器吗？可以。小心，别被火星烫到。', 'char_163_hpsts/CN_001');
INSERT INTO `t_operator_voice` VALUES (2093, 63, '交谈1', '只有不被使用的武器才不会受损。因此，没有划痕反而是对武器最大的亵渎。', 'char_163_hpsts/CN_002');
INSERT INTO `t_operator_voice` VALUES (2094, 63, '交谈2', '现代化的武器装备生产方式让许多传统工匠都失业了，不过跟不上时代的人理应被淘汰，这是规律。', 'char_163_hpsts/CN_003');
INSERT INTO `t_operator_voice` VALUES (2095, 63, '交谈3', '枪械......这种武器会给这个世界带来翻天覆地的变化。', 'char_163_hpsts/CN_004');
INSERT INTO `t_operator_voice` VALUES (2096, 63, '晋升后交谈1', '我的右腿？没什么大不了的，你知道，每个工匠多少都失误过。', 'char_163_hpsts/CN_005');
INSERT INTO `t_operator_voice` VALUES (2097, 63, '晋升后交谈2', '感谢你为我提供了展现技艺的机会。', 'char_163_hpsts/CN_006');
INSERT INTO `t_operator_voice` VALUES (2098, 63, '信赖提升后交谈1', '一些装备上能看到与我一样的名字？不......跟我可没什么关系。', 'char_163_hpsts/CN_007');
INSERT INTO `t_operator_voice` VALUES (2099, 63, '信赖提升后交谈2', '以钢铁与黄铜为骨......', 'char_163_hpsts/CN_008');
INSERT INTO `t_operator_voice` VALUES (2100, 63, '信赖提升后交谈3', '如果这锻锤还能为您尽上几分绵薄之力，我就已经心满意足了。', 'char_163_hpsts/CN_009');
INSERT INTO `t_operator_voice` VALUES (2101, 63, '闲置', '吧台又开始推新饮品了，今天喝哪款好呢。', 'char_163_hpsts/CN_010');
INSERT INTO `t_operator_voice` VALUES (2102, 63, '干员报到', '他们都叫我火神。如果需要的话，我可以给你们损毁的武器做维护，另外......我需要的锻冶设备就在这张图纸里。', 'char_163_hpsts/CN_011');
INSERT INTO `t_operator_voice` VALUES (2103, 63, '观看作战记录', '唔，有新的灵感。准备好鼓风机，该干活了。', 'char_163_hpsts/CN_012');
INSERT INTO `t_operator_voice` VALUES (2104, 63, '精英化晋升1', '看来你也能明白，一名优秀工匠对于军事组织的重要性。', 'char_163_hpsts/CN_013');
INSERT INTO `t_operator_voice` VALUES (2105, 63, '精英化晋升2', '维护武器，就是保护生命。你也认同这个观念？看来，我终于找到归宿了。', 'char_163_hpsts/CN_014');
INSERT INTO `t_operator_voice` VALUES (2106, 63, '编入队伍', '我希望能......专心工作。', 'char_163_hpsts/CN_017');
INSERT INTO `t_operator_voice` VALUES (2107, 63, '任命队长', '队长吗......如果需要的话......不过比起指挥人，我还是更擅长设计和制造武器。', 'char_163_hpsts/CN_018');
INSERT INTO `t_operator_voice` VALUES (2108, 63, '行动出发', '就不能让我留下好好工作吗？', 'char_163_hpsts/CN_019');
INSERT INTO `t_operator_voice` VALUES (2109, 63, '行动开始', '一群无可救药的家伙......', 'char_163_hpsts/CN_020');
INSERT INTO `t_operator_voice` VALUES (2110, 63, '选中干员1', '哦。', 'char_163_hpsts/CN_021');
INSERT INTO `t_operator_voice` VALUES (2111, 63, '选中干员2', '嗯？', 'char_163_hpsts/CN_022');
INSERT INTO `t_operator_voice` VALUES (2112, 63, '部署1', '好的......', 'char_163_hpsts/CN_023');
INSERT INTO `t_operator_voice` VALUES (2113, 63, '部署2', '一切正常。', 'char_163_hpsts/CN_024');
INSERT INTO `t_operator_voice` VALUES (2114, 63, '作战中1', '武装起来。', 'char_163_hpsts/CN_025');
INSERT INTO `t_operator_voice` VALUES (2115, 63, '作战中2', '暴怒的烈焰啊！', 'char_163_hpsts/CN_026');
INSERT INTO `t_operator_voice` VALUES (2116, 63, '作战中3', '我不需要治疗。', 'char_163_hpsts/CN_027');
INSERT INTO `t_operator_voice` VALUES (2117, 63, '作战中4', '下一击，就破坏掉你的武器！', 'char_163_hpsts/CN_028');
INSERT INTO `t_operator_voice` VALUES (2118, 63, '4星结束行动', '捡到敌人的装备就让我看看，我想知道现在他们都在做些什么。', 'char_163_hpsts/CN_029');
INSERT INTO `t_operator_voice` VALUES (2119, 63, '3星结束行动', '意料之中的胜利！这就是为什么人需要依靠好的装备。', 'char_163_hpsts/CN_030');
INSERT INTO `t_operator_voice` VALUES (2120, 63, '非3星结束行动', '把各自装备的破损情况提交给我，回去之后我会统一检修。', 'char_163_hpsts/CN_031');
INSERT INTO `t_operator_voice` VALUES (2121, 63, '行动失败', '是我技艺不精......', 'char_163_hpsts/CN_032');
INSERT INTO `t_operator_voice` VALUES (2122, 63, '进驻设施', '这里有熔炉吗？', 'char_163_hpsts/CN_033');
INSERT INTO `t_operator_voice` VALUES (2123, 63, '戳一下', '啊啊......', 'char_163_hpsts/CN_034');
INSERT INTO `t_operator_voice` VALUES (2124, 63, '信赖触摸', '想要什么好装备了吗？我可以试着帮你做出来。', 'char_163_hpsts/CN_036');
INSERT INTO `t_operator_voice` VALUES (2125, 63, '标题', '明日方舟。', 'char_163_hpsts/CN_037');
INSERT INTO `t_operator_voice` VALUES (2126, 63, '问候', '早安，博士。', 'char_163_hpsts/CN_042');
INSERT INTO `t_operator_voice` VALUES (2127, 64, '任命助理', '博士，如果房间卫生需要打扫的话，记得叫我~', 'char_145_prove/CN_001');
INSERT INTO `t_operator_voice` VALUES (2128, 64, '交谈1', '我的尾巴您很在意吗？毛茸茸的，很可爱？那——要摸摸看吗？其他人身上，可没有这样的尾巴哦~', 'char_145_prove/CN_002');
INSERT INTO `t_operator_voice` VALUES (2129, 64, '交谈2', '荒野里水是珍贵资源呢，虽然罗德岛并不那么缺水，但还是希望博士你要节约使用哦。', 'char_145_prove/CN_003');
INSERT INTO `t_operator_voice` VALUES (2130, 64, '交谈3', '搜救遭遇天灾的人也是我的职责，虽然大多数情况下都会空手而归......', 'char_145_prove/CN_004');
INSERT INTO `t_operator_voice` VALUES (2131, 64, '晋升后交谈1', '信标回收、矿床勘探、然后是可利用资源回收，这样任务就完成咯~', 'char_145_prove/CN_005');
INSERT INTO `t_operator_voice` VALUES (2132, 64, '晋升后交谈2', '葡萄先生是我的搭档。它什么都能做，非常可靠哦。它的来历？唔......对哦，我是怎么和它认识的呢......？', 'char_145_prove/CN_006');
INSERT INTO `t_operator_voice` VALUES (2133, 64, '信赖提升后交谈1', '那、那个红色的孩子是怎么回事......我......颤抖，止不住呢......那个，博士的手臂能稍稍借下吗......？这样的话，我会安心很多的......', 'char_145_prove/CN_007');
INSERT INTO `t_operator_voice` VALUES (2134, 64, '信赖提升后交谈2', '一般来说，野外的天灾工作者都会产生这样各种各样的异变。我有什么变化？知道得越多，就离危险越近哦，博士。', 'char_145_prove/CN_008');
INSERT INTO `t_operator_voice` VALUES (2135, 64, '信赖提升后交谈3', '荒野中的天灾工作者......一般都无法善终。不用担心我哦，能在罗德岛稍事休息，我已经感到......很幸福了。', 'char_145_prove/CN_009');
INSERT INTO `t_operator_voice` VALUES (2136, 64, '闲置', '怎么又睡在这种地方，真是的......总是一副毫无防备的样子。', 'char_145_prove/CN_010');
INSERT INTO `t_operator_voice` VALUES (2137, 64, '干员报到', '我是普罗旺斯，是个普通的天灾工作者~您这样盯着我看，是在意我的尾巴吗~？', 'char_145_prove/CN_011');
INSERT INTO `t_operator_voice` VALUES (2138, 64, '观看作战记录', '这样的话，就更接近您心目中我应有的样子了吗？', 'char_145_prove/CN_012');
INSERT INTO `t_operator_voice` VALUES (2139, 64, '精英化晋升1', '和博士一起工作，很愉快啊~', 'char_145_prove/CN_013');
INSERT INTO `t_operator_voice` VALUES (2140, 64, '精英化晋升2', '罗德岛附近出现了风暴的征兆——不用担心，组织避难就交给我吧！我可是这方面的专家！', 'char_145_prove/CN_014');
INSERT INTO `t_operator_voice` VALUES (2141, 64, '编入队伍', '这次和谁组队呢？', 'char_145_prove/CN_017');
INSERT INTO `t_operator_voice` VALUES (2142, 64, '任命队长', '不会有任何问题的。', 'char_145_prove/CN_018');
INSERT INTO `t_operator_voice` VALUES (2143, 64, '行动出发', '我的工作是把大家平安带回来。', 'char_145_prove/CN_019');
INSERT INTO `t_operator_voice` VALUES (2144, 64, '行动开始', '无论天灾还是敌人，都是我痛恨的对手！', 'char_145_prove/CN_020');
INSERT INTO `t_operator_voice` VALUES (2145, 64, '选中干员1', '该我出场了吗？', 'char_145_prove/CN_021');
INSERT INTO `t_operator_voice` VALUES (2146, 64, '选中干员2', '状态绝佳！', 'char_145_prove/CN_022');
INSERT INTO `t_operator_voice` VALUES (2147, 64, '部署1', '先从警告射击开始~', 'char_145_prove/CN_023');
INSERT INTO `t_operator_voice` VALUES (2148, 64, '部署2', '弩箭装填完毕！随时都可以！', 'char_145_prove/CN_024');
INSERT INTO `t_operator_voice` VALUES (2149, 64, '作战中1', '已经警告过了哦！灾害处理，现在开始！', 'char_145_prove/CN_025');
INSERT INTO `t_operator_voice` VALUES (2150, 64, '作战中2', '弱点洞察！', 'char_145_prove/CN_026');
INSERT INTO `t_operator_voice` VALUES (2151, 64, '作战中3', '我来分散敌人的注意力。', 'char_145_prove/CN_027');
INSERT INTO `t_operator_voice` VALUES (2152, 64, '作战中4', '荒野有它自己的准则！', 'char_145_prove/CN_028');
INSERT INTO `t_operator_voice` VALUES (2153, 64, '4星结束行动', '与天灾的破坏相比，敌人还真是脆弱呢。', 'char_145_prove/CN_029');
INSERT INTO `t_operator_voice` VALUES (2154, 64, '3星结束行动', '灾害评估报告，损伤是零~', 'char_145_prove/CN_030');
INSERT INTO `t_operator_voice` VALUES (2155, 64, '非3星结束行动', '大家都辛苦了，即便稍有损害也不能气馁哦。', 'char_145_prove/CN_031');
INSERT INTO `t_operator_voice` VALUES (2156, 64, '行动失败', '呃......评估失误了，真是我职业生涯的污点......！', 'char_145_prove/CN_032');
INSERT INTO `t_operator_voice` VALUES (2157, 64, '进驻设施', '如果是能好好休息的房间就好了~', 'char_145_prove/CN_033');
INSERT INTO `t_operator_voice` VALUES (2158, 64, '戳一下', '啊啊，博士？', 'char_145_prove/CN_034');
INSERT INTO `t_operator_voice` VALUES (2159, 64, '信赖触摸', '我的大尾巴毛绒绒~毛绒绒~♪', 'char_145_prove/CN_036');
INSERT INTO `t_operator_voice` VALUES (2160, 64, '标题', '明日方舟。', 'char_145_prove/CN_037');
INSERT INTO `t_operator_voice` VALUES (2161, 64, '问候', '啊，博士早上好~', 'char_145_prove/CN_042');
INSERT INTO `t_operator_voice` VALUES (2162, 65, '任命助理', '我正在警戒四周，没有问题。', 'char_158_milu/CN_001');
INSERT INTO `t_operator_voice` VALUES (2163, 65, '交谈1', '一些注视着你的视线是饱含恶意的。即使在罗德岛，您也并不安全。', 'char_158_milu/CN_002');
INSERT INTO `t_operator_voice` VALUES (2164, 65, '交谈2', '没必要害怕那些阴影中的敌人，这些罪人无论用什么方法躲藏，都欺骗不了我的眼睛。', 'char_158_milu/CN_003');
INSERT INTO `t_operator_voice` VALUES (2165, 65, '交谈3', '即使牺牲我的生命，也一定要完成这次复仇。', 'char_158_milu/CN_004');
INSERT INTO `t_operator_voice` VALUES (2166, 65, '晋升后交谈1', '我的族人和朋友，都葬身在故乡的森林里。只有落叶能掩埋他们。', 'char_158_milu/CN_005');
INSERT INTO `t_operator_voice` VALUES (2167, 65, '晋升后交谈2', '这把弩是为了审判罪人制造的。终有一天，我要用它降下最后的裁决。', 'char_158_milu/CN_006');
INSERT INTO `t_operator_voice` VALUES (2168, 65, '信赖提升后交谈1', '陨星。她曾经是我的朋友。我的故乡被摧毁的那天，只有我们几个人设法逃了出来。', 'char_158_milu/CN_007');
INSERT INTO `t_operator_voice` VALUES (2169, 65, '信赖提升后交谈2', '我一直在找那个告密者，把叛军引向故乡的罪人......会是陨星吗？不，我、我不认为是她——', 'char_158_milu/CN_008');
INSERT INTO `t_operator_voice` VALUES (2170, 65, '信赖提升后交谈3', '——我睡着了？抱、抱歉，最近巡逻的时间增加了......不，即使您说没关系也......谢谢您的关心。', 'char_158_milu/CN_009');
INSERT INTO `t_operator_voice` VALUES (2171, 65, '闲置', '我想稍微睡一下......', 'char_158_milu/CN_010');
INSERT INTO `t_operator_voice` VALUES (2172, 65, '干员报到', '狙击手守林人。为了复仇，我需要强大力量的帮助。', 'char_158_milu/CN_011');
INSERT INTO `t_operator_voice` VALUES (2173, 65, '观看作战记录', '我需要更多的力量。', 'char_158_milu/CN_012');
INSERT INTO `t_operator_voice` VALUES (2174, 65, '精英化晋升1', '感谢您的任命。', 'char_158_milu/CN_013');
INSERT INTO `t_operator_voice` VALUES (2175, 65, '精英化晋升2', '任命，感谢。从今以后，也请将我的力量用于正途，否则......', 'char_158_milu/CN_014');
INSERT INTO `t_operator_voice` VALUES (2176, 65, '编入队伍', '组队完成。', 'char_158_milu/CN_017');
INSERT INTO `t_operator_voice` VALUES (2177, 65, '任命队长', '了解。', 'char_158_milu/CN_018');
INSERT INTO `t_operator_voice` VALUES (2178, 65, '行动出发', '全体干员出击。', 'char_158_milu/CN_019');
INSERT INTO `t_operator_voice` VALUES (2179, 65, '行动开始', '发现敌情，立即采取行动。', 'char_158_milu/CN_020');
INSERT INTO `t_operator_voice` VALUES (2180, 65, '选中干员1', '了解。', 'char_158_milu/CN_021');
INSERT INTO `t_operator_voice` VALUES (2181, 65, '选中干员2', '请下达指令。', 'char_158_milu/CN_022');
INSERT INTO `t_operator_voice` VALUES (2182, 65, '部署1', '现在进行索敌。', 'char_158_milu/CN_023');
INSERT INTO `t_operator_voice` VALUES (2183, 65, '部署2', '到达目标地点。', 'char_158_milu/CN_024');
INSERT INTO `t_operator_voice` VALUES (2184, 65, '作战中1', '援护。', 'char_158_milu/CN_025');
INSERT INTO `t_operator_voice` VALUES (2185, 65, '作战中2', '瞄准。', 'char_158_milu/CN_026');
INSERT INTO `t_operator_voice` VALUES (2186, 65, '作战中3', '发射。', 'char_158_milu/CN_027');
INSERT INTO `t_operator_voice` VALUES (2187, 65, '作战中4', '开火！', 'char_158_milu/CN_028');
INSERT INTO `t_operator_voice` VALUES (2188, 65, '4星结束行动', '我会一直走下去，直到彻底实现复仇那天。', 'char_158_milu/CN_029');
INSERT INTO `t_operator_voice` VALUES (2189, 65, '3星结束行动', '作战结束。歼灭了所有目标。', 'char_158_milu/CN_030');
INSERT INTO `t_operator_voice` VALUES (2190, 65, '非3星结束行动', '战斗结束。有必要确认残余敌人的情况。', 'char_158_milu/CN_031');
INSERT INTO `t_operator_voice` VALUES (2191, 65, '行动失败', '马上撤退......', 'char_158_milu/CN_032');
INSERT INTO `t_operator_voice` VALUES (2192, 65, '进驻设施', '不习惯热闹的房间......', 'char_158_milu/CN_033');
INSERT INTO `t_operator_voice` VALUES (2193, 65, '戳一下', '啊......', 'char_158_milu/CN_034');
INSERT INTO `t_operator_voice` VALUES (2194, 65, '信赖触摸', '那个，摸我的角的话，有点为难......很痒......', 'char_158_milu/CN_036');
INSERT INTO `t_operator_voice` VALUES (2195, 65, '标题', '明日方舟。', 'char_158_milu/CN_037');
INSERT INTO `t_operator_voice` VALUES (2196, 65, '问候', '嗯？博士啊。', 'char_158_milu/CN_042');
INSERT INTO `t_operator_voice` VALUES (2197, 66, '任命助理', '唔......博士，你是不是有点缺乏运动啊。', 'char_173_slchan/CN_001');
INSERT INTO `t_operator_voice` VALUES (2198, 66, '交谈1', '罗德岛的大家都很和善，即使我想做恶作剧都有点不忍心下手呢......啊，吃饭的时间到了，我去帮忙！', 'char_173_slchan/CN_002');
INSERT INTO `t_operator_voice` VALUES (2199, 66, '交谈2', '登山可不只是我的兴趣而已，而是事业哦！事！业！', 'char_173_slchan/CN_003');
INSERT INTO `t_operator_voice` VALUES (2200, 66, '交谈3', '腿上的环？啊，你是问这个环是怎么回事吗？这只是个装饰品啦~因为这里有块结晶嘛。想摸摸看吗~？', 'char_173_slchan/CN_004');
INSERT INTO `t_operator_voice` VALUES (2201, 66, '晋升后交谈1', '角峰叔其实是老哥派来保护我的吧......这个老哥真是不坦率，啊啊，想想就来气！', 'char_173_slchan/CN_005');
INSERT INTO `t_operator_voice` VALUES (2202, 66, '晋升后交谈2', '我从小就想爬上喀兰圣山看看——自从姐姐成为圣女离我们而去之后，这想法就越来越强烈......博士，多多的锻炼我吧，总有天我要爬上它的顶峰，把姐姐接回家。', 'char_173_slchan/CN_006');
INSERT INTO `t_operator_voice` VALUES (2203, 66, '信赖提升后交谈1', '在登山领域，我可是不会输给任何人的！毕竟没有人比我更熟悉手上的工具了，就连作为武器，都是它们更顺手呢。', 'char_173_slchan/CN_007');
INSERT INTO `t_operator_voice` VALUES (2204, 66, '信赖提升后交谈2', '是啊，矿石病彻底改变了我。但就此消沉下去的话，我才真的不是我了吧？无论未来怎么样，自己决定的事情，就该自己去努力完成嘛。', 'char_173_slchan/CN_008');
INSERT INTO `t_operator_voice` VALUES (2205, 66, '信赖提升后交谈3', '下雪了呢。......希瓦艾什一家永远回不到过去，我明白的。一切都和过去不一样了。除了雪......和故乡一样的，一片片的雪。', 'char_173_slchan/CN_009');
INSERT INTO `t_operator_voice` VALUES (2206, 66, '闲置', '博——士！博——士啊啊！真是的，既然睡着了就没办法了！Bye-bye！', 'char_173_slchan/CN_010');
INSERT INTO `t_operator_voice` VALUES (2207, 66, '干员报到', '我就是将要触摸天际的崖心，请多关照！', 'char_173_slchan/CN_011');
INSERT INTO `t_operator_voice` VALUES (2208, 66, '观看作战记录', '嗯——很新奇的体验呢。', 'char_173_slchan/CN_012');
INSERT INTO `t_operator_voice` VALUES (2209, 66, '精英化晋升1', '晋升？好哇！我们吃个蛋糕庆祝下吧！好吧？看，从后勤组那儿顺手拿来的，不要紧的！', 'char_173_slchan/CN_013');
INSERT INTO `t_operator_voice` VALUES (2210, 66, '精英化晋升2', '嘿，博士挺有眼光的嘛。确实，我可是超强的，你要好好运用我的力量哦！', 'char_173_slchan/CN_014');
INSERT INTO `t_operator_voice` VALUES (2211, 66, '编入队伍', '好，干劲涌上来了！一口气登上最高峰吧！', 'char_173_slchan/CN_017');
INSERT INTO `t_operator_voice` VALUES (2212, 66, '任命队长', '唉？？突然就让我领导大家什么的，这......', 'char_173_slchan/CN_018');
INSERT INTO `t_operator_voice` VALUES (2213, 66, '行动出发', '接下来可不能粗心大意了，要认真对待。', 'char_173_slchan/CN_019');
INSERT INTO `t_operator_voice` VALUES (2214, 66, '行动开始', '我可不会手下留情！', 'char_173_slchan/CN_020');
INSERT INTO `t_operator_voice` VALUES (2215, 66, '选中干员1', '在呢！', 'char_173_slchan/CN_021');
INSERT INTO `t_operator_voice` VALUES (2216, 66, '选中干员2', '这里吗？', 'char_173_slchan/CN_022');
INSERT INTO `t_operator_voice` VALUES (2217, 66, '部署1', '交给我吧！', 'char_173_slchan/CN_023');
INSERT INTO `t_operator_voice` VALUES (2218, 66, '部署2', '完全没问题！', 'char_173_slchan/CN_024');
INSERT INTO `t_operator_voice` VALUES (2219, 66, '作战中1', '逮·住·你·了！', 'char_173_slchan/CN_025');
INSERT INTO `t_operator_voice` VALUES (2220, 66, '作战中2', '一钩一个，轻轻松松~', 'char_173_slchan/CN_026');
INSERT INTO `t_operator_voice` VALUES (2221, 66, '作战中3', '门外汉只会摔下万丈深渊！', 'char_173_slchan/CN_027');
INSERT INTO `t_operator_voice` VALUES (2222, 66, '作战中4', '以希瓦艾什之名起誓——！', 'char_173_slchan/CN_028');
INSERT INTO `t_operator_voice` VALUES (2223, 66, '4星结束行动', '世上没有无法攀登的山峰，也没有无法跨越的绝境！', 'char_173_slchan/CN_029');
INSERT INTO `t_operator_voice` VALUES (2224, 66, '3星结束行动', '有我在，这不是理所当然的嘛！', 'char_173_slchan/CN_030');
INSERT INTO `t_operator_voice` VALUES (2225, 66, '非3星结束行动', '啊，逃的可真快！', 'char_173_slchan/CN_031');
INSERT INTO `t_operator_voice` VALUES (2226, 66, '行动失败', '唔......我是不是出丑了......', 'char_173_slchan/CN_032');
INSERT INTO `t_operator_voice` VALUES (2227, 66, '进驻设施', '真暖和呢，我就在这窝会儿好了~', 'char_173_slchan/CN_033');
INSERT INTO `t_operator_voice` VALUES (2228, 66, '戳一下', '等等！别......', 'char_173_slchan/CN_034');
INSERT INTO `t_operator_voice` VALUES (2229, 66, '信赖触摸', '博士，下次让我教你登山吧，你一定会喜欢的~', 'char_173_slchan/CN_036');
INSERT INTO `t_operator_voice` VALUES (2230, 66, '标题', '明日方舟。', 'char_173_slchan/CN_037');
INSERT INTO `t_operator_voice` VALUES (2231, 66, '问候', '早安哦，博士~', 'char_173_slchan/CN_042');
INSERT INTO `t_operator_voice` VALUES (2232, 67, '任命助理', '呣？想要祈祷吗？抱歉，暂时没空哦。', 'char_174_slbell/CN_001');
INSERT INTO `t_operator_voice` VALUES (2233, 67, '交谈1', '呣？我是现任圣女没错哦。但无论是谁——都有被繁重事务压得喘不过气，想要轻松一下的时候吧。', 'char_174_slbell/CN_002');
INSERT INTO `t_operator_voice` VALUES (2234, 67, '交谈2', '在闲杂事务上浪费时间才叫懈怠，我只是......比较擅长寻找机会养精蓄锐而已。', 'char_174_slbell/CN_003');
INSERT INTO `t_operator_voice` VALUES (2235, 67, '交谈3', '出来玩耍就要尽兴，别把那些条条框框套在我身上呀。', 'char_174_slbell/CN_004');
INSERT INTO `t_operator_voice` VALUES (2236, 67, '晋升后交谈1', '喀兰的神祇吗？因为圣女职务的关系，我和她关系不浅呢。', 'char_174_slbell/CN_005');
INSERT INTO `t_operator_voice` VALUES (2237, 67, '晋升后交谈2', '我也很愿意帮助你，但圣女的工作实在是太繁忙了。这次就破例一回，可不要占用我太多时间哦。', 'char_174_slbell/CN_006');
INSERT INTO `t_operator_voice` VALUES (2238, 67, '信赖提升后交谈1', '妹——恩希亚那孩子，罗德岛的各位也都挺喜欢她的，是吧？所以，并不是那个人特殊对待她。现在的话，我也算稍微明白了些。嗯，这样就好了，我只要像现在这样看着她就好了。', 'char_174_slbell/CN_007');
INSERT INTO `t_operator_voice` VALUES (2239, 67, '信赖提升后交谈2', '小时候，我在外玩耍扭伤了脚，是哥哥背了我一晚上才找到了住家。那时我想着，一定要成为哥哥那样的人。但是现在......呵，没什么。', 'char_174_slbell/CN_008');
INSERT INTO `t_operator_voice` VALUES (2240, 67, '信赖提升后交谈3', '咦，你怎么找到我的？啧，我要把你的脚趾冻上......唔？咖啡？给我的？谢谢。博士要不要也在这偷会儿懒？这里的风景很好。把这里当作我们两个人的秘密吧。唔，好苦。', 'char_174_slbell/CN_009');
INSERT INTO `t_operator_voice` VALUES (2241, 67, '闲置', '难得的小憩呢。', 'char_174_slbell/CN_010');
INSERT INTO `t_operator_voice` VALUES (2242, 67, '干员报到', '嘘，我是偷偷跑出来——啊，嗯，人事主管你也在啊，是的，我是来协助罗德岛的。喀兰圣女，初雪，向你献上至洁的祝福。', 'char_174_slbell/CN_011');
INSERT INTO `t_operator_voice` VALUES (2243, 67, '观看作战记录', '在脑海中......有什么声音......在回响着......', 'char_174_slbell/CN_012');
INSERT INTO `t_operator_voice` VALUES (2244, 67, '精英化晋升1', '“汝等终将胜利”——此乃喀兰之允诺，亦是我所祈求。', 'char_174_slbell/CN_013');
INSERT INTO `t_operator_voice` VALUES (2245, 67, '精英化晋升2', '什么时候你也需要祝福啦......能稍等一会吗，我准备一下。', 'char_174_slbell/CN_014');
INSERT INTO `t_operator_voice` VALUES (2246, 67, '编入队伍', '愿喀兰圣山与我们同在。', 'char_174_slbell/CN_017');
INSERT INTO `t_operator_voice` VALUES (2247, 67, '任命队长', '循着铃音，你定能找到正确的方向。', 'char_174_slbell/CN_018');
INSERT INTO `t_operator_voice` VALUES (2248, 67, '行动出发', '让我为你向群山献上祈祷吧。', 'char_174_slbell/CN_019');
INSERT INTO `t_operator_voice` VALUES (2249, 67, '行动开始', '不洁之物理应被净化。', 'char_174_slbell/CN_020');
INSERT INTO `t_operator_voice` VALUES (2250, 67, '选中干员1', '小心一点。', 'char_174_slbell/CN_021');
INSERT INTO `t_operator_voice` VALUES (2251, 67, '选中干员2', '整理好了。', 'char_174_slbell/CN_022');
INSERT INTO `t_operator_voice` VALUES (2252, 67, '部署1', '战斗吧。', 'char_174_slbell/CN_023');
INSERT INTO `t_operator_voice` VALUES (2253, 67, '部署2', '祝福你。', 'char_174_slbell/CN_024');
INSERT INTO `t_operator_voice` VALUES (2254, 67, '作战中1', '神灵啊，愿您倾听我的声音。', 'char_174_slbell/CN_025');
INSERT INTO `t_operator_voice` VALUES (2255, 67, '作战中2', '铃声已至，风雪缠足。', 'char_174_slbell/CN_026');
INSERT INTO `t_operator_voice` VALUES (2256, 67, '作战中3', '这力量乃是圣山的馈赠。', 'char_174_slbell/CN_027');
INSERT INTO `t_operator_voice` VALUES (2257, 67, '作战中4', '圣铃既响，坚甲不再。', 'char_174_slbell/CN_028');
INSERT INTO `t_operator_voice` VALUES (2258, 67, '4星结束行动', '如有神助，正是此般。', 'char_174_slbell/CN_029');
INSERT INTO `t_operator_voice` VALUES (2259, 67, '3星结束行动', '喀兰的光芒照耀在我们身上。', 'char_174_slbell/CN_030');
INSERT INTO `t_operator_voice` VALUES (2260, 67, '非3星结束行动', '若是不流血就能取得胜利......不，忘了我说的吧。', 'char_174_slbell/CN_031');
INSERT INTO `t_operator_voice` VALUES (2261, 67, '行动失败', '回去吧......现在的局势对我方不利。', 'char_174_slbell/CN_032');
INSERT INTO `t_operator_voice` VALUES (2262, 67, '进驻设施', '是一个让人心情平静的地方呢。', 'char_174_slbell/CN_033');
INSERT INTO `t_operator_voice` VALUES (2263, 67, '戳一下', '呣？', 'char_174_slbell/CN_034');
INSERT INTO `t_operator_voice` VALUES (2264, 67, '信赖触摸', '闭上眼，让我带你去个新的地方......一定要闭眼哦。', 'char_174_slbell/CN_036');
INSERT INTO `t_operator_voice` VALUES (2265, 67, '标题', '明日方舟。', 'char_174_slbell/CN_037');
INSERT INTO `t_operator_voice` VALUES (2266, 67, '问候', '祝福你，博士。', 'char_174_slbell/CN_042');
INSERT INTO `t_operator_voice` VALUES (2267, 68, '任命助理', '您好，接下来负责值班的人是我......您的表情很复杂呢，我看上去有那么不可靠吗？', 'char_195_glassb/CN_001');
INSERT INTO `t_operator_voice` VALUES (2268, 68, '交谈1', '我们也不愿意被卷入战场，但其他人活下去的决心也许并没有我们强。要是您能理解这点，就已经足够了。', 'char_195_glassb/CN_002');
INSERT INTO `t_operator_voice` VALUES (2269, 68, '交谈2', '凛冬、我，还有其他人，其实都不是同一所学校的学生。不过，每座学校里都流传着凛冬的传说，以及“冬将军”这个称号。您难道不觉得，这个称号很酷吗？冬将军，嗯嗯......', 'char_195_glassb/CN_003');
INSERT INTO `t_operator_voice` VALUES (2270, 68, '交谈3', '我们是怎么从切尔诺伯格逃出来的？唔嗯......请别问了，有些答案，您不会喜欢的。', 'char_195_glassb/CN_004');
INSERT INTO `t_operator_voice` VALUES (2271, 68, '晋升后交谈1', '哦，我正在读神秘小说......能从那次事件中存活下来，是因为这本书上的东西帮了我很大的忙。', 'char_195_glassb/CN_005');
INSERT INTO `t_operator_voice` VALUES (2272, 68, '晋升后交谈2', '我最喜欢什么书？政治学、经济学、唯物辩证法......另外，烘焙杂志之类的也不错。', 'char_195_glassb/CN_006');
INSERT INTO `t_operator_voice` VALUES (2273, 68, '信赖提升后交谈1', '家？你是说，我们的故乡吗......已经，回不去了吧？您很清楚，那里已经什么都没有了。就算切尔诺伯格能够重建，我也......', 'char_195_glassb/CN_007');
INSERT INTO `t_operator_voice` VALUES (2274, 68, '信赖提升后交谈2', '罗德岛很安全，博士的办公室里又有很多很多书......如果可以的话，我不想离开这里......', 'char_195_glassb/CN_008');
INSERT INTO `t_operator_voice` VALUES (2275, 68, '信赖提升后交谈3', '将来该怎么办？嗯......不知道，因为没有哪本书会写这种事啊。希望未来能有个人告诉我吧......', 'char_195_glassb/CN_009');
INSERT INTO `t_operator_voice` VALUES (2276, 68, '闲置', '原来如此，我都没想到呢！“这趟列车中所有的乘客，都是凶手！”', 'char_195_glassb/CN_010');
INSERT INTO `t_operator_voice` VALUES (2277, 68, '干员报到', '我是真理，虽然还是一名学生，却因为会使用法术，所以就被招募了......是啊，身为学生却站在这种地方，真是奇怪，不是吗？', 'char_195_glassb/CN_011');
INSERT INTO `t_operator_voice` VALUES (2278, 68, '观看作战记录', '您希望我变强吗？', 'char_195_glassb/CN_012');
INSERT INTO `t_operator_voice` VALUES (2279, 68, '精英化晋升1', '您是不是觉得，您更了解我了？遗憾，还差很远呢。', 'char_195_glassb/CN_013');
INSERT INTO `t_operator_voice` VALUES (2280, 68, '精英化晋升2', '没办法了，就让我展示一下把整个办公室粉碎成沙子的能力吧......开玩笑的，这里还有很多珍贵的书本呢，不能做那样的事。', 'char_195_glassb/CN_014');
INSERT INTO `t_operator_voice` VALUES (2281, 68, '编入队伍', '我知道了。', 'char_195_glassb/CN_017');
INSERT INTO `t_operator_voice` VALUES (2282, 68, '任命队长', '各位......嗯，和我想象中的不太一样呢。', 'char_195_glassb/CN_018');
INSERT INTO `t_operator_voice` VALUES (2283, 68, '行动出发', '让我们拿出决心、热忱、以及十二万分的热情吧。', 'char_195_glassb/CN_019');
INSERT INTO `t_operator_voice` VALUES (2284, 68, '行动开始', '胜利的星，一定会照耀着我们。', 'char_195_glassb/CN_020');
INSERT INTO `t_operator_voice` VALUES (2285, 68, '选中干员1', '博士，什么事？', 'char_195_glassb/CN_021');
INSERT INTO `t_operator_voice` VALUES (2286, 68, '选中干员2', '请说吧。', 'char_195_glassb/CN_022');
INSERT INTO `t_operator_voice` VALUES (2287, 68, '部署1', '真讨厌啊，战场。', 'char_195_glassb/CN_023');
INSERT INTO `t_operator_voice` VALUES (2288, 68, '部署2', '只有战斗了。', 'char_195_glassb/CN_024');
INSERT INTO `t_operator_voice` VALUES (2289, 68, '作战中1', '来吧。', 'char_195_glassb/CN_025');
INSERT INTO `t_operator_voice` VALUES (2290, 68, '作战中2', '请别那么急匆匆的。', 'char_195_glassb/CN_026');
INSERT INTO `t_operator_voice` VALUES (2291, 68, '作战中3', '稍微慢一点。', 'char_195_glassb/CN_027');
INSERT INTO `t_operator_voice` VALUES (2292, 68, '作战中4', '见识一下吧。', 'char_195_glassb/CN_028');
INSERT INTO `t_operator_voice` VALUES (2293, 68, '4星结束行动', '为什么，他们哪怕舍弃生命也要战斗......这究竟能换来什么？', 'char_195_glassb/CN_029');
INSERT INTO `t_operator_voice` VALUES (2294, 68, '3星结束行动', '那个，要夸奖我们，也只能趁现在哦......', 'char_195_glassb/CN_030');
INSERT INTO `t_operator_voice` VALUES (2295, 68, '非3星结束行动', '那些逃走的敌人，应该不会再回来了吧......', 'char_195_glassb/CN_031');
INSERT INTO `t_operator_voice` VALUES (2296, 68, '行动失败', '果然，只凭我们是做不到的吧......', 'char_195_glassb/CN_032');
INSERT INTO `t_operator_voice` VALUES (2297, 68, '进驻设施', '是一间会发生有趣事件的密室呢。', 'char_195_glassb/CN_033');
INSERT INTO `t_operator_voice` VALUES (2298, 68, '戳一下', '呃......', 'char_195_glassb/CN_034');
INSERT INTO `t_operator_voice` VALUES (2299, 68, '信赖触摸', '这里好吵啊，我想去博士的办公室看书。', 'char_195_glassb/CN_036');
INSERT INTO `t_operator_voice` VALUES (2300, 68, '标题', '明日方舟。', 'char_195_glassb/CN_037');
INSERT INTO `t_operator_voice` VALUES (2301, 68, '问候', '你好，博士。', 'char_195_glassb/CN_042');
INSERT INTO `t_operator_voice` VALUES (2302, 69, '任命助理', '嗨~博士！您在做什么呢？好像很有趣的样子！', 'char_101_sora/CN_001');
INSERT INTO `t_operator_voice` VALUES (2303, 69, '交谈1', '罗德岛有没有适合唱歌的场所呢？我希望没有战斗的时候，大家能有个一起唱卡拉OK放松放松的地方！', 'char_101_sora/CN_002');
INSERT INTO `t_operator_voice` VALUES (2304, 69, '交谈2', '做偶像的时候，用歌声和表演来抓住Fans的心是我的工作，可是作为干员，我又该做些什么呢......？', 'char_101_sora/CN_003');
INSERT INTO `t_operator_voice` VALUES (2305, 69, '交谈3', '博士博士，有什么我能帮忙的吗？别看我这样，在企鹅物流里，我可也是会做些体力活的哦。', 'char_101_sora/CN_004');
INSERT INTO `t_operator_voice` VALUES (2306, 69, '晋升后交谈1', '果然我的专长就是唱歌！单人清唱也好，合唱领唱也行，流行歌曲偶像歌曲都没问题，甚至RAP都不在话下！不过，好像这些对干员的工作没什么帮助呢......如果能更多地帮到大家就好了。', 'char_101_sora/CN_005');
INSERT INTO `t_operator_voice` VALUES (2307, 69, '晋升后交谈2', '耳、耳朵的事？就算您这么问，我也不能......那个，就算问德克萨斯她也是不会说的！以后有机会我会向您解释......！', 'char_101_sora/CN_006');
INSERT INTO `t_operator_voice` VALUES (2308, 69, '信赖提升后交谈1', '德克萨斯是我的救命恩人，还教了我很多事情，所以只要有时间我都想待在她身边，帮她做点什么......嗯，只是想报恩而已！', 'char_101_sora/CN_007');
INSERT INTO `t_operator_voice` VALUES (2309, 69, '信赖提升后交谈2', '我看上去不太高兴？呣......因为，今天和德克萨斯一起做任务的人还是能天使对吧。哈......如果我也能像能天使那样强的话......', 'char_101_sora/CN_008');
INSERT INTO `t_operator_voice` VALUES (2310, 69, '信赖提升后交谈3', '博士！能不能教教我怎么使用武器！唱歌什么的，只能在后方支援大家，要是我能用武器战斗的话......欸欸？您也不太擅长战斗？', 'char_101_sora/CN_009');
INSERT INTO `t_operator_voice` VALUES (2311, 69, '闲置', '......德克萨斯，你在听我的歌吗？', 'char_101_sora/CN_010');
INSERT INTO `t_operator_voice` VALUES (2312, 69, '干员报到', '(｡･∀･)ﾉﾞ嗨！我是来自MSR的，大家的偶像空~！呃，抱歉抱歉~这种风格的自我介绍，好像不太适合罗德岛吧。呜......', 'char_101_sora/CN_011');
INSERT INTO `t_operator_voice` VALUES (2313, 69, '观看作战记录', '升级！', 'char_101_sora/CN_012');
INSERT INTO `t_operator_voice` VALUES (2314, 69, '精英化晋升1', '晋升？是像偶像等级一样的制度吗？', 'char_101_sora/CN_013');
INSERT INTO `t_operator_voice` VALUES (2315, 69, '精英化晋升2', '这样雀跃的心情，简直就像回到了初次站上LIVE舞台的那天......博士能这样信赖我，我真的很开心！至今为止的努力得到回报了......嗯？我的耳朵？怎、怎么了嘛......？', 'char_101_sora/CN_014');
INSERT INTO `t_operator_voice` VALUES (2316, 69, '编入队伍', '作为小队的一员，我会从基础知识开始学习的。', 'char_101_sora/CN_017');
INSERT INTO `t_operator_voice` VALUES (2317, 69, '任命队长', '队长吗？就像Center位一样吧？', 'char_101_sora/CN_018');
INSERT INTO `t_operator_voice` VALUES (2318, 69, '行动出发', '在战场上唱歌，真是新奇体验呢~', 'char_101_sora/CN_019');
INSERT INTO `t_operator_voice` VALUES (2319, 69, '行动开始', '如果我的歌声能阻止这场战争就好了。', 'char_101_sora/CN_020');
INSERT INTO `t_operator_voice` VALUES (2320, 69, '选中干员1', '该我出场了吗？', 'char_101_sora/CN_021');
INSERT INTO `t_operator_voice` VALUES (2321, 69, '选中干员2', '我的话没问题！', 'char_101_sora/CN_022');
INSERT INTO `t_operator_voice` VALUES (2322, 69, '部署1', '演唱会要开始喽！', 'char_101_sora/CN_023');
INSERT INTO `t_operator_voice` VALUES (2323, 69, '部署2', '开始了哦~！', 'char_101_sora/CN_024');
INSERT INTO `t_operator_voice` VALUES (2324, 69, '作战中1', '大家加油~！', 'char_101_sora/CN_025');
INSERT INTO `t_operator_voice` VALUES (2325, 69, '作战中2', '我会支持你！', 'char_101_sora/CN_026');
INSERT INTO `t_operator_voice` VALUES (2326, 69, '作战中3', '让形势逆转吧！', 'char_101_sora/CN_027');
INSERT INTO `t_operator_voice` VALUES (2327, 69, '作战中4', '别输了！', 'char_101_sora/CN_028');
INSERT INTO `t_operator_voice` VALUES (2328, 69, '4星结束行动', '大家的热情欢呼，今天也成功地传达给空了哟~！谢谢！', 'char_101_sora/CN_029');
INSERT INTO `t_operator_voice` VALUES (2329, 69, '3星结束行动', '成功了！大获全胜！德克萨斯，看到我活跃的表现了吗？', 'char_101_sora/CN_030');
INSERT INTO `t_operator_voice` VALUES (2330, 69, '非3星结束行动', '大家辛苦了！', 'char_101_sora/CN_031');
INSERT INTO `t_operator_voice` VALUES (2331, 69, '行动失败', '对不起......我没能成为大家的力量......', 'char_101_sora/CN_032');
INSERT INTO `t_operator_voice` VALUES (2332, 69, '进驻设施', '这里比企鹅物流的宿舍要大上好多呢！', 'char_101_sora/CN_033');
INSERT INTO `t_operator_voice` VALUES (2333, 69, '戳一下', '闪闪发光！', 'char_101_sora/CN_034');
INSERT INTO `t_operator_voice` VALUES (2334, 69, '信赖触摸', '欸嘿嘿~博士，谢谢你~！', 'char_101_sora/CN_036');
INSERT INTO `t_operator_voice` VALUES (2335, 69, '标题', '明日方舟。', 'char_101_sora/CN_037');
INSERT INTO `t_operator_voice` VALUES (2336, 69, '问候', '今天也要开开心心哦，博士。', 'char_101_sora/CN_042');
INSERT INTO `t_operator_voice` VALUES (2337, 70, '任命助理', '那个......我两个小时前，就已经......待在这里了......', 'char_215_mantic/CN_001');
INSERT INTO `t_operator_voice` VALUES (2338, 70, '交谈1', '对不起，我确实很没有存在感......但这个，并不是我能力的原因呀......', 'char_215_mantic/CN_002');
INSERT INTO `t_operator_voice` VALUES (2339, 70, '交谈2', '我的能力，实际上是......进入的特定环境后，让别人，意识不到我的存在......', 'char_215_mantic/CN_003');
INSERT INTO `t_operator_voice` VALUES (2340, 70, '交谈3', '我......一直交不到朋友。一个没法被注意到的朋友，谁会想要呢......', 'char_215_mantic/CN_004');
INSERT INTO `t_operator_voice` VALUES (2341, 70, '晋升后交谈1', '尾巴......不战斗的时候，会卷起来......所以，请别那么害怕......', 'char_215_mantic/CN_005');
INSERT INTO `t_operator_voice` VALUES (2342, 70, '晋升后交谈2', '博士，让我去做，那些危险的任务吧。毕竟，我的能力......非常合适......', 'char_215_mantic/CN_006');
INSERT INTO `t_operator_voice` VALUES (2343, 70, '信赖提升后交谈1', '放在胸前的，这些身份牌，是我捡到的。把人们存在过的痕迹，收集起来，我很喜欢......', 'char_215_mantic/CN_007');
INSERT INTO `t_operator_voice` VALUES (2344, 70, '信赖提升后交谈2', '那个叫“夜魔”的人，请别让她，靠近我......！那个，她，很危险......我的能力，对她不管用，为什么......', 'char_215_mantic/CN_008');
INSERT INTO `t_operator_voice` VALUES (2345, 70, '信赖提升后交谈3', '我......其实，想要把病治好的。但是，如果，真的治好了，是不是，会失去现在的能力......那样的我，还有价值吗......？', 'char_215_mantic/CN_009');
INSERT INTO `t_operator_voice` VALUES (2346, 70, '闲置', '......被博士丢在一边不管了......这种感觉，我，好讨厌......', 'char_215_mantic/CN_010');
INSERT INTO `t_operator_voice` VALUES (2347, 70, '干员报到', '我是狮蝎，能力是，悄无声息地夺走别人的生命......请别害怕，这次的目标，不是你......', 'char_215_mantic/CN_011');
INSERT INTO `t_operator_voice` VALUES (2348, 70, '观看作战记录', '嗯，谢谢。还有，请多关照......', 'char_215_mantic/CN_012');
INSERT INTO `t_operator_voice` VALUES (2349, 70, '精英化晋升1', '晋升，我，我终于等到了......我一直以为......是因为我的表现，不够好，所以您一直没有提拔我......', 'char_215_mantic/CN_013');
INSERT INTO `t_operator_voice` VALUES (2350, 70, '精英化晋升2', '我帮上您的忙了，对吧......？没有去处的我，这里，这个罗德岛......就是我的家。今后也请和我，一起守护这里......！', 'char_215_mantic/CN_014');
INSERT INTO `t_operator_voice` VALUES (2351, 70, '编入队伍', '我知道了。', 'char_215_mantic/CN_017');
INSERT INTO `t_operator_voice` VALUES (2352, 70, '任命队长', '我这样的队长，会被大家讨厌的......', 'char_215_mantic/CN_018');
INSERT INTO `t_operator_voice` VALUES (2353, 70, '行动出发', '别把我丢下......', 'char_215_mantic/CN_019');
INSERT INTO `t_operator_voice` VALUES (2354, 70, '行动开始', '请依据我的能力，指定战术吧......就算是比较阴暗的，也没关系......', 'char_215_mantic/CN_020');
INSERT INTO `t_operator_voice` VALUES (2355, 70, '选中干员1', '准备好了......', 'char_215_mantic/CN_021');
INSERT INTO `t_operator_voice` VALUES (2356, 70, '选中干员2', '我在这里......', 'char_215_mantic/CN_022');
INSERT INTO `t_operator_voice` VALUES (2357, 70, '部署1', '潜伏，开始......', 'char_215_mantic/CN_023');
INSERT INTO `t_operator_voice` VALUES (2358, 70, '部署2', '伏击，准备......', 'char_215_mantic/CN_024');
INSERT INTO `t_operator_voice` VALUES (2359, 70, '作战中1', '很痛苦吧......', 'char_215_mantic/CN_025');
INSERT INTO `t_operator_voice` VALUES (2360, 70, '作战中2', '别过来......', 'char_215_mantic/CN_026');
INSERT INTO `t_operator_voice` VALUES (2361, 70, '作战中3', '我也，讨厌，这样做......', 'char_215_mantic/CN_027');
INSERT INTO `t_operator_voice` VALUES (2362, 70, '作战中4', '永别了......', 'char_215_mantic/CN_028');
INSERT INTO `t_operator_voice` VALUES (2363, 70, '4星结束行动', '就算，是这样可怕的战场，您的判断力、集中力，一点都没有受到影响。博士，好帅气......', 'char_215_mantic/CN_029');
INSERT INTO `t_operator_voice` VALUES (2364, 70, '3星结束行动', '为了胜利，我们什么都得做，只是......', 'char_215_mantic/CN_030');
INSERT INTO `t_operator_voice` VALUES (2365, 70, '非3星结束行动', '要抓住，逃走的人的话，请交给我......', 'char_215_mantic/CN_031');
INSERT INTO `t_operator_voice` VALUES (2366, 70, '行动失败', '对不起......都是我没做好......', 'char_215_mantic/CN_032');
INSERT INTO `t_operator_voice` VALUES (2367, 70, '进驻设施', '我去角落里，坐着......', 'char_215_mantic/CN_033');
INSERT INTO `t_operator_voice` VALUES (2368, 70, '戳一下', '欸欸欸？？？', 'char_215_mantic/CN_034');
INSERT INTO `t_operator_voice` VALUES (2369, 70, '信赖触摸', '那个，您经常关注着我，虽然这是您的职责，我也......非常开心......', 'char_215_mantic/CN_036');
INSERT INTO `t_operator_voice` VALUES (2370, 70, '标题', '明日方舟。', 'char_215_mantic/CN_037');
INSERT INTO `t_operator_voice` VALUES (2371, 70, '问候', '博士......你好......', 'char_215_mantic/CN_042');
INSERT INTO `t_operator_voice` VALUES (2372, 71, '任命助理', '真的只能这样站着吗？不可以做其他事情？好~无~聊~啊~', 'char_241_panda/CN_001');
INSERT INTO `t_operator_voice` VALUES (2373, 71, '交谈1', '这个拳甲真的不重哦，你看，我一个女孩子也能轻松带在身上的东西，能沉到哪里去嘛。', 'char_241_panda/CN_002');
INSERT INTO `t_operator_voice` VALUES (2374, 71, '交谈2', '和我并肩作战过？唔，我不记得了——行侠仗义本来就是我的职责嘛~', 'char_241_panda/CN_003');
INSERT INTO `t_operator_voice` VALUES (2375, 71, '交谈3', '哈，那个叫猎蜂的拳手，真的很厉害！忍不住让人使出真拳脚呢。她收藏的蜂蜜也很好吃！博士下次也一起去吧！不过，先得学会如何打拳才行哦。', 'char_241_panda/CN_004');
INSERT INTO `t_operator_voice` VALUES (2376, 71, '晋升后交谈1', '在看电影期刊？......嗯？没错啊，这篇报道里的当红功夫影星就是我本人~唉~？不信？呜哇，你看，这不就是我的脸吗？！为什么不信！', 'char_241_panda/CN_005');
INSERT INTO `t_operator_voice` VALUES (2377, 71, '晋升后交谈2', '脱离过去的生活有没有什么遗憾？当然有啦。这个世界对感染者很严厉，有许多地方我都再也去不了，真可惜。', 'char_241_panda/CN_006');
INSERT INTO `t_operator_voice` VALUES (2378, 71, '信赖提升后交谈1', '附近的城市有小吃街呢。这边是糖果店？好像也不错。哦？游乐场？！博士博士，我能请一天假出去玩吗！', 'char_241_panda/CN_007');
INSERT INTO `t_operator_voice` VALUES (2379, 71, '信赖提升后交谈2', '对罗德岛的生活满不满意，呀，嘛，说实话......比电影本身都有趣！！有人切磋，有地方吃好吃的，工作就是行善！太完美了！', 'char_241_panda/CN_008');
INSERT INTO `t_operator_voice` VALUES (2380, 71, '信赖提升后交谈3', '博士，你看这个剧本怎么样！嗯？主演？是你和我啦。其中有一幕是我们从三十楼上跳下来然后滑行，超酷的！喂！别走啊！等一下！', 'char_241_panda/CN_009');
INSERT INTO `t_operator_voice` VALUES (2381, 71, '闲置', '博士在小憩，我也稍微偷个懒好了，唔......沙发......好软......', 'char_241_panda/CN_010');
INSERT INTO `t_operator_voice` VALUES (2382, 71, '干员报到', '以前在哪儿见过我？那肯定是在银幕上啦~不过现在的我叫食铁兽呢，以后的工作安排就麻烦你了哦。', 'char_241_panda/CN_011');
INSERT INTO `t_operator_voice` VALUES (2383, 71, '观看作战记录', '没有了？再看一会儿嘛，马上就要到最精彩的部分了！', 'char_241_panda/CN_012');
INSERT INTO `t_operator_voice` VALUES (2384, 71, '精英化晋升1', '晋~升~，欸嘿嘿，谢谢~', 'char_241_panda/CN_013');
INSERT INTO `t_operator_voice` VALUES (2385, 71, '精英化晋升2', '机会难得，就让你见识见识真正的拳法吧！', 'char_241_panda/CN_014');
INSERT INTO `t_operator_voice` VALUES (2386, 71, '编入队伍', '好！是时候大显身手了！', 'char_241_panda/CN_017');
INSERT INTO `t_operator_voice` VALUES (2387, 71, '任命队长', '唉唉，当队长吗？我不是很擅长哦！', 'char_241_panda/CN_018');
INSERT INTO `t_operator_voice` VALUES (2388, 71, '行动出发', '这一战一定会很精彩！', 'char_241_panda/CN_019');
INSERT INTO `t_operator_voice` VALUES (2389, 71, '行动开始', '在下食铁兽，请赐教！', 'char_241_panda/CN_020');
INSERT INTO `t_operator_voice` VALUES (2390, 71, '选中干员1', '嗯嗯。', 'char_241_panda/CN_021');
INSERT INTO `t_operator_voice` VALUES (2391, 71, '选中干员2', '要上了！', 'char_241_panda/CN_022');
INSERT INTO `t_operator_voice` VALUES (2392, 71, '部署1', '好！', 'char_241_panda/CN_023');
INSERT INTO `t_operator_voice` VALUES (2393, 71, '部署2', '大展拳脚咯！', 'char_241_panda/CN_024');
INSERT INTO `t_operator_voice` VALUES (2394, 71, '作战中1', '咿~~~呀！', 'char_241_panda/CN_025');
INSERT INTO `t_operator_voice` VALUES (2395, 71, '作战中2', '这可是我电影里的经典桥段！', 'char_241_panda/CN_026');
INSERT INTO `t_operator_voice` VALUES (2396, 71, '作战中3', '曲中生直，柔能生刚！', 'char_241_panda/CN_027');
INSERT INTO `t_operator_voice` VALUES (2397, 71, '作战中4', '就算是电影明星，也是有真功夫的！', 'char_241_panda/CN_028');
INSERT INTO `t_operator_voice` VALUES (2398, 71, '4星结束行动', '喔，哇哦，结束了吗！', 'char_241_panda/CN_029');
INSERT INTO `t_operator_voice` VALUES (2399, 71, '3星结束行动', '这就完了？我热身还没做好呢。', 'char_241_panda/CN_030');
INSERT INTO `t_operator_voice` VALUES (2400, 71, '非3星结束行动', '别想那么多啦，受点伤没什么大不了的。', 'char_241_panda/CN_031');
INSERT INTO `t_operator_voice` VALUES (2401, 71, '行动失败', '不行......好想回家睡觉......', 'char_241_panda/CN_032');
INSERT INTO `t_operator_voice` VALUES (2402, 71, '进驻设施', '还挺时髦的嘛！', 'char_241_panda/CN_033');
INSERT INTO `t_operator_voice` VALUES (2403, 71, '戳一下', '唉唉！头会昏的！', 'char_241_panda/CN_034');
INSERT INTO `t_operator_voice` VALUES (2404, 71, '信赖触摸', '嘿嘿，博士，想不想要我的签名？附赠我们两个的合照哦！', 'char_241_panda/CN_036');
INSERT INTO `t_operator_voice` VALUES (2405, 71, '标题', '明日方舟。', 'char_241_panda/CN_037');
INSERT INTO `t_operator_voice` VALUES (2406, 71, '问候', '博士！有没有好好吃早饭呢！', 'char_241_panda/CN_042');
INSERT INTO `t_operator_voice` VALUES (2407, 72, '任命助理', '老板，安排点差事给我们吧~', 'char_103_angel/CN_001');
INSERT INTO `t_operator_voice` VALUES (2408, 72, '交谈1', '老板，不办个派对吗？来点嘻哈，再来点烤苹果派~完美的人生可是不能停止聚会的！No party，No life！', 'char_103_angel/CN_002');
INSERT INTO `t_operator_voice` VALUES (2409, 72, '交谈2', '子弹上膛，准备万全★老板，今天送点什么呀！', 'char_103_angel/CN_003');
INSERT INTO `t_operator_voice` VALUES (2410, 72, '交谈3', '有人认为只要有钱什么都能做到，不过在我这里可是行不通的哦。如果我要那个人安息，他有多少钱都没有用~', 'char_103_angel/CN_004');
INSERT INTO `t_operator_voice` VALUES (2411, 72, '晋升后交谈1', '第一个愿望！请送我八把铳当礼物！我们天使都有自己的守护铳，但只有一把可不够看！', 'char_103_angel/CN_005');
INSERT INTO `t_operator_voice` VALUES (2412, 72, '晋升后交谈2', '第二个愿望......找个人把我头上这盏日光灯管关掉！', 'char_103_angel/CN_006');
INSERT INTO `t_operator_voice` VALUES (2413, 72, '信赖提升后交谈1', '德克萨斯那家伙能活得这么潇洒，可多亏有我罩着她，这不是明摆着的事嘛~', 'char_103_angel/CN_007');
INSERT INTO `t_operator_voice` VALUES (2414, 72, '信赖提升后交谈2', '如果您见到一名长着漆黑的角，散发着不祥气息的天使，请一定替我转告她：我从来没有忘记过她。', 'char_103_angel/CN_008');
INSERT INTO `t_operator_voice` VALUES (2415, 72, '信赖提升后交谈3', '老板，咱们企鹅物流还是挺不错的吧？要不要来当当我们的老大？欸嘿嘿~开玩笑的~', 'char_103_angel/CN_009');
INSERT INTO `t_operator_voice` VALUES (2416, 72, '闲置', '......主啊，这个人也是我们要拯救的吗？', 'char_103_angel/CN_010');
INSERT INTO `t_operator_voice` VALUES (2417, 72, '干员报到', '口令是“企鹅帝国万岁”，你就是雇主吗？叫我能天使。我和那个冷淡的鲁珀人可不一样，你要是想找点有趣的事做，随时都可以来叫我！', 'char_103_angel/CN_011');
INSERT INTO `t_operator_voice` VALUES (2418, 72, '观看作战记录', '我喜欢这种感觉！', 'char_103_angel/CN_012');
INSERT INTO `t_operator_voice` VALUES (2419, 72, '精英化晋升1', '老板，多谢提拔~', 'char_103_angel/CN_013');
INSERT INTO `t_operator_voice` VALUES (2420, 72, '精英化晋升2', '老板......不，义人，以手中的这把铳起誓，我将守护您的生命直至万物终结之日。', 'char_103_angel/CN_014');
INSERT INTO `t_operator_voice` VALUES (2421, 72, '编入队伍', '小组作战，我熟得很～', 'char_103_angel/CN_017');
INSERT INTO `t_operator_voice` VALUES (2422, 72, '任命队长', '没有比我更适合当队长的人！您真是别具慧眼！', 'char_103_angel/CN_018');
INSERT INTO `t_operator_voice` VALUES (2423, 72, '行动出发', '出发！让我们像风暴一样碾过去！', 'char_103_angel/CN_019');
INSERT INTO `t_operator_voice` VALUES (2424, 72, '行动开始', '我帮你们预定了地狱黄金地段的房产，请放心！', 'char_103_angel/CN_020');
INSERT INTO `t_operator_voice` VALUES (2425, 72, '选中干员1', '好！', 'char_103_angel/CN_021');
INSERT INTO `t_operator_voice` VALUES (2426, 72, '选中干员2', '轮到我出场了吗？', 'char_103_angel/CN_022');
INSERT INTO `t_operator_voice` VALUES (2427, 72, '部署1', '跟我来！', 'char_103_angel/CN_023');
INSERT INTO `t_operator_voice` VALUES (2428, 72, '部署2', '让我来制造点混乱。', 'char_103_angel/CN_024');
INSERT INTO `t_operator_voice` VALUES (2429, 72, '作战中1', '天意！', 'char_103_angel/CN_025');
INSERT INTO `t_operator_voice` VALUES (2430, 72, '作战中2', '摇滚！', 'char_103_angel/CN_026');
INSERT INTO `t_operator_voice` VALUES (2431, 72, '作战中3', '弹幕！', 'char_103_angel/CN_027');
INSERT INTO `t_operator_voice` VALUES (2432, 72, '作战中4', '苹果派！', 'char_103_angel/CN_028');
INSERT INTO `t_operator_voice` VALUES (2433, 72, '4星结束行动', '好！就照这个劲头向前冲！', 'char_103_angel/CN_029');
INSERT INTO `t_operator_voice` VALUES (2434, 72, '3星结束行动', '愿我的弹雨能熄灭你们的苦痛。', 'char_103_angel/CN_030');
INSERT INTO `t_operator_voice` VALUES (2435, 72, '非3星结束行动', '胜利的讯息即是甜蜜的福音。好~回去喝一杯吧！', 'char_103_angel/CN_031');
INSERT INTO `t_operator_voice` VALUES (2436, 72, '行动失败', '铳有卡壳的时候，人生也是如此，别介意别介意~', 'char_103_angel/CN_032');
INSERT INTO `t_operator_voice` VALUES (2437, 72, '进驻设施', '我喜欢这个地方！', 'char_103_angel/CN_033');
INSERT INTO `t_operator_voice` VALUES (2438, 72, '戳一下', '哟！', 'char_103_angel/CN_034');
INSERT INTO `t_operator_voice` VALUES (2439, 72, '信赖触摸', '老板！来试试我的武器吗？', 'char_103_angel/CN_036');
INSERT INTO `t_operator_voice` VALUES (2440, 72, '标题', '明日方舟。', 'char_103_angel/CN_037');
INSERT INTO `t_operator_voice` VALUES (2441, 72, '问候', '哟，老板！', 'char_103_angel/CN_042');
INSERT INTO `t_operator_voice` VALUES (2442, 73, '任命助理', '作战任务分配好了？没有的话，我可以再等等......', 'char_112_siege/CN_001');
INSERT INTO `t_operator_voice` VALUES (2443, 73, '交谈1', '保持冷静，继续前行。', 'char_112_siege/CN_002');
INSERT INTO `t_operator_voice` VALUES (2444, 73, '交谈2', '以前的伙食还挺不错的，但自从和因陀罗她们在一起之后......变得糟透了。', 'char_112_siege/CN_003');
INSERT INTO `t_operator_voice` VALUES (2445, 73, '交谈3', '我做了个梦......那是太阳升起的地方。视线无法触及之处，我的祖国就在那里......我或许，再也回不去了。', 'char_112_siege/CN_004');
INSERT INTO `t_operator_voice` VALUES (2446, 73, '晋升后交谈1', '回过神来，已经习惯了这种漂泊的生活，真奇妙啊——这是我从来没有想象过的事。', 'char_112_siege/CN_005');
INSERT INTO `t_operator_voice` VALUES (2447, 73, '晋升后交谈2', '你说高文他们？......与其说是我遇见他们，倒不如说是他们捡到了我。', 'char_112_siege/CN_006');
INSERT INTO `t_operator_voice` VALUES (2448, 73, '信赖提升后交谈1', '和因陀罗他们在维多利亚的街头并肩作战——回想起来，我也慢慢变得珍惜起这段回忆了。', 'char_112_siege/CN_007');
INSERT INTO `t_operator_voice` VALUES (2449, 73, '信赖提升后交谈2', '别放松警惕......！我知道你已经十分努力了，但仅凭我们现在的力量，总会发生力不能及的事情。', 'char_112_siege/CN_008');
INSERT INTO `t_operator_voice` VALUES (2450, 73, '信赖提升后交谈3', '如果遭遇了无法逃避的难题......我也是会有些许私心的。到那时，博士，希望你也能成为我的......不，没什么。', 'char_112_siege/CN_009');
INSERT INTO `t_operator_voice` VALUES (2451, 73, '闲置', '......嗯？睡着了？不知你的梦中，是否也会有故乡的景色。', 'char_112_siege/CN_010');
INSERT INTO `t_operator_voice` VALUES (2452, 73, '干员报到', '力量测试已经通过了吧。那么，请给我些任务，别让这把锤子太无聊。代号？大家都叫我“推进之王”，不是很好记，但我觉得还不错。', 'char_112_siege/CN_011');
INSERT INTO `t_operator_voice` VALUES (2453, 73, '观看作战记录', '感觉不赖。', 'char_112_siege/CN_012');
INSERT INTO `t_operator_voice` VALUES (2454, 73, '精英化晋升1', '晋升？哦——？', 'char_112_siege/CN_013');
INSERT INTO `t_operator_voice` VALUES (2455, 73, '精英化晋升2', '又是晋升？嗯，谢咯。仔细想想还挺奇妙的......因为在过去，做这件事的人是我呢。', 'char_112_siege/CN_014');
INSERT INTO `t_operator_voice` VALUES (2456, 73, '编入队伍', '这，是一场必要的战斗吗？', 'char_112_siege/CN_017');
INSERT INTO `t_operator_voice` VALUES (2457, 73, '任命队长', '可要好好熟悉我的作战风格。', 'char_112_siege/CN_018');
INSERT INTO `t_operator_voice` VALUES (2458, 73, '行动出发', '丢了一个钉子......死了一个国王。', 'char_112_siege/CN_019');
INSERT INTO `t_operator_voice` VALUES (2459, 73, '行动开始', '逃吧，被当成懦夫总比没命强。', 'char_112_siege/CN_020');
INSERT INTO `t_operator_voice` VALUES (2460, 73, '选中干员1', '哦？', 'char_112_siege/CN_021');
INSERT INTO `t_operator_voice` VALUES (2461, 73, '选中干员2', '准备好了吗？', 'char_112_siege/CN_022');
INSERT INTO `t_operator_voice` VALUES (2462, 73, '部署1', '麻烦。', 'char_112_siege/CN_023');
INSERT INTO `t_operator_voice` VALUES (2463, 73, '部署2', '一口气解决。', 'char_112_siege/CN_024');
INSERT INTO `t_operator_voice` VALUES (2464, 73, '作战中1', '这把武器可非庸人能使。', 'char_112_siege/CN_025');
INSERT INTO `t_operator_voice` VALUES (2465, 73, '作战中2', '闪开。', 'char_112_siege/CN_026');
INSERT INTO `t_operator_voice` VALUES (2466, 73, '作战中3', '上了战场，就要有饮败的觉悟！', 'char_112_siege/CN_027');
INSERT INTO `t_operator_voice` VALUES (2467, 73, '作战中4', '你的生死由我裁定。', 'char_112_siege/CN_028');
INSERT INTO `t_operator_voice` VALUES (2468, 73, '4星结束行动', '装模作样，不堪一击。', 'char_112_siege/CN_029');
INSERT INTO `t_operator_voice` VALUES (2469, 73, '3星结束行动', '对拒绝投降者无需怜悯。', 'char_112_siege/CN_030');
INSERT INTO `t_operator_voice` VALUES (2470, 73, '非3星结束行动', '结束了吧？', 'char_112_siege/CN_031');
INSERT INTO `t_operator_voice` VALUES (2471, 73, '行动失败', '意外的......疲惫......十分抱歉。', 'char_112_siege/CN_032');
INSERT INTO `t_operator_voice` VALUES (2472, 73, '进驻设施', '能找个地方让我睡一觉吗？', 'char_112_siege/CN_033');
INSERT INTO `t_operator_voice` VALUES (2473, 73, '戳一下', '嘁——', 'char_112_siege/CN_034');
INSERT INTO `t_operator_voice` VALUES (2474, 73, '信赖触摸', '外面太吵闹了......你的办公室就不一样，很安静，挺不错的，让我待一会吧。', 'char_112_siege/CN_036');
INSERT INTO `t_operator_voice` VALUES (2475, 73, '标题', '明日方舟。', 'char_112_siege/CN_037');
INSERT INTO `t_operator_voice` VALUES (2476, 73, '问候', '噢！博士。', 'char_112_siege/CN_042');
INSERT INTO `t_operator_voice` VALUES (2477, 74, '任命助理', '这个办公室怎么这么冷！喂，那个能烧吗？', 'char_134_ifrit/CN_001');
INSERT INTO `t_operator_voice` VALUES (2478, 74, '交谈1', '欸？这本书很贵吗？嘻嘻，叫我一声伊芙利特大人，我就还给你！', 'char_134_ifrit/CN_002');
INSERT INTO `t_operator_voice` VALUES (2479, 74, '交谈2', '什么？要我读这本书？我最讨厌的就是......唔？赫、赫默是这样说的吗？呃......这、这个字怎么念啊？', 'char_134_ifrit/CN_003');
INSERT INTO `t_operator_voice` VALUES (2480, 74, '交谈3', '我最讨厌穿白大褂的家伙盯着我看......就是在说你啊！在说你！别看了，脸转过去！', 'char_134_ifrit/CN_004');
INSERT INTO `t_operator_voice` VALUES (2481, 74, '晋升后交谈1', '唔呃......我知道啦，不会再用火吓唬大家了。', 'char_134_ifrit/CN_005');
INSERT INTO `t_operator_voice` VALUES (2482, 74, '晋升后交谈2', '退后面去！你......靠近我是想干什么？难道你也想把我放到试验台上......？', 'char_134_ifrit/CN_006');
INSERT INTO `t_operator_voice` VALUES (2483, 74, '信赖提升后交谈1', '塞雷娅来了吗？让我去见——赫默不准？无所谓的吧？你带我偷偷过去不就好了吗？', 'char_134_ifrit/CN_007');
INSERT INTO `t_operator_voice` VALUES (2484, 74, '信赖提升后交谈2', '赫默和塞雷娅，她们究竟为什么吵架？真搞不懂......喂！有没有什么阻止她们的办法？告诉我吧......？', 'char_134_ifrit/CN_008');
INSERT INTO `t_operator_voice` VALUES (2485, 74, '信赖提升后交谈3', '你怎么对待我都无所谓，对赫默绝对要温柔。她想要什么你都帮她尽力实现！如果你真能做到，从今以后，我就都听你的！', 'char_134_ifrit/CN_009');
INSERT INTO `t_operator_voice` VALUES (2486, 74, '闲置', '你这家伙，把我晾在一边一个人睡大觉，胆子很大啊！', 'char_134_ifrit/CN_010');
INSERT INTO `t_operator_voice` VALUES (2487, 74, '干员报到', '我就是伊芙利特！赫默说罗德岛是个好地方，但我不喜欢你们这些穿白大衣的家伙......除了赫默之外。', 'char_134_ifrit/CN_011');
INSERT INTO `t_operator_voice` VALUES (2488, 74, '观看作战记录', '强化？唔，就是我的火焰能烧得更旺，这种感觉？', 'char_134_ifrit/CN_012');
INSERT INTO `t_operator_voice` VALUES (2489, 74, '精英化晋升1', '这是什么？罗德岛干员晋升手册？干什么啊，晋升之后还有这么多麻烦规矩？我才不干！', 'char_134_ifrit/CN_013');
INSERT INTO `t_operator_voice` VALUES (2490, 74, '精英化晋升2', '嘻嘻，这就对了嘛！那些条条框框我都不懂。反正你需要我的帮助，光这一句就够了！', 'char_134_ifrit/CN_014');
INSERT INTO `t_operator_voice` VALUES (2491, 74, '编入队伍', '知道啦知道啦！', 'char_134_ifrit/CN_017');
INSERT INTO `t_operator_voice` VALUES (2492, 74, '任命队长', '我是队长？像赫默那样做就行了吗？', 'char_134_ifrit/CN_018');
INSERT INTO `t_operator_voice` VALUES (2493, 74, '行动出发', '喂，说好了我是队长，我来下出击命令才对吧！', 'char_134_ifrit/CN_019');
INSERT INTO `t_operator_voice` VALUES (2494, 74, '行动开始', '哟，来一次久违的BBQ吧！', 'char_134_ifrit/CN_020');
INSERT INTO `t_operator_voice` VALUES (2495, 74, '选中干员1', '喂！', 'char_134_ifrit/CN_021');
INSERT INTO `t_operator_voice` VALUES (2496, 74, '选中干员2', '真慢！', 'char_134_ifrit/CN_022');
INSERT INTO `t_operator_voice` VALUES (2497, 74, '部署1', '大地，沸腾吧！', 'char_134_ifrit/CN_023');
INSERT INTO `t_operator_voice` VALUES (2498, 74, '部署2', '地狱，随时填装！', 'char_134_ifrit/CN_024');
INSERT INTO `t_operator_voice` VALUES (2499, 74, '作战中1', '疼吗？疼吗！', 'char_134_ifrit/CN_025');
INSERT INTO `t_operator_voice` VALUES (2500, 74, '作战中2', '哈哈！再多叫几声！', 'char_134_ifrit/CN_026');
INSERT INTO `t_operator_voice` VALUES (2501, 74, '作战中3', '没人在乎你的惨叫！', 'char_134_ifrit/CN_027');
INSERT INTO `t_operator_voice` VALUES (2502, 74, '作战中4', '排好队！', 'char_134_ifrit/CN_028');
INSERT INTO `t_operator_voice` VALUES (2503, 74, '4星结束行动', '不够！完全不够！根本没人能打败我！', 'char_134_ifrit/CN_029');
INSERT INTO `t_operator_voice` VALUES (2504, 74, '3星结束行动', '这么多废弃物，一把火烧个干净吧？会很暖和的！', 'char_134_ifrit/CN_030');
INSERT INTO `t_operator_voice` VALUES (2505, 74, '非3星结束行动', '焦炭，满地焦炭，这种战斗我一个人就足够了。', 'char_134_ifrit/CN_031');
INSERT INTO `t_operator_voice` VALUES (2506, 74, '行动失败', '可恶......你......都是你的原因，让我出丑！', 'char_134_ifrit/CN_032');
INSERT INTO `t_operator_voice` VALUES (2507, 74, '进驻设施', '这里是什么地方啊？', 'char_134_ifrit/CN_033');
INSERT INTO `t_operator_voice` VALUES (2508, 74, '戳一下', '干什么！放开我！', 'char_134_ifrit/CN_034');
INSERT INTO `t_operator_voice` VALUES (2509, 74, '信赖触摸', '嘻嘻，你这家伙对我还挺好嘛，想和我一起玩吗？', 'char_134_ifrit/CN_036');
INSERT INTO `t_operator_voice` VALUES (2510, 74, '标题', '明日方舟。', 'char_134_ifrit/CN_037');
INSERT INTO `t_operator_voice` VALUES (2511, 74, '问候', '啊？找我有事吗？', 'char_134_ifrit/CN_042');
INSERT INTO `t_operator_voice` VALUES (2512, 75, '任命助理', '前辈，您借我的天灾研究笔记我都读完了，还有其他的吗？', 'char_180_amgoat/CN_001');
INSERT INTO `t_operator_voice` VALUES (2513, 75, '交谈1', '您说什么？为什么？为什么要叫您前辈？啊，因为您是天灾研究的专家呀，按照规矩一定要叫前辈的~', 'char_180_amgoat/CN_002');
INSERT INTO `t_operator_voice` VALUES (2514, 75, '交谈2', '那个，原——什么？啊，是问我听力恶化的原因？其实，是我以前在火山地带做考察的时候，不小心感染了矿石病的缘故......', 'char_180_amgoat/CN_003');
INSERT INTO `t_operator_voice` VALUES (2515, 75, '交谈3', '除了完成前辈您安排的工作，我还要抽空去凯尔希医生那里做检查。医生说，我的病症似乎是比较严重的那种......', 'char_180_amgoat/CN_004');
INSERT INTO `t_operator_voice` VALUES (2516, 75, '晋升后交谈1', '为了完成父母的研究，我还得更努力才行。如果我能证明他们的研究是有意义的，说不定，将会有一个影响世界的大发现呢。', 'char_180_amgoat/CN_005');
INSERT INTO `t_operator_voice` VALUES (2517, 75, '晋升后交谈2', '我的父母......已经去世了，他们在火山周边进行观测时遭遇了火山碎屑流......我必须继承父母的遗志，完成他们的学说，无论如何都要......', 'char_180_amgoat/CN_006');
INSERT INTO `t_operator_voice` VALUES (2518, 75, '信赖提升后交谈1', '玩伴吗？我和慕斯她们关系很好，所以一点也不寂寞~如果可以的话，我想和大家一起办读书会！嗯，就在前辈您的办公室，可以吗？', 'char_180_amgoat/CN_007');
INSERT INTO `t_operator_voice` VALUES (2519, 75, '信赖提升后交谈2', '我从凯尔希医生那里听到了，不只是听力，我的视力也在慢慢丧失......矿石病，究竟会把我变成什么样子呢？', 'char_180_amgoat/CN_008');
INSERT INTO `t_operator_voice` VALUES (2520, 75, '信赖提升后交谈3', '为什么能察觉到前辈站在我身后？那是因为我感受到了前辈的温度~虽然我的视力听力都变差了，但其他感官还很敏锐哦~即使变成现在这个样子，我呀，可没那么容易被打倒的~', 'char_180_amgoat/CN_009');
INSERT INTO `t_operator_voice` VALUES (2521, 75, '闲置', '前辈，睡着了呀......', 'char_180_amgoat/CN_010');
INSERT INTO `t_operator_voice` VALUES (2522, 75, '干员报到', '我是艾雅法拉，加入罗德岛前一直在研究火山地质。因为矿石病的影响，我的听力不太好，也许，以后要多多麻烦您了，前辈~', 'char_180_amgoat/CN_011');
INSERT INTO `t_operator_voice` VALUES (2523, 75, '观看作战记录', '请您给予我更多的指导，前辈~', 'char_180_amgoat/CN_012');
INSERT INTO `t_operator_voice` VALUES (2524, 75, '精英化晋升1', '晋升？是真的吗？能被前辈夸奖，真的好高兴！', 'char_180_amgoat/CN_013');
INSERT INTO `t_operator_voice` VALUES (2525, 75, '精英化晋升2', '这些小羊是妈妈留给我的，前辈也觉得它们很可爱吧？欸？热——很烫？啊请等等，不戴隔热手套的话会被它们烫伤的！', 'char_180_amgoat/CN_014');
INSERT INTO `t_operator_voice` VALUES (2526, 75, '编入队伍', '我知道了，前辈。', 'char_180_amgoat/CN_017');
INSERT INTO `t_operator_voice` VALUES (2527, 75, '任命队长', '除了武器，您也别忘记准备防灾用具哦~', 'char_180_amgoat/CN_018');
INSERT INTO `t_operator_voice` VALUES (2528, 75, '行动出发', '前辈，我们能照顾好自己，您专注指挥就好。', 'char_180_amgoat/CN_019');
INSERT INTO `t_operator_voice` VALUES (2529, 75, '行动开始', '请大家一定要注意安全。', 'char_180_amgoat/CN_020');
INSERT INTO `t_operator_voice` VALUES (2530, 75, '选中干员1', '前辈？', 'char_180_amgoat/CN_021');
INSERT INTO `t_operator_voice` VALUES (2531, 75, '选中干员2', '前辈，请您吩咐。', 'char_180_amgoat/CN_022');
INSERT INTO `t_operator_voice` VALUES (2532, 75, '部署1', '我会小心行动的。', 'char_180_amgoat/CN_023');
INSERT INTO `t_operator_voice` VALUES (2533, 75, '部署2', '加油！', 'char_180_amgoat/CN_024');
INSERT INTO `t_operator_voice` VALUES (2534, 75, '作战中1', '温度稳定。', 'char_180_amgoat/CN_025');
INSERT INTO `t_operator_voice` VALUES (2535, 75, '作战中2', '可能有点热哦？', 'char_180_amgoat/CN_026');
INSERT INTO `t_operator_voice` VALUES (2536, 75, '作战中3', '请小心火灾。', 'char_180_amgoat/CN_027');
INSERT INTO `t_operator_voice` VALUES (2537, 75, '作战中4', '这招怎么样！', 'char_180_amgoat/CN_028');
INSERT INTO `t_operator_voice` VALUES (2538, 75, '4星结束行动', '前辈平安无事吗？太好了......', 'char_180_amgoat/CN_029');
INSERT INTO `t_operator_voice` VALUES (2539, 75, '3星结束行动', '为什么要这样彼此争斗不休呢......', 'char_180_amgoat/CN_030');
INSERT INTO `t_operator_voice` VALUES (2540, 75, '非3星结束行动', '大家都没事吧？有什么我能做的吗？', 'char_180_amgoat/CN_031');
INSERT INTO `t_operator_voice` VALUES (2541, 75, '行动失败', '呜......没、没事的......', 'char_180_amgoat/CN_032');
INSERT INTO `t_operator_voice` VALUES (2542, 75, '进驻设施', '这个房间让我很安心~', 'char_180_amgoat/CN_033');
INSERT INTO `t_operator_voice` VALUES (2543, 75, '戳一下', '呀！别、别吓我，前辈！', 'char_180_amgoat/CN_034');
INSERT INTO `t_operator_voice` VALUES (2544, 75, '信赖触摸', '谢谢前辈不断地指引我的成长~', 'char_180_amgoat/CN_036');
INSERT INTO `t_operator_voice` VALUES (2545, 75, '标题', '明日方舟。', 'char_180_amgoat/CN_037');
INSERT INTO `t_operator_voice` VALUES (2546, 75, '问候', '前辈，早上好~', 'char_180_amgoat/CN_042');
INSERT INTO `t_operator_voice` VALUES (2547, 76, '任命助理', '{@nickname}，这是新咖啡机泡出的浓咖啡，要尝尝吗？不过每天只有一杯哦，熬夜可不是什么好事呢。', 'char_291_aglina/CN_001');
INSERT INTO `t_operator_voice` VALUES (2548, 76, '交谈1', '信使的工作并不轻松。送件人和收件人可能都有着自己的野心，包裹里也许埋藏着惊人的秘密......如果信使光盯着脚下的路，是会因为看不见落脚点而坠落的。', 'char_291_aglina/CN_002');
INSERT INTO `t_operator_voice` VALUES (2549, 76, '交谈2', '因为我的母亲来自东国，所以我的名字不那么像叙拉古人。大家都不怎么相信这是名字，所以我就干脆把安洁莉娜当作代号使用了。', 'char_291_aglina/CN_003');
INSERT INTO `t_operator_voice` VALUES (2550, 76, '交谈3', '早安{@nickname}！嗯是的，我要去送些文件什么的。虽然凯尔希医生说过我的能力在战场上很有用，但比起战斗，我更喜欢送信呢！', 'char_291_aglina/CN_004');
INSERT INTO `t_operator_voice` VALUES (2551, 76, '晋升后交谈1', '其实我不知道自己是否适合罗德岛......毕竟对我来说，很多事还是太沉重了。可颂安慰过我之后，我也依然在怀疑，我真的能承担这样的责任吗？', 'char_291_aglina/CN_005');
INSERT INTO `t_operator_voice` VALUES (2552, 76, '晋升后交谈2', '我不会再迷惘了。嗯，与其说我认同了罗德岛和{@nickname}你，不如说——作为信使，我已经能飞越大楼的间隙，而作为罗德岛干员的我，也要能跨过感染者和普通人之间的沟壑才行！', 'char_291_aglina/CN_006');
INSERT INTO `t_operator_voice` VALUES (2553, 76, '信赖提升后交谈1', '记忆是很重要的。正是过去的记忆在不断提醒我，感染者也和普通人一样，期望着美好的未来。我不知道{@nickname}有着怎样的过去，但我会和你一起找回来。', 'char_291_aglina/CN_007');
INSERT INTO `t_operator_voice` VALUES (2554, 76, '信赖提升后交谈2', '{@nickname}，别害羞，再靠近点！对对，sorridi~！看，是我和{@nickname}的大头贴哦~哼哼，就当做是纪念品吧。当然，这上面确实也有我的......一点点寄托呢。', 'char_291_aglina/CN_008');
INSERT INTO `t_operator_voice` VALUES (2555, 76, '信赖提升后交谈3', '甲板上的视野很好，很适合观赏星空呢。看到那颗孤零零的星星了吗？据说总有一天，它会等来自己的伴星，拥抱，起舞......但，那要等上多久？我又要等上多久，星星......才会明白呢？', 'char_291_aglina/CN_009');
INSERT INTO `t_operator_voice` VALUES (2556, 76, '闲置', '嗯哼哼~♪唔，唇彩果然还是选珊瑚红色比较好吧？', 'char_291_aglina/CN_010');
INSERT INTO `t_operator_voice` VALUES (2557, 76, '干员报到', '我是来自叙拉古的信使，安心院安洁莉娜。嗯，叫我安洁莉娜就可以了。我能用{@nickname}来称呼博士你吗？', 'char_291_aglina/CN_011');
INSERT INTO `t_operator_voice` VALUES (2558, 76, '观看作战记录', '战场很残酷......但我不想让{@nickname}失望。', 'char_291_aglina/CN_012');
INSERT INTO `t_operator_voice` VALUES (2559, 76, '精英化晋升1', '{@nickname}这么信任我，我也得拿出十二分的努力才行。毕竟，叙拉古的信使间流传着一句话，“跑得不够快的信使，是会被风吹落的”。', 'char_291_aglina/CN_013');
INSERT INTO `t_operator_voice` VALUES (2560, 76, '精英化晋升2', '我决定了。只要{@nickname}你还在为感染者奔走，我就会一直陪伴着你。{@nickname}已经为大家做了很多，现在，轮到我来为{@nickname}做点什么了。', 'char_291_aglina/CN_014');
INSERT INTO `t_operator_voice` VALUES (2561, 76, '编入队伍', '无论是战斗还是工作，都要全力以赴！', 'char_291_aglina/CN_017');
INSERT INTO `t_operator_voice` VALUES (2562, 76, '任命队长', '就算不怎么擅长，我也会努力去做的。', 'char_291_aglina/CN_018');
INSERT INTO `t_operator_voice` VALUES (2563, 76, '行动出发', '请大家一定要小心谨慎！', 'char_291_aglina/CN_019');
INSERT INTO `t_operator_voice` VALUES (2564, 76, '行动开始', '争斗要到什么时候才会停止呢......', 'char_291_aglina/CN_020');
INSERT INTO `t_operator_voice` VALUES (2565, 76, '选中干员1', '好~', 'char_291_aglina/CN_021');
INSERT INTO `t_operator_voice` VALUES (2566, 76, '选中干员2', '嗯嗯？', 'char_291_aglina/CN_022');
INSERT INTO `t_operator_voice` VALUES (2567, 76, '部署1', '轻轻地......嘿咻。', 'char_291_aglina/CN_023');
INSERT INTO `t_operator_voice` VALUES (2568, 76, '部署2', '嗯哼哼~', 'char_291_aglina/CN_024');
INSERT INTO `t_operator_voice` VALUES (2569, 76, '作战中1', '有没有感觉身体变重了？', 'char_291_aglina/CN_025');
INSERT INTO `t_operator_voice` VALUES (2570, 76, '作战中2', '要克服眼前的困难才行。', 'char_291_aglina/CN_026');
INSERT INTO `t_operator_voice` VALUES (2571, 76, '作战中3', '我可是很擅长这个法术的。', 'char_291_aglina/CN_027');
INSERT INTO `t_operator_voice` VALUES (2572, 76, '作战中4', '想不想试试漂浮在空中的感觉？', 'char_291_aglina/CN_028');
INSERT INTO `t_operator_voice` VALUES (2573, 76, '4星结束行动', '只有在困境中，人才会成长，对吧？', 'char_291_aglina/CN_029');
INSERT INTO `t_operator_voice` VALUES (2574, 76, '3星结束行动', '这是大家一起努力的成果，{@nickname}也会好好珍惜的，对吧。', 'char_291_aglina/CN_030');
INSERT INTO `t_operator_voice` VALUES (2575, 76, '非3星结束行动', '我也有因为疏忽而丢失信件的时候......', 'char_291_aglina/CN_031');
INSERT INTO `t_operator_voice` VALUES (2576, 76, '行动失败', '处境越是艰难，我们就越不能气馁！', 'char_291_aglina/CN_032');
INSERT INTO `t_operator_voice` VALUES (2577, 76, '进驻设施', '这里就像家一样呢！', 'char_291_aglina/CN_033');
INSERT INTO `t_operator_voice` VALUES (2578, 76, '戳一下', '呜哇~', 'char_291_aglina/CN_034');
INSERT INTO `t_operator_voice` VALUES (2579, 76, '信赖触摸', '我的头发很漂亮？嗯哼哼，我可是有秘诀的！', 'char_291_aglina/CN_036');
INSERT INTO `t_operator_voice` VALUES (2580, 76, '标题', '明日方舟。', 'char_291_aglina/CN_037');
INSERT INTO `t_operator_voice` VALUES (2581, 76, '问候', '早安，{@nickname}！', 'char_291_aglina/CN_042');
INSERT INTO `t_operator_voice` VALUES (2582, 77, '任命助理', '你好，博士......没有什么，想聊的话题吗？', 'char_147_shining/CN_001');
INSERT INTO `t_operator_voice` VALUES (2583, 77, '交谈1', '你的脸色很糟糕。胸口也很痛？不要逞强，请马上休息......别害怕，我就在你身边，一定会没事的......', 'char_147_shining/CN_002');
INSERT INTO `t_operator_voice` VALUES (2584, 77, '交谈2', '我的医疗法术似乎在什么地方见过......？那是错觉，嗯，一定是......', 'char_147_shining/CN_003');
INSERT INTO `t_operator_voice` VALUES (2585, 77, '交谈3', '“赦罪师”......？为什么会提到这个名字......对不起，这个话题我不能继续下去......', 'char_147_shining/CN_004');
INSERT INTO `t_operator_voice` VALUES (2586, 77, '晋升后交谈1', '我们萨卡兹人，把法术天赋和源石的力量结合在一起，开发出了能够治愈他人的源石技艺。一直以来，这种法术都被我们用来救助受难者。', 'char_147_shining/CN_005');
INSERT INTO `t_operator_voice` VALUES (2587, 77, '晋升后交谈2', '现在？现在，什么都没有了。那场战争，剥夺了我们所有的希望。', 'char_147_shining/CN_006');
INSERT INTO `t_operator_voice` VALUES (2588, 77, '信赖提升后交谈1', '我和临光？是啊，她是我旅途的伙伴，也是值得托付生命的人。', 'char_147_shining/CN_007');
INSERT INTO `t_operator_voice` VALUES (2589, 77, '信赖提升后交谈2', '为拯救痛苦的人们不惜牺牲自己的一切——临光那个孩子，就是这样一个人。', 'char_147_shining/CN_008');
INSERT INTO `t_operator_voice` VALUES (2590, 77, '信赖提升后交谈3', '曾经，我追随着一位身影辉煌的人，直到那光芒在阴谋中消散。这次，这光芒，将永远绽放、永不泯灭，哪怕要用我的生命作为交换，也在所不惜......', 'char_147_shining/CN_009');
INSERT INTO `t_operator_voice` VALUES (2591, 77, '闲置', '......这份寂静， 让我想起了身在故乡时的岁月。', 'char_147_shining/CN_010');
INSERT INTO `t_operator_voice` VALUES (2592, 77, '干员报到', '巡回医师闪灵。你在哪里见过我吗？或许在旅途中，我们也曾擦肩而过。', 'char_147_shining/CN_011');
INSERT INTO `t_operator_voice` VALUES (2593, 77, '观看作战记录', '只是用于愈合伤口的力量，是不够的。', 'char_147_shining/CN_012');
INSERT INTO `t_operator_voice` VALUES (2594, 77, '精英化晋升1', '单单作为医者，是无法拯救更多人的。', 'char_147_shining/CN_013');
INSERT INTO `t_operator_voice` VALUES (2595, 77, '精英化晋升2', '是的，不仅要作为医者，更是作为守护者而生存下去，长久以来，我一直将此作为目标。博士，聆听吧......请你，见证我的誓言。', 'char_147_shining/CN_014');
INSERT INTO `t_operator_voice` VALUES (2596, 77, '编入队伍', '该前往战场了？', 'char_147_shining/CN_017');
INSERT INTO `t_operator_voice` VALUES (2597, 77, '任命队长', '这是为了拯救我们自己而战，对吧？', 'char_147_shining/CN_018');
INSERT INTO `t_operator_voice` VALUES (2598, 77, '行动出发', '战争，无法逃避。', 'char_147_shining/CN_019');
INSERT INTO `t_operator_voice` VALUES (2599, 77, '行动开始', '能从战斗中寻得拯救......吗？', 'char_147_shining/CN_020');
INSERT INTO `t_operator_voice` VALUES (2600, 77, '选中干员1', '开始吧。', 'char_147_shining/CN_021');
INSERT INTO `t_operator_voice` VALUES (2601, 77, '选中干员2', '是的，我在听。', 'char_147_shining/CN_022');
INSERT INTO `t_operator_voice` VALUES (2602, 77, '部署1', '无论繁荣，还是灭亡。', 'char_147_shining/CN_023');
INSERT INTO `t_operator_voice` VALUES (2603, 77, '部署2', '只是一瞬间而已。', 'char_147_shining/CN_024');
INSERT INTO `t_operator_voice` VALUES (2604, 77, '作战中1', '你，由我来守护。', 'char_147_shining/CN_025');
INSERT INTO `t_operator_voice` VALUES (2605, 77, '作战中2', '别担心。', 'char_147_shining/CN_026');
INSERT INTO `t_operator_voice` VALUES (2606, 77, '作战中3', '如此的......悲伤。', 'char_147_shining/CN_027');
INSERT INTO `t_operator_voice` VALUES (2607, 77, '作战中4', '痛苦，我从未忘记。', 'char_147_shining/CN_028');
INSERT INTO `t_operator_voice` VALUES (2608, 77, '4星结束行动', '敌人愈是强大，愈是能鼓舞我们的生命意志和战斗精神。', 'char_147_shining/CN_029');
INSERT INTO `t_operator_voice` VALUES (2609, 77, '3星结束行动', '无论罪业多少次往复，都会与生命一同化为尘土。', 'char_147_shining/CN_030');
INSERT INTO `t_operator_voice` VALUES (2610, 77, '非3星结束行动', '不要再回到战场上了，这里没有救赎。', 'char_147_shining/CN_031');
INSERT INTO `t_operator_voice` VALUES (2611, 77, '行动失败', '胜利，失败......只不过是无限循环的一瞬。', 'char_147_shining/CN_032');
INSERT INTO `t_operator_voice` VALUES (2612, 77, '进驻设施', '让我待在安静的地方就可以了。', 'char_147_shining/CN_033');
INSERT INTO `t_operator_voice` VALUES (2613, 77, '戳一下', '怎么了？', 'char_147_shining/CN_034');
INSERT INTO `t_operator_voice` VALUES (2614, 77, '信赖触摸', '我的心情很久没这样平静过了，甚至连您的心跳，我也听得见。', 'char_147_shining/CN_036');
INSERT INTO `t_operator_voice` VALUES (2615, 77, '标题', '明日方舟。', 'char_147_shining/CN_037');
INSERT INTO `t_operator_voice` VALUES (2616, 77, '问候', '博士，今天的感觉怎么样？', 'char_147_shining/CN_042');
INSERT INTO `t_operator_voice` VALUES (2617, 78, '任命助理', '非常抱歉，明明我的工作是照看您，但我的身体没法很好地活动......', 'char_179_cgbird/CN_001');
INSERT INTO `t_operator_voice` VALUES (2618, 78, '交谈1', '因为我吸入过某种源石毒素，所以记忆和下肢神经系统都受到了损害——凯尔希医生是这样告诉我的......过去的我，到底经历过什么呢？', 'char_179_cgbird/CN_002');
INSERT INTO `t_operator_voice` VALUES (2619, 78, '交谈2', '这只蓝色的小鸟，时常飞来和我聊天......有时候，我会梦见自己变成这孩子的模样，在天空和大地上自由自在地飞翔......', 'char_179_cgbird/CN_003');
INSERT INTO `t_operator_voice` VALUES (2620, 78, '交谈3', '曾经，有人把我紧闭在空房间中。只有在治疗别人时，我才能见到其他人......', 'char_179_cgbird/CN_004');
INSERT INTO `t_operator_voice` VALUES (2621, 78, '晋升后交谈1', '只要握住这把法杖，我就能随心所欲地治愈伤痛......这，难道也是我原本就具备的能力吗？', 'char_179_cgbird/CN_005');
INSERT INTO `t_operator_voice` VALUES (2622, 78, '晋升后交谈2', '闪灵小姐告诉我，我们所背负着的，魔族挑起战争的罪恶，终有一天要被偿还。那些囚禁我的人也说过同样的话......可是......', 'char_179_cgbird/CN_006');
INSERT INTO `t_operator_voice` VALUES (2623, 78, '信赖提升后交谈1', '阿米娅小姐......每次看到她，我都会产生一种怀念的感觉......我曾在哪里，见过她？', 'char_179_cgbird/CN_007');
INSERT INTO `t_operator_voice` VALUES (2624, 78, '信赖提升后交谈2', '对我来说，记忆只是一些零散的碎片而已......同胞、源石、使命、反抗、战争......为什么，其中一枚碎片里，有您的身影呢？', 'char_179_cgbird/CN_008');
INSERT INTO `t_operator_voice` VALUES (2625, 78, '信赖提升后交谈3', '为什么我会是如今这副身姿呢？为什么我要去承受这迷乱的一切呢？博士，答案，是否就在您的手中呢？', 'char_179_cgbird/CN_009');
INSERT INTO `t_operator_voice` VALUES (2626, 78, '闲置', '......于睡梦中，您是否，也会成为一只自由的飞鸟呢？', 'char_179_cgbird/CN_010');
INSERT INTO `t_operator_voice` VALUES (2627, 78, '干员报到', '丽兹，这是我真正的名字。除此之外的事情，我都不记得了......', 'char_179_cgbird/CN_011');
INSERT INTO `t_operator_voice` VALUES (2628, 78, '观看作战记录', '为什么，要壮大我的力量呢。', 'char_179_cgbird/CN_012');
INSERT INTO `t_operator_voice` VALUES (2629, 78, '精英化晋升1', '晋升吗？我知道了，这一定是值得高兴的事吧。只是......非常抱歉，我完全没法体会，这种喜悦的感情......', 'char_179_cgbird/CN_013');
INSERT INTO `t_operator_voice` VALUES (2630, 78, '精英化晋升2', '谢谢博士亲自通知我晋升的事，只是......对于现在的我来说最重要的，是这只小鸟所诉说的外面的故事......', 'char_179_cgbird/CN_014');
INSERT INTO `t_operator_voice` VALUES (2631, 78, '编入队伍', '这里就是集合地点吗？', 'char_179_cgbird/CN_017');
INSERT INTO `t_operator_voice` VALUES (2632, 78, '任命队长', '如果这是您的命令，那么我一定会服从。', 'char_179_cgbird/CN_018');
INSERT INTO `t_operator_voice` VALUES (2633, 78, '行动出发', '按照说明手册操作就可以了吧？', 'char_179_cgbird/CN_019');
INSERT INTO `t_operator_voice` VALUES (2634, 78, '行动开始', '战场是......夺走生命的地方......', 'char_179_cgbird/CN_020');
INSERT INTO `t_operator_voice` VALUES (2635, 78, '选中干员1', '博士。', 'char_179_cgbird/CN_021');
INSERT INTO `t_operator_voice` VALUES (2636, 78, '选中干员2', '您要选择谁呢？', 'char_179_cgbird/CN_022');
INSERT INTO `t_operator_voice` VALUES (2637, 78, '部署1', '这就是......战场......', 'char_179_cgbird/CN_023');
INSERT INTO `t_operator_voice` VALUES (2638, 78, '部署2', '掠夺性命，非我之愿。', 'char_179_cgbird/CN_024');
INSERT INTO `t_operator_voice` VALUES (2639, 78, '作战中1', '请将我的力量，尽数收下。', 'char_179_cgbird/CN_025');
INSERT INTO `t_operator_voice` VALUES (2640, 78, '作战中2', '飞翔吧。', 'char_179_cgbird/CN_026');
INSERT INTO `t_operator_voice` VALUES (2641, 78, '作战中3', '你不会受伤。', 'char_179_cgbird/CN_027');
INSERT INTO `t_operator_voice` VALUES (2642, 78, '作战中4', '我将保护你。', 'char_179_cgbird/CN_028');
INSERT INTO `t_operator_voice` VALUES (2643, 78, '4星结束行动', '无论是怎样的战斗，我都......', 'char_179_cgbird/CN_029');
INSERT INTO `t_operator_voice` VALUES (2644, 78, '3星结束行动', '战斗，直至生命的尽头......这就是我们的宿命。', 'char_179_cgbird/CN_030');
INSERT INTO `t_operator_voice` VALUES (2645, 78, '非3星结束行动', '或许，他们已经逃离了命运的牢笼......', 'char_179_cgbird/CN_031');
INSERT INTO `t_operator_voice` VALUES (2646, 78, '行动失败', '这就是......绝望吗......', 'char_179_cgbird/CN_032');
INSERT INTO `t_operator_voice` VALUES (2647, 78, '进驻设施', '房间......也是一种牢笼。', 'char_179_cgbird/CN_033');
INSERT INTO `t_operator_voice` VALUES (2648, 78, '戳一下', '嗯，怎么了。', 'char_179_cgbird/CN_034');
INSERT INTO `t_operator_voice` VALUES (2649, 78, '信赖触摸', '博士，您现在在做的，就是所谓的亲密接触吗？', 'char_179_cgbird/CN_036');
INSERT INTO `t_operator_voice` VALUES (2650, 78, '标题', '明日方舟。', 'char_179_cgbird/CN_037');
INSERT INTO `t_operator_voice` VALUES (2651, 78, '问候', '您好，博士。', 'char_179_cgbird/CN_042');
INSERT INTO `t_operator_voice` VALUES (2652, 79, '任命助理', '博士请放心，有我在您就是安全的。', 'char_136_hsguma/CN_001');
INSERT INTO `t_operator_voice` VALUES (2653, 79, '交谈1', '最近来了很多新人。然而新面孔越多，您越要多加注意。', 'char_136_hsguma/CN_002');
INSERT INTO `t_operator_voice` VALUES (2654, 79, '交谈2', '无论发生什么情况，请别忘了紧急通讯频段，我会立刻前来处理。', 'char_136_hsguma/CN_003');
INSERT INTO `t_operator_voice` VALUES (2655, 79, '交谈3', '我说话冷冰冰的？毕竟是在工作中嘛。若在休息时间，我也不会那么正经的。', 'char_136_hsguma/CN_004');
INSERT INTO `t_operator_voice` VALUES (2656, 79, '晋升后交谈1', '罗德岛需要的不只是战斗干员。后勤、教育、事务交涉，无论哪个领域，都需要有专业人员支撑。', 'char_136_hsguma/CN_005');
INSERT INTO `t_operator_voice` VALUES (2657, 79, '晋升后交谈2', '以往要我出动必是恶战，而现在每场战斗都是恶战。', 'char_136_hsguma/CN_006');
INSERT INTO `t_operator_voice` VALUES (2658, 79, '信赖提升后交谈1', '这面盾叫做“般若”，是我从故乡带来的，它的“父亲”和家父是旧识。我在近卫局上任时，长官没少给我们白眼，但我和它都证明了自己——盾可以是武器，我也可以是。', 'char_136_hsguma/CN_007');
INSERT INTO `t_operator_voice` VALUES (2659, 79, '信赖提升后交谈2', '这面“般若”沾上了龙门各个帮派的血。本以为会有很多人记恨我，不知道怎么回事，原本蔑视我的人都变得对我毕恭毕敬了。可能是个子大的原因，对吧？', 'char_136_hsguma/CN_008');
INSERT INTO `t_operator_voice` VALUES (2660, 79, '信赖提升后交谈3', '呼，我再来倒一杯吧。什么？你怎么就不行了？别担心，就算你醉倒在地我也能把你送回房间的......更不安心了？哎呀，说话好歹要看看气氛哪。', 'char_136_hsguma/CN_009');
INSERT INTO `t_operator_voice` VALUES (2661, 79, '闲置', '嗯？没什么，我就是在这里守着你而已。', 'char_136_hsguma/CN_010');
INSERT INTO `t_operator_voice` VALUES (2662, 79, '干员报到', '星熊，重装干员，今后将是您的盾、您的利器、您的壁垒。请多多指教，博士。', 'char_136_hsguma/CN_011');
INSERT INTO `t_operator_voice` VALUES (2663, 79, '观看作战记录', '这都是博士您指导的好。', 'char_136_hsguma/CN_012');
INSERT INTO `t_operator_voice` VALUES (2664, 79, '精英化晋升1', '我的荣幸。', 'char_136_hsguma/CN_013');
INSERT INTO `t_operator_voice` VALUES (2665, 79, '精英化晋升2', '我要去哪里，没人能替我决定；但我做出决定，也没有人阻止得了。我准备好听从你的指挥了，博士。', 'char_136_hsguma/CN_014');
INSERT INTO `t_operator_voice` VALUES (2666, 79, '编入队伍', '我会保护大家。', 'char_136_hsguma/CN_017');
INSERT INTO `t_operator_voice` VALUES (2667, 79, '任命队长', '守护罗德岛也是我的责任。', 'char_136_hsguma/CN_018');
INSERT INTO `t_operator_voice` VALUES (2668, 79, '行动出发', '希望众人这次都能平安归来。', 'char_136_hsguma/CN_019');
INSERT INTO `t_operator_voice` VALUES (2669, 79, '行动开始', '既然有战斗的理由，就要有丧生的觉悟！', 'char_136_hsguma/CN_020');
INSERT INTO `t_operator_voice` VALUES (2670, 79, '选中干员1', '是，长官。', 'char_136_hsguma/CN_021');
INSERT INTO `t_operator_voice` VALUES (2671, 79, '选中干员2', '了解。', 'char_136_hsguma/CN_022');
INSERT INTO `t_operator_voice` VALUES (2672, 79, '部署1', '一步也不会退后。', 'char_136_hsguma/CN_023');
INSERT INTO `t_operator_voice` VALUES (2673, 79, '部署2', '一个都别想过去。', 'char_136_hsguma/CN_024');
INSERT INTO `t_operator_voice` VALUES (2674, 79, '作战中1', '般若会将你碾碎。', 'char_136_hsguma/CN_025');
INSERT INTO `t_operator_voice` VALUES (2675, 79, '作战中2', '想伤害我的朋友，先问问这面盾答不答应！', 'char_136_hsguma/CN_026');
INSERT INTO `t_operator_voice` VALUES (2676, 79, '作战中3', '百闻不如一见。', 'char_136_hsguma/CN_027');
INSERT INTO `t_operator_voice` VALUES (2677, 79, '作战中4', '般若啊，把你父亲铸造你时掺杂的怒火，全都喷发出来吧！', 'char_136_hsguma/CN_028');
INSERT INTO `t_operator_voice` VALUES (2678, 79, '4星结束行动', '登山者总会挑战更陡峭的山岩。', 'char_136_hsguma/CN_029');
INSERT INTO `t_operator_voice` VALUES (2679, 79, '3星结束行动', '在我面前伤害我的同伴，是我最不能容忍的事情！', 'char_136_hsguma/CN_030');
INSERT INTO `t_operator_voice` VALUES (2680, 79, '非3星结束行动', '受伤了吗，抱歉，我的失职。', 'char_136_hsguma/CN_031');
INSERT INTO `t_operator_voice` VALUES (2681, 79, '行动失败', '快点撤退，我来殿后！', 'char_136_hsguma/CN_032');
INSERT INTO `t_operator_voice` VALUES (2682, 79, '进驻设施', '我个子大，要进这屋子可有些麻烦。', 'char_136_hsguma/CN_033');
INSERT INTO `t_operator_voice` VALUES (2683, 79, '戳一下', '在。', 'char_136_hsguma/CN_034');
INSERT INTO `t_operator_voice` VALUES (2684, 79, '信赖触摸', '博士，现在不是工作时间了，休息的时候就该好好放松吧。', 'char_136_hsguma/CN_036');
INSERT INTO `t_operator_voice` VALUES (2685, 79, '标题', '明日方舟。', 'char_136_hsguma/CN_037');
INSERT INTO `t_operator_voice` VALUES (2686, 79, '问候', '哟，博士。', 'char_136_hsguma/CN_042');
INSERT INTO `t_operator_voice` VALUES (2687, 80, '任命助理', '我看过你的排班日程，现在确实是休息时间吧？那么，多有打扰。', 'char_202_demkni/CN_001');
INSERT INTO `t_operator_voice` VALUES (2688, 80, '交谈1', '万物的演化本就是不可动摇的自然法则。莱茵生命却妄图修改它，甚至想要支配它，多么愚蠢的图谋......', 'char_202_demkni/CN_002');
INSERT INTO `t_operator_voice` VALUES (2689, 80, '交谈2', '作为研究者，你是否也参与过一些不应触碰的实验？没有的话，自然最好。', 'char_202_demkni/CN_003');
INSERT INTO `t_operator_voice` VALUES (2690, 80, '交谈3', '你不觉得罗德岛太喧闹了吗？工作中就该保持安静、专注。你该严格管束你的部下。', 'char_202_demkni/CN_004');
INSERT INTO `t_operator_voice` VALUES (2691, 80, '晋升后交谈1', '我真正的能力，与急救这种行为并无关联。不过，为了配合你的战术指挥，我简单地调整了这个能力的使用方法。', 'char_202_demkni/CN_005');
INSERT INTO `t_operator_voice` VALUES (2692, 80, '晋升后交谈2', '你是不是以为，钙质化法术会让生物变得脆弱？错了，我可以将钙精炼成坚硬的珐琅质。就让敌人试试看吧，没人摧毁得了我的防壁。', 'char_202_demkni/CN_006');
INSERT INTO `t_operator_voice` VALUES (2693, 80, '信赖提升后交谈1', '你看到了我和赫默吵架？家常便饭而已......抱歉，给你和罗德岛带来了麻烦。', 'char_202_demkni/CN_007');
INSERT INTO `t_operator_voice` VALUES (2694, 80, '信赖提升后交谈2', '请你帮我转告伊芙利特，“无论今后发生什么，我都会保护你”......见面？不，我还没准备好去见她。', 'char_202_demkni/CN_008');
INSERT INTO `t_operator_voice` VALUES (2695, 80, '信赖提升后交谈3', '即使身处绝境，也要将自己傲然挺立的身姿，永远地刻在重要的人眼中。这是我的底线，也是我生存的准则。你也会这样去做的，对吗？', 'char_202_demkni/CN_009');
INSERT INTO `t_operator_voice` VALUES (2696, 80, '闲置', '如果没有其他任务需要处理，我就先一步返回自己的房间了。', 'char_202_demkni/CN_010');
INSERT INTO `t_operator_voice` VALUES (2697, 80, '干员报到', '塞雷娅，原莱茵生命研究所实验组成员。至于现在......为了让偏离传统的事物回归正途，我需要罗德岛的协助。', 'char_202_demkni/CN_011');
INSERT INTO `t_operator_voice` VALUES (2698, 80, '观看作战记录', '你在干什么？', 'char_202_demkni/CN_012');
INSERT INTO `t_operator_voice` VALUES (2699, 80, '精英化晋升1', '很好，如果想保持对下属的影响力，你必须时刻用自己的想法去推动他们。', 'char_202_demkni/CN_013');
INSERT INTO `t_operator_voice` VALUES (2700, 80, '精英化晋升2', '晋升？我并不希望这是种特殊对待。只是，为了那个孩子，我确实需要你的协助，直到那些被扭曲过的法则，全都被修正为止......！', 'char_202_demkni/CN_014');
INSERT INTO `t_operator_voice` VALUES (2701, 80, '编入队伍', '对于你的指挥，我有权进行监督，并提出相应意见。', 'char_202_demkni/CN_017');
INSERT INTO `t_operator_voice` VALUES (2702, 80, '任命队长', '把这次行动的简报，提交给我审核一下。', 'char_202_demkni/CN_018');
INSERT INTO `t_operator_voice` VALUES (2703, 80, '行动出发', '所有干员，必须严格遵守纪律。', 'char_202_demkni/CN_019');
INSERT INTO `t_operator_voice` VALUES (2704, 80, '行动开始', '就是他们制造了灾难，点燃了战争之火吗？', 'char_202_demkni/CN_020');
INSERT INTO `t_operator_voice` VALUES (2705, 80, '选中干员1', '让我来。', 'char_202_demkni/CN_021');
INSERT INTO `t_operator_voice` VALUES (2706, 80, '选中干员2', '压制他们。', 'char_202_demkni/CN_022');
INSERT INTO `t_operator_voice` VALUES (2707, 80, '部署1', '前进。', 'char_202_demkni/CN_023');
INSERT INTO `t_operator_voice` VALUES (2708, 80, '部署2', '害虫。', 'char_202_demkni/CN_024');
INSERT INTO `t_operator_voice` VALUES (2709, 80, '作战中1', '这种程度，又能阻止谁？', 'char_202_demkni/CN_025');
INSERT INTO `t_operator_voice` VALUES (2710, 80, '作战中2', '很快就会结束。', 'char_202_demkni/CN_026');
INSERT INTO `t_operator_voice` VALUES (2711, 80, '作战中3', '凝固吧。', 'char_202_demkni/CN_027');
INSERT INTO `t_operator_voice` VALUES (2712, 80, '作战中4', '不许放弃。', 'char_202_demkni/CN_028');
INSERT INTO `t_operator_voice` VALUES (2713, 80, '4星结束行动', '你的战术是现代的，构思却相当古老。你究竟是什么人？', 'char_202_demkni/CN_029');
INSERT INTO `t_operator_voice` VALUES (2714, 80, '3星结束行动', '一切必须回归既有的秩序，我不准许任何人违背法则。', 'char_202_demkni/CN_030');
INSERT INTO `t_operator_voice` VALUES (2715, 80, '非3星结束行动', '溃退的敌人也许正在呼叫援军，对残余敌人的搜索，不可怠慢。', 'char_202_demkni/CN_031');
INSERT INTO `t_operator_voice` VALUES (2716, 80, '行动失败', '究竟是哪里搞错了......？', 'char_202_demkni/CN_032');
INSERT INTO `t_operator_voice` VALUES (2717, 80, '进驻设施', '这个房间的布置很有意思，虽然，我个人更喜欢未来主义的设计。', 'char_202_demkni/CN_033');
INSERT INTO `t_operator_voice` VALUES (2718, 80, '戳一下', '嗯？什么？', 'char_202_demkni/CN_034');
INSERT INTO `t_operator_voice` VALUES (2719, 80, '信赖触摸', '博士，有时间的话去补习一些过往的知识吧，你会在其中，发现许多有趣的事。', 'char_202_demkni/CN_036');
INSERT INTO `t_operator_voice` VALUES (2720, 80, '标题', '明日方舟。', 'char_202_demkni/CN_037');
INSERT INTO `t_operator_voice` VALUES (2721, 80, '问候', '博士，身体如何？', 'char_202_demkni/CN_042');
INSERT INTO `t_operator_voice` VALUES (2722, 81, '任命助理', '消磨时间尚有更好的方法。想不想尝试一下？', 'char_172_svrash/CN_001');
INSERT INTO `t_operator_voice` VALUES (2723, 81, '交谈1', '总是这副心事重重的样子可不行，看见我应当微笑啊......好，就是这样。毕竟我是能帮你解决难题的最好人选，不是吗。', 'char_172_svrash/CN_002');
INSERT INTO `t_operator_voice` VALUES (2724, 81, '交谈2', '那个孩子，是叫阿米娅吗......了不起。若是现在不给她装上枷锁，就要有在未来为她戴上王冠的觉悟。', 'char_172_svrash/CN_003');
INSERT INTO `t_operator_voice` VALUES (2725, 81, '交谈3', '喀兰贸易公司与罗德岛之联盟的稳固程度，完全取决于你我的想法。看来还是要好好相处呀，我的盟友？', 'char_172_svrash/CN_004');
INSERT INTO `t_operator_voice` VALUES (2726, 81, '晋升后交谈1', '这份合约，我可以签署。没错，我确实看到其中每条条款都对你和罗德岛有利。', 'char_172_svrash/CN_005');
INSERT INTO `t_operator_voice` VALUES (2727, 81, '晋升后交谈2', '我当然明白这合约是对我的戏弄，可是这又如何呢，银灰愿意签署这份不平等的合同——因为你值得银灰这么做。', 'char_172_svrash/CN_006');
INSERT INTO `t_operator_voice` VALUES (2728, 81, '信赖提升后交谈1', '恩希亚是否给你添了麻烦？抱歉......保护她、引导她本应是我的责任。仅此一事，今日的我无能为力；也仅此一事，我想请{@nickname}你代为完成。', 'char_172_svrash/CN_007');
INSERT INTO `t_operator_voice` VALUES (2729, 81, '信赖提升后交谈2', '可否引我去罗德岛的甲板？不，就是眺望眺望风景。太久没有展翅，丹增这小家伙，想必也很怀念荒野的气息吧。', 'char_172_svrash/CN_008');
INSERT INTO `t_operator_voice` VALUES (2730, 81, '信赖提升后交谈3', '这样发展下去，你我互相博弈的那一天，迟早会到来。但是我，看上去很高兴？也许吧，毕竟我很期待和你交手，也会珍惜那一天到来前的时光。', 'char_172_svrash/CN_009');
INSERT INTO `t_operator_voice` VALUES (2731, 81, '闲置', '“——天空似乎暗了下来，要下雪了——”', 'char_172_svrash/CN_010');
INSERT INTO `t_operator_voice` VALUES (2732, 81, '干员报到', '银灰，你的盟友，前来助力。你不会让我失望的，对吗。', 'char_172_svrash/CN_011');
INSERT INTO `t_operator_voice` VALUES (2733, 81, '观看作战记录', '路途凶险，经验是必不可少的。', 'char_172_svrash/CN_012');
INSERT INTO `t_operator_voice` VALUES (2734, 81, '精英化晋升1', '虚衔虽不嫌多，但你也知道，我更看重其中有几分是信任。', 'char_172_svrash/CN_013');
INSERT INTO `t_operator_voice` VALUES (2735, 81, '精英化晋升2', '竟然邀请我参与战斗，你确实很有胆识——作为回敬，就让罗德岛见识下银灰的手段——！', 'char_172_svrash/CN_014');
INSERT INTO `t_operator_voice` VALUES (2736, 81, '编入队伍', '客随主便，按你的想法安排就好。', 'char_172_svrash/CN_017');
INSERT INTO `t_operator_voice` VALUES (2737, 81, '任命队长', '要我辅佐你进行指挥？可以，不过也要考虑下代价。', 'char_172_svrash/CN_018');
INSERT INTO `t_operator_voice` VALUES (2738, 81, '行动出发', '战场之上，善良是无法拯救他人的。', 'char_172_svrash/CN_019');
INSERT INTO `t_operator_voice` VALUES (2739, 81, '行动开始', '拿出你们全部的实力——这样至少能让我尽兴。', 'char_172_svrash/CN_020');
INSERT INTO `t_operator_voice` VALUES (2740, 81, '选中干员1', '有什么想法？', 'char_172_svrash/CN_021');
INSERT INTO `t_operator_voice` VALUES (2741, 81, '选中干员2', '决定了？', 'char_172_svrash/CN_022');
INSERT INTO `t_operator_voice` VALUES (2742, 81, '部署1', '我接受。', 'char_172_svrash/CN_023');
INSERT INTO `t_operator_voice` VALUES (2743, 81, '部署2', '可以。', 'char_172_svrash/CN_024');
INSERT INTO `t_operator_voice` VALUES (2744, 81, '作战中1', '“当他们耀武扬威时，无人知晓......”', 'char_172_svrash/CN_025');
INSERT INTO `t_operator_voice` VALUES (2745, 81, '作战中2', '“你为何来到这里——”', 'char_172_svrash/CN_026');
INSERT INTO `t_operator_voice` VALUES (2746, 81, '作战中3', '“我的仇敌，我的玩物？”', 'char_172_svrash/CN_027');
INSERT INTO `t_operator_voice` VALUES (2747, 81, '作战中4', '“......当他们哀嚎求饶时，无人应答。”', 'char_172_svrash/CN_028');
INSERT INTO `t_operator_voice` VALUES (2748, 81, '4星结束行动', '战斗越艰难，胜利的喜悦越炽热。', 'char_172_svrash/CN_029');
INSERT INTO `t_operator_voice` VALUES (2749, 81, '3星结束行动', '不错的战斗，即使是我都能感到心潮澎湃。', 'char_172_svrash/CN_030');
INSERT INTO `t_operator_voice` VALUES (2750, 81, '非3星结束行动', '你应该有更高的目标。', 'char_172_svrash/CN_031');
INSERT INTO `t_operator_voice` VALUES (2751, 81, '行动失败', '立刻撤退。现在还来得及。', 'char_172_svrash/CN_032');
INSERT INTO `t_operator_voice` VALUES (2752, 81, '进驻设施', '作为落脚之处而言，挺不错。', 'char_172_svrash/CN_033');
INSERT INTO `t_operator_voice` VALUES (2753, 81, '戳一下', '嗯？', 'char_172_svrash/CN_034');
INSERT INTO `t_operator_voice` VALUES (2754, 81, '信赖触摸', '过于疲劳了，抱歉。让我稍微休息一下。', 'char_172_svrash/CN_036');
INSERT INTO `t_operator_voice` VALUES (2755, 81, '标题', '明日方舟。', 'char_172_svrash/CN_037');
INSERT INTO `t_operator_voice` VALUES (2756, 81, '问候', '没什么变化啊，我的盟友。', 'char_172_svrash/CN_042');
INSERT INTO `t_operator_voice` VALUES (2757, 82, '任命助理', '嘿嘿！Dr.{@nickname}，今天又需要我来帮忙做些什么呢？', 'char_230_savage/CN_001');
INSERT INTO `t_operator_voice` VALUES (2758, 82, '交谈1', '平时的工作基本都是些作战任务，砸坏这砸坏那的......偶尔像这样整理整理文件，感觉也不错。', 'char_230_savage/CN_002');
INSERT INTO `t_operator_voice` VALUES (2759, 82, '交谈2', '我在雷姆必拓时也会负责些其它工作，像是矿物的开采啦，用电磁法进行地质勘探啦，嗯......很难理解吧，简单点说，就是把阻碍砸碎，然后往下挖！', 'char_230_savage/CN_003');
INSERT INTO `t_operator_voice` VALUES (2760, 82, '交谈3', '阿米娅身上的担子可是很重的，要是我能帮她分担些就好了......博士，你也是，有什么需要我的地方，一定要说哦！', 'char_230_savage/CN_004');
INSERT INTO `t_operator_voice` VALUES (2761, 82, '晋升后交谈1', '提供掩护、清空路线、控制战局——即使不能像各位重装干员一样化作队员们的屏障，我也能用我自己的方式保护大家！', 'char_230_savage/CN_005');
INSERT INTO `t_operator_voice` VALUES (2762, 82, '晋升后交谈2', '我......我保护好大家了吗？真是太好了......以后也请让我一直一直，待在博士和阿米娅妹妹的身边吧。', 'char_230_savage/CN_006');
INSERT INTO `t_operator_voice` VALUES (2763, 82, '信赖提升后交谈1', '博士，想吃卷心菜蔬果沙拉配蘑菇馅饼吗？嗯嗯，我做了三份哦，你的我的，还有阿米娅妹妹的。以前，阿米娅妹妹可是很喜欢吃我做的馅饼的！', 'char_230_savage/CN_007');
INSERT INTO `t_operator_voice` VALUES (2764, 82, '信赖提升后交谈2', '想想橄榄球场上的跑卫，一往无前，突破敌阵，为队友开辟前进的道路！然后倒下，将前方交给队友！！这，这，这难道不是跟我的工作一模一样吗！太帅了吧！虽然不包含最后的倒下环节就是了。', 'char_230_savage/CN_008');
INSERT INTO `t_operator_voice` VALUES (2765, 82, '信赖提升后交谈3', '只要能这样守在你和阿米娅身边，对我来说就足够了。记忆只是过去的一部分，而未来却是无限宽广的。我与阿米娅妹妹，以后也会和博士你一起，创造出更多的回忆。', 'char_230_savage/CN_009');
INSERT INTO `t_operator_voice` VALUES (2766, 82, '闲置', '博士如果没什么事的话......不如跟我一起去购置晚饭材料吧!', 'char_230_savage/CN_010');
INSERT INTO `t_operator_voice` VALUES (2767, 82, '干员报到', 'Dr.{@nickname}，来自雷姆必拓的干员暴行向你报道！我一得到你和阿米娅的消息，就立刻赶过来帮忙啦！什么？你什么都不记得？唉唉？！', 'char_230_savage/CN_011');
INSERT INTO `t_operator_voice` VALUES (2768, 82, '观看作战记录', '哦哦！原来如此原来如此......嗯嗯！', 'char_230_savage/CN_012');
INSERT INTO `t_operator_voice` VALUES (2769, 82, '精英化晋升1', '由你来指挥的话，我很放心哦。谢谢博士，我不会辜负你的期望的！', 'char_230_savage/CN_013');
INSERT INTO `t_operator_voice` VALUES (2770, 82, '精英化晋升2', '别担心，博士。再多敌人也无所谓——只要Dr.{@nickname}你还在我身后，我就会把面前所有的困难，全都击碎。', 'char_230_savage/CN_014');
INSERT INTO `t_operator_voice` VALUES (2771, 82, '编入队伍', '暴行，就位！', 'char_230_savage/CN_017');
INSERT INTO `t_operator_voice` VALUES (2772, 82, '任命队长', '大家好好跟上我吧！', 'char_230_savage/CN_018');
INSERT INTO `t_operator_voice` VALUES (2773, 82, '行动出发', '突击开始！', 'char_230_savage/CN_019');
INSERT INTO `t_operator_voice` VALUES (2774, 82, '行动开始', '目标位置已确定！突击！！', 'char_230_savage/CN_020');
INSERT INTO `t_operator_voice` VALUES (2775, 82, '选中干员1', '我准备好了！', 'char_230_savage/CN_021');
INSERT INTO `t_operator_voice` VALUES (2776, 82, '选中干员2', '下一步是......？', 'char_230_savage/CN_022');
INSERT INTO `t_operator_voice` VALUES (2777, 82, '部署1', '交给我了！', 'char_230_savage/CN_023');
INSERT INTO `t_operator_voice` VALUES (2778, 82, '部署2', '这个敌人是我的！', 'char_230_savage/CN_024');
INSERT INTO `t_operator_voice` VALUES (2779, 82, '作战中1', '吼啊——！', 'char_230_savage/CN_025');
INSERT INTO `t_operator_voice` VALUES (2780, 82, '作战中2', 'Mjolnir-3出力全开!', 'char_230_savage/CN_026');
INSERT INTO `t_operator_voice` VALUES (2781, 82, '作战中3', '简单!不是什么问题！', 'char_230_savage/CN_027');
INSERT INTO `t_operator_voice` VALUES (2782, 82, '作战中4', '准备完毕!', 'char_230_savage/CN_028');
INSERT INTO `t_operator_voice` VALUES (2783, 82, '4星结束行动', '哼哼，看来不管是怎样困难的任务都难不倒我们！', 'char_230_savage/CN_029');
INSERT INTO `t_operator_voice` VALUES (2784, 82, '3星结束行动', '啊？这就结束了？简单简单!', 'char_230_savage/CN_030');
INSERT INTO `t_operator_voice` VALUES (2785, 82, '非3星结束行动', '......也算发挥的勉强及格吧。', 'char_230_savage/CN_031');
INSERT INTO `t_operator_voice` VALUES (2786, 82, '行动失败', '前面太危险了！还是先从这里撤退......！', 'char_230_savage/CN_032');
INSERT INTO `t_operator_voice` VALUES (2787, 82, '进驻设施', '......欸！这样的细致的工作交给我没关系吗？', 'char_230_savage/CN_033');
INSERT INTO `t_operator_voice` VALUES (2788, 82, '戳一下', '嗯？嗯嗯嗯 ？', 'char_230_savage/CN_034');
INSERT INTO `t_operator_voice` VALUES (2789, 82, '信赖触摸', '今天的工作也顺利地完成了！', 'char_230_savage/CN_036');
INSERT INTO `t_operator_voice` VALUES (2790, 82, '标题', '明日方舟。', 'char_230_savage/CN_037');
INSERT INTO `t_operator_voice` VALUES (2791, 82, '问候', '呀嗬~！博士早上好啊~！', 'char_230_savage/CN_042');
INSERT INTO `t_operator_voice` VALUES (2792, 83, '任命助理', '今天的工作可真不少啊。所以，博士，要不要先玩玩游戏？最好的工作准备，难道不正是一份放松的心情吗？', 'char_282_catap/CN_001');
INSERT INTO `t_operator_voice` VALUES (2793, 83, '交谈1', '唔，总觉得好没劲，是不是因为月见夜不在啊？每当我看到他笑容闪耀，向着女生们搭讪再遭拒绝的场面，这一整天的心情，可就好的不得了~', 'char_282_catap/CN_002');
INSERT INTO `t_operator_voice` VALUES (2794, 83, '交谈2', '博士，你知道我们小组里谁最可靠吗？猜不出来吧？是斑点哦。别看他不常说话，做起事可是非常靠谱的。所以~如果干活的话，为什么不把他叫过来呢？现在只有我们两人，多累啊~', 'char_282_catap/CN_003');
INSERT INTO `t_operator_voice` VALUES (2795, 83, '交谈3', '哎~又被梓兰姐训了一顿......她也太爱操心了吧？一定是工作压力太大了~她还是得学学我，我可是最擅长寻找工作和休息中的平衡点的那种人~！', 'char_282_catap/CN_004');
INSERT INTO `t_operator_voice` VALUES (2796, 83, '晋升后交谈1', '嗯？杜宾教官，今天是不是不在？出差去了？......哼哼，终于！终于又能轻轻松松地过一天了！太棒了！博士我马上要去买点零食，你想吃薯片还是爆米花？让我帮你带一份！', 'char_282_catap/CN_005');
INSERT INTO `t_operator_voice` VALUES (2797, 83, '信赖提升后交谈1', '听，听我说小泡普卡，吵架什么的，我不会再干了！放、放下电锯吧！......惨了惨了，她暴走了啊！这、这可完蛋了，快跑博士，从窗户跳下去！别管了，快和我一起跳！', 'char_282_catap/CN_007');
INSERT INTO `t_operator_voice` VALUES (2798, 83, '信赖提升后交谈2', '我们当时在的那个小城啊，城镇里的大伙都被城主逼得活不下去，我也很讨厌那个脑满肠肥的家伙，就把他的金库炸了，把钱发给大家自谋生计去。想了想，还真是很痛快的经历嘛~', 'char_282_catap/CN_008');
INSERT INTO `t_operator_voice` VALUES (2799, 83, '信赖提升后交谈3', '别人怎么评价我我可不在乎。我只在乎自己每天过的舒不舒服，是不是在做自己乐意做的事。人生是自己的，就应该由自己去掌握，难道不是吗？', 'char_282_catap/CN_009');
INSERT INTO `t_operator_voice` VALUES (2800, 83, '闲置', '博士睡着了？嚯~这时候可不能打扰博士休息。所以，我就先出门走走吧~', 'char_282_catap/CN_010');
INSERT INTO `t_operator_voice` VALUES (2801, 83, '干员报到', '行动预备组A6，空爆，前来报道！哎呀~我一眼就能看出来，你和那个死板的，啊不，令人尊敬的教官杜宾小姐，可是一点都不一样，对吧？', 'char_282_catap/CN_011');
INSERT INTO `t_operator_voice` VALUES (2802, 83, '观看作战记录', '......唔唔，这一觉睡的可真香啊～', 'char_282_catap/CN_012');
INSERT INTO `t_operator_voice` VALUES (2803, 83, '精英化晋升1', '哼哼，总算要和新人训练营告别了......什么什么？新进干员晋升之后还是由杜宾小姐管理？你骗我的吧——？！', 'char_282_catap/CN_013');
INSERT INTO `t_operator_voice` VALUES (2804, 83, '编入队伍', '还没到出发时间吧？那我再去逛会儿~', 'char_282_catap/CN_017');
INSERT INTO `t_operator_voice` VALUES (2805, 83, '任命队长', '任命队长？哈~我等这句话好久了！', 'char_282_catap/CN_018');
INSERT INTO `t_operator_voice` VALUES (2806, 83, '行动出发', '唔？弹链被我放到哪去了？', 'char_282_catap/CN_019');
INSERT INTO `t_operator_voice` VALUES (2807, 83, '行动开始', '唉，我还是很讨厌战争啊，可也没办法。', 'char_282_catap/CN_020');
INSERT INTO `t_operator_voice` VALUES (2808, 83, '选中干员1', '你说~', 'char_282_catap/CN_021');
INSERT INTO `t_operator_voice` VALUES (2809, 83, '选中干员2', '我一定听指挥~', 'char_282_catap/CN_022');
INSERT INTO `t_operator_voice` VALUES (2810, 83, '部署1', '安全着陆！', 'char_282_catap/CN_023');
INSERT INTO `t_operator_voice` VALUES (2811, 83, '部署2', '开工了！', 'char_282_catap/CN_024');
INSERT INTO `t_operator_voice` VALUES (2812, 83, '作战中1', '就用这一发子弹，看准了！', 'char_282_catap/CN_025');
INSERT INTO `t_operator_voice` VALUES (2813, 83, '作战中2', '你们全都给我飞上天！', 'char_282_catap/CN_026');
INSERT INTO `t_operator_voice` VALUES (2814, 83, '4星结束行动', '刚才那个击破敌人的战术，布置得可真是漂亮啊！', 'char_282_catap/CN_029');
INSERT INTO `t_operator_voice` VALUES (2815, 83, '3星结束行动', '我就是喜欢能带我们打胜仗的指挥官，就像博士这样的！', 'char_282_catap/CN_030');
INSERT INTO `t_operator_voice` VALUES (2816, 83, '非3星结束行动', '放心~我早就在敌人的撤退路线上设陷阱了~欸？一个也没被踩中？！', 'char_282_catap/CN_031');
INSERT INTO `t_operator_voice` VALUES (2817, 83, '行动失败', '唉......！别磨蹭啦，逃吧！', 'char_282_catap/CN_032');
INSERT INTO `t_operator_voice` VALUES (2818, 83, '进驻设施', '嘿~帮我看下，杜宾教官，在不在这个房间里面？', 'char_282_catap/CN_033');
INSERT INTO `t_operator_voice` VALUES (2819, 83, '戳一下', '我跑！跑不掉啊......', 'char_282_catap/CN_034');
INSERT INTO `t_operator_voice` VALUES (2820, 83, '信赖触摸', '博士，是一位好长官！', 'char_282_catap/CN_036');
INSERT INTO `t_operator_voice` VALUES (2821, 83, '标题', '明日方舟。', 'char_282_catap/CN_037');
INSERT INTO `t_operator_voice` VALUES (2822, 83, '问候', '博士~我在这儿呢！', 'char_282_catap/CN_042');
INSERT INTO `t_operator_voice` VALUES (2823, 84, '任命助理', '感谢您的指名。今晚，有什么想对我说的吗？', 'char_283_midn/CN_001');
INSERT INTO `t_operator_voice` VALUES (2824, 84, '交谈1', '东夜魔王？您知道了啊...不过对我来说，以前的辉煌都算不了什么，毕竟，我的未来只会更加的耀眼。', 'char_283_midn/CN_002');
INSERT INTO `t_operator_voice` VALUES (2825, 84, '交谈2', '虽然工作的地点变了，但我要给各位带来笑容的职责却没有变过。', 'char_283_midn/CN_003');
INSERT INTO `t_operator_voice` VALUES (2826, 84, '交谈3', '梓兰小姐的幸福初始计量表一定是负数吧，不然怎么可能见到我都板着个脸啊？说来，您要是知道点她的什么事记得给我讲讲。', 'char_283_midn/CN_004');
INSERT INTO `t_operator_voice` VALUES (2827, 84, '晋升后交谈1', '我可是很强的，剑术这项优雅的运动，既能维持身材又能提升气质，有什么理由不勤加练习呢？喂，再强的人不努力也会变弱的！先搞清楚这点啊！', 'char_283_midn/CN_005');
INSERT INTO `t_operator_voice` VALUES (2828, 84, '信赖提升后交谈1', '人们说我以前的行当是把感情当做商品贩卖，“幸福”只是叫卖的吆喝。真是愚钝，若不付出真心，又怎么能看见客人的真心？', 'char_283_midn/CN_007');
INSERT INTO `t_operator_voice` VALUES (2829, 84, '信赖提升后交谈2', '我认为幸福是相互影响的，是一种真实存在的气场。我以得见大家真心而收获幸福，大家就一定能因我的幸福而幸福。', 'char_283_midn/CN_008');
INSERT INTO `t_operator_voice` VALUES (2830, 84, '信赖提升后交谈3', '现在我在A6小组的同事......他们的性格古怪，心地却无比善良。这是命运的馈赠，提醒我要珍惜真正重要的人。', 'char_283_midn/CN_009');
INSERT INTO `t_operator_voice` VALUES (2831, 84, '闲置', '既然闲下来了，就先看看书好了。', 'char_283_midn/CN_010');
INSERT INTO `t_operator_voice` VALUES (2832, 84, '干员报到', '请露出你最美的笑容来，客人，在我的身边，幸福乃理应获得之物。月见夜，今后也随时恭候您的指名。', 'char_283_midn/CN_011');
INSERT INTO `t_operator_voice` VALUES (2833, 84, '观看作战记录', '增长见闻，这点很重要！', 'char_283_midn/CN_012');
INSERT INTO `t_operator_voice` VALUES (2834, 84, '精英化晋升1', '喔噢，我的努力也有了回报了啊！为表庆祝，就摆上个香槟塔吧！当然，这次我请。', 'char_283_midn/CN_013');
INSERT INTO `t_operator_voice` VALUES (2835, 84, '编入队伍', '我就是成功的保障。', 'char_283_midn/CN_017');
INSERT INTO `t_operator_voice` VALUES (2836, 84, '任命队长', '跟随帅气的我吧！', 'char_283_midn/CN_018');
INSERT INTO `t_operator_voice` VALUES (2837, 84, '行动出发', '大家都拿出干劲来！', 'char_283_midn/CN_019');
INSERT INTO `t_operator_voice` VALUES (2838, 84, '行动开始', '好好看着我华丽的身姿。', 'char_283_midn/CN_020');
INSERT INTO `t_operator_voice` VALUES (2839, 84, '选中干员1', '嗯？', 'char_283_midn/CN_021');
INSERT INTO `t_operator_voice` VALUES (2840, 84, '选中干员2', '怎么了？', 'char_283_midn/CN_022');
INSERT INTO `t_operator_voice` VALUES (2841, 84, '部署1', '到达。', 'char_283_midn/CN_023');
INSERT INTO `t_operator_voice` VALUES (2842, 84, '部署2', '好的！', 'char_283_midn/CN_024');
INSERT INTO `t_operator_voice` VALUES (2843, 84, '作战中1', '剑啊，划破这月夜吧！', 'char_283_midn/CN_025');
INSERT INTO `t_operator_voice` VALUES (2844, 84, '作战中2', '渴饮鲜血吧！', 'char_283_midn/CN_026');
INSERT INTO `t_operator_voice` VALUES (2845, 84, '4星结束行动', '果然不能小瞧你啊，Dr.{@nickname}，您今天的闪耀程度已经快超越我了。', 'char_283_midn/CN_029');
INSERT INTO `t_operator_voice` VALUES (2846, 84, '3星结束行动', '这世间对于我来说只有一件事办不到，那就是——“不完美”。', 'char_283_midn/CN_030');
INSERT INTO `t_operator_voice` VALUES (2847, 84, '非3星结束行动', '这样也不坏，总之先庆祝一下吧！', 'char_283_midn/CN_031');
INSERT INTO `t_operator_voice` VALUES (2848, 84, '行动失败', '别回头。等到下次，再用笑容来面对彼此吧。', 'char_283_midn/CN_032');
INSERT INTO `t_operator_voice` VALUES (2849, 84, '进驻设施', '很棒的地方，晚上办场酒会吧。', 'char_283_midn/CN_033');
INSERT INTO `t_operator_voice` VALUES (2850, 84, '戳一下', '喂......衣领要被弄乱了。', 'char_283_midn/CN_034');
INSERT INTO `t_operator_voice` VALUES (2851, 84, '信赖触摸', '感谢您对我的照顾。', 'char_283_midn/CN_036');
INSERT INTO `t_operator_voice` VALUES (2852, 84, '标题', '明日方舟。', 'char_283_midn/CN_037');
INSERT INTO `t_operator_voice` VALUES (2853, 84, '问候', 'Dr.{@nickname}，晚上好。', 'char_283_midn/CN_042');
INSERT INTO `t_operator_voice` VALUES (2854, 85, '任命助理', '老板，我们去吃饭吧，这回轮到老板请客！欸，上次也是？不记得不记得啦。', 'char_137_brownb/CN_001');
INSERT INTO `t_operator_voice` VALUES (2855, 85, '交谈1', '以理服人？打不过的话就锻炼到打得过为止，是这个道理，我懂！', 'char_137_brownb/CN_002');
INSERT INTO `t_operator_voice` VALUES (2856, 85, '交谈2', '蜂蜜真好喝，但收集起来真是麻烦。前几天去采蜜的时候啊......我被蛰得不轻啊。', 'char_137_brownb/CN_003');
INSERT INTO `t_operator_voice` VALUES (2857, 85, '交谈3', '这对指虎？这可是我的宝贝。我的全胜战绩，可全是靠它打出来的！', 'char_137_brownb/CN_004');
INSERT INTO `t_operator_voice` VALUES (2858, 85, '晋升后交谈1', '地下格斗的规则，可比你知道的复杂多了。好多搞不清楚规则就参赛的人，都把命给丢了哦。', 'char_137_brownb/CN_005');
INSERT INTO `t_operator_voice` VALUES (2859, 85, '晋升后交谈2', '矿石病？那个怎么样都无所谓啦。管他是感染者还是别的什么人，只要一拳打昏，谁来还不都是一样嘛~', 'char_137_brownb/CN_006');
INSERT INTO `t_operator_voice` VALUES (2860, 85, '信赖提升后交谈1', '啊？厨房里那条鱼？哈，这会儿已经在我的胃里了！', 'char_137_brownb/CN_007');
INSERT INTO `t_operator_voice` VALUES (2861, 85, '信赖提升后交谈2', '欸？为什么要翻这份名册？哼哼，老板这就不懂了吧，不给自己增加点熟人，回头不就没有打架的对手了吗？', 'char_137_brownb/CN_008');
INSERT INTO `t_operator_voice` VALUES (2862, 85, '信赖提升后交谈3', '以前，我其实就是为了吃上饭才去打比赛的。但现在，在我来到罗德岛之后，这种烦恼就再也没有过了。真的很感谢你，老板！', 'char_137_brownb/CN_009');
INSERT INTO `t_operator_voice` VALUES (2863, 85, '闲置', '有烤鱼的味道！', 'char_137_brownb/CN_010');
INSERT INTO `t_operator_voice` VALUES (2864, 85, '干员报到', '哟！老板您眼光不错嘛！世上最强的地下拳手，就是我猎蜂！', 'char_137_brownb/CN_011');
INSERT INTO `t_operator_voice` VALUES (2865, 85, '观看作战记录', '学习对我来说太难了，还是放我出去打一架吧~', 'char_137_brownb/CN_012');
INSERT INTO `t_operator_voice` VALUES (2866, 85, '精英化晋升1', '哈哈，看来你还是蛮中意我的！', 'char_137_brownb/CN_013');
INSERT INTO `t_operator_voice` VALUES (2867, 85, '精英化晋升2', '你这么提拔我，我又怎么能辜负你呢？就等我到了战场上再大显身手吧，老板！', 'char_137_brownb/CN_014');
INSERT INTO `t_operator_voice` VALUES (2868, 85, '编入队伍', '我来了！', 'char_137_brownb/CN_017');
INSERT INTO `t_operator_voice` VALUES (2869, 85, '任命队长', '好！打赢之后我请各位吃饭！', 'char_137_brownb/CN_018');
INSERT INTO `t_operator_voice` VALUES (2870, 85, '行动出发', '没人能在我拳下挺过三回合！', 'char_137_brownb/CN_019');
INSERT INTO `t_operator_voice` VALUES (2871, 85, '行动开始', '想被我痛揍的，就是你们这帮家伙吗？', 'char_137_brownb/CN_020');
INSERT INTO `t_operator_voice` VALUES (2872, 85, '选中干员1', '咋了？', 'char_137_brownb/CN_021');
INSERT INTO `t_operator_voice` VALUES (2873, 85, '选中干员2', '要上吗？', 'char_137_brownb/CN_022');
INSERT INTO `t_operator_voice` VALUES (2874, 85, '部署1', '好叻。', 'char_137_brownb/CN_023');
INSERT INTO `t_operator_voice` VALUES (2875, 85, '部署2', '好，就这么办。', 'char_137_brownb/CN_024');
INSERT INTO `t_operator_voice` VALUES (2876, 85, '作战中1', '吃我一拳！', 'char_137_brownb/CN_025');
INSERT INTO `t_operator_voice` VALUES (2877, 85, '作战中2', '别想跑！', 'char_137_brownb/CN_026');
INSERT INTO `t_operator_voice` VALUES (2878, 85, '作战中3', '打打打打打！', 'char_137_brownb/CN_027');
INSERT INTO `t_operator_voice` VALUES (2879, 85, '作战中4', '来啊来啊，还有谁！', 'char_137_brownb/CN_028');
INSERT INTO `t_operator_voice` VALUES (2880, 85, '4星结束行动', '对手越强，我的斗志就越旺！', 'char_137_brownb/CN_029');
INSERT INTO `t_operator_voice` VALUES (2881, 85, '3星结束行动', '全撂倒了，老板，来看看我这辉煌的战果吧！', 'char_137_brownb/CN_030');
INSERT INTO `t_operator_voice` VALUES (2882, 85, '非3星结束行动', '收工啦，大家回去好好吃一顿！', 'char_137_brownb/CN_031');
INSERT INTO `t_operator_voice` VALUES (2883, 85, '行动失败', '真疼......老板呢？没事吧？', 'char_137_brownb/CN_032');
INSERT INTO `t_operator_voice` VALUES (2884, 85, '进驻设施', '这个房间比我以前住过的都要好！', 'char_137_brownb/CN_033');
INSERT INTO `t_operator_voice` VALUES (2885, 85, '戳一下', '嗯嗯？', 'char_137_brownb/CN_034');
INSERT INTO `t_operator_voice` VALUES (2886, 85, '信赖触摸', '有好吃的给我吗？太棒了，老板！', 'char_137_brownb/CN_036');
INSERT INTO `t_operator_voice` VALUES (2887, 85, '标题', '明日方舟。', 'char_137_brownb/CN_037');
INSERT INTO `t_operator_voice` VALUES (2888, 85, '问候', '早啊老板。', 'char_137_brownb/CN_042');
INSERT INTO `t_operator_voice` VALUES (2889, 86, '任命助理', '啊，光线好刺眼......博士，能稍微把办公室弄得暗一点吗？我不太适应明亮的房间......', 'char_164_nightm/CN_001');
INSERT INTO `t_operator_voice` VALUES (2890, 86, '交谈1', '那个，把我带到这里，真的是打算让我成为干员的吗？不是为了监视我？那位叫凯尔希的医生小姐，又是想对我做什么呢......？', 'char_164_nightm/CN_002');
INSERT INTO `t_operator_voice` VALUES (2891, 86, '交谈2', '这把法杖是父亲的遗物。用它释放的法术，好像是能让别人体验到“永夜”的幻觉。被这种幻觉囚禁的人......第二天，会因为贪睡没法起床......', 'char_164_nightm/CN_003');
INSERT INTO `t_operator_voice` VALUES (2892, 86, '交谈3', '我已经很习惯一个人独处啦，如果实在太寂寞，我就和自己说说话，嘿嘿......咦？这挺奇怪的吗？', 'char_164_nightm/CN_004');
INSERT INTO `t_operator_voice` VALUES (2893, 86, '晋升后交谈1', '仔细看我的眼睛。越来越困倦了，对吧？呵呵......那么，能不能告诉我，罗德岛的人，都把“源石”储存在什么地方呢？', 'char_164_nightm/CN_005');
INSERT INTO `t_operator_voice` VALUES (2894, 86, '晋升后交谈2', '您叫我么？呵呵，是啊，我的病情越来越严重了。嗯，都是因为您拒绝把“源石”交给我，所以这具身体也开始一点点地崩溃了......所以，究竟该怎么办呢，博士？', 'char_164_nightm/CN_006');
INSERT INTO `t_operator_voice` VALUES (2895, 86, '信赖提升后交谈1', '那个，先让我给您道个歉！就是、那个，也、也许我以后还会忽然说些奇怪的话，请您不要讨厌我.....【那个她】，我自己也没有办法控制......', 'char_164_nightm/CN_007');
INSERT INTO `t_operator_voice` VALUES (2896, 86, '信赖提升后交谈2', '我说，博士，如果我在这里就把你干掉，大家会露出什么样的表情呢？......呃、呃啊，不对，我得阻止自己才行......呵呵，果然还是想看到啊......', 'char_164_nightm/CN_008');
INSERT INTO `t_operator_voice` VALUES (2897, 86, '信赖提升后交谈3', '博士，你偶尔也会感到厌倦的，对吗？工作、生活、感情、眼前的一切......想不想一口气忘掉这些东西？我，能用我的能力来满足你......', 'char_164_nightm/CN_009');
INSERT INTO `t_operator_voice` VALUES (2898, 86, '闲置', '这是一张多么可爱的睡脸啊，让我真想就这样——', 'char_164_nightm/CN_010');
INSERT INTO `t_operator_voice` VALUES (2899, 86, '干员报到', '夜魔......啊，这只是我的代号。我的名字是葛罗莉亚，如果能叫我的名字，我会很高兴的......我很害怕，那个代号......', 'char_164_nightm/CN_011');
INSERT INTO `t_operator_voice` VALUES (2900, 86, '观看作战记录', '让我变得更强大......是为了做什么呢......？', 'char_164_nightm/CN_012');
INSERT INTO `t_operator_voice` VALUES (2901, 86, '精英化晋升1', '我一开始是以病人的身份来这里接受治疗的，不过现在看来，那个医生是想让我为你们战斗，对吧？', 'char_164_nightm/CN_013');
INSERT INTO `t_operator_voice` VALUES (2902, 86, '精英化晋升2', '这次晋升会不会有点勉强啊？不过无所谓，反正我只是侥幸活着罢了，你想怎么做，就随你怎么做吧。', 'char_164_nightm/CN_014');
INSERT INTO `t_operator_voice` VALUES (2903, 86, '编入队伍', '好好~知道了。', 'char_164_nightm/CN_017');
INSERT INTO `t_operator_voice` VALUES (2904, 86, '任命队长', '我一个人不行的......', 'char_164_nightm/CN_018');
INSERT INTO `t_operator_voice` VALUES (2905, 86, '行动出发', '各位，都会展现出什么样的精彩能力呢？我很期待啊~', 'char_164_nightm/CN_019');
INSERT INTO `t_operator_voice` VALUES (2906, 86, '行动开始', '敌人就是这些人啊。欸~好像很有趣的样子嘛。', 'char_164_nightm/CN_020');
INSERT INTO `t_operator_voice` VALUES (2907, 86, '选中干员1', '有、有什么东西可以吃吗？', 'char_164_nightm/CN_021');
INSERT INTO `t_operator_voice` VALUES (2908, 86, '选中干员2', '要不要让我来当你们的对手呢？', 'char_164_nightm/CN_022');
INSERT INTO `t_operator_voice` VALUES (2909, 86, '部署1', '这里是......战场......！', 'char_164_nightm/CN_023');
INSERT INTO `t_operator_voice` VALUES (2910, 86, '部署2', '我久违的战场啊~', 'char_164_nightm/CN_024');
INSERT INTO `t_operator_voice` VALUES (2911, 86, '作战中1', '啊哈哈，为什么这么严肃呢？', 'char_164_nightm/CN_025');
INSERT INTO `t_operator_voice` VALUES (2912, 86, '作战中2', '放松，放松点~', 'char_164_nightm/CN_026');
INSERT INTO `t_operator_voice` VALUES (2913, 86, '作战中3', '祝你有个好梦......', 'char_164_nightm/CN_027');
INSERT INTO `t_operator_voice` VALUES (2914, 86, '作战中4', '还、还要继续向前走吗？', 'char_164_nightm/CN_028');
INSERT INTO `t_operator_voice` VALUES (2915, 86, '4星结束行动', '胜利......真让人愉快啊。', 'char_164_nightm/CN_029');
INSERT INTO `t_operator_voice` VALUES (2916, 86, '3星结束行动', '嗯~抱歉喽。不过，这就是我为你们准备好的结局哦。', 'char_164_nightm/CN_030');
INSERT INTO `t_operator_voice` VALUES (2917, 86, '非3星结束行动', '能赢就好了，还有什么可以追求的呢？', 'char_164_nightm/CN_031');
INSERT INTO `t_operator_voice` VALUES (2918, 86, '行动失败', '我......究竟在做什么啊......', 'char_164_nightm/CN_032');
INSERT INTO `t_operator_voice` VALUES (2919, 86, '进驻设施', '给我一个角落就够了......', 'char_164_nightm/CN_033');
INSERT INTO `t_operator_voice` VALUES (2920, 86, '戳一下', '啊啊......', 'char_164_nightm/CN_034');
INSERT INTO `t_operator_voice` VALUES (2921, 86, '信赖触摸', '我、我最近是不是又给您惹了麻烦......？总之，还是先向您道歉吧，非常、非常的对不起......', 'char_164_nightm/CN_036');
INSERT INTO `t_operator_voice` VALUES (2922, 86, '标题', '明日方舟。', 'char_164_nightm/CN_037');
INSERT INTO `t_operator_voice` VALUES (2923, 86, '问候', '早安，博士~', 'char_164_nightm/CN_042');
INSERT INTO `t_operator_voice` VALUES (2924, 87, '任命助理', '报告Dr.{@nickname}！巡查完毕，没有异常！', 'char_220_grani/CN_001');
INSERT INTO `t_operator_voice` VALUES (2925, 87, '交谈1', '好饿啊，战斗消耗了我好多体力......哎，{@nickname}这里有食物吗？特别为我准备的？是，是苹果派！谢谢！我、我就不客气了！', 'char_220_grani/CN_002');
INSERT INTO `t_operator_voice` VALUES (2926, 87, '交谈2', '罗德岛的大家喜欢叫我“小个子”，嗯——我不会在意的，其实，以前骑警队的同事也会这样捉弄我。等等，禁止摸头！还有耳朵！不可以随便揉我的脸！', 'char_220_grani/CN_003');
INSERT INTO `t_operator_voice` VALUES (2927, 87, '交谈3', '{@nickname}，星期三和星期五晚上的常规训练我也许不能参加......嗯，这两天我会帮助民间组织维护治安、打击犯罪！唉？星期五因为有紧急任务，所以不行吗！', 'char_220_grani/CN_004');
INSERT INTO `t_operator_voice` VALUES (2928, 87, '晋升后交谈1', '罗德岛的大家实力都很强，不过较量起速度的话，我是不会输给任何人的！', 'char_220_grani/CN_005');
INSERT INTO `t_operator_voice` VALUES (2929, 87, '晋升后交谈2', '不知不觉中，已经和你共度了这么长的时光呢。{@nickname}，一起经历过的那些战斗，我一点都不会忘记。但停在这里可不行！我们的未来可是更加广阔的，对吗？', 'char_220_grani/CN_006');
INSERT INTO `t_operator_voice` VALUES (2930, 87, '信赖提升后交谈1', '刚才我在走廊上碰见了斯卡蒂......{@nickname}是不是觉得她平常对人很冷淡？其实她是个很善良的人哦，真的！', 'char_220_grani/CN_007');
INSERT INTO `t_operator_voice` VALUES (2931, 87, '信赖提升后交谈2', '和大家比起来，我很普通。既没有非凡的能力，也没有什么辉煌的战绩......但用自己的双手把这个世界变得更好这件事，与力量强不强是没有关系的！不过，不好好吃饭是一定不行的！', 'char_220_grani/CN_008');
INSERT INTO `t_operator_voice` VALUES (2932, 87, '信赖提升后交谈3', '{@nickname}始终在为大家着想，却从来没有顾及过自己的安全，这可不对！无论你今后遇到什么样的危险，我可都会拉住你的手的！嗯嗯，就这样约好了！', 'char_220_grani/CN_009');
INSERT INTO `t_operator_voice` VALUES (2933, 87, '闲置', '唔唔，为什么{@nickname}身上会飘出这么香的味道？你一定藏了什么好吃的，对不对！', 'char_220_grani/CN_010');
INSERT INTO `t_operator_voice` VALUES (2934, 87, '干员报到', '骑警格拉尼，听到了{@nickname}你求助的声音！没问题的，从今天开始，我会拼尽全力保护你！', 'char_220_grani/CN_011');
INSERT INTO `t_operator_voice` VALUES (2935, 87, '观看作战记录', '原来如此，在这种地方也不能疏忽，嗯嗯！', 'char_220_grani/CN_012');
INSERT INTO `t_operator_voice` VALUES (2936, 87, '精英化晋升1', '晋升勋章？给我的吗？谢谢{@nickname}！我会把它和骑警勋章挂在一起的！', 'char_220_grani/CN_013');
INSERT INTO `t_operator_voice` VALUES (2937, 87, '精英化晋升2', '从第一次执行任务到现在，这把长枪无数次支撑着我站起来。只要紧握住它，我的内心就会充满足以守护大家梦想的勇气。', 'char_220_grani/CN_014');
INSERT INTO `t_operator_voice` VALUES (2938, 87, '编入队伍', '需要我去侦查战场吗？', 'char_220_grani/CN_017');
INSERT INTO `t_operator_voice` VALUES (2939, 87, '任命队长', '把大家托付给我吗？没问题，我会努力保护整个小队的！', 'char_220_grani/CN_018');
INSERT INTO `t_operator_voice` VALUES (2940, 87, '行动出发', '各位，让我们把胜利带回罗德岛！', 'char_220_grani/CN_019');
INSERT INTO `t_operator_voice` VALUES (2941, 87, '行动开始', '你们的胡作非为也就到此为止了！', 'char_220_grani/CN_020');
INSERT INTO `t_operator_voice` VALUES (2942, 87, '选中干员1', '交给我吧！', 'char_220_grani/CN_021');
INSERT INTO `t_operator_voice` VALUES (2943, 87, '选中干员2', '嗯！', 'char_220_grani/CN_022');
INSERT INTO `t_operator_voice` VALUES (2944, 87, '部署1', '已就位。', 'char_220_grani/CN_023');
INSERT INTO `t_operator_voice` VALUES (2945, 87, '部署2', '奔跑起来吧！', 'char_220_grani/CN_024');
INSERT INTO `t_operator_voice` VALUES (2946, 87, '作战中1', '以我的枪尖开路！', 'char_220_grani/CN_025');
INSERT INTO `t_operator_voice` VALUES (2947, 87, '作战中2', '时间到，冲刺！', 'char_220_grani/CN_026');
INSERT INTO `t_operator_voice` VALUES (2948, 87, '作战中3', '只要交锋就会决出胜负！', 'char_220_grani/CN_027');
INSERT INTO `t_operator_voice` VALUES (2949, 87, '作战中4', '我的战场，可不只是在地面！', 'char_220_grani/CN_028');
INSERT INTO `t_operator_voice` VALUES (2950, 87, '4星结束行动', '为了那些需要保护的人，我绝不会让你们再前进一步！', 'char_220_grani/CN_029');
INSERT INTO `t_operator_voice` VALUES (2951, 87, '3星结束行动', '嗯！赢得真漂亮！大家的战斗非常精彩！', 'char_220_grani/CN_030');
INSERT INTO `t_operator_voice` VALUES (2952, 87, '非3星结束行动', '{@nickname}，请不要离我太远，也许还有漏网的敌人。', 'char_220_grani/CN_031');
INSERT INTO `t_operator_voice` VALUES (2953, 87, '行动失败', '可恶......我不会倒下的！', 'char_220_grani/CN_032');
INSERT INTO `t_operator_voice` VALUES (2954, 87, '进驻设施', '有谁需要帮忙吗？', 'char_220_grani/CN_033');
INSERT INTO `t_operator_voice` VALUES (2955, 87, '戳一下', '诶诶！', 'char_220_grani/CN_034');
INSERT INTO `t_operator_voice` VALUES (2956, 87, '信赖触摸', '如果有机会，我想和{@nickname}一起去看一看这个世界。', 'char_220_grani/CN_036');
INSERT INTO `t_operator_voice` VALUES (2957, 87, '标题', '明日方舟。', 'char_220_grani/CN_037');
INSERT INTO `t_operator_voice` VALUES (2958, 87, '问候', '你好，博士！', 'char_220_grani/CN_042');
INSERT INTO `t_operator_voice` VALUES (2959, 88, '任命助理', '为了你的安全着想，最好离我两米以上，也不要和我说话......我可不想你被冲着我来的危险伤到。', 'char_263_skadi/CN_001');
INSERT INTO `t_operator_voice` VALUES (2960, 88, '交谈1', '看过我的履历了？我的过去可是很糟糕的。嗯，让我呆在身边，不仅你会被卷入麻烦，我也会很难办的。', 'char_263_skadi/CN_002');
INSERT INTO `t_operator_voice` VALUES (2961, 88, '交谈2', '我的头发很长，很好看？啊，嗯，谢谢......要不要摸摸看？我的头发还是挺柔软的。这方面，我还算是有些自信的哦。', 'char_263_skadi/CN_003');
INSERT INTO `t_operator_voice` VALUES (2962, 88, '交谈3', '我没看错吧？那个人，现在叫幽灵鲨是吗，怎么穿成了修女的样子？失忆了？这可麻烦了......Dr.{@nickname}，你得帮帮她，可不能让她忘记自己真正的职责。', 'char_263_skadi/CN_004');
INSERT INTO `t_operator_voice` VALUES (2963, 88, '晋升后交谈1', '你这人，怎么这么执着，这样我不就只能老老实实保护你了吗。', 'char_263_skadi/CN_005');
INSERT INTO `t_operator_voice` VALUES (2964, 88, '晋升后交谈2', '远方浩瀚蓝色与轻抚土地的白色碎片之下，埋葬着原本我所在乎的一切。我很害怕，害怕厄运会再次夺走我重要的人，所以我才会选择逃离，我不希望我珍视的人......因我而受伤。', 'char_263_skadi/CN_006');
INSERT INTO `t_operator_voice` VALUES (2965, 88, '信赖提升后交谈1', '好，又度过了轻松的一天！没有会卷走队友的巨大触须，也没有蹲在角落里满手是血的疯狂敌人......光是上上战场什么的，对，已经很轻松了！', 'char_263_skadi/CN_007');
INSERT INTO `t_operator_voice` VALUES (2966, 88, '信赖提升后交谈2', '根据传说，我的族裔已经和那些灾祸战斗了无数年。说不定，我们也帮你们这些城市人把灾祸挡在了陆地之外......所以说，是不是该请我喝一杯，好好谢谢我？', 'char_263_skadi/CN_008');
INSERT INTO `t_operator_voice` VALUES (2967, 88, '信赖提升后交谈3', '当你历经漫长黑暗、终于浮出海面——睁开眼睛的刹那，灿烂星空滴落眼眶、无垠波浪拂过面颊，眼泪和家的味道，都是咸的。相比下，罗德岛的浴缸真是又窄又浅......我一定得带你去试试才行。', 'char_263_skadi/CN_009');
INSERT INTO `t_operator_voice` VALUES (2968, 88, '闲置', '睡着了？睡吧，Dr.{@nickname}，得做个干燥的好梦哟。', 'char_263_skadi/CN_010');
INSERT INTO `t_operator_voice` VALUES (2969, 88, '干员报到', '斯卡蒂，赏金猎人。你真要签下我？我可是那种，会给你带来灾祸的人哦。', 'char_263_skadi/CN_011');
INSERT INTO `t_operator_voice` VALUES (2970, 88, '观看作战记录', '呯呯地冲过去，咚地打倒敌人，啪地解决掉——原来你们是这么战斗的，我记住啦。', 'char_263_skadi/CN_012');
INSERT INTO `t_operator_voice` VALUES (2971, 88, '精英化晋升1', '这个就是那个，罗德岛干员常说的晋升吗？感觉不错，好像自己真的变强了呢，大概？', 'char_263_skadi/CN_013');
INSERT INTO `t_operator_voice` VALUES (2972, 88, '精英化晋升2', '这样渴求我的力量，真的让我很困扰......如果你真的无所畏惧，那么做好准备，和我一起直面无边无际的黑暗吧。', 'char_263_skadi/CN_014');
INSERT INTO `t_operator_voice` VALUES (2973, 88, '编入队伍', '我倒也挺擅长团队作战，只是......', 'char_263_skadi/CN_017');
INSERT INTO `t_operator_voice` VALUES (2974, 88, '任命队长', '你是把大家的性命交到我手上了吗？', 'char_263_skadi/CN_018');
INSERT INTO `t_operator_voice` VALUES (2975, 88, '行动出发', '对手是人类的话，终归还是没什么手感。', 'char_263_skadi/CN_019');
INSERT INTO `t_operator_voice` VALUES (2976, 88, '行动开始', '和我面对过的灾厄相比，你们也太弱了。', 'char_263_skadi/CN_020');
INSERT INTO `t_operator_voice` VALUES (2977, 88, '选中干员1', '听你的。', 'char_263_skadi/CN_021');
INSERT INTO `t_operator_voice` VALUES (2978, 88, '选中干员2', '我习惯了独行。', 'char_263_skadi/CN_022');
INSERT INTO `t_operator_voice` VALUES (2979, 88, '部署1', '起舞吧。', 'char_263_skadi/CN_023');
INSERT INTO `t_operator_voice` VALUES (2980, 88, '部署2', '小心为上。', 'char_263_skadi/CN_024');
INSERT INTO `t_operator_voice` VALUES (2981, 88, '作战中1', '谁知道潮湿的海草间蛰伏着什么？', 'char_263_skadi/CN_025');
INSERT INTO `t_operator_voice` VALUES (2982, 88, '作战中2', '这把剑的过去可是很恐怖的。', 'char_263_skadi/CN_026');
INSERT INTO `t_operator_voice` VALUES (2983, 88, '作战中3', '纠缠着我的噩梦啊，唱个歌吧。', 'char_263_skadi/CN_027');
INSERT INTO `t_operator_voice` VALUES (2984, 88, '作战中4', '别眨眼，你会错过自己的死状。', 'char_263_skadi/CN_028');
INSERT INTO `t_operator_voice` VALUES (2985, 88, '4星结束行动', '我们活下来了。', 'char_263_skadi/CN_029');
INSERT INTO `t_operator_voice` VALUES (2986, 88, '3星结束行动', '敌人怎么说，怎么想，都与我无关。我只知道他们会怎么倒下。', 'char_263_skadi/CN_030');
INSERT INTO `t_operator_voice` VALUES (2987, 88, '非3星结束行动', '生疏了吗？到手的赏金会变少哟。', 'char_263_skadi/CN_031');
INSERT INTO `t_operator_voice` VALUES (2988, 88, '行动失败', '这种程度的不幸，也只是个开始罢了。', 'char_263_skadi/CN_032');
INSERT INTO `t_operator_voice` VALUES (2989, 88, '进驻设施', '请给我安排个周围没人的房间。', 'char_263_skadi/CN_033');
INSERT INTO `t_operator_voice` VALUES (2990, 88, '戳一下', '啊......帽子！', 'char_263_skadi/CN_034');
INSERT INTO `t_operator_voice` VALUES (2991, 88, '信赖触摸', '离我那么近......我，我可没有能完全保护你的自信！', 'char_263_skadi/CN_036');
INSERT INTO `t_operator_voice` VALUES (2992, 88, '标题', '明日方舟。', 'char_263_skadi/CN_037');
INSERT INTO `t_operator_voice` VALUES (2993, 88, '问候', '注意安全，博士。', 'char_263_skadi/CN_042');
INSERT INTO `t_operator_voice` VALUES (2994, 89, '任命助理', '工作就像是陷阱，你得主动跳进去才行。', 'char_284_spot/CN_001');
INSERT INTO `t_operator_voice` VALUES (2995, 89, '交谈1', '我不说话只是因为不想，没有看不起谁的意思。', 'char_284_spot/CN_002');
INSERT INTO `t_operator_voice` VALUES (2996, 89, '交谈2', '月见夜又做什么了，他是不是终于因为在走廊上搭讪遭人讨厌甚至被送进医务室了？没有？真是遗憾。', 'char_284_spot/CN_003');
INSERT INTO `t_operator_voice` VALUES (2997, 89, '交谈3', '有事吗，博士？我大概能帮上点忙。不过，要是能不占用我用来看漫画的休息时间，当然会更好。', 'char_284_spot/CN_004');
INSERT INTO `t_operator_voice` VALUES (2998, 89, '晋升后交谈1', '虽然有时候我说话很直接，但该做的事情，我一定会做好。这不关职业道德什么事，只是我觉得连任务都没法完成的人，一定没有用心去做。', 'char_284_spot/CN_005');
INSERT INTO `t_operator_voice` VALUES (2999, 89, '信赖提升后交谈1', '罗德岛是个奇怪的地方。这里的人有什么情绪都会说出来，有什么需要帮忙的地方就自然会有人伸出援手。', 'char_284_spot/CN_007');
INSERT INTO `t_operator_voice` VALUES (3000, 89, '信赖提升后交谈2', '虽然A6行动预备组的各位，不是每天摆着张臭脸就是没有自控能力，要么自我意识过剩或者根本是颗移动炸弹，但我依然觉得他们很可靠。因为他们真的在努力，哪怕是偷偷的。', 'char_284_spot/CN_008');
INSERT INTO `t_operator_voice` VALUES (3001, 89, '信赖提升后交谈3', '我以前也是个像各个罗德岛干员一样自由自在的人，我的家庭却觉得那样的我是个怪胎。也许这里是个怪胎集合地也说不定，但至少，大家都很快乐。这样也不错。', 'char_284_spot/CN_009');
INSERT INTO `t_operator_voice` VALUES (3002, 89, '闲置', '我可以先回宿舍吗？', 'char_284_spot/CN_010');
INSERT INTO `t_operator_voice` VALUES (3003, 89, '干员报到', '你好，我是隶属行动预备组A6的斑点，职责大概是保护预备组每天都在闹腾的另外几个家伙。', 'char_284_spot/CN_011');
INSERT INTO `t_operator_voice` VALUES (3004, 89, '观看作战记录', '这场战斗我看过，敌人都完蛋了。', 'char_284_spot/CN_012');
INSERT INTO `t_operator_voice` VALUES (3005, 89, '精英化晋升1', '我会加倍努力工作的，从0.1到0.2的水平吧。', 'char_284_spot/CN_013');
INSERT INTO `t_operator_voice` VALUES (3006, 89, '编入队伍', '我就不询问这次作战的意义了。', 'char_284_spot/CN_017');
INSERT INTO `t_operator_voice` VALUES (3007, 89, '任命队长', '我想回宿舍了。', 'char_284_spot/CN_018');
INSERT INTO `t_operator_voice` VALUES (3008, 89, '行动出发', '知道了。', 'char_284_spot/CN_019');
INSERT INTO `t_operator_voice` VALUES (3009, 89, '行动开始', '你们直接投降能节约我很多时间。', 'char_284_spot/CN_020');
INSERT INTO `t_operator_voice` VALUES (3010, 89, '选中干员1', '怎么样都行。', 'char_284_spot/CN_021');
INSERT INTO `t_operator_voice` VALUES (3011, 89, '选中干员2', '我随便。', 'char_284_spot/CN_022');
INSERT INTO `t_operator_voice` VALUES (3012, 89, '部署1', '就这样吧。', 'char_284_spot/CN_023');
INSERT INTO `t_operator_voice` VALUES (3013, 89, '部署2', '挺好的。', 'char_284_spot/CN_024');
INSERT INTO `t_operator_voice` VALUES (3014, 89, '作战中1', '试试这个。', 'char_284_spot/CN_025');
INSERT INTO `t_operator_voice` VALUES (3015, 89, '作战中2', '应该不会伤到队友。', 'char_284_spot/CN_026');
INSERT INTO `t_operator_voice` VALUES (3016, 89, '作战中3', '破绽太多了。', 'char_284_spot/CN_027');
INSERT INTO `t_operator_voice` VALUES (3017, 89, '作战中4', '举盾也是很累的。', 'char_284_spot/CN_028');
INSERT INTO `t_operator_voice` VALUES (3018, 89, '4星结束行动', '每当你觉得一场战斗很轻松的时候，就最好再来一次。', 'char_284_spot/CN_029');
INSERT INTO `t_operator_voice` VALUES (3019, 89, '3星结束行动', '只要肯努力，就算大脑空空也能做好。', 'char_284_spot/CN_030');
INSERT INTO `t_operator_voice` VALUES (3020, 89, '非3星结束行动', '瑕疵是一种必然，从结果来说。', 'char_284_spot/CN_031');
INSERT INTO `t_operator_voice` VALUES (3021, 89, '行动失败', '别紧张，你这样没事。', 'char_284_spot/CN_032');
INSERT INTO `t_operator_voice` VALUES (3022, 89, '进驻设施', '这样也可以。', 'char_284_spot/CN_033');
INSERT INTO `t_operator_voice` VALUES (3023, 89, '戳一下', '哦。', 'char_284_spot/CN_034');
INSERT INTO `t_operator_voice` VALUES (3024, 89, '信赖触摸', '你这样摸，我的毛会打结的。', 'char_284_spot/CN_036');
INSERT INTO `t_operator_voice` VALUES (3025, 89, '标题', '明日方舟。', 'char_284_spot/CN_037');
INSERT INTO `t_operator_voice` VALUES (3026, 89, '问候', '博士。', 'char_284_spot/CN_042');
INSERT INTO `t_operator_voice` VALUES (3027, 90, '任命助理', '嗯嗯......又能见到博士了，泡普卡，真高兴......嘿嘿......', 'char_281_popka/CN_001');
INSERT INTO `t_operator_voice` VALUES (3028, 90, '交谈1', '博士，累了吗？这是泡普卡新泡的茶，还有梓兰姐姐做的饼干，给您......嗯嗯，要打起精神来哦......？', 'char_281_popka/CN_002');
INSERT INTO `t_operator_voice` VALUES (3029, 90, '交谈2', 'A6组的大家，为什么每天都会吵架呢......？再这样下去，大家会渐渐变成陌生人的......吵架绝对是一件错事，泡普卡，一定要让大家彻底明白才行......', 'char_281_popka/CN_003');
INSERT INTO `t_operator_voice` VALUES (3030, 90, '交谈3', '泡普卡的电锯，上面呜呜转的盘子有点松动了呢......可是好奇怪，泡普卡不记得最近有用过它啊......？', 'char_281_popka/CN_004');
INSERT INTO `t_operator_voice` VALUES (3031, 90, '晋升后交谈1', '博士，A6组的大家，现在是和和气气的吗？......嗯嗯，和和气气的，最好......而且，只要大家不再吵闹，泡普卡也能安安心心待在博士身边......', 'char_281_popka/CN_005');
INSERT INTO `t_operator_voice` VALUES (3032, 90, '信赖提升后交谈1', '......不知道为什么，最近，泡普卡总是会失去一段时间的记忆，就算问A6组的大家，大家也只会笑着对泡普卡说，什么事都没发生过......？', 'char_281_popka/CN_007');
INSERT INTO `t_operator_voice` VALUES (3033, 90, '信赖提升后交谈2', '呜、博、博士，泡普卡，刚才是不是昏过去了......？咦，大家怎么，都躺在地上？空爆姐姐？月见夜哥哥，斑点哥哥？你们，怎么满头都是包？怎么连梓兰姐姐也......', 'char_281_popka/CN_008');
INSERT INTO `t_operator_voice` VALUES (3034, 90, '信赖提升后交谈3', '泡普卡的力量，其实很强大？为了阻止大家继续争吵，所以泡普卡的力量，失控了，才不记得发生了什么？......原来，是这样啊，泡普卡，明白了。嗯嗯，这样的力量，泡普卡，绝对不会浪费的！', 'char_281_popka/CN_009');
INSERT INTO `t_operator_voice` VALUES (3035, 90, '闲置', '啊......！水杯，捏爆了......？呜......博士醒来的话，会生气的......', 'char_281_popka/CN_010');
INSERT INTO `t_operator_voice` VALUES (3036, 90, '干员报到', '行动预备组A6，泡普卡，报道。那个，A6组的大家，总是很吵闹，让博士担心了......泡普卡，知道该怎么做......', 'char_281_popka/CN_011');
INSERT INTO `t_operator_voice` VALUES (3037, 90, '观看作战记录', '......咦，博士，屏幕里的那个人，是泡普卡吗......？', 'char_281_popka/CN_012');
INSERT INTO `t_operator_voice` VALUES (3038, 90, '精英化晋升1', '泡普卡，高兴得不知道该说些什么好......！谢谢博士......！谢谢您！', 'char_281_popka/CN_013');
INSERT INTO `t_operator_voice` VALUES (3039, 90, '编入队伍', '叫我泡普卡......请多多关照......', 'char_281_popka/CN_017');
INSERT INTO `t_operator_voice` VALUES (3040, 90, '任命队长', '那个......布置战术什么的，泡普卡都还没学会呢......', 'char_281_popka/CN_018');
INSERT INTO `t_operator_voice` VALUES (3041, 90, '行动出发', '空爆姐姐，那个，出发时间到了哦......', 'char_281_popka/CN_019');
INSERT INTO `t_operator_voice` VALUES (3042, 90, '行动开始', '梓兰姐姐，别紧张......泡普卡，不会有事的......', 'char_281_popka/CN_020');
INSERT INTO `t_operator_voice` VALUES (3043, 90, '选中干员1', '嗯......', 'char_281_popka/CN_021');
INSERT INTO `t_operator_voice` VALUES (3044, 90, '选中干员2', '泡普卡，在这呢。', 'char_281_popka/CN_022');
INSERT INTO `t_operator_voice` VALUES (3045, 90, '部署1', '泡普卡，想制止这场争斗。', 'char_281_popka/CN_023');
INSERT INTO `t_operator_voice` VALUES (3046, 90, '部署2', '不要吵架哦......', 'char_281_popka/CN_024');
INSERT INTO `t_operator_voice` VALUES (3047, 90, '作战中1', '乖一点，不要动......', 'char_281_popka/CN_025');
INSERT INTO `t_operator_voice` VALUES (3048, 90, '作战中2', '锯子，在生气了......', 'char_281_popka/CN_026');
INSERT INTO `t_operator_voice` VALUES (3049, 90, '4星结束行动', '月见夜哥哥，该归队了哦......梓兰姐姐，在盯着你看呢......', 'char_281_popka/CN_029');
INSERT INTO `t_operator_voice` VALUES (3050, 90, '3星结束行动', '现在，争吵，可以停下来了吗......？', 'char_281_popka/CN_030');
INSERT INTO `t_operator_voice` VALUES (3051, 90, '非3星结束行动', '唔唔......博士，给，泡普卡的茶......博士，累了吧......？', 'char_281_popka/CN_031');
INSERT INTO `t_operator_voice` VALUES (3052, 90, '行动失败', '好......好疼......泡普卡，受伤了......？', 'char_281_popka/CN_032');
INSERT INTO `t_operator_voice` VALUES (3053, 90, '进驻设施', '请别让泡普卡一个人待在这......', 'char_281_popka/CN_033');
INSERT INTO `t_operator_voice` VALUES (3054, 90, '戳一下', '哇啊啊......', 'char_281_popka/CN_034');
INSERT INTO `t_operator_voice` VALUES (3055, 90, '信赖触摸', '博士，摸摸......', 'char_281_popka/CN_036');
INSERT INTO `t_operator_voice` VALUES (3056, 90, '标题', '明日方舟。', 'char_281_popka/CN_037');
INSERT INTO `t_operator_voice` VALUES (3057, 90, '问候', '博士，今天也和和气气的吗......？', 'char_281_popka/CN_042');
INSERT INTO `t_operator_voice` VALUES (3058, 91, '任命助理', '博士，您好......那个，有需求的话，就请呼唤我......嗯，我的代号，您，还记得住吗......？', 'char_253_greyy/CN_001');
INSERT INTO `t_operator_voice` VALUES (3059, 91, '交谈1', '博、博士，您好......嗯，我刚刚为医疗组检查过电源线路，顺便帮大家送些文件......没、我没有被使唤！毕竟，医生们肯为我治病，这是恩情，要报答......', 'char_253_greyy/CN_002');
INSERT INTO `t_operator_voice` VALUES (3060, 91, '交谈2', '最初，我学习电能类法术，仅仅是为了让贫民区的伙伴有光照，让夜晚不再那样难熬......黑夜，真的很可怕，生活在黑夜中的人，也是......', 'char_253_greyy/CN_003');
INSERT INTO `t_operator_voice` VALUES (3061, 91, '交谈3', '我真的很羡慕，罗德岛有这样的源石发电设备......您知道吗，我生活的地方，哪怕只有一台这样的机器，大家也不会变得习惯黑暗，将没有光亮的生活视为常事了。', 'char_253_greyy/CN_004');
INSERT INTO `t_operator_voice` VALUES (3062, 91, '晋升后交谈1', '教授我基础源石技艺的是妈妈，她说，我像年轻时的她一样懂得怎么使用法术，可也像她一样多病......如果她还在的话，我真希望她能和我一起来罗德岛......', 'char_253_greyy/CN_005');
INSERT INTO `t_operator_voice` VALUES (3063, 91, '晋升后交谈2', '在得到您的指导之前，我的法术只能照亮一个房间而已。不过，即使是那么小的一片火花，现在，也变成了能为大家带来帮助的能力了呢......！博士，真的很感谢您！', 'char_253_greyy/CN_006');
INSERT INTO `t_operator_voice` VALUES (3064, 91, '信赖提升后交谈1', '咦？梅尔小姐的库房电量异常？又、又是她，怎么办......您说，凯尔希医生的实验室要新增电缆？还有伊芙利特的房间又断电了？呜、呜啊啊啊......博士，帮帮我......', 'char_253_greyy/CN_007');
INSERT INTO `t_operator_voice` VALUES (3065, 91, '信赖提升后交谈2', '呼——咦？博士......？啊对不起博士我没有在偷睡！我、我只是......咦，这里是医务室？', 'char_253_greyy/CN_008');
INSERT INTO `t_operator_voice` VALUES (3066, 91, '信赖提升后交谈3', '博士，又要出门了吗？......嗯，我知道了。我会好好待在这里的......那个，博士，要早点回来哦......', 'char_253_greyy/CN_009');
INSERT INTO `t_operator_voice` VALUES (3067, 91, '闲置', '咿......！唔啊啊，静电好麻烦......', 'char_253_greyy/CN_010');
INSERT INTO `t_operator_voice` VALUES (3068, 91, '干员报到', '您、您好，我是格雷伊，电能相关的工作，还算擅长......那个，谢、谢谢您愿意收留我......！', 'char_253_greyy/CN_011');
INSERT INTO `t_operator_voice` VALUES (3069, 91, '观看作战记录', '这、这里的画面，暂停一下，可以吗？我、我记一下笔记......', 'char_253_greyy/CN_012');
INSERT INTO `t_operator_voice` VALUES (3070, 91, '精英化晋升1', '晋升吗？知道了，我这就去通知大家——欸？是、是安排我晋升......？咦？', 'char_253_greyy/CN_013');
INSERT INTO `t_operator_voice` VALUES (3071, 91, '精英化晋升2', '欸嘿嘿，博士，就让我为您照亮前进的道路吧！唔，好、好像说了些大话......？就算是大话，也是很认真的在说哦......！', 'char_253_greyy/CN_014');
INSERT INTO `t_operator_voice` VALUES (3072, 91, '编入队伍', '格雷伊，来给大家帮忙了......', 'char_253_greyy/CN_017');
INSERT INTO `t_operator_voice` VALUES (3073, 91, '任命队长', '队、队长......？嗯、嗯，我听您的安排......！', 'char_253_greyy/CN_018');
INSERT INTO `t_operator_voice` VALUES (3074, 91, '行动出发', '我去为后勤部队帮忙......！', 'char_253_greyy/CN_019');
INSERT INTO `t_operator_voice` VALUES (3075, 91, '行动开始', '博士，敌人开始集结了......', 'char_253_greyy/CN_020');
INSERT INTO `t_operator_voice` VALUES (3076, 91, '选中干员1', '我会尽、尽力......', 'char_253_greyy/CN_021');
INSERT INTO `t_operator_voice` VALUES (3077, 91, '选中干员2', '我、我应该能行......！', 'char_253_greyy/CN_022');
INSERT INTO `t_operator_voice` VALUES (3078, 91, '部署1', '我得慢慢来......', 'char_253_greyy/CN_023');
INSERT INTO `t_operator_voice` VALUES (3079, 91, '部署2', '可、可不能在这里扭伤了脚。', 'char_253_greyy/CN_024');
INSERT INTO `t_operator_voice` VALUES (3080, 91, '作战中1', '别再作恶了！', 'char_253_greyy/CN_025');
INSERT INTO `t_operator_voice` VALUES (3081, 91, '作战中2', '不要伤害大家......！', 'char_253_greyy/CN_026');
INSERT INTO `t_operator_voice` VALUES (3082, 91, '作战中3', '看这、这招！', 'char_253_greyy/CN_027');
INSERT INTO `t_operator_voice` VALUES (3083, 91, '作战中4', '被电到的话，后果很、很可怕的......！', 'char_253_greyy/CN_028');
INSERT INTO `t_operator_voice` VALUES (3084, 91, '4星结束行动', '博、博士，您辛苦了......！我们，一起回家吧......？', 'char_253_greyy/CN_029');
INSERT INTO `t_operator_voice` VALUES (3085, 91, '3星结束行动', '是、是我们胜利了......！博士！', 'char_253_greyy/CN_030');
INSERT INTO `t_operator_voice` VALUES (3086, 91, '非3星结束行动', '博士，那些逃走的人，不会......再回来了吧？', 'char_253_greyy/CN_031');
INSERT INTO `t_operator_voice` VALUES (3087, 91, '行动失败', '呜......博士......', 'char_253_greyy/CN_032');
INSERT INTO `t_operator_voice` VALUES (3088, 91, '进驻设施', '今天要做什么呢？', 'char_253_greyy/CN_033');
INSERT INTO `t_operator_voice` VALUES (3089, 91, '戳一下', '呜啊......', 'char_253_greyy/CN_034');
INSERT INTO `t_operator_voice` VALUES (3090, 91, '信赖触摸', '博士，个子真高......我、我什么时候才能追上博士......呢？', 'char_253_greyy/CN_036');
INSERT INTO `t_operator_voice` VALUES (3091, 91, '标题', '明日方舟。', 'char_253_greyy/CN_037');
INSERT INTO `t_operator_voice` VALUES (3092, 91, '问候', '博士，您回来了！', 'char_253_greyy/CN_042');
INSERT INTO `t_operator_voice` VALUES (3093, 92, '任命助理', '博士，我们的每一分钟，都很重要！一分钟，就有可能是数百万的亏损和额外的伤亡！欸，咖啡？请帮我加一包黄糖，多点奶沫，谢谢！', 'char_308_swire/CN_001');
INSERT INTO `t_operator_voice` VALUES (3094, 92, '交谈1', '虽然有人和我说你们是个黑幕重重的公司，走道里有很多可怕的感染者四处游荡，但我觉得，根本就是夸大其词嘛。罗德岛的干员都在为了更好的生活而奋斗，这不是和龙门警员们一模一样嘛！', 'char_308_swire/CN_002');
INSERT INTO `t_operator_voice` VALUES (3095, 92, '交谈2', '我说，教官办公室的布局，可以调整一下吧？至少加一些装饰，像是手织地毯，或者原子主义画作什么的？现在的办公室也太朴素了，虽然杜宾说她无所谓，但我的工作热情可是会下降的......', 'char_308_swire/CN_003');
INSERT INTO `t_operator_voice` VALUES (3096, 92, '交谈3', '唉，那个讨厌的陈，在不在这里？在近卫局的时候，一旦在过道上碰见她，就会被她恶狠狠地盯着，弄得我也开始怀疑自己是不是做错什么了！我不就是......有点，想要接管近卫局而已嘛！', 'char_308_swire/CN_004');
INSERT INTO `t_operator_voice` VALUES (3097, 92, '晋升后交谈1', '锻炼也是，学习也是，训练也是，研究竞争对手和市场变化也是......全都不能落下！', 'char_308_swire/CN_005');
INSERT INTO `t_operator_voice` VALUES (3098, 92, '晋升后交谈2', '虽然家族同意我暂时放下家族事业，但我很清楚，龙门想要发展，既要有治安和秩序的维护，也要有商业与金融的推动。无论是哪一个领域，只要龙门需要我，我就会投身其中！', 'char_308_swire/CN_006');
INSERT INTO `t_operator_voice` VALUES (3099, 92, '信赖提升后交谈1', '龙门各署的工作是很复杂的。维护治安，保护民众，疏导交通，贸易协管......唉，每天都忙到腰酸背痛。不过，只要我和部下们做的事对龙门有益我就，哈哈，心满意足！', 'char_308_swire/CN_007');
INSERT INTO `t_operator_voice` VALUES (3100, 92, '信赖提升后交谈2', '博士，为什么罗德岛的训练课程这么短？......资金有限？这怎么行，我要申请调去商务部门！虽、虽然我不喜欢经商，但如果你需要的话，我做这个也......也可以！', 'char_308_swire/CN_008');
INSERT INTO `t_operator_voice` VALUES (3101, 92, '信赖提升后交谈3', '其实我们龙门很需要博士你这样的人才，所以，所以......唔，如果你不喜欢办案，银行业和管理业也可以哦！我，我能为你准备很多机会的！啊？即使在罗德岛也可以合作？......哎呀！！', 'char_308_swire/CN_009');
INSERT INTO `t_operator_voice` VALUES (3102, 92, '闲置', '唔咕咕......好，好累啊......果然连续值班......会......呼......呼......', 'char_308_swire/CN_010');
INSERT INTO `t_operator_voice` VALUES (3103, 92, '干员报到', '龙门高级警司诗怀雅，现在，作为训练顾问，受聘于罗德岛。Dr.{@nickname}，为了龙门的未来，我们两人可得好好相处！', 'char_308_swire/CN_011');
INSERT INTO `t_operator_voice` VALUES (3104, 92, '观看作战记录', '唔唔，这样运用战术理论的话，也行得通！', 'char_308_swire/CN_012');
INSERT INTO `t_operator_voice` VALUES (3105, 92, '精英化晋升1', '晋升？唔，我在龙门的地位也不会提高吧？但如果你喜欢的话，我、我......要我接受，也不是不行！', 'char_308_swire/CN_013');
INSERT INTO `t_operator_voice` VALUES (3106, 92, '精英化晋升2', '也许，你想通过我和龙门缔结友好关系；或者，罗德岛需要我的能力；哪怕，你对我有其它需要......我没法全部保证。但，我会是你最忠诚的伙伴。', 'char_308_swire/CN_014');
INSERT INTO `t_operator_voice` VALUES (3107, 92, '编入队伍', '终于，需要我的智慧了吗！', 'char_308_swire/CN_017');
INSERT INTO `t_operator_voice` VALUES (3108, 92, '任命队长', '可要好好听从我的指挥！', 'char_308_swire/CN_018');
INSERT INTO `t_operator_voice` VALUES (3109, 92, '行动出发', '一口气消灭他们，就能节省开销！', 'char_308_swire/CN_019');
INSERT INTO `t_operator_voice` VALUES (3110, 92, '行动开始', '快点解决吧，可别浪费我宝贵的时间啊！', 'char_308_swire/CN_020');
INSERT INTO `t_operator_voice` VALUES (3111, 92, '选中干员1', '哈？', 'char_308_swire/CN_021');
INSERT INTO `t_operator_voice` VALUES (3112, 92, '选中干员2', '敌人的战术，我已经看得很清楚了！', 'char_308_swire/CN_022');
INSERT INTO `t_operator_voice` VALUES (3113, 92, '部署1', '我的战场，可不只是这里！', 'char_308_swire/CN_023');
INSERT INTO `t_operator_voice` VALUES (3114, 92, '部署2', '哪怕是竞争对手，也需要一点教育！', 'char_308_swire/CN_024');
INSERT INTO `t_operator_voice` VALUES (3115, 92, '作战中1', '时间就是金钱！', 'char_308_swire/CN_025');
INSERT INTO `t_operator_voice` VALUES (3116, 92, '作战中2', '快点快点，都动起来！', 'char_308_swire/CN_026');
INSERT INTO `t_operator_voice` VALUES (3117, 92, '作战中3', '龙门有龙门的骄傲！', 'char_308_swire/CN_027');
INSERT INTO `t_operator_voice` VALUES (3118, 92, '作战中4', '该沽出了！', 'char_308_swire/CN_028');
INSERT INTO `t_operator_voice` VALUES (3119, 92, '4星结束行动', '好呀，战斗和商业是一样的，风险与回报并存！', 'char_308_swire/CN_029');
INSERT INTO `t_operator_voice` VALUES (3120, 92, '3星结束行动', '资本，就该是兼并和收购！胜利，就该是侦破和搜捕！', 'char_308_swire/CN_030');
INSERT INTO `t_operator_voice` VALUES (3121, 92, '非3星结束行动', '不太好，不太好！你还可以做的更好吧？加油，我会和你一起努力的！', 'char_308_swire/CN_031');
INSERT INTO `t_operator_voice` VALUES (3122, 92, '行动失败', '太、太丢脸了！我居然......居然犯下这种失误！', 'char_308_swire/CN_032');
INSERT INTO `t_operator_voice` VALUES (3123, 92, '进驻设施', '还、还不赖......不，这房间还可以再装修下吧！', 'char_308_swire/CN_033');
INSERT INTO `t_operator_voice` VALUES (3124, 92, '戳一下', '大、大胆！', 'char_308_swire/CN_034');
INSERT INTO `t_operator_voice` VALUES (3125, 92, '信赖触摸', '一......一起吃晚饭吧？我，我来买单好了！', 'char_308_swire/CN_036');
INSERT INTO `t_operator_voice` VALUES (3126, 92, '标题', '明日方舟。', 'char_308_swire/CN_037');
INSERT INTO `t_operator_voice` VALUES (3127, 92, '问候', '嗷呜，博士！', 'char_308_swire/CN_042');
INSERT INTO `t_operator_voice` VALUES (3128, 93, '任命助理', '博士，现在起由我担任你的护卫。', 'char_010_chen/CN_001');
INSERT INTO `t_operator_voice` VALUES (3129, 93, '交谈1', '我一直觉得你们罗德岛很可疑，现在也一样。', 'char_010_chen/CN_002');
INSERT INTO `t_operator_voice` VALUES (3130, 93, '交谈2', '你怎么还在盯着我看？你的工作呢？', 'char_010_chen/CN_003');
INSERT INTO `t_operator_voice` VALUES (3131, 93, '交谈3', '哼。', 'char_010_chen/CN_004');
INSERT INTO `t_operator_voice` VALUES (3132, 93, '晋升后交谈1', '龙门的大街小巷我都走过。码头、招牌、坡道，那些风景印在我的心上。我永远都不会忘记。', 'char_010_chen/CN_005');
INSERT INTO `t_operator_voice` VALUES (3133, 93, '晋升后交谈2', '周边地区的局势并不稳定，虽然我会时刻保持警戒，但你也不能疏忽大意。', 'char_010_chen/CN_006');
INSERT INTO `t_operator_voice` VALUES (3134, 93, '信赖提升后交谈1', '也许有一天我会得到那个人的认可，在那之前......', 'char_010_chen/CN_007');
INSERT INTO `t_operator_voice` VALUES (3135, 93, '信赖提升后交谈2', '阿米娅深深信赖着你。看着你们两人站在一起，我偶尔也会觉得，如果我身边，也能有这样一个人......', 'char_010_chen/CN_008');
INSERT INTO `t_operator_voice` VALUES (3136, 93, '信赖提升后交谈3', '我......我从没试过去毫无保留地相信一个人，即使是那个人也......', 'char_010_chen/CN_009');
INSERT INTO `t_operator_voice` VALUES (3137, 93, '闲置', '博士？睡着了？哼，真没紧张感。', 'char_010_chen/CN_010');
INSERT INTO `t_operator_voice` VALUES (3138, 93, '干员报到', '陈，龙门近卫局特别督察组组长。基于一些特殊原因，我要暂时留在这里一段时间。之前的合作中，我想我们已经彼此了解了，请马上带我去工作现场吧。', 'char_010_chen/CN_011');
INSERT INTO `t_operator_voice` VALUES (3139, 93, '观看作战记录', '一切都是为了更好地执行任务。', 'char_010_chen/CN_012');
INSERT INTO `t_operator_voice` VALUES (3140, 93, '精英化晋升1', '晋升？这种事对我来说并不陌生。', 'char_010_chen/CN_013');
INSERT INTO `t_operator_voice` VALUES (3141, 93, '精英化晋升2', '知道了。常规干员无法胜任的任务，全部交给我来处理。', 'char_010_chen/CN_014');
INSERT INTO `t_operator_voice` VALUES (3142, 93, '编入队伍', '没问题。', 'char_010_chen/CN_017');
INSERT INTO `t_operator_voice` VALUES (3143, 93, '任命队长', '各位，记住，只有相互配合，才能高效行动。', 'char_010_chen/CN_018');
INSERT INTO `t_operator_voice` VALUES (3144, 93, '行动出发', '全体人员，做好战斗准备。', 'char_010_chen/CN_019');
INSERT INTO `t_operator_voice` VALUES (3145, 93, '行动开始', '准备好了吗？那么，一切按照计划进行。', 'char_010_chen/CN_020');
INSERT INTO `t_operator_voice` VALUES (3146, 93, '选中干员1', '这里是陈。', 'char_010_chen/CN_021');
INSERT INTO `t_operator_voice` VALUES (3147, 93, '选中干员2', '我随时可以。', 'char_010_chen/CN_022');
INSERT INTO `t_operator_voice` VALUES (3148, 93, '部署1', '行动。', 'char_010_chen/CN_023');
INSERT INTO `t_operator_voice` VALUES (3149, 93, '部署2', '快速解决他们。', 'char_010_chen/CN_024');
INSERT INTO `t_operator_voice` VALUES (3150, 93, '作战中1', '斩！', 'char_010_chen/CN_025');
INSERT INTO `t_operator_voice` VALUES (3151, 93, '作战中2', '闪！', 'char_010_chen/CN_026');
INSERT INTO `t_operator_voice` VALUES (3152, 93, '作战中3', '放下你的武器！', 'char_010_chen/CN_027');
INSERT INTO `t_operator_voice` VALUES (3153, 93, '作战中4', '不要逼我。', 'char_010_chen/CN_028');
INSERT INTO `t_operator_voice` VALUES (3154, 93, '4星结束行动', '罪恶绝不能被容忍，不管用什么办法我都要阻止你们。', 'char_010_chen/CN_029');
INSERT INTO `t_operator_voice` VALUES (3155, 93, '3星结束行动', '是谁给了你们自信前来挑战我？', 'char_010_chen/CN_030');
INSERT INTO `t_operator_voice` VALUES (3156, 93, '非3星结束行动', '伤员请等待医疗队的救援。其他干员保持警戒。', 'char_010_chen/CN_031');
INSERT INTO `t_operator_voice` VALUES (3157, 93, '行动失败', '有序撤退，不要让队伍崩溃！', 'char_010_chen/CN_032');
INSERT INTO `t_operator_voice` VALUES (3158, 93, '进驻设施', '这就是罗德岛的内部设施？意外的宽敞嘛。', 'char_010_chen/CN_033');
INSERT INTO `t_operator_voice` VALUES (3159, 93, '戳一下', '喂！干什么呢！', 'char_010_chen/CN_034');
INSERT INTO `t_operator_voice` VALUES (3160, 93, '信赖触摸', '博士，你心情不错嘛，因为最近作战比较顺利？正巧，我也一样。', 'char_010_chen/CN_036');
INSERT INTO `t_operator_voice` VALUES (3161, 93, '标题', '明日方舟。', 'char_010_chen/CN_037');
INSERT INTO `t_operator_voice` VALUES (3162, 93, '问候', '哦，是博士啊。', 'char_010_chen/CN_042');
INSERT INTO `t_operator_voice` VALUES (3163, 94, '任命助理', '博士，休息时间到了。上一轮工作结束之后，您不是还没离开过座位？休息，休息一下，嗯，就现在。', 'char_298_susuro/CN_001');
INSERT INTO `t_operator_voice` VALUES (3164, 94, '交谈1', '我的任务，是去定期寻访矿石病患者的情况，有些患者还好相处，但有些患者特别难以沟通呢。不过，这种情况对我来说也不是第一次，不必担心，我会让他们好好了解一下医生的重要性。', 'char_298_susuro/CN_002');
INSERT INTO `t_operator_voice` VALUES (3165, 94, '交谈2', '博士，失忆患者要注意的事，您了解过了吗？可不能太劳累，休息和工作同样重要！', 'char_298_susuro/CN_003');
INSERT INTO `t_operator_voice` VALUES (3166, 94, '交谈3', '我知道，总有人把医生的嘱咐当成耳边风，可是在战场上，医生却绝对不会错过任何一声呼救。', 'char_298_susuro/CN_004');
INSERT INTO `t_operator_voice` VALUES (3167, 94, '晋升后交谈1', '医学的重大进步，往往是通过残酷的战争和巨大的灾难来实现的，所以医生注定要和痛苦同行......嗯，这些，我早就想好了，只要能让患者回归他们该有的生活，那些痛苦我就一点也不会害怕。', 'char_298_susuro/CN_005');
INSERT INTO `t_operator_voice` VALUES (3168, 94, '晋升后交谈2', '和您在一起工作的时光，真的很......啊，我是说，很快乐！真是的，明知道我不擅长说这种话，还要装成听不清的样子，博士，不许再这样坏心眼了哦？', 'char_298_susuro/CN_006');
INSERT INTO `t_operator_voice` VALUES (3169, 94, '信赖提升后交谈1', '给，钢笔。为什么我会知道您想要这个？唔唔，我都已经在您身边工作那么多天了，这种程度的了解，理所当然的吧？', 'char_298_susuro/CN_007');
INSERT INTO `t_operator_voice` VALUES (3170, 94, '信赖提升后交谈2', '比起赫默医生和闪灵医生，我的能力还差的很远吧，但不管未来有多困难，我也不会害怕的，没有这种决心的话，怎么对得起您的信赖呢？', 'char_298_susuro/CN_008');
INSERT INTO `t_operator_voice` VALUES (3171, 94, '信赖提升后交谈3', '甲板上的阳光果然好暖和，微风揉着脸的感觉也好舒服......欸，现在可不是在偷懒，因为就算是博士，也会有疲惫的时候吧？所以，把您心里的烦恼，和我说一说吧，就算是小声说出来也没关系哦？', 'char_298_susuro/CN_009');
INSERT INTO `t_operator_voice` VALUES (3172, 94, '闲置', '......这份营养餐的菜单，究竟是谁写出来的啊？内、内容也太可怕了吧！', 'char_298_susuro/CN_010');
INSERT INTO `t_operator_voice` VALUES (3173, 94, '干员报到', '医疗干员苏苏洛向您报道。即使博士您身兼多种要职，是干员们的导师，但医生的话，您也是要听的哦。', 'char_298_susuro/CN_011');
INSERT INTO `t_operator_voice` VALUES (3174, 94, '观看作战记录', '嗯，这些作战记录的笔记我得多抄录几份，给末药她们带去。', 'char_298_susuro/CN_012');
INSERT INTO `t_operator_voice` VALUES (3175, 94, '精英化晋升1', '晋升仪式？唔，要被大家盯着看？是不是还要说些什么，让人难为情的话？呃不是，我一点都不紧张，真的......！', 'char_298_susuro/CN_013');
INSERT INTO `t_operator_voice` VALUES (3176, 94, '精英化晋升2', '能获得这次晋升，不只是为我个人，背后还有医疗组所有同伴们的努力，当然还有博士您的教导，非常，非常的感谢大家！', 'char_298_susuro/CN_014');
INSERT INTO `t_operator_voice` VALUES (3177, 94, '编入队伍', '嗯，请放心吧，我会看护好他们的。', 'char_298_susuro/CN_017');
INSERT INTO `t_operator_voice` VALUES (3178, 94, '任命队长', '都等一下！出战前的体检必须要做完才能出发！', 'char_298_susuro/CN_018');
INSERT INTO `t_operator_voice` VALUES (3179, 94, '行动出发', '医疗组的各位，作战开始后也要按照流程操作哦，请大家做好准备。', 'char_298_susuro/CN_019');
INSERT INTO `t_operator_voice` VALUES (3180, 94, '行动开始', '各位注意安全，尤其别乱碰任何疑似感染源的东西！', 'char_298_susuro/CN_020');
INSERT INTO `t_operator_voice` VALUES (3181, 94, '选中干员1', '我在这里呢！', 'char_298_susuro/CN_021');
INSERT INTO `t_operator_voice` VALUES (3182, 94, '选中干员2', '战场很吵！请您说大声点！', 'char_298_susuro/CN_022');
INSERT INTO `t_operator_voice` VALUES (3183, 94, '部署1', '医生就在这里呢，看这边！', 'char_298_susuro/CN_023');
INSERT INTO `t_operator_voice` VALUES (3184, 94, '部署2', '有没有人受伤？', 'char_298_susuro/CN_024');
INSERT INTO `t_operator_voice` VALUES (3185, 94, '作战中1', '别担心，你的伤很快就会好！', 'char_298_susuro/CN_025');
INSERT INTO `t_operator_voice` VALUES (3186, 94, '作战中2', '别只顾着作战，你还有伤啊！', 'char_298_susuro/CN_026');
INSERT INTO `t_operator_voice` VALUES (3187, 94, '作战中3', '听医生的话！', 'char_298_susuro/CN_027');
INSERT INTO `t_operator_voice` VALUES (3188, 94, '作战中4', '疼就喊出来，别忍着啊！', 'char_298_susuro/CN_028');
INSERT INTO `t_operator_voice` VALUES (3189, 94, '4星结束行动', '呼，作战结束。您也休息一下吧。这么繁重的指挥任务，会让身体吃不消的。', 'char_298_susuro/CN_029');
INSERT INTO `t_operator_voice` VALUES (3190, 94, '3星结束行动', '我方完全没有伤亡。果然，还是博士的指挥值得信服呢。', 'char_298_susuro/CN_030');
INSERT INTO `t_operator_voice` VALUES (3191, 94, '非3星结束行动', '敌人还会来吗？这里还有伤者无法行动，唔......', 'char_298_susuro/CN_031');
INSERT INTO `t_operator_voice` VALUES (3192, 94, '行动失败', '让一让，你们让一让，呃，别挤！伤员要交给医生！', 'char_298_susuro/CN_032');
INSERT INTO `t_operator_voice` VALUES (3193, 94, '进驻设施', '该休息的时候，一定要休息哦？', 'char_298_susuro/CN_033');
INSERT INTO `t_operator_voice` VALUES (3194, 94, '戳一下', '唔唔......', 'char_298_susuro/CN_034');
INSERT INTO `t_operator_voice` VALUES (3195, 94, '信赖触摸', '呼呼，工作之后就该喝一杯才行！博士，帮我拿一瓶果汁，谢谢！', 'char_298_susuro/CN_036');
INSERT INTO `t_operator_voice` VALUES (3196, 94, '标题', '明日方舟。', 'char_298_susuro/CN_037');
INSERT INTO `t_operator_voice` VALUES (3197, 94, '问候', '博士，你好。', 'char_298_susuro/CN_042');
INSERT INTO `t_operator_voice` VALUES (3198, 95, '任命助理', '我会在一边安安静静地调整倾诉者的......保证不会打扰你。', 'char_326_glacus/CN_001');
INSERT INTO `t_operator_voice` VALUES (3199, 95, '交谈1', '为什么大家总是对蓝毒小姐敬而远之呢？是我不太一样？......好吧。', 'char_326_glacus/CN_002');
INSERT INTO `t_operator_voice` VALUES (3200, 95, '交谈2', '博士有什么想说的话吗？没什么？总之......我就在这里。', 'char_326_glacus/CN_003');
INSERT INTO `t_operator_voice` VALUES (3201, 95, '交谈3', '宿舍很热的话会让人没有食欲......啊。这是家族遗传，真的不是在抱怨。', 'char_326_glacus/CN_004');
INSERT INTO `t_operator_voice` VALUES (3202, 95, '晋升后交谈1', '我之所以来到这里......奔跑都做不到的话，会有谁愿意主动离开故土呢？', 'char_326_glacus/CN_005');
INSERT INTO `t_operator_voice` VALUES (3203, 95, '晋升后交谈2', '虽然火神小姐和我一样不喜欢“旧的东西”，但我觉得，我们的想法从根本上还是有差别的。', 'char_326_glacus/CN_006');
INSERT INTO `t_operator_voice` VALUES (3204, 95, '信赖提升后交谈1', '博士，那个，蓝毒小姐说我总是像藏着什么想法似的，我会给人这种感觉吗？偶尔？不会吧......', 'char_326_glacus/CN_007');
INSERT INTO `t_operator_voice` VALUES (3205, 95, '信赖提升后交谈2', '并不是我不太愿意说话，只是以前的日子里，我其实很少有什么开口的权利——所以只是习惯而已！希望{@nickname}不要误会了！', 'char_326_glacus/CN_008');
INSERT INTO `t_operator_voice` VALUES (3206, 95, '信赖提升后交谈3', '对我而言，有两段记忆如奇迹般耀眼。其中之一，是当我第一次借助这孩子奔跑起来的时候，景色万般旖旎，视野是那么的开阔......另一次，唔，就是身在罗德岛的此时此刻吧。', 'char_326_glacus/CN_009');
INSERT INTO `t_operator_voice` VALUES (3207, 95, '闲置', '嗯？唔，博士什么时候睡着的......', 'char_326_glacus/CN_010');
INSERT INTO `t_operator_voice` VALUES (3208, 95, '干员报到', '干员格劳克斯，今日报到。请问我可以回房间了吗？', 'char_326_glacus/CN_011');
INSERT INTO `t_operator_voice` VALUES (3209, 95, '观看作战记录', '嗯？啊，好的，原来如此，我明白了。', 'char_326_glacus/CN_012');
INSERT INTO `t_operator_voice` VALUES (3210, 95, '精英化晋升1', '其实这把武器的名字是我自己取的，也许这才是我心里的想法......欸？刚才宣布的晋升干员原来是我吗？', 'char_326_glacus/CN_013');
INSERT INTO `t_operator_voice` VALUES (3211, 95, '精英化晋升2', '信任吗.....那个，我不知道该怎么表达，但我会继续努力的。', 'char_326_glacus/CN_014');
INSERT INTO `t_operator_voice` VALUES (3212, 95, '编入队伍', '队员们有做好接地措施吗？', 'char_326_glacus/CN_017');
INSERT INTO `t_operator_voice` VALUES (3213, 95, '任命队长', '欸？我是队长？难道刚才不小心就答应下来了......', 'char_326_glacus/CN_018');
INSERT INTO `t_operator_voice` VALUES (3214, 95, '行动出发', '这次能捡到什么有趣的东西呢？', 'char_326_glacus/CN_019');
INSERT INTO `t_operator_voice` VALUES (3215, 95, '行动开始', '电磁脉冲立场准备就绪。', 'char_326_glacus/CN_020');
INSERT INTO `t_operator_voice` VALUES (3216, 95, '选中干员1', '嗯？喊我吗？', 'char_326_glacus/CN_021');
INSERT INTO `t_operator_voice` VALUES (3217, 95, '选中干员2', '电量充足，随时准备。', 'char_326_glacus/CN_022');
INSERT INTO `t_operator_voice` VALUES (3218, 95, '部署1', '地面的触感......', 'char_326_glacus/CN_023');
INSERT INTO `t_operator_voice` VALUES (3219, 95, '部署2', '别担心，顺利登陆。', 'char_326_glacus/CN_024');
INSERT INTO `t_operator_voice` VALUES (3220, 95, '作战中1', '满功率输出！', 'char_326_glacus/CN_025');
INSERT INTO `t_operator_voice` VALUES (3221, 95, '作战中2', '顾不上误伤了......！', 'char_326_glacus/CN_026');
INSERT INTO `t_operator_voice` VALUES (3222, 95, '作战中3', '微波频率增幅！', 'char_326_glacus/CN_027');
INSERT INTO `t_operator_voice` VALUES (3223, 95, '作战中4', '坠落吧！', 'char_326_glacus/CN_028');
INSERT INTO `t_operator_voice` VALUES (3224, 95, '4星结束行动', '敌方反应沉默，请各自检查一下损害情况......', 'char_326_glacus/CN_029');
INSERT INTO `t_operator_voice` VALUES (3225, 95, '3星结束行动', '全单位沉默，脉冲立场解除，辛苦大家了。', 'char_326_glacus/CN_030');
INSERT INTO `t_operator_voice` VALUES (3226, 95, '非3星结束行动', '唔，刚才是不是有敌人逃走了？不追吗？', 'char_326_glacus/CN_031');
INSERT INTO `t_operator_voice` VALUES (3227, 95, '行动失败', '脉冲立场已经解除，快联络救援！', 'char_326_glacus/CN_032');
INSERT INTO `t_operator_voice` VALUES (3228, 95, '进驻设施', '没问题，但先等我找到那颗螺丝钉......', 'char_326_glacus/CN_033');
INSERT INTO `t_operator_voice` VALUES (3229, 95, '戳一下', '啊，走神了。', 'char_326_glacus/CN_034');
INSERT INTO `t_operator_voice` VALUES (3230, 95, '信赖触摸', '博士，能和我出去散散步吗？', 'char_326_glacus/CN_036');
INSERT INTO `t_operator_voice` VALUES (3231, 95, '标题', '明日方舟。', 'char_326_glacus/CN_037');
INSERT INTO `t_operator_voice` VALUES (3232, 95, '问候', '早上好，博士。', 'char_326_glacus/CN_042');
INSERT INTO `t_operator_voice` VALUES (3233, 96, '任命助理', '博士，我会在旁边写报告或者读书，需要的时候随时可以叫我。', 'char_348_ceylon/CN_001');
INSERT INTO `t_operator_voice` VALUES (3234, 96, '交谈1', '努力就会有回报，我一直这么认为，也一直是这么做的。', 'char_348_ceylon/CN_002');
INSERT INTO `t_operator_voice` VALUES (3235, 96, '交谈2', '......咦，矿石病居然还有这种特性吗！这可要立刻做个实验验证一下，博士，我先离开一会儿......嗯，不知道基地里有没有正好合适的观察对象呢......', 'char_348_ceylon/CN_003');
INSERT INTO `t_operator_voice` VALUES (3236, 96, '交谈3', '没有红茶的一天，可不是完整的一天。博士，你也要来一杯吗？我这里有全维多利亚最好的茶叶。', 'char_348_ceylon/CN_004');
INSERT INTO `t_operator_voice` VALUES (3237, 96, '晋升后交谈1', '我从小就和黑在一起，有关她的事，我全都知道，包括她来到我身边之前的经历。啊，不过这个可不能轻易告诉你。', 'char_348_ceylon/CN_005');
INSERT INTO `t_operator_voice` VALUES (3238, 96, '晋升后交谈2', '如果我能找到治疗黑的办法，不就意味着我也能治好其他感染者吗？放心吧，博士，我没有那么自私。但也请你明白，我不会掩饰，黑在我的心中是最重要的这件事。', 'char_348_ceylon/CN_006');
INSERT INTO `t_operator_voice` VALUES (3239, 96, '信赖提升后交谈1', '研究源石的道路十分广阔，博士。不过，对我来说，我只想找到能治疗黑的方法，所以，当我听说了罗德岛的一些事情后，我一直对这里心怀憧憬。', 'char_348_ceylon/CN_007');
INSERT INTO `t_operator_voice` VALUES (3240, 96, '信赖提升后交谈2', '你知道吗，博士，我最讨厌因为黑的感染者身份而排斥她的人。而在这里没有人会排斥她，我也能学到更多的医学知识......来罗德岛真是一个正确的决定。', 'char_348_ceylon/CN_008');
INSERT INTO `t_operator_voice` VALUES (3241, 96, '信赖提升后交谈3', '无论多么优秀的领导者，也有可能因为一念之差而被利益冲昏头脑，你要当心，博士。不过，哼哼，你也不用太担心，有我在，如果有一天你行差踏错，我会纠正你的。', 'char_348_ceylon/CN_009');
INSERT INTO `t_operator_voice` VALUES (3242, 96, '闲置', '博士，坐着睡觉可不是好习惯......好吧，至少给你加条毛毯。', 'char_348_ceylon/CN_010');
INSERT INTO `t_operator_voice` VALUES (3243, 96, '干员报到', '你好，我是锡兰，维多利亚国立大学毕业，专攻方向是源石研究，经过凯尔希医生批准，从今天起在罗德岛实习，请多关照，博士。', 'char_348_ceylon/CN_011');
INSERT INTO `t_operator_voice` VALUES (3244, 96, '观看作战记录', '不愧是罗德岛，在源石技艺的运用方面真是厉害呢。', 'char_348_ceylon/CN_012');
INSERT INTO `t_operator_voice` VALUES (3245, 96, '精英化晋升1', '这样我就是正式得到罗德岛承认了吗？谢谢，博士，我会更加努力的！', 'char_348_ceylon/CN_013');
INSERT INTO `t_operator_voice` VALUES (3246, 96, '精英化晋升2', '罗德岛对矿石病的研究果然名不虚传，在这里学习下去的话，总有一天我一定能把黑的病治好！博士，也请你助我一臂之力吧！', 'char_348_ceylon/CN_014');
INSERT INTO `t_operator_voice` VALUES (3247, 96, '编入队伍', '任务吗？正好，我可以试验一下新的源石运用技巧。', 'char_348_ceylon/CN_017');
INSERT INTO `t_operator_voice` VALUES (3248, 96, '任命队长', '放心吧，我还挺擅长指挥的。', 'char_348_ceylon/CN_018');
INSERT INTO `t_operator_voice` VALUES (3249, 96, '行动出发', '沿途不知道会不会有些稀有的材料呢？', 'char_348_ceylon/CN_019');
INSERT INTO `t_operator_voice` VALUES (3250, 96, '行动开始', '不要慌张，冷静应对。', 'char_348_ceylon/CN_020');
INSERT INTO `t_operator_voice` VALUES (3251, 96, '选中干员1', '伤员在哪里？', 'char_348_ceylon/CN_021');
INSERT INTO `t_operator_voice` VALUES (3252, 96, '选中干员2', '嗯？', 'char_348_ceylon/CN_022');
INSERT INTO `t_operator_voice` VALUES (3253, 96, '部署1', '辅助就交给我吧。', 'char_348_ceylon/CN_023');
INSERT INTO `t_operator_voice` VALUES (3254, 96, '部署2', '伤员不要剧烈活动。', 'char_348_ceylon/CN_024');
INSERT INTO `t_operator_voice` VALUES (3255, 96, '作战中1', '安心战斗吧，各位。', 'char_348_ceylon/CN_025');
INSERT INTO `t_operator_voice` VALUES (3256, 96, '作战中2', '集中精神面对眼前的敌人！', 'char_348_ceylon/CN_026');
INSERT INTO `t_operator_voice` VALUES (3257, 96, '作战中3', '这招应该更符合现在的状况。', 'char_348_ceylon/CN_027');
INSERT INTO `t_operator_voice` VALUES (3258, 96, '4星结束行动', '呼......呵呵，感觉就像是完成了一场困难的实验，虽然很累，不过也很充实。', 'char_348_ceylon/CN_029');
INSERT INTO `t_operator_voice` VALUES (3259, 96, '3星结束行动', '应该没有敌人了吧？那我去周围稍微逛逛。', 'char_348_ceylon/CN_030');
INSERT INTO `t_operator_voice` VALUES (3260, 96, '非3星结束行动', '要是我能像黑一样，肯定不会让他们跑了......', 'char_348_ceylon/CN_031');
INSERT INTO `t_operator_voice` VALUES (3261, 96, '行动失败', '博士，别愣着，来帮忙抬伤员！', 'char_348_ceylon/CN_032');
INSERT INTO `t_operator_voice` VALUES (3262, 96, '进驻设施', '和基地里其他的医生们交流一下好了。', 'char_348_ceylon/CN_033');
INSERT INTO `t_operator_voice` VALUES (3263, 96, '戳一下', '等等，我头发都要乱了！', 'char_348_ceylon/CN_034');
INSERT INTO `t_operator_voice` VALUES (3264, 96, '信赖触摸', '博士该不会又在座位上睡着了吧？', 'char_348_ceylon/CN_036');
INSERT INTO `t_operator_voice` VALUES (3265, 96, '标题', '明日方舟。', 'char_348_ceylon/CN_037');
INSERT INTO `t_operator_voice` VALUES (3266, 96, '问候', '你好，博士，来和我一起喝杯红茶吗？', 'char_348_ceylon/CN_042');
INSERT INTO `t_operator_voice` VALUES (3267, 97, '任命助理', '日程安排我都记录好了，需要的话，随时可以向我确认。', 'char_340_shwaz/CN_001');
INSERT INTO `t_operator_voice` VALUES (3268, 97, '交谈1', '会见血的任务，别让小姐去，让只能在阴影里生存的人去就够了。', 'char_340_shwaz/CN_002');
INSERT INTO `t_operator_voice` VALUES (3269, 97, '交谈2', '我不会告诉你有关小姐的任何私人情报。当然，你的私人情报我也不会告诉任何人。', 'char_340_shwaz/CN_003');
INSERT INTO `t_operator_voice` VALUES (3270, 97, '交谈3', '博士，我是保镖，我的任务是保护你，而不是做你的聊天对象。如果你想找人说话，我建议你去找其他人，不要和小姐一样，总是找我。', 'char_340_shwaz/CN_004');
INSERT INTO `t_operator_voice` VALUES (3271, 97, '晋升后交谈1', '我擅长的东西？杀人，保护人，还有......泡红茶。别露出那种表情，毕竟小姐的手艺......咳，总之，我很擅长。', 'char_340_shwaz/CN_005');
INSERT INTO `t_operator_voice` VALUES (3272, 97, '晋升后交谈2', '博士，根据迄今为止各种任务的危险程度，我认为，除了我的保护之外，你还需要学习一点防身术。否则，就连我也没有自信，能在所有情况下使你安然无恙。', 'char_340_shwaz/CN_006');
INSERT INTO `t_operator_voice` VALUES (3273, 97, '信赖提升后交谈1', '......一旦手上沾染鲜血，就再也不能回头了，博士，我不希望你也跨出这一步，会脏手的事，就交给我来吧。', 'char_340_shwaz/CN_007');
INSERT INTO `t_operator_voice` VALUES (3274, 97, '信赖提升后交谈2', '想听我复仇的经历？你会睡不好觉的，博士。不过......真奇怪，我居然会有想说给你听的冲动......那么，去酒吧吧。这会是个不短的故事。', 'char_340_shwaz/CN_008');
INSERT INTO `t_operator_voice` VALUES (3275, 97, '信赖提升后交谈3', '罪孽深重的人不会有好下场，我心知肚明。只是在遇见小姐和博士你之后......如果能早一点遇见你们，我就不会仅仅是个凶手了。即使是现在也不晚？博士，你这句话，也是真心的吗？', 'char_340_shwaz/CN_009');
INSERT INTO `t_operator_voice` VALUES (3276, 97, '闲置', '......就这么睡着了吗，和小姐一样，真是一点警戒心都没有。', 'char_340_shwaz/CN_010');
INSERT INTO `t_operator_voice` VALUES (3277, 97, '干员报到', '我是锡兰小姐的私人保镖，黑，跟随小姐加入罗德岛。可能的话，希望尽量让我和小姐一起行动。', 'char_340_shwaz/CN_011');
INSERT INTO `t_operator_voice` VALUES (3278, 97, '观看作战记录', '罗德岛充斥着战场上的怪物，这样的战斗，我只能尽量模仿。', 'char_340_shwaz/CN_012');
INSERT INTO `t_operator_voice` VALUES (3279, 97, '精英化晋升1', '现在，我明白了，为什么罗德岛会需要我......你们的理想，比我做过的事要疯狂百倍。', 'char_340_shwaz/CN_013');
INSERT INTO `t_operator_voice` VALUES (3280, 97, '精英化晋升2', '要唤醒我过去的回忆吗，没有问题。如果是你的话，也许能驯化我们战斗中的那些邪恶。请一定，要驯化我。', 'char_340_shwaz/CN_014');
INSERT INTO `t_operator_voice` VALUES (3281, 97, '编入队伍', '任务？知道了。', 'char_340_shwaz/CN_017');
INSERT INTO `t_operator_voice` VALUES (3282, 97, '任命队长', '听我指挥。', 'char_340_shwaz/CN_018');
INSERT INTO `t_operator_voice` VALUES (3283, 97, '行动出发', '走吧。', 'char_340_shwaz/CN_019');
INSERT INTO `t_operator_voice` VALUES (3284, 97, '行动开始', '让他们有来无回。', 'char_340_shwaz/CN_020');
INSERT INTO `t_operator_voice` VALUES (3285, 97, '选中干员1', '哪里需要我？', 'char_340_shwaz/CN_021');
INSERT INTO `t_operator_voice` VALUES (3286, 97, '选中干员2', '在。', 'char_340_shwaz/CN_022');
INSERT INTO `t_operator_voice` VALUES (3287, 97, '部署1', '这个位置......不错。', 'char_340_shwaz/CN_023');
INSERT INTO `t_operator_voice` VALUES (3288, 97, '部署2', '他们跑不了。', 'char_340_shwaz/CN_024');
INSERT INTO `t_operator_voice` VALUES (3289, 97, '作战中1', '我看见你了。', 'char_340_shwaz/CN_025');
INSERT INTO `t_operator_voice` VALUES (3290, 97, '作战中2', '真显眼。', 'char_340_shwaz/CN_026');
INSERT INTO `t_operator_voice` VALUES (3291, 97, '作战中3', '才这点人。', 'char_340_shwaz/CN_027');
INSERT INTO `t_operator_voice` VALUES (3292, 97, '作战中4', '小姐有仁慈之心，我没有。', 'char_340_shwaz/CN_028');
INSERT INTO `t_operator_voice` VALUES (3293, 97, '4星结束行动', '博士，这种危险的任务，你指挥的地方离战场越远越好。', 'char_340_shwaz/CN_029');
INSERT INTO `t_operator_voice` VALUES (3294, 97, '3星结束行动', '按照你的指示，没有漏网之鱼。', 'char_340_shwaz/CN_030');
INSERT INTO `t_operator_voice` VALUES (3295, 97, '非3星结束行动', '确认周围已经安全了，下一步怎么做？', 'char_340_shwaz/CN_031');
INSERT INTO `t_operator_voice` VALUES (3296, 97, '行动失败', '博士，走，我会优先确保你的安全。', 'char_340_shwaz/CN_032');
INSERT INTO `t_operator_voice` VALUES (3297, 97, '进驻设施', '巡逻吗？我知道了。', 'char_340_shwaz/CN_033');
INSERT INTO `t_operator_voice` VALUES (3298, 97, '戳一下', '小心走火，博士。', 'char_340_shwaz/CN_034');
INSERT INTO `t_operator_voice` VALUES (3299, 97, '信赖触摸', '博士，有什么情况吗。', 'char_340_shwaz/CN_036');
INSERT INTO `t_operator_voice` VALUES (3300, 97, '标题', '明日方舟。', 'char_340_shwaz/CN_037');
INSERT INTO `t_operator_voice` VALUES (3301, 97, '问候', '即使在基地里，也不要太过松懈，博士。', 'char_340_shwaz/CN_042');
INSERT INTO `t_operator_voice` VALUES (3302, 98, '任命助理', '博士，我来找你玩啦~', 'char_151_myrtle/CN_001');
INSERT INTO `t_operator_voice` VALUES (3303, 98, '交谈1', '博士，你可不要小看我，在我的老家，第一大道还有三条商店街的孩子们可都是听我号令的。', 'char_151_myrtle/CN_002');
INSERT INTO `t_operator_voice` VALUES (3304, 98, '交谈2', '虽然杜林肯定是因为懒得想才直接拿我们族的名字当代号，但是这样我就被占便宜了欸，“杜林的桃金娘”，听起来不像是她的所有物了吗？', 'char_151_myrtle/CN_003');
INSERT INTO `t_operator_voice` VALUES (3305, 98, '交谈3', '这里的器械虽然原理我都懂，但是用起来感觉好奇怪欸，有的有好多多余的部分，有的又意外的简便。', 'char_151_myrtle/CN_004');
INSERT INTO `t_operator_voice` VALUES (3306, 98, '晋升后交谈1', '这面旗子？啊，博士你眼光不错喔，这面布是我家的桌布，因为怎么也弄不脏所以离家前被我偷偷带出来了，啊哈哈，爸爸现在肯定气死了。', 'char_151_myrtle/CN_005');
INSERT INTO `t_operator_voice` VALUES (3307, 98, '晋升后交谈2', '欸，我的苹果很厉害吗？唔，在我家那边很普通吧。博士你要玩或者想拿去研究的话可以借你，别弄坏了就行~', 'char_151_myrtle/CN_006');
INSERT INTO `t_operator_voice` VALUES (3308, 98, '信赖提升后交谈1', '地上虽然有很多我还不适应的地方，不过好吃的东西真的有好多，我最喜欢罗德岛的食堂啦！', 'char_151_myrtle/CN_007');
INSERT INTO `t_operator_voice` VALUES (3309, 98, '信赖提升后交谈2', '博士，我必须要严肃地指出一个事实，那就是，我已经成年了！而且在杜林里，我的身高是偏高的！你要告诉其他人，不要看到我就下意识地摸我头，还给我塞零食！', 'char_151_myrtle/CN_008');
INSERT INTO `t_operator_voice` VALUES (3310, 98, '信赖提升后交谈3', '我说，博士，你对我的家乡感兴趣吗？我可以带你回去看看！不过，因为回家要走超久的路，所以等我有心情的时候再说吧~', 'char_151_myrtle/CN_009');
INSERT INTO `t_operator_voice` VALUES (3311, 98, '闲置', '博士要休息一下吗，那我旗子借你盖盖。', 'char_151_myrtle/CN_010');
INSERT INTO `t_operator_voice` VALUES (3312, 98, '干员报到', '我来报到啦！你就是博士对吧？可以叫我桃金娘，杜林族的桃金娘。杜林说在这里能找到工作，所以，有什么我能做的事吗？别看我个子小，我还挺厉害的哦。', 'char_151_myrtle/CN_011');
INSERT INTO `t_operator_voice` VALUES (3313, 98, '观看作战记录', '嗯，嗯......这场打的有意思，还有吗，博士？', 'char_151_myrtle/CN_012');
INSERT INTO `t_operator_voice` VALUES (3314, 98, '精英化晋升1', '咦，我升职了吗？那我可不可以去一些之前不能去的地方看看？', 'char_151_myrtle/CN_013');
INSERT INTO `t_operator_voice` VALUES (3315, 98, '精英化晋升2', '我一开始就说了嘛，我可是挺厉害的呢！嗯——哼！', 'char_151_myrtle/CN_014');
INSERT INTO `t_operator_voice` VALUES (3316, 98, '编入队伍', '任务？我去我去！', 'char_151_myrtle/CN_017');
INSERT INTO `t_operator_voice` VALUES (3317, 98, '任命队长', '大家不要离我太远哦。', 'char_151_myrtle/CN_018');
INSERT INTO `t_operator_voice` VALUES (3318, 98, '行动出发', '出——发——！', 'char_151_myrtle/CN_019');
INSERT INTO `t_operator_voice` VALUES (3319, 98, '行动开始', '有我在，大家放心上吧！', 'char_151_myrtle/CN_020');
INSERT INTO `t_operator_voice` VALUES (3320, 98, '选中干员1', '嗯？', 'char_151_myrtle/CN_021');
INSERT INTO `t_operator_voice` VALUES (3321, 98, '选中干员2', '呼啊，差点睡着了......', 'char_151_myrtle/CN_022');
INSERT INTO `t_operator_voice` VALUES (3322, 98, '部署1', '博士，这里景色还不错欸！', 'char_151_myrtle/CN_023');
INSERT INTO `t_operator_voice` VALUES (3323, 98, '部署2', '我来帮助其他人！', 'char_151_myrtle/CN_024');
INSERT INTO `t_operator_voice` VALUES (3324, 98, '作战中1', '我可要认真起来啦！', 'char_151_myrtle/CN_025');
INSERT INTO `t_operator_voice` VALUES (3325, 98, '作战中2', '大家快来我身边~', 'char_151_myrtle/CN_026');
INSERT INTO `t_operator_voice` VALUES (3326, 98, '作战中3', '小苹果，今天也拜托你啦。', 'char_151_myrtle/CN_027');
INSERT INTO `t_operator_voice` VALUES (3327, 98, '4星结束行动', '呼、呼，累死我了，博士，我想回去睡觉。', 'char_151_myrtle/CN_029');
INSERT INTO `t_operator_voice` VALUES (3328, 98, '3星结束行动', '大获全胜！博士，我有派上用场吧？', 'char_151_myrtle/CN_030');
INSERT INTO `t_operator_voice` VALUES (3329, 98, '非3星结束行动', '赢了就好嘛，博士，妈妈说过，斤斤计较老得快哦。', 'char_151_myrtle/CN_031');
INSERT INTO `t_operator_voice` VALUES (3330, 98, '行动失败', '大家快跑！', 'char_151_myrtle/CN_032');
INSERT INTO `t_operator_voice` VALUES (3331, 98, '进驻设施', '哇，罗德岛真大！', 'char_151_myrtle/CN_033');
INSERT INTO `t_operator_voice` VALUES (3332, 98, '戳一下', '咦，这样好有趣！', 'char_151_myrtle/CN_034');
INSERT INTO `t_operator_voice` VALUES (3333, 98, '信赖触摸', '食堂为什么不能24小时都开着呢？', 'char_151_myrtle/CN_036');
INSERT INTO `t_operator_voice` VALUES (3334, 98, '标题', '明日方舟。', 'char_151_myrtle/CN_037');
INSERT INTO `t_operator_voice` VALUES (3335, 98, '问候', '博士，有什么有趣的事情吗？', 'char_151_myrtle/CN_042');
INSERT INTO `t_operator_voice` VALUES (3336, 99, '任命助理', '想熄灭战火，要经历漫长的痛苦；兴起战争，却只需要一瞬间的失误。', 'char_188_helage/CN_001');
INSERT INTO `t_operator_voice` VALUES (3337, 99, '交谈1', '切尔诺伯格的陷落强烈地冲击了我们的诊所，“阿撒兹勒”。我要重建这座诊所、这个孩子们的家——在哪里都无所谓。为家人付出是无需理由的。', 'char_188_helage/CN_002');
INSERT INTO `t_operator_voice` VALUES (3338, 99, '交谈2', '罗德岛的立场虽然值得尊敬，但乌萨斯的感染者面对的处境比想象更恶劣。必须学会如何去运用暴力，只为生存，无关善恶。', 'char_188_helage/CN_003');
INSERT INTO `t_operator_voice` VALUES (3339, 99, '交谈3', '“每个乌萨斯人都应为他们的国家与皇帝感到骄傲”。我曾经也为之奋战过，但现在的我已经失去了热情。时间从我这里夺走了太多。', 'char_188_helage/CN_004');
INSERT INTO `t_operator_voice` VALUES (3340, 99, '晋升后交谈1', '军旅生活教会我两件事。第一，信任你的战友；第二，提防你信任的人。这片大地一直被这种矛盾纠缠着，没人能从中挣脱。', 'char_188_helage/CN_005');
INSERT INTO `t_operator_voice` VALUES (3341, 99, '晋升后交谈2', 'Dr.{@nickname}，你和孩子们没什么区别，未来正等着你们去了解它。像我这样无法背离过去、同样也没有明天的人，会为你们战斗到流尽最后一滴血。', 'char_188_helage/CN_006');
INSERT INTO `t_operator_voice` VALUES (3342, 99, '信赖提升后交谈1', '要与乌萨斯为敌也无妨。我的忠诚已经在我从军时完全地献给了它，而今只剩下幻觉和耻辱与我长久相伴。为了孩子们，我不会退缩，更不会向它投降。', 'char_188_helage/CN_007');
INSERT INTO `t_operator_voice` VALUES (3343, 99, '信赖提升后交谈2', '曾经有一场漫长的战争。当我终于划开敌军指挥部的舱门，却只来得及接过我濒死好友递给我的佩刀与怀表。敌人失去了指挥官，他的死为乌萨斯带来了片刻的胜利，而我，可悲地输掉了这场战争。', 'char_188_helage/CN_008');
INSERT INTO `t_operator_voice` VALUES (3344, 99, '信赖提升后交谈3', '怀表上的照片？被你看见了吗，哈哈。请看，小时候的奈音很可爱吧？现在的她可没这么文静，但她喜欢现在的自己。这就够了。{@nickname}，自由地生活下去吧，这是我平凡的愿望。', 'char_188_helage/CN_009');
INSERT INTO `t_operator_voice` VALUES (3345, 99, '闲置', '别懈怠。在罗德岛，你要时刻保持警惕。', 'char_188_helage/CN_010');
INSERT INTO `t_operator_voice` VALUES (3346, 99, '干员报到', '赫拉格，一介被感染了的武夫。为了重建我的诊所“阿撒兹勒”以及保护我的家人，我愿意和罗德岛合作。事先声明，我不懂医术。', 'char_188_helage/CN_011');
INSERT INTO `t_operator_voice` VALUES (3347, 99, '观看作战记录', '战场变得和过去不同了，它的残酷却从没变过。', 'char_188_helage/CN_012');
INSERT INTO `t_operator_voice` VALUES (3348, 99, '精英化晋升1', '军人的我对此习以为常；作为感染者的我却......感到新奇。感谢你的绶带，Dr.{@nickname}。', 'char_188_helage/CN_013');
INSERT INTO `t_operator_voice` VALUES (3349, 99, '精英化晋升2', '战争不会真正结束，但我相信即使是这片大地也有它的尽头。或许有一天你能真正结束我们的痛苦，Dr.{@nickname}。来吧，向我下令！', 'char_188_helage/CN_014');
INSERT INTO `t_operator_voice` VALUES (3350, 99, '编入队伍', '只有屠夫才渴血。我们是在付出生存的代价。', 'char_188_helage/CN_017');
INSERT INTO `t_operator_voice` VALUES (3351, 99, '任命队长', '我要用乌萨斯军人的标准要求他们。让他们服从命令！', 'char_188_helage/CN_018');
INSERT INTO `t_operator_voice` VALUES (3352, 99, '行动出发', '如果能够结束这一切，赔上我的生命也无妨。', 'char_188_helage/CN_019');
INSERT INTO `t_operator_voice` VALUES (3353, 99, '行动开始', '孱弱的敌人，你们没见过真正的战争。', 'char_188_helage/CN_020');
INSERT INTO `t_operator_voice` VALUES (3354, 99, '选中干员1', '战斗曾是我的天职。', 'char_188_helage/CN_021');
INSERT INTO `t_operator_voice` VALUES (3355, 99, '选中干员2', '生死只在一念之间。', 'char_188_helage/CN_022');
INSERT INTO `t_operator_voice` VALUES (3356, 99, '部署1', '杀死敌人只是下策。', 'char_188_helage/CN_023');
INSERT INTO `t_operator_voice` VALUES (3357, 99, '部署2', '我没法真正离开这里。我知道。', 'char_188_helage/CN_024');
INSERT INTO `t_operator_voice` VALUES (3358, 99, '作战中1', '以刀为鞭，可撩皮肉。', 'char_188_helage/CN_025');
INSERT INTO `t_operator_voice` VALUES (3359, 99, '作战中2', '以鞭行枪，可断兵刃。', 'char_188_helage/CN_026');
INSERT INTO `t_operator_voice` VALUES (3360, 99, '作战中3', '以枪作刀，可劈甲胄！', 'char_188_helage/CN_027');
INSERT INTO `t_operator_voice` VALUES (3361, 99, '作战中4', '“降斩”......你主人的牺牲绝非无谓。', 'char_188_helage/CN_028');
INSERT INTO `t_operator_voice` VALUES (3362, 99, '4星结束行动', '远离纷争的代价就是被卷入更多的纷争。', 'char_188_helage/CN_029');
INSERT INTO `t_operator_voice` VALUES (3363, 99, '3星结束行动', '别看。我去结束他们的挣扎。', 'char_188_helage/CN_030');
INSERT INTO `t_operator_voice` VALUES (3364, 99, '非3星结束行动', '不要大意，每个漏洞都会为敌人的反扑提供机会。', 'char_188_helage/CN_031');
INSERT INTO `t_operator_voice` VALUES (3365, 99, '行动失败', '失败并不可耻，至少你还有机会吸取教训。', 'char_188_helage/CN_032');
INSERT INTO `t_operator_voice` VALUES (3366, 99, '进驻设施', '真希望给孩子们找个能挡风遮雨的地方。', 'char_188_helage/CN_033');
INSERT INTO `t_operator_voice` VALUES (3367, 99, '戳一下', '嗯？', 'char_188_helage/CN_034');
INSERT INTO `t_operator_voice` VALUES (3368, 99, '信赖触摸', '想吃点心？我这里还剩一些。小心蛀牙。', 'char_188_helage/CN_036');
INSERT INTO `t_operator_voice` VALUES (3369, 99, '标题', '明日方舟。', 'char_188_helage/CN_037');
INSERT INTO `t_operator_voice` VALUES (3370, 99, '问候', '气色不错，博士。', 'char_188_helage/CN_042');
INSERT INTO `t_operator_voice` VALUES (3371, 100, '任命助理', '工作流程已经制定完成了，要好好执行哦。', 'char_274_astesi/CN_001');
INSERT INTO `t_operator_voice` VALUES (3372, 100, '交谈1', '天球仪的制造技术是个秘密，但就像您所知道的那样，世间万物都能用等价的东西来交换。所以，只要您的出价合适，我愿意将这秘密转让给你。', 'char_274_astesi/CN_002');
INSERT INTO `t_operator_voice` VALUES (3373, 100, '交谈2', '每天都要观测星体脖子好酸，不知道医疗部的同事有什么好办法......今天的当班医师是华法琳小姐？呃，还是过段时间再看吧。', 'char_274_astesi/CN_003');
INSERT INTO `t_operator_voice` VALUES (3374, 100, '交谈3', '从星辰排列中获取现在与未来的消息，这就是占星，可能和占卜不太一样，但结果总是不变的。所以——您想试试看吗？免费的哦。', 'char_274_astesi/CN_004');
INSERT INTO `t_operator_voice` VALUES (3375, 100, '晋升后交谈1', '“血亲间，趋同的基因，导致，呃，源石产生，共鸣性的，并发......”这才是真正的神秘学，完全理解不了......博士，能麻烦您帮我解读一下体检报告吗。', 'char_274_astesi/CN_005');
INSERT INTO `t_operator_voice` VALUES (3376, 100, '晋升后交谈2', '从受到矿石病感染开始，星辰对我的回应已经越来越微弱了。一定要在力量尽失前想到补救的办法，不然我......博士，您愿意帮助我吗？', 'char_274_astesi/CN_006');
INSERT INTO `t_operator_voice` VALUES (3377, 100, '信赖提升后交谈1', '您想要了解我加入的那个组织？抱歉......我什么都不能说。既是因为誓言所限，也和那些东西绝不能公开言说有关。如果您无论如何想要知道一些，那就......请把它想象成一个与世无争的茶会吧。', 'char_274_astesi/CN_007');
INSERT INTO `t_operator_voice` VALUES (3378, 100, '信赖提升后交谈2', '有时候，真的很羡慕星......不，现在该称呼她为埃琳娜了。我身上缺失的一切，似乎都在她身上。咦，是这样吗，博士？也是呢，因为相异，所以相依，这就是姐妹间的关系吧。', 'char_274_astesi/CN_008');
INSERT INTO `t_operator_voice` VALUES (3379, 100, '信赖提升后交谈3', '夜晚总会结束，群星也终将落幕，如果这就是一切的尽头，那我该做些有意义的事情了。比如？唔......博士，您介意我每天清晨叫醒您吗？', 'char_274_astesi/CN_009');
INSERT INTO `t_operator_voice` VALUES (3380, 100, '闲置', '茶已经备好了，放松一下吧。', 'char_274_astesi/CN_010');
INSERT INTO `t_operator_voice` VALUES (3381, 100, '干员报到', '星象学者，星极，以近卫干员身份任职，今后就由您差遣了，博士。', 'char_274_astesi/CN_011');
INSERT INTO `t_operator_voice` VALUES (3382, 100, '观看作战记录', '看完后不用写心得吗？', 'char_274_astesi/CN_012');
INSERT INTO `t_operator_voice` VALUES (3383, 100, '精英化晋升1', '晋升勋章？很精致的饰品，我会好好保管它的。', 'char_274_astesi/CN_013');
INSERT INTO `t_operator_voice` VALUES (3384, 100, '精英化晋升2', '群星闪耀，见证过往。', 'char_274_astesi/CN_014');
INSERT INTO `t_operator_voice` VALUES (3385, 100, '编入队伍', '我在这里，博士。', 'char_274_astesi/CN_017');
INSERT INTO `t_operator_voice` VALUES (3386, 100, '任命队长', '让我成为众人的星光吧。', 'char_274_astesi/CN_018');
INSERT INTO `t_operator_voice` VALUES (3387, 100, '行动出发', '我明白，已经没有回旋的余地了。', 'char_274_astesi/CN_019');
INSERT INTO `t_operator_voice` VALUES (3388, 100, '行动开始', '群星为我们照亮前路。', 'char_274_astesi/CN_020');
INSERT INTO `t_operator_voice` VALUES (3389, 100, '选中干员1', '我准备好了。', 'char_274_astesi/CN_021');
INSERT INTO `t_operator_voice` VALUES (3390, 100, '选中干员2', '该我了吗，博士。', 'char_274_astesi/CN_022');
INSERT INTO `t_operator_voice` VALUES (3391, 100, '部署1', '别慌，星极，像平常那样来就行。', 'char_274_astesi/CN_023');
INSERT INTO `t_operator_voice` VALUES (3392, 100, '部署2', '该证明自己了。', 'char_274_astesi/CN_024');
INSERT INTO `t_operator_voice` VALUES (3393, 100, '作战中1', '敦实的坚牛，同我一起守护。', 'char_274_astesi/CN_025');
INSERT INTO `t_operator_voice` VALUES (3394, 100, '作战中2', '游禽翱翔星海，借您羽翼一用！', 'char_274_astesi/CN_026');
INSERT INTO `t_operator_voice` VALUES (3395, 100, '作战中3', '悍蛟已至，还想挣脱么。', 'char_274_astesi/CN_027');
INSERT INTO `t_operator_voice` VALUES (3396, 100, '作战中4', '我之剑光，即为星光！', 'char_274_astesi/CN_028');
INSERT INTO `t_operator_voice` VALUES (3397, 100, '4星结束行动', '这就是，星辰都无法昭示的，“我们”的力量......', 'char_274_astesi/CN_029');
INSERT INTO `t_operator_voice` VALUES (3398, 100, '3星结束行动', '辛苦了博士，早早休息吧。', 'char_274_astesi/CN_030');
INSERT INTO `t_operator_voice` VALUES (3399, 100, '非3星结束行动', '如果我再加把劲，结果能变得更好吗？', 'char_274_astesi/CN_031');
INSERT INTO `t_operator_voice` VALUES (3400, 100, '行动失败', '好累......但是，还不能倒下......', 'char_274_astesi/CN_032');
INSERT INTO `t_operator_voice` VALUES (3401, 100, '进驻设施', '我看看，家具的摆放......嗯，没有问题，并不会招来奇怪的东西。', 'char_274_astesi/CN_033');
INSERT INTO `t_operator_voice` VALUES (3402, 100, '戳一下', '等，等等！', 'char_274_astesi/CN_034');
INSERT INTO `t_operator_voice` VALUES (3403, 100, '信赖触摸', '怎，怎么了，我脸上有什么吗？', 'char_274_astesi/CN_036');
INSERT INTO `t_operator_voice` VALUES (3404, 100, '标题', '明日方舟。', 'char_274_astesi/CN_037');
INSERT INTO `t_operator_voice` VALUES (3405, 100, '问候', '早安，博士。', 'char_274_astesi/CN_042');
INSERT INTO `t_operator_voice` VALUES (3406, 101, '任命助理', '就不能找个专业的人来做这些活嘛，文职工作什么的，我这种只懂服从命令的士兵搞不明白的呀！', 'char_260_durnar/CN_001');
INSERT INTO `t_operator_voice` VALUES (3407, 101, '交谈1', '人生已经够艰难啦，来点奶油、霜糖和果酱，调和一下吧。', 'char_260_durnar/CN_002');
INSERT INTO `t_operator_voice` VALUES (3408, 101, '交谈2', '要不是霜叶小姐还抱着她那把显眼的斧子，我绝对认不出现在的她。嗯......那是在战场上，而且我们可能算是敌人呢。不过，看到她在罗德岛生活得不错，我就放心了。', 'char_260_durnar/CN_003');
INSERT INTO `t_operator_voice` VALUES (3409, 101, '交谈3', '博士，别随便拿我的武器呀，这东西刚刚更换了源石，十分危险，握法不对的话......哎小心！我，我送你去医务室吧......', 'char_260_durnar/CN_004');
INSERT INTO `t_operator_voice` VALUES (3410, 101, '晋升后交谈1', '作为重装干员，我们的防御能力——最关键！无论是有机纤维背心的还是陶瓷装甲，只要有防御装备的新产品面世，立刻买来用就对啦。', 'char_260_durnar/CN_005');
INSERT INTO `t_operator_voice` VALUES (3411, 101, '晋升后交谈2', '以前的事情吗？以前......没什么好说的吧，一个脾气很差的士兵能有什么故事，对吧。功勋和徽章，呵，和死去的人的生命相比，这些算得了什么......', 'char_260_durnar/CN_006');
INSERT INTO `t_operator_voice` VALUES (3412, 101, '信赖提升后交谈1', '最近预备队新人们的素质可真不错，就是自行改善生活的本事实在是差了点，当年新兵偷藏压缩饼干的把戏可高明多了。', 'char_260_durnar/CN_007');
INSERT INTO `t_operator_voice` VALUES (3413, 101, '信赖提升后交谈2', '我和后勤部的干员打过招呼了，以后，他们会在所有零食包装上写好“工业原料”，到时候，签字通过就靠你啦，博士。', 'char_260_durnar/CN_008');
INSERT INTO `t_operator_voice` VALUES (3414, 101, '信赖提升后交谈3', '博士？嗯，啊啊，抱歉。有些事情，还是忘不掉啊。如果以前我的指挥官像博士你一样的话，哎......也许，过去就不会发生那种事了。', 'char_260_durnar/CN_009');
INSERT INTO `t_operator_voice` VALUES (3415, 101, '闲置', '想不想来点跳跳糖？我这什么口味都有，你肯定会满意的。', 'char_260_durnar/CN_010');
INSERT INTO `t_operator_voice` VALUES (3416, 101, '干员报到', '磐石般坚硬，雷霆般致命，那就是我，坚雷。请多指教哦，博士。', 'char_260_durnar/CN_011');
INSERT INTO `t_operator_voice` VALUES (3417, 101, '观看作战记录', '啊，不好，爆米花已经吃完了。', 'char_260_durnar/CN_012');
INSERT INTO `t_operator_voice` VALUES (3418, 101, '精英化晋升1', '晋升了？好呀好呀，薪水也涨了吗？太好了！', 'char_260_durnar/CN_013');
INSERT INTO `t_operator_voice` VALUES (3419, 101, '精英化晋升2', '以前的我只知道向前冲、不断地战斗，现在不一样了，我为大家而战，下令吧，博士。', 'char_260_durnar/CN_014');
INSERT INTO `t_operator_voice` VALUES (3420, 101, '编入队伍', '随时待命，哪里需要我？', 'char_260_durnar/CN_017');
INSERT INTO `t_operator_voice` VALUES (3421, 101, '任命队长', '我只有一个要求：安全第一，千万别忘了。', 'char_260_durnar/CN_018');
INSERT INTO `t_operator_voice` VALUES (3422, 101, '行动出发', '出发！哦对了，要注意周围的情况哦。', 'char_260_durnar/CN_019');
INSERT INTO `t_operator_voice` VALUES (3423, 101, '行动开始', '呼啊，战场的空气还是那么糟糕。', 'char_260_durnar/CN_020');
INSERT INTO `t_operator_voice` VALUES (3424, 101, '选中干员1', '是！', 'char_260_durnar/CN_021');
INSERT INTO `t_operator_voice` VALUES (3425, 101, '选中干员2', '已就位！', 'char_260_durnar/CN_022');
INSERT INTO `t_operator_voice` VALUES (3426, 101, '部署1', '支援就拜托你们了。', 'char_260_durnar/CN_023');
INSERT INTO `t_operator_voice` VALUES (3427, 101, '部署2', '掩护我！', 'char_260_durnar/CN_024');
INSERT INTO `t_operator_voice` VALUES (3428, 101, '作战中1', '守住战线！', 'char_260_durnar/CN_025');
INSERT INTO `t_operator_voice` VALUES (3429, 101, '作战中2', '别再做无谓的事了！', 'char_260_durnar/CN_026');
INSERT INTO `t_operator_voice` VALUES (3430, 101, '作战中3', '此路不通！', 'char_260_durnar/CN_027');
INSERT INTO `t_operator_voice` VALUES (3431, 101, '作战中4', '退下！', 'char_260_durnar/CN_028');
INSERT INTO `t_operator_voice` VALUES (3432, 101, '4星结束行动', '听说过荣誉勋章吗，我觉得吧，这东西大概就是为你准备的。', 'char_260_durnar/CN_029');
INSERT INTO `t_operator_voice` VALUES (3433, 101, '3星结束行动', '不用清理战场吗？好吧，那就收工走人咯！', 'char_260_durnar/CN_030');
INSERT INTO `t_operator_voice` VALUES (3434, 101, '非3星结束行动', '敌人还没走远，需要追击吗？', 'char_260_durnar/CN_031');
INSERT INTO `t_operator_voice` VALUES (3435, 101, '行动失败', '放轻松，博士......这个结果，不是你的错......', 'char_260_durnar/CN_032');
INSERT INTO `t_operator_voice` VALUES (3436, 101, '进驻设施', '哇，装修这么豪华，一定花了不少钱吧。', 'char_260_durnar/CN_033');
INSERT INTO `t_operator_voice` VALUES (3437, 101, '戳一下', '谁啊？！', 'char_260_durnar/CN_034');
INSERT INTO `t_operator_voice` VALUES (3438, 101, '信赖触摸', '你喝醉了吧，想听我唱军歌？......可别后悔啊。', 'char_260_durnar/CN_036');
INSERT INTO `t_operator_voice` VALUES (3439, 101, '标题', '明日方舟。', 'char_260_durnar/CN_037');
INSERT INTO `t_operator_voice` VALUES (3440, 101, '问候', '早上好~', 'char_260_durnar/CN_042');
INSERT INTO `t_operator_voice` VALUES (3441, 102, '任命助理', '你叫我来，是有新的狩猎任务吗？哦，没有吗......', 'char_190_clour/CN_001');
INSERT INTO `t_operator_voice` VALUES (3442, 102, '交谈1', '这个爪形吊坠？是我的复仇的纪念品，那些伤害过我同伴的乌萨斯佣兵会把类似的东西当成狗牌，所以我才不会认错他们。', 'char_190_clour/CN_002');
INSERT INTO `t_operator_voice` VALUES (3443, 102, '交谈2', '我的左手是在一次狩猎中被野兽撕碎的，幸好一位机械师帮我制作了义肢，我才能重新拉动弓弦。', 'char_190_clour/CN_003');
INSERT INTO `t_operator_voice` VALUES (3444, 102, '交谈3', '若是以前，我绝对会把嘲讽我伤痕的家伙一箭一箭地射成筛子！不过现在......呵，为了那种人动手，真是一点都不值得。', 'char_190_clour/CN_004');
INSERT INTO `t_operator_voice` VALUES (3445, 102, '晋升后交谈1', '若是有实在难以决断的事就凭直觉去选择，身体是不会骗人的，这可是资深猎手的经验。', 'char_190_clour/CN_005');
INSERT INTO `t_operator_voice` VALUES (3446, 102, '晋升后交谈2', '自从同伴们逝去之后，我一个人生活很久了，所以这种每天都要和人打交道的日子，我还不太适应......但既然你给了我充分的信任，任用我，那我一定得回报你。', 'char_190_clour/CN_006');
INSERT INTO `t_operator_voice` VALUES (3447, 102, '信赖提升后交谈1', '呃，义肢手指好像被小石子卡住了？讨厌，得去找机械师调整一下......不、不用你帮忙，我一个人也能行！', 'char_190_clour/CN_007');
INSERT INTO `t_operator_voice` VALUES (3448, 102, '信赖提升后交谈2', '你说，今后不会让我再失去同伴了？哼，是不是在说大话，我可是要亲眼见证的！', 'char_190_clour/CN_008');
INSERT INTO `t_operator_voice` VALUES (3449, 102, '信赖提升后交谈3', '这里的医生说可以抹去我身上新添的伤疤，但我拒绝了。我要留着它们，因为这是为你战斗时留下的......你，能明白我的意思吗？', 'char_190_clour/CN_009');
INSERT INTO `t_operator_voice` VALUES (3450, 102, '闲置', '睡着了？这个人什么场合都睡得着吗？这种体质倒是挺像我们猎人的......', 'char_190_clour/CN_010');
INSERT INTO `t_operator_voice` VALUES (3451, 102, '干员报到', '红云，猎人。给我工作吧，你不会失望的。', 'char_190_clour/CN_011');
INSERT INTO `t_operator_voice` VALUES (3452, 102, '观看作战记录', '还可以观看更多一些吗？', 'char_190_clour/CN_012');
INSERT INTO `t_operator_voice` VALUES (3453, 102, '精英化晋升1', '谢谢你，可......让我这样的人晋升，真的没问题吗？', 'char_190_clour/CN_013');
INSERT INTO `t_operator_voice` VALUES (3454, 102, '精英化晋升2', '矿石病也好，剩下的生命也好，已经全都无所谓了。现在让我最快乐的事，不就是和你一起并肩作战吗？', 'char_190_clour/CN_014');
INSERT INTO `t_operator_voice` VALUES (3455, 102, '编入队伍', '来了。', 'char_190_clour/CN_017');
INSERT INTO `t_operator_voice` VALUES (3456, 102, '任命队长', '要我当队长？我......真的合适吗？', 'char_190_clour/CN_018');
INSERT INTO `t_operator_voice` VALUES (3457, 102, '行动出发', '大捕猎，即将开幕！', 'char_190_clour/CN_019');
INSERT INTO `t_operator_voice` VALUES (3458, 102, '行动开始', '踏入陷阱的你们，还没明白自己的处境吗？', 'char_190_clour/CN_020');
INSERT INTO `t_operator_voice` VALUES (3459, 102, '选中干员1', '让我上。', 'char_190_clour/CN_021');
INSERT INTO `t_operator_voice` VALUES (3460, 102, '选中干员2', '下令吧。', 'char_190_clour/CN_022');
INSERT INTO `t_operator_voice` VALUES (3461, 102, '部署1', '我闻到风中的猎物气味了。', 'char_190_clour/CN_023');
INSERT INTO `t_operator_voice` VALUES (3462, 102, '部署2', '我就在这等着。', 'char_190_clour/CN_024');
INSERT INTO `t_operator_voice` VALUES (3463, 102, '作战中1', '再不逃的话，你就只有一个下场了！', 'char_190_clour/CN_025');
INSERT INTO `t_operator_voice` VALUES (3464, 102, '作战中2', '别小看荒野的子民！', 'char_190_clour/CN_026');
INSERT INTO `t_operator_voice` VALUES (3465, 102, '作战中3', '再看这一箭！', 'char_190_clour/CN_027');
INSERT INTO `t_operator_voice` VALUES (3466, 102, '作战中4', '这一箭，绝对会射穿你的喉咙！', 'char_190_clour/CN_028');
INSERT INTO `t_operator_voice` VALUES (3467, 102, '4星结束行动', '再强大的猎物，也只是猎物而已。', 'char_190_clour/CN_029');
INSERT INTO `t_operator_voice` VALUES (3468, 102, '3星结束行动', '真厉害。如果你生在叙拉古，一定会是位技艺超绝的猎人。', 'char_190_clour/CN_030');
INSERT INTO `t_operator_voice` VALUES (3469, 102, '非3星结束行动', '猎物逃走了，我去追。', 'char_190_clour/CN_031');
INSERT INTO `t_operator_voice` VALUES (3470, 102, '行动失败', '不能就这样结束......', 'char_190_clour/CN_032');
INSERT INTO `t_operator_voice` VALUES (3471, 102, '进驻设施', '有墙和天花板，已经是很棒的屋子了啊。', 'char_190_clour/CN_033');
INSERT INTO `t_operator_voice` VALUES (3472, 102, '戳一下', '呣......', 'char_190_clour/CN_034');
INSERT INTO `t_operator_voice` VALUES (3473, 102, '信赖触摸', '......今天的安排吗？帮你做任务，对着敌人射箭，把箭射光；任务结束，吃饭睡觉。就这么简单。', 'char_190_clour/CN_036');
INSERT INTO `t_operator_voice` VALUES (3474, 102, '标题', '明日方舟。', 'char_190_clour/CN_037');
INSERT INTO `t_operator_voice` VALUES (3475, 102, '问候', '哈，是你啊。', 'char_190_clour/CN_042');
INSERT INTO `t_operator_voice` VALUES (3476, 103, '任命助理', '你可以安心值班，博士。破片地雷已经架设完毕，没有人能干扰你的工作和休息。', 'char_279_excu/CN_001');
INSERT INTO `t_operator_voice` VALUES (3477, 103, '交谈1', '虽然不知道是谁的手笔，但我很清楚罗德岛内部有着专门对抗渗透、暗杀与内部破坏的专门人员，他们潜藏在舰船各处。错觉？不是的。我不会产生幻觉，博士。', 'char_279_excu/CN_002');
INSERT INTO `t_operator_voice` VALUES (3478, 103, '交谈2', '我为拉特兰中庭公证所服务，因此我甚少在拉特兰生活。即使生活在其他城邦，拉特兰公民的权益的方方面面依然有赖公证所的支持，譬如说大多数情况下，我与将死之人的联系比活人更紧密。', 'char_279_excu/CN_003');
INSERT INTO `t_operator_voice` VALUES (3479, 103, '交谈3', '控制系统拒绝访问？请允许我检查一下。嗯，是命令错位导致的前端崩溃，和框架没关系，很好解决。之后有类似的问题都可以找我，比起人类，我对机器的理解更深。', 'char_279_excu/CN_004');
INSERT INTO `t_operator_voice` VALUES (3480, 103, '晋升后交谈1', '罗德岛也有不少使用铳器的萨科塔人。我与他们不同，他们会因禁令而拒绝射击同类；而我，如有必要，我会毫不犹豫地射击其他公民。当然，现在还没有这种需求，大多数同类我用拳掌就能对付。', 'char_279_excu/CN_005');
INSERT INTO `t_operator_voice` VALUES (3481, 103, '晋升后交谈2', '不用顾忌公证所。他们都是些明事理的人，既然我和罗德岛签订的协约符合规定，他们就绝不会为难你。我认为你也很适合公证所的工作，他们很欢迎外籍员工。', 'char_279_excu/CN_006');
INSERT INTO `t_operator_voice` VALUES (3482, 103, '信赖提升后交谈1', '我有一位自小就拥有绝佳音乐天赋的远亲。与其他人的期待完全相反，她凭恃才能走上了混乱的道路。幸运的是，她似乎十分厌恶我，因为我既看不见她描绘的幻觉，也不知道情感对人有什么意义。', 'char_279_excu/CN_007');
INSERT INTO `t_operator_voice` VALUES (3483, 103, '信赖提升后交谈2', '秘密的纠纷比公开的多，混乱的局面比有序的多，第一时间拔出武器攻击我的人远比敞开大门欢迎我的人多。这无所谓，只要是委托我都能完成，无论是代死人杀死活人，还是代活人创造死人。', 'char_279_excu/CN_008');
INSERT INTO `t_operator_voice` VALUES (3484, 103, '信赖提升后交谈3', '被人当作怪胎是件稀松平常的事，因为我确实不能理解其他人，也不愿和他们往来。相反，像今天一样与你交谈是件有趣的事，你和公证所都没有因为我的处世方式否定我，这让我感觉很自在。谢谢。', 'char_279_excu/CN_009');
INSERT INTO `t_operator_voice` VALUES (3485, 103, '闲置', '休息是必要的，博士。请好好休息。', 'char_279_excu/CN_010');
INSERT INTO `t_operator_voice` VALUES (3486, 103, '干员报到', '罗德岛的博士，你好。我是与贵司签署了清理协议的拉特兰公民，这里是我的证件。一定要知道怎么称呼我？你可以叫我送葬人，如果你坚持的话。', 'char_279_excu/CN_011');
INSERT INTO `t_operator_voice` VALUES (3487, 103, '观看作战记录', '这些战斗有一定的参考价值。', 'char_279_excu/CN_012');
INSERT INTO `t_operator_voice` VALUES (3488, 103, '精英化晋升1', '感谢你。下次战斗需要的子弹我已经全部制造完毕了，如果是更糟糕的状况，请允许我动用重火力。晋升？我已经谢过你了。', 'char_279_excu/CN_013');
INSERT INTO `t_operator_voice` VALUES (3489, 103, '精英化晋升2', '再次的晋升？我明白了，一定是有艰巨的潜入任务，甚至是要投入敌人内部行事吧。如果你需要的话，我没有意见。什么？就仅仅是晋升？为什么？', 'char_279_excu/CN_014');
INSERT INTO `t_operator_voice` VALUES (3490, 103, '编入队伍', '条律中的语句需要执行者去实现它的效力。', 'char_279_excu/CN_017');
INSERT INTO `t_operator_voice` VALUES (3491, 103, '任命队长', '我不擅长和他人共事。', 'char_279_excu/CN_018');
INSERT INTO `t_operator_voice` VALUES (3492, 103, '行动出发', '启程。', 'char_279_excu/CN_019');
INSERT INTO `t_operator_voice` VALUES (3493, 103, '行动开始', '任务目标，消灭所有敌人。', 'char_279_excu/CN_020');
INSERT INTO `t_operator_voice` VALUES (3494, 103, '选中干员1', '开始吧。', 'char_279_excu/CN_021');
INSERT INTO `t_operator_voice` VALUES (3495, 103, '选中干员2', '你的请求我确认过了。', 'char_279_excu/CN_022');
INSERT INTO `t_operator_voice` VALUES (3496, 103, '部署1', '胜利是种幻觉。', 'char_279_excu/CN_023');
INSERT INTO `t_operator_voice` VALUES (3497, 103, '部署2', '只有条律是真实的。', 'char_279_excu/CN_024');
INSERT INTO `t_operator_voice` VALUES (3498, 103, '作战中1', '我从不浪费子弹。', 'char_279_excu/CN_025');
INSERT INTO `t_operator_voice` VALUES (3499, 103, '作战中2', '我完成生者与死者的嘱托。', 'char_279_excu/CN_026');
INSERT INTO `t_operator_voice` VALUES (3500, 103, '作战中3', '最大火力。', 'char_279_excu/CN_027');
INSERT INTO `t_operator_voice` VALUES (3501, 103, '作战中4', '如果必要，我会打破禁忌。', 'char_279_excu/CN_028');
INSERT INTO `t_operator_voice` VALUES (3502, 103, '4星结束行动', '任务完成。成功履行所有合约。', 'char_279_excu/CN_029');
INSERT INTO `t_operator_voice` VALUES (3503, 103, '3星结束行动', '任务完成。', 'char_279_excu/CN_030');
INSERT INTO `t_operator_voice` VALUES (3504, 103, '非3星结束行动', '任务完成，结果有缺陷。', 'char_279_excu/CN_031');
INSERT INTO `t_operator_voice` VALUES (3505, 103, '行动失败', '任务失败。', 'char_279_excu/CN_032');
INSERT INTO `t_operator_voice` VALUES (3506, 103, '进驻设施', '不必考虑我，我不需要稳定的居所。', 'char_279_excu/CN_033');
INSERT INTO `t_operator_voice` VALUES (3507, 103, '戳一下', '我感到迷惑。', 'char_279_excu/CN_034');
INSERT INTO `t_operator_voice` VALUES (3508, 103, '信赖触摸', '博士，需要我做些什么？', 'char_279_excu/CN_036');
INSERT INTO `t_operator_voice` VALUES (3509, 103, '标题', '明日方舟。', 'char_279_excu/CN_037');
INSERT INTO `t_operator_voice` VALUES (3510, 103, '问候', '博士，请多加小心。', 'char_279_excu/CN_042');
INSERT INTO `t_operator_voice` VALUES (3511, 104, '任命助理', '{@nickname}，今日的战场与往日相比太脆弱、太稚嫩了，这会让武器生锈。一旦武器生锈，战士离死亡也就不远了。', 'char_131_flameb/CN_001');
INSERT INTO `t_operator_voice` VALUES (3512, 104, '交谈1', '生命只是湮灭之前的一小段历程，趁你还能看见舷外的夕阳，好好珍惜吧，{@nickname}。', 'char_131_flameb/CN_002');
INSERT INTO `t_operator_voice` VALUES (3513, 104, '交谈2', '实战演习？我没有兴趣。罗德岛不乏强大的战士，然而，在游戏中挥霍体力只是无谓的浪费。只有在生死交错的刹那，我们的刀与剑才有相互碰撞的价值。', 'char_131_flameb/CN_003');
INSERT INTO `t_operator_voice` VALUES (3514, 104, '交谈3', '不关心感染者的境遇？不要误会，我不过是讨厌徒劳的战斗。真正值得我流血的敌人还没出现，仅此而已。', 'char_131_flameb/CN_004');
INSERT INTO `t_operator_voice` VALUES (3515, 104, '晋升后交谈1', '团队合作会夺去全力厮杀所解放出的本能，用信任与谋略换来一些平稳的幻觉。而幻觉会取走你的性命，{@nickname}。', 'char_131_flameb/CN_005');
INSERT INTO `t_operator_voice` VALUES (3516, 104, '晋升后交谈2', '这把刀，枭首了两位亲王，而另一把刺死了三个将军。混乱的大地上，什么事情都有可能发生。我不知道我的命会被谁拿去，只希望过程会比我刺杀那些人时更精彩。', 'char_131_flameb/CN_006');
INSERT INTO `t_operator_voice` VALUES (3517, 104, '信赖提升后交谈1', '闪灵吗，可惜，她原本可以是个很强的敌手，只是在“赦罪师”们卷入那种事情以后，就连她也觉得自己的剑刃沾满了污秽吧。不信任自己武器的剑士不值一提。', 'char_131_flameb/CN_007');
INSERT INTO `t_operator_voice` VALUES (3518, 104, '信赖提升后交谈2', '你知道那场战争吧，{@nickname}。杀死所有伏击者后，我和残存的部下冲进了背叛者的堡垒，最后活下来的只有我一个人。这场战斗究竟因何而起？你很清楚，我见过你，而且我记性很好。', 'char_131_flameb/CN_008');
INSERT INTO `t_operator_voice` VALUES (3519, 104, '信赖提升后交谈3', '{@nickname}，帮我拿一下柜子上的种子，谢谢。是，其实我喜欢花。无论它们的绽放有多么灿烂，最后花瓣也都会在风中凋零枯萎。下一次花期是什么时候呢，{@nickname}？', 'char_131_flameb/CN_009');
INSERT INTO `t_operator_voice` VALUES (3520, 104, '闲置', '警醒能延续你的生命，{@nickname}。', 'char_131_flameb/CN_010');
INSERT INTO `t_operator_voice` VALUES (3521, 104, '干员报到', '又见面了，{@nickname}。不要紧，我知道你现在的情况，也对你没有威胁。请把我当做武器来运用，博士。', 'char_131_flameb/CN_011');
INSERT INTO `t_operator_voice` VALUES (3522, 104, '观看作战记录', '一个优秀战士渴求的技艺是没法被记录下来的。', 'char_131_flameb/CN_012');
INSERT INTO `t_operator_voice` VALUES (3523, 104, '精英化晋升1', '晋升？原来你也会在意这种事情。', 'char_131_flameb/CN_013');
INSERT INTO `t_operator_voice` VALUES (3524, 104, '精英化晋升2', '我没有作为感染者的自觉。也许罗德岛能给我一点提示，我不在乎，但你们有自己的考量。我接受。', 'char_131_flameb/CN_014');
INSERT INTO `t_operator_voice` VALUES (3525, 104, '编入队伍', '啊，久违的腥风血雨。', 'char_131_flameb/CN_017');
INSERT INTO `t_operator_voice` VALUES (3526, 104, '任命队长', '我做过队长，下场不怎么好。', 'char_131_flameb/CN_018');
INSERT INTO `t_operator_voice` VALUES (3527, 104, '行动出发', '我将浴血。', 'char_131_flameb/CN_019');
INSERT INTO `t_operator_voice` VALUES (3528, 104, '行动开始', '开始吧。', 'char_131_flameb/CN_020');
INSERT INTO `t_operator_voice` VALUES (3529, 104, '选中干员1', '不要插手。', 'char_131_flameb/CN_021');
INSERT INTO `t_operator_voice` VALUES (3530, 104, '选中干员2', '做你该做的事。', 'char_131_flameb/CN_022');
INSERT INTO `t_operator_voice` VALUES (3531, 104, '部署1', '那么，谁来做我的对手？', 'char_131_flameb/CN_023');
INSERT INTO `t_operator_voice` VALUES (3532, 104, '部署2', '是你吗？还是你？', 'char_131_flameb/CN_024');
INSERT INTO `t_operator_voice` VALUES (3533, 104, '作战中1', '刀与火，先行。', 'char_131_flameb/CN_025');
INSERT INTO `t_operator_voice` VALUES (3534, 104, '作战中2', '血与骨，后至。', 'char_131_flameb/CN_026');
INSERT INTO `t_operator_voice` VALUES (3535, 104, '作战中3', '你死时会呼唤谁的名字？', 'char_131_flameb/CN_027');
INSERT INTO `t_operator_voice` VALUES (3536, 104, '作战中4', '与我无关。', 'char_131_flameb/CN_028');
INSERT INTO `t_operator_voice` VALUES (3537, 104, '4星结束行动', '这场战斗满足你了吗，{@nickname}？', 'char_131_flameb/CN_029');
INSERT INTO `t_operator_voice` VALUES (3538, 104, '3星结束行动', '羸弱的敌人证明不了什么，去寻找更严苛的战场吧。', 'char_131_flameb/CN_030');
INSERT INTO `t_operator_voice` VALUES (3539, 104, '非3星结束行动', '适当的手下留情也许会成为孵化强敌的契机，就随他们去吧。', 'char_131_flameb/CN_031');
INSERT INTO `t_operator_voice` VALUES (3540, 104, '行动失败', '你们走吧，消灭追兵这种事，向来是我去做的。', 'char_131_flameb/CN_032');
INSERT INTO `t_operator_voice` VALUES (3541, 104, '进驻设施', '挺安静的。', 'char_131_flameb/CN_033');
INSERT INTO `t_operator_voice` VALUES (3542, 104, '戳一下', '哦？', 'char_131_flameb/CN_034');
INSERT INTO `t_operator_voice` VALUES (3543, 104, '信赖触摸', '砥石似乎用完了......', 'char_131_flameb/CN_036');
INSERT INTO `t_operator_voice` VALUES (3544, 104, '标题', '明日方舟。', 'char_131_flameb/CN_037');
INSERT INTO `t_operator_voice` VALUES (3545, 104, '问候', '不错，你还活着。', 'char_131_flameb/CN_042');
INSERT INTO `t_operator_voice` VALUES (3546, 105, '任命助理', '在下次探险之前我会一直待在你身边的，博士。有需要我的地方，请别顾虑什么，大胆地派遣我吧。', 'char_248_mgllan/CN_001');
INSERT INTO `t_operator_voice` VALUES (3547, 105, '交谈1', '其实我知道博士很久了哦！嗯嗯，像是梅尔和缪缪她们，都和我说过博士你的，所以我才会想来罗德岛来看看博士你究竟是什么样子......我，我还给你带了小礼物哦！给！', 'char_248_mgllan/CN_002');
INSERT INTO `t_operator_voice` VALUES (3548, 105, '交谈2', '这个？这个是大帝的挂坠，就是企鹅物流的那个大帝呀，以前去企鹅物流的时候见过它好多次，真的好可爱，每次过去都会摸上好久！博士没有摸过吗？不敢？为什么？？', 'char_248_mgllan/CN_003');
INSERT INTO `t_operator_voice` VALUES (3549, 105, '交谈3', '伊芙芙最近好点了吗，在我上一次离开莱茵生命的时候，她的状态有点糟糕，真的好担心......', 'char_248_mgllan/CN_004');
INSERT INTO `t_operator_voice` VALUES (3550, 105, '晋升后交谈1', '梅尔最近有造什么新东西吗，我好久没有做过新的装备，也该和她一起做些研究啦。对了博士，悄悄告诉你个秘密，梅尔被人打扰的时候可是会——欸，你已经被咬过了？！', 'char_248_mgllan/CN_005');
INSERT INTO `t_operator_voice` VALUES (3551, 105, '晋升后交谈2', '我的无人机和咪波、西绪福斯、柯克西卡什么的，从操控原理上就不一样，使用数量有限制也是没有办法的事啊。不过，新的系统已经在开发了，过不了多久，哼哼哼，尽情期待吧！', 'char_248_mgllan/CN_006');
INSERT INTO `t_operator_voice` VALUES (3552, 105, '信赖提升后交谈1', '极北吗？那是个冰冷又残酷的梦境，但是啊，就是在那样的地方，埋藏着我的梦想。为了让美梦成真，我愿意一切付出努力去获得它。', 'char_248_mgllan/CN_007');
INSERT INTO `t_operator_voice` VALUES (3553, 105, '信赖提升后交谈2', '“小小园丁，小小花，开在屋檐下，没人照料它......”这是伊芙芙以前最爱的读本，可能是因为主人公和她境遇相同吧。但......她好像忘记了这本书，真奇怪。', 'char_248_mgllan/CN_008');
INSERT INTO `t_operator_voice` VALUES (3554, 105, '信赖提升后交谈3', '极北啊......那是个既神秘又美丽的地方。高耸的断崖，缀满积雪的孤独大树，闪耀着光芒的源石棱晶，绵延至大地尽头的无垠雪地......博士，一起去吧。和我一起去吧，好吗？', 'char_248_mgllan/CN_009');
INSERT INTO `t_operator_voice` VALUES (3555, 105, '闲置', '博士？博士不可以！如果睡着的话，会没命的！', 'char_248_mgllan/CN_010');
INSERT INTO `t_operator_voice` VALUES (3556, 105, '干员报到', '莱茵生命科考专员麦哲伦，发现了一艘神秘的科研船？哇哇哇，你，你是罗德岛的博士吧，啊嗯......要不要加入我的探险队呢？很有趣的！', 'char_248_mgllan/CN_011');
INSERT INTO `t_operator_voice` VALUES (3557, 105, '观看作战记录', '效率提升了呢。', 'char_248_mgllan/CN_012');
INSERT INTO `t_operator_voice` VALUES (3558, 105, '精英化晋升1', '升级完成！现在设备的反应速度，要比之前快上一倍哦。', 'char_248_mgllan/CN_013');
INSERT INTO `t_operator_voice` VALUES (3559, 105, '精英化晋升2', '如果博士想看看我研究的成果，我也必须要展现一下我的实力了！哼哼哼，事先说好，我可是很强的，可不要被吓到哦！', 'char_248_mgllan/CN_014');
INSERT INTO `t_operator_voice` VALUES (3560, 105, '编入队伍', '勘探和支援就交给我吧！', 'char_248_mgllan/CN_017');
INSERT INTO `t_operator_voice` VALUES (3561, 105, '任命队长', '各位，要注意保持联络哦，哔哔！', 'char_248_mgllan/CN_018');
INSERT INTO `t_operator_voice` VALUES (3562, 105, '行动出发', '还得调试下设备......好了！', 'char_248_mgllan/CN_019');
INSERT INTO `t_operator_voice` VALUES (3563, 105, '行动开始', '无人机调试完毕，随时可以升空。', 'char_248_mgllan/CN_020');
INSERT INTO `t_operator_voice` VALUES (3564, 105, '选中干员1', '嗯！', 'char_248_mgllan/CN_021');
INSERT INTO `t_operator_voice` VALUES (3565, 105, '选中干员2', '需要我的技术了吗？', 'char_248_mgllan/CN_022');
INSERT INTO `t_operator_voice` VALUES (3566, 105, '部署1', '开始工作！', 'char_248_mgllan/CN_023');
INSERT INTO `t_operator_voice` VALUES (3567, 105, '部署2', '设备运行良好！', 'char_248_mgllan/CN_024');
INSERT INTO `t_operator_voice` VALUES (3568, 105, '作战中1', '目标就位了吗？好，无人机激活！', 'char_248_mgllan/CN_025');
INSERT INTO `t_operator_voice` VALUES (3569, 105, '作战中2', '就是现在！雪盲可是很可怕的哦！', 'char_248_mgllan/CN_026');
INSERT INTO `t_operator_voice` VALUES (3570, 105, '作战中3', '3......2......1！', 'char_248_mgllan/CN_027');
INSERT INTO `t_operator_voice` VALUES (3571, 105, '作战中4', '“花啊！绽放吧！”', 'char_248_mgllan/CN_028');
INSERT INTO `t_operator_voice` VALUES (3572, 105, '4星结束行动', '当心啊，那里是扫描盲区！呃，已经清空了？不愧是博士，真可靠呢。', 'char_248_mgllan/CN_029');
INSERT INTO `t_operator_voice` VALUES (3573, 105, '3星结束行动', '没有其他敌对反应了。', 'char_248_mgllan/CN_030');
INSERT INTO `t_operator_voice` VALUES (3574, 105, '非3星结束行动', '附近还有人，要追击吗？', 'char_248_mgllan/CN_031');
INSERT INTO `t_operator_voice` VALUES (3575, 105, '行动失败', '被包抄了？是我的失误......', 'char_248_mgllan/CN_032');
INSERT INTO `t_operator_voice` VALUES (3576, 105, '进驻设施', '唔？居，居然有地暖！啊~太好了~', 'char_248_mgllan/CN_033');
INSERT INTO `t_operator_voice` VALUES (3577, 105, '戳一下', '唉？', 'char_248_mgllan/CN_034');
INSERT INTO `t_operator_voice` VALUES (3578, 105, '信赖触摸', '走嘛走嘛，溜冰没那么可怕的，有我在呢。', 'char_248_mgllan/CN_036');
INSERT INTO `t_operator_voice` VALUES (3579, 105, '标题', '明日方舟。', 'char_248_mgllan/CN_037');
INSERT INTO `t_operator_voice` VALUES (3580, 105, '问候', '有什么事吗？', 'char_248_mgllan/CN_042');
INSERT INTO `t_operator_voice` VALUES (3581, 106, '任命助理', '哟，博士，是不是到我换班的时间了？嗯？你说，我好像是忽然从哪儿冒出来的？哪有，我可是刚来办公室啊。', 'char_355_ethan/CN_001');
INSERT INTO `t_operator_voice` VALUES (3582, 106, '交谈1', '我上一份工作是在整合运动，这个我不是说过很多次了吗？背景调查之类的，我可是主动配合你们做的啊！哎，我在整合运动什么事都没做过，连洗心革面什么的都用不着啊。', 'char_355_ethan/CN_002');
INSERT INTO `t_operator_voice` VALUES (3583, 106, '交谈2', '要说我对整合运动有什么不满，伙食啊！当然是伙食！我那小队的主食就只有黑面包，硬得像块石头，扔进锅里随随便便炖成汤。我说，对自己的肠胃好一点啊。', 'char_355_ethan/CN_003');
INSERT INTO `t_operator_voice` VALUES (3584, 106, '交谈3', '你和整合运动的“幽灵”部队应该接触过很多次了吧？我以前就在那工作，任务只是做些侦查，从不和人动手，无聊时就和人打赌，谁被发现的次数最少，现在想想，我根本是在欺负人啊。', 'char_355_ethan/CN_004');
INSERT INTO `t_operator_voice` VALUES (3585, 106, '晋升后交谈1', '加入整合运动之前的事？当然是整天和抓感染者的人捉迷藏了，可我来去无形，那群追捕者只有目瞪口呆的份，哈哈......只是，总是这么躲着，对感染者们的处境也是没有任何帮助的。', 'char_355_ethan/CN_005');
INSERT INTO `t_operator_voice` VALUES (3586, 106, '晋升后交谈2', '当初，我躲进那些地下组织，曾经也想过去救助别的感染者，可渐渐的这颗心也变脏了。好在，现在我已经洗得干干净净了。什么意思？不要再问，不要再问喽。', 'char_355_ethan/CN_006');
INSERT INTO `t_operator_voice` VALUES (3587, 106, '信赖提升后交谈1', '“变色”这法术可复杂着呢！先得熟知这些玩意儿的材质和触感，每一条墙壁裂缝的形状，每一片泥土和树林的颜色，不然很快就会露怯，被抓到也是分分钟的事。', 'char_355_ethan/CN_007');
INSERT INTO `t_operator_voice` VALUES (3588, 106, '信赖提升后交谈2', '明明办公室只有你一个人，一转头却发现零食没了？嗯，这样啊，我明白了，我一定会抓住犯人的，嗯。', 'char_355_ethan/CN_008');
INSERT INTO `t_operator_voice` VALUES (3589, 106, '信赖提升后交谈3', '这是这次任务地点的布局？啊，明白了。哎，你真厉害啊，每次的战术都特别有成效，难怪整合运动被你打得落花流水。好，那我走了！', 'char_355_ethan/CN_009');
INSERT INTO `t_operator_voice` VALUES (3590, 106, '闲置', '欸？房门没锁吗？', 'char_355_ethan/CN_010');
INSERT INTO `t_operator_voice` VALUES (3591, 106, '干员报到', '你问我在哪儿？就在你面前！叫我伊桑吧。你问我刚才发生了啥？哈哈，现在还不能说哦！', 'char_355_ethan/CN_011');
INSERT INTO `t_operator_voice` VALUES (3592, 106, '观看作战记录', '我在哪儿？这不是在这儿吗？你看这录像，就这屏幕左上角，就左上角！', 'char_355_ethan/CN_012');
INSERT INTO `t_operator_voice` VALUES (3593, 106, '精英化晋升1', '哈哈，我说过吧，你肯定会喜欢上让我去做活的那种感觉。', 'char_355_ethan/CN_013');
INSERT INTO `t_operator_voice` VALUES (3594, 106, '精英化晋升2', '唉，博士，你可得好好记住我们这些人呐。尤其像我这样的，也许死在战场上都没人会发现我啊。可千万要记得我在哪儿啊，就交给你了！', 'char_355_ethan/CN_014');
INSERT INTO `t_operator_voice` VALUES (3595, 106, '编入队伍', '你说刚才有一面墙在你眼前移动了？哈哈，怎么可能有那么夸张的事啊。', 'char_355_ethan/CN_017');
INSERT INTO `t_operator_voice` VALUES (3596, 106, '任命队长', '嘿！我可是队长，你们都得听话点！', 'char_355_ethan/CN_018');
INSERT INTO `t_operator_voice` VALUES (3597, 106, '行动出发', '如果队伍里忽然少一个人，别恐慌。', 'char_355_ethan/CN_019');
INSERT INTO `t_operator_voice` VALUES (3598, 106, '行动开始', '要打架了？我先躲会儿！', 'char_355_ethan/CN_020');
INSERT INTO `t_operator_voice` VALUES (3599, 106, '选中干员1', '在这里，在这里！', 'char_355_ethan/CN_021');
INSERT INTO `t_operator_voice` VALUES (3600, 106, '选中干员2', '要怎么做？', 'char_355_ethan/CN_022');
INSERT INTO `t_operator_voice` VALUES (3601, 106, '部署1', '来了来了。', 'char_355_ethan/CN_023');
INSERT INTO `t_operator_voice` VALUES (3602, 106, '部署2', '就这儿好了。', 'char_355_ethan/CN_024');
INSERT INTO `t_operator_voice` VALUES (3603, 106, '作战中1', '打他！快打他！', 'char_355_ethan/CN_025');
INSERT INTO `t_operator_voice` VALUES (3604, 106, '作战中2', '暴力解决不了问题，但解决你没什么问题！', 'char_355_ethan/CN_026');
INSERT INTO `t_operator_voice` VALUES (3605, 106, '作战中3', '你看不见我！', 'char_355_ethan/CN_027');
INSERT INTO `t_operator_voice` VALUES (3606, 106, '作战中4', '你是不是眼前一黑？', 'char_355_ethan/CN_028');
INSERT INTO `t_operator_voice` VALUES (3607, 106, '4星结束行动', '打完了吗？哎呀好累哦好累哦。', 'char_355_ethan/CN_029');
INSERT INTO `t_operator_voice` VALUES (3608, 106, '3星结束行动', '我也好好努力了喔，你看得见的吧，对吧！', 'char_355_ethan/CN_030');
INSERT INTO `t_operator_voice` VALUES (3609, 106, '非3星结束行动', '逃就逃了，放他们走吧。', 'char_355_ethan/CN_031');
INSERT INTO `t_operator_voice` VALUES (3610, 106, '行动失败', '我建议你们先走，至少我能抓住他们的腿。', 'char_355_ethan/CN_032');
INSERT INTO `t_operator_voice` VALUES (3611, 106, '进驻设施', '真是不错的房间，让我来看看，什么地方比较显眼？', 'char_355_ethan/CN_033');
INSERT INTO `t_operator_voice` VALUES (3612, 106, '戳一下', '哦豁，被你发现了。', 'char_355_ethan/CN_034');
INSERT INTO `t_operator_voice` VALUES (3613, 106, '信赖触摸', '喂，要不要出去逛逛？', 'char_355_ethan/CN_036');
INSERT INTO `t_operator_voice` VALUES (3614, 106, '标题', '明日方舟。', 'char_355_ethan/CN_037');
INSERT INTO `t_operator_voice` VALUES (3615, 106, '问候', '哟，博士，气色不错，有什么好事吗？', 'char_355_ethan/CN_042');
INSERT INTO `t_operator_voice` VALUES (3616, 107, '任命助理', '要我帮忙是没问题啦，之后可得好好回报我哦。', 'char_275_breeze/CN_001');
INSERT INTO `t_operator_voice` VALUES (3617, 107, '交谈1', '维娜......不，推进之王小姐她总给我一种说不出的感觉，是为什么呢......？', 'char_275_breeze/CN_002');
INSERT INTO `t_operator_voice` VALUES (3618, 107, '交谈2', '我的老爹？哎呀，明知道我是离家出走来到这里的，却还要这么问吗？莫非博士你其实很不擅长聊天？', 'char_275_breeze/CN_003');
INSERT INTO `t_operator_voice` VALUES (3619, 107, '交谈3', '{@nickname}，请务必品尝一下我的料理......有草药的味道？哎、哎呀，我怎么会用博士您做实验呢？哈哈......', 'char_275_breeze/CN_004');
INSERT INTO `t_operator_voice` VALUES (3620, 107, '晋升后交谈1', '我曾觉得老爹他们麻木、迂腐，所以独自离开那里，寻找新的生活，走了很远，经历了很多，却反而理解了老爹他们。欸哈哈，搞什么嘛。', 'char_275_breeze/CN_005');
INSERT INTO `t_operator_voice` VALUES (3621, 107, '晋升后交谈2', '{@nickname}，你看见那朵离散的云了吗？那是我家乡的方向。', 'char_275_breeze/CN_006');
INSERT INTO `t_operator_voice` VALUES (3622, 107, '信赖提升后交谈1', '“信仰”是一个有趣的话题，我没有机会前往拉特兰，却没想到能在罗德岛听到拉特兰的种种事迹，不过是位过于......呃、活泼的姐姐呢，拉特兰原来是这样的地方吗？', 'char_275_breeze/CN_007');
INSERT INTO `t_operator_voice` VALUES (3623, 107, '信赖提升后交谈2', '多伦郡的航船需要灯塔才能回到港湾，罗德岛将是我最后旅程的避风港。至于{@nickname}你嘛，勉强凑合着吧......所以请一定要做到，我没有做到的事。', 'char_275_breeze/CN_008');
INSERT INTO `t_operator_voice` VALUES (3624, 107, '信赖提升后交谈3', '此时此地，便是格蕾丝·亚利桑那的信仰所在，{@nickname}博士，我将对您的希望虔诚，生死不渝。', 'char_275_breeze/CN_009');
INSERT INTO `t_operator_voice` VALUES (3625, 107, '闲置', '......是在梦中彷徨的表情呢。感到不安吗，博士。', 'char_275_breeze/CN_010');
INSERT INTO `t_operator_voice` VALUES (3626, 107, '干员报到', '微风，来自多伦郡的游学者，很荣幸见到您，博士。正式的礼仪太麻烦了，所以就免了吧。', 'char_275_breeze/CN_011');
INSERT INTO `t_operator_voice` VALUES (3627, 107, '观看作战记录', '......令人惊叹的医疗技艺，不愧是罗德岛。', 'char_275_breeze/CN_012');
INSERT INTO `t_operator_voice` VALUES (3628, 107, '精英化晋升1', '为您的恩赐献上由衷的祝福，博士。唔？真是失礼的反应，这可是正经的吻手礼，动作快点啦！', 'char_275_breeze/CN_013');
INSERT INTO `t_operator_voice` VALUES (3629, 107, '精英化晋升2', '哎呀，这次可不会像上次那么正式咯，难道你在期待着吗？我会将我的一切奉献给罗德岛，这是超过形式的觉悟哦。', 'char_275_breeze/CN_014');
INSERT INTO `t_operator_voice` VALUES (3630, 107, '编入队伍', '医护工作就交给我吧。', 'char_275_breeze/CN_017');
INSERT INTO `t_operator_voice` VALUES (3631, 107, '任命队长', '感谢您的信任，我会以干员们的安全为第一优先考虑。', 'char_275_breeze/CN_018');
INSERT INTO `t_operator_voice` VALUES (3632, 107, '行动出发', '起风了......', 'char_275_breeze/CN_019');
INSERT INTO `t_operator_voice` VALUES (3633, 107, '行动开始', '伤害别人的恶党，没有祈求饶恕的权利。', 'char_275_breeze/CN_020');
INSERT INTO `t_operator_voice` VALUES (3634, 107, '选中干员1', '在。', 'char_275_breeze/CN_021');
INSERT INTO `t_operator_voice` VALUES (3635, 107, '选中干员2', '请指示。', 'char_275_breeze/CN_022');
INSERT INTO `t_operator_voice` VALUES (3636, 107, '部署1', '速战速决。', 'char_275_breeze/CN_023');
INSERT INTO `t_operator_voice` VALUES (3637, 107, '部署2', '注意保护自己。', 'char_275_breeze/CN_024');
INSERT INTO `t_operator_voice` VALUES (3638, 107, '作战中1', '悼词回响！', 'char_275_breeze/CN_025');
INSERT INTO `t_operator_voice` VALUES (3639, 107, '作战中2', '风唱挽歌......', 'char_275_breeze/CN_026');
INSERT INTO `t_operator_voice` VALUES (3640, 107, '作战中3', '意志！', 'char_275_breeze/CN_027');
INSERT INTO `t_operator_voice` VALUES (3641, 107, '4星结束行动', '诸位，胜利属于我们。', 'char_275_breeze/CN_029');
INSERT INTO `t_operator_voice` VALUES (3642, 107, '3星结束行动', '每一次胜利都将成为火种，生生不息。', 'char_275_breeze/CN_030');
INSERT INTO `t_operator_voice` VALUES (3643, 107, '非3星结束行动', '请不要麻痹大意，风向显露着不安。', 'char_275_breeze/CN_031');
INSERT INTO `t_operator_voice` VALUES (3644, 107, '行动失败', '为什么总是执着于伤害他人......', 'char_275_breeze/CN_032');
INSERT INTO `t_operator_voice` VALUES (3645, 107, '进驻设施', '真是别具一格的设施呢。', 'char_275_breeze/CN_033');
INSERT INTO `t_operator_voice` VALUES (3646, 107, '戳一下', '等，等下！', 'char_275_breeze/CN_034');
INSERT INTO `t_operator_voice` VALUES (3647, 107, '信赖触摸', '博士，想听听我家乡的故事吗？', 'char_275_breeze/CN_036');
INSERT INTO `t_operator_voice` VALUES (3648, 107, '标题', '明日方舟。', 'char_275_breeze/CN_037');
INSERT INTO `t_operator_voice` VALUES (3649, 107, '问候', '您好，博士。', 'char_275_breeze/CN_042');
INSERT INTO `t_operator_voice` VALUES (3650, 108, '任命助理', '博士，虽然现在我是你的雇员，但你要是有一丁点可疑的行为，我可绝对不会坐视不管哦~', 'char_133_mm/CN_001');
INSERT INTO `t_operator_voice` VALUES (3651, 108, '交谈1', '罗德岛也布满了可疑的气息呢......', 'char_133_mm/CN_002');
INSERT INTO `t_operator_voice` VALUES (3652, 108, '交谈2', '调查的基础就是仔细观察！这是基本，但永远都管用！', 'char_133_mm/CN_003');
INSERT INTO `t_operator_voice` VALUES (3653, 108, '交谈3', '那边的家伙！把手举起来，面向我！啊，对不起，认错人了......', 'char_133_mm/CN_004');
INSERT INTO `t_operator_voice` VALUES (3654, 108, '晋升后交谈1', '我早就觉得那只企鹅超可疑的，你看，它长成那样，却挥金如土耶！居然，它居然还戴着金项链，你知道我们探员每个月才多少工资啊！', 'char_133_mm/CN_005');
INSERT INTO `t_operator_voice` VALUES (3655, 108, '晋升后交谈2', '唔，苦思冥想，怎么才能找机会调查下那家可疑的物流公司？直接闯进去？但那两个护卫感觉又超凶的......哎呀呀，我才没有害怕，我是打算用智取的！', 'char_133_mm/CN_006');
INSERT INTO `t_operator_voice` VALUES (3656, 108, '信赖提升后交谈1', '为啥要给我红豆面包？我在跟踪调查中才不会吃这种东西呢，你这家伙，电视剧看太多了喂！（咀嚼咀嚼）真正的侦探，在任务中，都是吃甜甜圈的！', 'char_133_mm/CN_007');
INSERT INTO `t_operator_voice` VALUES (3657, 108, '信赖提升后交谈2', '三个月后又三个月，三个月后又三个月，我卧底快十个月了！总队长！......喂！你这家伙别偷听我的电话！', 'char_133_mm/CN_008');
INSERT INTO `t_operator_voice` VALUES (3658, 108, '信赖提升后交谈3', '博士，那只企鹅好像注意到我在调查它了，我感觉，我要有生命危险啦......！', 'char_133_mm/CN_009');
INSERT INTO `t_operator_voice` VALUES (3659, 108, '闲置', '醒醒！醒醒！不要偷懒呀——！', 'char_133_mm/CN_010');
INSERT INTO `t_operator_voice` VALUES (3660, 108, '干员报到', '我是皇家侦探，梅，请配合我的调查！欸？你说我找错地方了？不可能！我有那个企鹅潜藏在这里的证据！', 'char_133_mm/CN_011');
INSERT INTO `t_operator_voice` VALUES (3661, 108, '观看作战记录', '调查工作中的我，很帅气吧？', 'char_133_mm/CN_012');
INSERT INTO `t_operator_voice` VALUES (3662, 108, '精英化晋升1', '感谢总探长的提拔，我......呃不对，你又不是我真正的上司！', 'char_133_mm/CN_013');
INSERT INTO `t_operator_voice` VALUES (3663, 108, '精英化晋升2', '精英小组？欸，我不是皇家侦探来着的吗！为什么我会被佣兵组织提拔啊！', 'char_133_mm/CN_014');
INSERT INTO `t_operator_voice` VALUES (3664, 108, '编入队伍', '可别让可疑分子混进队伍哦？', 'char_133_mm/CN_017');
INSERT INTO `t_operator_voice` VALUES (3665, 108, '任命队长', '呼呼呼，队长？现在得叫我总探长！', 'char_133_mm/CN_018');
INSERT INTO `t_operator_voice` VALUES (3666, 108, '行动出发', '全队出动！全队出动啦——！！', 'char_133_mm/CN_019');
INSERT INTO `t_operator_voice` VALUES (3667, 108, '行动开始', '犯罪分子们，给我束手就擒吧！', 'char_133_mm/CN_020');
INSERT INTO `t_operator_voice` VALUES (3668, 108, '选中干员1', '怎么了？发生了什么事件吗？', 'char_133_mm/CN_021');
INSERT INTO `t_operator_voice` VALUES (3669, 108, '选中干员2', '好啦好啦，我知道了！', 'char_133_mm/CN_022');
INSERT INTO `t_operator_voice` VALUES (3670, 108, '部署1', '现场搜查！', 'char_133_mm/CN_023');
INSERT INTO `t_operator_voice` VALUES (3671, 108, '部署2', '掏出你们的证件！', 'char_133_mm/CN_024');
INSERT INTO `t_operator_voice` VALUES (3672, 108, '作战中1', '这是一次警告！', 'char_133_mm/CN_025');
INSERT INTO `t_operator_voice` VALUES (3673, 108, '作战中2', '不许动！', 'char_133_mm/CN_026');
INSERT INTO `t_operator_voice` VALUES (3674, 108, '作战中3', '让你尝尝泰瑟枪的威力！', 'char_133_mm/CN_027');
INSERT INTO `t_operator_voice` VALUES (3675, 108, '作战中4', '不会疼的，只是让你麻痹一下！', 'char_133_mm/CN_028');
INSERT INTO `t_operator_voice` VALUES (3676, 108, '4星结束行动', '在我这个名侦探面前，没有什么事件是解决不了的！', 'char_133_mm/CN_029');
INSERT INTO `t_operator_voice` VALUES (3677, 108, '3星结束行动', '把这些嫌疑犯统统带走！呜，好吧，我自己来......', 'char_133_mm/CN_030');
INSERT INTO `t_operator_voice` VALUES (3678, 108, '非3星结束行动', '唔唔......我觉得我们还漏掉了不少线索？', 'char_133_mm/CN_031');
INSERT INTO `t_operator_voice` VALUES (3679, 108, '行动失败', '我、我们这里请求支援啊啊啊啊......', 'char_133_mm/CN_032');
INSERT INTO `t_operator_voice` VALUES (3680, 108, '进驻设施', '我有预感，有什么事件，将要在这里发生！', 'char_133_mm/CN_033');
INSERT INTO `t_operator_voice` VALUES (3681, 108, '戳一下', '恶徒之手，不可饶恕！', 'char_133_mm/CN_034');
INSERT INTO `t_operator_voice` VALUES (3682, 108, '信赖触摸', '别、别以为这样做，你就能洗脱自己的犯罪嫌疑哦......！', 'char_133_mm/CN_036');
INSERT INTO `t_operator_voice` VALUES (3683, 108, '标题', '明日方舟。', 'char_133_mm/CN_037');
INSERT INTO `t_operator_voice` VALUES (3684, 108, '问候', '博士，找到你了！', 'char_133_mm/CN_042');
INSERT INTO `t_operator_voice` VALUES (3685, 109, '任命助理', '博士你要去哪，现在不是值班时间吗？要去买午餐？那我陪你去吧，我也有些想买的东西，正好可以充当一下你的护卫。', 'char_243_waaifu/CN_001');
INSERT INTO `t_operator_voice` VALUES (3686, 109, '交谈1', '抱歉博士，罗德岛的工作我只能当作一份兼职，毕竟只有完成学业我才能拥有正常的未来，直接脱离社会之类的，恕我拒绝。', 'char_243_waaifu/CN_002');
INSERT INTO `t_operator_voice` VALUES (3687, 109, '交谈2', '要送交的文件根据类型不同贴上了不同颜色的标签，备份档案已经全部送到，时间规划在这张表上，夜宵我买好了放在小冰箱里，记得隔水加热。之后还有什么要做的，办公室的灯泡需要我换一下吗？', 'char_243_waaifu/CN_003');
INSERT INTO `t_operator_voice` VALUES (3688, 109, '交谈3', '我有没有和你说过我的另一份工作？没有？那我就不说了。别多心，博士，是真没什么可说的，如果那家事务所能有罗德岛十分之一勤奋，他们早就垄断龙门的所有侦探生意了。', 'char_243_waaifu/CN_004');
INSERT INTO `t_operator_voice` VALUES (3689, 109, '晋升后交谈1', '好奇我的武艺是怎么来的？这个啊，都是我那臭老爸从小开始的奇怪教育造成的。当然，我并不讨厌这身武艺，行侠仗义，离不开它。', 'char_243_waaifu/CN_005');
INSERT INTO `t_operator_voice` VALUES (3690, 109, '晋升后交谈2', '武艺和人心并无区别，武有阴阳，人有曲直。去纠正不对的事情、去战胜用错地方的武艺、以及去制服邪恶的人，对我来说都是再正常不过的事情了。世道险恶，总得有人挺身而出。', 'char_243_waaifu/CN_006');
INSERT INTO `t_operator_voice` VALUES (3691, 109, '信赖提升后交谈1', '你说在哪看见了我？龙门城区？可能确实是我，我刚刚下课。你看见我痛打欺负过路人的混混，然后在警员抵达之前溜走了？你认错人了。对，你一定认错人了，别问了。', 'char_243_waaifu/CN_007');
INSERT INTO `t_operator_voice` VALUES (3692, 109, '信赖提升后交谈2', '我那臭老爸，嘴里说着要寻找武学真谛，现在也不知去了哪里。拜托，这可是现代社会！像他这样没常识没责任心的人，在哪里丧命都不奇怪。但他教我的，我也没忘记——“武有武德，人有人心。”', 'char_243_waaifu/CN_008');
INSERT INTO `t_operator_voice` VALUES (3693, 109, '信赖提升后交谈3', '博士，罗德岛有收到寄给我的信件吗？有？太好了。这里是我的成绩单......成绩很重要。至今所有科目，我没拿过A以外的成绩；如果我做的不够，许多事情会因为我能力不足而留下遗憾。', 'char_243_waaifu/CN_009');
INSERT INTO `t_operator_voice` VALUES (3694, 109, '闲置', '竟然在睡觉......算了，帮你把工作做完吧。', 'char_243_waaifu/CN_010');
INSERT INTO `t_operator_voice` VALUES (3695, 109, '干员报到', '请问这里就是罗德岛吗？你好，我是今天起在这里打工的槐琥，请严格要求我，我一定能满足各位的工作需求。嗯，一定。', 'char_243_waaifu/CN_011');
INSERT INTO `t_operator_voice` VALUES (3696, 109, '观看作战记录', '如果在这里能这样出拳，就一定能击倒对方。', 'char_243_waaifu/CN_012');
INSERT INTO `t_operator_voice` VALUES (3697, 109, '精英化晋升1', '非常感谢，我不会辜负你对我的这份认可。', 'char_243_waaifu/CN_013');
INSERT INTO `t_operator_voice` VALUES (3698, 109, '精英化晋升2', '我时常会看见不义之事在眼前发生。每当我没有能力阻止，我就会下定决心精进自己。所以，有什么需要我的地方，尽管开口，我一定全力以赴！', 'char_243_waaifu/CN_014');
INSERT INTO `t_operator_voice` VALUES (3699, 109, '编入队伍', '是要去打击邪恶了吗？', 'char_243_waaifu/CN_017');
INSERT INTO `t_operator_voice` VALUES (3700, 109, '任命队长', '我没有太多经验，但我不会推辞责任。', 'char_243_waaifu/CN_018');
INSERT INTO `t_operator_voice` VALUES (3701, 109, '行动出发', '诛恶义举，在所不辞！', 'char_243_waaifu/CN_019');
INSERT INTO `t_operator_voice` VALUES (3702, 109, '行动开始', '斩草除根，当仁不让！', 'char_243_waaifu/CN_020');
INSERT INTO `t_operator_voice` VALUES (3703, 109, '选中干员1', '在！', 'char_243_waaifu/CN_021');
INSERT INTO `t_operator_voice` VALUES (3704, 109, '选中干员2', '请说！', 'char_243_waaifu/CN_022');
INSERT INTO `t_operator_voice` VALUES (3705, 109, '部署1', '别无他法。', 'char_243_waaifu/CN_023');
INSERT INTO `t_operator_voice` VALUES (3706, 109, '部署2', '好！', 'char_243_waaifu/CN_024');
INSERT INTO `t_operator_voice` VALUES (3707, 109, '作战中1', '你们背离了社会的准则！', 'char_243_waaifu/CN_025');
INSERT INTO `t_operator_voice` VALUES (3708, 109, '作战中2', '不许欺负弱小！', 'char_243_waaifu/CN_026');
INSERT INTO `t_operator_voice` VALUES (3709, 109, '作战中3', '就让你们见识一下，双刀八斩法！', 'char_243_waaifu/CN_027');
INSERT INTO `t_operator_voice` VALUES (3710, 109, '作战中4', '心思所指，拳意所至！', 'char_243_waaifu/CN_028');
INSERT INTO `t_operator_voice` VALUES (3711, 109, '4星结束行动', '念头不正，终生不正。现在悔改，为时未晚。', 'char_243_waaifu/CN_029');
INSERT INTO `t_operator_voice` VALUES (3712, 109, '3星结束行动', '精诚所至，金石为开。', 'char_243_waaifu/CN_030');
INSERT INTO `t_operator_voice` VALUES (3713, 109, '非3星结束行动', '我还得继续磨练自己。现在这样，绝对不够。', 'char_243_waaifu/CN_031');
INSERT INTO `t_operator_voice` VALUES (3714, 109, '行动失败', '父亲......我还不够强。', 'char_243_waaifu/CN_032');
INSERT INTO `t_operator_voice` VALUES (3715, 109, '进驻设施', '至少比某个又旧又乱的侦探事务所要好上许多。', 'char_243_waaifu/CN_033');
INSERT INTO `t_operator_voice` VALUES (3716, 109, '戳一下', '想偷袭？！......博士，请不要悄悄接近我。来，我送你去医疗部门。', 'char_243_waaifu/CN_034');
INSERT INTO `t_operator_voice` VALUES (3717, 109, '信赖触摸', '想去散步？走，我陪你。有我在，你会很安全。', 'char_243_waaifu/CN_036');
INSERT INTO `t_operator_voice` VALUES (3718, 109, '标题', '明日方舟。', 'char_243_waaifu/CN_037');
INSERT INTO `t_operator_voice` VALUES (3719, 109, '问候', '要一起去喝茶吗，博士？', 'char_243_waaifu/CN_042');
INSERT INTO `t_operator_voice` VALUES (3720, 110, '任命助理', '博士，需要我帮忙搬什么东西吗？', 'char_325_bison/CN_001');
INSERT INTO `t_operator_voice` VALUES (3721, 110, '交谈1', '这个吵闹的金属乐声......能天使姐又在自顾自开派对了。博士，要不要我去提醒她一下？', 'char_325_bison/CN_002');
INSERT INTO `t_operator_voice` VALUES (3722, 110, '交谈2', '体力活的话，我很擅长。公司经营方面的事务，我也懂一些。所以，博士，有什么我能派上用场的地方吗？', 'char_325_bison/CN_003');
INSERT INTO `t_operator_voice` VALUES (3723, 110, '交谈3', '因为父亲这一代在龙门扎根，我其实是土生土长的龙门人......博士你要是想了解龙门的话，我倒是可以帮上忙。', 'char_325_bison/CN_004');
INSERT INTO `t_operator_voice` VALUES (3724, 110, '晋升后交谈1', '博士，想知道正规物流公司的运作方式的话，直接来问我比较好。企鹅物流是个特例，毫无恶意地说，他们应该没有任何参考价值......', 'char_325_bison/CN_005');
INSERT INTO `t_operator_voice` VALUES (3725, 110, '晋升后交谈2', '任务之外的安排？一般除了休息，就是陪可颂姐逛街吧，我要负责帮她提东西，还要制止她乱花钱......博士也想一起来吗？', 'char_325_bison/CN_006');
INSERT INTO `t_operator_voice` VALUES (3726, 110, '信赖提升后交谈1', '我过去一直认为，对任何公司来说，规范的流程和高效的执行才是能让它长久运转的关键。所以最开始的时候，我非常不理解，为什么企鹅物流这样的公司能够存续下来。', 'char_325_bison/CN_007');
INSERT INTO `t_operator_voice` VALUES (3727, 110, '信赖提升后交谈2', '和企鹅物流的人接触了以后，和几位姐姐们的相处让我明白了一件事——这个世界，比我想的要宽广许多。条理与规则没有错，但它不适用于所有人。', 'char_325_bison/CN_008');
INSERT INTO `t_operator_voice` VALUES (3728, 110, '信赖提升后交谈3', '博士，或许你自己没有发现，但是，能让那么多个性迥异的人都信服你，其实是一件非常不简单的事......要是有一天，我也能变得像博士你一样就好了。', 'char_325_bison/CN_009');
INSERT INTO `t_operator_voice` VALUES (3729, 110, '闲置', '博士......唔，在休息吗，那我先打扫一下卫生吧。', 'char_325_bison/CN_010');
INSERT INTO `t_operator_voice` VALUES (3730, 110, '干员报到', '初次见面，博士，我是拜松。按照合约内容，接下来我也会作为罗德岛的一员行动，请多指教。', 'char_325_bison/CN_011');
INSERT INTO `t_operator_voice` VALUES (3731, 110, '观看作战记录', '谢谢，我会好好学习的。', 'char_325_bison/CN_012');
INSERT INTO `t_operator_voice` VALUES (3732, 110, '精英化晋升1', '谢谢，博士。只要脚踏实地，总会有所收获，这是我的信条。', 'char_325_bison/CN_013');
INSERT INTO `t_operator_voice` VALUES (3733, 110, '精英化晋升2', '在企鹅物流和罗德岛，我看到了更宽广的世界......博士，总有一天，我也会在这个世界里找到属于自己的位置。', 'char_325_bison/CN_014');
INSERT INTO `t_operator_voice` VALUES (3734, 110, '编入队伍', '要执行任务吗？我了解了。', 'char_325_bison/CN_017');
INSERT INTO `t_operator_voice` VALUES (3735, 110, '任命队长', '现场指挥的话......我稍微有些经验。', 'char_325_bison/CN_018');
INSERT INTO `t_operator_voice` VALUES (3736, 110, '行动出发', '嗯，任务地点和任务目标我都好好记住了。', 'char_325_bison/CN_019');
INSERT INTO `t_operator_voice` VALUES (3737, 110, '行动开始', '呼......我不会给大家拖后腿的。', 'char_325_bison/CN_020');
INSERT INTO `t_operator_voice` VALUES (3738, 110, '选中干员1', '我该怎么做，博士？', 'char_325_bison/CN_021');
INSERT INTO `t_operator_voice` VALUES (3739, 110, '选中干员2', '到我了吗，博士。', 'char_325_bison/CN_022');
INSERT INTO `t_operator_voice` VALUES (3740, 110, '部署1', '我会努力的。', 'char_325_bison/CN_023');
INSERT INTO `t_operator_voice` VALUES (3741, 110, '部署2', '我知道了。', 'char_325_bison/CN_024');
INSERT INTO `t_operator_voice` VALUES (3742, 110, '作战中1', '我还坚持得住。', 'char_325_bison/CN_025');
INSERT INTO `t_operator_voice` VALUES (3743, 110, '作战中2', '再坚持一下吗？我知道了。', 'char_325_bison/CN_026');
INSERT INTO `t_operator_voice` VALUES (3744, 110, '作战中3', '请放心，我的盾还是很坚固的。', 'char_325_bison/CN_027');
INSERT INTO `t_operator_voice` VALUES (3745, 110, '4星结束行动', '博士......你要是转业的话，一定是每家公司都想要的那种管理人才。', 'char_325_bison/CN_029');
INSERT INTO `t_operator_voice` VALUES (3746, 110, '3星结束行动', '博士......果然很厉害。', 'char_325_bison/CN_030');
INSERT INTO `t_operator_voice` VALUES (3747, 110, '非3星结束行动', '任务的基础目标已经达成了，这样就可以了吧？', 'char_325_bison/CN_031');
INSERT INTO `t_operator_voice` VALUES (3748, 110, '行动失败', '博士，不要泄气，我们还有机会。', 'char_325_bison/CN_032');
INSERT INTO `t_operator_voice` VALUES (3749, 110, '进驻设施', '罗德岛里面......真大。', 'char_325_bison/CN_033');
INSERT INTO `t_operator_voice` VALUES (3750, 110, '戳一下', '要我去哪里？', 'char_325_bison/CN_034');
INSERT INTO `t_operator_voice` VALUES (3751, 110, '信赖触摸', '空姐的新专辑......真好听。', 'char_325_bison/CN_036');
INSERT INTO `t_operator_voice` VALUES (3752, 110, '标题', '明日方舟。', 'char_325_bison/CN_037');
INSERT INTO `t_operator_voice` VALUES (3753, 110, '问候', '早上好，博士！', 'char_325_bison/CN_042');
INSERT INTO `t_operator_voice` VALUES (3754, 111, '任命助理', '呵呵，博士，你运气不错，我今天没什么事要做，可以陪你一会儿。', 'char_213_mostma/CN_001');
INSERT INTO `t_operator_voice` VALUES (3755, 111, '交谈1', '龙门的市井，乌萨斯的郊区，维多利亚的古废墟......博士，这个世界上有无数美景，只不过，很少有人去发现。', 'char_213_mostma/CN_002');
INSERT INTO `t_operator_voice` VALUES (3756, 111, '交谈2', '博士，想听我旅途中发生的故事吗？那么，我建议你准备一些美味的茶点，不然，再精彩的故事也会变得乏味的。', 'char_213_mostma/CN_003');
INSERT INTO `t_operator_voice` VALUES (3757, 111, '交谈3', '即使我曾观察过无数不同的团体，罗德岛的有趣程度也是数一数二的。究竟是时代将为你们倾倒，还是你们终被时代吞没......我很好奇。', 'char_213_mostma/CN_004');
INSERT INTO `t_operator_voice` VALUES (3758, 111, '晋升后交谈1', '博士，关于下一个任务地点，我有一些很不错的美食推荐，想听吗？', 'char_213_mostma/CN_005');
INSERT INTO `t_operator_voice` VALUES (3759, 111, '晋升后交谈2', '博士，你似乎已经了解了何为堕天使，不过很遗憾，我不会告诉你详细。即使有一天我能与你分享我的一切，这也会是我唯一的秘密吧。', 'char_213_mostma/CN_006');
INSERT INTO `t_operator_voice` VALUES (3760, 111, '信赖提升后交谈1', '博士，你不用去考虑如何和我打好关系，对我来说，友情、亲情、爱情，我不讨厌，但并不需要......不过，呵呵，即使我这么说，你也不会放弃吧，没关系，你可以尝试，我不会介意的。', 'char_213_mostma/CN_007');
INSERT INTO `t_operator_voice` VALUES (3761, 111, '信赖提升后交谈2', '我回来......嗯？博士，难道你在等我吗？而且茶几上的这些点心......好吧，这可真是盛情难却呢，那么作为谢礼，我就为你讲一讲，在这次送货中，发生了什么有趣的事吧。', 'char_213_mostma/CN_008');
INSERT INTO `t_operator_voice` VALUES (3762, 111, '信赖提升后交谈3', '......原来如此，当我想到有你这样一个朋友时，那股油然而生的喜悦并不虚假。收回前言，博士。或许，我并不是不需要这样的温暖。', 'char_213_mostma/CN_009');
INSERT INTO `t_operator_voice` VALUES (3763, 111, '闲置', '......呵呵，祝你好梦，博士。', 'char_213_mostma/CN_010');
INSERT INTO `t_operator_voice` VALUES (3764, 111, '干员报到', '你好，博士，我是莫斯提马。你应该听大帝说过，我不受罗德岛与企鹅物流的合约约束，可以自由行动。不过，闲时我也会在罗德岛逗留。那么，请多指教。', 'char_213_mostma/CN_011');
INSERT INTO `t_operator_voice` VALUES (3765, 111, '观看作战记录', '就让我看看，罗德岛的作战方式吧。', 'char_213_mostma/CN_012');
INSERT INTO `t_operator_voice` VALUES (3766, 111, '精英化晋升1', '博士，依赖我可不是一个好选择。', 'char_213_mostma/CN_013');
INSERT INTO `t_operator_voice` VALUES (3767, 111, '精英化晋升2', '博士，听说你想看我使出全力。保险起见，我先问一句——我不用去在乎赔偿问题吧？', 'char_213_mostma/CN_014');
INSERT INTO `t_operator_voice` VALUES (3768, 111, '编入队伍', '来了。', 'char_213_mostma/CN_017');
INSERT INTO `t_operator_voice` VALUES (3769, 111, '任命队长', '嗯......抱歉，可以让大家自由行动吗？', 'char_213_mostma/CN_018');
INSERT INTO `t_operator_voice` VALUES (3770, 111, '行动出发', '这个地方的话......我来带路吧。', 'char_213_mostma/CN_019');
INSERT INTO `t_operator_voice` VALUES (3771, 111, '行动开始', '速战速决吧。', 'char_213_mostma/CN_020');
INSERT INTO `t_operator_voice` VALUES (3772, 111, '选中干员1', '嗯哼？', 'char_213_mostma/CN_021');
INSERT INTO `t_operator_voice` VALUES (3773, 111, '选中干员2', '呵呵......', 'char_213_mostma/CN_022');
INSERT INTO `t_operator_voice` VALUES (3774, 111, '部署1', '天气不错。', 'char_213_mostma/CN_023');
INSERT INTO `t_operator_voice` VALUES (3775, 111, '部署2', '风景不错。', 'char_213_mostma/CN_024');
INSERT INTO `t_operator_voice` VALUES (3776, 111, '作战中1', '七分力应该差不多了吧。', 'char_213_mostma/CN_025');
INSERT INTO `t_operator_voice` VALUES (3777, 111, '作战中2', '差不多该结束了吧？', 'char_213_mostma/CN_026');
INSERT INTO `t_operator_voice` VALUES (3778, 111, '作战中3', '虽说无冤无仇，不过这就是生活呢。', 'char_213_mostma/CN_027');
INSERT INTO `t_operator_voice` VALUES (3779, 111, '作战中4', '结束后吃点什么好呢？', 'char_213_mostma/CN_028');
INSERT INTO `t_operator_voice` VALUES (3780, 111, '4星结束行动', '偶尔追求一些刺激，也是不坏的选择。', 'char_213_mostma/CN_029');
INSERT INTO `t_operator_voice` VALUES (3781, 111, '3星结束行动', '嗯？我还以为会漏掉几个。', 'char_213_mostma/CN_030');
INSERT INTO `t_operator_voice` VALUES (3782, 111, '非3星结束行动', '信使一般不会关注战斗的结果，只要活着，就是胜利。', 'char_213_mostma/CN_031');
INSERT INTO `t_operator_voice` VALUES (3783, 111, '行动失败', '团队协作......对我来说果然还是有些陌生啊。', 'char_213_mostma/CN_032');
INSERT INTO `t_operator_voice` VALUES (3784, 111, '进驻设施', '我可以四处逛逛吗？', 'char_213_mostma/CN_033');
INSERT INTO `t_operator_voice` VALUES (3785, 111, '戳一下', '嗯？', 'char_213_mostma/CN_034');
INSERT INTO `t_operator_voice` VALUES (3786, 111, '信赖触摸', '来聊聊天吧，博士。', 'char_213_mostma/CN_036');
INSERT INTO `t_operator_voice` VALUES (3787, 111, '标题', '明日方舟。', 'char_213_mostma/CN_037');
INSERT INTO `t_operator_voice` VALUES (3788, 111, '问候', '博士，今天过得好吗？', 'char_213_mostma/CN_042');
INSERT INTO `t_operator_voice` VALUES (3789, 112, '任命助理', '这里，是这次的文档资料，博士。没见过我做文职工作？可是，比起战斗，我更喜欢......用笔写......', 'char_261_sddrag/CN_001');
INSERT INTO `t_operator_voice` VALUES (3790, 112, '交谈1', '博士，你看窗外，夜晚......真平静。以前，每个夜晚都充满了火光和焦臭。只有看着现在这样的夜色，我的情绪才能平静下来......', 'char_261_sddrag/CN_002');
INSERT INTO `t_operator_voice` VALUES (3791, 112, '交谈2', '有一个红发的瓦伊凡孩子，一直在盯着我看......她在怀疑我的身份吧？但是，她没有证据......哼，不会让她找到的。', 'char_261_sddrag/CN_003');
INSERT INTO `t_operator_voice` VALUES (3792, 112, '交谈3', '到定期体检的时间了？我......我知道了。但是，我，不相信那些医生。要检查的话......请你，帮我操作。', 'char_261_sddrag/CN_004');
INSERT INTO `t_operator_voice` VALUES (3793, 112, '晋升后交谈1', '你们有很多问题，我不可能回答，这既是为了你好，也是劝阻你们......不要不自量力。', 'char_261_sddrag/CN_005');
INSERT INTO `t_operator_voice` VALUES (3794, 112, '晋升后交谈2', '......我很强。我知道自己有可怕的力量，但我......不喜欢。你如果想要的话，我就，去战斗......', 'char_261_sddrag/CN_006');
INSERT INTO `t_operator_voice` VALUES (3795, 112, '信赖提升后交谈1', '这把枪......我不怎么能发挥它的力量。即使这样，它也取走了很多生命。罪恶感？......我们的生命都一样廉价。所以我，没有罪恶感。', 'char_261_sddrag/CN_007');
INSERT INTO `t_operator_voice` VALUES (3796, 112, '信赖提升后交谈2', '矿石病，我不在乎......没有矿石病，我就没机会在这里生活。会剥走我的生命？我，也不在乎......如果大地上有那么多种事物都能杀死你，为什么要单单在意这一种呢？', 'char_261_sddrag/CN_008');
INSERT INTO `t_operator_voice` VALUES (3797, 112, '信赖提升后交谈3', '自己是个影子这件事，我不排斥。只有影子才能在这里，隐姓埋名......但是，但是，要是和我有相同面容的人找到了你，不要和她战斗！你会死的，我不想，不想你死......', 'char_261_sddrag/CN_009');
INSERT INTO `t_operator_voice` VALUES (3798, 112, '闲置', '我会为你守夜的......就请放心地休息吧......晚安。', 'char_261_sddrag/CN_010');
INSERT INTO `t_operator_voice` VALUES (3799, 112, '干员报到', '是你们......救起了我吗？想知道我的名字？......就叫我“苇草”吧......就像池边的芦苇，怎么样，都可以的......', 'char_261_sddrag/CN_011');
INSERT INTO `t_operator_voice` VALUES (3800, 112, '观看作战记录', '你们这样战斗，很辛苦......但是，有很多人能活下去。', 'char_261_sddrag/CN_012');
INSERT INTO `t_operator_voice` VALUES (3801, 112, '精英化晋升1', '前进，深池的战士们！......以前我常常被迫这么说，实际上，很不喜欢......', 'char_261_sddrag/CN_013');
INSERT INTO `t_operator_voice` VALUES (3802, 112, '精英化晋升2', '你有着让我感到平静的梦想，我......想帮你去实现它。只要我煽动深池的火，它会为你在黑夜里烧出道路。', 'char_261_sddrag/CN_014');
INSERT INTO `t_operator_voice` VALUES (3803, 112, '编入队伍', '战火，会烧尽荒野......', 'char_261_sddrag/CN_017');
INSERT INTO `t_operator_voice` VALUES (3804, 112, '任命队长', '既然你嘱咐过我，我就不会再忽视别人的生命......', 'char_261_sddrag/CN_018');
INSERT INTO `t_operator_voice` VALUES (3805, 112, '行动出发', '就这样，我们去收割......', 'char_261_sddrag/CN_019');
INSERT INTO `t_operator_voice` VALUES (3806, 112, '行动开始', '你们怎么像麦秆一样易燃......？', 'char_261_sddrag/CN_020');
INSERT INTO `t_operator_voice` VALUES (3807, 112, '选中干员1', '我的火，很烫......', 'char_261_sddrag/CN_021');
INSERT INTO `t_operator_voice` VALUES (3808, 112, '选中干员2', '不会有其他人看到我在这吧......？', 'char_261_sddrag/CN_022');
INSERT INTO `t_operator_voice` VALUES (3809, 112, '部署1', '啊......', 'char_261_sddrag/CN_023');
INSERT INTO `t_operator_voice` VALUES (3810, 112, '部署2', '只会剩下灰烬......', 'char_261_sddrag/CN_024');
INSERT INTO `t_operator_voice` VALUES (3811, 112, '作战中1', '活着的火焰会净化你们污浊的内在。', 'char_261_sddrag/CN_025');
INSERT INTO `t_operator_voice` VALUES (3812, 112, '作战中2', '我就是深池。', 'char_261_sddrag/CN_026');
INSERT INTO `t_operator_voice` VALUES (3813, 112, '作战中3', '我......就是深池！', 'char_261_sddrag/CN_027');
INSERT INTO `t_operator_voice` VALUES (3814, 112, '作战中4', '姐姐......', 'char_261_sddrag/CN_028');
INSERT INTO `t_operator_voice` VALUES (3815, 112, '4星结束行动', '无尽的战斗与无尽的闷燃......你不是真的想要这个，对吧？', 'char_261_sddrag/CN_029');
INSERT INTO `t_operator_voice` VALUES (3816, 112, '3星结束行动', '如果是为了我们共同的愿望而战，我可以忍受......', 'char_261_sddrag/CN_030');
INSERT INTO `t_operator_voice` VALUES (3817, 112, '非3星结束行动', '我们又能从战场上得到什么呢？回去吧，我们，快点回去......', 'char_261_sddrag/CN_031');
INSERT INTO `t_operator_voice` VALUES (3818, 112, '行动失败', '不。我不会让任何人伤害你......绝不会。', 'char_261_sddrag/CN_032');
INSERT INTO `t_operator_voice` VALUES (3819, 112, '进驻设施', '我可以有单独的房间吗？很窄，也不要紧的......', 'char_261_sddrag/CN_033');
INSERT INTO `t_operator_voice` VALUES (3820, 112, '戳一下', '呃......！', 'char_261_sddrag/CN_034');
INSERT INTO `t_operator_voice` VALUES (3821, 112, '信赖触摸', '博士......我的体温，会烫伤你吗？', 'char_261_sddrag/CN_036');
INSERT INTO `t_operator_voice` VALUES (3822, 112, '标题', '明日方舟。', 'char_261_sddrag/CN_037');
INSERT INTO `t_operator_voice` VALUES (3823, 112, '问候', '博士......？不，没什么。', 'char_261_sddrag/CN_042');
INSERT INTO `t_operator_voice` VALUES (3824, 113, '任命助理', '护卫？......啧，我知道了。', 'char_356_broca/CN_001');
INSERT INTO `t_operator_voice` VALUES (3825, 113, '交谈1', '那个叫阿米娅的小女孩也是这里的领导者？罗德岛到底是什么黑心公司。', 'char_356_broca/CN_002');
INSERT INTO `t_operator_voice` VALUES (3826, 113, '交谈2', '我不喜欢吃白食，有什么体力活需要我，可以找我。', 'char_356_broca/CN_003');
INSERT INTO `t_operator_voice` VALUES (3827, 113, '交谈3', '我不讨厌被命令，但我讨厌不知道自己在为什么战斗的感觉。', 'char_356_broca/CN_004');
INSERT INTO `t_operator_voice` VALUES (3828, 113, '晋升后交谈1', '兴趣？为什么我要告诉你？......啧，别用那种眼神看我，告诉你行了吧，我喜欢自己做各种改造。', 'char_356_broca/CN_005');
INSERT INTO `t_operator_voice` VALUES (3829, 113, '晋升后交谈2', '如果没有贾维和奥斯塔，我已经死在那天那条小巷子里了。所以，我这条命是他们的，无论他们想做什么，我都会帮他们。', 'char_356_broca/CN_006');
INSERT INTO `t_operator_voice` VALUES (3830, 113, '信赖提升后交谈1', '我不讨厌你，但我......无法完全信任你。', 'char_356_broca/CN_007');
INSERT INTO `t_operator_voice` VALUES (3831, 113, '信赖提升后交谈2', '好好做事，好好赚钱，好好生活，曾经，我的想法就这么简单......不过，奥斯塔说得对，在叙拉古这个地方，这样的想法，应该叫梦想。', 'char_356_broca/CN_008');
INSERT INTO `t_operator_voice` VALUES (3832, 113, '信赖提升后交谈3', '我承认，你这家伙作为领导者来说还可以......但是，奥斯塔说过，一个组织的腐化，有时候就是从掉以轻心的时候开始的，我会盯紧你。', 'char_356_broca/CN_009');
INSERT INTO `t_operator_voice` VALUES (3833, 113, '闲置', '睡着了吗，真是悠哉的家伙。啧，毯子放哪儿去了......', 'char_356_broca/CN_010');
INSERT INTO `t_operator_voice` VALUES (3834, 113, '干员报到', '既然贾维决定加入你们，我也没什么好说的。你可以叫我布洛卡，我会听你的命令，但别指望我做多余的事，我对你们这样的大组织可没有好感。', 'char_356_broca/CN_011');
INSERT INTO `t_operator_voice` VALUES (3835, 113, '观看作战记录', '......谢谢。', 'char_356_broca/CN_012');
INSERT INTO `t_operator_voice` VALUES (3836, 113, '精英化晋升1', '加薪？......我不是很需要。', 'char_356_broca/CN_013');
INSERT INTO `t_operator_voice` VALUES (3837, 113, '精英化晋升2', '或许奥斯塔是对的，罗德岛可以信任。这样的话，那我......', 'char_356_broca/CN_014');
INSERT INTO `t_operator_voice` VALUES (3838, 113, '编入队伍', '好。', 'char_356_broca/CN_017');
INSERT INTO `t_operator_voice` VALUES (3839, 113, '任命队长', '......我不擅长带队。', 'char_356_broca/CN_018');
INSERT INTO `t_operator_voice` VALUES (3840, 113, '行动出发', '走吧。', 'char_356_broca/CN_019');
INSERT INTO `t_operator_voice` VALUES (3841, 113, '行动开始', '该打架了。', 'char_356_broca/CN_020');
INSERT INTO `t_operator_voice` VALUES (3842, 113, '选中干员1', '别对我指手画脚。', 'char_356_broca/CN_021');
INSERT INTO `t_operator_voice` VALUES (3843, 113, '选中干员2', '啧，我知道了。', 'char_356_broca/CN_022');
INSERT INTO `t_operator_voice` VALUES (3844, 113, '部署1', '那些就是敌人？', 'char_356_broca/CN_023');
INSERT INTO `t_operator_voice` VALUES (3845, 113, '部署2', '我能应付。', 'char_356_broca/CN_024');
INSERT INTO `t_operator_voice` VALUES (3846, 113, '作战中1', '不会很疼。', 'char_356_broca/CN_025');
INSERT INTO `t_operator_voice` VALUES (3847, 113, '作战中2', '快点结束吧。', 'char_356_broca/CN_026');
INSERT INTO `t_operator_voice` VALUES (3848, 113, '作战中3', '去死。', 'char_356_broca/CN_027');
INSERT INTO `t_operator_voice` VALUES (3849, 113, '作战中4', '闭嘴。', 'char_356_broca/CN_028');
INSERT INTO `t_operator_voice` VALUES (3850, 113, '4星结束行动', '......你这家伙，还挺厉害。', 'char_356_broca/CN_029');
INSERT INTO `t_operator_voice` VALUES (3851, 113, '3星结束行动', '都搞定了。', 'char_356_broca/CN_030');
INSERT INTO `t_operator_voice` VALUES (3852, 113, '非3星结束行动', '我去追？', 'char_356_broca/CN_031');
INSERT INTO `t_operator_voice` VALUES (3853, 113, '行动失败', '别慌，你努力了，奥斯塔也会有失手的时候。', 'char_356_broca/CN_032');
INSERT INTO `t_operator_voice` VALUES (3854, 113, '进驻设施', '我随便走走。', 'char_356_broca/CN_033');
INSERT INTO `t_operator_voice` VALUES (3855, 113, '戳一下', '你......！', 'char_356_broca/CN_034');
INSERT INTO `t_operator_voice` VALUES (3856, 113, '信赖触摸', '博士，你很烦。', 'char_356_broca/CN_036');
INSERT INTO `t_operator_voice` VALUES (3857, 113, '标题', '明日方舟。', 'char_356_broca/CN_037');
INSERT INTO `t_operator_voice` VALUES (3858, 113, '问候', '早。', 'char_356_broca/CN_042');
INSERT INTO `t_operator_voice` VALUES (3859, 114, '任命助理', '今天有什么工作？要是不急，那就稍微向后面延一延嘛。', 'char_302_glaze/CN_001');
INSERT INTO `t_operator_voice` VALUES (3860, 114, '交谈1', '我能凭借源石技艺感知附近生物，这台辅助设备还能增幅效果，听起来很厉害吧？所以，戍卫队就派我去参加守城巡逻，抓些翻墙逃课的学生什么的，可真是知人善用啊。', 'char_302_glaze/CN_002');
INSERT INTO `t_operator_voice` VALUES (3861, 114, '交谈2', '事先说好哦，所谓拉特兰的秘密什么的，我可一点也不知道。我只想过悠闲自在的生活而已，知道的越多，对自己也就越没有好处，对吗。', 'char_302_glaze/CN_003');
INSERT INTO `t_operator_voice` VALUES (3862, 114, '交谈3', '唔？那个人，难不成是公证所的？他是来监督我“履行义务”的？拜托啊，我可是每月都好好缴纳税款的。反正不管是不是，以后要是他出现，你就悄悄告诉我，好方便我跑路咯。', 'char_302_glaze/CN_004');
INSERT INTO `t_operator_voice` VALUES (3863, 114, '晋升后交谈1', '之前我蛮无聊的，就去舰桥上面偷懒......啊不，去望下风景，结果就被大家误以为是准备对什么人下手了，这不是把我当成杀手了吗？那种工作方式我才不想呢，多麻烦啊。', 'char_302_glaze/CN_005');
INSERT INTO `t_operator_voice` VALUES (3864, 114, '晋升后交谈2', '过去的上司总对我们唠叨“要以生命守护拉特兰的荣耀”，可我对这种口号没有一点兴趣，也讨厌那时一成不变的生活。还是在你这里工作更好，因为我能得到更加实际的东西。', 'char_302_glaze/CN_006');
INSERT INTO `t_operator_voice` VALUES (3865, 114, '信赖提升后交谈1', '头发有点乱？哦，好像起床之后一直没有打理，毕竟今天没什么任务......真麻烦，帮我看下，辫子有没有扎歪？我总觉得怪怪的。办公室好歹也装个镜子嘛。', 'char_302_glaze/CN_007');
INSERT INTO `t_operator_voice` VALUES (3866, 114, '信赖提升后交谈2', '喏，给你蛋糕。我说你啊，休息的时候都做什么？适当出门走走，去附近城市转转，应该挺不错的？安保嘛，就找一位能随时把接近你的坏人“啪啪”地击毙的优秀干员随行，完美。', 'char_302_glaze/CN_008');
INSERT INTO `t_operator_voice` VALUES (3867, 114, '信赖提升后交谈3', '现在看会儿电影？反正工作都差不多都做完了嘛。就看那部讲公主和外国旅人相遇的浪漫电影怎么样？等下，我得先把防止被打扰的牌子挂在门口，我可不想被打扰。', 'char_302_glaze/CN_009');
INSERT INTO `t_operator_voice` VALUES (3868, 114, '闲置', '......做点什么好呢。', 'char_302_glaze/CN_010');
INSERT INTO `t_operator_voice` VALUES (3869, 114, '干员报到', '安比尔，狙击手，拉特兰出身......说明身份什么真麻烦，就这样吧。喏，刚开包的巧克力，你也来一根。', 'char_302_glaze/CN_011');
INSERT INTO `t_operator_voice` VALUES (3870, 114, '观看作战记录', '......时间这么长啊，就没有一次性播放所有录像的功能吗？', 'char_302_glaze/CN_012');
INSERT INTO `t_operator_voice` VALUES (3871, 114, '精英化晋升1', '谢谢。说来，晋升的同时薪水会不会再提高些？我最近想买点东西......', 'char_302_glaze/CN_013');
INSERT INTO `t_operator_voice` VALUES (3872, 114, '精英化晋升2', '啊，谢谢你，我已经在罗德岛待这么久了吗？不知不觉间，我已经得到了很多，而且都是些不错的回忆呢。', 'char_302_glaze/CN_014');
INSERT INTO `t_operator_voice` VALUES (3873, 114, '编入队伍', '这次的队友看起来都挺能干的嘛。', 'char_302_glaze/CN_017');
INSERT INTO `t_operator_voice` VALUES (3874, 114, '任命队长', '事先说好，我喜欢的战术可都不那么常规。', 'char_302_glaze/CN_018');
INSERT INTO `t_operator_voice` VALUES (3875, 114, '行动出发', '准备好了？那走咯。', 'char_302_glaze/CN_019');
INSERT INTO `t_operator_voice` VALUES (3876, 114, '行动开始', '希望这次任务能赶在晚饭前结束。', 'char_302_glaze/CN_020');
INSERT INTO `t_operator_voice` VALUES (3877, 114, '选中干员1', '嗯？', 'char_302_glaze/CN_021');
INSERT INTO `t_operator_voice` VALUES (3878, 114, '选中干员2', '等我吃完这根。', 'char_302_glaze/CN_022');
INSERT INTO `t_operator_voice` VALUES (3879, 114, '部署1', '射击辅助平台展开。', 'char_302_glaze/CN_023');
INSERT INTO `t_operator_voice` VALUES (3880, 114, '部署2', '自动校准中。', 'char_302_glaze/CN_024');
INSERT INTO `t_operator_voice` VALUES (3881, 114, '作战中1', '在这边。', 'char_302_glaze/CN_025');
INSERT INTO `t_operator_voice` VALUES (3882, 114, '作战中2', '你藏得不够隐蔽。', 'char_302_glaze/CN_026');
INSERT INTO `t_operator_voice` VALUES (3883, 114, '作战中3', '外面可是很危险的。', 'char_302_glaze/CN_027');
INSERT INTO `t_operator_voice` VALUES (3884, 114, '作战中4', '我送你回家吧。', 'char_302_glaze/CN_028');
INSERT INTO `t_operator_voice` VALUES (3885, 114, '4星结束行动', '每一次都能做到这种程度，真不愧是博士你啊。', 'char_302_glaze/CN_029');
INSERT INTO `t_operator_voice` VALUES (3886, 114, '3星结束行动', '没有一发子弹被浪费，完美。', 'char_302_glaze/CN_030');
INSERT INTO `t_operator_voice` VALUES (3887, 114, '非3星结束行动', '赢得没那么漂亮，不过毕竟是胜利，对吧？', 'char_302_glaze/CN_031');
INSERT INTO `t_operator_voice` VALUES (3888, 114, '行动失败', '可见目标还在增加，敌人马上就要发动进攻了，博士，快撤退......', 'char_302_glaze/CN_032');
INSERT INTO `t_operator_voice` VALUES (3889, 114, '进驻设施', '博士，自动贩卖机里的手指饼干该补货了哦。', 'char_302_glaze/CN_033');
INSERT INTO `t_operator_voice` VALUES (3890, 114, '戳一下', '什么啊。', 'char_302_glaze/CN_034');
INSERT INTO `t_operator_voice` VALUES (3891, 114, '信赖触摸', '听说销售站新增了一批服装？唔唔，博士，你可一定要陪我去看看。', 'char_302_glaze/CN_036');
INSERT INTO `t_operator_voice` VALUES (3892, 114, '标题', '明日方舟。', 'char_302_glaze/CN_037');
INSERT INTO `t_operator_voice` VALUES (3893, 114, '问候', '哟。', 'char_302_glaze/CN_042');
INSERT INTO `t_operator_voice` VALUES (3894, 115, '任命助理', '也好，和你待在一起的话，至少能从满是粉尘的空气中稍微喘口气。', 'char_367_swllow/CN_001');
INSERT INTO `t_operator_voice` VALUES (3895, 115, '交谈1', '我不避讳关于感染者的话题？没错，他们和你我不同，这显而易见，但他们也有赢得希望的权利，这点上我们又是一样的。所以我没必要太过照顾他们，这才是一视同仁，不是吗？', 'char_367_swllow/CN_002');
INSERT INTO `t_operator_voice` VALUES (3896, 115, '交谈2', '煌有时候太莽撞了，我知道她很强，但是......什么？你能应付得来？啧，算了，回头向阿米娅好好请教一下，到底怎么对付那个家伙。', 'char_367_swllow/CN_003');
INSERT INTO `t_operator_voice` VALUES (3897, 115, '交谈3', '我只是客观的叙述意见而已，既然不愿意被他人歧视，就不要抱着自己需要被顾虑的心态......什么，回到宿舍大哭？虽然有点过意不去，这样的人真的适合成为干员吗？', 'char_367_swllow/CN_004');
INSERT INTO `t_operator_voice` VALUES (3898, 115, '晋升后交谈1', '罗德岛在变化，也许{@nickname}没什么感觉，毕竟这些变化本就是因你而起，况且也不是所有人都像我一样招人讨厌。不过，我倒是挺喜欢罗德岛现在的方向。', 'char_367_swllow/CN_005');
INSERT INTO `t_operator_voice` VALUES (3899, 115, '晋升后交谈2', '以前我喜欢选择一些路途遥远的任务，就像是要逃离罗德岛一样。明明罗德岛收养了我，这可真是忘恩负义。现在？呵呵，如果还有干员喜欢把血撒得到处都是，那我应该还是会申请去其他地区吧。', 'char_367_swllow/CN_006');
INSERT INTO `t_operator_voice` VALUES (3900, 115, '信赖提升后交谈1', '博士，你会害怕吗？天灾，源石，矿石病，还有感染者。嗯，我现在都在被噩梦侵扰。不过现在有阿米娅，还有煌那样的笨蛋，好像也没有一开始那么恐怖了。', 'char_367_swllow/CN_007');
INSERT INTO `t_operator_voice` VALUES (3901, 115, '信赖提升后交谈2', '你为什么被称为博士？不，我不是要你回忆什么，只是这个称呼让我很......怀念。看着那些感染者们围绕在你身边，稍微，有点回想起过去的事情。', 'char_367_swllow/CN_008');
INSERT INTO `t_operator_voice` VALUES (3902, 115, '信赖提升后交谈3', '其实我知道，母亲把我送来的时候就已经疯了。我无法对她混沌的痛苦感同身受，但我能理解，因为我也靠着那股执念走到了今天。只是，我找到了新的方向......我想找到她，我想拯救她。', 'char_367_swllow/CN_009');
INSERT INTO `t_operator_voice` VALUES (3903, 115, '闲置', '你肩负着罗德岛的将来，撑起桥梁的人可不能如此懈怠。', 'char_367_swllow/CN_010');
INSERT INTO `t_operator_voice` VALUES (3904, 115, '干员报到', '你就是{@nickname}？我听阿米娅提起过你，我就是灰喉，请不要让我失望。', 'char_367_swllow/CN_011');
INSERT INTO `t_operator_voice` VALUES (3905, 115, '观看作战记录', '为了效率，适当的加速播放吧，我看得清。', 'char_367_swllow/CN_012');
INSERT INTO `t_operator_voice` VALUES (3906, 115, '精英化晋升1', '即使是现在，和我关系不好的干员依旧不在少数，让我获得晋升恐怕会引起非议的喔？', 'char_367_swllow/CN_013');
INSERT INTO `t_operator_voice` VALUES (3907, 115, '精英化晋升2', '感染者也好，普通人也罢，既然我们以自己的意志踩在这片土地上，就必须要努力活着，对吧？', 'char_367_swllow/CN_014');
INSERT INTO `t_operator_voice` VALUES (3908, 115, '编入队伍', '我更喜欢独自行动。', 'char_367_swllow/CN_017');
INSERT INTO `t_operator_voice` VALUES (3909, 115, '任命队长', '虽然他们会不会听我的命令，就是另一回事了。', 'char_367_swllow/CN_018');
INSERT INTO `t_operator_voice` VALUES (3910, 115, '行动出发', '战场的全貌，尽收眼底。', 'char_367_swllow/CN_019');
INSERT INTO `t_operator_voice` VALUES (3911, 115, '行动开始', '击溃他们就行了吧？', 'char_367_swllow/CN_020');
INSERT INTO `t_operator_voice` VALUES (3912, 115, '选中干员1', '我在这里。', 'char_367_swllow/CN_021');
INSERT INTO `t_operator_voice` VALUES (3913, 115, '选中干员2', '太慢了。', 'char_367_swllow/CN_022');
INSERT INTO `t_operator_voice` VALUES (3914, 115, '部署1', '那么，我去了。', 'char_367_swllow/CN_023');
INSERT INTO `t_operator_voice` VALUES (3915, 115, '部署2', '其他人跟得上吗？', 'char_367_swllow/CN_024');
INSERT INTO `t_operator_voice` VALUES (3916, 115, '作战中1', '动作也太慢了。', 'char_367_swllow/CN_025');
INSERT INTO `t_operator_voice` VALUES (3917, 115, '作战中2', '我看得清他们，他们看不清我，可惜。', 'char_367_swllow/CN_026');
INSERT INTO `t_operator_voice` VALUES (3918, 115, '作战中3', '靠技巧足以解决这些问题。', 'char_367_swllow/CN_027');
INSERT INTO `t_operator_voice` VALUES (3919, 115, '作战中4', '他们的视野里空无一物，无需担心。', 'char_367_swllow/CN_028');
INSERT INTO `t_operator_voice` VALUES (3920, 115, '4星结束行动', '这样的胜利不会常见，我们的力量是有限的。', 'char_367_swllow/CN_029');
INSERT INTO `t_operator_voice` VALUES (3921, 115, '3星结束行动', '我没有任何天赋可言，所以我作为弩手，绝不能允许敌人的尸首出现在三米之内。', 'char_367_swllow/CN_030');
INSERT INTO `t_operator_voice` VALUES (3922, 115, '非3星结束行动', '伤员和医疗干员留下，我去追就好，反正以前我也经常独自行动。', 'char_367_swllow/CN_031');
INSERT INTO `t_operator_voice` VALUES (3923, 115, '行动失败', '注意周围，我去确保撤退路线的安全。', 'char_367_swllow/CN_032');
INSERT INTO `t_operator_voice` VALUES (3924, 115, '进驻设施', '尽管在这里待了很多年，我却很少去其他地方......', 'char_367_swllow/CN_033');
INSERT INTO `t_operator_voice` VALUES (3925, 115, '戳一下', '唔啊！？', 'char_367_swllow/CN_034');
INSERT INTO `t_operator_voice` VALUES (3926, 115, '信赖触摸', '你总是和我待在一起，不会让那些感染者干员们不满吗？', 'char_367_swllow/CN_036');
INSERT INTO `t_operator_voice` VALUES (3927, 115, '标题', '明日方舟。', 'char_367_swllow/CN_037');
INSERT INTO `t_operator_voice` VALUES (3928, 115, '问候', '你好。', 'char_367_swllow/CN_042');
INSERT INTO `t_operator_voice` VALUES (3929, 116, '任命助理', '我说，文职工作我可是完全做不来，让我值班真的合适吗？不如让我出战吧，有战果的话，你也会开心的吧？', 'char_017_huang/CN_001');
INSERT INTO `t_operator_voice` VALUES (3930, 116, '交谈1', '我为感染者而战。我们的遭遇，有眼睛的人都看得很清楚，只是绝大多数人根本不在乎我们的人生。我在乎，在乎得很。罗德岛的许多干员都在为我提供支援，我绝不会辜负他们的期望。', 'char_017_huang/CN_002');
INSERT INTO `t_operator_voice` VALUES (3931, 116, '交谈2', '在罗德岛工作的原因啊，自然是因为没什么地方可去吧？不，换句话说，我想不出比罗德岛条件还好的地方会是什么样。高薪，先进的配套医疗条件，还有可爱的小兔子在，简直太棒了好吗？', 'char_017_huang/CN_003');
INSERT INTO `t_operator_voice` VALUES (3932, 116, '交谈3', '作为罗德岛攻坚干员，我既没有强到能毁灭战局的法术，也没有能一个人对抗大群敌人的战斗技巧。但是，哈，当我把我还算擅长的两样事结合到一块儿，轰，效果立竿见影！这大概就是化学反应吧。', 'char_017_huang/CN_004');
INSERT INTO `t_operator_voice` VALUES (3933, 116, '晋升后交谈1', '阿米娅是我见过最有天分的战士之一。她对局势的判断特别清晰，她的法术我光是看着都感到害怕。只不过仔细想想，她是怎么在这个年龄是怎么获得这种实力的？真的。我宁可她没有这样的实力。', 'char_017_huang/CN_005');
INSERT INTO `t_operator_voice` VALUES (3934, 116, '晋升后交谈2', '如果需要我帮忙，别客气，开口直说！你信任我，我相信你，你指挥，我战斗，成了！现在的我们，就是罗德岛最佳拍档！啊，这些话，可别告诉阿米娅哦。', 'char_017_huang/CN_006');
INSERT INTO `t_operator_voice` VALUES (3935, 116, '信赖提升后交谈1', '在见到博士你之前，就已经有很多人向我提过你失忆前的事迹。当然，你好像和他们说的那个博士不太一样，我是不是也该把你们当作两个不同的人？过去的博士，以及现在的博士，哈哈。', 'char_017_huang/CN_007');
INSERT INTO `t_operator_voice` VALUES (3936, 116, '信赖提升后交谈2', '凯尔希医生说了，总有一天你会真正担起罗德岛里属于你的那份责任。凯尔希医生救了我的命，我信任她；而她也说了，你是怎么样的人，需要我自己去确认。我会的，只要矿石病还没夺走我的生命。', 'char_017_huang/CN_008');
INSERT INTO `t_operator_voice` VALUES (3937, 116, '信赖提升后交谈3', '啊，博士，毛巾递我一下。嗯，今天的训练任务已经完成了。保证每天六小时的训练，才能一直处在备战状态，接到命令就立即出发。行啦，让你久等了，这次我们去哪？说好了，这回你出钱哦？', 'char_017_huang/CN_009');
INSERT INTO `t_operator_voice` VALUES (3938, 116, '闲置', '要是把博士抬去寝室，会不会被人误会啊？干脆就这样子放着好了......？', 'char_017_huang/CN_010');
INSERT INTO `t_operator_voice` VALUES (3939, 116, '干员报到', '罗德岛干员，煌，向你报道。拿着，这是可以直接调遣我的权限认可书，凯尔希医生已经签了名了。好了博士，就让其他小队看看我们的本事吧！', 'char_017_huang/CN_011');
INSERT INTO `t_operator_voice` VALUES (3940, 116, '观看作战记录', '这样的战局我也经历过很多次了，更艰难的可能还在后面等着我们。', 'char_017_huang/CN_012');
INSERT INTO `t_operator_voice` VALUES (3941, 116, '精英化晋升1', '哈，有没有感觉周围的空气变得燥热了？没有？咦，我再加加温试试，还想着这样能炒热气氛来着......', 'char_017_huang/CN_013');
INSERT INTO `t_operator_voice` VALUES (3942, 116, '精英化晋升2', '阿米娅、凯尔希医生、还有现在的你，罗德岛干员们承认你们，是因为你们没有放弃他们。我相信为感染者而战是正义的，博士。我们的战斗是有价值的。', 'char_017_huang/CN_014');
INSERT INTO `t_operator_voice` VALUES (3943, 116, '编入队伍', '要出战了吗？算我一个吧！', 'char_017_huang/CN_017');
INSERT INTO `t_operator_voice` VALUES (3944, 116, '任命队长', '哦哦，指挥啊，把敌人全都干掉什么的，我还是懂的！', 'char_017_huang/CN_018');
INSERT INTO `t_operator_voice` VALUES (3945, 116, '行动出发', '攻坚干员的标准可是很高的，要跟紧我的脚步！', 'char_017_huang/CN_019');
INSERT INTO `t_operator_voice` VALUES (3946, 116, '行动开始', '虽然有点暴力，但我可是很喜欢切裂钢铁和掩体的！', 'char_017_huang/CN_020');
INSERT INTO `t_operator_voice` VALUES (3947, 116, '选中干员1', '高温气体动力学好难学的！', 'char_017_huang/CN_021');
INSERT INTO `t_operator_voice` VALUES (3948, 116, '选中干员2', '见识下精英干员们教给我的锯技吧！', 'char_017_huang/CN_022');
INSERT INTO `t_operator_voice` VALUES (3949, 116, '部署1', '这里的温度还是太低了。', 'char_017_huang/CN_023');
INSERT INTO `t_operator_voice` VALUES (3950, 116, '部署2', '听见这链锯的轰鸣了吗？', 'char_017_huang/CN_024');
INSERT INTO `t_operator_voice` VALUES (3951, 116, '作战中1', '如果看见哪里着火了，那只是副作用！', 'char_017_huang/CN_025');
INSERT INTO `t_operator_voice` VALUES (3952, 116, '作战中2', '不想被锯成两半的话，现在就投降吧！', 'char_017_huang/CN_026');
INSERT INTO `t_operator_voice` VALUES (3953, 116, '作战中3', '我洒下的血只会让气氛更加热烈！', 'char_017_huang/CN_027');
INSERT INTO `t_operator_voice` VALUES (3954, 116, '作战中4', '鲜血染尽武器，蒸腾整片战场！', 'char_017_huang/CN_028');
INSERT INTO `t_operator_voice` VALUES (3955, 116, '4星结束行动', '没有一种牺牲是无谓的，没有一种胜利是毫无代价的。', 'char_017_huang/CN_029');
INSERT INTO `t_operator_voice` VALUES (3956, 116, '3星结束行动', '感染者的权利，就由感染者自己来争取。', 'char_017_huang/CN_030');
INSERT INTO `t_operator_voice` VALUES (3957, 116, '非3星结束行动', '下次再努力吧，我们总可以做得更好的，对不对？', 'char_017_huang/CN_031');
INSERT INTO `t_operator_voice` VALUES (3958, 116, '行动失败', '遗憾，运气似乎没站在我们这边。', 'char_017_huang/CN_032');
INSERT INTO `t_operator_voice` VALUES (3959, 116, '进驻设施', '这次罗德岛有没有添置什么新设施啊？', 'char_017_huang/CN_033');
INSERT INTO `t_operator_voice` VALUES (3960, 116, '戳一下', '别，别闹！', 'char_017_huang/CN_034');
INSERT INTO `t_operator_voice` VALUES (3961, 116, '信赖触摸', '很热！我在流汗欸！', 'char_017_huang/CN_036');
INSERT INTO `t_operator_voice` VALUES (3962, 116, '标题', '明日方舟。', 'char_017_huang/CN_037');
INSERT INTO `t_operator_voice` VALUES (3963, 116, '问候', '博士，我们什么时候出任务呢？', 'char_017_huang/CN_042');
INSERT INTO `t_operator_voice` VALUES (3964, 117, '任命助理', '博、博士！不好意思，能让一下吗？你脚下有一枚硬币......', 'char_383_snsant/CN_001');
INSERT INTO `t_operator_voice` VALUES (3965, 117, '交谈1', '我的理想就是让整个泰拉......啊，不，还是先从龙门......不，首先要让罗德岛认可我，或者，让博士你由衷地表扬我！对，暂时就是这个理想......', 'char_383_snsant/CN_002');
INSERT INTO `t_operator_voice` VALUES (3966, 117, '交谈2', '呜，昨天离开办公室的时候忘记关灯了......欸？罗德岛有发电室所以不需要缴纳电费？真的？那以后岂不是能吃得起晚饭了？', 'char_383_snsant/CN_003');
INSERT INTO `t_operator_voice` VALUES (3967, 117, '交谈3', '这就是杰西卡订购的源石弹药......呃？博、博士，这个金额是不是算错了？还是说这是什么偏远小国的货币？不是？就是龙门币？原原原原来如此，我我我没事，只是有点意意意意外。', 'char_383_snsant/CN_004');
INSERT INTO `t_operator_voice` VALUES (3968, 117, '晋升后交谈1', '矿石病......唔，虽然有点超出我的专业领域，但似乎是个值得探讨的方向......可是如果再多出一个课题，就要从一日两餐变成两日一餐了，呜......', 'char_383_snsant/CN_005');
INSERT INTO `t_operator_voice` VALUES (3969, 117, '晋升后交谈2', '勤俭节约是一种美德，但其实对我而言，更接近一种习惯吧......别、别看我这样，如果朋友或者博士你真的需要帮助的话，我还是会慷慨解囊的！真的！', 'char_383_snsant/CN_006');
INSERT INTO `t_operator_voice` VALUES (3970, 117, '信赖提升后交谈1', '您想看看我的钩爪？可以是可以......但是千万不要碰这个按钮，还有握住握把的时候小心拇指......等等，缠绳索方法也不对，还有——啊，抱歉，别在意我，您接着看吧......', 'char_383_snsant/CN_007');
INSERT INTO `t_operator_voice` VALUES (3971, 117, '信赖提升后交谈2', '等我成为万众瞩目的大科学家，赚到好多好多的钱之后，我一定要每日三餐都加上甜点，和杰西卡一样，然后，毫无负担地为龙门建设出一份力，嗯......如果有那一天呢。', 'char_383_snsant/CN_008');
INSERT INTO `t_operator_voice` VALUES (3972, 117, '信赖提升后交谈3', '钱可是很重要的，回龙门之前，我拒绝过研究所的一大笔挽留费，所以我的自研课题全泡汤了......不过还是回家更重要啦，钱可以再攒，而且现在我已经不会因为数字太大而头晕目眩了！', 'char_383_snsant/CN_009');
INSERT INTO `t_operator_voice` VALUES (3973, 117, '闲置', '......睡着了？那剩下的就交给我吧，虽然肚子有点饿。', 'char_383_snsant/CN_010');
INSERT INTO `t_operator_voice` VALUES (3974, 117, '干员报到', '呜，我的头！不好意思，您、您就是博士？初次见面，我叫雪雉，对不起，不该低着头走路的......', 'char_383_snsant/CN_011');
INSERT INTO `t_operator_voice` VALUES (3975, 117, '观看作战记录', '唔啊，这可真不是加把劲就能学得来的呀......', 'char_383_snsant/CN_012');
INSERT INTO `t_operator_voice` VALUES (3976, 117, '精英化晋升1', '诶诶？晋、晋升吗？稍微感觉到了一点压力，不过没关系，这样就离出人头地更进一步了！', 'char_383_snsant/CN_013');
INSERT INTO `t_operator_voice` VALUES (3977, 117, '精英化晋升2', '“天道酬勤”，你说对吧，博士？', 'char_383_snsant/CN_014');
INSERT INTO `t_operator_voice` VALUES (3978, 117, '编入队伍', '没关系，只要像训练时那样好好努力就行了，呜......', 'char_383_snsant/CN_017');
INSERT INTO `t_operator_voice` VALUES (3979, 117, '任命队长', '让我、我来做队长吗？我真的不懂战斗的事情喔？', 'char_383_snsant/CN_018');
INSERT INTO `t_operator_voice` VALUES (3980, 117, '行动出发', '钩爪检查完毕，深呼吸，我可以的！', 'char_383_snsant/CN_019');
INSERT INTO `t_operator_voice` VALUES (3981, 117, '行动开始', '发现敌人！', 'char_383_snsant/CN_020');
INSERT INTO `t_operator_voice` VALUES (3982, 117, '选中干员1', '我、我吗？', 'char_383_snsant/CN_021');
INSERT INTO `t_operator_voice` VALUES (3983, 117, '选中干员2', '我尽力！', 'char_383_snsant/CN_022');
INSERT INTO `t_operator_voice` VALUES (3984, 117, '部署1', '钩爪，不会出问题吧？', 'char_383_snsant/CN_023');
INSERT INTO `t_operator_voice` VALUES (3985, 117, '部署2', '第一次 ，离战场这么近......', 'char_383_snsant/CN_024');
INSERT INTO `t_operator_voice` VALUES (3986, 117, '作战中1', '钩爪准备！', 'char_383_snsant/CN_025');
INSERT INTO `t_operator_voice` VALUES (3987, 117, '作战中2', '别动喔，这样会轻松点！', 'char_383_snsant/CN_026');
INSERT INTO `t_operator_voice` VALUES (3988, 117, '作战中3', '好、好重！', 'char_383_snsant/CN_027');
INSERT INTO `t_operator_voice` VALUES (3989, 117, '作战中4', '装备状态不错！', 'char_383_snsant/CN_028');
INSERT INTO `t_operator_voice` VALUES (3990, 117, '4星结束行动', '只要我们齐心协力，没有迈不过去的坎，博士！', 'char_383_snsant/CN_029');
INSERT INTO `t_operator_voice` VALUES (3991, 117, '3星结束行动', '没有一丁点浪费，完美的胜利！', 'char_383_snsant/CN_030');
INSERT INTO `t_operator_voice` VALUES (3992, 117, '非3星结束行动', '有逃跑的敌人，要、要追吗？', 'char_383_snsant/CN_031');
INSERT INTO `t_operator_voice` VALUES (3993, 117, '行动失败', '我、我去把那些损坏的零件都收集起来，至少可以减少损失......', 'char_383_snsant/CN_032');
INSERT INTO `t_operator_voice` VALUES (3994, 117, '进驻设施', '唔啊，这就是罗德岛的内部吗，好羡慕......', 'char_383_snsant/CN_033');
INSERT INTO `t_operator_voice` VALUES (3995, 117, '戳一下', '咿呀！', 'char_383_snsant/CN_034');
INSERT INTO `t_operator_voice` VALUES (3996, 117, '信赖触摸', '博士？', 'char_383_snsant/CN_036');
INSERT INTO `t_operator_voice` VALUES (3997, 117, '标题', '明日方舟。', 'char_383_snsant/CN_037');
INSERT INTO `t_operator_voice` VALUES (3998, 117, '问候', '您好，博士。', 'char_383_snsant/CN_042');
INSERT INTO `t_operator_voice` VALUES (3999, 118, '任命助理', '嗨，老板，吃过没，我这有些饼干你要吗？', 'char_226_hmau/CN_001');
INSERT INTO `t_operator_voice` VALUES (4000, 118, '交谈1', '老板，阿要是来骚扰您，还麻烦您多担待担待，这小子虽然爱耍人，不过没什么恶意的，您要是真受不了，喊我来收拾他也行。', 'char_226_hmau/CN_002');
INSERT INTO `t_operator_voice` VALUES (4001, 118, '交谈2', '老板，您要是饿了就说一声，我去给您炒几个菜。别看我这样，其实我从老鲤那里学了几手的。', 'char_226_hmau/CN_003');
INSERT INTO `t_operator_voice` VALUES (4002, 118, '交谈3', '老板，罗德岛有没有开设机械班的？我对机械很有兴趣，想要进修一下。', 'char_226_hmau/CN_004');
INSERT INTO `t_operator_voice` VALUES (4003, 118, '晋升后交谈1', '哈哈，老板，不瞒你说，其实很长一段时间里，我都以为每个侦探都会做老鲤那些事，比如去黑帮火并现场喝茶看戏什么的。', 'char_226_hmau/CN_005');
INSERT INTO `t_operator_voice` VALUES (4004, 118, '晋升后交谈2', '老板，您也知道，以阿那小子的头脑和能力，将来一定会有大出息的。但对我来说，我只希望，他永远不要变回以前的样子。', 'char_226_hmau/CN_006');
INSERT INTO `t_operator_voice` VALUES (4005, 118, '信赖提升后交谈1', '我和老鲤的关系啊，怎么说呢，最早我还是比较怕他的，因为我特别不会应付那种一看城府就很深的人......现在肯定不怕了，也认识这么多年了嘛。', 'char_226_hmau/CN_007');
INSERT INTO `t_operator_voice` VALUES (4006, 118, '信赖提升后交谈2', '老板，您要是有什么想知道的事，我可以帮您联络老鲤，只要是龙门里发生的事情，我还没见过他不知道的。', 'char_226_hmau/CN_008');
INSERT INTO `t_operator_voice` VALUES (4007, 118, '信赖提升后交谈3', '打打杀杀的事情，老实说我不是很擅长，大家坐下和和气气的该有多好。不过很多时候就是没办法，我也有不想失去的东西。以前，那是侦探所，现在，也包括罗德岛了。', 'char_226_hmau/CN_009');
INSERT INTO `t_operator_voice` VALUES (4008, 118, '闲置', '老......呼，好险，差点就把老板吵醒了，那我也眯一会儿好了。', 'char_226_hmau/CN_010');
INSERT INTO `t_operator_voice` VALUES (4009, 118, '干员报到', '您就是老鲤说的博士了吧，您好，我是吽，擅长的是安保工作和机械修理，您要是出门缺个保镖，或者电视冰箱坏了，都可以叫我。', 'char_226_hmau/CN_011');
INSERT INTO `t_operator_voice` VALUES (4010, 118, '观看作战记录', '人外有人，天外有天呐，我也要加把劲才行。', 'char_226_hmau/CN_012');
INSERT INTO `t_operator_voice` VALUES (4011, 118, '精英化晋升1', '谢谢老板，我会更加努力的。', 'char_226_hmau/CN_013');
INSERT INTO `t_operator_voice` VALUES (4012, 118, '精英化晋升2', '老板，别客气，尽管差遣我就好。别的事情我没什么自信，但是在皮糙肉厚这一点上，我自认还是有点水平的！', 'char_226_hmau/CN_014');
INSERT INTO `t_operator_voice` VALUES (4013, 118, '编入队伍', '任务？没问题。', 'char_226_hmau/CN_017');
INSERT INTO `t_operator_voice` VALUES (4014, 118, '任命队长', '我保证让大家平安回来。', 'char_226_hmau/CN_018');
INSERT INTO `t_operator_voice` VALUES (4015, 118, '行动出发', '大家跟紧我，可别走丢了。', 'char_226_hmau/CN_019');
INSERT INTO `t_operator_voice` VALUES (4016, 118, '行动开始', '有我在，别怕。', 'char_226_hmau/CN_020');
INSERT INTO `t_operator_voice` VALUES (4017, 118, '选中干员1', '包在我身上！', 'char_226_hmau/CN_021');
INSERT INTO `t_operator_voice` VALUES (4018, 118, '选中干员2', '哪里需要我？', 'char_226_hmau/CN_022');
INSERT INTO `t_operator_voice` VALUES (4019, 118, '部署1', '都来我的身后！', 'char_226_hmau/CN_023');
INSERT INTO `t_operator_voice` VALUES (4020, 118, '部署2', '我来掩护各位！', 'char_226_hmau/CN_024');
INSERT INTO `t_operator_voice` VALUES (4021, 118, '作战中1', '别想前进一步！', 'char_226_hmau/CN_025');
INSERT INTO `t_operator_voice` VALUES (4022, 118, '作战中2', '有谁需要治疗？', 'char_226_hmau/CN_026');
INSERT INTO `t_operator_voice` VALUES (4023, 118, '作战中3', '别想伤害我的同伴！', 'char_226_hmau/CN_027');
INSERT INTO `t_operator_voice` VALUES (4024, 118, '作战中4', '这点小伤不算什么！', 'char_226_hmau/CN_028');
INSERT INTO `t_operator_voice` VALUES (4025, 118, '4星结束行动', '哈，哈，哈......老板，不好意思，让我歇会儿，可把我累坏了。', 'char_226_hmau/CN_029');
INSERT INTO `t_operator_voice` VALUES (4026, 118, '3星结束行动', '老板，您在这边休息一下吧，我去打扫一下战场。', 'char_226_hmau/CN_030');
INSERT INTO `t_operator_voice` VALUES (4027, 118, '非3星结束行动', '老板，别离我太远，还有漏网之鱼。', 'char_226_hmau/CN_031');
INSERT INTO `t_operator_voice` VALUES (4028, 118, '行动失败', '快，老板，上我的背，我背着您撤退！', 'char_226_hmau/CN_032');
INSERT INTO `t_operator_voice` VALUES (4029, 118, '进驻设施', '巡逻就交给我吧。', 'char_226_hmau/CN_033');
INSERT INTO `t_operator_voice` VALUES (4030, 118, '戳一下', '哪里有机器坏了吗？', 'char_226_hmau/CN_034');
INSERT INTO `t_operator_voice` VALUES (4031, 118, '信赖触摸', '老板，需要我帮忙吗？', 'char_226_hmau/CN_036');
INSERT INTO `t_operator_voice` VALUES (4032, 118, '标题', '明日方舟。', 'char_226_hmau/CN_037');
INSERT INTO `t_operator_voice` VALUES (4033, 118, '问候', '老板，早啊。', 'char_226_hmau/CN_042');
INSERT INTO `t_operator_voice` VALUES (4034, 119, '任命助理', '对了，老板，你叫我的时候记得拖一下长音，不然我不一定能听得出来你在叫我。', 'char_225_haak/CN_001');
INSERT INTO `t_operator_voice` VALUES (4035, 119, '交谈1', '喂喂喂，不是吧，那个血魔小姐，难道她就是“血先生”？！嗯......上次的医术对决还没有结果呢，现在不是最好的机会吗？不过试验品就要重新准备一下了，嘿嘿嘿。', 'char_225_haak/CN_002');
INSERT INTO `t_operator_voice` VALUES (4036, 119, '交谈2', '吽那家伙，又是说了些让你看管我之类的话吧。照顾？啊，确实是他会用的词儿。老板，我看得出你和我一样，是那种随心所欲的人吧？只要你愿意，别听那些傻话，我们有很多有趣的事能做哦？', 'char_225_haak/CN_003');
INSERT INTO `t_operator_voice` VALUES (4037, 119, '交谈3', '试剂、药物、手术和培菌，嗯，这些都是我的兴趣，当然，也仅仅只是兴趣。医生？不建议那么叫我哦，我没什么救人的本事。让人意识到生命的意义才叫拯救，我只是不让他们死。', 'char_225_haak/CN_004');
INSERT INTO `t_operator_voice` VALUES (4038, 119, '晋升后交谈1', '医疗部的人找我？别了吧，我不怎么喜欢专业医生的。坚信自己的职业的高尚，为了拯救别人而感到满足......信念一旦破碎，是会死的。总是看着这种事发生，我有些难过，真的。', 'char_225_haak/CN_005');
INSERT INTO `t_operator_voice` VALUES (4039, 119, '晋升后交谈2', '我不讨厌罗德岛，相反，我觉得你们很专业。这片大地上每时每刻都有人在死，光是缩起来医疗制药，直到泰拉毁灭也不会发挥什么效果的。没多少人明白这一点，但你们心里很清楚，嗯嗯，真好！', 'char_225_haak/CN_006');
INSERT INTO `t_operator_voice` VALUES (4040, 119, '信赖提升后交谈1', '嘲笑，讨厌？没有啦老板，我只是感觉所有人的命运都很悲惨。伤痛当然可怕，但只要创造它们的人还在，医学又有什么意义？谁能拯救要被杀的人？泰拉需要的并不是医生，老板。', 'char_225_haak/CN_007');
INSERT INTO `t_operator_voice` VALUES (4041, 119, '信赖提升后交谈2', '我爸，也算是很厉害的医生了，天天把救死扶伤挂在嘴边。然而他被侮蔑、排挤加上陷害，一下就成了个黑医生，无恶不作那种。我也不觉得这种事有多稀奇啦，真的，虽然它也算改变了我的人生吧。', 'char_225_haak/CN_008');
INSERT INTO `t_operator_voice` VALUES (4042, 119, '信赖提升后交谈3', '槐琥姐和吽出去了吗？出去了？那太好了，就让我在你办公室里浪费下时间吧。其实啊，他们才是能让大地变好的人。而老板你，我猜猜，其实我们两个更相近些，是不是？', 'char_225_haak/CN_009');
INSERT INTO `t_operator_voice` VALUES (4043, 119, '闲置', '欸，你很累吗？要不要我给你个新配方，免费的哦。', 'char_225_haak/CN_010');
INSERT INTO `t_operator_voice` VALUES (4044, 119, '干员报到', '你就是博士吧？幸会幸会。我叫阿，对，就是阿——的阿。事务所在罗德岛有些事情要处理，所以我就来了。事先问问，你对医疗事故敏感吗？', 'char_225_haak/CN_011');
INSERT INTO `t_operator_voice` VALUES (4045, 119, '观看作战记录', '嗯，你是想说，我可以在这种战斗里帮上忙吗？不能吧？', 'char_225_haak/CN_012');
INSERT INTO `t_operator_voice` VALUES (4046, 119, '精英化晋升1', '升职？还有这样的好事，果然事务所是在压榨我的劳动力吧，是吧。', 'char_225_haak/CN_013');
INSERT INTO `t_operator_voice` VALUES (4047, 119, '精英化晋升2', '我其实不清楚自己算不算是个有用的人啦。毕竟在我看来，有用的人应该是好人才行，我嘛，还是有自知之明的。不过，你要是得病了，记得找我哦。', 'char_225_haak/CN_014');
INSERT INTO `t_operator_voice` VALUES (4048, 119, '编入队伍', '任务吗？好啊，走吧。', 'char_225_haak/CN_017');
INSERT INTO `t_operator_voice` VALUES (4049, 119, '任命队长', '那新药的效果，就拜托各位之后告诉我咯。', 'char_225_haak/CN_018');
INSERT INTO `t_operator_voice` VALUES (4050, 119, '行动出发', '一旦我开始动手术，可就没人能阻止我了。', 'char_225_haak/CN_019');
INSERT INTO `t_operator_voice` VALUES (4051, 119, '行动开始', '放心，有我在，你们想死都难。', 'char_225_haak/CN_020');
INSERT INTO `t_operator_voice` VALUES (4052, 119, '选中干员1', '我下的药可不轻。', 'char_225_haak/CN_021');
INSERT INTO `t_operator_voice` VALUES (4053, 119, '选中干员2', '战场不适合我，但治疗实在是很快乐啊。', 'char_225_haak/CN_022');
INSERT INTO `t_operator_voice` VALUES (4054, 119, '部署1', '就让我们临床实验一下新成果吧。', 'char_225_haak/CN_023');
INSERT INTO `t_operator_voice` VALUES (4055, 119, '部署2', '平时不好好善待自己，出问题的时候可是会超级痛的哦。', 'char_225_haak/CN_024');
INSERT INTO `t_operator_voice` VALUES (4056, 119, '作战中1', '所有的生命都平等，但你的好像有点例外。', 'char_225_haak/CN_025');
INSERT INTO `t_operator_voice` VALUES (4057, 119, '作战中2', '这管试剂打下去，你就知道厉害了。', 'char_225_haak/CN_026');
INSERT INTO `t_operator_voice` VALUES (4058, 119, '作战中3', '我这种药水，说不定能让坏人重新做人。', 'char_225_haak/CN_027');
INSERT INTO `t_operator_voice` VALUES (4059, 119, '作战中4', '医学，就是一旦打开就没法关上的恶毒匣子。', 'char_225_haak/CN_028');
INSERT INTO `t_operator_voice` VALUES (4060, 119, '4星结束行动', '也许我的技术不能让这片大地变得更好，但你我合起来，能让它变得没那么坏。', 'char_225_haak/CN_029');
INSERT INTO `t_operator_voice` VALUES (4061, 119, '3星结束行动', '只要没人看见，抓走敌人做实验就不会违反公约啦，放心放心。', 'char_225_haak/CN_030');
INSERT INTO `t_operator_voice` VALUES (4062, 119, '非3星结束行动', '你真仁慈啊，居然就这样放他们走掉了。', 'char_225_haak/CN_031');
INSERT INTO `t_operator_voice` VALUES (4063, 119, '行动失败', '别紧张，老板，只要没死，我都救得回来。', 'char_225_haak/CN_032');
INSERT INTO `t_operator_voice` VALUES (4064, 119, '进驻设施', '看看，我的新实验室会在哪里呢？', 'char_225_haak/CN_033');
INSERT INTO `t_operator_voice` VALUES (4065, 119, '戳一下', '啊？', 'char_225_haak/CN_034');
INSERT INTO `t_operator_voice` VALUES (4066, 119, '信赖触摸', '老板，找到新试验品了吗？', 'char_225_haak/CN_036');
INSERT INTO `t_operator_voice` VALUES (4067, 119, '标题', '明日方舟。', 'char_225_haak/CN_037');
INSERT INTO `t_operator_voice` VALUES (4068, 119, '问候', '老板，今天身体还好吧？', 'char_225_haak/CN_042');
INSERT INTO `t_operator_voice` VALUES (4069, 120, '任命助理', '啊，好闲，我想去逛街......不管了，我要出门！......嗯？你怎么还不换鞋？你当然要跟我走啊，不然呢。', 'char_2014_nian/CN_001');
INSERT INTO `t_operator_voice` VALUES (4070, 120, '交谈1', '看你一副很无聊的样子......喏，这个玩具给你。在工作？工作不就是很无聊吗？你可要看紧了，这个小偶人，一不注意就会消失掉哦。', 'char_2014_nian/CN_002');
INSERT INTO `t_operator_voice` VALUES (4071, 120, '交谈2', '哎，好饿啊，自己做个菜比我打个锅还难......我有个弟弟很擅长这事儿，每次聚会的时候，你想吃什么他就做什么，那才叫厨师！罗德岛食堂做的是个锤子菜啊。', 'char_2014_nian/CN_003');
INSERT INTO `t_operator_voice` VALUES (4072, 120, '交谈3', '龙门是个不错的地方，但口味太淡了，一点都不辣，这怎么行！我和你说，辣，是种生活态度，当些微的刺痛感和浓烈的冲动在舌头上面爆开的时候，你会觉得，“啊，我的人生真有滋味。”', 'char_2014_nian/CN_004');
INSERT INTO `t_operator_voice` VALUES (4073, 120, '晋升后交谈1', '我擅长的，原先只是种非常生活化的技术，只是我实在太容易被现状影响了，现在的我可真是战火缠身哪。熟悉了一把把武器拿在手里的感觉，偶尔手头空空还会有那么点不适应，真难办。', 'char_2014_nian/CN_005');
INSERT INTO `t_operator_voice` VALUES (4074, 120, '晋升后交谈2', '仪式之所以能不断流传，不在于它本身有什么意义，而在于它为众人带来了什么意义。演出有收获，企盼有回报，这又是我喜欢的故事类型，何乐不为呢？', 'char_2014_nian/CN_006');
INSERT INTO `t_operator_voice` VALUES (4075, 120, '信赖提升后交谈1', '不适应现实的人会被现实淘汰，这算是很普通的常识吧？但我的兄弟姐妹里，还是有那么几个一直不肯放下架子呢。活的那么无趣，岁月会变成一种折磨啊。', 'char_2014_nian/CN_007');
INSERT INTO `t_operator_voice` VALUES (4076, 120, '信赖提升后交谈2', '这么多年过来，我的梦已经变得很模糊了。唉，比起做梦，还有更重要的事情等着我去做，也许这就是活着。活着真难啊，可别人努力活下去的样子，我真是喜欢得不得了。你，要不要试试做我的砧？', 'char_2014_nian/CN_008');
INSERT INTO `t_operator_voice` VALUES (4077, 120, '信赖提升后交谈3', '冶铸是种凡俗工艺，但它也曾是往日生活的某种起源。纵使其它的一切都磨灭了，我也会把它保存得好好的，这是我曾经的选择。你也作出过重要的选择吧？看到最后吧。看到最后，才算完满。', 'char_2014_nian/CN_009');
INSERT INTO `t_operator_voice` VALUES (4078, 120, '闲置', '你怎么又睡着了？起来起来，陪我去买点东西，快点快点。', 'char_2014_nian/CN_010');
INSERT INTO `t_operator_voice` VALUES (4079, 120, '干员报到', '有人吗？贵公司的员工和我说这里生活条件很好，免费食宿，还有多种多样的娱乐方式......闲杂人员不得入内？哎，别这样，给个面子嘛。', 'char_2014_nian/CN_011');
INSERT INTO `t_operator_voice` VALUES (4080, 120, '观看作战记录', '嗯？这些东西对我毫无帮助啊。技巧什么的，只对看重它的人有用吧。', 'char_2014_nian/CN_012');
INSERT INTO `t_operator_voice` VALUES (4081, 120, '精英化晋升1', '晋升？我想想......是哦，过去好像也有这种事情来着。挺好的，谢谢你！', 'char_2014_nian/CN_013');
INSERT INTO `t_operator_voice` VALUES (4082, 120, '精英化晋升2', '人和器物一样，有着品格之分。你还算有点意思，或者说......你本就不太一样？你似乎值得我铸把兵器。哎，可你又打不了架，铸剑送你，很浪费啊。', 'char_2014_nian/CN_014');
INSERT INTO `t_operator_voice` VALUES (4083, 120, '编入队伍', '干戈之事，真是在哪个时代都避不过啊。', 'char_2014_nian/CN_017');
INSERT INTO `t_operator_voice` VALUES (4084, 120, '任命队长', '啊？指挥？我没做过欸，行不行啊我。', 'char_2014_nian/CN_018');
INSERT INTO `t_operator_voice` VALUES (4085, 120, '行动出发', '打造武器的工匠，必须承担它们带来的血腥。', 'char_2014_nian/CN_019');
INSERT INTO `t_operator_voice` VALUES (4086, 120, '行动开始', '这把剑能把你们的存在价值连同肉体一起划开。', 'char_2014_nian/CN_020');
INSERT INTO `t_operator_voice` VALUES (4087, 120, '选中干员1', '哦？要我亲自动手吗？', 'char_2014_nian/CN_021');
INSERT INTO `t_operator_voice` VALUES (4088, 120, '选中干员2', '铸乃众相之柱。', 'char_2014_nian/CN_022');
INSERT INTO `t_operator_voice` VALUES (4089, 120, '部署1', '“生杀之机，焉能拱手相让？”', 'char_2014_nian/CN_023');
INSERT INTO `t_operator_voice` VALUES (4090, 120, '部署2', '哎，赶紧解决了事啦。', 'char_2014_nian/CN_024');
INSERT INTO `t_operator_voice` VALUES (4091, 120, '作战中1', '以子之矛攻子之盾，就会产生大爆炸！', 'char_2014_nian/CN_025');
INSERT INTO `t_operator_voice` VALUES (4092, 120, '作战中2', '我的身体内部，可是一千四百度喔。', 'char_2014_nian/CN_026');
INSERT INTO `t_operator_voice` VALUES (4093, 120, '作战中3', '“明王圣帝，谁能去兵哉？”', 'char_2014_nian/CN_027');
INSERT INTO `t_operator_voice` VALUES (4094, 120, '作战中4', '我为什么要在你们这些低劣造物上浪费时间？', 'char_2014_nian/CN_028');
INSERT INTO `t_operator_voice` VALUES (4095, 120, '4星结束行动', '不错嘛！在你身上，我看到了你们的一线希望哦。', 'char_2014_nian/CN_029');
INSERT INTO `t_operator_voice` VALUES (4096, 120, '3星结束行动', '这点小菜还不够填牙缝的。', 'char_2014_nian/CN_030');
INSERT INTO `t_operator_voice` VALUES (4097, 120, '非3星结束行动', '喂，怎么出差错了？铸件里可不能有气孔呀。', 'char_2014_nian/CN_031');
INSERT INTO `t_operator_voice` VALUES (4098, 120, '行动失败', '行不行啊......让我出力的话，各位队员将会遇到的危险，也许比现在还大。', 'char_2014_nian/CN_032');
INSERT INTO `t_operator_voice` VALUES (4099, 120, '进驻设施', '我能不能自己装饰寝室？', 'char_2014_nian/CN_033');
INSERT INTO `t_operator_voice` VALUES (4100, 120, '戳一下', '欸，不要啦。', 'char_2014_nian/CN_034');
INSERT INTO `t_operator_voice` VALUES (4101, 120, '信赖触摸', '别动！在给你做挂饰呢，稳重点！', 'char_2014_nian/CN_036');
INSERT INTO `t_operator_voice` VALUES (4102, 120, '标题', '明日方舟。', 'char_2014_nian/CN_037');
INSERT INTO `t_operator_voice` VALUES (4103, 120, '问候', '博士，你吃了没？', 'char_2014_nian/CN_042');
INSERT INTO `t_operator_voice` VALUES (4104, 121, '任命助理', '看仔细，文书先以事项分类，再依轻重缓急时限排列，最后从近至远推进，这样处理起来最有效率！......等等，这些文书工作我似乎都不小心完成了。这似乎......是你的工作？', 'char_306_leizi/CN_001');
INSERT INTO `t_operator_voice` VALUES (4105, 121, '交谈1', '我查阅过精英干员煌的所有档案，没有任何对她真名、出生地、出生时间的记录。别和我说你不知情，你明明是这里的领头人之一。真不知道？那这......如何是好。抱歉，我错怪你了。', 'char_306_leizi/CN_002');
INSERT INTO `t_operator_voice` VALUES (4106, 121, '交谈2', '不必太顾忌我。除我手上的案件外，舰上的所有事情，我一概会当作不曾发生。只不过，如果我门外的走道再发生爆炸，我就要让肇事者的头发竖上个两星期！', 'char_306_leizi/CN_003');
INSERT INTO `t_operator_voice` VALUES (4107, 121, '交谈3', '所谓“身在其位，当谋其政”，作为罗德岛的领头人之一，你的责任并不是......哎？发梢又翘起来了？稍等！容我先去、先去整理妆容！好奇怪，早上已经驱过一遍电荷了啊......', 'char_306_leizi/CN_004');
INSERT INTO `t_operator_voice` VALUES (4108, 121, '晋升后交谈1', '时至今日，我也依稀能想起在龙门第一次见到你们的景象。煌，你，陈，身上都藏着无数秘密......我一定会一一揭开。秘密要相互交换？不，你，你，太早了......太早了！', 'char_306_leizi/CN_005');
INSERT INTO `t_operator_voice` VALUES (4109, 121, '晋升后交谈2', '有事都可以问我。只有你开口问，我才能判断是非，决定自己要不要出手。顾及身份？这无关身份！御史台、大理寺、肃政院......都只是机构。有人执行时，法律才能证明自己是正义的。', 'char_306_leizi/CN_006');
INSERT INTO `t_operator_voice` VALUES (4110, 121, '信赖提升后交谈1', '罗德岛的理念，和我的信念有冲突。但我理解求同存异的必要性......提供援助来换取信息，这点也是一样的。', 'char_306_leizi/CN_007');
INSERT INTO `t_operator_voice` VALUES (4111, 121, '信赖提升后交谈2', '无论我身在何地，我体内的电流都奔腾不息。雷电是公义的象征，它驱策着我，时时警醒着我......又有人投诉我弄坏了空调？......无礼！怎么可能！开关空调，我甚至都不用遥控器！', 'char_306_leizi/CN_008');
INSERT INTO `t_operator_voice` VALUES (4112, 121, '信赖提升后交谈3', '煌的身上牵扯着一桩大案。所有线索都已经遭人刻意消灭，但单单几个身份和时间点的巧合，都足以令我怀疑起各个事件间的联系。这件事情与你无关，但你该防患于未然。我只能警告你到这一步了。', 'char_306_leizi/CN_009');
INSERT INTO `t_operator_voice` VALUES (4113, 121, '闲置', '竟然连自己的身体状态都掌控不好......太懈怠了！趴在这里睡觉，难道还等着我给你盖上毯子吗？......没下次了。', 'char_306_leizi/CN_010');
INSERT INTO `t_operator_voice` VALUES (4114, 121, '干员报到', '我已经同贵司签订了搜查协议，不必多虑，这完全是个人行为。如果你想，可以称呼我，惊蛰。问我是谁？忘了在哪见过我？什么？！', 'char_306_leizi/CN_011');
INSERT INTO `t_operator_voice` VALUES (4115, 121, '观看作战记录', '原来如此，和练兵的思路完全不同。', 'char_306_leizi/CN_012');
INSERT INTO `t_operator_voice` VALUES (4116, 121, '精英化晋升1', '晋升？是说......升迁？罗德岛的升迁流程，和普通的民间企业似乎不太一样。', 'char_306_leizi/CN_013');
INSERT INTO `t_operator_voice` VALUES (4117, 121, '精英化晋升2', '我，惊蛰，身负雷法和律法，是大炎的手与眼、理与义。我的理念和罗德岛的并不相同，感染者与普通人的隔阂，我也无心跨过。但现在，我可与你并肩对敌！', 'char_306_leizi/CN_014');
INSERT INTO `t_operator_voice` VALUES (4118, 121, '编入队伍', '罗德岛的究竟有几分实力，我只信自己双眼见证。', 'char_306_leizi/CN_017');
INSERT INTO `t_operator_voice` VALUES (4119, 121, '任命队长', '可以，不过，有代价。', 'char_306_leizi/CN_018');
INSERT INTO `t_operator_voice` VALUES (4120, 121, '行动出发', '听我号令！', 'char_306_leizi/CN_019');
INSERT INTO `t_operator_voice` VALUES (4121, 121, '行动开始', '我对敌人的身份没有兴趣。', 'char_306_leizi/CN_020');
INSERT INTO `t_operator_voice` VALUES (4122, 121, '选中干员1', '就位。', 'char_306_leizi/CN_021');
INSERT INTO `t_operator_voice` VALUES (4123, 121, '选中干员2', '未尝不可。', 'char_306_leizi/CN_022');
INSERT INTO `t_operator_voice` VALUES (4124, 121, '部署1', '好，我知道了。', 'char_306_leizi/CN_023');
INSERT INTO `t_operator_voice` VALUES (4125, 121, '部署2', '听！雷声隆隆！', 'char_306_leizi/CN_024');
INSERT INTO `t_operator_voice` VALUES (4126, 121, '作战中1', '五正霆威，起！', 'char_306_leizi/CN_025');
INSERT INTO `t_operator_voice` VALUES (4127, 121, '作战中2', '有罪无赦，五雷轰顶！', 'char_306_leizi/CN_026');
INSERT INTO `t_operator_voice` VALUES (4128, 121, '作战中3', '一气白雷正法，纵贯方圆三化！', 'char_306_leizi/CN_027');
INSERT INTO `t_operator_voice` VALUES (4129, 121, '作战中4', '雷法的本质......是我的内在和大地化为一体。', 'char_306_leizi/CN_028');
INSERT INTO `t_operator_voice` VALUES (4130, 121, '4星结束行动', '做的不错。你可以稍稍自豪一下，我的标准是很高的。', 'char_306_leizi/CN_029');
INSERT INTO `t_operator_voice` VALUES (4131, 121, '3星结束行动', '不行。你能做得比现在更好！你一定有这种能力，决不能自满！', 'char_306_leizi/CN_030');
INSERT INTO `t_operator_voice` VALUES (4132, 121, '非3星结束行动', '怎么回事，怎么回事？怎么回事？', 'char_306_leizi/CN_031');
INSERT INTO `t_operator_voice` VALUES (4133, 121, '行动失败', '耻辱......！你，走！回去反省！我会抵挡所有敌人，走！', 'char_306_leizi/CN_032');
INSERT INTO `t_operator_voice` VALUES (4134, 121, '进驻设施', '充电插口在哪？', 'char_306_leizi/CN_033');
INSERT INTO `t_operator_voice` VALUES (4135, 121, '戳一下', '休得无礼！', 'char_306_leizi/CN_034');
INSERT INTO `t_operator_voice` VALUES (4136, 121, '信赖触摸', '啊......疼吗？要小心我身上的电流。', 'char_306_leizi/CN_036');
INSERT INTO `t_operator_voice` VALUES (4137, 121, '标题', '明日方舟。', 'char_306_leizi/CN_037');
INSERT INTO `t_operator_voice` VALUES (4138, 121, '问候', '勿要懈怠，博士。', 'char_306_leizi/CN_042');
INSERT INTO `t_operator_voice` VALUES (4139, 122, '任命助理', '我，呃，我该干什么？只要把这摞纸放进盒子里就行了吗？', 'char_2013_cerber/CN_001');
INSERT INTO `t_operator_voice` VALUES (4140, 122, '交谈1', '博士，在干活吗？我可以看吗？好耶！沙发我来啦~', 'char_2013_cerber/CN_002');
INSERT INTO `t_operator_voice` VALUES (4141, 122, '交谈2', '我可是超级强的，别说一个坏人，一百个都打得过。呃，一百个好像有点多......但九十九个还是没问题的！嗯！', 'char_2013_cerber/CN_003');
INSERT INTO `t_operator_voice` VALUES (4142, 122, '交谈3', '我在一个房间里找到了好多这样的小瓶子，里面的酱味道还挺不错的。辣？对哦，好像是有点辣，但还是很好吃啊，博士要不要来一点。', 'char_2013_cerber/CN_004');
INSERT INTO `t_operator_voice` VALUES (4143, 122, '晋升后交谈1', '我的窝在这里，博士的窝在我头上，火神大姐的窝在我左面......好的，我记住了！再记一遍吗？呃，好的。我的窝在头上，博士的窝在左面，大姐的窝在这里......', 'char_2013_cerber/CN_005');
INSERT INTO `t_operator_voice` VALUES (4144, 122, '晋升后交谈2', '原来我是从这里出发的啊，那那那我之前走到哪儿去了？你又是在哪儿抓到我的？这个地图上都能找到吗！', 'char_2013_cerber/CN_006');
INSERT INTO `t_operator_voice` VALUES (4145, 122, '信赖提升后交谈1', '我好饿，肚子已经咕噜咕噜好久了。我们可以去食堂了吗？', 'char_2013_cerber/CN_007');
INSERT INTO `t_operator_voice` VALUES (4146, 122, '信赖提升后交谈2', '源石技艺的东西我不太懂，别人用我收集来的武器好像不会出现那些酷炫效果。不过你放心，只要我在旁边，坏人不会有机会伤害你的。我分得清谁是坏人。', 'char_2013_cerber/CN_008');
INSERT INTO `t_operator_voice` VALUES (4147, 122, '信赖提升后交谈3', '坏人们都是成堆出现的，好朋友却不会总在身边。所以多一把武器，就多一份力量，我说的对吧。那，背后再多塞一把锤头，好吗？标枪也行，真的很有用的，不可以不可以。', 'char_2013_cerber/CN_009');
INSERT INTO `t_operator_voice` VALUES (4148, 122, '闲置', '博士呼噜呼噜的样子还挺有意思的。', 'char_2013_cerber/CN_010');
INSERT INTO `t_operator_voice` VALUES (4149, 122, '干员报到', '你好像还挺和善的，是叫博士对吧。我是刻俄柏，要是觉得拗口可以叫我小刻，以后就在你这儿待着了。', 'char_2013_cerber/CN_011');
INSERT INTO `t_operator_voice` VALUES (4150, 122, '观看作战记录', '我在看，我真的在看！', 'char_2013_cerber/CN_012');
INSERT INTO `t_operator_voice` VALUES (4151, 122, '精英化晋升1', '这个小小的纽扣是送给我的吗？我别起来啦，好看吗？', 'char_2013_cerber/CN_013');
INSERT INTO `t_operator_voice` VALUES (4152, 122, '精英化晋升2', '一把不够，两把来凑，三把备用。只要武器的问题解决好，坏蛋我见一个打一个。', 'char_2013_cerber/CN_014');
INSERT INTO `t_operator_voice` VALUES (4153, 122, '编入队伍', '我都听你的。', 'char_2013_cerber/CN_017');
INSERT INTO `t_operator_voice` VALUES (4154, 122, '任命队长', '指挥？什么是指挥？', 'char_2013_cerber/CN_018');
INSERT INTO `t_operator_voice` VALUES (4155, 122, '行动出发', '回来能有好吃的吗？', 'char_2013_cerber/CN_019');
INSERT INTO `t_operator_voice` VALUES (4156, 122, '行动开始', '呼......好！', 'char_2013_cerber/CN_020');
INSERT INTO `t_operator_voice` VALUES (4157, 122, '选中干员1', '嗯？', 'char_2013_cerber/CN_021');
INSERT INTO `t_operator_voice` VALUES (4158, 122, '选中干员2', '我听到啦！', 'char_2013_cerber/CN_022');
INSERT INTO `t_operator_voice` VALUES (4159, 122, '部署1', '包在我身上。', 'char_2013_cerber/CN_023');
INSERT INTO `t_operator_voice` VALUES (4160, 122, '部署2', '我出发啦！', 'char_2013_cerber/CN_024');
INSERT INTO `t_operator_voice` VALUES (4161, 122, '作战中1', '嘿呀！', 'char_2013_cerber/CN_025');
INSERT INTO `t_operator_voice` VALUES (4162, 122, '作战中2', '去吧！', 'char_2013_cerber/CN_026');
INSERT INTO `t_operator_voice` VALUES (4163, 122, '作战中3', '哒哒哒哒哒！', 'char_2013_cerber/CN_027');
INSERT INTO `t_operator_voice` VALUES (4164, 122, '作战中4', '猎物快停下！', 'char_2013_cerber/CN_028');
INSERT INTO `t_operator_voice` VALUES (4165, 122, '4星结束行动', '有博士动脑子我就轻松多了，博士你真棒！', 'char_2013_cerber/CN_029');
INSERT INTO `t_operator_voice` VALUES (4166, 122, '3星结束行动', '对付他们只要砰！啪！两下就好啦。', 'char_2013_cerber/CN_030');
INSERT INTO `t_operator_voice` VALUES (4167, 122, '非3星结束行动', '你受伤了？要我帮忙包扎吗？', 'char_2013_cerber/CN_031');
INSERT INTO `t_operator_voice` VALUES (4168, 122, '行动失败', '快走快走，晚了就走不了了！', 'char_2013_cerber/CN_032');
INSERT INTO `t_operator_voice` VALUES (4169, 122, '进驻设施', '这个软软的，躺上去一定很舒服。', 'char_2013_cerber/CN_033');
INSERT INTO `t_operator_voice` VALUES (4170, 122, '戳一下', '咬你哦！', 'char_2013_cerber/CN_034');
INSERT INTO `t_operator_voice` VALUES (4171, 122, '信赖触摸', '我能跟着你走吗？', 'char_2013_cerber/CN_036');
INSERT INTO `t_operator_voice` VALUES (4172, 122, '标题', '明日方舟。', 'char_2013_cerber/CN_037');
INSERT INTO `t_operator_voice` VALUES (4173, 122, '问候', '博士好！嗯，是该这样说，没错。', 'char_2013_cerber/CN_042');
INSERT INTO `t_operator_voice` VALUES (4174, 123, '任命助理', '笑容能让人打起精神，试试看微笑吧，博士~', 'char_385_finlpp/CN_001');
INSERT INTO `t_operator_voice` VALUES (4175, 123, '交谈1', '罗德岛应该不是那种黑心企业吧？龙门工业废料处理许可证、龙门城邦排污许可证......嗯，看起来证件是没有问题，而且也有好好在做排污处理，还挺正规的嘛！', 'char_385_finlpp/CN_002');
INSERT INTO `t_operator_voice` VALUES (4176, 123, '交谈2', '呜啊，这个水好好喝~甜甜的，凉凉的，有点像我家乡的山泉......水源在哪里？是怎么过滤的？设施用了什么器材？快告诉我嘛博士！', 'char_385_finlpp/CN_003');
INSERT INTO `t_operator_voice` VALUES (4177, 123, '交谈3', '这个月的废水排放量有超标的迹象诶，呃，是不是我泡澡泡太多了......要反省一下！', 'char_385_finlpp/CN_004');
INSERT INTO `t_operator_voice` VALUES (4178, 123, '晋升后交谈1', '我最喜欢家乡啦！乡下地方虽然比不上城里气派，生活也没那么方便，但是我们那儿的水清凉好味，空气也新鲜，嘿嘿，这些可都是城里少有的呢！', 'char_385_finlpp/CN_005');
INSERT INTO `t_operator_voice` VALUES (4179, 123, '晋升后交谈2', '城邦附近的污染越来越严重了，之前家乡的水源也出了问题，花了我们很大力气才解决。天灾明目张胆地威胁着我们，污染却会不知不觉地缩小我们的生存空间！保护生活环境，也是很重要的！', 'char_385_finlpp/CN_006');
INSERT INTO `t_operator_voice` VALUES (4180, 123, '信赖提升后交谈1', '嘿咻，嘿咻，最后一只靴子......上来了！呼，这样一来这条水道就干净多啦，不枉我每天都来清理！谢谢你陪我一起，博士！', 'char_385_finlpp/CN_007');
INSERT INTO `t_operator_voice` VALUES (4181, 123, '信赖提升后交谈2', '我们的族裔里流传着一段传说，我们是从离这片大地很远的地方来的。族人越来越少的现在，从哪儿来到哪儿去，谁也说不清了......', 'char_385_finlpp/CN_008');
INSERT INTO `t_operator_voice` VALUES (4182, 123, '信赖提升后交谈3', '在我的家乡，每个丰收的季节，每家每户都会做好多好多好吃的！红烧的啦，糖醋的啦，蒸的啦炸的啦炒的啦腌的啦，什么都有！博士和我一起回老家去玩吧？乡亲们都很热情好客的~', 'char_385_finlpp/CN_009');
INSERT INTO `t_operator_voice` VALUES (4183, 123, '闲置', '呜呜，好干燥......好想泡澡......', 'char_385_finlpp/CN_010');
INSERT INTO `t_operator_voice` VALUES (4184, 123, '干员报到', '您好！我是城邦水道清理人，代号清流！今后贵舰的用水卫生安全就请放心交给我~啊，这是我从老家带来的腌菜，大家都可以尝尝，保证好吃~', 'char_385_finlpp/CN_011');
INSERT INTO `t_operator_voice` VALUES (4185, 123, '观看作战记录', '要积攒经验的话，还是直接动手实践比较快吧？', 'char_385_finlpp/CN_012');
INSERT INTO `t_operator_voice` VALUES (4186, 123, '精英化晋升1', '要涨工资了吗？现在升职加薪，接下来就是走上巅峰啦，嘿嘿，我要赶快写信告诉村里的大家这个好消息~', 'char_385_finlpp/CN_013');
INSERT INTO `t_operator_voice` VALUES (4187, 123, '精英化晋升2', '能够看得见的威胁，和不易被察觉的威胁，紧急程度上也许有些先后之分，但这些对我们来说同样都很危险！感谢您的理解，博士。', 'char_385_finlpp/CN_014');
INSERT INTO `t_operator_voice` VALUES (4188, 123, '编入队伍', '没问题，交给我吧！', 'char_385_finlpp/CN_017');
INSERT INTO `t_operator_voice` VALUES (4189, 123, '任命队长', '责任重大呀......我会努力的！', 'char_385_finlpp/CN_018');
INSERT INTO `t_operator_voice` VALUES (4190, 123, '行动出发', '罗德岛治污小队，出发！', 'char_385_finlpp/CN_019');
INSERT INTO `t_operator_voice` VALUES (4191, 123, '行动开始', '突击检查！抓住那个往水槽里倒颜料的家伙！', 'char_385_finlpp/CN_020');
INSERT INTO `t_operator_voice` VALUES (4192, 123, '选中干员1', '撸袖子打架......呃，我这件衣服没有袖子。', 'char_385_finlpp/CN_021');
INSERT INTO `t_operator_voice` VALUES (4193, 123, '选中干员2', '呜哇，真的打起来了！', 'char_385_finlpp/CN_022');
INSERT INTO `t_operator_voice` VALUES (4194, 123, '部署1', '清理工作我最擅长了！', 'char_385_finlpp/CN_023');
INSERT INTO `t_operator_voice` VALUES (4195, 123, '部署2', '送水上门~', 'char_385_finlpp/CN_024');
INSERT INTO `t_operator_voice` VALUES (4196, 123, '作战中1', '腾空翻转！', 'char_385_finlpp/CN_025');
INSERT INTO `t_operator_voice` VALUES (4197, 123, '作战中2', '你这是无证排污！', 'char_385_finlpp/CN_026');
INSERT INTO `t_operator_voice` VALUES (4198, 123, '作战中3', ' 水利万物。', 'char_385_finlpp/CN_027');
INSERT INTO `t_operator_voice` VALUES (4199, 123, '作战中4', '怎么样，被泼点凉水有没有让你清醒一点？', 'char_385_finlpp/CN_028');
INSERT INTO `t_operator_voice` VALUES (4200, 123, '4星结束行动', '没有污染物残留！很好，本次任务完美成功~', 'char_385_finlpp/CN_029');
INSERT INTO `t_operator_voice` VALUES (4201, 123, '3星结束行动', '战斗结束！战后的清理工作也不能松懈哦。', 'char_385_finlpp/CN_030');
INSERT INTO `t_operator_voice` VALUES (4202, 123, '非3星结束行动', '哎，敌人跑哪儿去了？我，我看不到了！', 'char_385_finlpp/CN_031');
INSERT INTO `t_operator_voice` VALUES (4203, 123, '行动失败', '情况不妙呀，留得青山在不怕没柴烧！这次我们还是先撤退吧？', 'char_385_finlpp/CN_032');
INSERT INTO `t_operator_voice` VALUES (4204, 123, '进驻设施', '希望能有大大的浴池呀~', 'char_385_finlpp/CN_033');
INSERT INTO `t_operator_voice` VALUES (4205, 123, '戳一下', '嗯？', 'char_385_finlpp/CN_034');
INSERT INTO `t_operator_voice` VALUES (4206, 123, '信赖触摸', '偷偷告诉你，我会站着游泳哦~', 'char_385_finlpp/CN_036');
INSERT INTO `t_operator_voice` VALUES (4207, 123, '标题', '明日方舟。', 'char_385_finlpp/CN_037');
INSERT INTO `t_operator_voice` VALUES (4208, 123, '问候', '早呀，博士！要不要来一杯清凉山泉水？', 'char_385_finlpp/CN_042');
INSERT INTO `t_operator_voice` VALUES (4209, 124, '任命助理', '博士，准备好工作了吗，不许说没好哦。来，3，2，1，开工！', 'char_337_utage/CN_001');
INSERT INTO `t_operator_voice` VALUES (4210, 124, '交谈1', '指甲可是女孩子的魅力点之一，不好好保养怎么行。', 'char_337_utage/CN_002');
INSERT INTO `t_operator_voice` VALUES (4211, 124, '交谈2', '为什么会叫宴？没什么复杂的原因，就想天天像开宴会一样开心。只要心情好了，什么都会好起来。', 'char_337_utage/CN_003');
INSERT INTO `t_operator_voice` VALUES (4212, 124, '交谈3', '你看到了啊，博士。那就没办法了——陪我一起去把这张双人优惠券用掉吧。', 'char_337_utage/CN_004');
INSERT INTO `t_operator_voice` VALUES (4213, 124, '晋升后交谈1', '患上了矿石病被送到这里来，妈妈怕我受欺负才把家传的狮子王送给我防身。结果嘛，欺负我的人没砍到，坏人倒是割了一茬又一茬。博士你呀，还真是挺会使唤人的。', 'char_337_utage/CN_005');
INSERT INTO `t_operator_voice` VALUES (4214, 124, '晋升后交谈2', '无论是谁，认真做事的时候都会变得很可怕吧。我是六人份的可怕？呃，有这么吓人吗......', 'char_337_utage/CN_006');
INSERT INTO `t_operator_voice` VALUES (4215, 124, '信赖提升后交谈1', '明天帮我买十盒限量饼干棒吧，钱从我工资里预支就好。啊？已经预支完了？那......总之，拜托你了，博士，你一定能想到办法的！', 'char_337_utage/CN_007');
INSERT INTO `t_operator_voice` VALUES (4216, 124, '信赖提升后交谈2', '博士有时候敏锐得要命，有时候又像块木头一样，到底是为什么呢？啊，我懂了，是那个吧，《闺蜜悄悄话指南》之类的，对吧？不愧是博士呢。', 'char_337_utage/CN_008');
INSERT INTO `t_operator_voice` VALUES (4217, 124, '信赖提升后交谈3', '太过依赖一个人不是件好事呢，不过，如果是博士的话......啊博士！不没什么我只是在胡思乱想，您您您找我有什么事吗！', 'char_337_utage/CN_009');
INSERT INTO `t_operator_voice` VALUES (4218, 124, '闲置', '累了就睡吧，别想太多。', 'char_337_utage/CN_010');
INSERT INTO `t_operator_voice` VALUES (4219, 124, '干员报到', '你好呀博士，我是刚当上干员没多久的宴，兴趣是......嗯，不用那么详细吗？那，以后就请您多多关照了哦。', 'char_337_utage/CN_011');
INSERT INTO `t_operator_voice` VALUES (4220, 124, '观看作战记录', '放完了？', 'char_337_utage/CN_012');
INSERT INTO `t_operator_voice` VALUES (4221, 124, '精英化晋升1', '升职了的话，能不能少干些活呀。唉，不行吗，好吧......', 'char_337_utage/CN_013');
INSERT INTO `t_operator_voice` VALUES (4222, 124, '精英化晋升2', '该动真格的了。让我们把讨厌的家伙，全部碾碎~', 'char_337_utage/CN_014');
INSERT INTO `t_operator_voice` VALUES (4223, 124, '编入队伍', '好喔，指挥就拜托各位了。', 'char_337_utage/CN_017');
INSERT INTO `t_operator_voice` VALUES (4224, 124, '任命队长', '是队长啊......嗯？我？队长？', 'char_337_utage/CN_018');
INSERT INTO `t_operator_voice` VALUES (4225, 124, '行动出发', '出发。', 'char_337_utage/CN_019');
INSERT INTO `t_operator_voice` VALUES (4226, 124, '行动开始', '来吧，来吧，我等不及了。', 'char_337_utage/CN_020');
INSERT INTO `t_operator_voice` VALUES (4227, 124, '选中干员1', '嗯。', 'char_337_utage/CN_021');
INSERT INTO `t_operator_voice` VALUES (4228, 124, '选中干员2', '需要我了吗？', 'char_337_utage/CN_022');
INSERT INTO `t_operator_voice` VALUES (4229, 124, '部署1', '我开动了~', 'char_337_utage/CN_023');
INSERT INTO `t_operator_voice` VALUES (4230, 124, '部署2', '哼哼。', 'char_337_utage/CN_024');
INSERT INTO `t_operator_voice` VALUES (4231, 124, '作战中1', '切哪儿比较好呢。', 'char_337_utage/CN_025');
INSERT INTO `t_operator_voice` VALUES (4232, 124, '作战中2', '捅得准就不会痛了。', 'char_337_utage/CN_026');
INSERT INTO `t_operator_voice` VALUES (4233, 124, '作战中3', '不要的零件，就送给我吧。', 'char_337_utage/CN_027');
INSERT INTO `t_operator_voice` VALUES (4234, 124, '作战中4', '你流泪的样子，还挺可爱的。', 'char_337_utage/CN_028');
INSERT INTO `t_operator_voice` VALUES (4235, 124, '4星结束行动', '啊，发泄完了，真开心。博士，我们去吃可丽饼吧。', 'char_337_utage/CN_029');
INSERT INTO `t_operator_voice` VALUES (4236, 124, '3星结束行动', '很尽兴哦，多谢款待~', 'char_337_utage/CN_030');
INSERT INTO `t_operator_voice` VALUES (4237, 124, '非3星结束行动', '剩下的几个家伙是专门为我而留的吗。那，我就不客气了。', 'char_337_utage/CN_031');
INSERT INTO `t_operator_voice` VALUES (4238, 124, '行动失败', '别发呆了博士，快跑起来。', 'char_337_utage/CN_032');
INSERT INTO `t_operator_voice` VALUES (4239, 124, '进驻设施', '只要能随时买到新的杂志，住所怎么样其实无所谓的啦。', 'char_337_utage/CN_033');
INSERT INTO `t_operator_voice` VALUES (4240, 124, '戳一下', '嗯？', 'char_337_utage/CN_034');
INSERT INTO `t_operator_voice` VALUES (4241, 124, '信赖触摸', '一起去唱k吗。', 'char_337_utage/CN_036');
INSERT INTO `t_operator_voice` VALUES (4242, 124, '标题', '明日方舟。', 'char_337_utage/CN_037');
INSERT INTO `t_operator_voice` VALUES (4243, 124, '问候', '早上好，博士。', 'char_337_utage/CN_042');
INSERT INTO `t_operator_voice` VALUES (4244, 125, '任命助理', '今天可以不用出门吗，太、太好了......像我这样的感染者，不应该穿着这样的衣服吧。自己亲手缝制的衣服，却没有穿它的权利......嗯？在罗德岛，可以吗？真的？', 'char_252_bibeak/CN_001');
INSERT INTO `t_operator_voice` VALUES (4245, 125, '交谈1', '欸，我的衣服？啊，这、这件是我自己设计的，裙摆使用了一些传统的意象，虽然比不上爸爸的作品......但这也是我对复古风格的一种尝试。', 'char_252_bibeak/CN_002');
INSERT INTO `t_operator_voice` VALUES (4246, 125, '交谈2', '午安，博士，我在尝试整理房间......经常把房间弄得一团糟，对不起......以前从来没有自己整理过，现在慢慢练习，很有趣，但也经常打碎装饰......', 'char_252_bibeak/CN_003');
INSERT INTO `t_operator_voice` VALUES (4247, 125, '交谈3', '脱离了那种生活，一开始确实不太适应，但之前我只是觉得自己成了感染者，没法再过那种生活，所以担惊受怕......现在不是了。我想学些新的东西，想为罗德岛的和我一样的病人们做些事。', 'char_252_bibeak/CN_004');
INSERT INTO `t_operator_voice` VALUES (4248, 125, '晋升后交谈1', '从小，爸爸就一直要求我学习剑术，小时候我并不喜欢......但在那场事故之后，有时握着剑，会比牵着纺线更让我安心。', 'char_252_bibeak/CN_005');
INSERT INTO `t_operator_voice` VALUES (4249, 125, '晋升后交谈2', '我曾经很害怕出门，被感染后，总觉得一切都在鄙夷我......但是现在我已经明白，大家身上闪烁着的自尊心，和是否感染一点关系都没有。这才是最美丽的纹饰啊。', 'char_252_bibeak/CN_006');
INSERT INTO `t_operator_voice` VALUES (4250, 125, '信赖提升后交谈1', '博士，那个......可，可以的话，我、我替您做几件衣服......吧？我觉得你很适合我的一些想法呢。啊，其他的干员们，我也时常这么请求的......', 'char_252_bibeak/CN_007');
INSERT INTO `t_operator_voice` VALUES (4251, 125, '信赖提升后交谈2', '那些用矿石刺我的人，我原本当然很恨他们的，只是现在......如果一直被囚禁在那种鄙夷和绝望中，我也会做出可怕的事吧。我一点也不后悔逃出我的宅子，哪怕那里有我很多温暖的回忆。', 'char_252_bibeak/CN_008');
INSERT INTO `t_operator_voice` VALUES (4252, 125, '信赖提升后交谈3', '大家教我的东西，我想全都加在为大家编织的衣服中......呵呵，虽然这样看起来，元素有些冗杂了呢。但当我用剑和他们一起战斗，以及用针去缝出花纹的时候，我的心境就是这样的呀。', 'char_252_bibeak/CN_009');
INSERT INTO `t_operator_voice` VALUES (4253, 125, '闲置', '趁现在，来编点小东西吧。', 'char_252_bibeak/CN_010');
INSERT INTO `t_operator_voice` VALUES (4254, 125, '干员报到', '你，你好博士......我的代号是柏喙......请问，这儿有针织缝纫室吗？', 'char_252_bibeak/CN_011');
INSERT INTO `t_operator_voice` VALUES (4255, 125, '观看作战记录', '他们战斗的方式，我从来都没想过......', 'char_252_bibeak/CN_012');
INSERT INTO `t_operator_voice` VALUES (4256, 125, '精英化晋升1', '晋、晋升？我......可以吗？我其实不那么擅长战斗......谢谢你。', 'char_252_bibeak/CN_013');
INSERT INTO `t_operator_voice` VALUES (4257, 125, '精英化晋升2', '我的身边......有罗德岛的干员们、有博士您在，我相信在有限的生命里，我也能活得和普通人一样精彩......甚至，过上一种他们从未想象过的生活。', 'char_252_bibeak/CN_014');
INSERT INTO `t_operator_voice` VALUES (4258, 125, '编入队伍', '好、好的！', 'char_252_bibeak/CN_017');
INSERT INTO `t_operator_voice` VALUES (4259, 125, '任命队长', '欸？我、我来指挥吗？', 'char_252_bibeak/CN_018');
INSERT INTO `t_operator_voice` VALUES (4260, 125, '行动出发', '就像纹理一样，只有一种织法。', 'char_252_bibeak/CN_019');
INSERT INTO `t_operator_voice` VALUES (4261, 125, '行动开始', '争斗还是不可避免吗......', 'char_252_bibeak/CN_020');
INSERT INTO `t_operator_voice` VALUES (4262, 125, '选中干员1', '啊，好的！', 'char_252_bibeak/CN_021');
INSERT INTO `t_operator_voice` VALUES (4263, 125, '选中干员2', '战、战斗的服装已经准备好了！', 'char_252_bibeak/CN_022');
INSERT INTO `t_operator_voice` VALUES (4264, 125, '部署1', '用剑和用针，是一样的吗？', 'char_252_bibeak/CN_023');
INSERT INTO `t_operator_voice` VALUES (4265, 125, '部署2', '我也......可以战斗！相信我！', 'char_252_bibeak/CN_024');
INSERT INTO `t_operator_voice` VALUES (4266, 125, '作战中1', '我，我是不会留情的！', 'char_252_bibeak/CN_025');
INSERT INTO `t_operator_voice` VALUES (4267, 125, '作战中2', '有话请留到战斗以后，我有要保护的人！', 'char_252_bibeak/CN_026');
INSERT INTO `t_operator_voice` VALUES (4268, 125, '作战中3', '像引线穿针一样......出剑！', 'char_252_bibeak/CN_027');
INSERT INTO `t_operator_voice` VALUES (4269, 125, '作战中4', '生命难道也可以裁剪吗？', 'char_252_bibeak/CN_028');
INSERT INTO `t_operator_voice` VALUES (4270, 125, '4星结束行动', '您辛苦了，博士！那个，我的表现怎么样呢？', 'char_252_bibeak/CN_029');
INSERT INTO `t_operator_voice` VALUES (4271, 125, '3星结束行动', '终、终于结束了！如果各位的制服有破损的话，我可以帮忙修补......', 'char_252_bibeak/CN_030');
INSERT INTO `t_operator_voice` VALUES (4272, 125, '非3星结束行动', '啊，有人逃走了......不过，比死得痛苦......要好很多。', 'char_252_bibeak/CN_031');
INSERT INTO `t_operator_voice` VALUES (4273, 125, '行动失败', '我，我......我还是，还是很没用......', 'char_252_bibeak/CN_032');
INSERT INTO `t_operator_voice` VALUES (4274, 125, '进驻设施', '嗯......纺织间在哪里呢？', 'char_252_bibeak/CN_033');
INSERT INTO `t_operator_voice` VALUES (4275, 125, '戳一下', '呀！', 'char_252_bibeak/CN_034');
INSERT INTO `t_operator_voice` VALUES (4276, 125, '信赖触摸', '啊，博士，帮我拿一下布料好吗？', 'char_252_bibeak/CN_036');
INSERT INTO `t_operator_voice` VALUES (4277, 125, '标题', '明日方舟。', 'char_252_bibeak/CN_037');
INSERT INTO `t_operator_voice` VALUES (4278, 125, '问候', '您、您好，博士。', 'char_252_bibeak/CN_042');
INSERT INTO `t_operator_voice` VALUES (4279, 126, '任命助理', '嗯？这就是博士的房间吗？实在是很乏味，就像萨尔贡那些一成不变的山丘一样。至少放个游戏机吧？', 'char_379_sesa/CN_001');
INSERT INTO `t_operator_voice` VALUES (4280, 126, '交谈1', '{@nickname}，我在你身上感到了一种共鸣，真是奇怪，是因为你我都有着更多的可能性吗？不过你忘记了过去，这样会感到轻松吗，还是会更加痛苦呢？', 'char_379_sesa/CN_002');
INSERT INTO `t_operator_voice` VALUES (4281, 126, '交谈2', '比起被刻意教化的知识，天生的智慧才是我引以为傲的力量，{@nickname}！有罗德岛这样的盟友，我们都会离伟大的真实更近的。喂，别撇开视线啊，你是不是觉得很羞耻啊，是不是？', 'char_379_sesa/CN_003');
INSERT INTO `t_operator_voice` VALUES (4282, 126, '交谈3', '“我愿渡往彼岸”，那是过去别人用来和我接头的暗号。桥是两片土地的连接者，灌输力量，传递智慧......听我说完啊，转身就走很没礼貌！', 'char_379_sesa/CN_004');
INSERT INTO `t_operator_voice` VALUES (4283, 126, '晋升后交谈1', '罗德岛里总有些缺乏格调的家伙，伪装是种修饰，不是种简单皮囊啊。要是不好好精心雕琢下的话，只会让人觉得外表破破烂烂而已吧？你说我穿的破烂？你根本就不懂这种风格......', 'char_379_sesa/CN_005');
INSERT INTO `t_operator_voice` VALUES (4284, 126, '晋升后交谈2', '你相不相信武器有灵魂？它们经历过什么，曾被谁握在手中，又是为了什么而战——不！这些不是灵魂，对武器来说，任何信念和意义都是被强加的。你我是武器吗，还是强加灵魂的人呢？', 'char_379_sesa/CN_006');
INSERT INTO `t_operator_voice` VALUES (4285, 126, '信赖提升后交谈1', '天才？世上真有天才吗......萨尔贡的多数工匠在我看来不过是尚在学习走路的普通学徒，叮叮当当地摆弄金属，但只要走得够久，他们也能走出整条道路。', 'char_379_sesa/CN_007');
INSERT INTO `t_operator_voice` VALUES (4286, 126, '信赖提升后交谈2', '想要我帮忙调整武器？我的本事是不能见人的，“让所有人都能拿起武器的方法是创造战争的方法”，这个封锁自己的镣铐，我必须戴上。还是说，你想要那把能搅乱大地的开锁器？', 'char_379_sesa/CN_008');
INSERT INTO `t_operator_voice` VALUES (4287, 126, '信赖提升后交谈3', '你知道我的身份？也知道我的过去？不，你不知道，你不应该知道。我隐姓埋名，就是为了逃避以前的一切，忘了吧，把那些都埋在沙漠深处。', 'char_379_sesa/CN_009');
INSERT INTO `t_operator_voice` VALUES (4288, 126, '闲置', '这样的安静，不坏。', 'char_379_sesa/CN_010');
INSERT INTO `t_operator_voice` VALUES (4289, 126, '干员报到', '我知道你在寻找什么，罗德岛的博士，你渴望真相和强大！而我慑砂，愿意赐予你这份力量......哎别，别这样，别推我出门啊，我是认真来求职的！', 'char_379_sesa/CN_011');
INSERT INTO `t_operator_voice` VALUES (4290, 126, '观看作战记录', '这种武器出力...唔嗯......', 'char_379_sesa/CN_012');
INSERT INTO `t_operator_voice` VALUES (4291, 126, '精英化晋升1', '......想要力量？还没到时候，别引人注目，这很~~危险。', 'char_379_sesa/CN_013');
INSERT INTO `t_operator_voice` VALUES (4292, 126, '精英化晋升2', '“桥是连接，弹药是智慧，武器是意志，谐律是平等。”遮掩真相的迷雾，你想不想把它们彻底粉碎？也许我能借你一点点力量......就一点点。', 'char_379_sesa/CN_014');
INSERT INTO `t_operator_voice` VALUES (4293, 126, '编入队伍', '“桥”在渴求......你们手中的武器，让我看下，行吗？', 'char_379_sesa/CN_017');
INSERT INTO `t_operator_voice` VALUES (4294, 126, '任命队长', '我来引导你们......这个油擦在弩槽里就行，记住要均匀。', 'char_379_sesa/CN_018');
INSERT INTO `t_operator_voice` VALUES (4295, 126, '行动出发', '到了你们展现成果的时候了！但武器要轻拿轻放。', 'char_379_sesa/CN_019');
INSERT INTO `t_operator_voice` VALUES (4296, 126, '行动开始', '战场，既摧毁武器，也摧毁它们的主人。', 'char_379_sesa/CN_020');
INSERT INTO `t_operator_voice` VALUES (4297, 126, '选中干员1', '手臂在颤抖...', 'char_379_sesa/CN_021');
INSERT INTO `t_operator_voice` VALUES (4298, 126, '选中干员2', '解放我吧！是这么喊没错吧？', 'char_379_sesa/CN_022');
INSERT INTO `t_operator_voice` VALUES (4299, 126, '部署1', '终于......', 'char_379_sesa/CN_023');
INSERT INTO `t_operator_voice` VALUES (4300, 126, '部署2', '毁灭，终将来临！', 'char_379_sesa/CN_024');
INSERT INTO `t_operator_voice` VALUES (4301, 126, '作战中1', '将武器从躯壳中解放——', 'char_379_sesa/CN_025');
INSERT INTO `t_operator_voice` VALUES (4302, 126, '作战中2', '灵魂啊！共鸣吧！', 'char_379_sesa/CN_026');
INSERT INTO `t_operator_voice` VALUES (4303, 126, '作战中3', '我是你们的毁灭！这么说说没关系吧？', 'char_379_sesa/CN_027');
INSERT INTO `t_operator_voice` VALUES (4304, 126, '作战中4', '你们又怎么会懂受害者的痛苦？', 'char_379_sesa/CN_028');
INSERT INTO `t_operator_voice` VALUES (4305, 126, '4星结束行动', '如此程度的激战必然伴随着痛苦，但手中的武器绝对不会撒谎，它在高歌胜利！', 'char_379_sesa/CN_029');
INSERT INTO `t_operator_voice` VALUES (4306, 126, '3星结束行动', '你有些过度依赖我的力量了，别说了，老规矩，一杯红茶两块方糖。', 'char_379_sesa/CN_030');
INSERT INTO `t_operator_voice` VALUES (4307, 126, '非3星结束行动', '能逃过武器带来的杀戮，也是好事。', 'char_379_sesa/CN_031');
INSERT INTO `t_operator_voice` VALUES (4308, 126, '行动失败', '无论逃多久，武器都会等下去，等到消灭你我的那天。', 'char_379_sesa/CN_032');
INSERT INTO `t_operator_voice` VALUES (4309, 126, '进驻设施', '伟大的真实需要揭示它的空间！不，也不用那么大的个间......', 'char_379_sesa/CN_033');
INSERT INTO `t_operator_voice` VALUES (4310, 126, '戳一下', '小心！我还......不太能控制好我的力量！', 'char_379_sesa/CN_034');
INSERT INTO `t_operator_voice` VALUES (4311, 126, '信赖触摸', '你想听故事吗？我有不少很古旧的故事。', 'char_379_sesa/CN_036');
INSERT INTO `t_operator_voice` VALUES (4312, 126, '标题', '明日方舟。', 'char_379_sesa/CN_037');
INSERT INTO `t_operator_voice` VALUES (4313, 126, '问候', '嘿，博士！', 'char_379_sesa/CN_042');
INSERT INTO `t_operator_voice` VALUES (4314, 127, '任命助理', '助理？就是要一直站在别人身边的那个吧，我在军队里也见过哦。等等，还要做文书工作？要搜集战况？还要发报告？！好，好麻烦！', 'char_222_bpipe/CN_001');
INSERT INTO `t_operator_voice` VALUES (4315, 127, '交谈1', '陈陈在学校也总摆着张臭脸，她那个样子啊，可容易遭人讨厌啦，但后来的她可是让所有人都刮目相看呢！虽然包扎伤口啦，帮她烧饭啦，和她结队作战啦什么的我也没少干，还挺累人的......', 'char_222_bpipe/CN_002');
INSERT INTO `t_operator_voice` VALUES (4316, 127, '交谈2', '器械操作，我很会的呀！割麦机啊，拖拉机啊，翻土机啊，我全都很擅长的。嗯？掌上终端，显示屏，无线路系统？小型机械的话，我不太......哎，我就碰了一下，它怎么冒烟了？！', 'char_222_bpipe/CN_003');
INSERT INTO `t_operator_voice` VALUES (4317, 127, '交谈3', '你昨天是不是没有好好吃饭？工作，工作，工作可不是借口！身体垮了的话，就什么事都别想做了。想吃什么就说吧，我这就去做！啊，太精致的不行哦，我做不来的。', 'char_222_bpipe/CN_004');
INSERT INTO `t_operator_voice` VALUES (4318, 127, '晋升后交谈1', '罗德岛这艘船啊，实在是......比维多利亚的军舰好了不知道多少倍啊！阳光，麦田，成群的牧兽......博士，我们也种点什么吧，要么就土豆好了？种土豆，我可是很有经验的！', 'char_222_bpipe/CN_005');
INSERT INTO `t_operator_voice` VALUES (4319, 127, '晋升后交谈2', '你说，一个人的故乡该到哪里为止？是离牵引车最远的房子，还是大地与蓝天的交界，又或者是地图上不断重画的红蓝杠？那么多场仗打下来......有时候我甚至会突然忘记家乡的模样。', 'char_222_bpipe/CN_006');
INSERT INTO `t_operator_voice` VALUES (4320, 127, '信赖提升后交谈1', '那个叫苇草的姑娘，我一定见过她。但现在她这个样子，看起来又孤僻又可怜，她真是我看到的那个可怕的人吗？真奇怪，我得问问她......我不能和她说话？欸，为什么呀！', 'char_222_bpipe/CN_007');
INSERT INTO `t_operator_voice` VALUES (4321, 127, '信赖提升后交谈2', '跑去读军校也有妈妈的原因啦，都怪她老说“瓦伊凡的女人就是要不断变强”什么的......算是不服气吧？现在想想，没必要变得像妈妈那样强壮呀。欸，我看起来还是可爱居多的对吧。对吧？', 'char_222_bpipe/CN_008');
INSERT INTO `t_operator_voice` VALUES (4322, 127, '信赖提升后交谈3', '还在军队时，我遇见过一支鬼魂一样的部队，他们在维多利亚的暗处肆虐，目击者却又大多下落不明。我有发掘它内幕的义务，这也是我到处乱跑的原因。啊，你别插手，你也有自己的天职吧？', 'char_222_bpipe/CN_009');
INSERT INTO `t_operator_voice` VALUES (4323, 127, '闲置', '啊！你在偷懒！真好啊，不过睡觉还是要在草垛里才舒服吧？哎，这样一想真的好困......我也睡一小会儿，应该没事吧？', 'char_222_bpipe/CN_010');
INSERT INTO `t_operator_voice` VALUES (4324, 127, '干员报到', '你好！你就是博士，对不对？龙门的陈警司说我可以在这找到帮手！要先签名？嗯，风——笛——好了！欸？“成为干员”？“全日驻舰”？那是什么呀！', 'char_222_bpipe/CN_011');
INSERT INTO `t_operator_voice` VALUES (4325, 127, '观看作战记录', '哦哦，罗德岛打起仗来，还挺有模有样的嘛！', 'char_222_bpipe/CN_012');
INSERT INTO `t_operator_voice` VALUES (4326, 127, '精英化晋升1', '晋升？嗯......我说不上是喜欢还是讨厌呢。毕竟对于军人来说，责任同时也意味着更多的争战，甚至是更多的错误啊。', 'char_222_bpipe/CN_013');
INSERT INTO `t_operator_voice` VALUES (4327, 127, '精英化晋升2', '加入罗德岛对我来说是件好事。矿石病和感染者，虽然开始会觉得很可怕，但逃避是不能解决任何问题的，对吗？谢谢你，我也认清了自己应该去做的事情。', 'char_222_bpipe/CN_014');
INSERT INTO `t_operator_voice` VALUES (4328, 127, '编入队伍', '立正！', 'char_222_bpipe/CN_017');
INSERT INTO `t_operator_voice` VALUES (4329, 127, '任命队长', '由我带队，立即执行作战计划！', 'char_222_bpipe/CN_018');
INSERT INTO `t_operator_voice` VALUES (4330, 127, '行动出发', '遵循命令，立刻出发！', 'char_222_bpipe/CN_019');
INSERT INTO `t_operator_voice` VALUES (4331, 127, '行动开始', '全体散开！', 'char_222_bpipe/CN_020');
INSERT INTO `t_operator_voice` VALUES (4332, 127, '选中干员1', '请说。', 'char_222_bpipe/CN_021');
INSERT INTO `t_operator_voice` VALUES (4333, 127, '选中干员2', '随时候命。', 'char_222_bpipe/CN_022');
INSERT INTO `t_operator_voice` VALUES (4334, 127, '部署1', '进攻！', 'char_222_bpipe/CN_023');
INSERT INTO `t_operator_voice` VALUES (4335, 127, '部署2', '我和我的破城矛，来自维多利亚！', 'char_222_bpipe/CN_024');
INSERT INTO `t_operator_voice` VALUES (4336, 127, '作战中1', '“战术使人思维明晰！”', 'char_222_bpipe/CN_025');
INSERT INTO `t_operator_voice` VALUES (4337, 127, '作战中2', '“纪律使人意志坚定！”', 'char_222_bpipe/CN_026');
INSERT INTO `t_operator_voice` VALUES (4338, 127, '作战中3', '“勇敢能够击溃邪恶！”', 'char_222_bpipe/CN_027');
INSERT INTO `t_operator_voice` VALUES (4339, 127, '作战中4', '“仁慈能够拯救生命！”', 'char_222_bpipe/CN_028');
INSERT INTO `t_operator_voice` VALUES (4340, 127, '4星结束行动', '无论是在学校还是军队，我都见过更严峻的事态。', 'char_222_bpipe/CN_029');
INSERT INTO `t_operator_voice` VALUES (4341, 127, '3星结束行动', '咻......这回也没有辜负这身制服呢！', 'char_222_bpipe/CN_030');
INSERT INTO `t_operator_voice` VALUES (4342, 127, '非3星结束行动', '敌人溃逃了！各位追击时，一定要小心！', 'char_222_bpipe/CN_031');
INSERT INTO `t_operator_voice` VALUES (4343, 127, '行动失败', '你先走！', 'char_222_bpipe/CN_032');
INSERT INTO `t_operator_voice` VALUES (4344, 127, '进驻设施', '温室该往哪走呀？', 'char_222_bpipe/CN_033');
INSERT INTO `t_operator_voice` VALUES (4345, 127, '戳一下', '欸欸？', 'char_222_bpipe/CN_034');
INSERT INTO `t_operator_voice` VALUES (4346, 127, '信赖触摸', '想不想去野餐？就我们两个！', 'char_222_bpipe/CN_036');
INSERT INTO `t_operator_voice` VALUES (4347, 127, '标题', '明日方舟。', 'char_222_bpipe/CN_037');
INSERT INTO `t_operator_voice` VALUES (4348, 127, '问候', '要不要一起去务农啊博士！', 'char_222_bpipe/CN_042');
INSERT INTO `t_operator_voice` VALUES (4349, 128, '任命助理', '唤我前来，定是渴望欣赏我优美的舞姿，对吧？......呃，帮忙搬运行李？我来这里才不是为了干力气活啊！', 'char_159_peacok/CN_001');
INSERT INTO `t_operator_voice` VALUES (4350, 128, '交谈1', '你身为领导者竟然如此羸弱，这是怎么回事？若不是怕阿米娅伤心哭泣，我定要就地将你断罪！', 'char_159_peacok/CN_002');
INSERT INTO `t_operator_voice` VALUES (4351, 128, '交谈2', '你连何为断罪都不知道吗？听好了，所谓断罪，是第一代断罪大师融合了传统舞步和古代格斗术，所创造出的全新暗杀拳法！我正是他的第三代传人！', 'char_159_peacok/CN_003');
INSERT INTO `t_operator_voice` VALUES (4352, 128, '交谈3', '断罪可不是什么野蛮的格斗术，这是一门将美丽与思念灌注进拳头，再以拳交心的艺术！知道了吗！', 'char_159_peacok/CN_004');
INSERT INTO `t_operator_voice` VALUES (4353, 128, '晋升后交谈1', '所谓艺术，是努力和智慧的结晶，当人们开始忽视艺术的真正价值，将这种神圣的产物当作游戏时，就会有断罪者现世。', 'char_159_peacok/CN_005');
INSERT INTO `t_operator_voice` VALUES (4354, 128, '晋升后交谈2', '连我身上这如同名画的压迫感都察觉不到，你是没有资格留在这个拯救者的队伍里的！唔唔唔唔，断！罪！', 'char_159_peacok/CN_006');
INSERT INTO `t_operator_voice` VALUES (4355, 128, '信赖提升后交谈1', '我居然是矿石病的感染者，而且这病没办法治愈吗？！可恶......果然这也是我的宿命吗？是挥动断罪之拳的人的宿命吗！喂，你这家伙，不许移开目光！我明明在讲一个很悲伤的故事！', 'char_159_peacok/CN_007');
INSERT INTO `t_operator_voice` VALUES (4356, 128, '信赖提升后交谈2', '想成为断罪者？那么，你来想一下这个问题的答案！“你现在站在铁路分叉器前面，那边开来一列坐满杜林人的火车，前面有两条铁轨，其中一条铁轨上绑着几个艺术造诣太差的艺术家......”', 'char_159_peacok/CN_008');
INSERT INTO `t_operator_voice` VALUES (4357, 128, '信赖提升后交谈3', '连最简单的断罪难题都回答不上来！你这家伙一点艺术天赋都没有！吃我这招！断罪·二式！', 'char_159_peacok/CN_009');
INSERT INTO `t_operator_voice` VALUES (4358, 128, '闲置', '......嘿！哈！不，不对，不是这种效果，再来一遍——', 'char_159_peacok/CN_010');
INSERT INTO `t_operator_voice` VALUES (4359, 128, '干员报到', '我乃断罪者，就以这个充满信念的名号作为代号吧。你问我有何才能？我懂得致命而美丽的断罪艺术！这就足够了！', 'char_159_peacok/CN_011');
INSERT INTO `t_operator_voice` VALUES (4360, 128, '观看作战记录', '这种变强的机会，多多益善！', 'char_159_peacok/CN_012');
INSERT INTO `t_operator_voice` VALUES (4361, 128, '精英化晋升1', '呵呵呵，晋升！断罪隐藏的力量被释放的那一天已经不远了！', 'char_159_peacok/CN_013');
INSERT INTO `t_operator_voice` VALUES (4362, 128, '精英化晋升2', '这种感觉......我感觉我的艺术造诣更深了，但我的身体里，好像有什么东西被创造了出来......！', 'char_159_peacok/CN_014');
INSERT INTO `t_operator_voice` VALUES (4363, 128, '编入队伍', '因为偶尔会断罪到队友，所以我还是一个人行动比较安全。', 'char_159_peacok/CN_017');
INSERT INTO `t_operator_voice` VALUES (4364, 128, '任命队长', '总之，开始断罪！', 'char_159_peacok/CN_018');
INSERT INTO `t_operator_voice` VALUES (4365, 128, '行动出发', '审判时间！', 'char_159_peacok/CN_019');
INSERT INTO `t_operator_voice` VALUES (4366, 128, '行动开始', '来吧，清算你们的罪孽！', 'char_159_peacok/CN_020');
INSERT INTO `t_operator_voice` VALUES (4367, 128, '选中干员1', '现在就出发！', 'char_159_peacok/CN_021');
INSERT INTO `t_operator_voice` VALUES (4368, 128, '选中干员2', '好！', 'char_159_peacok/CN_022');
INSERT INTO `t_operator_voice` VALUES (4369, 128, '部署1', '嘿咻——', 'char_159_peacok/CN_023');
INSERT INTO `t_operator_voice` VALUES (4370, 128, '部署2', '你这家伙做我的对手够格吗？', 'char_159_peacok/CN_024');
INSERT INTO `t_operator_voice` VALUES (4371, 128, '作战中1', '断罪！', 'char_159_peacok/CN_025');
INSERT INTO `t_operator_voice` VALUES (4372, 128, '作战中2', '喝！', 'char_159_peacok/CN_026');
INSERT INTO `t_operator_voice` VALUES (4373, 128, '作战中3', '哈！', 'char_159_peacok/CN_027');
INSERT INTO `t_operator_voice` VALUES (4374, 128, '作战中4', '后退！！', 'char_159_peacok/CN_028');
INSERT INTO `t_operator_voice` VALUES (4375, 128, '4星结束行动', '成王败寇——这就是断罪的教义！', 'char_159_peacok/CN_029');
INSERT INTO `t_operator_voice` VALUES (4376, 128, '3星结束行动', '怎么样！是不是已经为我英勇的雄姿所倾——咦，已经撤退了？等等我啊！', 'char_159_peacok/CN_030');
INSERT INTO `t_operator_voice` VALUES (4377, 128, '非3星结束行动', '也好也好，虽然一点也不华丽，不过普通的胜利也不错。', 'char_159_peacok/CN_031');
INSERT INTO `t_operator_voice` VALUES (4378, 128, '行动失败', '呜......身为华丽的断罪者，决不允许有如此失态......！', 'char_159_peacok/CN_032');
INSERT INTO `t_operator_voice` VALUES (4379, 128, '进驻设施', '很难给这个房间断罪啊~', 'char_159_peacok/CN_033');
INSERT INTO `t_operator_voice` VALUES (4380, 128, '戳一下', '嗯？', 'char_159_peacok/CN_034');
INSERT INTO `t_operator_voice` VALUES (4381, 128, '信赖触摸', '哇啊啊！你这家伙这次又想干什么！不会再让你随心所欲地捉弄我了！', 'char_159_peacok/CN_036');
INSERT INTO `t_operator_voice` VALUES (4382, 128, '标题', '明日方舟。', 'char_159_peacok/CN_037');
INSERT INTO `t_operator_voice` VALUES (4383, 128, '问候', '哦！这不是博士吗！', 'char_159_peacok/CN_042');
INSERT INTO `t_operator_voice` VALUES (4384, 129, '任命助理', '您的房间里没有安全隐患，可以安心工作了，我会在门口守着，帮您回绝不必要的杂事。', 'char_333_sidero/CN_001');
INSERT INTO `t_operator_voice` VALUES (4385, 129, '交谈1', '这里的自动门坏了？稍等——喝！好了，您先进去处理事务，我来联系维修人员。', 'char_333_sidero/CN_002');
INSERT INTO `t_operator_voice` VALUES (4386, 129, '交谈2', '想了解米诺斯的风土？明白了，我会整理一份报告。不，关于米诺斯的情报三言两语说不清楚，我必须详细地汇报给您。', 'char_333_sidero/CN_003');
INSERT INTO `t_operator_voice` VALUES (4387, 129, '交谈3', '时刻更新知识是佣兵的必备功课，如果不能及时了解资讯，很有可能哪天就栽在使用新手法的敌人手里了，所以绝对不可以有一丝懈怠。您明白了吧，博士。博士？', 'char_333_sidero/CN_004');
INSERT INTO `t_operator_voice` VALUES (4388, 129, '晋升后交谈1', '呃，花园里的米诺斯小姐到底做了什么......脑袋里空空的，紧张感也消失了......现在状态......不太对。博士，我需要恢复，暂时没法胜任护卫的工作，还请谅解......', 'char_333_sidero/CN_005');
INSERT INTO `t_operator_voice` VALUES (4389, 129, '晋升后交谈2', '喝，哈！看来今天状态不错，或许和预备干员们一起活动一下身体会更舒畅。哦，现在是地狱周吗？看来又要麻烦杜宾帮忙安排个位置了啊。', 'char_333_sidero/CN_006');
INSERT INTO `t_operator_voice` VALUES (4390, 129, '信赖提升后交谈1', '科林尼亚建立在商人的天秤上，每个人都想着把对方的价值压榨到极致。与之相比，其他城邦的人们要有趣多了，就像你一样，博士。成为他们的护卫，总会发生很多有趣的事。', 'char_333_sidero/CN_007');
INSERT INTO `t_operator_voice` VALUES (4391, 129, '信赖提升后交谈2', '您需要适当的锻炼，博士。保健操那只是活动筋骨，我说的是锻炼，包含健康饮食减脂增肌在内的全方位的锻炼。哦，您又答应了？那还请拿出成果来，要是又——什，我话还没说完，别跑！', 'char_333_sidero/CN_008');
INSERT INTO `t_operator_voice` VALUES (4392, 129, '信赖提升后交谈3', '又吃完了？再来一碗？好......是，我第一次做正餐，平时都是吃干粮果腹的，不怎么下厨。给，最后一碗，吃完休息好以后请遵守我们的约定，好好参加运动，这样我也算没白学这项技能。', 'char_333_sidero/CN_009');
INSERT INTO `t_operator_voice` VALUES (4393, 129, '闲置', '以小憩来充分放松......吗，真是奢侈的休息方式。', 'char_333_sidero/CN_010');
INSERT INTO `t_operator_voice` VALUES (4394, 129, '干员报到', '初次见面，新雇主，本人是科林尼亚的雇佣兵，铸铁。手续已经完成了，接下来请期待我的工作成果。', 'char_333_sidero/CN_011');
INSERT INTO `t_operator_voice` VALUES (4395, 129, '观看作战记录', '这样的片段也只是参考，请把精力用在实战上。', 'char_333_sidero/CN_012');
INSERT INTO `t_operator_voice` VALUES (4396, 129, '精英化晋升1', '一枚徽章，一份信赖。我收下了。', 'char_333_sidero/CN_013');
INSERT INTO `t_operator_voice` VALUES (4397, 129, '精英化晋升2', '挥舞我的刀刃，斩断所有威胁，我说到做到。', 'char_333_sidero/CN_014');
INSERT INTO `t_operator_voice` VALUES (4398, 129, '编入队伍', '听从您的指挥。', 'char_333_sidero/CN_017');
INSERT INTO `t_operator_voice` VALUES (4399, 129, '任命队长', '传达您的命令。', 'char_333_sidero/CN_018');
INSERT INTO `t_operator_voice` VALUES (4400, 129, '行动出发', '我会时刻注意队伍情况，请您安心。', 'char_333_sidero/CN_019');
INSERT INTO `t_operator_voice` VALUES (4401, 129, '行动开始', '开始了。', 'char_333_sidero/CN_020');
INSERT INTO `t_operator_voice` VALUES (4402, 129, '选中干员1', '是。', 'char_333_sidero/CN_021');
INSERT INTO `t_operator_voice` VALUES (4403, 129, '选中干员2', '准备进入预定地点。', 'char_333_sidero/CN_022');
INSERT INTO `t_operator_voice` VALUES (4404, 129, '部署1', '看看你们有什么能耐。', 'char_333_sidero/CN_023');
INSERT INTO `t_operator_voice` VALUES (4405, 129, '部署2', '休想再进一步。', 'char_333_sidero/CN_024');
INSERT INTO `t_operator_voice` VALUES (4406, 129, '作战中1', '倾覆吧！', 'char_333_sidero/CN_025');
INSERT INTO `t_operator_voice` VALUES (4407, 129, '作战中2', '倒下就别想再起来了！', 'char_333_sidero/CN_026');
INSERT INTO `t_operator_voice` VALUES (4408, 129, '作战中3', '喝！', 'char_333_sidero/CN_027');
INSERT INTO `t_operator_voice` VALUES (4409, 129, '作战中4', '让你们见识一下米诺斯的战技！', 'char_333_sidero/CN_028');
INSERT INTO `t_operator_voice` VALUES (4410, 129, '4星结束行动', '以您的指挥技术来看，我的保护似乎有些多余。', 'char_333_sidero/CN_029');
INSERT INTO `t_operator_voice` VALUES (4411, 129, '3星结束行动', '您没有受伤就好，我们准备离开吧。', 'char_333_sidero/CN_030');
INSERT INTO `t_operator_voice` VALUES (4412, 129, '非3星结束行动', '因为我的疏忽让您受了惊吓，真的，很抱歉。', 'char_333_sidero/CN_031');
INSERT INTO `t_operator_voice` VALUES (4413, 129, '行动失败', '无论如何，得把博士安全送回基地......', 'char_333_sidero/CN_032');
INSERT INTO `t_operator_voice` VALUES (4414, 129, '进驻设施', '太杂乱了，得好好整理一下。', 'char_333_sidero/CN_033');
INSERT INTO `t_operator_voice` VALUES (4415, 129, '戳一下', '有什么事？', 'char_333_sidero/CN_034');
INSERT INTO `t_operator_voice` VALUES (4416, 129, '信赖触摸', '博士！您自己提的集训，怎么能带头脱逃呢？停下！', 'char_333_sidero/CN_036');
INSERT INTO `t_operator_voice` VALUES (4417, 129, '标题', '明日方舟。', 'char_333_sidero/CN_037');
INSERT INTO `t_operator_voice` VALUES (4418, 129, '问候', '没有异常！', 'char_333_sidero/CN_042');
INSERT INTO `t_operator_voice` VALUES (4419, 130, '任命助理', '打扰了，博士，今天是我值班......嗯？哎，我的刀，怎么卡在门框上了......', 'char_301_cutter/CN_001');
INSERT INTO `t_operator_voice` VALUES (4420, 130, '交谈1', '在我家乡，土壤一天比一天贫瘠，收的那点粮食连自己都喂不饱，大多数人无法谋生，只好做佣兵，只要给钱，什么都做。', 'char_301_cutter/CN_002');
INSERT INTO `t_operator_voice` VALUES (4421, 130, '交谈2', '呃，他们说我的刀太危险，禁止我再进厨房。我只是想帮忙，可是，那个砧板，我的刀稍微一碰就裂开了......唉，那个，要赔偿吗？', 'char_301_cutter/CN_003');
INSERT INTO `t_operator_voice` VALUES (4422, 130, '交谈3', '每次晒着太阳，都会忍不住犯困。你放心，在确认安全之前，我不会真的...睡着......的.........ZZZzzzzz............', 'char_301_cutter/CN_004');
INSERT INTO `t_operator_voice` VALUES (4423, 130, '晋升后交谈1', '在工作上，我有自己的原则，我不伤害无辜，也不让无关的人承受损失。......说实话，这好难。', 'char_301_cutter/CN_005');
INSERT INTO `t_operator_voice` VALUES (4424, 130, '晋升后交谈2', '这个项链，借你用，要收好，这是守护符。不行，刀不能给你，我不在的时候，就用这个将就一下吧。', 'char_301_cutter/CN_006');
INSERT INTO `t_operator_voice` VALUES (4425, 130, '信赖提升后交谈1', '博士，你看着我的眼睛别动，哎，你别动......嗯，嗯嗯，放心了，你不是那种任用他人行恶的人，我看得出来。', 'char_301_cutter/CN_007');
INSERT INTO `t_operator_voice` VALUES (4426, 130, '信赖提升后交谈2', '小时候的事，其实我不太记得了，是老师抚养我长大，还教我如何用刀。老师告诉我，我父母无辜被牵连，死在一场争斗中，他捡到我时我就抱着这把刀，就算被割伤也不松开。', 'char_301_cutter/CN_008');
INSERT INTO `t_operator_voice` VALUES (4427, 130, '信赖提升后交谈3', '持刀的人总会追寻一场对决，而夺人性命的人，最终也将成为他人手中亡魂。或许有一天，我也会迎来这样的结局......到时候，希望你能见证。', 'char_301_cutter/CN_009');
INSERT INTO `t_operator_voice` VALUES (4428, 130, '闲置', '有我守着，安心睡吧。', 'char_301_cutter/CN_010');
INSERT INTO `t_operator_voice` VALUES (4429, 130, '干员报到', '刻刀，只是个普通的雇佣兵。我得了矿石病，所以打算用我的能力换取治疗机会，这把刀应该还能派上些用场......嗯，就是这样。', 'char_301_cutter/CN_011');
INSERT INTO `t_operator_voice` VALUES (4430, 130, '观看作战记录', '好厉害！原来还有这种用刀的方法！', 'char_301_cutter/CN_012');
INSERT INTO `t_operator_voice` VALUES (4431, 130, '精英化晋升1', '除了刀，自由就是我唯一的宝物，如果你愿意保障它，我就都听你的。', 'char_301_cutter/CN_013');
INSERT INTO `t_operator_voice` VALUES (4432, 130, '精英化晋升2', '佣兵的刀，不是那种没见过血，服服帖帖的小玩具。或许也会有一天，我将无法再控制我的刀。不过，不用担心，至少现在......它会为你所用。', 'char_301_cutter/CN_014');
INSERT INTO `t_operator_voice` VALUES (4433, 130, '编入队伍', '了解。刻刀入队了。', 'char_301_cutter/CN_017');
INSERT INTO `t_operator_voice` VALUES (4434, 130, '任命队长', '明白，我可以挑选自己的队员吗？', 'char_301_cutter/CN_018');
INSERT INTO `t_operator_voice` VALUES (4435, 130, '行动出发', '准备好，注意不要波及无辜人员。', 'char_301_cutter/CN_019');
INSERT INTO `t_operator_voice` VALUES (4436, 130, '行动开始', '刀子，擦亮了吗？', 'char_301_cutter/CN_020');
INSERT INTO `t_operator_voice` VALUES (4437, 130, '选中干员1', '明白了。', 'char_301_cutter/CN_021');
INSERT INTO `t_operator_voice` VALUES (4438, 130, '选中干员2', '我可以等待。', 'char_301_cutter/CN_022');
INSERT INTO `t_operator_voice` VALUES (4439, 130, '部署1', '战场就是坟场。', 'char_301_cutter/CN_023');
INSERT INTO `t_operator_voice` VALUES (4440, 130, '部署2', '我要做的只是挥刀而已。', 'char_301_cutter/CN_024');
INSERT INTO `t_operator_voice` VALUES (4441, 130, '作战中1', '别出声。', 'char_301_cutter/CN_025');
INSERT INTO `t_operator_voice` VALUES (4442, 130, '作战中2', '别回头。', 'char_301_cutter/CN_026');
INSERT INTO `t_operator_voice` VALUES (4443, 130, '作战中3', '直视我。', 'char_301_cutter/CN_027');
INSERT INTO `t_operator_voice` VALUES (4444, 130, '作战中4', '你慢了。', 'char_301_cutter/CN_028');
INSERT INTO `t_operator_voice` VALUES (4445, 130, '4星结束行动', '或许总有一天，会出现我的刀斩不断的东西。但，看来不是现在，不是你们。', 'char_301_cutter/CN_029');
INSERT INTO `t_operator_voice` VALUES (4446, 130, '3星结束行动', '呼......按照合同完成了，需要清点一下吗？', 'char_301_cutter/CN_030');
INSERT INTO `t_operator_voice` VALUES (4447, 130, '非3星结束行动', '失去战意的人，不该是我的攻击目标。', 'char_301_cutter/CN_031');
INSERT INTO `t_operator_voice` VALUES (4448, 130, '行动失败', '走，博士，尽可能地走远！这里......还有我。', 'char_301_cutter/CN_032');
INSERT INTO `t_operator_voice` VALUES (4449, 130, '进驻设施', '可以晒到太阳，嗯，暖暖的......', 'char_301_cutter/CN_033');
INSERT INTO `t_operator_voice` VALUES (4450, 130, '戳一下', '唔，别拉耳朵。', 'char_301_cutter/CN_034');
INSERT INTO `t_operator_voice` VALUES (4451, 130, '信赖触摸', '要一起晒太阳吗？', 'char_301_cutter/CN_036');
INSERT INTO `t_operator_voice` VALUES (4452, 130, '标题', '明日方舟。', 'char_301_cutter/CN_037');
INSERT INTO `t_operator_voice` VALUES (4453, 130, '问候', '啊，博士你来了。今天有哪些工作？', 'char_301_cutter/CN_042');
INSERT INTO `t_operator_voice` VALUES (4454, 131, '任命助理', '你身边没有恶灵存在，所以我可以走了吗？', 'char_254_vodfox/CN_001');
INSERT INTO `t_operator_voice` VALUES (4455, 131, '交谈1', '记忆承载着命运，你的命运曾戛然而止，所以那些恶灵并没有继续纠缠你，可是它们的痕迹仍旧清晰可见......博士，请不要随便把我的话当耳旁风......为了你好。', 'char_254_vodfox/CN_002');
INSERT INTO `t_operator_voice` VALUES (4456, 131, '交谈2', '有什么事情的话可以和小莫提说，没关系，小莫提会帮忙的。在你沉入梦乡的时候，一切问题都会解决的。', 'char_254_vodfox/CN_003');
INSERT INTO `t_operator_voice` VALUES (4457, 131, '交谈3', '那些会让人哭出来的感情都只是小莫提的食粮，所以我不怎么哭，这样不好吗？', 'char_254_vodfox/CN_004');
INSERT INTO `t_operator_voice` VALUES (4458, 131, '晋升后交谈1', '诅咒的真面目？你想看看小莫提里面是什么？不行......至少现在不行......小莫提也会疼的，对，是这样的，弄疼小莫提的人自己也会疼的哭鼻子的，没有骗你。', 'char_254_vodfox/CN_005');
INSERT INTO `t_operator_voice` VALUES (4459, 131, '晋升后交谈2', '清单上还缺些东西：毛线球、用过的纽扣、新鲜的苹果、拉特兰的土、碾碎的显像管......博士，帮我。', 'char_254_vodfox/CN_006');
INSERT INTO `t_operator_voice` VALUES (4460, 131, '信赖提升后交谈1', '苹果，给你，只是多余的施术材料，我不喜欢浪费......谢谢？都说不是为了你特意准备的......欸，干嘛，别摸头......算了。', 'char_254_vodfox/CN_007');
INSERT INTO `t_operator_voice` VALUES (4461, 131, '信赖提升后交谈2', '因为有小莫提在，所以我用不着情感，但是大家夸奖我的时候，我总觉得心里很奇怪......而且小莫提不想带走那些情感，我很不安......这样没关系？真的？那就......这样吧。', 'char_254_vodfox/CN_008');
INSERT INTO `t_operator_voice` VALUES (4462, 131, '信赖提升后交谈3', '博士，给你，这是小莫提的分身，请一定要把它带在身上......诅咒？不，不是的博士......博士收下的话，我就像是一直待在博士身边一样了......不行吗？', 'char_254_vodfox/CN_009');
INSERT INTO `t_operator_voice` VALUES (4463, 131, '闲置', '安睡吧♪安睡吧♪被梦湮没吧♪', 'char_254_vodfox/CN_010');
INSERT INTO `t_operator_voice` VALUES (4464, 131, '干员报到', '新名字是巫恋......这是小莫提......至于其他的事情......你最好不要知道，小莫提说过，这对大家都不好......', 'char_254_vodfox/CN_011');
INSERT INTO `t_operator_voice` VALUES (4465, 131, '观看作战记录', '这些对仪式没有帮助......', 'char_254_vodfox/CN_012');
INSERT INTO `t_operator_voice` VALUES (4466, 131, '精英化晋升1', '填入棉花、缝起嘴巴、塞上耳朵、盖上眼睛......新的人偶完成了。', 'char_254_vodfox/CN_013');
INSERT INTO `t_operator_voice` VALUES (4467, 131, '精英化晋升2', '小莫提并不需要这样的仪式，但是......我......很开心，我会和它一起努力的。', 'char_254_vodfox/CN_014');
INSERT INTO `t_operator_voice` VALUES (4468, 131, '编入队伍', '工作吗？知道了。', 'char_254_vodfox/CN_017');
INSERT INTO `t_operator_voice` VALUES (4469, 131, '任命队长', '要是能像小莫提一样听话，就好了。', 'char_254_vodfox/CN_018');
INSERT INTO `t_operator_voice` VALUES (4470, 131, '行动出发', '小莫提，该走了。', 'char_254_vodfox/CN_019');
INSERT INTO `t_operator_voice` VALUES (4471, 131, '行动开始', '今天的运势是......你们真不走运。', 'char_254_vodfox/CN_020');
INSERT INTO `t_operator_voice` VALUES (4472, 131, '选中干员1', '怎么了？', 'char_254_vodfox/CN_021');
INSERT INTO `t_operator_voice` VALUES (4473, 131, '选中干员2', '大家都准备好了。', 'char_254_vodfox/CN_022');
INSERT INTO `t_operator_voice` VALUES (4474, 131, '部署1', '玩耍的时间到了，小莫提。', 'char_254_vodfox/CN_023');
INSERT INTO `t_operator_voice` VALUES (4475, 131, '部署2', '你们的怨恨，我收下了。', 'char_254_vodfox/CN_024');
INSERT INTO `t_operator_voice` VALUES (4476, 131, '作战中1', '去吧，莫提。', 'char_254_vodfox/CN_025');
INSERT INTO `t_operator_voice` VALUES (4477, 131, '作战中2', '安息吧，让大地拥抱一切。', 'char_254_vodfox/CN_026');
INSERT INTO `t_operator_voice` VALUES (4478, 131, '作战中3', '是谁？', 'char_254_vodfox/CN_027');
INSERT INTO `t_operator_voice` VALUES (4479, 131, '作战中4', '你们回不了家了。', 'char_254_vodfox/CN_028');
INSERT INTO `t_operator_voice` VALUES (4480, 131, '4星结束行动', '恶灵，都消散了。', 'char_254_vodfox/CN_029');
INSERT INTO `t_operator_voice` VALUES (4481, 131, '3星结束行动', '就像它们低语的那样，意料之中的胜利。', 'char_254_vodfox/CN_030');
INSERT INTO `t_operator_voice` VALUES (4482, 131, '非3星结束行动', '有人逃走了，没关系，小莫提已经缠住了他们的命运......', 'char_254_vodfox/CN_031');
INSERT INTO `t_operator_voice` VALUES (4483, 131, '行动失败', '诅咒......你们...... ', 'char_254_vodfox/CN_032');
INSERT INTO `t_operator_voice` VALUES (4484, 131, '进驻设施', '这里......有恶灵的气息......还不错。', 'char_254_vodfox/CN_033');
INSERT INTO `t_operator_voice` VALUES (4485, 131, '戳一下', '你要做什么？', 'char_254_vodfox/CN_034');
INSERT INTO `t_operator_voice` VALUES (4486, 131, '信赖触摸', '要陪我做裁缝吗？小莫提也很开心......', 'char_254_vodfox/CN_036');
INSERT INTO `t_operator_voice` VALUES (4487, 131, '标题', '明日方舟。', 'char_254_vodfox/CN_037');
INSERT INTO `t_operator_voice` VALUES (4488, 131, '问候', '你们好，博士，还有......', 'char_254_vodfox/CN_042');
INSERT INTO `t_operator_voice` VALUES (4489, 132, '任命助理', '别去刻意寻找，我一直在。', 'char_250_phatom/CN_001');
INSERT INTO `t_operator_voice` VALUES (4490, 132, '交谈1', '黑夜中的人无法给你指路，也看不到你指出的方向。别停留，你该离开了......', 'char_250_phatom/CN_002');
INSERT INTO `t_operator_voice` VALUES (4491, 132, '交谈2', '在这幅假面之下，我认不出自己是谁。', 'char_250_phatom/CN_003');
INSERT INTO `t_operator_voice` VALUES (4492, 132, '交谈3', '这位是Miss.Christine。我不知道她从哪里来，也不知道她会在什么时候离开。如果她喜欢你，她会主动靠近......没错，像现在这样。', 'char_250_phatom/CN_004');
INSERT INTO `t_operator_voice` VALUES (4493, 132, '晋升后交谈1', '曾经的我也并非孤身一人。我有过一些伙伴，作为一个小剧团四处周游。但当矿石病爆发蔓延，一切到此结束，一切又由此而始......', 'char_250_phatom/CN_005');
INSERT INTO `t_operator_voice` VALUES (4494, 132, '晋升后交谈2', '这片黑暗是庇护所，也是宝座，更是乐园。过去的亡灵从未离去，而我在他们的骸骨上诅咒这一切......看到我这副模样，你还想聆听我的歌声，你还敢...站在我身前吗？', 'char_250_phatom/CN_006');
INSERT INTO `t_operator_voice` VALUES (4495, 132, '信赖提升后交谈1', '喉咙上的这个装置压迫着我的脖颈，但这正是我需要的。和面具一样，它遮掩了我的罪恶，隐藏了我的声音。想听我真正的声音？不，不能。这不是你该承受的......', 'char_250_phatom/CN_007');
INSERT INTO `t_operator_voice` VALUES (4496, 132, '信赖提升后交谈2', '我的歌声结束了所有人的生命。无止境的夜，不停止的歌，那些从喉咙里呕出的发颤高音和内脏碎片，令人恐惧，也让人窒息......却又那么......美妙。博士，你告诉我，我到底......在做什么？', 'char_250_phatom/CN_008');
INSERT INTO `t_operator_voice` VALUES (4497, 132, '信赖提升后交谈3', '我能帮你取走许多生命，但我必须先确认自己是否清醒。有什么在尝试左右我，它不愿我走下舞台，它想让歌声永远回荡下去......甚至去杀死你。再次扮演你的刺客前，我必须先找到真相。', 'char_250_phatom/CN_009');
INSERT INTO `t_operator_voice` VALUES (4498, 132, '闲置', '即使现在并非黑夜，黑影也不曾离开你的身边。小心你那些不合时宜的睡意，博士。', 'char_250_phatom/CN_010');
INSERT INTO `t_operator_voice` VALUES (4499, 132, '干员报到', '代号傀影，黑夜中的幽灵，幕布后的幻影。告诉我，你的敌人在哪，是谁......', 'char_250_phatom/CN_011');
INSERT INTO `t_operator_voice` VALUES (4500, 132, '观看作战记录', '杀戮不是目的，而是手段。', 'char_250_phatom/CN_012');
INSERT INTO `t_operator_voice` VALUES (4501, 132, '精英化晋升1', '如你所愿。罗德岛的曲调由你来定，你不需要引导或保护......是的，我很清楚。你才是你自己的主人。', 'char_250_phatom/CN_013');
INSERT INTO `t_operator_voice` VALUES (4502, 132, '精英化晋升2', '这一步无法回头，无法后望。我将与你分享的东西，恐怕并不像你想象中那样美妙。......过来，来这里，这黑夜的幕布，就让我为你揭开。', 'char_250_phatom/CN_014');
INSERT INTO `t_operator_voice` VALUES (4503, 132, '编入队伍', '该我登台了吗？', 'char_250_phatom/CN_017');
INSERT INTO `t_operator_voice` VALUES (4504, 132, '任命队长', '给与引导，这我不陌生。', 'char_250_phatom/CN_018');
INSERT INTO `t_operator_voice` VALUES (4505, 132, '行动出发', '就按你所说的方向......', 'char_250_phatom/CN_019');
INSERT INTO `t_operator_voice` VALUES (4506, 132, '行动开始', '戏剧该揭开它的帷幕了。', 'char_250_phatom/CN_020');
INSERT INTO `t_operator_voice` VALUES (4507, 132, '选中干员1', '我在。', 'char_250_phatom/CN_021');
INSERT INTO `t_operator_voice` VALUES (4508, 132, '选中干员2', '那么，潜入夜幕吧。', 'char_250_phatom/CN_022');
INSERT INTO `t_operator_voice` VALUES (4509, 132, '部署1', '我自暗影中现身。', 'char_250_phatom/CN_023');
INSERT INTO `t_operator_voice` VALUES (4510, 132, '部署2', '听我的声音。听我歌唱。', 'char_250_phatom/CN_024');
INSERT INTO `t_operator_voice` VALUES (4511, 132, '作战中1', '看到镜中你自己的脸了吗。', 'char_250_phatom/CN_025');
INSERT INTO `t_operator_voice` VALUES (4512, 132, '作战中2', '演员是台词的傀儡。', 'char_250_phatom/CN_026');
INSERT INTO `t_operator_voice` VALUES (4513, 132, '作战中3', '有罪者......当受罚。', 'char_250_phatom/CN_027');
INSERT INTO `t_operator_voice` VALUES (4514, 132, '作战中4', '为了我——歌唱吧！歌唱吧！歌唱吧！', 'char_250_phatom/CN_028');
INSERT INTO `t_operator_voice` VALUES (4515, 132, '4星结束行动', '这就是由你引导而来的胜利吗......', 'char_250_phatom/CN_029');
INSERT INTO `t_operator_voice` VALUES (4516, 132, '3星结束行动', '结束了。胜利并不甜美，纷争总是孕化了其它滋味。', 'char_250_phatom/CN_030');
INSERT INTO `t_operator_voice` VALUES (4517, 132, '非3星结束行动', '被他们逃了。对他们来说是件好事......对我来说，或许也是。', 'char_250_phatom/CN_031');
INSERT INTO `t_operator_voice` VALUES (4518, 132, '行动失败', '撤退。......你已经迷失了方向。', 'char_250_phatom/CN_032');
INSERT INTO `t_operator_voice` VALUES (4519, 132, '进驻设施', '嗯？这面镜子后面，似乎......', 'char_250_phatom/CN_033');
INSERT INTO `t_operator_voice` VALUES (4520, 132, '戳一下', '什么？', 'char_250_phatom/CN_034');
INSERT INTO `t_operator_voice` VALUES (4521, 132, '信赖触摸', '这个脚步声......是你，博士。', 'char_250_phatom/CN_036');
INSERT INTO `t_operator_voice` VALUES (4522, 132, '标题', '明日方舟。', 'char_250_phatom/CN_037');
INSERT INTO `t_operator_voice` VALUES (4523, 132, '问候', '你身后的影子看起来比昨天更沉重，博士。', 'char_250_phatom/CN_042');
INSERT INTO `t_operator_voice` VALUES (4524, 133, '任命助理', 'Thermal-EX，热量冲击准备启动！什么，尚未到达指定作战时间吗？那就延后一下。延迟至10秒后启动冲击！', 'char_376_therex/CN_001');
INSERT INTO `t_operator_voice` VALUES (4525, 133, '交谈1', '听说东方有句古话：天下武功，唯热不破。', 'char_376_therex/CN_002');
INSERT INTO `t_operator_voice` VALUES (4526, 133, '交谈2', '博士！能和你在一起我真是太棒了！我炽热的内心久久无法平静！——我是说我身上所搭载的雷神工业定制款全量热力法术冲击发生器！', 'char_376_therex/CN_003');
INSERT INTO `t_operator_voice` VALUES (4527, 133, '交谈3', '很久以前，神灵说：“要有光”于是就有了光，神灵又说：“要有力量。”于是就有了雷神挑衅者熔岩猫魔鬼高性能版。', 'char_376_therex/CN_004');
INSERT INTO `t_operator_voice` VALUES (4528, 133, '信赖提升后交谈1', '作为一台完美的工业级量产型作战平台，我感到非常高兴！如今不但有梅尔小姐在不断优化我的性能，还有煌小姐这样灼热的伙伴能成为我的好搭档！太棒了！', 'char_376_therex/CN_007');
INSERT INTO `t_operator_voice` VALUES (4529, 133, '信赖提升后交谈2', '虽然我已经如此耀眼，但是对我来说，只有无人能敌的博士您才是最明亮的！', 'char_376_therex/CN_008');
INSERT INTO `t_operator_voice` VALUES (4530, 133, '信赖提升后交谈3', '我知道我能发出的光与热是如此短暂......但是，只要能照亮{@nickname}博士您前进的道路就好！', 'char_376_therex/CN_009');
INSERT INTO `t_operator_voice` VALUES (4531, 133, '闲置', '即使在你休眠的时候，我也可以作为你的暖炉工作！真不愧是我！', 'char_376_therex/CN_010');
INSERT INTO `t_operator_voice` VALUES (4532, 133, '干员报到', '诞生于雷神工业，改进于伟大的梅尔之手，膨胀的炙热之心——Thermal-EX ，前来助阵！', 'char_376_therex/CN_011');
INSERT INTO `t_operator_voice` VALUES (4533, 133, '观看作战记录', '嗷嗷！这股炽热的力量是......是......是热更新！', 'char_376_therex/CN_012');
INSERT INTO `t_operator_voice` VALUES (4534, 133, '编入队伍', '轮到我大显身手的时候了！', 'char_376_therex/CN_017');
INSERT INTO `t_operator_voice` VALUES (4535, 133, '任命队长', '现在情况如何？', 'char_376_therex/CN_018');
INSERT INTO `t_operator_voice` VALUES (4536, 133, '行动出发', '全军出击！', 'char_376_therex/CN_019');
INSERT INTO `t_operator_voice` VALUES (4537, 133, '行动开始', '摇滚起来！', 'char_376_therex/CN_020');
INSERT INTO `t_operator_voice` VALUES (4538, 133, '选中干员1', '准备发射！', 'char_376_therex/CN_021');
INSERT INTO `t_operator_voice` VALUES (4539, 133, '选中干员2', '蓄能中！', 'char_376_therex/CN_022');
INSERT INTO `t_operator_voice` VALUES (4540, 133, '部署1', '我来帮你！', 'char_376_therex/CN_023');
INSERT INTO `t_operator_voice` VALUES (4541, 133, '部署2', '冲击准备！', 'char_376_therex/CN_024');
INSERT INTO `t_operator_voice` VALUES (4542, 133, '作战中1', 'Sonic Boom—!', 'char_376_therex/CN_025');
INSERT INTO `t_operator_voice` VALUES (4543, 133, '作战中2', '开始冲击！', 'char_376_therex/CN_026');
INSERT INTO `t_operator_voice` VALUES (4544, 133, '作战中3', '看我的！', 'char_376_therex/CN_027');
INSERT INTO `t_operator_voice` VALUES (4545, 133, '作战中4', '赞美热量！', 'char_376_therex/CN_028');
INSERT INTO `t_operator_voice` VALUES (4546, 133, '4星结束行动', '不愧是我！果然{@nickname}博士的指挥和我才是绝配啊！', 'char_376_therex/CN_029');
INSERT INTO `t_operator_voice` VALUES (4547, 133, '3星结束行动', '非~常~完~美！！！！{@nickname}博士！！！', 'char_376_therex/CN_030');
INSERT INTO `t_operator_voice` VALUES (4548, 133, '非3星结束行动', '我撤退只是一个意外，原本战场上一定不会有漏网之鱼的！', 'char_376_therex/CN_031');
INSERT INTO `t_operator_voice` VALUES (4549, 133, '行动失败', '危险，被暗算了！烟雾弹已投放！博士，我来掩护你撤退！', 'char_376_therex/CN_032');
INSERT INTO `t_operator_voice` VALUES (4550, 133, '进驻设施', '休息吗？哈哈对我来说还早着呢！我刚更换新的储能装置，离今天的工作结束还有50小时呢！', 'char_376_therex/CN_033');
INSERT INTO `t_operator_voice` VALUES (4551, 133, '戳一下', '唔呵！我的热传感器感受到您炽热的内心了！', 'char_376_therex/CN_034');
INSERT INTO `t_operator_voice` VALUES (4552, 133, '信赖触摸', '我的处理器已经被您的热情融化了！', 'char_376_therex/CN_036');
INSERT INTO `t_operator_voice` VALUES (4553, 133, '标题', '明日方舟。', 'char_376_therex/CN_037');
INSERT INTO `t_operator_voice` VALUES (4554, 133, '问候', '欢迎回来，博士！今天您也是如此充满着光与热啊！', 'char_376_therex/CN_042');
INSERT INTO `t_operator_voice` VALUES (4555, 134, '任命助理', '有什么工作尽管吩咐，做副手我还是很有经验的。', 'char_401_elysm/CN_001');
INSERT INTO `t_operator_voice` VALUES (4556, 134, '交谈1', '这杆发信器能将信号传递到相当远的地方，也是小队之间进行沟通的有力手段。而且啊，我的法术能让信号变得很强，让小队之间的交流畅通无阻，战地通讯，简直就是为我设计的工作嘛。', 'char_401_elysm/CN_002');
INSERT INTO `t_operator_voice` VALUES (4557, 134, '交谈2', '找不到路？没问题，交给我。要去这里的话，先从左边的楼梯上到四层，然后右转，走过第三个岔口后左转，再下一层，继续向左，可露希尔小姐的三号工程室对面就是。怎么样，是不是简洁易懂？', 'char_401_elysm/CN_003');
INSERT INTO `t_operator_voice` VALUES (4558, 134, '交谈3', '我和棘刺约好，下次双方都不再留手，一定要分出个胜负。这次我一定将他杀得片甲不留......什么？罗德岛禁止私斗？别误会，我是在说下棋，你觉得输了的人就剃光头，这个赌注怎么样？', 'char_401_elysm/CN_004');
INSERT INTO `t_operator_voice` VALUES (4559, 134, '晋升后交谈1', '我当然也不习惯当别人的副手，但是我的队长只有靠我才能传达命令，我也就只能留在队伍里照顾她啦。等等，这些话你知我知，为了我们两个人的前途着想，千万别让她知道，行吗？', 'char_401_elysm/CN_005');
INSERT INTO `t_operator_voice` VALUES (4560, 134, '晋升后交谈2', '战场上通讯的重要性不用多说，会率先受到攻击，也是当然的。放心吧，只要有我在，就一定让博士你的指令顺利传达到每一个作战人员。', 'char_401_elysm/CN_006');
INSERT INTO `t_operator_voice` VALUES (4561, 134, '信赖提升后交谈1', '博士，你来得正好。其实，我正在烦恼，为什么大家宁愿看地图，也不找我问路呢？队长她之前说我油腔滑调惹人烦，怎么会这样，明明我对大家都很温柔啊。', 'char_401_elysm/CN_007');
INSERT INTO `t_operator_voice` VALUES (4562, 134, '信赖提升后交谈2', '在战场上，士气是很重要的，看到这面旗帜了吗，作为先锋，我举起它就是为了给大家带来勇气——等等，博士，你不会相信了吧？开玩笑啦，这个真的只是发信装置而已。', 'char_401_elysm/CN_008');
INSERT INTO `t_operator_voice` VALUES (4563, 134, '信赖提升后交谈3', '博士你值得我信任。别谦虚，我对自己的眼光有信心。用战友来称呼你，怎么样？队长和我本来也水火不容，但我相信战友你和她一样，最后都会被我的真诚感动。不会？队长也寄信投诉我？呃？', 'char_401_elysm/CN_009');
INSERT INTO `t_operator_voice` VALUES (4564, 134, '闲置', '博士，这边的文件......嗯？居然睡着了吗。唉，就算是因为有我在，也不能这么没戒心啊。', 'char_401_elysm/CN_010');
INSERT INTO `t_operator_voice` VALUES (4565, 134, '干员报到', '三号作战小队，先锋通讯员极境，前来报到。这应该还是头一次不是机械通讯，而是直接与你对话吧，博士？', 'char_401_elysm/CN_011');
INSERT INTO `t_operator_voice` VALUES (4566, 134, '观看作战记录', '很多内部的作战记录里都能看到我自己，这种感觉有点奇怪。', 'char_401_elysm/CN_012');
INSERT INTO `t_operator_voice` VALUES (4567, 134, '精英化晋升1', '非常感谢。不过再晋升的话，我的职位该不会就要超过队长了吧？', 'char_401_elysm/CN_013');
INSERT INTO `t_operator_voice` VALUES (4568, 134, '精英化晋升2', '我一向相信自己的判断，博士。如果下命令的那个人是你，我不会有所质疑，所谓战友，不就是这样吗。', 'char_401_elysm/CN_014');
INSERT INTO `t_operator_voice` VALUES (4569, 134, '编入队伍', '当然，我很乐意。', 'char_401_elysm/CN_017');
INSERT INTO `t_operator_voice` VALUES (4570, 134, '任命队长', '放心，只是暂时代理。毕竟队长现在不在嘛。', 'char_401_elysm/CN_018');
INSERT INTO `t_operator_voice` VALUES (4571, 134, '行动出发', '那么就出发吧，大家跟随我的路线行动。', 'char_401_elysm/CN_019');
INSERT INTO `t_operator_voice` VALUES (4572, 134, '行动开始', '耳麦都检查好了吗？', 'char_401_elysm/CN_020');
INSERT INTO `t_operator_voice` VALUES (4573, 134, '选中干员1', '没问题。', 'char_401_elysm/CN_021');
INSERT INTO `t_operator_voice` VALUES (4574, 134, '选中干员2', '信号稳定，随时可以行动。', 'char_401_elysm/CN_022');
INSERT INTO `t_operator_voice` VALUES (4575, 134, '部署1', '对面的目标是谁呢。', 'char_401_elysm/CN_023');
INSERT INTO `t_operator_voice` VALUES (4576, 134, '部署2', '以主之名，聚集于此旗之下——嗯，我一直都想尝试说一次这种台词。', 'char_401_elysm/CN_024');
INSERT INTO `t_operator_voice` VALUES (4577, 134, '作战中1', '不，对不起，我并不信教。', 'char_401_elysm/CN_025');
INSERT INTO `t_operator_voice` VALUES (4578, 134, '作战中2', '谁会！在战场上！拿旗子当武器啊！', 'char_401_elysm/CN_026');
INSERT INTO `t_operator_voice` VALUES (4579, 134, '作战中3', '看清了吗！', 'char_401_elysm/CN_027');
INSERT INTO `t_operator_voice` VALUES (4580, 134, '作战中4', '速战速决！', 'char_401_elysm/CN_028');
INSERT INTO `t_operator_voice` VALUES (4581, 134, '4星结束行动', '再次确认，我们果然很适合搭档。', 'char_401_elysm/CN_029');
INSERT INTO `t_operator_voice` VALUES (4582, 134, '3星结束行动', '不错的配合，来击个掌吧。', 'char_401_elysm/CN_030');
INSERT INTO `t_operator_voice` VALUES (4583, 134, '非3星结束行动', '丧失斗志的已经不算是敌人了，到此为止吧。', 'char_401_elysm/CN_031');
INSERT INTO `t_operator_voice` VALUES (4584, 134, '行动失败', '太危险了，最后的防线由我来守，博士，请立即指挥撤退！', 'char_401_elysm/CN_032');
INSERT INTO `t_operator_voice` VALUES (4585, 134, '进驻设施', '嗯？这个房间是不是翻新过？', 'char_401_elysm/CN_033');
INSERT INTO `t_operator_voice` VALUES (4586, 134, '戳一下', '什么事？', 'char_401_elysm/CN_034');
INSERT INTO `t_operator_voice` VALUES (4587, 134, '信赖触摸', '听说我头上的这撮红发，掉光就不会再长了。问题很严峻，博士。', 'char_401_elysm/CN_036');
INSERT INTO `t_operator_voice` VALUES (4588, 134, '标题', '明日方舟。', 'char_401_elysm/CN_037');
INSERT INTO `t_operator_voice` VALUES (4589, 134, '问候', '今天有什么任务吗？', 'char_401_elysm/CN_042');
INSERT INTO `t_operator_voice` VALUES (4590, 135, '任命助理', '博士的房间还是挺整洁的嘛，还不赖。', 'char_400_weedy/CN_001');
INSERT INTO `t_operator_voice` VALUES (4591, 135, '交谈1', '水是大家生活的核心，我已经跟可露希尔小姐交流过改善罗德岛净水系统的事情了，希望我的研究成果能给帮上忙......这样我也不用担心用水不干净了。', 'char_400_weedy/CN_002');
INSERT INTO `t_operator_voice` VALUES (4592, 135, '交谈2', '这只仿生海龙小叶是我以家族流传的图案为原型制作出来的，除了辅助作战，在日常生活中也可以帮我不少忙，比如倒个水拎些重物之类的。', 'char_400_weedy/CN_003');
INSERT INTO `t_operator_voice` VALUES (4593, 135, '交谈3', '和格劳克斯约好的自动化工程实验时间差不多快到了，博士有兴趣一起去看看吗？', 'char_400_weedy/CN_004');
INSERT INTO `t_operator_voice` VALUES (4594, 135, '晋升后交谈1', '这些蓄水炮本来都是为了工程才制造的，除了高压水以外，还可以释放出高温蒸汽束。所以请不要随便触碰，他们可不是可露希尔的小车。', 'char_400_weedy/CN_005');
INSERT INTO `t_operator_voice` VALUES (4595, 135, '晋升后交谈2', '这些高阶的作战资料是我自己根据作战情况总结的。为了之后的战斗，就麻烦博士仔细阅读一下。然后之后还有好几个我想完善的作战实验也麻烦博士帮我一起，博士肯定不会拒绝我的吧。', 'char_400_weedy/CN_006');
INSERT INTO `t_operator_voice` VALUES (4596, 135, '信赖提升后交谈1', '平时我也偶尔会去一趟调香师小姐的庭院休息休息。那里的气味就能让人能彻底放松下来，对于缓解研究的疲劳实在是再有效不过了，下次博士要一起去试试吗？', 'char_400_weedy/CN_007');
INSERT INTO `t_operator_voice` VALUES (4597, 135, '信赖提升后交谈2', '我才不会一个人的时候跟仿生海龙聊天！给他起名字是为了做区分！', 'char_400_weedy/CN_008');
INSERT INTO `t_operator_voice` VALUES (4598, 135, '信赖提升后交谈3', '受了不少博士的关照，最近我也越来越习惯待在有博士在的环境里了。除了格劳克斯和研究的同事，明明还挺少见的。', 'char_400_weedy/CN_009');
INSERT INTO `t_operator_voice` VALUES (4599, 135, '闲置', '安静的环境真好。', 'char_400_weedy/CN_010');
INSERT INTO `t_operator_voice` VALUES (4600, 135, '干员报到', '你好，我是罗德岛工程部的温蒂，很早以前就听说过博士的大名了，希望之后能多多指教。啊，握手的话请用这边带着手套的右手。', 'char_400_weedy/CN_011');
INSERT INTO `t_operator_voice` VALUES (4601, 135, '观看作战记录', '嗯，没想到这些还挺有帮助的。', 'char_400_weedy/CN_012');
INSERT INTO `t_operator_voice` VALUES (4602, 135, '精英化晋升1', '晋升之后，我的个人房间可以再升级一次吗？', 'char_400_weedy/CN_013');
INSERT INTO `t_operator_voice` VALUES (4603, 135, '精英化晋升2', '感谢博士愿意给体弱的我这样的肯定。之后也让我为了罗德岛继续努力吧。', 'char_400_weedy/CN_014');
INSERT INTO `t_operator_voice` VALUES (4604, 135, '编入队伍', '我也要一起吗？', 'char_400_weedy/CN_017');
INSERT INTO `t_operator_voice` VALUES (4605, 135, '任命队长', '大家听我指挥！', 'char_400_weedy/CN_018');
INSERT INTO `t_operator_voice` VALUES (4606, 135, '行动出发', '出击！', 'char_400_weedy/CN_019');
INSERT INTO `t_operator_voice` VALUES (4607, 135, '行动开始', '一切都跟战前预想的一样，开始作战！', 'char_400_weedy/CN_020');
INSERT INTO `t_operator_voice` VALUES (4608, 135, '选中干员1', '我在这里。', 'char_400_weedy/CN_021');
INSERT INTO `t_operator_voice` VALUES (4609, 135, '选中干员2', '器械准备完备。', 'char_400_weedy/CN_022');
INSERT INTO `t_operator_voice` VALUES (4610, 135, '部署1', '交给我吧！', 'char_400_weedy/CN_023');
INSERT INTO `t_operator_voice` VALUES (4611, 135, '部署2', '小叶，我们加油！', 'char_400_weedy/CN_024');
INSERT INTO `t_operator_voice` VALUES (4612, 135, '作战中1', '发射！', 'char_400_weedy/CN_025');
INSERT INTO `t_operator_voice` VALUES (4613, 135, '作战中2', '锁定敌人！攻击！', 'char_400_weedy/CN_026');
INSERT INTO `t_operator_voice` VALUES (4614, 135, '作战中3', '全都让开！', 'char_400_weedy/CN_027');
INSERT INTO `t_operator_voice` VALUES (4615, 135, '作战中4', '哈！', 'char_400_weedy/CN_028');
INSERT INTO `t_operator_voice` VALUES (4616, 135, '4星结束行动', '只要战术正确了，根本就是小菜一碟。', 'char_400_weedy/CN_029');
INSERT INTO `t_operator_voice` VALUES (4617, 135, '3星结束行动', '工程学的作用不可小觑！', 'char_400_weedy/CN_030');
INSERT INTO `t_operator_voice` VALUES (4618, 135, '非3星结束行动', '......等我回去调整一下设备的参数，不会有下次了。', 'char_400_weedy/CN_031');
INSERT INTO `t_operator_voice` VALUES (4619, 135, '行动失败', '小叶， 掩护我们离开这里！', 'char_400_weedy/CN_032');
INSERT INTO `t_operator_voice` VALUES (4620, 135, '进驻设施', '这里有打扫过吗？', 'char_400_weedy/CN_033');
INSERT INTO `t_operator_voice` VALUES (4621, 135, '戳一下', '等，等一下！', 'char_400_weedy/CN_034');
INSERT INTO `t_operator_voice` VALUES (4622, 135, '信赖触摸', '博士，让我也来帮忙做点什么吧。', 'char_400_weedy/CN_036');
INSERT INTO `t_operator_voice` VALUES (4623, 135, '标题', '明日方舟。', 'char_400_weedy/CN_037');
INSERT INTO `t_operator_voice` VALUES (4624, 135, '问候', '博士，你好。', 'char_400_weedy/CN_042');
INSERT INTO `t_operator_voice` VALUES (4625, 136, '任命助理', '我倒是想过无数种回到这里的可能性，看见她当日看见的这幅风景......可怎么也没想到会和你站在一起，这还真过分啊，“博士”。', 'char_113_cqbw/CN_001');
INSERT INTO `t_operator_voice` VALUES (4626, 136, '交谈1', '问我去了哪里？不能说诶，凯尔希绕过了你直接给我下达了命令......你不信？好，你对她的信任真是令人信服呢。好啦，只是去挥霍了一下财产。衣服而已，已经检查过了，怎么，很意外吗？', 'char_113_cqbw/CN_002');
INSERT INTO `t_operator_voice` VALUES (4627, 136, '交谈2', '有几个小干员总是会咬牙切齿地看着我，他们什么时候才会真的动手呢......凯尔希解释过？在忍耐？什么嘛，尊重个人欲望可是很重要的一件事哦，就让他们放开手做吧，我正无聊呢。', 'char_113_cqbw/CN_003');
INSERT INTO `t_operator_voice` VALUES (4628, 136, '交谈3', '你过去很喜欢和我们打交道，雇佣兵是高效的战争工具，像快餐一样，开袋即食，用之即弃。你问我发生过什么？嗯，有些人不希望我话太多哎~但什么都记不得可真是幸福，是吧？', 'char_113_cqbw/CN_004');
INSERT INTO `t_operator_voice` VALUES (4629, 136, '晋升后交谈1', '拯救感染者？几乎所有卡兹戴尔的萨卡兹都是感染者，压迫和矛盾却多种多样......“拯救”具体来说能做到哪一步？连她一个萨卡兹都保护不好的凯尔希和现在的你，又打算怎么做？', 'char_113_cqbw/CN_005');
INSERT INTO `t_operator_voice` VALUES (4630, 136, '晋升后交谈2', '我真想知道，如果这些天真的干员们亲眼目睹那些善良的人温柔的人逐个死去，他们还能坚持说这些话，做这些事吗？欸，没有瞧不起谁，只是提醒你们，不要随便把那个人的想法临摹得这么简单。', 'char_113_cqbw/CN_006');
INSERT INTO `t_operator_voice` VALUES (4631, 136, '信赖提升后交谈1', '你啊，阿米娅身上的那个，怎么看凯尔希都有事情瞒着你吧？亏你能在什么都记不得的时候取得其他人的信任，不，你会信任其他人才更奇怪，真不知道那两个人看见你现在的样子会怎么想。', 'char_113_cqbw/CN_007');
INSERT INTO `t_operator_voice` VALUES (4632, 136, '信赖提升后交谈2', '这里还真是什么人都有啊。比起过去，奇怪的家伙更多了。啊，别误会，那些人的想法我完全不在乎，只是，阿米娅做得似乎还不错，至少她也努力想成为那个人的样子，我还不算讨厌。', 'char_113_cqbw/CN_008');
INSERT INTO `t_operator_voice` VALUES (4633, 136, '信赖提升后交谈3', 'Dr.{@nickname}，既然从没人对你提起，那当你回忆起那件事后你会作何反应，我相当感兴趣——别这么困惑，你只要先记住这个名字：特蕾西娅，对，特蕾西娅。', 'char_113_cqbw/CN_009');
INSERT INTO `t_operator_voice` VALUES (4634, 136, '闲置', '真是优哉游哉的呢，谁会想到有一天那个{@nickname}会毫无防备地睡在这里......忘记了过去，你就这么轻松吗？', 'char_113_cqbw/CN_010');
INSERT INTO `t_operator_voice` VALUES (4635, 136, '干员报到', '萨卡兹佣兵，W，真是好久不见，虽然这个“好久”可能含义不同。但没关系，既然现在的你不是我要找的你，那你暂时是安全的，暂时哦。', 'char_113_cqbw/CN_011');
INSERT INTO `t_operator_voice` VALUES (4636, 136, '观看作战记录', '真是熟悉到让人害怕的风格，我说你啊，真的失忆了吗？', 'char_113_cqbw/CN_012');
INSERT INTO `t_operator_voice` VALUES (4637, 136, '精英化晋升1', '好，我在听。没想到你会亲自负责这种小事，比起被人给予，我还是更乐意自己抢过来啊。', 'char_113_cqbw/CN_013');
INSERT INTO `t_operator_voice` VALUES (4638, 136, '精英化晋升2', '和一个这样的你并肩作战，真是种奇妙的体验。看来，在你回想起那天发生的事情之前，我们还是可以好好相处的嘛。', 'char_113_cqbw/CN_014');
INSERT INTO `t_operator_voice` VALUES (4639, 136, '编入队伍', '彼此，都尽量活下来吧。', 'char_113_cqbw/CN_017');
INSERT INTO `t_operator_voice` VALUES (4640, 136, '任命队长', '雇佣兵的指挥都是实用风格，可别指望我会照顾其他人喔。', 'char_113_cqbw/CN_018');
INSERT INTO `t_operator_voice` VALUES (4641, 136, '行动出发', '嗯，又是美好的一天。', 'char_113_cqbw/CN_019');
INSERT INTO `t_operator_voice` VALUES (4642, 136, '行动开始', '只要全部干掉就完事了吧？别想那么简单？那真麻烦啊......', 'char_113_cqbw/CN_020');
INSERT INTO `t_operator_voice` VALUES (4643, 136, '选中干员1', '收到。', 'char_113_cqbw/CN_021');
INSERT INTO `t_operator_voice` VALUES (4644, 136, '选中干员2', '明白。', 'char_113_cqbw/CN_022');
INSERT INTO `t_operator_voice` VALUES (4645, 136, '部署1', '说不定我会偷偷溜到其他地方去呢。', 'char_113_cqbw/CN_023');
INSERT INTO `t_operator_voice` VALUES (4646, 136, '部署2', '好啦，我就在这里好好休息吧。', 'char_113_cqbw/CN_024');
INSERT INTO `t_operator_voice` VALUES (4647, 136, '作战中1', '是谁会抽中大奖呢？', 'char_113_cqbw/CN_025');
INSERT INTO `t_operator_voice` VALUES (4648, 136, '作战中2', '遍地惊喜，任君自取~', 'char_113_cqbw/CN_026');
INSERT INTO `t_operator_voice` VALUES (4649, 136, '作战中3', '我数到三，你们还有投降的机会，三~', 'char_113_cqbw/CN_027');
INSERT INTO `t_operator_voice` VALUES (4650, 136, '作战中4', '砰！哈哈哈——', 'char_113_cqbw/CN_028');
INSERT INTO `t_operator_voice` VALUES (4651, 136, '4星结束行动', '嗯，能在这样的战场上发挥到这种地步，这种感觉真让人怀念。', 'char_113_cqbw/CN_029');
INSERT INTO `t_operator_voice` VALUES (4652, 136, '3星结束行动', '你看到刚才那个人的表情了吗？那种意识到自己将要四分五裂却无能为力的绝望感，还挺有趣的不是吗！', 'char_113_cqbw/CN_030');
INSERT INTO `t_operator_voice` VALUES (4653, 136, '非3星结束行动', '会出现这种失误真是少见，是你的本领生疏了，还是你顾虑的东西变多了？', 'char_113_cqbw/CN_031');
INSERT INTO `t_operator_voice` VALUES (4654, 136, '行动失败', '撤退？把伤亡控制在最小？欸，从你嘴里听到这种命令，还真让人难以适应啊。', 'char_113_cqbw/CN_032');
INSERT INTO `t_operator_voice` VALUES (4655, 136, '进驻设施', '这扇门，已经修好了啊。', 'char_113_cqbw/CN_033');
INSERT INTO `t_operator_voice` VALUES (4656, 136, '戳一下', '看起来，这儿多了很多东西，也少了很多人。', 'char_113_cqbw/CN_034');
INSERT INTO `t_operator_voice` VALUES (4657, 136, '信赖触摸', '......哪边才是本来的你？', 'char_113_cqbw/CN_036');
INSERT INTO `t_operator_voice` VALUES (4658, 136, '标题', '明日方舟。', 'char_113_cqbw/CN_037');
INSERT INTO `t_operator_voice` VALUES (4659, 136, '问候', 'Dr.{@nickname}，今天你想为罗德岛带来什么？一如既往吗？', 'char_113_cqbw/CN_042');
INSERT INTO `t_operator_voice` VALUES (4660, 137, '任命助理', '适当的开个小差吧，那样对身体好。这可是实话，难道您现在不觉得愉快吗？', 'char_343_tknogi/CN_001');
INSERT INTO `t_operator_voice` VALUES (4661, 137, '交谈1', '“寻找灾害的人”，也就是“灾巡”，按这里的说法应该是叫“天灾信使”吧。又苦、又累、还要被平民和官僚们揶揄，当干员情况会不会好些呢，不会？呵呵，也是。', 'char_343_tknogi/CN_002');
INSERT INTO `t_operator_voice` VALUES (4662, 137, '交谈2', '明日......嗯，原来如此。我亲爱的上司，明天出门的时候记得带伞哦。', 'char_343_tknogi/CN_003');
INSERT INTO `t_operator_voice` VALUES (4663, 137, '交谈3', '要是还能留在东国，我也不想走那么远。但是......被这东西植在肉上，哪儿还能被称作“家”呢。', 'char_343_tknogi/CN_004');
INSERT INTO `t_operator_voice` VALUES (4664, 137, '晋升后交谈1', '把薪资让渡给有需要的人，总好过放在屋子里。反正也就那一点，拿出来，放在别人手里，看着他们微笑，亲口说出感谢的话语，多令人欢喜啊。', 'char_343_tknogi/CN_005');
INSERT INTO `t_operator_voice` VALUES (4665, 137, '晋升后交谈2', '我时常在想，嘴里偶尔滑出的几句古怪话语到底是出自我本人的意识，还是那位素未谋面的神灵大人呢。我的亲族行仪时的话语都摘自古书，但是我总是会不经意间脱口而出，真是困扰呢。', 'char_343_tknogi/CN_006');
INSERT INTO `t_operator_voice` VALUES (4666, 137, '信赖提升后交谈1', '一次一次地跳，神态、姿势、唱词，不允许有一丝区别。有些人乐于让这样的东西充斥生命，还和别人说那能给上天带来喜乐。真是罪过。', 'char_343_tknogi/CN_007');
INSERT INTO `t_operator_voice` VALUES (4667, 137, '信赖提升后交谈2', '我和家族住在山上，离神社不远。山下就是一座佛寺，僧人们晚上都会回到那里。那么问题来了，博士。你觉得会发生些什么？哼哼，结果我留在书桌上了，空闲的时候拆开看看吧。', 'char_343_tknogi/CN_008');
INSERT INTO `t_operator_voice` VALUES (4668, 137, '信赖提升后交谈3', '这么晚了，还在想心事吗？善思善辩是你的优点，可别让它们妨碍睡眠。来，这是东国的熏香，只要闻一下——哎呀，效果是不是好过头了？呼，也罢，今晚就为你守夜吧。', 'char_343_tknogi/CN_009');
INSERT INTO `t_operator_voice` VALUES (4669, 137, '闲置', '闭上双眼，让内心平静下来，好——', 'char_343_tknogi/CN_010');
INSERT INTO `t_operator_voice` VALUES (4670, 137, '干员报到', '初次见面，忘了介绍的礼数可不行。吾乃月禾，曾是东国一地的小小“灾巡”，能成为您的下属，真是不胜荣幸。', 'char_343_tknogi/CN_011');
INSERT INTO `t_operator_voice` VALUES (4671, 137, '观看作战记录', '不同的交流方式？有趣......', 'char_343_tknogi/CN_012');
INSERT INTO `t_operator_voice` VALUES (4672, 137, '精英化晋升1', '啊，是酬劳，哎呀哎呀，用在哪里好呢？', 'char_343_tknogi/CN_013');
INSERT INTO `t_operator_voice` VALUES (4673, 137, '精英化晋升2', '白夜如火，雾影犹存，您又将驻足何处？慢慢来，可别行错了路。', 'char_343_tknogi/CN_014');
INSERT INTO `t_operator_voice` VALUES (4674, 137, '编入队伍', '不负您的信赖。', 'char_343_tknogi/CN_017');
INSERT INTO `t_operator_voice` VALUES (4675, 137, '任命队长', '统率众人，责任重大。', 'char_343_tknogi/CN_018');
INSERT INTO `t_operator_voice` VALUES (4676, 137, '行动出发', '时辰已到，走吧。', 'char_343_tknogi/CN_019');
INSERT INTO `t_operator_voice` VALUES (4677, 137, '行动开始', '比起祈祷，还有更重要的事情要做。', 'char_343_tknogi/CN_020');
INSERT INTO `t_operator_voice` VALUES (4678, 137, '选中干员1', '是了。', 'char_343_tknogi/CN_021');
INSERT INTO `t_operator_voice` VALUES (4679, 137, '选中干员2', '嗯哼。', 'char_343_tknogi/CN_022');
INSERT INTO `t_operator_voice` VALUES (4680, 137, '部署1', '除秽。', 'char_343_tknogi/CN_023');
INSERT INTO `t_operator_voice` VALUES (4681, 137, '部署2', '涤净。', 'char_343_tknogi/CN_024');
INSERT INTO `t_operator_voice` VALUES (4682, 137, '作战中1', '墨焰升腾，身寸尽去。', 'char_343_tknogi/CN_025');
INSERT INTO `t_operator_voice` VALUES (4683, 137, '作战中2', '暮夜沉溺，再无归路。', 'char_343_tknogi/CN_026');
INSERT INTO `t_operator_voice` VALUES (4684, 137, '作战中3', '悠悠茫世，惶然无知。', 'char_343_tknogi/CN_027');
INSERT INTO `t_operator_voice` VALUES (4685, 137, '作战中4', '此耀高昂，他者勿涉！', 'char_343_tknogi/CN_028');
INSERT INTO `t_operator_voice` VALUES (4686, 137, '4星结束行动', '完全帮不上忙呢，这可是件值得庆贺的事。', 'char_343_tknogi/CN_029');
INSERT INTO `t_operator_voice` VALUES (4687, 137, '3星结束行动', '恭喜恭喜，任务圆满完成了。', 'char_343_tknogi/CN_030');
INSERT INTO `t_operator_voice` VALUES (4688, 137, '非3星结束行动', '不碍结果的小事，我已代为完成，可以离开了。', 'char_343_tknogi/CN_031');
INSERT INTO `t_operator_voice` VALUES (4689, 137, '行动失败', '鲁莽行事的结果大多都不尽人意，再思考一下吧。', 'char_343_tknogi/CN_032');
INSERT INTO `t_operator_voice` VALUES (4690, 137, '进驻设施', '我对住所没有要求，任您安排。', 'char_343_tknogi/CN_033');
INSERT INTO `t_operator_voice` VALUES (4691, 137, '戳一下', '哦？', 'char_343_tknogi/CN_034');
INSERT INTO `t_operator_voice` VALUES (4692, 137, '信赖触摸', '有什么不开心的事可以和我说，嗯，什么都行。', 'char_343_tknogi/CN_036');
INSERT INTO `t_operator_voice` VALUES (4693, 137, '标题', '明日方舟。', 'char_343_tknogi/CN_037');
INSERT INTO `t_operator_voice` VALUES (4694, 137, '问候', '别来无恙。', 'char_343_tknogi/CN_042');
INSERT INTO `t_operator_voice` VALUES (4695, 138, '任命助理', '有事快说，有屁快放，姑奶奶忙得很，没多少时间应付你。', 'char_378_asbest/CN_001');
INSERT INTO `t_operator_voice` VALUES (4696, 138, '交谈1', '呀！......混账东西，谁允许你碰我的尾巴的，小心我把你扇飞！还有，忘掉我刚才的声音，忘不掉我可以帮你物理遗忘。', 'char_378_asbest/CN_002');
INSERT INTO `t_operator_voice` VALUES (4697, 138, '交谈2', '我这块门板？看也知道吧，我自己做的，多功能。建议你别乱碰，会出人命的。', 'char_378_asbest/CN_003');
INSERT INTO `t_operator_voice` VALUES (4698, 138, '交谈3', '不得不说，罗德岛反正也要到处跑，我跟着你们的轨迹四处探险好像比我自己走路要快点，嗯，不错，稍微对你们改观了。', 'char_378_asbest/CN_004');
INSERT INTO `t_operator_voice` VALUES (4699, 138, '晋升后交谈1', '别把我和麦哲伦相提并论，我可不是什么探险家，我只是为了追求刺激才在这片大地上到处跑的。为后人开辟道路？姑奶奶可没那么高尚。', 'char_378_asbest/CN_005');
INSERT INTO `t_operator_voice` VALUES (4700, 138, '晋升后交谈2', '越是危险我越要去，越是疯狂我越享受，要是不这样，那人生又有什么意思。', 'char_378_asbest/CN_006');
INSERT INTO `t_operator_voice` VALUES (4701, 138, '信赖提升后交谈1', '麦哲伦？我跟这个自来熟的小丫头片子是在黑流树海认识的，那可真是姑奶奶有生以来最吵闹的一次探险了。不过这丫头确实有点水平，这我承认。', 'char_378_asbest/CN_007');
INSERT INTO `t_operator_voice` VALUES (4702, 138, '信赖提升后交谈2', '朋友？谁要那玩意，姑奶奶的人生目标就是做个混账东西，最好谁也别喜欢我，我也没空去喜欢别人。比起这个，今晚喝一杯？给个准话。', 'char_378_asbest/CN_008');
INSERT INTO `t_operator_voice` VALUES (4703, 138, '信赖提升后交谈3', '我的病？我知道挺严重的，但是只要没给别人添麻烦，怎么对待是我的自由。我的想法很简单，死在旅途上又没什么不好，你要真有本事可以试试到时候把我救回来。加油啊，博士，我不看好你。', 'char_378_asbest/CN_009');
INSERT INTO `t_operator_voice` VALUES (4704, 138, '闲置', '睡着了？很好，让我来制造一点噪音吧，哈哈。', 'char_378_asbest/CN_010');
INSERT INTO `t_operator_voice` VALUES (4705, 138, '干员报到', '虽然我一年里至少有一半时间在外探险，但工资我要正常待遇，也要给我留一间宿舍，你要这样的员工？......要了？哈？啧，神经。', 'char_378_asbest/CN_011');
INSERT INTO `t_operator_voice` VALUES (4706, 138, '观看作战记录', '算了，就当学点防身术......', 'char_378_asbest/CN_012');
INSERT INTO `t_operator_voice` VALUES (4707, 138, '精英化晋升1', '“罗德岛科考队名誉队长”？我？哈？', 'char_378_asbest/CN_013');
INSERT INTO `t_operator_voice` VALUES (4708, 138, '精英化晋升2', '我不会给你任何我能为罗德岛做什么的保证，这不是姑奶奶的风格。不过如果你想知道这片大地的未解之谜，那就由我来为你揭开吧！', 'char_378_asbest/CN_014');
INSERT INTO `t_operator_voice` VALUES (4709, 138, '编入队伍', '团队行动？饶了我吧......', 'char_378_asbest/CN_017');
INSERT INTO `t_operator_voice` VALUES (4710, 138, '任命队长', '啧，博士，你想为难我就直说。', 'char_378_asbest/CN_018');
INSERT INTO `t_operator_voice` VALUES (4711, 138, '行动出发', '跟不上就自个找点事做吧。', 'char_378_asbest/CN_019');
INSERT INTO `t_operator_voice` VALUES (4712, 138, '行动开始', '啧，麻烦。', 'char_378_asbest/CN_020');
INSERT INTO `t_operator_voice` VALUES (4713, 138, '选中干员1', '是是是。', 'char_378_asbest/CN_021');
INSERT INTO `t_operator_voice` VALUES (4714, 138, '选中干员2', '在在在。', 'char_378_asbest/CN_022');
INSERT INTO `t_operator_voice` VALUES (4715, 138, '部署1', '好好好。', 'char_378_asbest/CN_023');
INSERT INTO `t_operator_voice` VALUES (4716, 138, '部署2', '行行行。', 'char_378_asbest/CN_024');
INSERT INTO `t_operator_voice` VALUES (4717, 138, '作战中1', '吃屎去吧。', 'char_378_asbest/CN_025');
INSERT INTO `t_operator_voice` VALUES (4718, 138, '作战中2', '赏你们一门板。', 'char_378_asbest/CN_026');
INSERT INTO `t_operator_voice` VALUES (4719, 138, '作战中3', '啊，好无聊。', 'char_378_asbest/CN_027');
INSERT INTO `t_operator_voice` VALUES (4720, 138, '作战中4', '怎么没完没了的！', 'char_378_asbest/CN_028');
INSERT INTO `t_operator_voice` VALUES (4721, 138, '4星结束行动', '姑奶奶见惯了大风大浪，这点程度还不算什么。', 'char_378_asbest/CN_029');
INSERT INTO `t_operator_voice` VALUES (4722, 138, '3星结束行动', '唉，没劲，我去附近逛逛。', 'char_378_asbest/CN_030');
INSERT INTO `t_operator_voice` VALUES (4723, 138, '非3星结束行动', '差不多得了。', 'char_378_asbest/CN_031');
INSERT INTO `t_operator_voice` VALUES (4724, 138, '行动失败', '啧......难道现在我必须安慰一下这家伙吗......', 'char_378_asbest/CN_032');
INSERT INTO `t_operator_voice` VALUES (4725, 138, '进驻设施', '该回宿舍睡觉了。', 'char_378_asbest/CN_033');
INSERT INTO `t_operator_voice` VALUES (4726, 138, '戳一下', '嗯？！', 'char_378_asbest/CN_034');
INSERT INTO `t_operator_voice` VALUES (4727, 138, '信赖触摸', '对了，要先去趟博士的办公室......', 'char_378_asbest/CN_036');
INSERT INTO `t_operator_voice` VALUES (4728, 138, '标题', '明日方舟。', 'char_378_asbest/CN_037');
INSERT INTO `t_operator_voice` VALUES (4729, 138, '问候', '你很闲吗？', 'char_378_asbest/CN_042');
INSERT INTO `t_operator_voice` VALUES (4730, 139, '任命助理', '为什么把武器放在这里？不是说了吗，你要安心工作，谁来打扰你，就先问过我手里的家伙。', 'char_405_absin/CN_001');
INSERT INTO `t_operator_voice` VALUES (4731, 139, '交谈1', '这里是个好地方，大家都遵从秩序做自己的工作，简直就像是隔绝于现实之外，理想中的孤岛一样。', 'char_405_absin/CN_002');
INSERT INTO `t_operator_voice` VALUES (4732, 139, '交谈2', '博士，那边的那些学生，他们都来自切尔诺伯格吗？他们都是那边的中学的？......原来如此，我还要再多观察一下。', 'char_405_absin/CN_003');
INSERT INTO `t_operator_voice` VALUES (4733, 139, '交谈3', '在切尔诺伯格时的指挥官就是你？既然如此，你应该也看到了那里的现状，博士，为什么你还能保持平静？我真的做不到......', 'char_405_absin/CN_004');
INSERT INTO `t_operator_voice` VALUES (4734, 139, '晋升后交谈1', '我的工作做得很好？像这样慌慌张张逃离故土，一事无成的我，真的有帮到什么人吗？......唔，干嘛，你干嘛摸我的头啊。', 'char_405_absin/CN_005');
INSERT INTO `t_operator_voice` VALUES (4735, 139, '晋升后交谈2', '谁要我的保护？我能保护谁？自那场动乱以来，我就一直在问自己......我看到人们心中的恶意在溢出，最终淹没整座城市。在那样的情况下，或许正义根本不值一提，不复存在。', 'char_405_absin/CN_006');
INSERT INTO `t_operator_voice` VALUES (4736, 139, '信赖提升后交谈1', '我的头发怎么了，欸，有点乱？没办法，这是在逃出来的时候，我自己剪的......哎，你那是什么表情啊，别在意，头发而已，放着不管的话总会再长出来的。', 'char_405_absin/CN_007');
INSERT INTO `t_operator_voice` VALUES (4737, 139, '信赖提升后交谈2', '这身装备原属于我的父亲，一个尽职尽责，整天把保护市民挂在嘴边的警员。我能够理解，我从没有怪过他，我和母亲都没有。只是......博士，我父亲所做的一切，真的值得吗？', 'char_405_absin/CN_008');
INSERT INTO `t_operator_voice` VALUES (4738, 139, '信赖提升后交谈3', '那些人、那些市民的表情，变得和闯入城市的凶徒一样，但他们明明都是我和父亲该保护的人。博士，到底什么才是正确，到底，什么才是，我该怎么办......我不知道......', 'char_405_absin/CN_009');
INSERT INTO `t_operator_voice` VALUES (4739, 139, '闲置', '好困......但是我不能、不能睡着......呼啊......', 'char_405_absin/CN_010');
INSERT INTO `t_operator_voice` VALUES (4740, 139, '干员报到', '感谢救助，我这条命算是欠你们的。虽然能做的不多，但以后如果有用得着我的地方，尽管说。代号？......嗯，就叫我苦艾吧。', 'char_405_absin/CN_011');
INSERT INTO `t_operator_voice` VALUES (4741, 139, '观看作战记录', '看这些，又有什么意义呢。', 'char_405_absin/CN_012');
INSERT INTO `t_operator_voice` VALUES (4742, 139, '精英化晋升1', '晋升就不必了，我在这里受到的照顾已经够多了......呃，一定要？总之，那个，谢谢。', 'char_405_absin/CN_013');
INSERT INTO `t_operator_voice` VALUES (4743, 139, '精英化晋升2', '到底什么才是正义，至今我也没能想明白。但你对我说过，我所坚持的东西不是没有意义的......也许你说得对，我，想要相信。', 'char_405_absin/CN_014');
INSERT INTO `t_operator_voice` VALUES (4744, 139, '编入队伍', '明白了，我去准备一下。', 'char_405_absin/CN_017');
INSERT INTO `t_operator_voice` VALUES (4745, 139, '任命队长', '谁是队长，现场的调配该怎么......欸，是我？', 'char_405_absin/CN_018');
INSERT INTO `t_operator_voice` VALUES (4746, 139, '行动出发', '不要大意。行动！', 'char_405_absin/CN_019');
INSERT INTO `t_operator_voice` VALUES (4747, 139, '行动开始', '停下！前方三十米外有可疑人员......又是那些人吗。', 'char_405_absin/CN_020');
INSERT INTO `t_operator_voice` VALUES (4748, 139, '选中干员1', '再一次......', 'char_405_absin/CN_021');
INSERT INTO `t_operator_voice` VALUES (4749, 139, '选中干员2', '了解。', 'char_405_absin/CN_022');
INSERT INTO `t_operator_voice` VALUES (4750, 139, '部署1', '突击检查，双手抱头！不许动！', 'char_405_absin/CN_023');
INSERT INTO `t_operator_voice` VALUES (4751, 139, '部署2', '这一次......一定！', 'char_405_absin/CN_024');
INSERT INTO `t_operator_voice` VALUES (4752, 139, '作战中1', '别乱动！', 'char_405_absin/CN_025');
INSERT INTO `t_operator_voice` VALUES (4753, 139, '作战中2', '这里禁止通行！', 'char_405_absin/CN_026');
INSERT INTO `t_operator_voice` VALUES (4754, 139, '作战中3', '最后一次警告！准备射击！', 'char_405_absin/CN_027');
INSERT INTO `t_operator_voice` VALUES (4755, 139, '作战中4', '为什么总是会变成这样......', 'char_405_absin/CN_028');
INSERT INTO `t_operator_voice` VALUES (4756, 139, '4星结束行动', '如果当时有你在的话，或许......', 'char_405_absin/CN_029');
INSERT INTO `t_operator_voice` VALUES (4757, 139, '3星结束行动', '任务完成。博士，你没有受伤吧？', 'char_405_absin/CN_030');
INSERT INTO `t_operator_voice` VALUES (4758, 139, '非3星结束行动', '在这些人中，会不会也有切尔诺伯格的市民呢。', 'char_405_absin/CN_031');
INSERT INTO `t_operator_voice` VALUES (4759, 139, '行动失败', '恐惧在爆发，混乱在爆发，必须得走，必须得赶快撤退！！', 'char_405_absin/CN_032');
INSERT INTO `t_operator_voice` VALUES (4760, 139, '进驻设施', '这片露台......如果积雪足够多，可以像这样跳下去玩。嘿！', 'char_405_absin/CN_033');
INSERT INTO `t_operator_voice` VALUES (4761, 139, '戳一下', '唔！干、干什么！', 'char_405_absin/CN_034');
INSERT INTO `t_operator_voice` VALUES (4762, 139, '信赖触摸', '是博士啊。口渴吗，来一起喝一点吧。', 'char_405_absin/CN_036');
INSERT INTO `t_operator_voice` VALUES (4763, 139, '标题', '明日方舟。', 'char_405_absin/CN_037');
INSERT INTO `t_operator_voice` VALUES (4764, 139, '问候', '怎么了，要不要来点什么醒醒神。', 'char_405_absin/CN_042');
INSERT INTO `t_operator_voice` VALUES (4765, 140, '任命助理', '嗯？博士今天也想找我来给你陪聊解闷吗？还是说博士有什么有意思的事情一定得跟我说说？', 'char_373_lionhd/CN_001');
INSERT INTO `t_operator_voice` VALUES (4766, 140, '交谈1', '刚才路过走道的那位是暴行大姐？当初在自治州成立时，还有后来自治州跟雷姆必拓矿业公司达成合作契约时，她的活跃可都不比我差哦？', 'char_373_lionhd/CN_002');
INSERT INTO `t_operator_voice` VALUES (4767, 140, '交谈2', '有些地方对卡特斯人的刻板印象绝对有问题唉，擅长回避危险为什么要被说成是胆小嘛！倒不如说回避危险的技巧只有敢于深赴险境的人才能够掌握，你说对不对？', 'char_373_lionhd/CN_003');
INSERT INTO `t_operator_voice` VALUES (4768, 140, '交谈3', '好啦，我有乖乖在配合检查和吃药啦，只是医疗部那位负责帮我做记录的小姐姐实在太拘谨太小心翼翼了，有点忍不住捉弄她一下，帮她放松下神经。啊，不准把我刚才说的告诉医疗部的家伙。', 'char_373_lionhd/CN_004');
INSERT INTO `t_operator_voice` VALUES (4769, 140, '晋升后交谈1', '我说，博士啊，罗德岛不是不强制要求穿统一制服嘛，所以你也别老是穿着那同一套搭配啊。你看我这身怎么样，这家的设计品味很不错哦，帮你也挑几件吧？', 'char_373_lionhd/CN_005');
INSERT INTO `t_operator_voice` VALUES (4770, 140, '晋升后交谈2', '我还挺喜欢罗德岛这地方的。和那些到处宣讲或者许诺的大人物不同，每个人都只是单纯地在这里找到了各自的目标，然后齐心协力地把舰船开动了起来。就像雷姆必拓的大伙们一样。', 'char_373_lionhd/CN_006');
INSERT INTO `t_operator_voice` VALUES (4771, 140, '信赖提升后交谈1', '手上这把？是我改造过的雷姆必拓的制式爆破枪。当然，改造之前也是用源石技艺驱动的施术单元。嗯.....博士你应该不会认为，单凭力气去用武器敲碎岩石是种有效率的普适工作方式吧？', 'char_373_lionhd/CN_007');
INSERT INTO `t_operator_voice` VALUES (4772, 140, '信赖提升后交谈2', '“尤立卡号”的故事，博士也有兴趣了？我是觉得没什么可讲的小秘密啦，稍微正经点的纪实书上写的都八九不离十。不过要是能把救出人质的过程写详细些就更好了，嗯，不少囚牢都是我拆的。', 'char_373_lionhd/CN_008');
INSERT INTO `t_operator_voice` VALUES (4773, 140, '信赖提升后交谈3', '矿队雇佣天灾信使随行现今也不是什么稀奇事了。像我这样出生在矿船上，又正好对技术类的书籍有点兴趣，就顺便学以致用的也不奇怪。但博士的知识是从哪学来的呢？这里似乎没有相关的文献啊。', 'char_373_lionhd/CN_009');
INSERT INTO `t_operator_voice` VALUES (4774, 140, '闲置', '要小睡一会吗？放心，有急事的话我也会毫不客气地吵醒你的！', 'char_373_lionhd/CN_010');
INSERT INTO `t_operator_voice` VALUES (4775, 140, '干员报到', '天灾信使莱恩哈特，不知道博士你之前有没有在哪听说过？没有？啊，那就好办了，看来我不用像之前被人事姐姐缠住时一样再讲一遍故事了。', 'char_373_lionhd/CN_011');
INSERT INTO `t_operator_voice` VALUES (4776, 140, '观看作战记录', '看起来还是阵地战更适合我大显身手。', 'char_373_lionhd/CN_012');
INSERT INTO `t_operator_voice` VALUES (4777, 140, '精英化晋升1', '晋升？好啊好啊......嗯？没有什么帅气的晋升干员专用新制服吗？小饰品也可以的嘛，也没有？欸......', 'char_373_lionhd/CN_013');
INSERT INTO `t_operator_voice` VALUES (4778, 140, '精英化晋升2', '罗德岛选择要走的这条道路比天灾信使们要崎岖得多，但博士好像还没有要止步的意思。那我也只好牺牲些娱乐时间再多陪博士扫清一些障碍了吧？', 'char_373_lionhd/CN_014');
INSERT INTO `t_operator_voice` VALUES (4779, 140, '编入队伍', '该出勤啦！哦？这次的任务内容是找人打架吗。', 'char_373_lionhd/CN_017');
INSERT INTO `t_operator_voice` VALUES (4780, 140, '任命队长', '我来带领作战小队？嗯，也不是不行，小事一桩。', 'char_373_lionhd/CN_018');
INSERT INTO `t_operator_voice` VALUES (4781, 140, '行动出发', '这就是任务目标点的路线啊......我可以抄近道吗？', 'char_373_lionhd/CN_019');
INSERT INTO `t_operator_voice` VALUES (4782, 140, '行动开始', '那就来看看我们的敌人是不是空有数量的乌合之众吧。', 'char_373_lionhd/CN_020');
INSERT INTO `t_operator_voice` VALUES (4783, 140, '选中干员1', '嗯？哪里哪里？嗯？', 'char_373_lionhd/CN_021');
INSERT INTO `t_operator_voice` VALUES (4784, 140, '选中干员2', '博士，你会给我留个好位置吧？', 'char_373_lionhd/CN_022');
INSERT INTO `t_operator_voice` VALUES (4785, 140, '部署1', '哦！总算轮到我啦！', 'char_373_lionhd/CN_023');
INSERT INTO `t_operator_voice` VALUES (4786, 140, '部署2', '多吸引些敌人进包围圈，之后就包在我身上啦！', 'char_373_lionhd/CN_024');
INSERT INTO `t_operator_voice` VALUES (4787, 140, '作战中1', '谁说枪头就只能用来捅人？', 'char_373_lionhd/CN_025');
INSERT INTO `t_operator_voice` VALUES (4788, 140, '作战中2', '依赖装甲只会让你变得脆弱。', 'char_373_lionhd/CN_026');
INSERT INTO `t_operator_voice` VALUES (4789, 140, '作战中3', '哦~以众欺寡......你们是这么打算的吗？', 'char_373_lionhd/CN_027');
INSERT INTO `t_operator_voice` VALUES (4790, 140, '作战中4', '让我甄别一下你们是金玉还是废料吧！', 'char_373_lionhd/CN_028');
INSERT INTO `t_operator_voice` VALUES (4791, 140, '4星结束行动', '砰！好，这次的强敌也都已经圆满解决了。那么按照之前说好的，博士得犒劳一下自己，在下个停靠点陪我去逛一趟当地的商业街哦？', 'char_373_lionhd/CN_029');
INSERT INTO `t_operator_voice` VALUES (4792, 140, '3星结束行动', '这种程度的敌人对我和博士来说还不算什么，对吧？', 'char_373_lionhd/CN_030');
INSERT INTO `t_operator_voice` VALUES (4793, 140, '非3星结束行动', '收拾残兵败将实在不是我的强项，所以博士再不赶紧派人去追的话，你这次的作战评价可要降低咯？', 'char_373_lionhd/CN_031');
INSERT INTO `t_operator_voice` VALUES (4794, 140, '行动失败', '啊，好像搞砸了，博士先撤吧。没事，这里有我，敌人哪追得上来。', 'char_373_lionhd/CN_032');
INSERT INTO `t_operator_voice` VALUES (4795, 140, '进驻设施', '过会要不要去采购部看看新衣服呢，要不要呢。', 'char_373_lionhd/CN_033');
INSERT INTO `t_operator_voice` VALUES (4796, 140, '戳一下', '嗯？找我？', 'char_373_lionhd/CN_034');
INSERT INTO `t_operator_voice` VALUES (4797, 140, '信赖触摸', '怎么啦，是想带我外出逛逛吗？', 'char_373_lionhd/CN_036');
INSERT INTO `t_operator_voice` VALUES (4798, 140, '标题', '明日方舟。', 'char_373_lionhd/CN_037');
INSERT INTO `t_operator_voice` VALUES (4799, 140, '问候', '哟，博士，今天的活忙完啦？', 'char_373_lionhd/CN_042');
INSERT INTO `t_operator_voice` VALUES (4800, 141, '任命助理', '嗯哼哼~啊，博士，今天过得还愉快吗？', 'char_258_podego/CN_001');
INSERT INTO `t_operator_voice` VALUES (4801, 141, '交谈1', '这是我亲手做的花茶！博士来一杯吧，会让你一整天都心情舒畅哦！', 'char_258_podego/CN_002');
INSERT INTO `t_operator_voice` VALUES (4802, 141, '交谈2', '温室里的阳光总是暖洋洋的，就算我是管理员，有时候也想偷个懒呢。', 'char_258_podego/CN_003');
INSERT INTO `t_operator_voice` VALUES (4803, 141, '交谈3', '用自己的劳动成果，来让大家忘掉忧愁和烦恼，这大概就是花匠们的愿望吧~', 'char_258_podego/CN_004');
INSERT INTO `t_operator_voice` VALUES (4804, 141, '晋升后交谈1', '比起栽培来说，战斗并不是我擅长的事。但是只要一想到能帮到大家，我就充满了勇气。', 'char_258_podego/CN_005');
INSERT INTO `t_operator_voice` VALUES (4805, 141, '晋升后交谈2', '这些瓶子？里面都是我收集了好久的花粉和种子。我摆弄它们的时候一般会戴上防毒面具，再戴上手套，不然可能会中毒的。', 'char_258_podego/CN_006');
INSERT INTO `t_operator_voice` VALUES (4806, 141, '信赖提升后交谈1', '唔，这几天好像大家都有点疲惫呢。不过，只要大家看到我种的花，一定会打起精神来的！', 'char_258_podego/CN_007');
INSERT INTO `t_operator_voice` VALUES (4807, 141, '信赖提升后交谈2', '博士博士，今天要来温室吗？好像大家这几天都很忙啊......不过如果没人来温室，白白让花期过去的话，也太可惜了吧？', 'char_258_podego/CN_008');
INSERT INTO `t_operator_voice` VALUES (4808, 141, '信赖提升后交谈3', '照管温室确实很辛苦。但是，只要我种的花能让别人露出笑容，我的工作就有价值了吧！', 'char_258_podego/CN_009');
INSERT INTO `t_operator_voice` VALUES (4809, 141, '闲置', '博士？睡着了......希望花朵的芬芳，能够飘进你的梦中哦。', 'char_258_podego/CN_010');
INSERT INTO `t_operator_voice` VALUES (4810, 141, '干员报到', '博士，波登可向你问好！有空的话，博士也来温室坐坐吧！', 'char_258_podego/CN_011');
INSERT INTO `t_operator_voice` VALUES (4811, 141, '观看作战记录', '啊，我还以为是关于栽培技巧的录像呢。', 'char_258_podego/CN_012');
INSERT INTO `t_operator_voice` VALUES (4812, 141, '精英化晋升1', '艺术家？不不不，我没有那么厉害啦，我只是一个普通的花匠而已。', 'char_258_podego/CN_013');
INSERT INTO `t_operator_voice` VALUES (4813, 141, '精英化晋升2', '博士你看！我培育了好久的新品种终于绽放了。是不是很好看呢？', 'char_258_podego/CN_014');
INSERT INTO `t_operator_voice` VALUES (4814, 141, '编入队伍', '嗯？我也要一起行动吗！', 'char_258_podego/CN_017');
INSERT INTO `t_operator_voice` VALUES (4815, 141, '任命队长', '啊......我只有照管温室的经验，能帮上忙吗？', 'char_258_podego/CN_018');
INSERT INTO `t_operator_voice` VALUES (4816, 141, '行动出发', '看到敌人了，大家一起上吧！', 'char_258_podego/CN_019');
INSERT INTO `t_operator_voice` VALUES (4817, 141, '行动开始', '一定要照顾好自己啊，各位！', 'char_258_podego/CN_020');
INSERT INTO `t_operator_voice` VALUES (4818, 141, '选中干员1', '嗯？', 'char_258_podego/CN_021');
INSERT INTO `t_operator_voice` VALUES (4819, 141, '选中干员2', '要去哪？', 'char_258_podego/CN_022');
INSERT INTO `t_operator_voice` VALUES (4820, 141, '部署1', '明白了。', 'char_258_podego/CN_023');
INSERT INTO `t_operator_voice` VALUES (4821, 141, '部署2', '我会照办的。', 'char_258_podego/CN_024');
INSERT INTO `t_operator_voice` VALUES (4822, 141, '作战中1', '小心花粉过敏哦。', 'char_258_podego/CN_025');
INSERT INTO `t_operator_voice` VALUES (4823, 141, '作战中2', '啊，把他们交给我吧。', 'char_258_podego/CN_026');
INSERT INTO `t_operator_voice` VALUES (4824, 141, '作战中3', '这种杂草我可见过好多了啊。', 'char_258_podego/CN_027');
INSERT INTO `t_operator_voice` VALUES (4825, 141, '作战中4', '让我来掩护大家！', 'char_258_podego/CN_028');
INSERT INTO `t_operator_voice` VALUES (4826, 141, '4星结束行动', '呼，已经安全了吗？各位辛苦了！', 'char_258_podego/CN_029');
INSERT INTO `t_operator_voice` VALUES (4827, 141, '3星结束行动', '取得完美的胜利和栽培出美丽的花朵一样，都是个辛苦的事情啊。', 'char_258_podego/CN_030');
INSERT INTO `t_operator_voice` VALUES (4828, 141, '非3星结束行动', '博士，大家都在等你的下一步指令哦。', 'char_258_podego/CN_031');
INSERT INTO `t_operator_voice` VALUES (4829, 141, '行动失败', '啊......会变成这个样子，可真是没想到啊......', 'char_258_podego/CN_032');
INSERT INTO `t_operator_voice` VALUES (4830, 141, '进驻设施', '要不要在这里，种上花呢？', 'char_258_podego/CN_033');
INSERT INTO `t_operator_voice` VALUES (4831, 141, '戳一下', '啊，博士？', 'char_258_podego/CN_034');
INSERT INTO `t_operator_voice` VALUES (4832, 141, '信赖触摸', '博士！想要用花来装点办公室吗？', 'char_258_podego/CN_036');
INSERT INTO `t_operator_voice` VALUES (4833, 141, '标题', '明日方舟。', 'char_258_podego/CN_037');
INSERT INTO `t_operator_voice` VALUES (4834, 141, '问候', '博士，你好啊！', 'char_258_podego/CN_042');
INSERT INTO `t_operator_voice` VALUES (4835, 142, '任命助理', '能帮助博士是我的荣幸。', 'char_197_poca/CN_001');
INSERT INTO `t_operator_voice` VALUES (4836, 142, '交谈1', '曾经我天真地以为，如果我想要得到什么，就是可以得到的。现在我已经知道，即使大部分东西确实如此，但有些东西是不行的。那是只靠我自己，无论如何也无法获得的东西......', 'char_197_poca/CN_002');
INSERT INTO `t_operator_voice` VALUES (4837, 142, '交谈2', '无论是什么样的工作都请交给我试一试吧，没事的，经历了那样的事情后，对我来说，所谓贵族的体面，已经最无所谓的东西了。我现在只是和凛冬她们一样，受到保护的普通学生而已。', 'char_197_poca/CN_003');
INSERT INTO `t_operator_voice` VALUES (4838, 142, '交谈3', '博士，如果你想了解切尔诺伯格的事情，我或许可以提供一些独特的视角。虽然我还是一介学生，但你知道，学生会长总是能够提前接触到一些成人社会的东西。', 'char_197_poca/CN_004');
INSERT INTO `t_operator_voice` VALUES (4839, 142, '晋升后交谈1', '像真理这样排斥麻醉自己的娱乐而对追求知识的人，即使在我过去的贵族学校中也是很少见的。要是当时她是和我一起的同学......不，现在想这些也没有意义了。', 'char_197_poca/CN_005');
INSERT INTO `t_operator_voice` VALUES (4840, 142, '晋升后交谈2', '因为一场悲剧而痛恨一切，与因为一场喜剧就热爱一切一样，都是片面的——是这样吗？博士，你已经决定好如何对待将要面对的一切了吗？而我又该如何去面对呢？我已经有些搞不清楚了。', 'char_197_poca/CN_006');
INSERT INTO `t_operator_voice` VALUES (4841, 142, '信赖提升后交谈1', '博士，我有个不情之请。可以请你多关照一下凛冬她们的......精神状况吗？她们看起来适应了现在的生活，但是我知道，其实不是的。就连我自己，时而也会想起那段时光......', 'char_197_poca/CN_007');
INSERT INTO `t_operator_voice` VALUES (4842, 142, '信赖提升后交谈2', '博士，你能明白吗，虽然我可以说我是受害者，但我知道，在某个时刻，我也曾经沉溺其中，我在某个时刻，不自觉地站在加害者的那一边，我是......永远无法原谅自己的。', 'char_197_poca/CN_008');
INSERT INTO `t_operator_voice` VALUES (4843, 142, '信赖提升后交谈3', '博士，成为大人后，就能够变得容易忘记吗？我如果变成大人，就能够原谅自己了吗？变成像博士这样成熟的大人，就能够好好处理自己心里的那些讨人厌的感情吗？', 'char_197_poca/CN_009');
INSERT INTO `t_operator_voice` VALUES (4844, 142, '闲置', '博士睡着的样子......稍微有些可爱呢。', 'char_197_poca/CN_010');
INSERT INTO `t_operator_voice` VALUES (4845, 142, '干员报到', '你好，博士，请允许我再次向罗德岛表达谢意，感谢你们能够收留我和我的朋友们。我已经在后勤部门休息得够久了，现在也让我做一些凛冬她们在做的事吧。', 'char_197_poca/CN_011');
INSERT INTO `t_operator_voice` VALUES (4846, 142, '观看作战记录', '我也必须要适应战场才行呢......', 'char_197_poca/CN_012');
INSERT INTO `t_operator_voice` VALUES (4847, 142, '精英化晋升1', '看来我在学生会中累积的经验在大人的工作中依然适用，这真是再好不过了。', 'char_197_poca/CN_013');
INSERT INTO `t_operator_voice` VALUES (4848, 142, '精英化晋升2', '有关自己该做的事，有关未来，有关我自己，我还有许多不明白的地方，想得越多，不明白的就越多。不过，我还年轻，我还有思考的时间，是这样吧，博士？', 'char_197_poca/CN_014');
INSERT INTO `t_operator_voice` VALUES (4849, 142, '编入队伍', '检验一下训练的成果吧。', 'char_197_poca/CN_017');
INSERT INTO `t_operator_voice` VALUES (4850, 142, '任命队长', '我会尽力的。', 'char_197_poca/CN_018');
INSERT INTO `t_operator_voice` VALUES (4851, 142, '行动出发', '各位，让我们出发吧。', 'char_197_poca/CN_019');
INSERT INTO `t_operator_voice` VALUES (4852, 142, '行动开始', '各位，按照计划行动吧。', 'char_197_poca/CN_020');
INSERT INTO `t_operator_voice` VALUES (4853, 142, '选中干员1', '交给我吧。', 'char_197_poca/CN_021');
INSERT INTO `t_operator_voice` VALUES (4854, 142, '选中干员2', '该我出场了吗？', 'char_197_poca/CN_022');
INSERT INTO `t_operator_voice` VALUES (4855, 142, '部署1', '呼，放轻松，放轻松。', 'char_197_poca/CN_023');
INSERT INTO `t_operator_voice` VALUES (4856, 142, '部署2', '先背诵一遍战斗手册......', 'char_197_poca/CN_024');
INSERT INTO `t_operator_voice` VALUES (4857, 142, '作战中1', '瞄准，发射！', 'char_197_poca/CN_025');
INSERT INTO `t_operator_voice` VALUES (4858, 142, '作战中2', '咦，难道出故障了？呀啊！', 'char_197_poca/CN_026');
INSERT INTO `t_operator_voice` VALUES (4859, 142, '作战中3', '早露，相信自己，你可以的。', 'char_197_poca/CN_027');
INSERT INTO `t_operator_voice` VALUES (4860, 142, '作战中4', '虽然我们无冤无仇......', 'char_197_poca/CN_028');
INSERT INTO `t_operator_voice` VALUES (4861, 142, '4星结束行动', '博士，我们做到了！......啊，唔，咳咳，真是令人喜悦的胜利呢。', 'char_197_poca/CN_029');
INSERT INTO `t_operator_voice` VALUES (4862, 142, '3星结束行动', '呼，没有辜负博士的期望呢。', 'char_197_poca/CN_030');
INSERT INTO `t_operator_voice` VALUES (4863, 142, '非3星结束行动', '博士，抱歉，如果我再努力一些......', 'char_197_poca/CN_031');
INSERT INTO `t_operator_voice` VALUES (4864, 142, '行动失败', '回去之后要开检讨会了呢......', 'char_197_poca/CN_032');
INSERT INTO `t_operator_voice` VALUES (4865, 142, '进驻设施', '不知道有没有沙龙一样的地方呢。', 'char_197_poca/CN_033');
INSERT INTO `t_operator_voice` VALUES (4866, 142, '戳一下', '诶诶？！', 'char_197_poca/CN_034');
INSERT INTO `t_operator_voice` VALUES (4867, 142, '信赖触摸', '今天也是美好的一天。', 'char_197_poca/CN_036');
INSERT INTO `t_operator_voice` VALUES (4868, 142, '标题', '明日方舟。', 'char_197_poca/CN_037');
INSERT INTO `t_operator_voice` VALUES (4869, 142, '问候', '博士，见到你真高兴。', 'char_197_poca/CN_042');
INSERT INTO `t_operator_voice` VALUES (4870, 143, '任命助理', '不就是让我文档归类嘛，这种事我经常干，小菜一碟。', 'char_328_cammou/CN_001');
INSERT INTO `t_operator_voice` VALUES (4871, 143, '交谈1', '博士快看我新做的视频，你一定会喜欢的。', 'char_328_cammou/CN_002');
INSERT INTO `t_operator_voice` VALUES (4872, 143, '交谈2', '对，就是那个空心图标，帮忙点一下好嘛，啊，如果可以的话，旁边那个图标也点一下，嘿嘿，谢谢啦。', 'char_328_cammou/CN_003');
INSERT INTO `t_operator_voice` VALUES (4873, 143, '交谈3', '素材！点子！梗！什么都想不出来！我要掉粉了......', 'char_328_cammou/CN_004');
INSERT INTO `t_operator_voice` VALUES (4874, 143, '晋升后交谈1', '我只是个业余的探险家啦，做好计划拍点儿东西就回来了，不会在外面待上太久。再说了，野外哪有自家小屋子舒服。', 'char_328_cammou/CN_005');
INSERT INTO `t_operator_voice` VALUES (4875, 143, '晋升后交谈2', '医疗部的人肯定又和你说什么奇怪的话了吧。哎呀我承认，有些时候我是有那么点小冲动，但博士你放心，我绝对不会给你添麻烦的。', 'char_328_cammou/CN_006');
INSERT INTO `t_operator_voice` VALUES (4876, 143, '信赖提升后交谈1', '视频剪辑可是我的绝活，你是要做罗德岛宣传片吗博士。有计划书？好的我看看。嗯......罗德岛注意事项、均衡饮食宣传片、消防教育片......这都是些什么啊！', 'char_328_cammou/CN_007');
INSERT INTO `t_operator_voice` VALUES (4877, 143, '信赖提升后交谈2', '直觉告诉我，有一只可怕的菲林就在附近，博士，你可要当心了。他们总会趁你分心的瞬间从黑暗的角落里跳出来，然后一爪子把你摁在地上。唉？不会？', 'char_328_cammou/CN_008');
INSERT INTO `t_operator_voice` VALUES (4878, 143, '信赖提升后交谈3', '和大家一起生活所获得的快乐和拍下新东西的快乐是能相加的！就是因为发现了这一点，我才决定来当干员的，这样，我的快乐，就可以分享给你了呀。', 'char_328_cammou/CN_009');
INSERT INTO `t_operator_voice` VALUES (4879, 143, '闲置', '悄悄拍张照好了，嗯，就拍一张。', 'char_328_cammou/CN_010');
INSERT INTO `t_operator_voice` VALUES (4880, 143, '干员报到', '大家好，这里是卡达的频道！唉？不能在博士房间拍素材？好吧......', 'char_328_cammou/CN_011');
INSERT INTO `t_operator_voice` VALUES (4881, 143, '观看作战记录', '下次我来拍吧。', 'char_328_cammou/CN_012');
INSERT INTO `t_operator_voice` VALUES (4882, 143, '精英化晋升1', '晋升了？好耶！我们来合个影纪念一下吧博士！', 'char_328_cammou/CN_013');
INSERT INTO `t_operator_voice` VALUES (4883, 143, '精英化晋升2', '放轻松，博士，我一兴奋就会这样，没什么好害怕的。', 'char_328_cammou/CN_014');
INSERT INTO `t_operator_voice` VALUES (4884, 143, '编入队伍', '大家好呀。', 'char_328_cammou/CN_017');
INSERT INTO `t_operator_voice` VALUES (4885, 143, '任命队长', '包在我身上。', 'char_328_cammou/CN_018');
INSERT INTO `t_operator_voice` VALUES (4886, 143, '行动出发', '博士，可别落下什么哦。', 'char_328_cammou/CN_019');
INSERT INTO `t_operator_voice` VALUES (4887, 143, '行动开始', '看看这次能拍到些什么有趣的东西。', 'char_328_cammou/CN_020');
INSERT INTO `t_operator_voice` VALUES (4888, 143, '选中干员1', '在呢。', 'char_328_cammou/CN_021');
INSERT INTO `t_operator_voice` VALUES (4889, 143, '选中干员2', '听你的。', 'char_328_cammou/CN_022');
INSERT INTO `t_operator_voice` VALUES (4890, 143, '部署1', '无人机升空。', 'char_328_cammou/CN_023');
INSERT INTO `t_operator_voice` VALUES (4891, 143, '部署2', '开工。', 'char_328_cammou/CN_024');
INSERT INTO `t_operator_voice` VALUES (4892, 143, '作战中1', '发射！', 'char_328_cammou/CN_025');
INSERT INTO `t_operator_voice` VALUES (4893, 143, '作战中2', '大家让一下！', 'char_328_cammou/CN_026');
INSERT INTO `t_operator_voice` VALUES (4894, 143, '作战中3', '我看到你们了。', 'char_328_cammou/CN_027');
INSERT INTO `t_operator_voice` VALUES (4895, 143, '作战中4', '来，看镜头！', 'char_328_cammou/CN_028');
INSERT INTO `t_operator_voice` VALUES (4896, 143, '4星结束行动', '刚刚那个片段简直酷毙了，一会回母舰记得来我这儿看啊。', 'char_328_cammou/CN_029');
INSERT INTO `t_operator_voice` VALUES (4897, 143, '3星结束行动', '太精彩了，简直就是现成的作战记录。', 'char_328_cammou/CN_030');
INSERT INTO `t_operator_voice` VALUES (4898, 143, '非3星结束行动', '刚刚拍照的时候太兴奋，有人溜走了。', 'char_328_cammou/CN_031');
INSERT INTO `t_operator_voice` VALUES (4899, 143, '行动失败', '镜头碎了......', 'char_328_cammou/CN_032');
INSERT INTO `t_operator_voice` VALUES (4900, 143, '进驻设施', '我的视频小窝要开张啦。', 'char_328_cammou/CN_033');
INSERT INTO `t_operator_voice` VALUES (4901, 143, '戳一下', '哎呀！', 'char_328_cammou/CN_034');
INSERT INTO `t_operator_voice` VALUES (4902, 143, '信赖触摸', '来博士，给你看个好东西。', 'char_328_cammou/CN_036');
INSERT INTO `t_operator_voice` VALUES (4903, 143, '标题', '明日方舟。', 'char_328_cammou/CN_037');
INSERT INTO `t_operator_voice` VALUES (4904, 143, '问候', '嗨~', 'char_328_cammou/CN_042');
INSERT INTO `t_operator_voice` VALUES (4905, 144, '任命助理', '在你离开之前，我负责你的护卫工作。', 'char_294_ayer/CN_001');
INSERT INTO `t_operator_voice` VALUES (4906, 144, '交谈1', '博士，有没有那种快速增加身体携带电荷的方法。找会用电的干员？有道理，我去试试。', 'char_294_ayer/CN_002');
INSERT INTO `t_operator_voice` VALUES (4907, 144, '交谈2', '矿石鉴别找莱恩哈特去，我不擅长这个。', 'char_294_ayer/CN_003');
INSERT INTO `t_operator_voice` VALUES (4908, 144, '交谈3', '莱茵的大人物见我就拧着眉头，好像遇到什么害兽一样。我什么时候惹到她的，真是麻烦。', 'char_294_ayer/CN_004');
INSERT INTO `t_operator_voice` VALUES (4909, 144, '晋升后交谈1', '耳朵竖起来了？没关系，嘶，只是被电的，有点舒服。一会，嘶，一会就会垂下去了。', 'char_294_ayer/CN_005');
INSERT INTO `t_operator_voice` VALUES (4910, 144, '晋升后交谈2', '买这宝贝花了我两年的积蓄外加一笔负债。要不是它物超所值，黑市的卡特斯奸商早就把我丢开矿船里运走了。能有今天的舒坦日子，全靠这会漏电的好家伙。', 'char_294_ayer/CN_006');
INSERT INTO `t_operator_voice` VALUES (4911, 144, '信赖提升后交谈1', '哦，你在啊，新鲜冰镇盐渍毛豆，来一根？不用？你要学会理解这种食物，拿着，吃。', 'char_294_ayer/CN_007');
INSERT INTO `t_operator_voice` VALUES (4912, 144, '信赖提升后交谈2', '莱恩哈特太吵了，事又多，还是你这里消停。什么？他和医疗部的人开玩笑被围住了？掌握不好分寸要命这事，他就是学不会，啧。我去圆个场，博士你方便一起来吗？', 'char_294_ayer/CN_008');
INSERT INTO `t_operator_voice` VALUES (4913, 144, '信赖提升后交谈3', '我保护莱特，只是因为我们从小就是这样过来的。他出事，我摆平，反之也一样，但我和你可没那么长久稳定的关系，别以为帮我贴创可贴就能套近乎，明白了吗。上次我也是这么说的？啧，完了。', 'char_294_ayer/CN_009');
INSERT INTO `t_operator_voice` VALUES (4914, 144, '闲置', '嗯？！嗯......是你啊。没什么，我就躺会回回力气。你要想在这休息，我把位置让给你。', 'char_294_ayer/CN_010');
INSERT INTO `t_operator_voice` VALUES (4915, 144, '干员报到', '天灾信使护卫，断崖。 合作愉快。', 'char_294_ayer/CN_011');
INSERT INTO `t_operator_voice` VALUES (4916, 144, '观看作战记录', '很专业，值得学习。', 'char_294_ayer/CN_012');
INSERT INTO `t_operator_voice` VALUES (4917, 144, '精英化晋升1', '最近的薪水是不是给多了？升职？啊，好的，谢谢。', 'char_294_ayer/CN_013');
INSERT INTO `t_operator_voice` VALUES (4918, 144, '精英化晋升2', '护卫不是防御，是掌控。掌控保护者周边的空间，扫除一切威胁。我证明给你看，如何？', 'char_294_ayer/CN_014');
INSERT INTO `t_operator_voice` VALUES (4919, 144, '编入队伍', '入队，等待部署任务。', 'char_294_ayer/CN_017');
INSERT INTO `t_operator_voice` VALUES (4920, 144, '任命队长', '按照自己的节奏行动起来。', 'char_294_ayer/CN_018');
INSERT INTO `t_operator_voice` VALUES (4921, 144, '行动出发', '周围没有异动，小队可以出发。', 'char_294_ayer/CN_019');
INSERT INTO `t_operator_voice` VALUES (4922, 144, '行动开始', '发现目标，上了！', 'char_294_ayer/CN_020');
INSERT INTO `t_operator_voice` VALUES (4923, 144, '选中干员1', '啊。', 'char_294_ayer/CN_021');
INSERT INTO `t_operator_voice` VALUES (4924, 144, '选中干员2', '等你命令。', 'char_294_ayer/CN_022');
INSERT INTO `t_operator_voice` VALUES (4925, 144, '部署1', '装置，启动。', 'char_294_ayer/CN_023');
INSERT INTO `t_operator_voice` VALUES (4926, 144, '部署2', '干活了。', 'char_294_ayer/CN_024');
INSERT INTO `t_operator_voice` VALUES (4927, 144, '作战中1', '晚了！', 'char_294_ayer/CN_025');
INSERT INTO `t_operator_voice` VALUES (4928, 144, '作战中2', '拿你性命！', 'char_294_ayer/CN_026');
INSERT INTO `t_operator_voice` VALUES (4929, 144, '作战中3', '进攻就是最好的护卫。', 'char_294_ayer/CN_027');
INSERT INTO `t_operator_voice` VALUES (4930, 144, '作战中4', '只有生死，无关胜负。', 'char_294_ayer/CN_028');
INSERT INTO `t_operator_voice` VALUES (4931, 144, '4星结束行动', '一切都掌握得不错，哼，恭喜。', 'char_294_ayer/CN_029');
INSERT INTO `t_operator_voice` VALUES (4932, 144, '3星结束行动', '时间正好，该走了。', 'char_294_ayer/CN_030');
INSERT INTO `t_operator_voice` VALUES (4933, 144, '非3星结束行动', '脱离的人在你计划之内，我可以这么认为吧。', 'char_294_ayer/CN_031');
INSERT INTO `t_operator_voice` VALUES (4934, 144, '行动失败', '我身上的伤没什么好在意的，只要你安全回去就好。', 'char_294_ayer/CN_032');
INSERT INTO `t_operator_voice` VALUES (4935, 144, '进驻设施', '你安排吧，我没什么要求。', 'char_294_ayer/CN_033');
INSERT INTO `t_operator_voice` VALUES (4936, 144, '戳一下', '别碰耳朵。', 'char_294_ayer/CN_034');
INSERT INTO `t_operator_voice` VALUES (4937, 144, '信赖触摸', '你看上去很困扰。说吧，有什么事。', 'char_294_ayer/CN_036');
INSERT INTO `t_operator_voice` VALUES (4938, 144, '标题', '明日方舟。', 'char_294_ayer/CN_037');
INSERT INTO `t_operator_voice` VALUES (4939, 144, '问候', '有事？', 'char_294_ayer/CN_042');
INSERT INTO `t_operator_voice` VALUES (4940, 145, '任命助理', '桌面，60分；地面，63分；空气......56分。有待改进啊，博士。', 'char_345_folnic/CN_001');
INSERT INTO `t_operator_voice` VALUES (4941, 145, '交谈1', '最近身体有什么感觉不适的地方吗。嗯，皮肤干燥，嗯嗯，肩膀僵硬，眼球干涩，掉头发，乏力......很好，老问题而已，没什么大碍。只要您开始运动，这些很快就能解决。', 'char_345_folnic/CN_002');
INSERT INTO `t_operator_voice` VALUES (4942, 145, '交谈2', '医疗部的生活就是日常会议、岛内出诊、掌握干员身体情况、新药研究与开发，没什么新鲜事。', 'char_345_folnic/CN_003');
INSERT INTO `t_operator_voice` VALUES (4943, 145, '交谈3', '妈妈，不管您在哪里，请保佑我吧。', 'char_345_folnic/CN_004');
INSERT INTO `t_operator_voice` VALUES (4944, 145, '晋升后交谈1', '多亏大家照顾我们这些负责医疗的人，工作起来焦虑感少了很多，很快乐。当然，也有很多头疼的事情，比如煌干员回来时总搞得浑身是血，每次都是，她不知道清理那些体液有多麻烦吗？', 'char_345_folnic/CN_005');
INSERT INTO `t_operator_voice` VALUES (4945, 145, '晋升后交谈2', '我说过多少次了不要熬夜不要暴饮暴食，您就是听不进去，对不对？那你还管理什么健康，一辈子都躺在椅子上算了！！......不好意思，我去冷静一下。', 'char_345_folnic/CN_006');
INSERT INTO `t_operator_voice` VALUES (4946, 145, '信赖提升后交谈1', '我不太能理解凯尔希老师在思考什么，她一直是那样......独特的人吗？哪怕只有一点，我也希望自己能追上她的步伐。', 'char_345_folnic/CN_007');
INSERT INTO `t_operator_voice` VALUES (4947, 145, '信赖提升后交谈2', '只要恶人能受到惩罚，被非议又怎么样？我不介意把这些毒剂泼洒给敌人，这是他们应得的。', 'char_345_folnic/CN_008');
INSERT INTO `t_operator_voice` VALUES (4948, 145, '信赖提升后交谈3', '乌萨斯这个单词只会给我痛苦，我不想听，我一辈子都不想再听到。但它，是个巨人，发声振聋发聩。会被逼疯的，只有我。', 'char_345_folnic/CN_009');
INSERT INTO `t_operator_voice` VALUES (4949, 145, '闲置', '你也知道自己之前环境清洁评测的分不高吧，再这样下去，博士可就是罗德岛的卫生吊车尾了。呼，这些我暂时帮你收拾了，剩下的记得空闲时清扫一下。', 'char_345_folnic/CN_010');
INSERT INTO `t_operator_voice` VALUES (4950, 145, '干员报到', '我是医疗部的亚叶，现在开始参加战地医疗任务，同时应凯尔希老师的要求，对您的健康情况进行管理，请多指教。', 'char_345_folnic/CN_011');
INSERT INTO `t_operator_voice` VALUES (4951, 145, '观看作战记录', '看来那时候药物的投入量还不够，下次再加三倍应该能达到预定效果。', 'char_345_folnic/CN_012');
INSERT INTO `t_operator_voice` VALUES (4952, 145, '精英化晋升1', '感谢认可，这些都是我应当做的。', 'char_345_folnic/CN_013');
INSERT INTO `t_operator_voice` VALUES (4953, 145, '精英化晋升2', '总有一天，我会成为像凯尔希老师那样的医生，拭目以待吧。', 'char_345_folnic/CN_014');
INSERT INTO `t_operator_voice` VALUES (4954, 145, '编入队伍', '我会尽到职责，保护各位的生命。', 'char_345_folnic/CN_017');
INSERT INTO `t_operator_voice` VALUES (4955, 145, '任命队长', '医疗支援已覆盖全队，接下来就看博士你的了。', 'char_345_folnic/CN_018');
INSERT INTO `t_operator_voice` VALUES (4956, 145, '行动出发', '没事，我能有什么心理负担。', 'char_345_folnic/CN_019');
INSERT INTO `t_operator_voice` VALUES (4957, 145, '行动开始', '已接敌，准备反击与治疗。', 'char_345_folnic/CN_020');
INSERT INTO `t_operator_voice` VALUES (4958, 145, '选中干员1', '还没想好？', 'char_345_folnic/CN_021');
INSERT INTO `t_operator_voice` VALUES (4959, 145, '选中干员2', '时不我待。', 'char_345_folnic/CN_022');
INSERT INTO `t_operator_voice` VALUES (4960, 145, '部署1', '医疗支援已介入。', 'char_345_folnic/CN_023');
INSERT INTO `t_operator_voice` VALUES (4961, 145, '部署2', '已获得攻击许可。', 'char_345_folnic/CN_024');
INSERT INTO `t_operator_voice` VALUES (4962, 145, '作战中1', '开始投药。', 'char_345_folnic/CN_025');
INSERT INTO `t_operator_voice` VALUES (4963, 145, '作战中2', '别担心，马上就起效了。', 'char_345_folnic/CN_026');
INSERT INTO `t_operator_voice` VALUES (4964, 145, '作战中3', '三、二、一！', 'char_345_folnic/CN_027');
INSERT INTO `t_operator_voice` VALUES (4965, 145, '作战中4', '已配置过量药物，开始使用！', 'char_345_folnic/CN_028');
INSERT INTO `t_operator_voice` VALUES (4966, 145, '4星结束行动', '我应该，也帮上了一点忙吧？', 'char_345_folnic/CN_029');
INSERT INTO `t_operator_voice` VALUES (4967, 145, '3星结束行动', '节省医疗资源是件好事，但在战场上没这个必要哦？', 'char_345_folnic/CN_030');
INSERT INTO `t_operator_voice` VALUES (4968, 145, '非3星结束行动', '还好，只是擦伤，清创以后贴个创可贴就行。', 'char_345_folnic/CN_031');
INSERT INTO `t_operator_voice` VALUES (4969, 145, '行动失败', '转移伤员，紧急手术！准备骨髓内输液！', 'char_345_folnic/CN_032');
INSERT INTO `t_operator_voice` VALUES (4970, 145, '进驻设施', '检测完毕，可以入住了。', 'char_345_folnic/CN_033');
INSERT INTO `t_operator_voice` VALUES (4971, 145, '戳一下', '欸？', 'char_345_folnic/CN_034');
INSERT INTO `t_operator_voice` VALUES (4972, 145, '信赖触摸', '有时候对你的言词是有点......过激了，但您也得明白自己的不足之处，可以吗。', 'char_345_folnic/CN_036');
INSERT INTO `t_operator_voice` VALUES (4973, 145, '标题', '明日方舟。', 'char_345_folnic/CN_037');
INSERT INTO `t_operator_voice` VALUES (4974, 145, '问候', '工作辛苦了。', 'char_345_folnic/CN_042');
INSERT INTO `t_operator_voice` VALUES (4975, 146, '任命助理', '博士需要帮忙吗？那学业就一会再说......', 'char_358_lisa/CN_001');
INSERT INTO `t_operator_voice` VALUES (4976, 146, '交谈1', '在意我的尾巴吗？其实来到罗德岛之后，我也意识到尾巴的数量好像和其他沃尔珀姐姐们不太一样，也不知道该找谁商量......不过爸爸的同事们都有很多尾巴呀，真奇怪。', 'char_358_lisa/CN_002');
INSERT INTO `t_operator_voice` VALUES (4977, 146, '交谈2', '烦恼？谢谢博士的关心，大家对我都很好，我不能太任性，不过......不过罗德岛的床铺有点小，睡觉的时候尾巴很难办；还有我养的盆栽总是有人帮我浇好水，其实不用这么麻烦的。', 'char_358_lisa/CN_003');
INSERT INTO `t_operator_voice` VALUES (4978, 146, '交谈3', '阿米娅姐姐真的很厉害，她有很多我没有想过的想法，也能很好的成为大家的表率。但阿米娅姐姐和我一样，她不是大人，她也会失落，也会焦躁，博士，你应该多陪陪阿米娅姐姐。', 'char_358_lisa/CN_004');
INSERT INTO `t_operator_voice` VALUES (4979, 146, '晋升后交谈1', '第一次从东国坐了好——久的车到叙拉古的时候，我真的非常惊讶！原来大地各处的风貌竟然能有这么大的差别！虽然现在只能跟在前辈身后，回忆也不全都是美好的，但我很珍惜现在的生活，嗯！', 'char_358_lisa/CN_005');
INSERT INTO `t_operator_voice` VALUES (4980, 146, '晋升后交谈2', '爸爸变出的火焰很漂亮，明明是火，却像是水晶一样。来到罗德岛之后，我才意识到那是爸爸用来保护他人的力量，爸爸那时候教会我的东西，可以用来帮助各位前辈！所以我会好好努力的！', 'char_358_lisa/CN_006');
INSERT INTO `t_operator_voice` VALUES (4981, 146, '信赖提升后交谈1', '自从手臂受伤之后，妈妈就很少让我出门了。其实我不怪妈妈，我更担心妈妈怎么对付那些坏人。所以我现在要好好生活，这样她就能放心了吧？只要放下心，妈妈可是很强的！', 'char_358_lisa/CN_007');
INSERT INTO `t_operator_voice` VALUES (4982, 146, '信赖提升后交谈2', '虽然我成为干员的时间不长，但是我知道我们在做一件很——困难的事情，对吧！虽然前辈们平时都是有说有笑的，可也都会藏起自己的心思吧。如果我再变强一点，是不是也能帮大家分担苦恼了呢？', 'char_358_lisa/CN_008');
INSERT INTO `t_operator_voice` VALUES (4983, 146, '信赖提升后交谈3', '博士您......失忆了？真的？凯尔希医生才告诉我这件事......嗯，今天就先休息一下吧。博士偶尔也需要好好思考一下吧？剩下的工作就交给我，呃，和阿米娅姐姐吧！嘿嘿！', 'char_358_lisa/CN_009');
INSERT INTO `t_operator_voice` VALUES (4984, 146, '闲置', '这样会着凉的，得赶紧去找毯子。', 'char_358_lisa/CN_010');
INSERT INTO `t_operator_voice` VALUES (4985, 146, '干员报到', '罗德岛干员丽萨......啊不对，是铃兰，干员铃兰！是我自己挑的代号喔，今天开始正式作为干员行动，请多指教！', 'char_358_lisa/CN_011');
INSERT INTO `t_operator_voice` VALUES (4986, 146, '观看作战记录', '这、这就是前辈们战斗的样子，感觉好厉害......', 'char_358_lisa/CN_012');
INSERT INTO `t_operator_voice` VALUES (4987, 146, '精英化晋升1', '博士，今天凯尔希医生夸奖我了......诶，晋升？不是单纯的夸奖？诶？', 'char_358_lisa/CN_013');
INSERT INTO `t_operator_voice` VALUES (4988, 146, '精英化晋升2', '快看，博士！这是我的源石技艺，是努力的成果哦！这样我就可以和前辈们一起去更多地方，去看更多的事情，去帮助更多的人了吧？', 'char_358_lisa/CN_014');
INSERT INTO `t_operator_voice` VALUES (4989, 146, '编入队伍', '难得的出战机会，我会好好努力的！', 'char_358_lisa/CN_017');
INSERT INTO `t_operator_voice` VALUES (4990, 146, '任命队长', '欸，我来指挥各位前辈吗？知、知道了，交给我吧！队长可不能软弱！', 'char_358_lisa/CN_018');
INSERT INTO `t_operator_voice` VALUES (4991, 146, '行动出发', '任务，开始。', 'char_358_lisa/CN_019');
INSERT INTO `t_operator_voice` VALUES (4992, 146, '行动开始', '敌人出现了，呜，不能劝他们放弃吗？', 'char_358_lisa/CN_020');
INSERT INTO `t_operator_voice` VALUES (4993, 146, '选中干员1', '啊，好的！', 'char_358_lisa/CN_021');
INSERT INTO `t_operator_voice` VALUES (4994, 146, '选中干员2', '博士请说！', 'char_358_lisa/CN_022');
INSERT INTO `t_operator_voice` VALUES (4995, 146, '部署1', '不要紧张，不要紧张。', 'char_358_lisa/CN_023');
INSERT INTO `t_operator_voice` VALUES (4996, 146, '部署2', '我们都可以做到的，嗯！', 'char_358_lisa/CN_024');
INSERT INTO `t_operator_voice` VALUES (4997, 146, '作战中1', '小、小心！', 'char_358_lisa/CN_025');
INSERT INTO `t_operator_voice` VALUES (4998, 146, '作战中2', '不会有事吧？没事吗？', 'char_358_lisa/CN_026');
INSERT INTO `t_operator_voice` VALUES (4999, 146, '作战中3', '坚持住！', 'char_358_lisa/CN_027');
INSERT INTO `t_operator_voice` VALUES (5000, 146, '作战中4', '请等一下！', 'char_358_lisa/CN_028');
INSERT INTO `t_operator_voice` VALUES (5001, 146, '4星结束行动', '这就是前辈们真正面对的战场......博士，我觉得我们可以做得更好。我会努力的。', 'char_358_lisa/CN_029');
INSERT INTO `t_operator_voice` VALUES (5002, 146, '3星结束行动', '博士，我有好好表现吗？我没有拖大家的后腿吧？真的？太好了......', 'char_358_lisa/CN_030');
INSERT INTO `t_operator_voice` VALUES (5003, 146, '非3星结束行动', '博士，你没事吧？有其他人受伤了？我、我去照顾他们！', 'char_358_lisa/CN_031');
INSERT INTO `t_operator_voice` VALUES (5004, 146, '行动失败', '抱歉，是我还努力得不够......', 'char_358_lisa/CN_032');
INSERT INTO `t_operator_voice` VALUES (5005, 146, '进驻设施', '我在这里住了很久了，不会迷路的。', 'char_358_lisa/CN_033');
INSERT INTO `t_operator_voice` VALUES (5006, 146, '戳一下', '咿呀！', 'char_358_lisa/CN_034');
INSERT INTO `t_operator_voice` VALUES (5007, 146, '信赖触摸', '博士？啊，要吃小蛋糕吗？本来是给医疗部门的各位准备的慰问品，不过博士也很辛苦......所以不要客气！', 'char_358_lisa/CN_036');
INSERT INTO `t_operator_voice` VALUES (5008, 146, '标题', '明日方舟。', 'char_358_lisa/CN_037');
INSERT INTO `t_operator_voice` VALUES (5009, 146, '问候', '博士，辛苦了。', 'char_358_lisa/CN_042');
INSERT INTO `t_operator_voice` VALUES (5010, 147, '任命助理', '博士，今天要努力工作？嗯，好啊，我会陪博士一起。如果你偷懒的话，就用这根长杖，像这样，嘿，敲敲头。咚咚。', 'char_344_beewax/CN_001');
INSERT INTO `t_operator_voice` VALUES (5011, 147, '交谈1', '蜜蜡、蜜蜡、蜜蜡......嗯，好，再多念几遍应该就能习惯了。啊，博士，有什么事吗？我正在熟悉自己的代号，以免在别人喊我的时候反应不过来，会闹笑话的。', 'char_344_beewax/CN_002');
INSERT INTO `t_operator_voice` VALUES (5012, 147, '交谈2', '这个虫子，因为月见夜先生好像很害怕，一见就原地弹跳起来，所以我就帮他捉起来了。但是，现在该往哪里丢呢......咦，博士，你为什么要向后退？', 'char_344_beewax/CN_003');
INSERT INTO `t_operator_voice` VALUES (5013, 147, '交谈3', '衣服的尺寸稍微大一点，这样披在身上会很安心，我很喜欢这种感觉。只是，偶尔，会不小心踩到——欸，啊，呜哇......！......呜呜，又摔倒了。好痛......', 'char_344_beewax/CN_004');
INSERT INTO `t_operator_voice` VALUES (5014, 147, '晋升后交谈1', '民谣协会本月的新作，是我很喜欢的旋律呢。欸，博士没听说过协会？全称是民谣与源石技艺研究协会......没听过？那电音技艺同盟呢？说唱与民谣联合展望会呢？都没听过？', 'char_344_beewax/CN_005');
INSERT INTO `t_operator_voice` VALUES (5015, 147, '晋升后交谈2', '头上的印记？啊......这个是，族中代代相传的祭祀之印，是我们信奉的金色的神灵对族人的祝福。无颜的神灵庇佑我们，不论我们走到哪里，不论我们是否记得......', 'char_344_beewax/CN_006');
INSERT INTO `t_operator_voice` VALUES (5016, 147, '信赖提升后交谈1', '离开部族，去别的城邦和国家游历，是我们的族人都必须经历的试炼，只有顺利通过，才能成为像姐姐那样可靠的成年人。嗯，博士也相信我一定没问题的，对吧？', 'char_344_beewax/CN_007');
INSERT INTO `t_operator_voice` VALUES (5017, 147, '信赖提升后交谈2', '嘿嘿，我的角，很大很漂亮吧，在族人中能比得上我的人也不多见呢！保养的秘诀是每天涂一遍香油，用刷子轻轻地刷——等等，博士，今天的护理还没有完成，就算是博士也不能随便乱碰！', 'char_344_beewax/CN_008');
INSERT INTO `t_operator_voice` VALUES (5018, 147, '信赖提升后交谈3', '博士，请到这边来，嗯，嗯，闭上眼睛，让我碰碰你的额头。烈阳、风暴、还有泛起波浪的金色沙原，神灵寄宿在孕养我们的这片地上，给予族人们庇佑和祝福。现在，我将这份祝福也分给你，博士。', 'char_344_beewax/CN_009');
INSERT INTO `t_operator_voice` VALUES (5019, 147, '闲置', '呼......呼......姐姐......大笨蛋......呼......嗯，嗯？啊，博士，我睡着了......？欸，梦话？我说了什么吗？', 'char_344_beewax/CN_010');
INSERT INTO `t_operator_voice` VALUES (5020, 147, '干员报到', '......。啊，抱歉，我走神了。你好，博士，我是，我的代号是，呃......对了，我想起来了，是蜜蜡。好听吗？这是最近我的家族的人给我取的！', 'char_344_beewax/CN_011');
INSERT INTO `t_operator_voice` VALUES (5021, 147, '观看作战记录', '嗯......？这些都要看吗？', 'char_344_beewax/CN_012');
INSERT INTO `t_operator_voice` VALUES (5022, 147, '精英化晋升1', '晋升？嗯......不是很明白。', 'char_344_beewax/CN_013');
INSERT INTO `t_operator_voice` VALUES (5023, 147, '精英化晋升2', '博士，或许罗德岛的前路注定艰险而漫长，但是，请记住，神灵的裁决与我的祝福......不论何时，都与你同在。', 'char_344_beewax/CN_014');
INSERT INTO `t_operator_voice` VALUES (5024, 147, '编入队伍', '这次要去哪里呢？', 'char_344_beewax/CN_017');
INSERT INTO `t_operator_voice` VALUES (5025, 147, '任命队长', '我当队长......？虽然不知道博士你的选择标准，不过，可以啊。', 'char_344_beewax/CN_018');
INSERT INTO `t_operator_voice` VALUES (5026, 147, '行动出发', '那么，我们出发吧。', 'char_344_beewax/CN_019');
INSERT INTO `t_operator_voice` VALUES (5027, 147, '行动开始', '那边，有什么人在。请大家小心。', 'char_344_beewax/CN_020');
INSERT INTO `t_operator_voice` VALUES (5028, 147, '选中干员1', '嗯，我在这里。', 'char_344_beewax/CN_021');
INSERT INTO `t_operator_voice` VALUES (5029, 147, '选中干员2', '什么事。', 'char_344_beewax/CN_022');
INSERT INTO `t_operator_voice` VALUES (5030, 147, '部署1', '这里，就交给我。', 'char_344_beewax/CN_023');
INSERT INTO `t_operator_voice` VALUES (5031, 147, '部署2', '所站立之地，即为神灵眷土。', 'char_344_beewax/CN_024');
INSERT INTO `t_operator_voice` VALUES (5032, 147, '作战中1', '吞没他们。', 'char_344_beewax/CN_025');
INSERT INTO `t_operator_voice` VALUES (5033, 147, '作战中2', '压碎他们。', 'char_344_beewax/CN_026');
INSERT INTO `t_operator_voice` VALUES (5034, 147, '作战中3', '在金色沙原中睡吧。', 'char_344_beewax/CN_027');
INSERT INTO `t_operator_voice` VALUES (5035, 147, '作战中4', '此即为神灵之祝福。', 'char_344_beewax/CN_028');
INSERT INTO `t_operator_voice` VALUES (5036, 147, '4星结束行动', '希望他们，也能够得到祝福......', 'char_344_beewax/CN_029');
INSERT INTO `t_operator_voice` VALUES (5037, 147, '3星结束行动', '结束了。呼，博士，我帮上忙了吗？', 'char_344_beewax/CN_030');
INSERT INTO `t_operator_voice` VALUES (5038, 147, '非3星结束行动', '啊，他们跑掉了。嗯......这样也不坏。', 'char_344_beewax/CN_031');
INSERT INTO `t_operator_voice` VALUES (5039, 147, '行动失败', '已经不行了，博士，我会拖住敌人，请快些离开。', 'char_344_beewax/CN_032');
INSERT INTO `t_operator_voice` VALUES (5040, 147, '进驻设施', '好干净的房间，如果不小心把沙子撒上去，是不是不太好？', 'char_344_beewax/CN_033');
INSERT INTO `t_operator_voice` VALUES (5041, 147, '戳一下', '呜啊......又踩到衣服了......', 'char_344_beewax/CN_034');
INSERT INTO `t_operator_voice` VALUES (5042, 147, '信赖触摸', '啊，博士你看！我又抓到了虫子......！', 'char_344_beewax/CN_036');
INSERT INTO `t_operator_voice` VALUES (5043, 147, '标题', '明日方舟。', 'char_344_beewax/CN_037');
INSERT INTO `t_operator_voice` VALUES (5044, 147, '问候', '博士，我在等你呢。', 'char_344_beewax/CN_042');
INSERT INTO `t_operator_voice` VALUES (5045, 148, '任命助理', '“需要交给稀音小姐的任务，请直接让镜头来代劳吧。”', 'char_336_folivo/CN_001');
INSERT INTO `t_operator_voice` VALUES (5046, 148, '交谈1', '“稀音小姐是各大摄影比赛自然和静物组的双料冠军，如果有人愿意等，拿肖像组冠军的概率也会大幅增加。博士你来当稀音小姐的模特好不好？”', 'char_336_folivo/CN_002');
INSERT INTO `t_operator_voice` VALUES (5047, 148, '交谈2', '“检测到博士的耐心正在消逝，开始运行安慰程序——距离稀音小姐完成拍摄还剩不足半小时，请再坚持一下。”', 'char_336_folivo/CN_003');
INSERT INTO `t_operator_voice` VALUES (5048, 148, '交谈3', '“隔壁的札拉克又来找稀音小姐了，虽然小姐还没笑出来，但我知道她很开心。”', 'char_336_folivo/CN_004');
INSERT INTO `t_operator_voice` VALUES (5049, 148, '晋升后交谈1', '“其实稀音小姐原本要给哥伦比亚的摄影器材巨头当专职摄影师的，不知怎么就来您这里当干员了。据镜头推测可能是因为对方让她拍的东西，她不喜欢。是的，稀音小姐就是这么挑剔。”', 'char_336_folivo/CN_005');
INSERT INTO `t_operator_voice` VALUES (5050, 148, '晋升后交谈2', '看到喜欢的，就要拍下，存起来。', 'char_336_folivo/CN_006');
INSERT INTO `t_operator_voice` VALUES (5051, 148, '信赖提升后交谈1', '“博士你又想逗稀音小姐笑吗？其实她已经笑了，虽然想看到笑容需要等，再等，继续等，一直等到嘴角的弧度变得完美为止。这个过程会耗时——不知道几分钟，全看小姐今天心情如何。”', 'char_336_folivo/CN_007');
INSERT INTO `t_operator_voice` VALUES (5052, 148, '信赖提升后交谈2', '“镜头一直在等一个能够理解稀音小姐的人，但现在罗德岛内有许多人都愿意来接触她，镜头感到了快乐，希望有一天小姐能真正找到完美替代镜头的生物。”', 'char_336_folivo/CN_008');
INSERT INTO `t_operator_voice` VALUES (5053, 148, '信赖提升后交谈3', '这是照相机，给你的礼物。谢谢你，博士。', 'char_336_folivo/CN_009');
INSERT INTO `t_operator_voice` VALUES (5054, 148, '闲置', '“正在播放音乐——《D.D.D.热门串烧》。”', 'char_336_folivo/CN_010');
INSERT INTO `t_operator_voice` VALUES (5055, 148, '干员报到', '“摄影师稀音与摄影辅助机器人镜头，很高兴能认识你。”您好。', 'char_336_folivo/CN_011');
INSERT INTO `t_operator_voice` VALUES (5056, 148, '观看作战记录', '“请求重放，重复一遍，请求重放。”', 'char_336_folivo/CN_012');
INSERT INTO `t_operator_voice` VALUES (5057, 148, '精英化晋升1', '“配件已升级，稀音小姐向你表示感谢。”', 'char_336_folivo/CN_013');
INSERT INTO `t_operator_voice` VALUES (5058, 148, '精英化晋升2', '你也有想看的风景吗。', 'char_336_folivo/CN_014');
INSERT INTO `t_operator_voice` VALUES (5059, 148, '编入队伍', '“已获取队友数据。”', 'char_336_folivo/CN_017');
INSERT INTO `t_operator_voice` VALUES (5060, 148, '任命队长', '“已获取子数据。”', 'char_336_folivo/CN_018');
INSERT INTO `t_operator_voice` VALUES (5061, 148, '行动出发', '“确认到出行讯号。”', 'char_336_folivo/CN_019');
INSERT INTO `t_operator_voice` VALUES (5062, 148, '行动开始', '“已启动巡航程序。”', 'char_336_folivo/CN_020');
INSERT INTO `t_operator_voice` VALUES (5063, 148, '选中干员1', '“镜头为您效劳。”', 'char_336_folivo/CN_021');
INSERT INTO `t_operator_voice` VALUES (5064, 148, '选中干员2', '在。', 'char_336_folivo/CN_022');
INSERT INTO `t_operator_voice` VALUES (5065, 148, '部署1', '“开始摄像。”', 'char_336_folivo/CN_023');
INSERT INTO `t_operator_voice` VALUES (5066, 148, '部署2', '嗯。', 'char_336_folivo/CN_024');
INSERT INTO `t_operator_voice` VALUES (5067, 148, '作战中1', '“拍摄——”', 'char_336_folivo/CN_025');
INSERT INTO `t_operator_voice` VALUES (5068, 148, '作战中2', '“曝光——”', 'char_336_folivo/CN_026');
INSERT INTO `t_operator_voice` VALUES (5069, 148, '作战中3', '“全景——”', 'char_336_folivo/CN_027');
INSERT INTO `t_operator_voice` VALUES (5070, 148, '作战中4', '“延时——”', 'char_336_folivo/CN_028');
INSERT INTO `t_operator_voice` VALUES (5071, 148, '4星结束行动', '恭喜。', 'char_336_folivo/CN_029');
INSERT INTO `t_operator_voice` VALUES (5072, 148, '3星结束行动', '“任务完成，正在把稀音小姐运回基地。”', 'char_336_folivo/CN_030');
INSERT INTO `t_operator_voice` VALUES (5073, 148, '非3星结束行动', '“回基地的路不太平坦呢。”', 'char_336_folivo/CN_031');
INSERT INTO `t_operator_voice` VALUES (5074, 148, '行动失败', '“请撤离该区域，请撤离该区域。”', 'char_336_folivo/CN_032');
INSERT INTO `t_operator_voice` VALUES (5075, 148, '进驻设施', '“请为稀音小姐安排吊床，谢谢。”', 'char_336_folivo/CN_033');
INSERT INTO `t_operator_voice` VALUES (5076, 148, '戳一下', '唔？', 'char_336_folivo/CN_034');
INSERT INTO `t_operator_voice` VALUES (5077, 148, '信赖触摸', '拍一张吗？', 'char_336_folivo/CN_036');
INSERT INTO `t_operator_voice` VALUES (5078, 148, '标题', '明日方舟。', 'char_336_folivo/CN_037');
INSERT INTO `t_operator_voice` VALUES (5079, 148, '问候', '又见面了。“您好，您好。”', 'char_336_folivo/CN_042');
INSERT INTO `t_operator_voice` VALUES (5080, 149, '任命助理', '喂，博士，有没有什么好玩的事情？', 'char_349_chiave/CN_001');
INSERT INTO `t_operator_voice` VALUES (5081, 149, '交谈1', '啧，得想个办法让食堂的青椒永远消失。', 'char_349_chiave/CN_002');
INSERT INTO `t_operator_voice` VALUES (5082, 149, '交谈2', '炎国有一句谚语，叫——“及时行乐”，嘿，这句话很适合小爷我。未来？这种东西让奥斯塔去想就好了。', 'char_349_chiave/CN_003');
INSERT INTO `t_operator_voice` VALUES (5083, 149, '交谈3', '喂，博士，别老坐那儿啊，我们来找点乐子吧，比如把这间办公室改造一下怎么样？', 'char_349_chiave/CN_004');
INSERT INTO `t_operator_voice` VALUES (5084, 149, '晋升后交谈1', '奥斯塔以前跟我说，我这样活不久。我当时告诉他，如果我不这样，那活久了又有什么意思？', 'char_349_chiave/CN_005');
INSERT INTO `t_operator_voice` VALUES (5085, 149, '晋升后交谈2', '想要的就去争，喜欢的就去抢，小爷我的字典里，从来没有“放弃”两个字！', 'char_349_chiave/CN_006');
INSERT INTO `t_operator_voice` VALUES (5086, 149, '信赖提升后交谈1', '我来想做什么，奥斯塔想怎么做，布洛卡就负责一起做，一直以来，我们都是这么过来的。', 'char_349_chiave/CN_007');
INSERT INTO `t_operator_voice` VALUES (5087, 149, '信赖提升后交谈2', '博士，皱着眉头想什么呢，有什么烦恼说给我听听，我来帮你出主意，这个我擅长。', 'char_349_chiave/CN_008');
INSERT INTO `t_operator_voice` VALUES (5088, 149, '信赖提升后交谈3', '我说，博士，哪天你要是不当罗德岛的头头了，来跟我们一起干怎么样？小爷我平时不服谁，但对你是服气的，我给你打下手也ok啊。', 'char_349_chiave/CN_009');
INSERT INTO `t_operator_voice` VALUES (5089, 149, '闲置', '喂，博士......怎么睡着了，算了，那等会儿再说。', 'char_349_chiave/CN_010');
INSERT INTO `t_operator_voice` VALUES (5090, 149, '干员报到', '嘿，你就是博士吧？你好你好，幸会幸会，小爷贾维，今天起就是你的手下了！', 'char_349_chiave/CN_011');
INSERT INTO `t_operator_voice` VALUES (5091, 149, '观看作战记录', '啧啧，奥斯塔，快过来瞧瞧，我有一天也要变得这么强！', 'char_349_chiave/CN_012');
INSERT INTO `t_operator_voice` VALUES (5092, 149, '精英化晋升1', '啊？升职？那是什么？', 'char_349_chiave/CN_013');
INSERT INTO `t_operator_voice` VALUES (5093, 149, '精英化晋升2', '不得了啊，罗德岛里有趣的家伙也太多了，这让我怎么坐得住，要变得更强，更强才行！', 'char_349_chiave/CN_014');
INSERT INTO `t_operator_voice` VALUES (5094, 149, '编入队伍', '嘿，走着！', 'char_349_chiave/CN_017');
INSERT INTO `t_operator_voice` VALUES (5095, 149, '任命队长', '听我说，各位，我有个计划！', 'char_349_chiave/CN_018');
INSERT INTO `t_operator_voice` VALUES (5096, 149, '行动出发', '哈哈，让我们干一票大的！', 'char_349_chiave/CN_019');
INSERT INTO `t_operator_voice` VALUES (5097, 149, '行动开始', '把他们全都干趴下就行了吧？', 'char_349_chiave/CN_020');
INSERT INTO `t_operator_voice` VALUES (5098, 149, '选中干员1', '还没轮到小爷？', 'char_349_chiave/CN_021');
INSERT INTO `t_operator_voice` VALUES (5099, 149, '选中干员2', '别让我太无聊啊。', 'char_349_chiave/CN_022');
INSERT INTO `t_operator_voice` VALUES (5100, 149, '部署1', '我已经等不及了！', 'char_349_chiave/CN_023');
INSERT INTO `t_operator_voice` VALUES (5101, 149, '部署2', '哈哈，你们死定了！', 'char_349_chiave/CN_024');
INSERT INTO `t_operator_voice` VALUES (5102, 149, '作战中1', '尝尝这个！', 'char_349_chiave/CN_025');
INSERT INTO `t_operator_voice` VALUES (5103, 149, '作战中2', '喂，别跑啊！', 'char_349_chiave/CN_026');
INSERT INTO `t_operator_voice` VALUES (5104, 149, '作战中3', '好了好了，你们已经很努力了。', 'char_349_chiave/CN_027');
INSERT INTO `t_operator_voice` VALUES (5105, 149, '作战中4', '我兴奋起来了！', 'char_349_chiave/CN_028');
INSERT INTO `t_operator_voice` VALUES (5106, 149, '4星结束行动', '哈，就是要这样的任务才刺激啊！', 'char_349_chiave/CN_029');
INSERT INTO `t_operator_voice` VALUES (5107, 149, '3星结束行动', '啧啧，没什么挑战啊，博士。', 'char_349_chiave/CN_030');
INSERT INTO `t_operator_voice` VALUES (5108, 149, '非3星结束行动', '啊？溜了几个？我都没发现。', 'char_349_chiave/CN_031');
INSERT INTO `t_operator_voice` VALUES (5109, 149, '行动失败', 'Che cazzo uomo！', 'char_349_chiave/CN_032');
INSERT INTO `t_operator_voice` VALUES (5110, 149, '进驻设施', '大哥大姐们好啊！', 'char_349_chiave/CN_033');
INSERT INTO `t_operator_voice` VALUES (5111, 149, '戳一下', '干嘛？', 'char_349_chiave/CN_034');
INSERT INTO `t_operator_voice` VALUES (5112, 149, '信赖触摸', '嘿，博士，心情不错啊。', 'char_349_chiave/CN_036');
INSERT INTO `t_operator_voice` VALUES (5113, 149, '标题', '明日方舟。', 'char_349_chiave/CN_037');
INSERT INTO `t_operator_voice` VALUES (5114, 149, '问候', '博士，吃过没？', 'char_349_chiave/CN_042');
INSERT INTO `t_operator_voice` VALUES (5115, 150, '任命助理', '老板，要我做点夜宵吗？', 'char_272_strong/CN_001');
INSERT INTO `t_operator_voice` VALUES (5116, 150, '交谈1', '老板，你要的。还有这个，你之前提过......大家都说我记忆力很好，不过我没什么感觉。', 'char_272_strong/CN_002');
INSERT INTO `t_operator_voice` VALUES (5117, 150, '交谈2', '我没事的时候，会研究厨艺......不止是海鲜。老板喜欢吃什么吗？', 'char_272_strong/CN_003');
INSERT INTO `t_operator_voice` VALUES (5118, 150, '交谈3', '......老板，难道说，一般需要由我来找话题吗？', 'char_272_strong/CN_004');
INSERT INTO `t_operator_voice` VALUES (5119, 150, '晋升后交谈1', '老板，你要去龙门夜市的话，我可以带路。那里很热闹，就是不太安全。', 'char_272_strong/CN_005');
INSERT INTO `t_operator_voice` VALUES (5120, 150, '晋升后交谈2', '凯尔希大姐特别允许我用罗德岛的渠道来进货，可以尝试一些新东西。老板，有什么特别的要求吗？', 'char_272_strong/CN_006');
INSERT INTO `t_operator_voice` VALUES (5121, 150, '信赖提升后交谈1', '过去，因为一点原因，我一直被星熊警官盯着......不过，我知道，她和街坊的叔叔婶婶们一样，都是真心为我好。', 'char_272_strong/CN_007');
INSERT INTO `t_operator_voice` VALUES (5122, 150, '信赖提升后交谈2', '有人说，以我的刀功和记忆力，还有长相，天生就适合做恶人。我不喜欢，但也不知道怎么办......不过来到罗德岛后，我逐渐知道了，我只要做我想做的事就好。', 'char_272_strong/CN_008');
INSERT INTO `t_operator_voice` VALUES (5123, 150, '信赖提升后交谈3', '老板，你觉得，我的记忆力有什么能派上用场的地方吗......以前我从没想过，但是现在，我想，要是能帮上忙就好了。', 'char_272_strong/CN_009');
INSERT INTO `t_operator_voice` VALUES (5124, 150, '闲置', '老......啊，睡着了，那我继续看书。', 'char_272_strong/CN_010');
INSERT INTO `t_operator_voice` VALUES (5125, 150, '干员报到', '老板好，我叫孑。星熊警官说我更适合在这里做事，我就来了。我懂水产，会做菜，刀功也还可以，应该能帮上忙。', 'char_272_strong/CN_011');
INSERT INTO `t_operator_voice` VALUES (5126, 150, '观看作战记录', '原来如此，战斗和做菜，也差不多。', 'char_272_strong/CN_012');
INSERT INTO `t_operator_voice` VALUES (5127, 150, '精英化晋升1', '老板，这是说，我能把摊开大点的意思吗？', 'char_272_strong/CN_013');
INSERT INTO `t_operator_voice` VALUES (5128, 150, '精英化晋升2', '老板，我发现，把敌人当食材来看待的话，战斗也挺简单的。', 'char_272_strong/CN_014');
INSERT INTO `t_operator_voice` VALUES (5129, 150, '编入队伍', '啊，忘了收摊......', 'char_272_strong/CN_017');
INSERT INTO `t_operator_voice` VALUES (5130, 150, '任命队长', '带队叉鱼的话，我熟。', 'char_272_strong/CN_018');
INSERT INTO `t_operator_voice` VALUES (5131, 150, '行动出发', '......嗯？要出发了吗？', 'char_272_strong/CN_019');
INSERT INTO `t_operator_voice` VALUES (5132, 150, '行动开始', '数量还不少。', 'char_272_strong/CN_020');
INSERT INTO `t_operator_voice` VALUES (5133, 150, '选中干员1', '啊？', 'char_272_strong/CN_021');
INSERT INTO `t_operator_voice` VALUES (5134, 150, '选中干员2', '在。', 'char_272_strong/CN_022');
INSERT INTO `t_operator_voice` VALUES (5135, 150, '部署1', '我试试。', 'char_272_strong/CN_023');
INSERT INTO `t_operator_voice` VALUES (5136, 150, '部署2', '呼......', 'char_272_strong/CN_024');
INSERT INTO `t_operator_voice` VALUES (5137, 150, '作战中1', '去鳞。', 'char_272_strong/CN_025');
INSERT INTO `t_operator_voice` VALUES (5138, 150, '作战中2', '去内脏。', 'char_272_strong/CN_026');
INSERT INTO `t_operator_voice` VALUES (5139, 150, '作战中3', '洗净。', 'char_272_strong/CN_027');
INSERT INTO `t_operator_voice` VALUES (5140, 150, '作战中4', '切块。', 'char_272_strong/CN_028');
INSERT INTO `t_operator_voice` VALUES (5141, 150, '4星结束行动', '老板，我们刚才，是不是干了一件不得了的事？', 'char_272_strong/CN_029');
INSERT INTO `t_operator_voice` VALUES (5142, 150, '3星结束行动', '老板，这就......算完了吗？', 'char_272_strong/CN_030');
INSERT INTO `t_operator_voice` VALUES (5143, 150, '非3星结束行动', '老板，要追吗？', 'char_272_strong/CN_031');
INSERT INTO `t_operator_voice` VALUES (5144, 150, '行动失败', '老板，我觉得，跑路比较好。', 'char_272_strong/CN_032');
INSERT INTO `t_operator_voice` VALUES (5145, 150, '进驻设施', '不知道有没有适合摆摊的地方......', 'char_272_strong/CN_033');
INSERT INTO `t_operator_voice` VALUES (5146, 150, '戳一下', '嗯？', 'char_272_strong/CN_034');
INSERT INTO `t_operator_voice` VALUES (5147, 150, '信赖触摸', '不知道老板有没有忌口......', 'char_272_strong/CN_036');
INSERT INTO `t_operator_voice` VALUES (5148, 150, '标题', '明日方舟。', 'char_272_strong/CN_037');
INSERT INTO `t_operator_voice` VALUES (5149, 150, '问候', '老板，今天气色不错啊。', 'char_272_strong/CN_042');
INSERT INTO `t_operator_voice` VALUES (5150, 151, '任命助理', '希望博士你不会像莫妮卡那么难搞。', 'char_218_cuttle/CN_001');
INSERT INTO `t_operator_voice` VALUES (5151, 151, '交谈1', '莫妮卡的品味是很古怪的，比如她为自己设计代号时，备选方案里都是贤者、面包师之类的古怪名字，现在这个白炽已经是相对比较正常的一个了。', 'char_218_cuttle/CN_002');
INSERT INTO `t_operator_voice` VALUES (5152, 151, '交谈2', '那两个叫斯卡蒂和幽灵鲨的同族，我看到她们就好像......我不知道该怎么形容，就好像小说里常说的一见钟情的感觉？不会吧，这样我是不是应该好好打扮一下......', 'char_218_cuttle/CN_003');
INSERT INTO `t_operator_voice` VALUES (5153, 151, '交谈3', '其实，我一开始对于来罗德岛没什么兴趣，毕竟你们聘请的是莫妮卡，我只是她的保镖。不过她走了我就失业了，找工作也挺麻烦的，就跟来碰碰运气了。', 'char_218_cuttle/CN_004');
INSERT INTO `t_operator_voice` VALUES (5154, 151, '晋升后交谈1', '我和莫妮卡在伊比利亚时是一起生活的，照顾这个作息毫不规律又多事的女人真是一件麻烦的事。不过也没有办法，我和家里的关系不好，没有去处，是她收留的我。', 'char_218_cuttle/CN_005');
INSERT INTO `t_operator_voice` VALUES (5155, 151, '晋升后交谈2', '因为这把武器的造型，曾经我还被审判庭找上门，然后他们拆开看到构造后把我臭骂了一顿。虽然我也知道会让人误会，但我才不管，过了几次他们也就放弃了，让我别拿去吓人就行，哼哼。', 'char_218_cuttle/CN_006');
INSERT INTO `t_operator_voice` VALUES (5156, 151, '信赖提升后交谈1', '博士，罗德岛应该是允许干员自由交友的吧？......不，呃，我只是想和那些拉特兰人交个朋友。我从小就非常喜欢拉特兰的枪械，所以才会自己仿制了这把枪。', 'char_218_cuttle/CN_007');
INSERT INTO `t_operator_voice` VALUES (5157, 151, '信赖提升后交谈2', '说起来，听我老妈说，我小的时候似乎被卷进过什么事情，不过那时候我太小了，现在已经完全没有印象了，她也不想告诉我的样子。反正我现在活得好好的，应该不是什么大事吧。', 'char_218_cuttle/CN_008');
INSERT INTO `t_operator_voice` VALUES (5158, 151, '信赖提升后交谈3', '博士，有时候我觉得你真是挺厉害的，我光是应付莫妮卡一个都觉得够呛了，而你要关心那么多干员......总之，要是遇到什么难事的话，你可以喊我，你知道，我在照顾人上，还蛮有心得的。', 'char_218_cuttle/CN_009');
INSERT INTO `t_operator_voice` VALUES (5159, 151, '闲置', '作息不规律这点倒是和莫妮卡一模一样，哈......毛毯，毛毯......', 'char_218_cuttle/CN_010');
INSERT INTO `t_operator_voice` VALUES (5160, 151, '干员报到', '我之前是莫妮卡......哦，现在应该叫她白炽了，反正算是她的保镖吧。这次她终于找到了正经工作，我也可以做些别的事了，总之，请多关照。', 'char_218_cuttle/CN_011');
INSERT INTO `t_operator_voice` VALUES (5161, 151, '观看作战记录', '这就是专业人士的水准吗......', 'char_218_cuttle/CN_012');
INSERT INTO `t_operator_voice` VALUES (5162, 151, '精英化晋升1', '来之前我还在想，医药公司的保安感觉也不会很忙，要是没应聘上也可以做做保洁什么的。老实说，现在我有点后悔。', 'char_218_cuttle/CN_013');
INSERT INTO `t_operator_voice` VALUES (5163, 151, '精英化晋升2', '其实我自己都没想到我能做到这一步呢......虽然以前没什么想法，不过切实地体会到自己变强了的感觉真不赖。谢了，博士。', 'char_218_cuttle/CN_014');
INSERT INTO `t_operator_voice` VALUES (5164, 151, '编入队伍', '到任务时间了吗？我知道了。', 'char_218_cuttle/CN_017');
INSERT INTO `t_operator_voice` VALUES (5165, 151, '任命队长', '我来带队？嗯......我尽力吧。', 'char_218_cuttle/CN_018');
INSERT INTO `t_operator_voice` VALUES (5166, 151, '行动出发', '希望不会遇到什么麻烦。', 'char_218_cuttle/CN_019');
INSERT INTO `t_operator_voice` VALUES (5167, 151, '行动开始', '啊，麻烦来了。', 'char_218_cuttle/CN_020');
INSERT INTO `t_operator_voice` VALUES (5168, 151, '选中干员1', '我准备好了。', 'char_218_cuttle/CN_021');
INSERT INTO `t_operator_voice` VALUES (5169, 151, '选中干员2', '啊，想听点音乐。', 'char_218_cuttle/CN_022');
INSERT INTO `t_operator_voice` VALUES (5170, 151, '部署1', '敌人还没有发现我的样子。', 'char_218_cuttle/CN_023');
INSERT INTO `t_operator_voice` VALUES (5171, 151, '部署2', '这里视野很好。', 'char_218_cuttle/CN_024');
INSERT INTO `t_operator_voice` VALUES (5172, 151, '作战中1', '集中精神......', 'char_218_cuttle/CN_025');
INSERT INTO `t_operator_voice` VALUES (5173, 151, '作战中2', '你好，再见。', 'char_218_cuttle/CN_026');
INSERT INTO `t_operator_voice` VALUES (5174, 151, '作战中3', '这个人的穿搭还不错......', 'char_218_cuttle/CN_027');
INSERT INTO `t_operator_voice` VALUES (5175, 151, '作战中4', '好烦，敌人好多。', 'char_218_cuttle/CN_028');
INSERT INTO `t_operator_voice` VALUES (5176, 151, '4星结束行动', '博士，吃糖吗？放松一下比较好，我也有点累了。', 'char_218_cuttle/CN_029');
INSERT INTO `t_operator_voice` VALUES (5177, 151, '3星结束行动', '我再去确认一下有没有漏掉的。', 'char_218_cuttle/CN_030');
INSERT INTO `t_operator_voice` VALUES (5178, 151, '非3星结束行动', '差不多就行吗？也好，我也嫌麻烦。', 'char_218_cuttle/CN_031');
INSERT INTO `t_operator_voice` VALUES (5179, 151, '行动失败', '博士，走，我已经确保了一条撤退路线了。', 'char_218_cuttle/CN_032');
INSERT INTO `t_operator_voice` VALUES (5180, 151, '进驻设施', '先熟悉一下环境好了。', 'char_218_cuttle/CN_033');
INSERT INTO `t_operator_voice` VALUES (5181, 151, '戳一下', '喂。', 'char_218_cuttle/CN_034');
INSERT INTO `t_operator_voice` VALUES (5182, 151, '信赖触摸', '博士，需要我帮忙？', 'char_218_cuttle/CN_036');
INSERT INTO `t_operator_voice` VALUES (5183, 151, '标题', '明日方舟。', 'char_218_cuttle/CN_037');
INSERT INTO `t_operator_voice` VALUES (5184, 151, '问候', '博士，吃糖吗？', 'char_218_cuttle/CN_042');
INSERT INTO `t_operator_voice` VALUES (5185, 152, '任命助理', '开始工作前，先喝了这个。好，这样一来，在接下来的药效时间内，博士你就不会偷懒了，是不是？', 'char_293_thorns/CN_001');
INSERT INTO `t_operator_voice` VALUES (5186, 152, '交谈1', '提取液的分离和解析已经差不多了，下一步就是提纯，实际效果还有必要再实验一下......嗯？为什么盯着我的头发看，博士。头发......爆炸了？不要在意，实验失败已经不是一次两次了。', 'char_293_thorns/CN_002');
INSERT INTO `t_operator_voice` VALUES (5187, 152, '交谈2', '我随身带着的这种药剂？不致命，但小心不要碰到比较好。', 'char_293_thorns/CN_003');
INSERT INTO `t_operator_voice` VALUES (5188, 152, '交谈3', '时刻保持头脑运转，放弃思考就等于把要害递到对方手上，我曾经被人这么教导过，也恰好一直记着。', 'char_293_thorns/CN_004');
INSERT INTO `t_operator_voice` VALUES (5189, 152, '晋升后交谈1', '我的种群原本有着与现在截然不同的样貌，我们的皮肤坚硬如甲壳，头发却柔顺如流水，但灼热的日光和干燥的空气最终把我们变成了现在的样子。你相信了？对不起，刚才我是在胡说。', 'char_293_thorns/CN_005');
INSERT INTO `t_operator_voice` VALUES (5190, 152, '晋升后交谈2', '如果你不是拉特兰宗教的信徒，就最好别去伊比利亚。他们所谓的‘最纯正的信仰’就根植在那片土地上，那里的人虔诚得恐怕就连拉特兰人也得自愧弗如。我？这些都同我没有什么关系。', 'char_293_thorns/CN_006');
INSERT INTO `t_operator_voice` VALUES (5191, 152, '信赖提升后交谈1', '博士，你知晓伊比利亚惩戒军过去的历史吗？只要你曾听过任何一点相关的只言片语，就应该会知道，那片土地上的居住者，远比他们冷厉素朴的灰白建筑要疯狂得多。', 'char_293_thorns/CN_007');
INSERT INTO `t_operator_voice` VALUES (5192, 152, '信赖提升后交谈2', '攻击的角度、闪避的线路、防御的姿势，每一个动作都经过计算，没有任何无用的浪费，这才是他们所说的至高之术。有没有觉得，这和你的指挥风格很相似，博士？', 'char_293_thorns/CN_008');
INSERT INTO `t_operator_voice` VALUES (5193, 152, '信赖提升后交谈3', '休息的时候就放松一点，什么都不去想，单纯吹吹风，或者晒晒太阳？......好吧，偶尔尝试一下或许也不错。上来，到这里来，这里的风，会让我想起故乡......', 'char_293_thorns/CN_009');
INSERT INTO `t_operator_voice` VALUES (5194, 152, '闲置', '呼......呼......。嗯？......怎么回事，我竟然也跟着睡着了吗。', 'char_293_thorns/CN_010');
INSERT INTO `t_operator_voice` VALUES (5195, 152, '干员报到', '你就是博士吗，自我介绍一下，代号棘刺，今天起就任——嗯？我的裤脚卷起来了？......不要在意，这是常有的事。', 'char_293_thorns/CN_011');
INSERT INTO `t_operator_voice` VALUES (5196, 152, '观看作战记录', '嗯......这些资料确实有可取之处。', 'char_293_thorns/CN_012');
INSERT INTO `t_operator_voice` VALUES (5197, 152, '精英化晋升1', '晋升？这种事情你来决定就好。我可以离开了吗？', 'char_293_thorns/CN_013');
INSERT INTO `t_operator_voice` VALUES (5198, 152, '精英化晋升2', '快速高效地削减敌对者的行动力，以最小的代价获得胜利——我自认完全理解了你的指挥。所以我使用这种麻痹型的神经毒素来配合攻击，这有什么问题吗？', 'char_293_thorns/CN_014');
INSERT INTO `t_operator_voice` VALUES (5199, 152, '编入队伍', '让我见识一下罗德岛的战斗。', 'char_293_thorns/CN_017');
INSERT INTO `t_operator_voice` VALUES (5200, 152, '任命队长', '队长？最好还是不要期待，我不太擅长指挥。', 'char_293_thorns/CN_018');
INSERT INTO `t_operator_voice` VALUES (5201, 152, '行动出发', '速战速决，我的时间很宝贵。', 'char_293_thorns/CN_019');
INSERT INTO `t_operator_voice` VALUES (5202, 152, '行动开始', '小心一些，不要大意了。', 'char_293_thorns/CN_020');
INSERT INTO `t_operator_voice` VALUES (5203, 152, '选中干员1', '明白。', 'char_293_thorns/CN_021');
INSERT INTO `t_operator_voice` VALUES (5204, 152, '选中干员2', '给我指令。', 'char_293_thorns/CN_022');
INSERT INTO `t_operator_voice` VALUES (5205, 152, '部署1', '不会花费太长时间的。', 'char_293_thorns/CN_023');
INSERT INTO `t_operator_voice` VALUES (5206, 152, '部署2', '让他们都失去行动力，就可以了吧。', 'char_293_thorns/CN_024');
INSERT INTO `t_operator_voice` VALUES (5207, 152, '作战中1', '别乱动，让我们都省些功夫。', 'char_293_thorns/CN_025');
INSERT INTO `t_operator_voice` VALUES (5208, 152, '作战中2', '你的攻击都在推算范围内。', 'char_293_thorns/CN_026');
INSERT INTO `t_operator_voice` VALUES (5209, 152, '作战中3', '没用的。', 'char_293_thorns/CN_027');
INSERT INTO `t_operator_voice` VALUES (5210, 152, '作战中4', '看仔细，这才是伊比利亚的至高之术！', 'char_293_thorns/CN_028');
INSERT INTO `t_operator_voice` VALUES (5211, 152, '4星结束行动', '完美的结果。难道这也都在你的计算之中？', 'char_293_thorns/CN_029');
INSERT INTO `t_operator_voice` VALUES (5212, 152, '3星结束行动', '任务完成。非常精准有效率的指挥，博士。', 'char_293_thorns/CN_030');
INSERT INTO `t_operator_voice` VALUES (5213, 152, '非3星结束行动', '你的指挥有疏漏，而我的执行不当放大了这个疏漏。回去之后我们可以一起反省。', 'char_293_thorns/CN_031');
INSERT INTO `t_operator_voice` VALUES (5214, 152, '行动失败', '考虑到小队的损耗，除了立刻撤退，没有其他更好的选择。', 'char_293_thorns/CN_032');
INSERT INTO `t_operator_voice` VALUES (5215, 152, '进驻设施', '这里的防护措施怎么样？', 'char_293_thorns/CN_033');
INSERT INTO `t_operator_voice` VALUES (5216, 152, '戳一下', '不好，药剂洒出来了。', 'char_293_thorns/CN_034');
INSERT INTO `t_operator_voice` VALUES (5217, 152, '信赖触摸', '头发？啊......我才刚梳过，怎么又乱了。', 'char_293_thorns/CN_036');
INSERT INTO `t_operator_voice` VALUES (5218, 152, '标题', '明日方舟。', 'char_293_thorns/CN_037');
INSERT INTO `t_operator_voice` VALUES (5219, 152, '问候', '你看起来似乎很疲倦，要不要我来帮你清醒一下，博士。', 'char_293_thorns/CN_042');
INSERT INTO `t_operator_voice` VALUES (5220, 161, '任命助理', '没问题，博士，我就待在你身边。', 'char_366_acdrop/CN_001');
INSERT INTO `t_operator_voice` VALUES (5221, 161, '交谈1', '滑行的时候瞄准？很简单啊，想学吗？来，扶着我的手。', 'char_366_acdrop/CN_002');
INSERT INTO `t_operator_voice` VALUES (5222, 161, '交谈2', '以前我和很多朋友一样都以标新立异为荣，等到真成了感染者，发现这一点也不酷。生活应该由自己掌握，而不是那么点源石粉尘。', 'char_366_acdrop/CN_003');
INSERT INTO `t_operator_voice` VALUES (5223, 161, '交谈3', '用弩的技巧是个老警察教我的，滑板的技巧是他的女儿教我的，至于二者怎么结合起来的......这就叫无师自通吧。', 'char_366_acdrop/CN_004');
INSERT INTO `t_operator_voice` VALUES (5224, 161, '晋升后交谈1', '不要误会，滑板只是爱好的一部分......也是我找到自我的第一个途径，之后的精彩才是重头戏，想听吗？', 'char_366_acdrop/CN_005');
INSERT INTO `t_operator_voice` VALUES (5225, 161, '晋升后交谈2', '为什么要成为作战干员......？博士，你觉得那些嘻嘻哈哈的“街头混混”最看重的是什么？当然是自由，我乐意为自由而战，所以我为罗德岛而战。', 'char_366_acdrop/CN_006');
INSERT INTO `t_operator_voice` VALUES (5226, 161, '信赖提升后交谈1', '成为感染者的遗憾有很多，比如我在公园的涂鸦才刚到一半，比如现在吃不到那个老警察独家配方的洋葱圈，嗯？仅此而已哦。', 'char_366_acdrop/CN_007');
INSERT INTO `t_operator_voice` VALUES (5227, 161, '信赖提升后交谈2', '我是抱着治病的心态来到罗德岛的，本来以为会是个到处都是消毒水味，也不让人随意走动的地方......结果还真出乎预料，就是舰内不允许涂鸦，不过伊桑他......不，没什么。', 'char_366_acdrop/CN_008');
INSERT INTO `t_operator_voice` VALUES (5228, 161, '信赖提升后交谈3', '我在汉堡甜甜圈套餐，滑板和街舞比赛里找到了解放自己的方法，你呢，博士？太累了的话，来找我和红豆她们，就当......玩耍吧。玩耍是天性，是很重要的。', 'char_366_acdrop/CN_009');
INSERT INTO `t_operator_voice` VALUES (5229, 161, '闲置', '累了就休息吧，还有我在。', 'char_366_acdrop/CN_010');
INSERT INTO `t_operator_voice` VALUES (5230, 161, '干员报到', '干员，酸糖，这块滑板是登记的装备，嗯，有什么问题尽管交给我吧。', 'char_366_acdrop/CN_011');
INSERT INTO `t_operator_voice` VALUES (5231, 161, '观看作战记录', '还真酷。', 'char_366_acdrop/CN_012');
INSERT INTO `t_operator_voice` VALUES (5232, 161, '精英化晋升1', '晋升吗，谢谢你。上一次这样还是在磐蟹杯上拿奖的时候吧。', 'char_366_acdrop/CN_013');
INSERT INTO `t_operator_voice` VALUES (5233, 161, '精英化晋升2', '那个老家伙教我用弩的初衷只是在混乱的街区里保护自己，谁想得到会有这一天呢，为感染者自由而战的这一天。', 'char_366_acdrop/CN_014');
INSERT INTO `t_operator_voice` VALUES (5234, 161, '编入队伍', '得令。', 'char_366_acdrop/CN_017');
INSERT INTO `t_operator_voice` VALUES (5235, 161, '任命队长', '团队合作也是得分的重点。', 'char_366_acdrop/CN_018');
INSERT INTO `t_operator_voice` VALUES (5236, 161, '行动出发', '出发。', 'char_366_acdrop/CN_019');
INSERT INTO `t_operator_voice` VALUES (5237, 161, '行动开始', '发现敌人。', 'char_366_acdrop/CN_020');
INSERT INTO `t_operator_voice` VALUES (5238, 161, '选中干员1', '我在这。', 'char_366_acdrop/CN_021');
INSERT INTO `t_operator_voice` VALUES (5239, 161, '选中干员2', '说吧，怎么对付他们？', 'char_366_acdrop/CN_022');
INSERT INTO `t_operator_voice` VALUES (5240, 161, '部署1', '真让人跃跃欲试啊。', 'char_366_acdrop/CN_023');
INSERT INTO `t_operator_voice` VALUES (5241, 161, '部署2', '漂亮的地形。', 'char_366_acdrop/CN_024');
INSERT INTO `t_operator_voice` VALUES (5242, 161, '作战中1', '跳吧！', 'char_366_acdrop/CN_025');
INSERT INTO `t_operator_voice` VALUES (5243, 161, '作战中2', '炫技时间。', 'char_366_acdrop/CN_026');
INSERT INTO `t_operator_voice` VALUES (5244, 161, '作战中3', '我在这儿喔。', 'char_366_acdrop/CN_027');
INSERT INTO `t_operator_voice` VALUES (5245, 161, '作战中4', '空翻，然后命中。', 'char_366_acdrop/CN_028');
INSERT INTO `t_operator_voice` VALUES (5246, 161, '4星结束行动', '辛苦了，博士......啊，回去之后陪我换两颗螺丝钉吧？', 'char_366_acdrop/CN_029');
INSERT INTO `t_operator_voice` VALUES (5247, 161, '3星结束行动', '挑战成功的瞬间永远是最有自豪感的，对吧？', 'char_366_acdrop/CN_030');
INSERT INTO `t_operator_voice` VALUES (5248, 161, '非3星结束行动', '要追吗？我的话，追得上。', 'char_366_acdrop/CN_031');
INSERT INTO `t_operator_voice` VALUES (5249, 161, '行动失败', '唔，这种低级的动作失误......', 'char_366_acdrop/CN_032');
INSERT INTO `t_operator_voice` VALUES (5250, 161, '进驻设施', '这块墙很适合......不，我什么都没说。', 'char_366_acdrop/CN_033');
INSERT INTO `t_operator_voice` VALUES (5251, 161, '戳一下', '唔哇！', 'char_366_acdrop/CN_034');
INSERT INTO `t_operator_voice` VALUES (5252, 161, '信赖触摸', '要一起去小卖部吗？如果路上我没有一次失误，请我喝果汁。', 'char_366_acdrop/CN_036');
INSERT INTO `t_operator_voice` VALUES (5253, 161, '标题', '明日方舟。', 'char_366_acdrop/CN_037');
INSERT INTO `t_operator_voice` VALUES (5254, 161, '问候', '哟，博士。', 'char_366_acdrop/CN_042');
INSERT INTO `t_operator_voice` VALUES (5255, 162, '任命助理', '处理文件？好啊，该怎么做，你教我。嗯，先分类，这样？再排序，按照时间顺序......啊，纸弄破了......对、对不住，我不是有意的。', 'char_415_flint/CN_001');
INSERT INTO `t_operator_voice` VALUES (5256, 162, '交谈1', '这里的有些人，是不是高大过头了？......不，没有，我才没有很在意，个头高大与否与实力无关，我只要把他们都打倒就行了。', 'char_415_flint/CN_002');
INSERT INTO `t_operator_voice` VALUES (5257, 162, '交谈2', '我走之后，我们部族怎么办？这是什么傻问题，比比谁的拳头大，选个新的头领把日子继续过下去不就行了。', 'char_415_flint/CN_003');
INSERT INTO `t_operator_voice` VALUES (5258, 162, '交谈3', '我听了很多嘉维尔的事，像胸口碎石单手就能撕碎狂暴凶兽之类的。她真有那么强？比煌大头目还强？华法琳说，可以找嘉维尔治疗来切身体会她的强大......嗯？博士，为什么要拦着我？', 'char_415_flint/CN_004');
INSERT INTO `t_operator_voice` VALUES (5259, 162, '晋升后交谈1', '我是为了变强才来这里的，这个目的现在也没变。不仅是煌大头目和嘉维尔，这里还有许多值得我挑战的家伙......嗯？我看起来很开心？是这样没错，我不讨厌呆在这里。', 'char_415_flint/CN_005');
INSERT INTO `t_operator_voice` VALUES (5260, 162, '晋升后交谈2', '罗德岛的人，是不是都想得太多了？博士你也是，每天都要考虑那么多东西，会累吧。不会？必须得做？......也是，不少东西只有你能搞得定，没办法。嗯，那你就加油吧，有事随时叫我。', 'char_415_flint/CN_006');
INSERT INTO `t_operator_voice` VALUES (5261, 162, '信赖提升后交谈1', '博士，帮我个忙，上面，摆在上面的那个花篮，帮我拿一下。欸，你也够不着？伤脑筋了......要不然，我把举你起来试试？', 'char_415_flint/CN_007');
INSERT INTO `t_operator_voice` VALUES (5262, 162, '信赖提升后交谈2', '祖玛玛这次和我出同一个任务？放心，我不会突然和她打起来的，我和她要决胜负，机会要多少有多少。而且，我们要是起冲突，你会为难吧？嗯，我也不太想让你为难。', 'char_415_flint/CN_008');
INSERT INTO `t_operator_voice` VALUES (5263, 162, '信赖提升后交谈3', '这朵花，是不是很好看，这是我自己种的。喂，干嘛那么吃惊，我又不是只知道变强的那种战斗疯子，有点爱好也很正常吧。嗯？你也喜欢吗？那送给你。嗯，不错，很衬你。', 'char_415_flint/CN_009');
INSERT INTO `t_operator_voice` VALUES (5264, 162, '闲置', '博士，我摘了点花来......嗯？睡了？哼，真是个悠闲的家伙，这花......就放头上吧，嗯，还挺好看的。', 'char_415_flint/CN_010');
INSERT INTO `t_operator_voice` VALUES (5265, 162, '干员报到', '又见面了啊，我是我们部族最高大强壮的战士，燧石。听说你们这里有很多很强的家伙？很好，我会挨个去挑战。啊对了，你知道在哪儿能找到煌大头目吗？', 'char_415_flint/CN_011');
INSERT INTO `t_operator_voice` VALUES (5266, 162, '观看作战记录', '不错，这招不错！博士，我想挑战这人！在哪里能找到这上面的这位战士？', 'char_415_flint/CN_012');
INSERT INTO `t_operator_voice` VALUES (5267, 162, '精英化晋升1', '晋升？行，你决定就好。总之，我还是像之前一样战斗就行了吧？', 'char_415_flint/CN_013');
INSERT INTO `t_operator_voice` VALUES (5268, 162, '精英化晋升2', '博士你啊，就是想得太多了，有什么敌人我们靠拳头打过去不就行了！来，放松点，该休息的时候就别那么紧绷，要过来一起躺着吗。', 'char_415_flint/CN_014');
INSERT INTO `t_operator_voice` VALUES (5269, 162, '编入队伍', '行，出发时叫我一声。', 'char_415_flint/CN_017');
INSERT INTO `t_operator_voice` VALUES (5270, 162, '任命队长', '没问题，交给我。', 'char_415_flint/CN_018');
INSERT INTO `t_operator_voice` VALUES (5271, 162, '行动出发', '出发，各自注意掩护，别留下痕迹。', 'char_415_flint/CN_019');
INSERT INTO `t_operator_voice` VALUES (5272, 162, '行动开始', '猎物上门了。', 'char_415_flint/CN_020');
INSERT INTO `t_operator_voice` VALUES (5273, 162, '选中干员1', '上了。', 'char_415_flint/CN_021');
INSERT INTO `t_operator_voice` VALUES (5274, 162, '选中干员2', '行。', 'char_415_flint/CN_022');
INSERT INTO `t_operator_voice` VALUES (5275, 162, '部署1', '一决胜负。', 'char_415_flint/CN_023');
INSERT INTO `t_operator_voice` VALUES (5276, 162, '部署2', '你们，挨个来还是一起上？', 'char_415_flint/CN_024');
INSERT INTO `t_operator_voice` VALUES (5277, 162, '作战中1', '哼，来得好！', 'char_415_flint/CN_025');
INSERT INTO `t_operator_voice` VALUES (5278, 162, '作战中2', '这拳如何！', 'char_415_flint/CN_026');
INSERT INTO `t_operator_voice` VALUES (5279, 162, '作战中3', '你们的眼神，很讨厌。', 'char_415_flint/CN_027');
INSERT INTO `t_operator_voice` VALUES (5280, 162, '作战中4', '不许俯视我！', 'char_415_flint/CN_028');
INSERT INTO `t_operator_voice` VALUES (5281, 162, '4星结束行动', '不用拳头也能获胜？唔，这就是你的作风吗......', 'char_415_flint/CN_029');
INSERT INTO `t_operator_voice` VALUES (5282, 162, '3星结束行动', '干得不错。看不出来，你还挺有一套的。', 'char_415_flint/CN_030');
INSERT INTO `t_operator_voice` VALUES (5283, 162, '非3星结束行动', '呿，那种小喽啰，逃了就逃了吧，没必要追。', 'char_415_flint/CN_031');
INSERT INTO `t_operator_voice` VALUES (5284, 162, '行动失败', '别自责，是我还不够强。', 'char_415_flint/CN_032');
INSERT INTO `t_operator_voice` VALUES (5285, 162, '进驻设施', '哎，我能在房间里挂两个沙袋吗？', 'char_415_flint/CN_033');
INSERT INTO `t_operator_voice` VALUES (5286, 162, '戳一下', '呃！', 'char_415_flint/CN_034');
INSERT INTO `t_operator_voice` VALUES (5287, 162, '信赖触摸', '嗯？蹲在这里做什么，要我摸摸你的头吗？', 'char_415_flint/CN_036');
INSERT INTO `t_operator_voice` VALUES (5288, 162, '标题', '明日方舟。', 'char_415_flint/CN_037');
INSERT INTO `t_operator_voice` VALUES (5289, 162, '问候', '打起精神，今天有什么需要我帮忙的吗。', 'char_415_flint/CN_042');
INSERT INTO `t_operator_voice` VALUES (5290, 163, '任命助理', '这些工作嘉维尔是不会做的，部族里也没人想到要做，所以，我有空的时候会干一些。', 'char_411_tomimi/CN_001');
INSERT INTO `t_operator_voice` VALUES (5291, 163, '交谈1', '医疗部缺实习护士的话，我可以去试试看，基础护理知识，我还是多少懂一点的。', 'char_411_tomimi/CN_002');
INSERT INTO `t_operator_voice` VALUES (5292, 163, '交谈2', '博士，签字笔好像没墨了。啊，等下，请不要扔！如果您不介意的话，那个，能留给我吗？', 'char_411_tomimi/CN_003');
INSERT INTO `t_operator_voice` VALUES (5293, 163, '交谈3', '罗德岛不能往下挖吗？地下没有宝物吗？珍贵的东西都锁在房间里？好可惜啊......', 'char_411_tomimi/CN_004');
INSERT INTO `t_operator_voice` VALUES (5294, 163, '晋升后交谈1', '尾巴，又长了啊，再这样长下去，真的会被人嫌弃的啊，呜呜......好烦恼。', 'char_411_tomimi/CN_005');
INSERT INTO `t_operator_voice` VALUES (5295, 163, '晋升后交谈2', '尾巴大是天生的，强大又帅气是天生的，脑袋好也是天生的。既然没法选择出身，就怀抱着自己的独特生活下去吧。唔，当然博士您偶尔能帮帮我的话......是最好啦。', 'char_411_tomimi/CN_006');
INSERT INTO `t_operator_voice` VALUES (5296, 163, '信赖提升后交谈1', '嘉维尔会旷工我是可以理解的，但她怎么会旷餐呢？是有什么东西让她困惑，害得她不得不躲起来吗？别人好像都没发现，能救她的只有我了！啊博士您在啊，能帮我一个忙吗？', 'char_411_tomimi/CN_007');
INSERT INTO `t_operator_voice` VALUES (5297, 163, '信赖提升后交谈2', '您真的要看吗，背包里东西可能会有点多哦？好吧，我也好久没清点过了。唔，我们来看看：包裹着树叶的琥珀、一颗牙齿？一块带血黑结晶......一沓签名？！博士您不能再看了！到此为止！', 'char_411_tomimi/CN_008');
INSERT INTO `t_operator_voice` VALUES (5298, 163, '信赖提升后交谈3', '能够引人直视，直到炽热的火焰烧干眼眶也不愿合上眼皮。嘉维尔她，大概就是这样闪耀的东西吧。哦对了，之前那段描述是我从书上看的，我觉得不错就背下来自己用了，还合适吧。', 'char_411_tomimi/CN_009');
INSERT INTO `t_operator_voice` VALUES (5299, 163, '闲置', '捕梦网，就放这里吧，希望您有个干净舒适的好梦。', 'char_411_tomimi/CN_010');
INSERT INTO `t_operator_voice` VALUES (5300, 163, '干员报到', '您好，博士。呃我不是来捣乱的，我是，嗯，学习，对，来学习的。嘉维尔都过来了，我也不好一个人待在家乡，对吧。', 'char_411_tomimi/CN_011');
INSERT INTO `t_operator_voice` VALUES (5301, 163, '观看作战记录', '还，还是上战场直接操练一下比较好，对吧。', 'char_411_tomimi/CN_012');
INSERT INTO `t_operator_voice` VALUES (5302, 163, '精英化晋升1', '谢谢您送我这么好看的徽章，博士。嗯？唉，晋升奖章？啊，呃，呜呜......我，真的有资格拿吗？', 'char_411_tomimi/CN_013');
INSERT INTO `t_operator_voice` VALUES (5303, 163, '精英化晋升2', '我总有一天会配得上这面旗的，总有一天。', 'char_411_tomimi/CN_014');
INSERT INTO `t_operator_voice` VALUES (5304, 163, '编入队伍', '让我学学看您的技巧，要是真能学会就好了。', 'char_411_tomimi/CN_017');
INSERT INTO `t_operator_voice` VALUES (5305, 163, '任命队长', '交给我吗，真的吗？', 'char_411_tomimi/CN_018');
INSERT INTO `t_operator_voice` VALUES (5306, 163, '行动出发', '走了，打，嗯，打爆他们。', 'char_411_tomimi/CN_019');
INSERT INTO `t_operator_voice` VALUES (5307, 163, '行动开始', '等一下，我还没准备好！', 'char_411_tomimi/CN_020');
INSERT INTO `t_operator_voice` VALUES (5308, 163, '选中干员1', '您没看见我，没看见我......', 'char_411_tomimi/CN_021');
INSERT INTO `t_operator_voice` VALUES (5309, 163, '选中干员2', '试试看？', 'char_411_tomimi/CN_022');
INSERT INTO `t_operator_voice` VALUES (5310, 163, '部署1', '上，上了！', 'char_411_tomimi/CN_023');
INSERT INTO `t_operator_voice` VALUES (5311, 163, '部署2', '需要挨打的家伙，都站出来！', 'char_411_tomimi/CN_024');
INSERT INTO `t_operator_voice` VALUES (5312, 163, '作战中1', '振作起来！', 'char_411_tomimi/CN_025');
INSERT INTO `t_operator_voice` VALUES (5313, 163, '作战中2', '再撑一会，再撑一会！', 'char_411_tomimi/CN_026');
INSERT INTO `t_operator_voice` VALUES (5314, 163, '作战中3', '哈！', 'char_411_tomimi/CN_027');
INSERT INTO `t_operator_voice` VALUES (5315, 163, '作战中4', '还有谁！', 'char_411_tomimi/CN_028');
INSERT INTO `t_operator_voice` VALUES (5316, 163, '4星结束行动', '以前博士您就是这么暴打我的。', 'char_411_tomimi/CN_029');
INSERT INTO `t_operator_voice` VALUES (5317, 163, '3星结束行动', '赢了呢。', 'char_411_tomimi/CN_030');
INSERT INTO `t_operator_voice` VALUES (5318, 163, '非3星结束行动', '唔，背好酸，休息一下。', 'char_411_tomimi/CN_031');
INSERT INTO `t_operator_voice` VALUES (5319, 163, '行动失败', '我......对不起......', 'char_411_tomimi/CN_032');
INSERT INTO `t_operator_voice` VALUES (5320, 163, '进驻设施', '啊啊，这个还不能丢，请给我吧，我自己来处理。', 'char_411_tomimi/CN_033');
INSERT INTO `t_operator_voice` VALUES (5321, 163, '戳一下', '尾，尾巴......', 'char_411_tomimi/CN_034');
INSERT INTO `t_operator_voice` VALUES (5322, 163, '信赖触摸', '来尝尝萨尔贡土法烧烤好不好，我来烤，您吃就好。', 'char_411_tomimi/CN_036');
INSERT INTO `t_operator_voice` VALUES (5323, 163, '标题', '明日方舟。', 'char_411_tomimi/CN_037');
INSERT INTO `t_operator_voice` VALUES (5324, 163, '问候', '唔唔，博士。', 'char_411_tomimi/CN_042');
INSERT INTO `t_operator_voice` VALUES (5325, 164, '任命助理', '需要我帮忙吗？好。', 'char_416_zumama/CN_001');
INSERT INTO `t_operator_voice` VALUES (5326, 164, '交谈1', '这里不准打地铺吗？那我在走廊里搭帐篷好了......也不行？为什么？在部族的时候，我都是累了直接在旁边睡的。', 'char_416_zumama/CN_002');
INSERT INTO `t_operator_voice` VALUES (5327, 164, '交谈2', '博士，为什么我和Lancet-2一起居住的申请被拒绝了？她是机器？不，我们已经是姐妹了，她是大姐，我是小妹。', 'char_416_zumama/CN_003');
INSERT INTO `t_operator_voice` VALUES (5328, 164, '交谈3', '博士，嘉维尔那样的医生在外面很常见吗？她给我检查矿石病的时候，我觉得和她在揍我时差不多......所以我最后总是忍不住和她打起来。', 'char_416_zumama/CN_004');
INSERT INTO `t_operator_voice` VALUES (5329, 164, '晋升后交谈1', '大祭司爷爷？啊，他没有跟过来呢，不过他说过，偶尔会来罗德岛看一看我的。这么说起来，他真的知道罗德岛在哪儿吗......', 'char_416_zumama/CN_005');
INSERT INTO `t_operator_voice` VALUES (5330, 164, '晋升后交谈2', '以前，我的梦想是造出和移动城市一样大的机器。现在，我的梦想是，造出一台会说话的，和移动城市一样大的机器。可露希尔师父已经答应我教给我最厉害的机器知识了。', 'char_416_zumama/CN_006');
INSERT INTO `t_operator_voice` VALUES (5331, 164, '信赖提升后交谈1', '......噢，我懂了，也就是说，博士是对很有知识的人的尊称，就和大酋长一样。那等我在这里学了很多知识后，大家也会叫我祖玛玛博士吧。唔，不会吗？', 'char_416_zumama/CN_007');
INSERT INTO `t_operator_voice` VALUES (5332, 164, '信赖提升后交谈2', '博士，原本我想让你有空的时候和我一起回萨尔贡，告诉我一些事情该怎么做。不过你好像很忙的样子......所以，我可以让Lancet-2大姐和我回去吗？可露希尔师父也可以。', 'char_416_zumama/CN_008');
INSERT INTO `t_operator_voice` VALUES (5333, 164, '信赖提升后交谈3', '外面的人整天都在为没有意义的事烦恼和打架，真奇怪，博士你也是，感觉你总是被这些事情搞得很难过的样子，给你，扳手，来和我一起造机器吧，看着机器越来越大越来越厉害，很快乐的。', 'char_416_zumama/CN_009');
INSERT INTO `t_operator_voice` VALUES (5334, 164, '闲置', '看着博士睡觉，我也有点困了......呼啊......', 'char_416_zumama/CN_010');
INSERT INTO `t_operator_voice` VALUES (5335, 164, '干员报到', '测试我都通过了，现在我可以留在这里了吧？我想学习建造Lancet-2那样会说话的机器......唔，代号？我的名字是祖玛玛，用名字不行吗？', 'char_416_zumama/CN_011');
INSERT INTO `t_operator_voice` VALUES (5336, 164, '观看作战记录', '居然有能和嘉维尔打平手的人......', 'char_416_zumama/CN_012');
INSERT INTO `t_operator_voice` VALUES (5337, 164, '精英化晋升1', '升职？就是从战士变成勇士一样感觉的东西吗？......嗯，要庆祝一下呢，今晚和Lancet-2大姐一起在舰桥上烤肉好了。', 'char_416_zumama/CN_013');
INSERT INTO `t_operator_voice` VALUES (5338, 164, '精英化晋升2', '大祭司爷爷说过，这片大地是很广阔的。以前我不明白为什么他说这句话的语气有些悲伤，来到罗德岛后我有些明白了。广阔，不一定是好事。', 'char_416_zumama/CN_014');
INSERT INTO `t_operator_voice` VALUES (5339, 164, '编入队伍', '该狩猎了吗？', 'char_416_zumama/CN_017');
INSERT INTO `t_operator_voice` VALUES (5340, 164, '任命队长', '猎物是什么？', 'char_416_zumama/CN_018');
INSERT INTO `t_operator_voice` VALUES (5341, 164, '行动出发', '博士也要一起去吗？', 'char_416_zumama/CN_019');
INSERT INTO `t_operator_voice` VALUES (5342, 164, '行动开始', '全部解决掉就好了吗？', 'char_416_zumama/CN_020');
INSERT INTO `t_operator_voice` VALUES (5343, 164, '选中干员1', '我随时都可以。', 'char_416_zumama/CN_021');
INSERT INTO `t_operator_voice` VALUES (5344, 164, '选中干员2', '让我去吧。', 'char_416_zumama/CN_022');
INSERT INTO `t_operator_voice` VALUES (5345, 164, '部署1', '有人配合会更有效率。', 'char_416_zumama/CN_023');
INSERT INTO `t_operator_voice` VALUES (5346, 164, '部署2', '守住这里？唔，不难。', 'char_416_zumama/CN_024');
INSERT INTO `t_operator_voice` VALUES (5347, 164, '作战中1', '你们比嘉维尔要弱。', 'char_416_zumama/CN_025');
INSERT INTO `t_operator_voice` VALUES (5348, 164, '作战中2', '我的力气也不小哦。', 'char_416_zumama/CN_026');
INSERT INTO `t_operator_voice` VALUES (5349, 164, '作战中3', '无论什么猎物都要全力以赴。', 'char_416_zumama/CN_027');
INSERT INTO `t_operator_voice` VALUES (5350, 164, '作战中4', '咬住你们了。', 'char_416_zumama/CN_028');
INSERT INTO `t_operator_voice` VALUES (5351, 164, '4星结束行动', '大丰收。', 'char_416_zumama/CN_029');
INSERT INTO `t_operator_voice` VALUES (5352, 164, '3星结束行动', '和嘉维尔比，还差一点。', 'char_416_zumama/CN_030');
INSERT INTO `t_operator_voice` VALUES (5353, 164, '非3星结束行动', '我去追。', 'char_416_zumama/CN_031');
INSERT INTO `t_operator_voice` VALUES (5354, 164, '行动失败', '没关系，我也输给嘉维尔很多次，不丢人。', 'char_416_zumama/CN_032');
INSERT INTO `t_operator_voice` VALUES (5355, 164, '进驻设施', 'Lancet-2大姐在哪里？', 'char_416_zumama/CN_033');
INSERT INTO `t_operator_voice` VALUES (5356, 164, '戳一下', '唔？', 'char_416_zumama/CN_034');
INSERT INTO `t_operator_voice` VALUES (5357, 164, '信赖触摸', '怎么样才能像博士一样博学呢......', 'char_416_zumama/CN_036');
INSERT INTO `t_operator_voice` VALUES (5358, 164, '标题', '明日方舟。', 'char_416_zumama/CN_037');
INSERT INTO `t_operator_voice` VALUES (5359, 164, '问候', '博士，我正好有问题想向你请教。', 'char_416_zumama/CN_042');
INSERT INTO `t_operator_voice` VALUES (5360, 165, '任命助理', '文件都整理好了，今天已经没别的事情了吧？不如来聊聊上次的作战吧，老师。', 'char_271_spikes/CN_001');
INSERT INTO `t_operator_voice` VALUES (5361, 165, '交谈1', '在我施放法术时，最好不要靠得太近......不走开吗？那就请随意。', 'char_271_spikes/CN_002');
INSERT INTO `t_operator_voice` VALUES (5362, 165, '交谈2', '为什么喊你老师？因为在你身边能学到东西，比如战斗的布局和指挥，还有很多别的......除此之外，还需要什么其他理由吗。', 'char_271_spikes/CN_003');
INSERT INTO `t_operator_voice` VALUES (5363, 165, '交谈3', '喏，这个给你，大概是什么点心吧。不用谢，反正是别人送的东西，我没什么兴趣。', 'char_271_spikes/CN_004');
INSERT INTO `t_operator_voice` VALUES (5364, 165, '晋升后交谈1', '用杖的萨科塔人很稀奇？别被某些整天只知道砰砰砰，什么事情都依赖暴力解决的家伙影响了，萨科塔人也不是人人都会选择铳枪作为武器的，至少我就不喜欢。', 'char_271_spikes/CN_005');
INSERT INTO `t_operator_voice` VALUES (5365, 165, '晋升后交谈2', '与其共享同一个秘密，不如交换两个秘密，这样才称得上平衡与稳固。所以，如果想知道我的秘密，那就考虑一下要拿出什么样的东西与我交换吧，老师。', 'char_271_spikes/CN_006');
INSERT INTO `t_operator_voice` VALUES (5366, 165, '信赖提升后交谈1', '拉特兰的土地上没有感染者，萨科塔人一旦感染，就会遭到流放。但感染者仍然还属于拉特兰，不论他们身处何方，都仍享有拉特兰的种种保障......只是无法再一次踏上故土，仅此而已。', 'char_271_spikes/CN_007');
INSERT INTO `t_operator_voice` VALUES (5367, 165, '信赖提升后交谈2', '父母的遗嘱里给我留了点小麻烦，费了我不少功夫去解决。如果不是那个死脑筋的公证所执行人添乱的话，事情还会更顺利点......哼，算了，不管怎么说，都还在我的计划之中。', 'char_271_spikes/CN_008');
INSERT INTO `t_operator_voice` VALUES (5368, 165, '信赖提升后交谈3', '这次的作战，我想抓几个人来试试我的新法术。不行？......好吧，好吧，虽然很遗憾，但如果是老师阻止的话，这次就算了。嗯？不仅是这次，今后也不允许？真严格啊，老师。', 'char_271_spikes/CN_009');
INSERT INTO `t_operator_voice` VALUES (5369, 165, '闲置', '睡了？工作时间睡着的人会被在脸上涂鸦......开玩笑的，我知道你醒着。', 'char_271_spikes/CN_010');
INSERT INTO `t_operator_voice` VALUES (5370, 165, '干员报到', '代号芳汀，今天起正式加入。你就是博士啊......那就让我们试着好好相处吧，{@nickname}老师。', 'char_271_spikes/CN_011');
INSERT INTO `t_operator_voice` VALUES (5371, 165, '观看作战记录', '战术学习吗，好像有点意思。', 'char_271_spikes/CN_012');
INSERT INTO `t_operator_voice` VALUES (5372, 165, '精英化晋升1', '好像和之前也没什么区别嘛。还是说，老师要教我什么密藏战术了？', 'char_271_spikes/CN_013');
INSERT INTO `t_operator_voice` VALUES (5373, 165, '精英化晋升2', '博士，你是故意在试探我吗？好啊，正巧我也有点兴奋起来了。那么，接下来不论发生什么，都是我们之间的秘密哦。', 'char_271_spikes/CN_014');
INSERT INTO `t_operator_voice` VALUES (5374, 165, '编入队伍', '了解，听你安排。', 'char_271_spikes/CN_017');
INSERT INTO `t_operator_voice` VALUES (5375, 165, '任命队长', '这次是实战课题？好啊，让我想想该怎么布置......', 'char_271_spikes/CN_018');
INSERT INTO `t_operator_voice` VALUES (5376, 165, '行动出发', '只要能达成目标，过程乱来点也不要紧吧？', 'char_271_spikes/CN_019');
INSERT INTO `t_operator_voice` VALUES (5377, 165, '行动开始', '欢迎，我在等各位。', 'char_271_spikes/CN_020');
INSERT INTO `t_operator_voice` VALUES (5378, 165, '选中干员1', '到我了？', 'char_271_spikes/CN_021');
INSERT INTO `t_operator_voice` VALUES (5379, 165, '选中干员2', '别急。', 'char_271_spikes/CN_022');
INSERT INTO `t_operator_voice` VALUES (5380, 165, '部署1', '不错的位置。', 'char_271_spikes/CN_023');
INSERT INTO `t_operator_voice` VALUES (5381, 165, '部署2', '做什么都可以吧？', 'char_271_spikes/CN_024');
INSERT INTO `t_operator_voice` VALUES (5382, 165, '作战中1', '已经警告过各位了哦。', 'char_271_spikes/CN_025');
INSERT INTO `t_operator_voice` VALUES (5383, 165, '作战中2', '很难受吗？', 'char_271_spikes/CN_026');
INSERT INTO `t_operator_voice` VALUES (5384, 165, '作战中3', '告诉我，现在是什么感觉？', 'char_271_spikes/CN_027');
INSERT INTO `t_operator_voice` VALUES (5385, 165, '作战中4', '不错的惨叫声。', 'char_271_spikes/CN_028');
INSERT INTO `t_operator_voice` VALUES (5386, 165, '4星结束行动', '比我预估的情况还要更顺利，还是小看了老师呀。', 'char_271_spikes/CN_029');
INSERT INTO `t_operator_voice` VALUES (5387, 165, '3星结束行动', '不出意料的结果，没有任何意外，反而稍微有点无聊。', 'char_271_spikes/CN_030');
INSERT INTO `t_operator_voice` VALUES (5388, 165, '非3星结束行动', '溜了几个，要追击吗？从战术上来说，我不推荐。', 'char_271_spikes/CN_031');
INSERT INTO `t_operator_voice` VALUES (5389, 165, '行动失败', '局面不太妙啊......这时候该怎么部署，不用我说吧？', 'char_271_spikes/CN_032');
INSERT INTO `t_operator_voice` VALUES (5390, 165, '进驻设施', '不能住得离老师更近一点吗？', 'char_271_spikes/CN_033');
INSERT INTO `t_operator_voice` VALUES (5391, 165, '戳一下', '嗯？', 'char_271_spikes/CN_034');
INSERT INTO `t_operator_voice` VALUES (5392, 165, '信赖触摸', '正好有时间，来把上次任务的复盘做完吧，老师。', 'char_271_spikes/CN_036');
INSERT INTO `t_operator_voice` VALUES (5393, 165, '标题', '明日方舟。', 'char_271_spikes/CN_037');
INSERT INTO `t_operator_voice` VALUES (5394, 165, '问候', '你看起来好像很困，再睡一会吧，老师。', 'char_271_spikes/CN_042');
INSERT INTO `t_operator_voice` VALUES (5395, 166, '任命助理', '博士，需要我帮你做什么？', 'char_365_aprl/CN_001');
INSERT INTO `t_operator_voice` VALUES (5396, 166, '交谈1', '哈......每天能洗热水澡真是太好了......自从得了感染病后，我就很少洗真正的热水澡了，因为换了住处之后条件不允许呢。而且这里食物也很干净，啊，我爱罗德岛~', 'char_365_aprl/CN_002');
INSERT INTO `t_operator_voice` VALUES (5397, 166, '交谈2', '《四月》啊？《四月》是一首关于春天的歌，歌词的内容大概是在春天那种温暖的天气里，躺在草地上看着天空想东想西这样一种悠哉的感觉，博士你要听吗？你一定会喜欢的~', 'char_365_aprl/CN_003');
INSERT INTO `t_operator_voice` VALUES (5398, 166, '交谈3', '咦，我的唇釉呢......啊，想起来了，之前借给暗索了。嗯？啊，因为我说要教她化妆呢。她底子很好的，打扮一下就可爱多了。', 'char_365_aprl/CN_004');
INSERT INTO `t_operator_voice` VALUES (5399, 166, '晋升后交谈1', '在雷姆必拓，虽然公司说对感染者有补助金，但这笔钱其实到手上就很少，而且工作机会也少了许多......看到罗德岛的广告来试试真是正确的选择呢，在这里能感觉到自己被当人看。', 'char_365_aprl/CN_005');
INSERT INTO `t_operator_voice` VALUES (5400, 166, '晋升后交谈2', '得了矿石病后，我偶尔也会被人说，感染者穿得这么鲜艳干什么，感染者听什么音乐......对于这样的人，我的回答永远都是——因为我喜欢~', 'char_365_aprl/CN_006');
INSERT INTO `t_operator_voice` VALUES (5401, 166, '信赖提升后交谈1', '在雷姆必拓时，我原本的职业是猎人，负责驱赶城市周围的野兽以及护卫公司的队伍。但是感染矿石病后我就失业了，一直靠自己打猎维生，那段日子真是不好过呢......', 'char_365_aprl/CN_007');
INSERT INTO `t_operator_voice` VALUES (5402, 166, '信赖提升后交谈2', '我的父母在我很小的时候就因为矿难去世了，我是靠公司的补助长大的，所以其实我一直对公司还是很感激的，觉得感染矿石病是我自己不小心什么的......但是其实不是这样的，对吧，博士？', 'char_365_aprl/CN_008');
INSERT INTO `t_operator_voice` VALUES (5403, 166, '信赖提升后交谈3', '为了感染者做些什么......以前我从来没有想过呢，毕竟让自己能够活下去已经很困难了。不过来到罗德岛后，我开始想要试一试了，不只是为自己，而是为所有遭受同样苦难的人做些什么。', 'char_365_aprl/CN_009');
INSERT INTO `t_operator_voice` VALUES (5404, 166, '闲置', '博士，你要睡一会儿的话，我把我的耳塞借你吧。', 'char_365_aprl/CN_010');
INSERT INTO `t_operator_voice` VALUES (5405, 166, '干员报到', '罗德岛的设施和待遇竟然这么好......啊，抱歉，博士，我只是有些吃惊。我的代号就叫四月吧，嗯，来源是我最喜欢的一首歌哦。', 'char_365_aprl/CN_011');
INSERT INTO `t_operator_voice` VALUES (5406, 166, '观看作战记录', '专业人士真是厉害呢......', 'char_365_aprl/CN_012');
INSERT INTO `t_operator_voice` VALUES (5407, 166, '精英化晋升1', '诶？加薪？......啊，我不是不满意，不如说，我很满意，我只是在想，自己的工作得到别人承认这种事，真是很久没有过了呢。', 'char_365_aprl/CN_013');
INSERT INTO `t_operator_voice` VALUES (5408, 166, '精英化晋升2', '在雷姆必拓时，我喜欢坐在高楼的屋顶听歌，那里可以看到整座城市的景色。而现在，在罗德岛的舰桥上我可以看到更多的景色。得病也不全是坏事呢，博士。', 'char_365_aprl/CN_014');
INSERT INTO `t_operator_voice` VALUES (5409, 166, '编入队伍', '来啦~', 'char_365_aprl/CN_017');
INSERT INTO `t_operator_voice` VALUES (5410, 166, '任命队长', '大家跟紧我哦！', 'char_365_aprl/CN_018');
INSERT INTO `t_operator_voice` VALUES (5411, 166, '行动出发', '让我们出发吧！', 'char_365_aprl/CN_019');
INSERT INTO `t_operator_voice` VALUES (5412, 166, '行动开始', '戴上耳机，开始行动吧~', 'char_365_aprl/CN_020');
INSERT INTO `t_operator_voice` VALUES (5413, 166, '选中干员1', '我的弓已经准备好了~', 'char_365_aprl/CN_021');
INSERT INTO `t_operator_voice` VALUES (5414, 166, '选中干员2', '要射谁？', 'char_365_aprl/CN_022');
INSERT INTO `t_operator_voice` VALUES (5415, 166, '部署1', '视野不错~', 'char_365_aprl/CN_023');
INSERT INTO `t_operator_voice` VALUES (5416, 166, '部署2', '交给我吧~', 'char_365_aprl/CN_024');
INSERT INTO `t_operator_voice` VALUES (5417, 166, '作战中1', '来首动感的音乐吧。', 'char_365_aprl/CN_025');
INSERT INTO `t_operator_voice` VALUES (5418, 166, '作战中2', '这个节奏正适合现在！', 'char_365_aprl/CN_026');
INSERT INTO `t_operator_voice` VALUES (5419, 166, '作战中3', '我已经锁定你们了~', 'char_365_aprl/CN_027');
INSERT INTO `t_operator_voice` VALUES (5420, 166, '作战中4', '这一箭感觉不会偏！', 'char_365_aprl/CN_028');
INSERT INTO `t_operator_voice` VALUES (5421, 166, '4星结束行动', '博士，要不要听点舒缓的音乐放松一下？', 'char_365_aprl/CN_029');
INSERT INTO `t_operator_voice` VALUES (5422, 166, '3星结束行动', '一个都没有漏掉哦~', 'char_365_aprl/CN_030');
INSERT INTO `t_operator_voice` VALUES (5423, 166, '非3星结束行动', '博士，有几个逃掉了呢，要去追吗？', 'char_365_aprl/CN_031');
INSERT INTO `t_operator_voice` VALUES (5424, 166, '行动失败', '博士，我来掩护你！', 'char_365_aprl/CN_032');
INSERT INTO `t_operator_voice` VALUES (5425, 166, '进驻设施', '逛几次都觉得罗德岛真大呢。', 'char_365_aprl/CN_033');
INSERT INTO `t_operator_voice` VALUES (5426, 166, '戳一下', '诶诶？', 'char_365_aprl/CN_034');
INSERT INTO `t_operator_voice` VALUES (5427, 166, '信赖触摸', '博士应该在办公室吧？', 'char_365_aprl/CN_036');
INSERT INTO `t_operator_voice` VALUES (5428, 166, '标题', '明日方舟。', 'char_365_aprl/CN_037');
INSERT INTO `t_operator_voice` VALUES (5429, 166, '问候', '博士，要听歌吗？', 'char_365_aprl/CN_042');
INSERT INTO `t_operator_voice` VALUES (5430, 167, '任命助理', '这里的书都可以看吗？全部都可以？嗯，嗯，我会小心，不会弄坏的！', 'char_388_mint/CN_001');
INSERT INTO `t_operator_voice` VALUES (5431, 167, '交谈1', '啊，不可以，不能随便乱碰我的书，它会咬你的手。不是在开玩笑，是真的哦，你看，平常我都会像这样把它绑起来。', 'char_388_mint/CN_002');
INSERT INTO `t_operator_voice` VALUES (5432, 167, '交谈2', '博士，你知道图书室在哪里吗？刚刚我也问了别人，但是那个人说得太快了，好难懂哦。', 'char_388_mint/CN_003');
INSERT INTO `t_operator_voice` VALUES (5433, 167, '交谈3', '天火学姐，天火学姐，等等我，呜，不要走得这么快嘛......这次我保证不会再半途乱跑了，我保证！', 'char_388_mint/CN_004');
INSERT INTO `t_operator_voice` VALUES (5434, 167, '晋升后交谈1', '虽然喜欢看书，但我也喜欢和大家在一起玩，和喜欢的人呆在一起，就会很开心。别看我这样，运动方面我还是很擅长的哦，特别是爬树，我能爬很高呢！', 'char_388_mint/CN_005');
INSERT INTO `t_operator_voice` VALUES (5435, 167, '晋升后交谈2', '今天和艾雅法拉小姐聊天又获得了新的灵感，她对于地质坏境真的很有研究！幸好我们小组的课题没有因为我出的意外被叫停，嘿嘿，如果这次能出结果，我就可以申请留校继续读书啦。', 'char_388_mint/CN_006');
INSERT INTO `t_operator_voice` VALUES (5436, 167, '信赖提升后交谈1', '天火学姐是学校里的前辈，虽然我入学之后，学姐没多久就跳级毕业了，但是我也有被学姐关照过......对了，我来这里接受治疗，也是学姐帮忙介绍的呢。', 'char_388_mint/CN_007');
INSERT INTO `t_operator_voice` VALUES (5437, 167, '信赖提升后交谈2', '欸，为什么一直跟在博士身后？因为跟在博士身边，感觉很安心，所以下意识就......博士，嫌我烦了吗？要赶我走了吗？', 'char_388_mint/CN_008');
INSERT INTO `t_operator_voice` VALUES (5438, 167, '信赖提升后交谈3', '博士，这个送给你。嗯，是回来的路上找到的，我找到很多好看的石头，可是回过神来时，就和大家走散了。欸，之前摆在桌上的矿石原石？嗯，那个也是我放的......博士喜欢吗？', 'char_388_mint/CN_009');
INSERT INTO `t_operator_voice` VALUES (5439, 167, '闲置', '嗯......嗯嗯嗯......嘿！啊，博士，对不起，吵醒你了？刚刚，有小虫子落在你头上，我一时没忍住......痛不痛，我帮你吹吹。', 'char_388_mint/CN_010');
INSERT INTO `t_operator_voice` VALUES (5440, 167, '干员报到', '谢谢你陪我一起搬这些书，放在这里就可以了。那个，我的代号是薄绿，你呢？......欸，博士？你就是天火学姐说的那个博士吗？', 'char_388_mint/CN_011');
INSERT INTO `t_operator_voice` VALUES (5441, 167, '观看作战记录', '哇......好厉害，大家都好厉害。', 'char_388_mint/CN_012');
INSERT INTO `t_operator_voice` VALUES (5442, 167, '精英化晋升1', '晋升会有什么奖励吗？可以借更多的书吗？', 'char_388_mint/CN_013');
INSERT INTO `t_operator_voice` VALUES (5443, 167, '精英化晋升2', '博士，请坐这里。如何，阳光很好，很舒服吧？嘿嘿，这里是我的秘密基地，是我最喜欢的地方了，绝对不可以告诉别人哦。', 'char_388_mint/CN_014');
INSERT INTO `t_operator_voice` VALUES (5444, 167, '编入队伍', '好呀，博士也一起去吗？', 'char_388_mint/CN_017');
INSERT INTO `t_operator_voice` VALUES (5445, 167, '任命队长', '欸，我当队长？', 'char_388_mint/CN_018');
INSERT INTO `t_operator_voice` VALUES (5446, 167, '行动出发', '出发了哦。', 'char_388_mint/CN_019');
INSERT INTO `t_operator_voice` VALUES (5447, 167, '行动开始', '那些就是我们的敌人？', 'char_388_mint/CN_020');
INSERT INTO `t_operator_voice` VALUES (5448, 167, '选中干员1', '哎，什么，我在听，我在听的。', 'char_388_mint/CN_021');
INSERT INTO `t_operator_voice` VALUES (5449, 167, '选中干员2', '嗯，交给我。', 'char_388_mint/CN_022');
INSERT INTO `t_operator_voice` VALUES (5450, 167, '部署1', '嘿呦咻。', 'char_388_mint/CN_023');
INSERT INTO `t_operator_voice` VALUES (5451, 167, '部署2', '请不要妨碍我们！', 'char_388_mint/CN_024');
INSERT INTO `t_operator_voice` VALUES (5452, 167, '作战中1', '嘿！', 'char_388_mint/CN_025');
INSERT INTO `t_operator_voice` VALUES (5453, 167, '作战中2', '请安静一点。', 'char_388_mint/CN_026');
INSERT INTO `t_operator_voice` VALUES (5454, 167, '作战中3', '抓到你了哦。', 'char_388_mint/CN_027');
INSERT INTO `t_operator_voice` VALUES (5455, 167, '作战中4', '要不要看我变魔术？', 'char_388_mint/CN_028');
INSERT INTO `t_operator_voice` VALUES (5456, 167, '4星结束行动', '真的和博士说的一模一样，好厉害......', 'char_388_mint/CN_029');
INSERT INTO `t_operator_voice` VALUES (5457, 167, '3星结束行动', '呼，结束了，终于可以回去了。', 'char_388_mint/CN_030');
INSERT INTO `t_operator_voice` VALUES (5458, 167, '非3星结束行动', '他们不再拦着我们了，这样就可以了吧？', 'char_388_mint/CN_031');
INSERT INTO `t_operator_voice` VALUES (5459, 167, '行动失败', '不行，很危险......博士，不要去那边！', 'char_388_mint/CN_032');
INSERT INTO `t_operator_voice` VALUES (5460, 167, '进驻设施', '那个，其实，我睡图书室也可以的。', 'char_388_mint/CN_033');
INSERT INTO `t_operator_voice` VALUES (5461, 167, '戳一下', '呀！', 'char_388_mint/CN_034');
INSERT INTO `t_operator_voice` VALUES (5462, 167, '信赖触摸', '啊，博士，要一起看书吗？坐在这里很舒服哦。', 'char_388_mint/CN_036');
INSERT INTO `t_operator_voice` VALUES (5463, 167, '标题', '明日方舟。', 'char_388_mint/CN_037');
INSERT INTO `t_operator_voice` VALUES (5464, 167, '问候', '你在这里啊博士，找到你了。', 'char_388_mint/CN_042');
INSERT INTO `t_operator_voice` VALUES (5465, 168, '任命助理', '你连自己的份内之事都完成不了，需要我来帮忙？', 'char_350_surtr/CN_001');
INSERT INTO `t_operator_voice` VALUES (5466, 168, '交谈1', '不要让我去给其他人做法术指导了，我可没兴趣去跟一群普通人玩源石技艺游戏。', 'char_350_surtr/CN_002');
INSERT INTO `t_operator_voice` VALUES (5467, 168, '交谈2', '我会在这里不是因为我需要在这里，只是因为我想而已。', 'char_350_surtr/CN_003');
INSERT INTO `t_operator_voice` VALUES (5468, 168, '交谈3', '我最讨厌的就是不停问为什么的人，希望你不是。', 'char_350_surtr/CN_004');
INSERT INTO `t_operator_voice` VALUES (5469, 168, '晋升后交谈1', '看来你已经对我的源石技艺有所了解了，希望你自己也清楚其危险性。', 'char_350_surtr/CN_005');
INSERT INTO `t_operator_voice` VALUES (5470, 168, '晋升后交谈2', '反应过来的时候，源石技艺和这把法杖已经伴随着我了。我一点都不记得什么时候得到了这样的能力，当然我也并不在意。', 'char_350_surtr/CN_006');
INSERT INTO `t_operator_voice` VALUES (5471, 168, '信赖提升后交谈1', '我有着各种各样的记忆，我也不知道它们是怎么来的，我也不知道哪些是真正属于我的。为了找到记忆的来源，我一直在各个国家中来回旅行，而现在来到了罗德岛。', 'char_350_surtr/CN_007');
INSERT INTO `t_operator_voice` VALUES (5472, 168, '信赖提升后交谈2', '没有什么比一份冰淇淋更能让我从混乱的记忆中冷静下来了，以前在我还是一个人的时候多亏了它们。', 'char_350_surtr/CN_008');
INSERT INTO `t_operator_voice` VALUES (5473, 168, '信赖提升后交谈3', '在罗德岛的记忆跟以前的那些完全不同，充满了真实感，第一次让我觉得自己就是自己。这一切也多亏了你和罗德岛的大家。......干嘛，该感谢的时候我还是会感谢的好吧？', 'char_350_surtr/CN_009');
INSERT INTO `t_operator_voice` VALUES (5474, 168, '闲置', '啧......浪费时间。', 'char_350_surtr/CN_010');
INSERT INTO `t_operator_voice` VALUES (5475, 168, '干员报到', '我是史尔特尔，你就是他们说的博士吗。今后我会在罗德岛工作，希望你们不要让我觉得无聊。', 'char_350_surtr/CN_011');
INSERT INTO `t_operator_voice` VALUES (5476, 168, '观看作战记录', '就这样？', 'char_350_surtr/CN_012');
INSERT INTO `t_operator_voice` VALUES (5477, 168, '精英化晋升1', '见识过我的实力之后，开始重视我了？', 'char_350_surtr/CN_013');
INSERT INTO `t_operator_voice` VALUES (5478, 168, '精英化晋升2', '看重的不是这能力，而是我本身？既然你都这样说了，那我也拿出相应的努力来回报你吧。', 'char_350_surtr/CN_014');
INSERT INTO `t_operator_voice` VALUES (5479, 168, '编入队伍', '需要我了吗。', 'char_350_surtr/CN_017');
INSERT INTO `t_operator_voice` VALUES (5480, 168, '任命队长', '我一个人就够了。', 'char_350_surtr/CN_018');
INSERT INTO `t_operator_voice` VALUES (5481, 168, '行动出发', '出发了。', 'char_350_surtr/CN_019');
INSERT INTO `t_operator_voice` VALUES (5482, 168, '行动开始', '要消灭的就是他们吗。', 'char_350_surtr/CN_020');
INSERT INTO `t_operator_voice` VALUES (5483, 168, '选中干员1', '不要对我指手画脚。', 'char_350_surtr/CN_021');
INSERT INTO `t_operator_voice` VALUES (5484, 168, '选中干员2', '不要多说，我知道。', 'char_350_surtr/CN_022');
INSERT INTO `t_operator_voice` VALUES (5485, 168, '部署1', '不错的地方。', 'char_350_surtr/CN_023');
INSERT INTO `t_operator_voice` VALUES (5486, 168, '部署2', '我上了！', 'char_350_surtr/CN_024');
INSERT INTO `t_operator_voice` VALUES (5487, 168, '作战中1', '去死吧！', 'char_350_surtr/CN_025');
INSERT INTO `t_operator_voice` VALUES (5488, 168, '作战中2', '一个也别想逃走。', 'char_350_surtr/CN_026');
INSERT INTO `t_operator_voice` VALUES (5489, 168, '作战中3', '莱万汀！', 'char_350_surtr/CN_027');
INSERT INTO `t_operator_voice` VALUES (5490, 168, '作战中4', '愚蠢的家伙！', 'char_350_surtr/CN_028');
INSERT INTO `t_operator_voice` VALUES (5491, 168, '4星结束行动', '哼，小菜一碟。', 'char_350_surtr/CN_029');
INSERT INTO `t_operator_voice` VALUES (5492, 168, '3星结束行动', '理所当然。', 'char_350_surtr/CN_030');
INSERT INTO `t_operator_voice` VALUES (5493, 168, '非3星结束行动', '连这么简单的事情都做不好吗。', 'char_350_surtr/CN_031');
INSERT INTO `t_operator_voice` VALUES (5494, 168, '行动失败', '没用的人都赶快撤退！这里我来。', 'char_350_surtr/CN_032');
INSERT INTO `t_operator_voice` VALUES (5495, 168, '进驻设施', '还算是干净的房间。', 'char_350_surtr/CN_033');
INSERT INTO `t_operator_voice` VALUES (5496, 168, '戳一下', '我知道自己走。', 'char_350_surtr/CN_034');
INSERT INTO `t_operator_voice` VALUES (5497, 168, '信赖触摸', '安静。', 'char_350_surtr/CN_036');
INSERT INTO `t_operator_voice` VALUES (5498, 168, '标题', '明日方舟。', 'char_350_surtr/CN_037');
INSERT INTO `t_operator_voice` VALUES (5499, 168, '问候', '啊，你在这里。', 'char_350_surtr/CN_042');
INSERT INTO `t_operator_voice` VALUES (5500, 170, '任命助理', '有些驮兽要被鞭子抽了以后才会磨磨蹭蹭地上工，你不一样，博士。你比驮兽聪明多了，对吧？', 'char_265_sophia/CN_001');
INSERT INTO `t_operator_voice` VALUES (5501, 170, '交谈1', '博士，为了防止你产生奇怪的误会，事先声明，虽然玛莉娅叫我姑母，但那只是辈分如此，我没有比她大多少，我还很年轻，也没结婚，明白了吗？', 'char_265_sophia/CN_002');
INSERT INTO `t_operator_voice` VALUES (5502, 170, '交谈2', '我和杜宾教官之间的争执？啊，不必在意，我承认她同样是一位出色的教官，我们只是在对雇员的训练方针上有一些小——小的分歧而已。', 'char_265_sophia/CN_003');
INSERT INTO `t_operator_voice` VALUES (5503, 170, '交谈3', '罗德岛里尽是些是个性丰富又有实力的人呢，其中随便挑一些去参加骑士竞赛恐怕都能吸引不少眼球吧......不过，那种东西还是少掺和为好。', 'char_265_sophia/CN_004');
INSERT INTO `t_operator_voice` VALUES (5504, 170, '晋升后交谈1', '博士，你现在有空吗？麻烦你来帮我一起搬点东西。你问是什么......呃，是我一时兴起买的各种各样的东西。', 'char_265_sophia/CN_005');
INSERT INTO `t_operator_voice` VALUES (5505, 170, '晋升后交谈2', '我的伤？......我已经不打算再次踏上那个舞台，也早就习惯了使用右手，所以不劳费心。还是说，这是博士你在委婉地表达我的战斗能力没有达到干员的标准？', 'char_265_sophia/CN_006');
INSERT INTO `t_operator_voice` VALUES (5506, 170, '信赖提升后交谈1', '玛莉娅这家伙，一有空就会溜到工程部去，真是服了她这个性子，唉，究竟是哪里的教育出了问题呢......算了，总比她硬拿起武器做自己不想做的事要强。', 'char_265_sophia/CN_007');
INSERT INTO `t_operator_voice` VALUES (5507, 170, '信赖提升后交谈2', '嗯？是博士啊。没什么，我只是忽然感到有些空虚，玛嘉烈从来都不用我操心，玛莉娅也在这里找到了想做的事，那么我自己呢......哎呀，我怎么对你说起这个来了，请务必不要放在心上。', 'char_265_sophia/CN_008');
INSERT INTO `t_operator_voice` VALUES (5508, 170, '信赖提升后交谈3', '博士，虽然我们之间关系不差，但是你可不要指望什么都不做以后就来依靠我，我虽然讨厌危险的事，但也不喜欢堕落的人。不过，要是你走投无路的话......咳，那就到时候再说！', 'char_265_sophia/CN_009');
INSERT INTO `t_operator_voice` VALUES (5509, 170, '闲置', '真是个悠哉的家伙呢，算了，就让你休息十分钟吧。', 'char_265_sophia/CN_010');
INSERT INTO `t_operator_voice` VALUES (5510, 170, '干员报到', '啊，这里就是罗德岛了吧？鞭刃佐菲娅，就这么叫我吧。作为卡西米尔的前竞技骑士，我会好好训练你的雇员的。', 'char_265_sophia/CN_011');
INSERT INTO `t_operator_voice` VALUES (5511, 170, '观看作战记录', '这种战斗方式我从来没有听说过！', 'char_265_sophia/CN_012');
INSERT INTO `t_operator_voice` VALUES (5512, 170, '精英化晋升1', '这是罗德岛特有的升职吗？......好吧，虽然我对薪水不怎么关注，不过付出得到回报的感觉并不坏，谢谢。', 'char_265_sophia/CN_013');
INSERT INTO `t_operator_voice` VALUES (5513, 170, '精英化晋升2', '唔，仔细想想，待遇不差，生活质量也不低，还能经常见到玛莉娅和玛嘉烈，一直留在这里当教官好像也是个不错的选择呢。', 'char_265_sophia/CN_014');
INSERT INTO `t_operator_voice` VALUES (5514, 170, '编入队伍', '让我看一看人员配置。', 'char_265_sophia/CN_017');
INSERT INTO `t_operator_voice` VALUES (5515, 170, '任命队长', '那就由我来带队吧。', 'char_265_sophia/CN_018');
INSERT INTO `t_operator_voice` VALUES (5516, 170, '行动出发', '打起精神来，该出发了！', 'char_265_sophia/CN_019');
INSERT INTO `t_operator_voice` VALUES (5517, 170, '行动开始', '没有忘记自己该做什么吧？', 'char_265_sophia/CN_020');
INSERT INTO `t_operator_voice` VALUES (5518, 170, '选中干员1', '该我出场了。', 'char_265_sophia/CN_021');
INSERT INTO `t_operator_voice` VALUES (5519, 170, '选中干员2', '不错的时机呢，博士。', 'char_265_sophia/CN_022');
INSERT INTO `t_operator_voice` VALUES (5520, 170, '部署1', '首先确认战况。', 'char_265_sophia/CN_023');
INSERT INTO `t_operator_voice` VALUES (5521, 170, '部署2', '鞭刃佐菲娅，出击！', 'char_265_sophia/CN_024');
INSERT INTO `t_operator_voice` VALUES (5522, 170, '作战中1', '别小看我！', 'char_265_sophia/CN_025');
INSERT INTO `t_operator_voice` VALUES (5523, 170, '作战中2', '有破绽！', 'char_265_sophia/CN_026');
INSERT INTO `t_operator_voice` VALUES (5524, 170, '作战中3', '不堪一击！', 'char_265_sophia/CN_027');
INSERT INTO `t_operator_voice` VALUES (5525, 170, '作战中4', '该结束了！', 'char_265_sophia/CN_028');
INSERT INTO `t_operator_voice` VALUES (5526, 170, '4星结束行动', '本来挑选这么危险的任务我是打算结束后好好说你两句的，不过完成得这么漂亮就算了......', 'char_265_sophia/CN_029');
INSERT INTO `t_operator_voice` VALUES (5527, 170, '3星结束行动', '你还真是精益求精呢......虽然不是坏事就是了。', 'char_265_sophia/CN_030');
INSERT INTO `t_operator_voice` VALUES (5528, 170, '非3星结束行动', '既然胜利了不就可以了吗，不要太执着。', 'char_265_sophia/CN_031');
INSERT INTO `t_operator_voice` VALUES (5529, 170, '行动失败', '博士，别傻站着，跟我走！', 'char_265_sophia/CN_032');
INSERT INTO `t_operator_voice` VALUES (5530, 170, '进驻设施', '不错的室内风格呢。', 'char_265_sophia/CN_033');
INSERT INTO `t_operator_voice` VALUES (5531, 170, '戳一下', '呀？！', 'char_265_sophia/CN_034');
INSERT INTO `t_operator_voice` VALUES (5532, 170, '信赖触摸', '博士，工作的时候不要分心。', 'char_265_sophia/CN_036');
INSERT INTO `t_operator_voice` VALUES (5533, 170, '标题', '明日方舟。', 'char_265_sophia/CN_037');
INSERT INTO `t_operator_voice` VALUES (5534, 170, '问候', '日安，博士。', 'char_265_sophia/CN_042');
INSERT INTO `t_operator_voice` VALUES (5535, 171, '任命助理', '做助理的话，我可以在旁边做些手工吗？', 'char_346_aosta/CN_001');
INSERT INTO `t_operator_voice` VALUES (5536, 171, '交谈1', '博士，如果你喜欢追求刺激，可以去叙拉古生活一段时间。然后你就会不喜欢了。', 'char_346_aosta/CN_002');
INSERT INTO `t_operator_voice` VALUES (5537, 171, '交谈2', '贾维的性格，有的时候连我都怀疑，他究竟是怎么活到今天没有被人打死的。', 'char_346_aosta/CN_003');
INSERT INTO `t_operator_voice` VALUES (5538, 171, '交谈3', '博士，你对DIY感兴趣吗？其实，我们三个的武器和衣服，都是自己设计和制作的。如果你也喜欢的话，我们可以交流一下。', 'char_346_aosta/CN_004');
INSERT INTO `t_operator_voice` VALUES (5539, 171, '晋升后交谈1', '在帮贾维擦屁股的过程中，我发现了一件事，那就是——想要真正变得强大，光靠暴力是不够的。', 'char_346_aosta/CN_005');
INSERT INTO `t_operator_voice` VALUES (5540, 171, '晋升后交谈2', '在叙拉古，许多同龄人都想成为一个家族的一员，但也有许多脚踏实地生活的人。我有时候，觉得他们比我们更厉害。', 'char_346_aosta/CN_006');
INSERT INTO `t_operator_voice` VALUES (5541, 171, '信赖提升后交谈1', '（哈欠声）......不好意思，博士，我昨晚没有睡好。罗德岛太安全了，我不是很习惯。', 'char_346_aosta/CN_007');
INSERT INTO `t_operator_voice` VALUES (5542, 171, '信赖提升后交谈2', '贾维、布洛卡、我，我们三个是伙伴，也在一起做了不少事。但我有时候也会想，这样的生活，真的就是适合我们的生活吗？', 'char_346_aosta/CN_008');
INSERT INTO `t_operator_voice` VALUES (5543, 171, '信赖提升后交谈3', '其实，我小时候的梦想是成为一名公务员，直到父亲第一次让我拿起武器。我并不讨厌和他们四处闯荡的生活，但始终感觉缺少了一些什么，现在我知道了，我想成为博士你这样内心强大的人。', 'char_346_aosta/CN_009');
INSERT INTO `t_operator_voice` VALUES (5544, 171, '闲置', '博士......睡着了吗，真是个幸福的家伙。', 'char_346_aosta/CN_010');
INSERT INTO `t_operator_voice` VALUES (5545, 171, '干员报到', '博士，你好。我叫奥斯塔。之前承蒙照顾，我们三个人才没有被干掉。不嫌弃的话，请让我们在这里工作一段时间吧。', 'char_346_aosta/CN_011');
INSERT INTO `t_operator_voice` VALUES (5546, 171, '观看作战记录', '......看来，我们三个能活到今天，真的只是运气比较好。', 'char_346_aosta/CN_012');
INSERT INTO `t_operator_voice` VALUES (5547, 171, '精英化晋升1', '博士，罗德岛真的是一家医药公司吗？我感觉，这里的人一起出动的话，说不定可以拿下叙拉古一座城。', 'char_346_aosta/CN_013');
INSERT INTO `t_operator_voice` VALUES (5548, 171, '精英化晋升2', '在罗德岛，或许我能找到一直以来我在追求的生活方式......博士，我能在舰内开一家DIY店吗？', 'char_346_aosta/CN_014');
INSERT INTO `t_operator_voice` VALUES (5549, 171, '编入队伍', '但愿不会是很麻烦的事情。', 'char_346_aosta/CN_017');
INSERT INTO `t_operator_voice` VALUES (5550, 171, '任命队长', '我尽力吧。', 'char_346_aosta/CN_018');
INSERT INTO `t_operator_voice` VALUES (5551, 171, '行动出发', '尽量避免没必要的战斗吧。', 'char_346_aosta/CN_019');
INSERT INTO `t_operator_voice` VALUES (5552, 171, '行动开始', '没办法，快点解决吧。', 'char_346_aosta/CN_020');
INSERT INTO `t_operator_voice` VALUES (5553, 171, '选中干员1', '我去吧。', 'char_346_aosta/CN_021');
INSERT INTO `t_operator_voice` VALUES (5554, 171, '选中干员2', '嗯？', 'char_346_aosta/CN_022');
INSERT INTO `t_operator_voice` VALUES (5555, 171, '部署1', '全都要解决掉吗？', 'char_346_aosta/CN_023');
INSERT INTO `t_operator_voice` VALUES (5556, 171, '部署2', '让我来试一试吧。', 'char_346_aosta/CN_024');
INSERT INTO `t_operator_voice` VALUES (5557, 171, '作战中1', '你好。', 'char_346_aosta/CN_025');
INSERT INTO `t_operator_voice` VALUES (5558, 171, '作战中2', '再见。', 'char_346_aosta/CN_026');
INSERT INTO `t_operator_voice` VALUES (5559, 171, '作战中3', '该结束了。', 'char_346_aosta/CN_027');
INSERT INTO `t_operator_voice` VALUES (5560, 171, '作战中4', '啧。', 'char_346_aosta/CN_028');
INSERT INTO `t_operator_voice` VALUES (5561, 171, '4星结束行动', '呼、呼......博士，挑战极限是你的乐趣之一吗？', 'char_346_aosta/CN_029');
INSERT INTO `t_operator_voice` VALUES (5562, 171, '3星结束行动', '全吃下了，但愿这些人有让我们这么做的价值。', 'char_346_aosta/CN_030');
INSERT INTO `t_operator_voice` VALUES (5563, 171, '非3星结束行动', '跑了几个，不过问题应该不大。', 'char_346_aosta/CN_031');
INSERT INTO `t_operator_voice` VALUES (5564, 171, '行动失败', '博士，你先走，我来殿后，这种事我擅长。', 'char_346_aosta/CN_032');
INSERT INTO `t_operator_voice` VALUES (5565, 171, '进驻设施', '那我随便走走吧。', 'char_346_aosta/CN_033');
INSERT INTO `t_operator_voice` VALUES (5566, 171, '戳一下', '我自己有脚，博士。', 'char_346_aosta/CN_034');
INSERT INTO `t_operator_voice` VALUES (5567, 171, '信赖触摸', '博士，你好。', 'char_346_aosta/CN_036');
INSERT INTO `t_operator_voice` VALUES (5568, 171, '标题', '明日方舟。', 'char_346_aosta/CN_037');
INSERT INTO `t_operator_voice` VALUES (5569, 171, '问候', '你好，博士。', 'char_346_aosta/CN_042');
INSERT INTO `t_operator_voice` VALUES (5570, 172, '任命助理', '虽然我对自己的剑术和源石技艺都不敢太抱自信......不过如果有什么器械维修方面的问题也可以拜托我哦！', 'char_423_blemsh/CN_001');
INSERT INTO `t_operator_voice` VALUES (5571, 172, '交谈1', '佐菲娅是我的远房姑母，也是我的剑术教练，多亏了她我才能在骑士竞赛中生存下来，不过姑母她偶尔——啊不，是姐姐，欸，博士！千万不要让她知道我经常说漏嘴的事情喔，她其实还挺在意的！', 'char_423_blemsh/CN_002');
INSERT INTO `t_operator_voice` VALUES (5572, 172, '交谈2', '博士，其实我比起骑士，更擅长工匠方面的工作喔！不管是修理闹钟还是锻造护甲，我都可以帮得上忙的！所以博士！能带我去看看火神小姐和可露希尔小姐的工坊吗？拜托！', 'char_423_blemsh/CN_003');
INSERT INTO `t_operator_voice` VALUES (5573, 172, '交谈3', '虽然那两位萨卡兹小姐有点难以靠近，不过既然姐姐都那么信任他们，一定都是很厉害的人吧。耀骑士与萨卡兹为了感染者并肩作战......唔啊，感觉不止是新闻头条的范畴了。', 'char_423_blemsh/CN_004');
INSERT INTO `t_operator_voice` VALUES (5574, 172, '晋升后交谈1', '其实我并没有取得像姐姐那样“耀骑士”的封号啦。“瑕光”只是我给自己取的干员代号......不过，如果我真的在骑士的道路上继续前行，也许“瑕光骑士”这个名字也不赖呢。', 'char_423_blemsh/CN_005');
INSERT INTO `t_operator_voice` VALUES (5575, 172, '晋升后交谈2', '卡西米尔的骑士竞技，那只不过是场庞大的商业游戏，它豢养着所有人......来到罗德岛之后我才意识到，卡西米尔的病灶也许不止在背离骑士传统这个方面......远远不止。', 'char_423_blemsh/CN_006');
INSERT INTO `t_operator_voice` VALUES (5576, 172, '信赖提升后交谈1', '哎，比起竞赛骑士，罗德岛的干员们倒更像是小说里的古老骑士嘛，其实我以前沉迷过一段时间的骑士小说喔，骑士、敌人、冒险、财富。不过这些本来就能在骑士竞技里得到，所以就没落啦。', 'char_423_blemsh/CN_007');
INSERT INTO `t_operator_voice` VALUES (5577, 172, '信赖提升后交谈2', '时隔许久再见到姐姐，她就像变了个人一样，比起过去那耀眼的光芒，更像柔和的太阳，温暖，伟岸。是罗德岛带给了她这种变化吗？那我也想见识一下，姐姐在这里发现的东西！', 'char_423_blemsh/CN_008');
INSERT INTO `t_operator_voice` VALUES (5578, 172, '信赖提升后交谈3', '我没能成为真正的骑士，但也因此见识到了更广袤的大地，也许现在，我反而稍微能明白一点了。“所谓骑士，是照亮整片大地的崇高者”，{@nickname}，请继续见证我的觉悟。', 'char_423_blemsh/CN_009');
INSERT INTO `t_operator_voice` VALUES (5579, 172, '闲置', '博士？啊，睡着了吗？博士也会做梦吗？', 'char_423_blemsh/CN_010');
INSERT INTO `t_operator_voice` VALUES (5580, 172, '干员报到', '卡西米尔骑士，代号“瑕光”，嘿——早就听姐姐说过很多你的事情啦，博士，直接喊我玛莉娅也可以哦？', 'char_423_blemsh/CN_011');
INSERT INTO `t_operator_voice` VALUES (5581, 172, '观看作战记录', '等等，欸，这样也行的？', 'char_423_blemsh/CN_012');
INSERT INTO `t_operator_voice` VALUES (5582, 172, '精英化晋升1', '晋升吗......为了以后能继续捍卫临光家的荣耀，我会继续努力的！', 'char_423_blemsh/CN_013');
INSERT INTO `t_operator_voice` VALUES (5583, 172, '精英化晋升2', '突然意识到，好像身边尽是些长辈，从来没有普通的朋友可以给我提提意见呢......博士？这身装备是我自己动手的喔？你觉得怎么样？好看吗？', 'char_423_blemsh/CN_014');
INSERT INTO `t_operator_voice` VALUES (5584, 172, '编入队伍', '瑕光，一定不辱使命！', 'char_423_blemsh/CN_017');
INSERT INTO `t_operator_voice` VALUES (5585, 172, '任命队长', '队、队长？我吗？我可从来没打过团体战哦......？', 'char_423_blemsh/CN_018');
INSERT INTO `t_operator_voice` VALUES (5586, 172, '行动出发', '正面就交给我吧！', 'char_423_blemsh/CN_019');
INSERT INTO `t_operator_voice` VALUES (5587, 172, '行动开始', '家训是，“不畏苦暗”！', 'char_423_blemsh/CN_020');
INSERT INTO `t_operator_voice` VALUES (5588, 172, '选中干员1', '准备就绪。', 'char_423_blemsh/CN_021');
INSERT INTO `t_operator_voice` VALUES (5589, 172, '选中干员2', '为战斗祷告吧。', 'char_423_blemsh/CN_022');
INSERT INTO `t_operator_voice` VALUES (5590, 172, '部署1', '我绝不会退缩半步！', 'char_423_blemsh/CN_023');
INSERT INTO `t_operator_voice` VALUES (5591, 172, '部署2', '光芒将坚守此地！', 'char_423_blemsh/CN_024');
INSERT INTO `t_operator_voice` VALUES (5592, 172, '作战中1', '光！', 'char_423_blemsh/CN_025');
INSERT INTO `t_operator_voice` VALUES (5593, 172, '作战中2', '有我在！', 'char_423_blemsh/CN_026');
INSERT INTO `t_operator_voice` VALUES (5594, 172, '作战中3', '不用担心！', 'char_423_blemsh/CN_027');
INSERT INTO `t_operator_voice` VALUES (5595, 172, '作战中4', '忏悔吧！', 'char_423_blemsh/CN_028');
INSERT INTO `t_operator_voice` VALUES (5596, 172, '4星结束行动', '虽然很微小......但我们的光芒，坚持到了最后......！', 'char_423_blemsh/CN_029');
INSERT INTO `t_operator_voice` VALUES (5597, 172, '3星结束行动', '每一场胜利都会成为照亮前路的光，博士，辛苦了。', 'char_423_blemsh/CN_030');
INSERT INTO `t_operator_voice` VALUES (5598, 172, '非3星结束行动', '还......还没有结束！', 'char_423_blemsh/CN_031');
INSERT INTO `t_operator_voice` VALUES (5599, 172, '行动失败', '伤员交给我来保护，其他人先撤退！', 'char_423_blemsh/CN_032');
INSERT INTO `t_operator_voice` VALUES (5600, 172, '进驻设施', '这里就是罗德岛的内部啊，真想看看结构图是什么样的。', 'char_423_blemsh/CN_033');
INSERT INTO `t_operator_voice` VALUES (5601, 172, '戳一下', '唔啊！别、别吓人啊......', 'char_423_blemsh/CN_034');
INSERT INTO `t_operator_voice` VALUES (5602, 172, '信赖触摸', '博士！嘘！我是背着佐菲娅姑母偷偷溜过来的，嘿嘿，今天就让我偷个懒吧！', 'char_423_blemsh/CN_036');
INSERT INTO `t_operator_voice` VALUES (5603, 172, '标题', '明日方舟。', 'char_423_blemsh/CN_037');
INSERT INTO `t_operator_voice` VALUES (5604, 172, '问候', '啊，您好，博士。', 'char_423_blemsh/CN_042');
INSERT INTO `t_operator_voice` VALUES (5605, 173, '任命助理', '博士，我来帮你的忙吧！', 'char_347_jaksel/CN_001');
INSERT INTO `t_operator_voice` VALUES (5606, 173, '交谈1', '博士，你已经坐在那里工作很久了，这样对身体不好的，来跟我一起做一些伸展运动吧！活动一下身体，然后再补充一下能量，这样才能更好地工作哦。', 'char_347_jaksel/CN_002');
INSERT INTO `t_operator_voice` VALUES (5607, 173, '交谈2', '博士博士，那个人竟然在罗德岛上诶！啊，现在她的代号应该是坚雷了，反正她好厉害的！我以前只在征兵广告上见过她！而且她人超好的，给了我好多零食，诶嘿嘿。', 'char_347_jaksel/CN_003');
INSERT INTO `t_operator_voice` VALUES (5608, 173, '交谈3', '博士，我的体能在同龄人里算是很好的哦，我来给你表演一个原地后空翻吧！看好了！', 'char_347_jaksel/CN_004');
INSERT INTO `t_operator_voice` VALUES (5609, 173, '晋升后交谈1', '这把铳？啊，它是铳型施......施什么来着，啊，对了，施术单元！虽然来到这里后还是按照习惯申请了一把，不过比起用法术，我果然还是更加喜欢用拳头作战呢。', 'char_347_jaksel/CN_005');
INSERT INTO `t_operator_voice` VALUES (5610, 173, '晋升后交谈2', '虽然我也知道我很幸运，但来到罗德岛后我才知道其他的感染者原来过得这么不好......博士，我该怎么做才能帮到其他人呢？我想帮助他们！', 'char_347_jaksel/CN_006');
INSERT INTO `t_operator_voice` VALUES (5611, 173, '信赖提升后交谈1', '......嗯？啊，博士，我正在给爸爸写信。诶，我没介绍过我爸爸吗？我爸爸是很厉害的警察哦，爸爸他可厉害啦！博士我来给你讲讲他的事迹吧！', 'char_347_jaksel/CN_007');
INSERT INTO `t_operator_voice` VALUES (5612, 173, '信赖提升后交谈2', '其实我来罗德岛是爸爸推荐的，他在知道罗德岛能够治疗矿石病后就一直让我努力申请能被派到这边来。虽然不是很想和爸爸分开，不过我也知道，我能健康生活才是最让他开心的吧。', 'char_347_jaksel/CN_008');
INSERT INTO `t_operator_voice` VALUES (5613, 173, '信赖提升后交谈3', '虽然还比不上爸爸，不过博士也很可靠呢，嗯，在我心里现在博士已经是排第二可靠的人了！嘻嘻，对了，博士，我在给爸爸的信里写过不少有关你的事哦，他也说很想见见你呢！', 'char_347_jaksel/CN_009');
INSERT INTO `t_operator_voice` VALUES (5614, 173, '闲置', '博——！啊，博士睡着了，那我小声一点......', 'char_347_jaksel/CN_010');
INSERT INTO `t_operator_voice` VALUES (5615, 173, '干员报到', '博士你好！我是新加入的干员，代号杰克！我过去在哥伦比亚担任辅警，让我们好好相处吧！', 'char_347_jaksel/CN_011');
INSERT INTO `t_operator_voice` VALUES (5616, 173, '观看作战记录', '好厉害，感觉比爸爸都厉害......', 'char_347_jaksel/CN_012');
INSERT INTO `t_operator_voice` VALUES (5617, 173, '精英化晋升1', '咦，我要升职了吗？哇，太棒了！', 'char_347_jaksel/CN_013');
INSERT INTO `t_operator_voice` VALUES (5618, 173, '精英化晋升2', '在罗德岛的生活让我学到了许多呢......说不定有一天我能追上爸爸也说不定呢！博士，我会继续加油的，也要麻烦你多指导我啦~', 'char_347_jaksel/CN_014');
INSERT INTO `t_operator_voice` VALUES (5619, 173, '编入队伍', '我来啦！', 'char_347_jaksel/CN_017');
INSERT INTO `t_operator_voice` VALUES (5620, 173, '任命队长', '我会努力的！', 'char_347_jaksel/CN_018');
INSERT INTO `t_operator_voice` VALUES (5621, 173, '行动出发', '一起加油吧！', 'char_347_jaksel/CN_019');
INSERT INTO `t_operator_voice` VALUES (5622, 173, '行动开始', '作战开始！', 'char_347_jaksel/CN_020');
INSERT INTO `t_operator_voice` VALUES (5623, 173, '选中干员1', '博士，到我了吗？', 'char_347_jaksel/CN_021');
INSERT INTO `t_operator_voice` VALUES (5624, 173, '选中干员2', '热身完毕！', 'char_347_jaksel/CN_022');
INSERT INTO `t_operator_voice` VALUES (5625, 173, '部署1', '交给我吧！', 'char_347_jaksel/CN_023');
INSERT INTO `t_operator_voice` VALUES (5626, 173, '部署2', '到我发挥的时候啦！', 'char_347_jaksel/CN_024');
INSERT INTO `t_operator_voice` VALUES (5627, 173, '作战中1', '吃我一拳！', 'char_347_jaksel/CN_025');
INSERT INTO `t_operator_voice` VALUES (5628, 173, '作战中2', '休想通过！', 'char_347_jaksel/CN_026');
INSERT INTO `t_operator_voice` VALUES (5629, 173, '作战中3', '可不要小看我啦！', 'char_347_jaksel/CN_027');
INSERT INTO `t_operator_voice` VALUES (5630, 173, '作战中4', '一鼓作气！', 'char_347_jaksel/CN_028');
INSERT INTO `t_operator_voice` VALUES (5631, 173, '4星结束行动', '我有几次都觉得要输了，最后居然赢了，博士好厉害啊！', 'char_347_jaksel/CN_029');
INSERT INTO `t_operator_voice` VALUES (5632, 173, '3星结束行动', '博士博士，我有好好努力了吧！', 'char_347_jaksel/CN_030');
INSERT INTO `t_operator_voice` VALUES (5633, 173, '非3星结束行动', '博士，要我去追他们吗？', 'char_347_jaksel/CN_031');
INSERT INTO `t_operator_voice` VALUES (5634, 173, '行动失败', '对不起，博士，要是我再努力一点就好了......', 'char_347_jaksel/CN_032');
INSERT INTO `t_operator_voice` VALUES (5635, 173, '进驻设施', '舰内好大呀！', 'char_347_jaksel/CN_033');
INSERT INTO `t_operator_voice` VALUES (5636, 173, '戳一下', '哇啊啊！', 'char_347_jaksel/CN_034');
INSERT INTO `t_operator_voice` VALUES (5637, 173, '信赖触摸', '博士，你好呀~', 'char_347_jaksel/CN_036');
INSERT INTO `t_operator_voice` VALUES (5638, 173, '标题', '明日方舟。', 'char_347_jaksel/CN_037');
INSERT INTO `t_operator_voice` VALUES (5639, 173, '问候', '啊，是博士！', 'char_347_jaksel/CN_042');
INSERT INTO `t_operator_voice` VALUES (5640, 174, '任命助理', '这些报告都请交给我吧，处理文字相关的工作，会很开心......', 'char_436_whispr/CN_001');
INSERT INTO `t_operator_voice` VALUES (5641, 174, '交谈1', '我刚才拒绝那个叫铃兰的孩子，是不是不太好？但是她说，要和大家一起喝茶，我真的不太擅长应对这种事情。', 'char_436_whispr/CN_002');
INSERT INTO `t_operator_voice` VALUES (5642, 174, '交谈2', '我的身体，比大多数人更脆弱。我会轻易死去，遗忘过去的一切，然后作为幼体迎来新生。“返老还童”......这是一种多么残酷的命运啊......', 'char_436_whispr/CN_003');
INSERT INTO `t_operator_voice` VALUES (5643, 174, '交谈3', '所有的回忆最后都会变成泡沫。不管是痛苦的还是喜悦的，那些不想忘记的过去，总会悄然从我身边逃离。', 'char_436_whispr/CN_004');
INSERT INTO `t_operator_voice` VALUES (5644, 174, '晋升后交谈1', '虽然已经不记得了，但有些东西仍然流淌在我的血液里。小心大海，博士，那是会将我们都吞噬的漆黑噩梦，那是......恐惧本身。', 'char_436_whispr/CN_005');
INSERT INTO `t_operator_voice` VALUES (5645, 174, '晋升后交谈2', '有时我会好奇，像华法琳小姐那样真正的长生种族，是怎样看待生死与别离。如果已经不会因此而痛苦，是不是只是因为早已痛到习惯，痛到麻木了呢？', 'char_436_whispr/CN_006');
INSERT INTO `t_operator_voice` VALUES (5646, 174, '信赖提升后交谈1', '博士，能帮我把这些花，送给那些在治疗的孩子们吗？还像之前一样，就放在他们的床头。......谢谢你，愿意一直帮我保守秘密。', 'char_436_whispr/CN_007');
INSERT INTO `t_operator_voice` VALUES (5647, 174, '信赖提升后交谈2', '博士，不好了，我们快去找可露希尔小姐！棘刺先生他们好像把走廊上的水管炸开了，温蒂现在正在发火！我身上也湿透了......欸，什么，我看起来很开心？怎么会呢！', 'char_436_whispr/CN_008');
INSERT INTO `t_operator_voice` VALUES (5648, 174, '信赖提升后交谈3', '每看过一场电影，我都会将票根存下。就算已经将内容都忘记，这些残留的痕迹，也会一直提醒我。......博士，可以和我一起看一场电影吗？我也想要，留存下和你一起度过的时间。', 'char_436_whispr/CN_009');
INSERT INTO `t_operator_voice` VALUES (5649, 174, '闲置', '请睡吧。呵呵，等到你醒来，会不会就此将我忘记？', 'char_436_whispr/CN_010');
INSERT INTO `t_operator_voice` VALUES (5650, 174, '干员报到', '我的代号是絮雨，不过是一介巡游医师。如果可以的话，就将我当做在屋檐下避雨的旅人吧，请不要太在意我的事......', 'char_436_whispr/CN_011');
INSERT INTO `t_operator_voice` VALUES (5651, 174, '观看作战记录', '这样激烈的战斗，我恐怕......没办法活下来。', 'char_436_whispr/CN_012');
INSERT INTO `t_operator_voice` VALUES (5652, 174, '精英化晋升1', '谢谢。但还是像之前一样就好......', 'char_436_whispr/CN_013');
INSERT INTO `t_operator_voice` VALUES (5653, 174, '精英化晋升2', '那些无法因一次死亡而清算的悲伤，总是像丝丝缕缕的线，缠绕在我的肌肤上......博士，也曾忘记过去的你，是否能明白这份心情？', 'char_436_whispr/CN_014');
INSERT INTO `t_operator_voice` VALUES (5654, 174, '编入队伍', '支援工作的话，就请交给我。', 'char_436_whispr/CN_017');
INSERT INTO `t_operator_voice` VALUES (5655, 174, '任命队长', '我来......当队长？欸？', 'char_436_whispr/CN_018');
INSERT INTO `t_operator_voice` VALUES (5656, 174, '行动出发', '请跟紧我，不要离得太远。', 'char_436_whispr/CN_019');
INSERT INTO `t_operator_voice` VALUES (5657, 174, '行动开始', '请各位小心，一定要保护好自己。', 'char_436_whispr/CN_020');
INSERT INTO `t_operator_voice` VALUES (5658, 174, '选中干员1', '好的。', 'char_436_whispr/CN_021');
INSERT INTO `t_operator_voice` VALUES (5659, 174, '选中干员2', '准备好注射了。', 'char_436_whispr/CN_022');
INSERT INTO `t_operator_voice` VALUES (5660, 174, '部署1', '受伤的人请到我这儿来。', 'char_436_whispr/CN_023');
INSERT INTO `t_operator_voice` VALUES (5661, 174, '部署2', '让我来消除痛苦。', 'char_436_whispr/CN_024');
INSERT INTO `t_operator_voice` VALUES (5662, 174, '作战中1', '请先消毒。', 'char_436_whispr/CN_025');
INSERT INTO `t_operator_voice` VALUES (5663, 174, '作战中2', '不要害怕。', 'char_436_whispr/CN_026');
INSERT INTO `t_operator_voice` VALUES (5664, 174, '作战中3', '我的治疗是不会痛的。', 'char_436_whispr/CN_027');
INSERT INTO `t_operator_voice` VALUES (5665, 174, '作战中4', '我熟悉这死亡的气息。', 'char_436_whispr/CN_028');
INSERT INTO `t_operator_voice` VALUES (5666, 174, '4星结束行动', '睡吧。愿所有人都能在雨声里做个好梦。', 'char_436_whispr/CN_029');
INSERT INTO `t_operator_voice` VALUES (5667, 174, '3星结束行动', '这就是他们故事的结局，一个又一个悲剧......', 'char_436_whispr/CN_030');
INSERT INTO `t_operator_voice` VALUES (5668, 174, '非3星结束行动', '请稍后再追击，让我先替大家治疗。', 'char_436_whispr/CN_031');
INSERT INTO `t_operator_voice` VALUES (5669, 174, '行动失败', '对不起，都怪我拖累了大家......', 'char_436_whispr/CN_032');
INSERT INTO `t_operator_voice` VALUES (5670, 174, '进驻设施', '这里很安静，我能听到雨的声音。', 'char_436_whispr/CN_033');
INSERT INTO `t_operator_voice` VALUES (5671, 174, '戳一下', '欸？吓、吓了我一跳......', 'char_436_whispr/CN_034');
INSERT INTO `t_operator_voice` VALUES (5672, 174, '信赖触摸', '请、请不要动！头发缠住了......', 'char_436_whispr/CN_036');
INSERT INTO `t_operator_voice` VALUES (5673, 174, '标题', '明日方舟。', 'char_436_whispr/CN_037');
INSERT INTO `t_operator_voice` VALUES (5674, 174, '问候', '今天要做些什么呢，博士？', 'char_436_whispr/CN_042');
INSERT INTO `t_operator_voice` VALUES (5675, 175, '任命助理', '嗯？啊，看到这抔泥土了吗......它会轻语，它会舞蹈，它是我的朋友。', 'char_311_mudrok/CN_001');
INSERT INTO `t_operator_voice` VALUES (5676, 175, '交谈1', '可露希尔小姐提醒过我，在罗德岛上最好不要“全副武装”地走来走去......但是以真面目示人......我有点，不习惯，抱歉......', 'char_311_mudrok/CN_002');
INSERT INTO `t_operator_voice` VALUES (5677, 175, '交谈2', '感谢信？啊，是昨天的那些萨卡兹工人吗？我的这身装备和法术性质很适合做些体力活，不过建材和燃料都那么沉重，他们居然靠肉身人力就确保了罗德岛的航行......真厉害啊。', 'char_311_mudrok/CN_003');
INSERT INTO `t_operator_voice` VALUES (5678, 175, '交谈3', '那位精英干员......我以为我在莱塔尼亚见到的那些奇奇怪怪的源石技艺已够异想天开了，可当他随手拨开迷雾的时候我就明白，作为萨卡兹，他很年轻，却更加古老。', 'char_311_mudrok/CN_004');
INSERT INTO `t_operator_voice` VALUES (5679, 175, '晋升后交谈1', '我时常会想起那些离我们而去的战士和同伴，偶尔我会有一种错觉，即使是现在，我也在逃亡的路上......博士，您去过卡兹戴尔吗？那是我旅途的起点，也曾是我误以为的终点。', 'char_311_mudrok/CN_005');
INSERT INTO `t_operator_voice` VALUES (5680, 175, '晋升后交谈2', '凯尔希医生吗......虽然很吃惊，但是也能让人更安心一些......不过罗德岛似乎与其他雇佣兵队伍有合作关系，博士可以告诉我吗？最好别知道？啊......那我大概猜到是谁了。', 'char_311_mudrok/CN_006');
INSERT INTO `t_operator_voice` VALUES (5681, 175, '信赖提升后交谈1', '我成为罗德岛干员是为了让他们能好好治疗，好好活下去，这样才能让那些战士们......死得其所。但是......来到罗德岛后，我才第一次清晰地认识到，自己真的是在为什么而战。', 'char_311_mudrok/CN_007');
INSERT INTO `t_operator_voice` VALUES (5682, 175, '信赖提升后交谈2', '我一个人对着泥人偶说话......？原、原来被看见了吗？那只是一种简化的仪式......呃？其实大家都知道？真的？那我下次会注意的，在没人的深夜......欸，也不要？', 'char_311_mudrok/CN_008');
INSERT INTO `t_operator_voice` VALUES (5683, 175, '信赖提升后交谈3', '从卡兹戴尔到莱塔尼亚，那么多烙印刻在那些受难者的身上，等到受难者不得不拿起武器的时候，却发现自己其实一无所有。博士是怎么看待我的？信任？信任......能让我活下去吗？', 'char_311_mudrok/CN_009');
INSERT INTO `t_operator_voice` VALUES (5684, 175, '闲置', '......打盹的这位就是博士，记住他吧，他会陪着我们走过很远很远的路。', 'char_311_mudrok/CN_010');
INSERT INTO `t_operator_voice` VALUES (5685, 175, '干员报到', '萨卡兹雇佣兵，泥岩......您就是博士吗，罗德岛救了我不止一次，现在，请让我回报您吧。', 'char_311_mudrok/CN_011');
INSERT INTO `t_operator_voice` VALUES (5686, 175, '观看作战记录', '我领教过罗德岛的战斗方法，不过，依旧是很好的参考......', 'char_311_mudrok/CN_012');
INSERT INTO `t_operator_voice` VALUES (5687, 175, '精英化晋升1', '我曾被称作整合运动，又被称作泥岩小队，而现在......我又被过去的对手嘉奖，这就是所谓的“命途多舛”？', 'char_311_mudrok/CN_013');
INSERT INTO `t_operator_voice` VALUES (5688, 175, '精英化晋升2', '嗯？感到很新鲜？因为我在战斗的时候是绝不会摘下头盔的......因为很多原因，不过现在不同了，要、要保持这样吗？如果是要求的话......', 'char_311_mudrok/CN_014');
INSERT INTO `t_operator_voice` VALUES (5689, 175, '编入队伍', '现在我只是罗德岛的干员，我服从您的指挥。', 'char_311_mudrok/CN_017');
INSERT INTO `t_operator_voice` VALUES (5690, 175, '任命队长', '队长......我不是很喜欢当领袖的感觉，不过......也习惯了。', 'char_311_mudrok/CN_018');
INSERT INTO `t_operator_voice` VALUES (5691, 175, '行动出发', '该上战场了。', 'char_311_mudrok/CN_019');
INSERT INTO `t_operator_voice` VALUES (5692, 175, '行动开始', '敌人......', 'char_311_mudrok/CN_020');
INSERT INTO `t_operator_voice` VALUES (5693, 175, '选中干员1', '泥土会回应我的意志。', 'char_311_mudrok/CN_021');
INSERT INTO `t_operator_voice` VALUES (5694, 175, '选中干员2', '请说，岩石在听。', 'char_311_mudrok/CN_022');
INSERT INTO `t_operator_voice` VALUES (5695, 175, '部署1', '战场，从未改变。', 'char_311_mudrok/CN_023');
INSERT INTO `t_operator_voice` VALUES (5696, 175, '部署2', '为自己而战吧。', 'char_311_mudrok/CN_024');
INSERT INTO `t_operator_voice` VALUES (5697, 175, '作战中1', '大地与我为友。', 'char_311_mudrok/CN_025');
INSERT INTO `t_operator_voice` VALUES (5698, 175, '作战中2', '垒成山脉吧。', 'char_311_mudrok/CN_026');
INSERT INTO `t_operator_voice` VALUES (5699, 175, '作战中3', '倾听我的请求。', 'char_311_mudrok/CN_027');
INSERT INTO `t_operator_voice` VALUES (5700, 175, '作战中4', '沃土磐石，站起来吧。', 'char_311_mudrok/CN_028');
INSERT INTO `t_operator_voice` VALUES (5701, 175, '4星结束行动', '逃离是无用的......反抗它，哪怕是被迫反抗它，这是我唯一懂得的对待苦难的方式。', 'char_311_mudrok/CN_029');
INSERT INTO `t_operator_voice` VALUES (5702, 175, '3星结束行动', '朋友们在低语庆贺，是我们赢了，博士。', 'char_311_mudrok/CN_030');
INSERT INTO `t_operator_voice` VALUES (5703, 175, '非3星结束行动', '追吧......我会用朋友的眼注视战场。', 'char_311_mudrok/CN_031');
INSERT INTO `t_operator_voice` VALUES (5704, 175, '行动失败', '这不是第一次背对强敌逃跑，如果这样能让更多人活下来的话......我会负责你们的背后。', 'char_311_mudrok/CN_032');
INSERT INTO `t_operator_voice` VALUES (5705, 175, '进驻设施', '天花板，比想象中矮一些。', 'char_311_mudrok/CN_033');
INSERT INTO `t_operator_voice` VALUES (5706, 175, '戳一下', '......呃？您刚才碰我了吗？', 'char_311_mudrok/CN_034');
INSERT INTO `t_operator_voice` VALUES (5707, 175, '信赖触摸', '博士？您能陪我一会吗？我想......我想问您一些关于卡兹戴尔的事情，啊，朋友们也很喜欢你。', 'char_311_mudrok/CN_036');
INSERT INTO `t_operator_voice` VALUES (5708, 175, '标题', '明日方舟。', 'char_311_mudrok/CN_037');
